INSERT INTO `articleusers` (`id`, `title`, `slug`, `content`, `user_id`, `apply`, `created_at`, `updated_at`) VALUES
(4, 'Thầy trò đường tăng', 'thay-tro-duong-tang', '<p>Sau khi trải qua 81 kiếp nạn, th&agrave;y tr&ograve; đường tăng cũng đến đc đất phật để thỉnh kinh. Anh em hồ hởi gặp như Lai.&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>- Như Lai: c&aacute;c ch&uacute; c&oacute; mang theo USB ko đấy ?&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>- Đường Tăng: sặc..&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>- Như Lai: thế anh truyền kinh cho c&aacute;c ch&uacute; bằng g&igrave; b&acirc;y giờ?&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>- Ngộ Kh&ocirc;ng nhanh tr&iacute; : anh bắn bluetooth v&agrave;o di động cho em.&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>- Ngộ Kh&ocirc;ng lắc mạnh tay con di động anycall haptic hiện ngay bluetooth enable.</p>\n\n<p>&nbsp;</p>\n\n<p>- Như Lai ăn chơi kh&ocirc;ng k&eacute;m r&uacute;t con netbook từ t&uacute;i quần hiệu sony vaio P k&iacute;ch&nbsp;thước 16x9 ra, chỉ trong v&agrave;i gi&acirc;y, việc truyền kinh đ&atilde; xong v&agrave; Như Lai bay đi.&nbsp;</p>\n\n<p>&nbsp;</p>\n\n<p>- Đường Tăng lẩm bẩm, biết thế ở nh&agrave; search Google download cho nhanh.</p>\n\n<p>st</p>', 4, 1, '2022-08-03 06:27:09', '2022-08-03 06:29:50'),
(5, 'Mất trộm bò', 'mat-trom-bo', '<p style=\"text-align:justify\"><span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">Một người vừa mới tậu được con b&ograve;. Tối đi ngủ, anh ta chốt chuồng b&ograve; cẩn thận rồi lại đặt c&aacute;i chỏng ngay lối ra v&agrave;o m&agrave; nằm ngủ. Ấy thế m&agrave; ban đ&ecirc;m, kẻ trộm vẫn dắt mất b&ograve; của anh ta.</span></span><br />\r\n<span style=\"font-size:18px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,Verdana,sans-serif\"><span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">X&oacute;t ruột, anh ta tr&igrave;nh quan:</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">-Bẩm quan, chắc l&agrave; ch&uacute;ng n&oacute; dắt b&ograve; chui qua ch&otilde;ng con nằm m&agrave; đi ra.</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">Quan nghe n&oacute;i v&ocirc; l&yacute; qu&aacute; bật cười :</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">-Con b&ograve; chứ c&oacute; phải con ch&oacute;, con m&egrave;o đ&acirc;u m&agrave; chui qua gầm ch&otilde;ng!</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">-Dạ, bẩm quan, thế th&igrave; ch&uacute;ng n&oacute; dắt b&ograve; của con chui qua lối n&agrave;o ạ? S&aacute;ng dậy c&aacute;i ch&otilde;ng con nằm vẫn để y nguy&ecirc;n ở chỗ cũ chắn lối ra v&agrave;o kia m&agrave; !</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">-Đồ ngốc! M&agrave;y ngủ say, ch&uacute;ng n&oacute; khi&ecirc;ng ch&otilde;ng m&agrave;y nằm ra một b&ecirc;n, dắt b&ograve; ra rồi lại khi&ecirc;ng về chỗ cũ...</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">Người kia vỡ lẽ n&oacute;i:</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">-&Agrave;, thế ra quan th&ocirc;ng đồng với bọn trộm, n&ecirc;n mới tỏ tường được như thế chứ !</span></span></span></span></span><span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">Một người vừa mới tậu được con b&ograve;. Tối đi ngủ, anh ta chốt chuồng b&ograve; cẩn thận rồi lại đặt c&aacute;i chỏng ngay lối ra v&agrave;o m&agrave; nằm ngủ. Ấy thế m&agrave; ban đ&ecirc;m, kẻ trộm vẫn dắt mất b&ograve; của anh ta.</span></span><br />\r\n<span style=\"font-size:18px\"><span style=\"color:#000000\"><span style=\"font-family:Arial,Verdana,sans-serif\"><span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">X&oacute;t ruột, anh ta tr&igrave;nh quan:</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">-Bẩm quan, chắc l&agrave; ch&uacute;ng n&oacute; dắt b&ograve; chui qua ch&otilde;ng con nằm m&agrave; đi ra.</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">Quan nghe n&oacute;i v&ocirc; l&yacute; qu&aacute; bật cười :</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">-Con b&ograve; chứ c&oacute; phải con ch&oacute;, con m&egrave;o đ&acirc;u m&agrave; chui qua gầm ch&otilde;ng!</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">-Dạ, bẩm quan, thế th&igrave; ch&uacute;ng n&oacute; dắt b&ograve; của con chui qua lối n&agrave;o ạ? S&aacute;ng dậy c&aacute;i ch&otilde;ng con nằm vẫn để y nguy&ecirc;n ở chỗ cũ chắn lối ra v&agrave;o kia m&agrave; !</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">-Đồ ngốc! M&agrave;y ngủ say, ch&uacute;ng n&oacute; khi&ecirc;ng ch&otilde;ng m&agrave;y nằm ra một b&ecirc;n, dắt b&ograve; ra rồi lại khi&ecirc;ng về chỗ cũ...</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">Người kia vỡ lẽ n&oacute;i:</span></span><br />\r\n<span style=\"font-size:large\"><span style=\"font-family:&quot;times new roman&quot;,serif\">-&Agrave;, thế ra quan th&ocirc;ng đồng với bọn trộm, n&ecirc;n mới tỏ tường được như thế chứ !</span></span></span></span></span></p>', 4, 1, '2022-08-03 06:49:42', '2022-08-03 06:49:42'),
(6, 'Trả ơn con lợn', 'tra-on-con-lon', '<p><span style=\"font-size:20px\">C&oacute;&nbsp;hai&nbsp;anh kết nghĩa đen&nbsp;s&aacute;ch. Một anh gặp may thi đỗ, ra l&agrave;m quan, liền trở mặt. Bạn đến thăm nhiều lần, lần n&agrave;o cũng cho l&iacute;nh ra bảo, khi th&igrave; ng&agrave;i giấc, ng&agrave;i ngơi, khi th&igrave; ng&agrave;i bận việc c&ocirc;ng kh&ocirc;ng tiếp.Năm bảy phen nhứ thế, anh n&agrave;y giận lắm.</span></p>\r\n\r\n<p><span style=\"font-size:20px\">Một h&ocirc;m, anh&nbsp;ta&nbsp;mua một con lợn, quay v&agrave;ng, để l&ecirc;n m&acirc;m bưng tới. L&iacute;nh lệ v&agrave;o bẩm, một l&aacute;t trở ra, niềm nở mời v&agrave;o. V&agrave;o đến nơi, quan ch&agrave;o hỏi cồn v&atilde; rồi sai l&iacute;nh lệ mang trầu ra mời. Anh ta cầm lấy miếng trầu, đ&uacute;t v&agrave;o miệng con lợn, v&aacute;i n&oacute; mấy v&aacute;i m&agrave; rằng.<br />\r\n- Tao trả ơn m&agrave;y! Nhờ m&agrave;y tao mới lại lọt v&agrave;o đến cửa quan để nh&igrave;n lại mặt ban cũ!</span></p>', 5, 1, '2022-08-03 07:25:44', '2022-08-03 07:28:17'),
(7, 'Học trò dốt làm thơ', 'hoc-tro-dot-lam-tho', '<p>Thầy đồ dặy:</p>\r\n\r\n<p>- L&agrave;m thơ th&igrave; phải cho tự nhi&ecirc;n, tả đ&uacute;ng cảnh thực mới gọi l&agrave; hay.</p>\r\n\r\n<p>Bốn anh học tr&ograve; v&atilde;n cảnh đền nhớ lời thầy dạy, cao hứng, l&agrave;m một b&agrave;i thơ. Anh thứ nhất thấy tượng Quan C&ocirc;ng liền ứng khẩu:</p>\r\n\r\n<p>H&aacute;n Vương ăn ớt mặt đỏ gay,</p>\r\n\r\n<p>Anh thứ hai nh&igrave;n sang b&ecirc;n cạnh, thấy tượng Quan B&igrave;nh đọc lu&ocirc;n:</p>\r\n\r\n<p>B&ecirc;n kia th&aacute;i tử đ&uacute;ng khoanh tay</p>\r\n\r\n<p>Anh thứ ba thấy tượng Ch&acirc;u Xương đọc tiếp:</p>\r\n\r\n<p>Thằng mọi r&acirc;u ria cầm c&aacute;i m&aacute;c,</p>\r\n\r\n<p>Anh thứ tư thấy con hạc cỡi tr&ecirc;n lững con r&ugrave;a, mới kết rằng:</p>\r\n\r\n<p>Con cua nằm dưới ch&uacute; c&ograve; gầy.</p>\r\n\r\n<p>Về đọc thầy nghe, thầy tấm tắc khen:</p>\r\n\r\n<p>- Kh&aacute; đấy! C&aacute;c anh biết nghe lời t&ocirc;i dạy. Gắng l&ecirc;n, sang năm đi thi thể n&agrave;o cũng đỗ!</p>', 5, 0, '2022-08-03 07:27:28', '2022-08-03 07:27:28');

