INSERT INTO `products` (`id`, `name_product`, `img_product`, `slug_product`, `content_product`, `kichhoat`, `hot`, `tinhtrang`, `view_product`, `id_author`, `theloai_id`, `danhmuc_id`, `created_at`, `updated_at`) VALUES
(1, 'One Pice', '986.jpg', 'one-pice', '<p><em><strong>One Piece&nbsp;</strong></em>(ワンピース&nbsp;<em>Wan Pīsu</em><a href=\"https://vi.wikipedia.org/wiki/Tr%E1%BB%A3_gi%C3%BAp:Ti%E1%BA%BFng_Nh%E1%BA%ADt\" title=\"Trợ giúp:Tiếng Nhật\"><sup>?</sup></a>), từng được xuất bản tại Việt Nam dưới t&ecirc;n gọi&nbsp;<strong>Đảo Hải Tặc</strong>&nbsp;l&agrave; bộ&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Manga\" title=\"Manga\">manga</a>&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Sh%C5%8Dnen_manga\" title=\"Shōnen manga\">d&agrave;nh cho lứa tuổi thiếu ni&ecirc;n</a>&nbsp;của t&aacute;c giả&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Oda_Eiichiro\" title=\"Oda Eiichiro\">Oda Eiichiro</a>, được đăng định k&igrave; tr&ecirc;n tạp ch&iacute;&nbsp;<em><a href=\"https://vi.wikipedia.org/wiki/Weekly_Sh%C5%8Dnen_Jump\" title=\"Weekly Shōnen Jump\">Weekly Shōnen Jump</a></em>, ra mắt lần đầu tr&ecirc;n ấn bản số 34 v&agrave;o ng&agrave;y 19 th&aacute;ng 7 năm 1997. Bản&nbsp;<em><a href=\"https://vi.wikipedia.org/wiki/Tank%C5%8Dbon\" title=\"Tankōbon\">tankōbon</a></em>&nbsp;của truyện do&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Sh%C5%ABeisha\" title=\"Shūeisha\">Shueisha</a>&nbsp;ph&aacute;t h&agrave;nh với tập đầu ti&ecirc;n v&agrave;o ng&agrave;y 24 th&aacute;ng 12 năm 1997. One Piece kể về cuộc h&agrave;nh tr&igrave;nh của&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Monkey_D._Luffy\" title=\"Monkey D. Luffy\">Monkey D. Luffy</a>&nbsp;- thuyền trưởng của băng hải tặc Mũ Rơm v&agrave; c&aacute;c đồng đội của cậu. Luffy t&igrave;m kiếm v&ugrave;ng biển b&iacute; ẩn nơi cất giữ kho b&aacute;u lớn nhất thế giới One Piece, với mục ti&ecirc;u trở th&agrave;nh T&acirc;n Vua Hải Tặc.</p>\r\n\r\n<p>One Piece cũng được chuyển thể sang một v&agrave;i loại h&igrave;nh truyền th&ocirc;ng kh&aacute;c nhau. Một&nbsp;<a href=\"https://vi.wikipedia.org/wiki/OVA\" title=\"OVA\">OVA</a>&nbsp;được h&atilde;ng&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Production_I.G\" title=\"Production I.G\">Production I.G</a>&nbsp;sản xuất v&agrave;o năm 1998. Tiếp đ&oacute;, phi&ecirc;n bản anime d&agrave;i tập do h&atilde;ng&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Toei_Animation\" title=\"Toei Animation\">Toei Animation</a>&nbsp;thực hiện, bắt đầu l&ecirc;n s&oacute;ng truyền h&igrave;nh&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Nh%E1%BA%ADt_B%E1%BA%A3n\" title=\"Nhật Bản\">Nhật Bản</a>&nbsp;v&agrave;o năm 1999. Toei cũng sản xuất 11 phim hoạt h&igrave;nh, một OVA v&agrave; 5 chương tr&igrave;nh truyền h&igrave;nh đặc biệt li&ecirc;n quan. Một v&agrave;i c&ocirc;ng ty đ&atilde; ph&aacute;t triển nhiều sản phẩm kh&aacute;c dựa v&agrave;o truyện như thẻ game,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Video_game\" title=\"Video game\">video game</a>. Bộ manga phi&ecirc;n bản tiếng Anh được cấp ph&eacute;p cho h&atilde;ng&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=Viz_Media&amp;action=edit&amp;redlink=1\" title=\"Viz Media (trang không tồn tại)\">Viz Media</a>&nbsp;ph&aacute;t h&agrave;nh ở thị trường&nbsp;<a href=\"https://vi.wikipedia.org/wiki/B%E1%BA%AFc_M%E1%BB%B9\" title=\"Bắc Mỹ\">Bắc Mỹ</a>, h&atilde;ng&nbsp;<a href=\"https://vi.wikipedia.org/w/index.php?title=Gollancz_Manga&amp;action=edit&amp;redlink=1\" title=\"Gollancz Manga (trang không tồn tại)\">Gollancz Manga</a>&nbsp;ở&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Anh_Qu%E1%BB%91c\" title=\"Anh Quốc\">Anh Quốc</a>,&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Madman_Entertainment\" title=\"Madman Entertainment\">Madman Entertainment</a>&nbsp;ở&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Australia\" title=\"Australia\">Australia</a>&nbsp;v&agrave;&nbsp;<a href=\"https://vi.wikipedia.org/wiki/New_Zealand\" title=\"New Zealand\">New Zealand</a>. Ở Bắc Mỹ, bộ anime được cấp ph&eacute;p bản địa h&oacute;a v&agrave; ph&acirc;n phối bằng tiếng Anh bởi h&atilde;ng&nbsp;<a href=\"https://vi.wikipedia.org/wiki/Funimation_Entertainment\" title=\"Funimation Entertainment\">Funimation Entertainment</a>.</p>', '0', '0', '0', 1382, 1, 1, 1, '2022-07-30 00:33:35', '2022-10-26 22:00:16'),
(3, 'Địa ngục công ngụ', '737.jpg', 'dia-nguc-cong-ngu', '<p>Bạn đang theo d&otilde;i&nbsp;<a href=\"https://truyenchu.vn/\">truyện hay mới</a>&nbsp;Địa Ngục C&ocirc;ng Ngụ của t&aacute;c giả Hắc Sắc Hoả Chung rất hấp dẫn v&agrave; l&ocirc;i cuốn. L&agrave; một truyện được giới thiệu với bạn đọc tr&ecirc;n trang đọc truyện chữ online. Đọc truyện bạn đọc sẽ được dẫn dắt v&agrave;o một thế giới mới lạ, những t&igrave;nh tiết đặc sắc, đọc truyện Huyền Huyễn, Th&aacute;m Hiểm, Linh Dị n&agrave;y để trải nghiệm v&agrave; cảm nhận bạn nh&eacute;.</p>\r\n\r\n<p>Trở th&agrave;nh ch&acirc;n thực phim kinh dị diễn vi&ecirc;n, tại phim kinh dị thế giới b&ecirc;n trong, gi&atilde;y dụa cầu sinh!</p>\r\n\r\n<p>Tiến v&agrave;o t&ograve;a chung cư n&agrave;y, chẳng kh&aacute;c n&agrave;o bước l&ecirc;n một con đường kh&ocirc;ng c&oacute; lối về.</p>\r\n\r\n<p>Chỉ c&oacute; người bị n&oacute; chọn tr&uacute;ng, mới c&oacute; thể nh&igrave;n thấy n&oacute;, mới c&oacute; thể tiến nhập n&oacute;. M&agrave; một khi bị lựa chọn trở th&agrave;nh chung cư c&aacute;c gia đ&igrave;nh, liền kh&ocirc;ng c&ograve;n c&oacute; lựa chọn. Nếu như muốn rời khỏi chung cư, cũng chỉ c&oacute; bị n&oacute; nguyền rủa m&agrave; giết chết.</p>\r\n\r\n<p>Tại n&agrave;y trong căn hộ, một khi gian ph&ograve;ng tr&ecirc;n v&aacute;ch tường xuất hiện chữ bằng m&aacute;u, nhất định phải căn cứ chữ bằng m&aacute;u chỉ thị, đến địa điểm chỉ định, tại quy định ng&agrave;y b&ecirc;n trong chờ đủ kia đoạn ng&agrave;y, một khi vi phạm cũng giống vậy sẽ chết. M&agrave; một khi đến chỉ điểm địa điểm, liền sẽ đối mặt v&ocirc; tận linh dị khủng bố hiện tượng, v&ocirc; số &aacute;c mộng chỗ s&acirc;u u hồn quỷ mị, đem ở khắp mọi nơi, đ&ograve;i lấy t&iacute;nh mạng của ngươi.</p>\r\n\r\n<p>D&ugrave; cho c&oacute; thể sống s&oacute;t, trở lại chung cư, cũng muốn gặp phải lần tiếp theo, v&aacute;ch tường xuất hiện chữ bằng m&aacute;u thời khắc.</p>\r\n\r\n<p>M&agrave; c&oacute; thể rời khỏi t&ograve;a chung cư n&agrave;y phương ph&aacute;p, chỉ c&oacute; một. . .</p>', '0', '0', '0', 68, 2, 1, 4, '2022-07-31 08:01:07', '2022-08-03 05:10:04'),
(4, 'DẪN SÓI VÀO NHÀ', '453.jpg', 'dan-soi-vao-nha', '<p>Bạn đang theo d&otilde;i&nbsp;<a href=\"https://truyenchu.vn/\">truyeen</a>&nbsp;Dẫn S&oacute;i V&agrave;o Nh&agrave; của t&aacute;c giả V&acirc;n C&aacute;t Cẩm T&uacute; rất hấp dẫn v&agrave; l&ocirc;i cuốn. L&agrave; một truyện được giới thiệu với bạn đọc tr&ecirc;n trang đọc truyện chữ online. Đọc truyện bạn đọc sẽ được dẫn dắt v&agrave;o một thế giới mới lạ, những t&igrave;nh tiết đặc sắc, đọc truyện Ng&ocirc;n T&igrave;nh, Sắc, H&agrave;i Hước n&agrave;y để trải nghiệm v&agrave; cảm nhận bạn nh&eacute;.</p>\r\n\r\n<p><strong>Giới thiệu:</strong></p>\r\n\r\n<p><strong><em>[Ta l&agrave; văn &aacute;n văn nghệ]</em></strong></p>\r\n\r\n<p>Hạ Chi T&igrave;nh nghĩ rằng anh trai c&ocirc; l&agrave; người ph&uacute;c hắc nhất thế giới, cho đến khi c&ocirc; gặp Tống An Thần: &quot;Đừng lo, anh sẽ kh&ocirc;ng ăn thịt em, &iacute;t nhất l&agrave; kh&ocirc;ng phải b&acirc;y giờ.&quot;</p>\r\n\r\n<p>&quot;Hạ Chi T&igrave;nh, em n&oacute;i cho anh, phải c&oacute; bao nhi&ecirc;u ki&ecirc;n cường, mới d&aacute;m đối với một người nhớ m&atilde;i kh&ocirc;ng qu&ecirc;n.&quot;</p>\r\n\r\n<p><em><strong>[Ta l&agrave; văn &aacute;n h&agrave;i hước]</strong></em></p>\r\n\r\n<p>C&acirc;u chuyện về một con s&oacute;i đội lốt cừu vắt hết &oacute;c lừa n&agrave;ng thỏ trắng.</p>\r\n\r\n<p><em>Tag nam ch&iacute;nh: cao, gi&agrave;u, đẹp trai, c&ugrave;ng với IQ qu&aacute; mức dẫn đến một bụng ph&uacute;c hắc.</em></p>\r\n\r\n<p><em>Tag nữ ch&iacute;nh: (hệ thống kh&ocirc;ng nhận ra)</em></p>\r\n\r\n<p><em>Tag nội dung: đ&ocirc; thị t&igrave;nh duy&ecirc;n</em></p>\r\n\r\n<p><em>Từ kh&oacute;a t&igrave;m kiếm: nh&acirc;n vật ch&iacute;nh: Hạ Chi T&igrave;nh, Tống An Thần / vai phụ: Hạ Chi Sơ, L&acirc;m Du / Thứ kh&aacute;c: vui nhộn.</em></p>', '0', '1', '0', 53, 3, 2, 1, '2022-07-31 08:22:38', '2022-08-03 04:41:51'),
(5, 'TAM QUỐC CHI HÀN MÔN THIÊN HẠ', '282.jpg', 'tam-quoc-chi-han-mon-thien-ha', '<p>Bạn đang theo d&otilde;i&nbsp;<a href=\"https://truyenchu.vn/danh-sach/truyen-full\">dđọc truyện full</a>&nbsp;Tam Quốc Chi H&agrave;n M&ocirc;n Thi&ecirc;n Hạ của t&aacute;c giả Thi&ecirc;n Thi&ecirc;n Bất Hưu rất hấp dẫn v&agrave; l&ocirc;i cuốn. L&agrave; một truyện được giới thiệu với bạn đọc tr&ecirc;n trang đọc truyện chữ online. Đọc truyện bạn đọc sẽ được dẫn dắt v&agrave;o một thế giới mới lạ, những t&igrave;nh tiết đặc sắc, đọc truyện Qu&acirc;n Sự, Lịch Sử n&agrave;y để trải nghiệm v&agrave; cảm nhận bạn nh&eacute;.</p>\r\n\r\n<p>Trong bụng giấu kinh sử, trong ngực ẩn binh gi&aacute;p.</p>\r\n\r\n<p>Vận tr&ugrave; như Phạm L&atilde;i, quyết s&aacute;ch như Trần B&igrave;nh.</p>\r\n\r\n<p>Đ&aacute;ng tiếc th&acirc;n trước tang, Trung Nguy&ecirc;n lương đống khuynh.</p>\r\n\r\n<p>Xuy&ecirc;n qua trở th&agrave;nh Qu&aacute;ch Gia đối mặt Đ&ocirc;ng H&aacute;n vương triều sắp sụp đổ đ&iacute;ch cục diện, n&ecirc;n đi nơi n&agrave;o?</p>\r\n\r\n<p>L&agrave; bước tr&ecirc;n vận mệnh đường phụ t&aacute; một thế hệ gian h&ugrave;ng T&agrave;o Th&aacute;o?</p>\r\n\r\n<p>Vẫn l&agrave; kh&aacute;c mưu đường ra x&ocirc;ng ra một c&aacute;i thuộc loại Qu&aacute;ch Gia ch&iacute;nh m&igrave;nh đ&iacute;ch t&acirc;n thi&ecirc;n địa?</p>\r\n\r\n<p>Quần h&ugrave;ng tranh gi&agrave;nh, ki&ecirc;u h&ugrave;ng cũng khởi, ai l&agrave; thi&ecirc;n mệnh sở về?</p>\r\n\r\n<p>Thi&ecirc;n t&agrave;i Qu&aacute;ch Gia mang ngươi đi v&agrave;o tam quốc, tạo ra h&agrave;n m&ocirc;n thi&ecirc;n hạ!</p>', '0', '0', '0', 147, 4, 5, 1, '2022-07-31 17:54:57', '2022-09-29 14:01:20'),
(6, 'CHƯ THIÊN PHÚC VẬN', '115.jpg', 'chu-thien-phuc-van', '<p>Bạn đang theo d&otilde;i&nbsp;<a href=\"https://truyenchu.vn/\">truyện hay.vn</a>&nbsp;Chư Thi&ecirc;n Ph&uacute;c Vận của t&aacute;c giả Ng&atilde; Khiếu B&agrave;i V&acirc;n Chưởng rất hấp dẫn v&agrave; l&ocirc;i cuốn. L&agrave; một truyện được giới thiệu với bạn đọc tr&ecirc;n trang đọc truyện chữ online. Đọc truyện bạn đọc sẽ được dẫn dắt v&agrave;o một thế giới mới lạ, những t&igrave;nh tiết đặc sắc, đọc truyện Kiếm Hiệp, Xuy&ecirc;n Kh&ocirc;ng n&agrave;y để trải nghiệm v&agrave; cảm nhận bạn nh&eacute;.</p>\r\n\r\n<p>Con vợ lẽ của c&ocirc;ng phủ cường giả chi lộ!</p>\r\n\r\n<p>Bị hạn chế ở hậu trạch chỉ c&oacute; thể l&agrave;m tiểu trong suốt ph&aacute; như thế n&agrave;o?</p>\r\n\r\n<p>Vừa mới đến tuổi phản nghịch đ&atilde; bị dụ dỗ l&agrave;m ăn chơi tr&aacute;c t&aacute;ng ph&aacute; như thế n&agrave;o?</p>\r\n\r\n<p>Vừa mới th&agrave;nh ni&ecirc;n đ&atilde; bị cha ruột n&eacute;m tới t&aacute;i ngoại l&atilde;nh địa tự sinh tự diệt ph&aacute; như thế n&agrave;o?</p>\r\n\r\n<p>Đuổi kịp linh kh&iacute; sống lại thi&ecirc;n địa dị biến&hellip;&hellip;</p>\r\n\r\n<p>May mắn anh em c&oacute; b&agrave;n tay v&agrave;ng Ph&uacute;c Vận Bảo Th&aacute;p!</p>\r\n\r\n<p>Xuy&ecirc;n qua chư thi&ecirc;n cường đại tự th&acirc;n, hết thảy trở ngại đều l&agrave; m&acirc;y bay!</p>\r\n\r\n<p>Cấp bậc:</p>\r\n\r\n<p>Nội gia quyền: Minh K&iacute;nh - &Aacute;m K&iacute;nh - H&oacute;a K&iacute;nh - Đan K&iacute;nh - Cương K&iacute;nh...</p>\r\n\r\n<p>Ch&acirc;n kh&iacute;: Hậu Thi&ecirc;n - Ti&ecirc;n Thi&ecirc;n - T&ocirc;ng Sư - Đại T&ocirc;ng Sư - Thi&ecirc;n Nh&acirc;n - Thần Th&ocirc;ng - Nh&acirc;n Ti&ecirc;n - Địa Ti&ecirc;n - Thi&ecirc;n Ti&ecirc;n - Kim Ti&ecirc;n - Đại La Kim Ti&ecirc;n - ...</p>\r\n\r\n<p>Truyện n&agrave;y cực hay từ chương 295 trở đi, c&aacute;c đồng hữu c&oacute; thể đọc từ chương 295.</p>', '0', '0', '0', 351, 1, 5, 4, '2022-07-31 17:58:02', '2022-08-05 09:20:10'),
(7, 'Nữ Vương Đồng Nát Ở Tinh Tế', '98.jpg', 'nu-vuong-dong-nat-o-tinh-te', '<p>Bạn đang theo d&otilde;i&nbsp;<a href=\"https://truyenchu.vn/\">trueyn</a>&nbsp;Nữ Vương Đồng N&aacute;t Ở Tinh Tế của t&aacute;c giả Liễu Thăng Thăng rất hấp dẫn v&agrave; l&ocirc;i cuốn. L&agrave; một truyện được giới thiệu với bạn đọc tr&ecirc;n trang đọc truyện chữ online. Đọc truyện bạn đọc sẽ được dẫn dắt v&agrave;o một thế giới mới lạ, những t&igrave;nh tiết đặc sắc, đọc truyện Ng&ocirc;n T&igrave;nh, Qu&acirc;n Sự, Xuy&ecirc;n Kh&ocirc;ng, Sắc, Nữ Cường, H&agrave;i Hước, Kh&aacute;c n&agrave;y để trải nghiệm v&agrave; cảm nhận bạn nh&eacute;.</p>\r\n\r\n<p><strong>T&aacute;c giả:&nbsp;</strong>Liễu Thăng Thăng</p>\r\n\r\n<p><strong>Thể loại:&nbsp;</strong>Qu&acirc;n Sự, Xuy&ecirc;n Kh&ocirc;ng, Truyện Nữ Cường, Ng&ocirc;n T&igrave;nh, H&agrave;i Hước, Truyện Kh&aacute;c</p>\r\n\r\n<p><strong>Giới thiệu:</strong></p>\r\n\r\n<p>Xuy&ecirc;n đến thời đại tinh tế tr&ecirc;n một tinh cầu r&aacute;c, Qu&yacute; Dữu ngh&egrave;o đến mức sắp đ&agrave;o đất để ăn, nhưng&hellip;&hellip; Đất ở đ&acirc;y c&oacute; độc kh&ocirc;ng ăn được&hellip;&hellip; Kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o, c&ocirc; chỉ c&oacute; thể hạ m&igrave;nh đi nhặt r&aacute;c, b&aacute;n đồ n&aacute;t&hellip;&hellip;</p>\r\n\r\n<p>Hạt Thảo Quả của chị đ&acirc;y xinh đẹp như thế m&agrave; kh&ocirc;ng ai th&egrave;m, nhặt về xe hạt treo l&ecirc;n mạng b&aacute;n th&ocirc;i.</p>\r\n\r\n<p>Nhưng treo l&ecirc;n mạng xong th&igrave; c&oacute; người nhận x&eacute;t ch&ecirc; bai: &ldquo;Ngay cả hạt Thảo Quả cũng mang đi b&aacute;n, th&egrave;m tiền đến mức đi&ecirc;n rồi hả?&rdquo;</p>\r\n\r\n<p>Mấy ng&agrave;y sau, người n&agrave;y hối hận muốn đấm tường: &ldquo;Cho t&ocirc;i xin một hạt Thảo Quả đi, một hạt th&ocirc;i! Một hạt l&agrave; tốt rồi.&rdquo;</p>\r\n\r\n<p>Qu&yacute; Dữu lạnh l&ugrave;ng n&oacute;i: &ldquo;Kh&ocirc;ng b&aacute;n!&rdquo;</p>\r\n\r\n<p>V&iacute; dụ tiếp theo n&agrave;o, cục đ&aacute; vụn đầy tr&ecirc;n đường s&aacute;ng lấp l&aacute;nh vậy m&agrave; kh&ocirc;ng ai muốn, nhặt l&ecirc;n đ&aacute;nh th&agrave;nh v&ograve;ng tay treo l&ecirc;n mạng b&aacute;n th&ocirc;i.&rdquo;</p>\r\n\r\n<p>Mọi người &aacute;c mồm &aacute;c miệng: &ldquo;Cục đ&aacute; n&aacute;t n&agrave;y c&ograve;n lấy ra b&aacute;n nữa, chưa từng thấy tiền bao giờ hả?&rdquo;</p>\r\n\r\n<p>Mấy ng&agrave;y sau, mọi người kh&oacute;c l&oacute;c thảm thiết n&oacute;i: &ldquo;L&agrave; t&ocirc;i c&oacute; mắt kh&ocirc;ng tr&ograve;ng, l&agrave; mắt t&ocirc;i vụng về, cục đ&aacute; xinh đẹp như vậy xin b&aacute;n cho t&ocirc;i một c&aacute;i đi.&rdquo;</p>\r\n\r\n<p>Qu&yacute; Dữu bu&ocirc;ng tay: &ldquo;Kh&ocirc;ng c&oacute;.&rdquo;</p>\r\n\r\n<p>V&iacute; dụ như&hellip;&hellip;</p>\r\n\r\n<p>Cậu thiếu ni&ecirc;n xinh đẹp giống như thần ti&ecirc;n n&agrave;y lại l&agrave; t&ecirc;n Th&aacute;i Tử phế vật m&agrave; trong miệng người d&acirc;n đế quốc người người k&ecirc;u đ&aacute;nh, người người phỉ nhổ sao? Thiếu ni&ecirc;n xinh đẹp như vậy thế m&agrave; sắp chết đến nơi rồi, nhặt về trước đ&atilde;, kh&ocirc;ng chết th&igrave; coi như gi&agrave;n hoa b&agrave;y cho đẹp mắt cũng được.</p>\r\n\r\n<p>Nhưng ---</p>\r\n\r\n<p>T&ecirc;n n&agrave;y cười xấu xa: &ldquo;Gi&agrave;n hoa? Tối h&ocirc;m qua anh kh&ocirc;ng nỗ lực đủ sao? Em x&aacute;c định anh chỉ l&agrave; gi&agrave;n hoa th&ocirc;i hả?&rdquo;</p>', '0', '1', '0', 567, 1, 2, 1, '2022-07-31 18:00:52', '2022-08-22 13:03:08'),
(8, 'TRỌNG SINH NGỘ KHÔNG TU YÊU LỤC', '609.jpg', 'trong-sinh-ngo-khong-tu-yeu-luc', '<p>Chắc hẳn ai đọc truyện n&agrave;y cũng đ&atilde; từng đọc qua Chấp Ma ( Hợp Thể Song Tu) của l&atilde;o Mặc &iacute;t nhất một lần, t&aacute;c phẩm n&agrave;y được l&atilde;o viết trước Chấp Ma n&ecirc;n b&uacute;t lực chưa cao th&acirc;m lắm, truyện kể về T&ocirc;n Thiệu - Tử Đấu Ti&ecirc;n Ho&agrave;ng trọng sinh T&acirc;y Du thế giới một đường tu ch&acirc;n đến ho&agrave;n th&agrave;nh tỉnh mộng kiếp chưởng khống T&acirc;y Du thế giới....</p>\r\n\r\n<p>Lời dẫn: &quot; Kh&ocirc;ng tưởng T&acirc;y Du thế giới, T&ocirc;n Thiệu trọng sinh th&agrave;nh Ngộ Kh&ocirc;ng, nhưng kh&ocirc;ng chỉ l&agrave; Ngộ Kh&ocirc;ng. Ở trước Phật dập đầu một c&aacute;i, mang l&ecirc;n Kim C&ocirc;, giải quyết xong qu&aacute; khứ, vẫn l&agrave; đi theo L&atilde;o Qu&acirc;n, l&agrave;m c&aacute;i đồng tử, luyện đan tu đạo?</p>\r\n\r\n<p>Trời sinh thạch hầu, Ti&ecirc;n Thi&ecirc;n đạo thể. Hồng M&ocirc;ng sơ khai, ta l&agrave;m ngoan thạch. Th&aacute;nh Nh&acirc;n t&iacute;nh hết Thi&ecirc;n Đạo, nhưng to&aacute;n kh&ocirc;ng ra T&ocirc;n Thiệu vận mệnh.</p>\r\n\r\n<p>Bốn mươi năm, hắn tr&ugrave;ng tu đến nguy&ecirc;n t&aacute;c tu vi. Năm mươi năm, hắn đạp ph&aacute; thi&ecirc;n địa lu&acirc;n hồi, trở th&agrave;nh vạn cổ thứ nhất Y&ecirc;u Đế, thống lĩnh bầy y&ecirc;u.</p>\r\n\r\n<p>Hắn ph&aacute; n&aacute;t V&acirc;n Trung Tử tam hoa tụ đỉnh, trảm qua Dược Sư Phật phật ảnh, khinh nhờn Vương Mẫu, c&ugrave;ng Nữ Oa h&ograve; h&eacute;t.</p>\r\n\r\n<p>Như Lai, Ngọc đế, ai c&oacute; thể r&agrave;ng buộc! Tam Giới, lục đạo, ai c&oacute; thể trở ngại! N&agrave;y T&acirc;y Du thế giới, nhất định phải bị T&ocirc;n Thiệu một c&ocirc;n đảo loạn! Vạn Cổ thanh thi&ecirc;n ai l&agrave;m Đế, bằng v&agrave;o ta chỉ tay to&aacute;i Thi&ecirc;n Đ&igrave;nh! &quot;</p>', '0', '1', '0', 1238, 1, 1, 4, '2022-07-31 18:05:06', '2022-08-03 07:44:51'),
(9, 'TRẤN NHỎ', '15.jpg', 'tran-nho', '<p>Bạn đang theo d&otilde;i&nbsp;<a href=\"https://truyenchu.vn/danh-sach/truyen-full\">truyen full bo</a>&nbsp;Trấn Nhỏ của t&aacute;c giả D&atilde; Thỏ Địch Khả rất hấp dẫn v&agrave; l&ocirc;i cuốn. L&agrave; một truyện được giới thiệu với bạn đọc tr&ecirc;n trang đọc truyện chữ online. Đọc truyện bạn đọc sẽ được dẫn dắt v&agrave;o một thế giới mới lạ, những t&igrave;nh tiết đặc sắc, đọc truyện Dị Năng, Trinh Th&aacute;m, Sắc, Đam Mỹ, Phương T&acirc;y n&agrave;y để trải nghiệm v&agrave; cảm nhận bạn nh&eacute;.</p>\r\n\r\n<p>T&ecirc;n gốc: 小镇 &ndash; Tiểu trấn</p>\r\n\r\n<p>Edit+Beta: LH</p>\r\n\r\n<p>T&igrave;nh trạng bản gốc: Ho&agrave;n 63 chương + PN (170609)</p>\r\n\r\n<p>Thể loại: Hiện đại, Trinh th&aacute;m, Hồi hộp &ndash; Gay cấn, Dị năng, Cường cường, Hỗ*, Thanh thủy, 1&times;1, &Acirc;u phong</p>\r\n\r\n<p>&mdash;&mdash;&mdash;</p>\r\n\r\n<p>Văn &aacute;n:</p>\r\n\r\n<p>T&ocirc;i thật l&ograve;ng n&oacute;i cho c&aacute;c người: những g&igrave; t&ocirc;i l&agrave;m, người tin v&agrave;o t&ocirc;i cũng sẽ l&agrave;m điều đ&oacute;.</p>\r\n\r\n<p>- Ph&uacute;c &acirc;m John 14-12</p>\r\n\r\n<p>&mdash;&mdash;&mdash;</p>\r\n\r\n<p>Kh&aacute;i qu&aacute;t: M&ocirc; t&iacute;p Mystery-Thriller kinh điển: Một anh đẹp giai tr&ecirc;n đường du lịch bị lạc v&agrave;o một nơi k&igrave; qu&aacute;i gặp phải biết bao chuyện quỷ dị kh&ocirc;ng hiểu g&igrave; hết quyết t&acirc;m đeo đuổi t&igrave;m ra sự thật ch&acirc;n ch&iacute;nh xuy&ecirc;n suốt qu&aacute; tr&igrave;nh v&ocirc; t&igrave;nh vớt được một anh giai đẹp kh&aacute;c.</p>\r\n\r\n<p>&mdash;&mdash;&mdash;</p>\r\n\r\n<p>*Hỗ l&agrave; hỗ sủng lẫn hỗ c&ocirc;ng. Truyện tuy thanh thủy văn nhưng nếu x&eacute;t kĩ 2 đoạn H k&eacute;o r&egrave;m trong truyện vẫn c&oacute; thể nhận thấy được đ&acirc;y l&agrave; hỗ c&ocirc;ng. Nếu bạn kh&ocirc;ng th&iacute;ch thể loại n&agrave;y th&igrave; bỏ qua, h&atilde;y t&ocirc;n trọng sở th&iacute;ch của người kh&aacute;c v&agrave; đừng g&acirc;y mếch l&ograve;ng nhau.</p>\r\n\r\n<p>LH: Thật ra hồi đ&oacute; m&igrave;nh cũng kh&ocirc;ng đọc được hỗ c&ocirc;ng đ&acirc;u nhưng lại nh&igrave;n tr&uacute;ng bộ n&agrave;y, giờ m&igrave;nh đọc ngon l&agrave;nh. L&uacute;c đầu đọc sơ qua m&igrave;nh cũng kh&ocirc;ng biết, edit kĩ mới ph&aacute;t hiện. D đại tả rất ẩn dụ, kh&ocirc;ng biết c&oacute; phải c&oacute; ẩn &yacute; hay kh&ocirc;ng, m&igrave;nh l&ecirc;n trang t&aacute;c giả đăng tải truyện th&igrave; kh&ocirc;ng thấy c&oacute; tag. N&oacute;i t&oacute;m lại nếu trước giờ chưa đọc hỗ c&ocirc;ng bạn c&oacute; thể thử với bộ n&agrave;y, biết đ&acirc;u bạn sẽ th&iacute;ch, c&ograve;n kh&ocirc;ng cũng đừng b&igrave;nh luận kh&ocirc;ng hay g&acirc;y mất l&ograve;ng nhau.</p>', '0', '1', '0', 12, 1, 1, 4, '2022-08-03 07:39:07', '2022-08-03 07:41:15'),
(10, 'TA LÀ ĐẠI NGƯỜI CHƠI', '847.jpg', 'ta-la-dai-nguoi-choi', '<p>Bạn đang theo d&otilde;i&nbsp;<a href=\"https://truyenchu.vn/danh-sach/truyen-full\">truyện full mới nhất</a>&nbsp;Ta L&agrave; Đại Người Chơi của t&aacute;c giả Hội Thuyết Thoại Trửu Tử rất hấp dẫn v&agrave; l&ocirc;i cuốn. L&agrave; một truyện được giới thiệu với bạn đọc tr&ecirc;n trang đọc truyện chữ online. Đọc truyện bạn đọc sẽ được dẫn dắt v&agrave;o một thế giới mới lạ, những t&igrave;nh tiết đặc sắc, đọc truyện V&otilde;ng Du, Xuy&ecirc;n Kh&ocirc;ng n&agrave;y để trải nghiệm v&agrave; cảm nhận bạn nh&eacute;.</p>\r\n\r\n<p>Nhậm H&ograve;a xuy&ecirc;n qua thế giới song song dự định lấy viết s&aacute;ch l&agrave;m gi&agrave;u ph&aacute;t t&agrave;i, nhưng m&agrave; bỗng nhi&ecirc;n xuất hiện c&aacute;i Thi&ecirc;n Phạt hệ thống n&oacute;i cho hắn, ph&agrave;m l&agrave; lợi dụng kiếp trước vui chơi giải tr&iacute; kinh nghiệm kiếm tiền, đều phải trước l&agrave;m c&aacute;i liều mạng nhiệm vụ!</p>\r\n\r\n<p>Muốn viết ca, c&oacute; thể, l&agrave;m c&aacute;i nhiệm vụ trước.</p>\r\n\r\n<p>Muốn ch&eacute;p s&aacute;ch, c&oacute; thể, l&agrave;m c&aacute;i nhiệm vụ trước.</p>\r\n\r\n<p>Nhiệm vụ kh&ocirc;ng ho&agrave;n th&agrave;nh, hoặc l&agrave; bệnh liệt dương 100 ng&agrave;y, hoặc l&agrave; JJ biến ngắn 1 centimet!</p>\r\n\r\n<p>Nhậm H&ograve;a đều m&ecirc;, bằng c&aacute;i g&igrave; người kh&aacute;c xuy&ecirc;n qua đều l&agrave; b&agrave;n tay v&agrave;ng, chỉ ta bị Thi&ecirc;n Phạt hệ thống nh&igrave;n chằm chằm?</p>\r\n\r\n<p>Ngươi n&oacute;i r&otilde; cho ta!</p>', '0', '0', '0', 444, 1, 5, 4, '2022-08-03 07:43:07', '2022-08-03 07:43:07');

