--
-- Đang đổ dữ liệu cho bảng `sessions`
--

INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(2, '1', 'Bắt đầu liền là Thiên Long Nhân?', '<p>Muốn n&oacute;i thế giới mới nơi n&agrave;y, đ&acirc;y ch&iacute;nh l&agrave; to&agrave;n bộ Grand Line. . . Kh&ocirc;ng, n&ecirc;n n&oacute;i l&agrave; to&agrave;n bộ thế giới b&ecirc;n trong nguy hiểm nhất hải vực, nơi n&agrave;y bi&ecirc;̉n cả t&aacute;o bạo kh&ocirc;ng th&ocirc;i, mỗi ng&agrave;y đều tại diễn ra kh&aacute;c biệt kỳ cảnh, đi thuyền tại v&ugrave;ng biển n&agrave;y b&ecirc;n trong, v&ocirc; luận l&agrave; lợi hại cỡ n&agrave;o l&atilde;o thủ, đều t&ugrave;y thời có khả năng mất mạng, d&ugrave; sao nơi n&agrave;y hết thảy, đều ph&aacute; vỡ lấy tất cả h&agrave;ng hải gia m&ocirc;n nhận biết m&agrave;.<br />\r\nV&ocirc; luận l&agrave; bay ở tr&ecirc;n bầu trời h&ograve;n đảo, vẫn l&agrave; đột nhi&ecirc;n vỡ ra mặt biển, lại hoặc l&agrave; tinh kh&ocirc;ng ph&iacute;a dưới, đột nhi&ecirc;n rơi ra so đội thuyền c&ograve;n muốn lớn mưa đ&aacute;, t&oacute;m lại l&agrave; chỉ c&oacute; ngươi kh&ocirc;ng nghĩ tới, kh&ocirc;ng c&oacute; c&aacute;i mới thế giới l&agrave;m kh&ocirc;ng được, cho d&ugrave; l&agrave; mặt biển đột nhi&ecirc;n biến th&agrave;nh th&ocirc;n phệ hết thảy cực n&oacute;ng nham tương, tại thế giới mới, cũng l&agrave; phi thường b&igrave;nh thường sự t&igrave;nh.<br />\r\nM&agrave; bi&ecirc;̉n cả l&agrave; c&ocirc;ng bằng, cũng nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave; như thế, bi&ecirc;̉n cả l&agrave; v&ocirc; t&igrave;nh, khỏi phải kh&ocirc;ng cần biết ngươi l&agrave; c&aacute;i g&igrave; th&acirc;n phận, Hải tặc cũng tốt, Hải qu&acirc;n cũng được, hay l&agrave; c&aacute;i g&igrave; nh&agrave; mạo hiểm, qu&yacute; tộc, thương đội, những n&agrave;y cũng kh&ocirc;ng đ&aacute;ng kể, tại đại trong Hải nh&atilde;n, tất cả mọi người th&acirc;n phận đều l&agrave; b&igrave;nh đẳng, c&aacute;i kia ch&iacute;nh l&agrave; con d&acirc;n của n&oacute;.<br />\r\nB&acirc;y giờ tại thế giới mới m&ocirc;n hộ địa khu hải vực, c&aacute;i n&agrave;o đ&oacute; kh&ocirc;ng biết t&ecirc;n tr&ecirc;n đảo nhỏ, liền c&oacute; một c&aacute;i tại gặp nạn về sau, cẩu thả c&ograve;n sống gia hỏa.</p>', '<p>Muốn n&oacute;i thế giới mới nơi n&agrave;y, đ&acirc;y ch&iacute;nh l&agrave; to&agrave;n bộ Grand Line. . . Kh&ocirc;ng, n&ecirc;n n&oacute;i l&agrave; to&agrave;n bộ thế giới b&ecirc;n trong nguy hiểm nhất hải vực, nơi n&agrave;y bi&ecirc;̉n cả t&aacute;o bạo kh&ocirc;ng th&ocirc;i, mỗi ng&agrave;y đều tại diễn ra kh&aacute;c biệt kỳ cảnh, đi thuyền tại v&ugrave;ng biển n&agrave;y b&ecirc;n trong, v&ocirc; luận l&agrave; lợi hại cỡ n&agrave;o l&atilde;o thủ, đều t&ugrave;y thời có khả năng mất mạng, d&ugrave; sao nơi n&agrave;y hết thảy, đều ph&aacute; vỡ lấy tất cả h&agrave;ng hải gia m&ocirc;n nhận biết m&agrave;.<br />\r\nV&ocirc; luận l&agrave; bay ở tr&ecirc;n bầu trời h&ograve;n đảo, vẫn l&agrave; đột nhi&ecirc;n vỡ ra mặt biển, lại hoặc l&agrave; tinh kh&ocirc;ng ph&iacute;a dưới, đột nhi&ecirc;n rơi ra so đội thuyền c&ograve;n muốn lớn mưa đ&aacute;, t&oacute;m lại l&agrave; chỉ c&oacute; ngươi kh&ocirc;ng nghĩ tới, kh&ocirc;ng c&oacute; c&aacute;i mới thế giới l&agrave;m kh&ocirc;ng được, cho d&ugrave; l&agrave; mặt biển đột nhi&ecirc;n biến th&agrave;nh th&ocirc;n phệ hết thảy cực n&oacute;ng nham tương, tại thế giới mới, cũng l&agrave; phi thường b&igrave;nh thường sự t&igrave;nh.<br />\r\nM&agrave; bi&ecirc;̉n cả l&agrave; c&ocirc;ng bằng, cũng nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave; như thế, bi&ecirc;̉n cả l&agrave; v&ocirc; t&igrave;nh, khỏi phải kh&ocirc;ng cần biết ngươi l&agrave; c&aacute;i g&igrave; th&acirc;n phận, Hải tặc cũng tốt, Hải qu&acirc;n cũng được, hay l&agrave; c&aacute;i g&igrave; nh&agrave; mạo hiểm, qu&yacute; tộc, thương đội, những n&agrave;y cũng kh&ocirc;ng đ&aacute;ng kể, tại đại trong Hải nh&atilde;n, tất cả mọi người th&acirc;n phận đều l&agrave; b&igrave;nh đẳng, c&aacute;i kia ch&iacute;nh l&agrave; con d&acirc;n của n&oacute;.<br />\r\nB&acirc;y giờ tại thế giới mới m&ocirc;n hộ địa khu hải vực, c&aacute;i n&agrave;o đ&oacute; kh&ocirc;ng biết t&ecirc;n tr&ecirc;n đảo nhỏ, liền c&oacute; một c&aacute;i tại gặp nạn về sau, cẩu thả c&ograve;n sống gia hỏa.<br />\r\nMuốn n&oacute;i sống tạm, ngược lại cũng kh&ocirc;ng phải thật đ&iacute;ch vận may sống tiếp được, x&aacute;c thực n&oacute;i, hẳn l&agrave; gia hỏa n&agrave;y chết một lần, sau đ&oacute; bị một c&aacute;i đến từ thế giới kh&aacute;c linh hồn chiếm lấy rồi th&acirc;n thể, mở ra ho&agrave;n to&agrave;n mới nh&acirc;n sinh lữ tr&igrave;nh?<br />\r\nL&uacute;c n&agrave;y ở c&aacute;i n&agrave;y kh&ocirc;ng biết t&ecirc;n h&ograve;n đảo b&ecirc;n bờ biển b&ecirc;n tr&ecirc;n, c&oacute; một c&aacute;i nh&igrave;n qua mười mấy tuổi lớn nhỏ, th&acirc;n rộng thể b&eacute;o, quần &aacute;o r&aacute;ch rưới, lại c&oacute; thể nh&igrave;n ra nguy&ecirc;n bản hoa lệ, c&oacute; một đầu nhạt m&aacute;i t&oacute;c d&agrave;i v&agrave;ng &oacute;ng, diện mục c&oacute; vẻ hơi ương ngạnh, l&agrave;n da c&oacute; ch&uacute;t bệnh trạng trắng n&otilde;n cảm gi&aacute;c tiểu b&agrave;n đ&ocirc;n.<br />\r\nPh&oacute;ng tầm mắt nh&igrave;n, phảng phất l&agrave; c&aacute; nh&acirc;n đều c&oacute; thể nh&igrave;n ra đến tiểu gia hỏa n&agrave;y xuất th&acirc;n bất ph&agrave;m, c&oacute; thể l&agrave; c&aacute;i tứ chi kh&ocirc;ng cần, ngũ cốc kh&ocirc;ng ph&acirc;n, ngoại trừ sẽ ngồi ăn rồi chờ chết b&ecirc;n ngo&agrave;i, c&ograve;n lại c&aacute;i g&igrave; cũng đều kh&ocirc;ng hiểu ngu ngốc qu&yacute; tộc.<br />\r\nTr&ecirc;n thực tế, cũng quả thật l&agrave; như thế, c&aacute;i n&agrave;y tiểu b&agrave;n đ&ocirc;n th&acirc;n phận địa vị cực lớn, muốn n&oacute;i tr&ecirc;n thế giới n&agrave;y c&oacute; c&aacute;i g&igrave; người &aacute;p đảo cao hơn hết, từ xuất sinh đến tử vong, đều hưởng thụ lấy thế gian vốn c&oacute; tất cả mỹ hảo, nắm giữ lấy kh&ocirc;ng c&oacute; g&igrave; s&aacute;nh kịp quyền lực?<br />\r\nNgươi khoan h&atilde;y n&oacute;i, c&aacute;i n&agrave;y đ&aacute;ng chết thế giới thật c&oacute;, c&aacute;i kia ch&iacute;nh l&agrave; được xưng l&agrave; &#39;Thế giới qu&yacute; tộc&#39; Thi&ecirc;n Long Nh&acirc;n.<br />\r\nN&oacute;i l&ecirc;n thế giới qu&yacute; tộc, vậy liền kh&ocirc;ng thể kh&ocirc;ng nhấc l&ecirc;n một đoạn t&aacute;m trăm năm trước chuyện cũ, tương truyền t&aacute;m trăm năm trước, c&oacute; hai mươi c&aacute;i quốc gia Vương tộc v&igrave; kiến lập thế giới trật tự mới, từ đ&oacute; g&acirc;y dựng một c&aacute;i ho&agrave;n to&agrave;n mới ch&iacute;nh quyền, xưng l&agrave; &#39;Ch&iacute;nh Phủ Thế Giới&#39;, m&agrave; c&aacute;i n&agrave;y Ch&iacute;nh Phủ Thế Giới, b&acirc;y giờ cũng đ&atilde; thống trị to&agrave;n bộ thế giới c&oacute; t&aacute;m trăm năm l&acirc;u.<br />\r\nTrong l&uacute;c n&agrave;y, c&aacute;i kia đ&atilde; từng qu&acirc;n l&acirc;m thi&ecirc;n hạ hai mươi vị Vương hậu duệ, bị thế gian mang theo &#39;Tạo vật chủ hậu duệ&#39;, &#39;Thần hậu duệ&#39; loại h&igrave;nh đủ loại vĩ đại xưng h&agrave;o.<br />\r\nNhững n&agrave;y hậu duệ, được gọi chung l&agrave; &#39;Thế giới qu&yacute; tộc &middot; Thi&ecirc;n Long Nh&acirc;n&#39;, Thi&ecirc;n Long Nh&acirc;n tồn tại, liền l&agrave; Ch&iacute;nh Phủ Thế Giới tồn tại ph&aacute;p l&yacute; t&iacute;nh đại biểu, d&ugrave; sao Ch&iacute;nh Phủ Thế Giới l&agrave; từ Thi&ecirc;n Long Nh&acirc;n c&aacute;c vị tổ ti&ecirc;n chỗ x&acirc;y dựng m&agrave;.<br />\r\nLại th&ecirc;m cho tới nay, Ch&iacute;nh Phủ Thế Giới tầng cao nhất đều l&agrave; từ lợi hại Thi&ecirc;n Long Nh&acirc;n nắm giữ, bởi vậy c&aacute;i n&agrave;y t&aacute;m trăm năm đến, Thi&ecirc;n Long Nh&acirc;n giai tầng cũng đang kh&ocirc;ng ngừng &#39;Thăng hoa&#39;, b&acirc;y giờ đến xem, bọn hắn được hưởng lấy người b&igrave;nh thường vĩnh viễn cũng v&ocirc; ph&aacute;p với tới đặc th&ugrave; quyền lực c&ugrave;ng ưu đ&atilde;i.<br />\r\nĐ&aacute;nh c&aacute;i đơn giản nhất so s&aacute;nh tới n&oacute;i đi, Hải qu&acirc;n Đại tướng, đ&acirc;y ch&iacute;nh l&agrave; Hải qu&acirc;n b&ecirc;n trong gần với Nguy&ecirc;n So&aacute;i chức cao, ph&oacute;ng nh&atilde;n to&agrave;n bộ thế giới, cũng l&agrave; thuộc về c&oacute; th&acirc;n phận, c&oacute; địa vị, c&oacute; quyền lực người, l&agrave; thuộc về loại kia ra lệnh một tiếng, c&oacute; thể đem một t&ograve;a đảo đều ph&aacute;n v&igrave; &#39;Tội &aacute;c&#39;, d&ugrave;ng Lệnh triệu tập bất thường (Buster Call) triệt để x&oacute;a đi tồn tại.<br />\r\nThực lực bản th&acirc;n cũng l&agrave; thuộc về c&aacute;i thế giới n&agrave;y nhất lưu, kh&ocirc;ng n&oacute;i l&agrave; c&aacute;i g&igrave; mạnh nhất, nhưng &iacute;t ra cũng l&agrave; tại &#39;Mạnh nhất đ&aacute;m người kia b&ecirc;n trong&#39;.<br />\r\nM&agrave; như vậy a một c&aacute;i mặc kệ h&agrave;nh tẩu ở nơi n&agrave;o đều được xưng tụng l&agrave; uy phong b&aacute;t diện nh&acirc;n vật, tại Thi&ecirc;n Long Nh&acirc;n nơi n&agrave;y, liền th&agrave;nh c&aacute;i &#39;Bảo ti&ecirc;u&#39;.<br />\r\nKh&ocirc;ng sai, lấy Thi&ecirc;n Long Nh&acirc;n th&acirc;n phận cao qu&yacute;, v&ocirc; luận l&agrave; c&aacute;i kia xưng h&ugrave;ng bi&ecirc;̉n cả Hải qu&acirc;n tổng bộ vẫn l&agrave; c&aacute;i kia thần thần b&iacute; b&iacute; CP cơ quan, đều chẳng qua l&agrave; m&igrave;nh &#39;T&ocirc;i tớ&#39; m&agrave; th&ocirc;i, một khi Thi&ecirc;n Long Nh&acirc;n nhận lấy mạo phạm, như vậy Hải qu&acirc;n tổng bộ c&ugrave;ng CP cơ quan nhất định phải lấy nhất cao quy c&aacute;ch cấp độ đi giữ g&igrave;n Thi&ecirc;n Long Nh&acirc;n t&ocirc;n qu&yacute;.<br />\r\nN&oacute;i c&aacute;ch kh&aacute;c, một khi Thi&ecirc;n Long Nh&acirc;n náo đ&atilde; xảy ra chuyện g&igrave; đến, Hải qu&acirc;n Đại tướng loại cao thủ cấp bậc n&agrave;y, đều muốn đ&iacute;ch th&acirc;n đi ra cho Thi&ecirc;n Long Nh&acirc;n thu thập cục diện rối rắm.<br />\r\nC&aacute;i thế giới n&agrave;y, liền l&agrave; như thế kh&ocirc;ng giảng đạo l&yacute;, cũng ch&iacute;nh l&agrave; bởi v&igrave; loại n&agrave;y kh&ocirc;ng giảng đạo l&yacute;, để Thi&ecirc;n Long Nh&acirc;n giai tầng tại c&aacute;i n&agrave;y t&aacute;m trăm năm ở giữa, kh&ocirc;ng ngừng &#39;Thăng hoa&#39; bản th&acirc;n, đem thế gian hết thảy đều kh&ocirc;ng để v&agrave;o mắt, trong mắt chỉ c&oacute; c&ugrave;ng thuộc về Thi&ecirc;n Long Nh&acirc;n người một nh&agrave;, c&ograve;n lại tồn tại, đều l&agrave; &#39;N&ocirc; lệ&#39; th&ocirc;i.<br />\r\nThậm ch&iacute; bởi v&igrave; tự cho m&igrave;nh cao qu&yacute;, m&agrave; lấy ra ch&uacute;t bong b&oacute;ng che đầu, nguy&ecirc;n nh&acirc;n liền l&agrave; kh&ocirc;ng muốn c&ugrave;ng đồng dạng &#39;D&acirc;n đen&#39; nh&oacute;m h&ocirc; hấp đồng dạng kh&ocirc;ng kh&iacute;, mặc d&ugrave; lộ ra buồn cười như vậy c&ugrave;ng nực cười, nhưng cũng từ kh&iacute;a cạnh hiển lộ r&otilde; r&agrave;ng Thi&ecirc;n Long Nh&acirc;n ngạo mạn c&ugrave;ng v&ocirc; lễ.<br />\r\nM&agrave; l&uacute;c n&agrave;y c&aacute;i n&agrave;y lưu lạc tại v&ocirc; danh tr&ecirc;n hoang đảo tiểu b&agrave;n đ&ocirc;n, ch&iacute;nh l&agrave; một t&ecirc;n th&acirc;n phận cực kỳ t&ocirc;n qu&yacute; Thi&ecirc;n Long Nh&acirc;n.<br />\r\n&quot;Donquixote &middot; Claudius. . . Danh tự c&ograve;n l&ecirc;n rất b&aacute; đạo, liền l&agrave; người đơn giản c&oacute; ch&uacute;t xuẩn đến qu&aacute; phận đi? Bất qu&aacute; ngẫm lại cũng thế, những n&agrave;y đ&acirc;̀y n&atilde;o ruột gi&agrave; hạ vị Thi&ecirc;n Long Nh&acirc;n nh&oacute;m kh&ocirc;ng đều l&agrave; c&aacute;i dạng n&agrave;y sao?&quot; Tiểu b&agrave;n đ&ocirc;n l&uacute;c n&agrave;y ngồi tại c&aacute;i n&agrave;y bờ biển n&agrave;o đ&oacute; tảng đ&aacute; b&ecirc;n tr&ecirc;n, kh&ocirc;ng ngừng đậu đen rau muống lấy m&igrave;nh tiền th&acirc;n.<br />\r\nDonquixote liền kh&ocirc;ng cần n&oacute;i nhiều, đường đường chính chính Thi&ecirc;n Long Nh&acirc;n thế gia, t&aacute;m trăm năm trước, Dressrosa Vương tộc, về sau ti&ecirc;n tổ g&acirc;y dựng Ch&iacute;nh Phủ Thế Giới, từ đ&oacute; để Donquixote gia tộc từ Dressrosa di cư đến Th&aacute;nh Địa &middot; Marijoa, trở th&agrave;nh b&acirc;y giờ mười ch&iacute;n c&aacute;i Thi&ecirc;n Long Nh&acirc;n trong gia tộc một th&agrave;nh vi&ecirc;n.<br />\r\nKh&ocirc;ng sai, liền l&agrave; mười ch&iacute;n c&aacute;i, d&ugrave; sao c&aacute;i kia năm đ&oacute; hai mươi vị Vương hậu duệ thứ nhất. . . Alabasta Nefertari Vương tộc cũng kh&ocirc;ng c&oacute; di cư Th&aacute;nh Địa &middot; Marijoa, cho n&ecirc;n cũng kh&ocirc;ng c&oacute; trở th&agrave;nh Thi&ecirc;n Long Nh&acirc;n, ngược lại l&agrave; đi qua tám trăm năm thời gian thay đổi, biến th&agrave;nh Thi&ecirc;n Long Nh&acirc;n nh&oacute;m trong mắt &#39;Thổ b&aacute; vương&#39; .<br />\r\nBất qu&aacute; mặc d&ugrave; l&agrave; như thế, nhưng l&agrave; Nefertari Vương tộc bao nhi&ecirc;u tại Th&aacute;nh Địa &middot; Marijoa c&ugrave;ng Ch&iacute;nh Phủ Thế Giới b&ecirc;n n&agrave;y c&oacute; ch&uacute;t bề mặt ở, nhưng đ&acirc;y đều l&agrave; n&oacute;i sau, liền kh&ocirc;ng cần n&oacute;i th&ecirc;m c&aacute;i g&igrave;.<br />\r\nT&oacute;m lại c&aacute;i n&agrave;y tiểu b&agrave;n đ&ocirc;n liền l&agrave; Donquixote trong gia tộc một th&agrave;nh vi&ecirc;n, từ về mặt th&acirc;n phận đến xem, c&oacute; thể n&oacute;i l&agrave; ng&agrave;y sau c&aacute;i kia Đại hải tặc &middot; Heavenly Yaksha &middot; Donquixote &middot; Doflamingo đại biểu ca đi, hắn so Doflamingo muốn lớn hơn v&agrave;i tuổi.<br />\r\nVề phần t&ecirc;n l&agrave; &#39;Claudius&#39;, c&oacute; lẽ tr&ecirc;n thế giới n&agrave;y ngược lại l&agrave; kh&ocirc;ng c&oacute; c&aacute;i g&igrave; đặc th&ugrave;, chẳng qua hiện nay Claudius thế nhưng l&agrave; rất r&otilde; r&agrave;ng, c&aacute;i t&ecirc;n n&agrave;y ch&iacute;nh l&agrave; kiếp trước La M&atilde; đế quốc ho&agrave;ng đế truyền thừa t&ecirc;n, n&oacute;i l&agrave; Haki, cũng x&aacute;c thực Haki.<br />\r\nDonquixote &middot; Claudius cầm trong tay một c&acirc;y c&ocirc;n gỗ, tại c&aacute;i kia tr&ecirc;n bờ c&aacute;t khoa tay m&uacute;a ch&acirc;n một lần về sau, nhẹ giọng tự nhủ: &quot;Bất kể n&oacute;i thế n&agrave;o, tốt xấu trước chải vuốt một ch&uacute;t t&igrave;nh huống a. . .&quot;<br />\r\n&quot;Đầu ti&ecirc;n điểm thứ nhất, tiền th&acirc;n c&aacute;i n&agrave;y thật qu&aacute; ngu xuẩn ngớ ngẩn gia hỏa bị người hố đ&atilde; ăn Tr&aacute;i &Aacute;c Quỷ. . . C&aacute;i n&agrave;y ta liền kh&ocirc;ng cải biến được, bất qu&aacute; tốt xấu l&agrave; hệ Logia năng lực, bằng v&agrave;o ta c&aacute;i n&agrave;y ưu t&uacute; đại n&atilde;o. . . Hẳn l&agrave; sẽ kh&ocirc;ng giống c&aacute;i n&agrave;y ngu ngốc đồng dạng l&agrave;m trễ nải năng lực a?&quot; Claudius nhẹ giọng nỉ non n&oacute;i.<br />\r\nKh&ocirc;ng sai, c&aacute;i n&agrave;y đ&aacute;ng chết tiền th&acirc;n, r&otilde; r&agrave;ng l&agrave; một người cao qu&yacute; Thi&ecirc;n Long Nh&acirc;n, lại c&ograve;n bị người hố ăn c&aacute;i Tr&aacute;i &Aacute;c Quỷ, cho d&ugrave; l&agrave; hệ Logia, nhưng cũng đủ để nh&igrave;n ra hắn c&oacute; bao nhi&ecirc;u ngu xuẩn.<br />\r\nĐương nhi&ecirc;n, muốn n&oacute;i b&acirc;y giờ Claudius sẽ có hay kh&ocirc;ng có hiện tại Thi&ecirc;n Long Nh&acirc;n loại kia kh&ocirc;ng hiểu thấu cao qu&yacute; cảm gi&aacute;c, t&igrave;nh nguyện đem v&ocirc; c&ugrave;ng cường đại Tr&aacute;i &Aacute;c Quỷ coi như đồ chơi n&eacute;m cho n&ocirc; lệ, cũng kh&ocirc;ng nguyện lấy ra tăng cường thực lực của m&igrave;nh, đ&acirc;y nhất định l&agrave; kh&ocirc;ng c&oacute;, Claudius l&agrave; c&aacute;i tương đối phải thiết thực người, hắn mới kh&ocirc;ng c&oacute; loại n&agrave;y &#39;Phong kiến&#39; &yacute; nghĩ, huống chi, đều đi tới Hải tặc trong thế giới, nếu l&agrave; kh&ocirc;ng trải nghiệm một c&aacute;i Tr&aacute;i &Aacute;c Quỷ cảm gi&aacute;c, c&ugrave;ng loại kia vượt mức b&igrave;nh thường lực lượng thần b&iacute;, chẳng phải l&agrave; c&oacute; loại đến kh&ocirc;ng một lần trải nghiệm cảm gi&aacute;c?<br />\r\nBởi vậy nếu l&agrave; th&acirc;n thể n&agrave;y kh&ocirc;ng c&oacute; ngu xuẩn bị hố lấy ăn Tr&aacute;i &Aacute;c Quỷ, Claudius c&ograve;n nghĩ đến nếu l&agrave; c&oacute; cơ hội, liền đi l&agrave;m c&aacute;i g&igrave; Goro Goro no Mi năng lực như vậy tới chơi đ&ugrave;a đ&acirc;u, b&acirc;y giờ xem ra, giấc mộng n&agrave;y l&agrave; thực hiện kh&ocirc;ng được nữa, thật sự l&agrave; bực m&igrave;nh.<br />\r\n&quot;Điểm thứ hai, Claudius. . . Cũng ch&iacute;nh l&agrave; ta, l&agrave; thuộc về hạ vị Thi&ecirc;n Long Nh&acirc;n, cũng chẳng tr&aacute;ch hồ gia hỏa n&agrave;y đ&acirc;̀y n&atilde;o ruột gi&agrave;, d&aacute;ng người cồng kềnh, nh&igrave;n qua tựa như c&aacute;i phế vật, đơn giản ảnh hưởng ta anh tuấn suất kh&iacute; linh hồn. . .&quot; Claudius c&oacute; ch&uacute;t bất đắc dĩ lắc đầu, nh&igrave;n xem c&aacute;i kia căng căng lui lui triều trong nước, dung mạo của m&igrave;nh, mở miệng đậu đen rau muống n&oacute;i.<br />\r\nNhắc tới hạ vị Thi&ecirc;n Long Nh&acirc;n, kỳ thật cũng rất dễ l&yacute; giải, Ch&iacute;nh Phủ Thế Giới loại n&agrave;y khổng lồ cơ cấu, c&oacute; thể duy tr&igrave; c&ugrave;ng ổn định thế giới t&aacute;m trăm năm l&acirc;u, c&aacute;i kia cưỡi tại Ch&iacute;nh Phủ Thế Giới tr&ecirc;n đầu tầng ch&oacute;t nhất những c&aacute;i kia Thi&ecirc;n Long Nh&acirc;n, lu&ocirc;n kh&ocirc;ng khả năng tất cả đều l&agrave; giống &#39;Claudius&#39; dạng n&agrave;y chỉ biết l&agrave; ngồi ăn rồi chờ chết phế vật mới l&agrave;, bởi vậy c&aacute;i n&agrave;y Thi&ecirc;n Long Nh&acirc;n mặc d&ugrave; l&agrave; đồng dạng cao qu&yacute; huyết mạch, nhưng cũng sẽ ph&acirc;n ra tr&ecirc;n dưới giai cấp.<br />\r\nĐ&aacute;nh c&aacute;i đơn giản nhất so s&aacute;nh tới n&oacute;i, vậy liền n&acirc;ng n&acirc;ng b&acirc;y giờ Ch&iacute;nh Phủ Thế Giới b&ecirc;n ngo&agrave;i nhất quyền cao lực chưởng khống giả nh&oacute;m &middot; Gorosei.<br />\r\nGorosei bọn hắn năm c&aacute;i vốn l&agrave; thuộc về Thi&ecirc;n Long Nh&acirc;n giai tầng, m&agrave; lại l&agrave; địa vị cao nhất Thi&ecirc;n Long Nh&acirc;n, giống bọn hắn kẻ như vậy, rất r&otilde; r&agrave;ng liền c&ugrave;ng Claudius phế vật như vậy kh&aacute;c biệt, c&oacute; được tr&iacute; tuệ đại n&atilde;o c&ugrave;ng kinh nghiệm phong ph&uacute;, nắm giữ lấy Ch&iacute;nh Phủ Thế Giới chiếc n&agrave;y cự lu&acirc;n b&aacute;nh l&aacute;i, đi thuyền tại b&ecirc;n trong thế giới n&agrave;y.<br />\r\nTrừ bỏ địa vị cao nhất Thi&ecirc;n Long Nh&acirc;n b&ecirc;n ngo&agrave;i, Thi&ecirc;n Long Nh&acirc;n b&ecirc;n trong c&ograve;n chia l&agrave;m thượng vị, trung vị Thi&ecirc;n Long Nh&acirc;n, cũng ch&iacute;nh l&agrave; từng c&aacute;i Thi&ecirc;n Long Nh&acirc;n gia tộc tộc trưởng c&ograve;n c&oacute; một số bản th&acirc;n m&igrave;nh tương đối c&oacute; năng lực Thi&ecirc;n Long Nh&acirc;n c&aacute;i g&igrave;, bọn hắn tại Thi&ecirc;n Long Nh&acirc;n b&ecirc;n trong cũng coi l&agrave; tay cầm một phương quyền lực, đầu &oacute;c b&igrave;nh thường gia hỏa, cuối c&ugrave;ng liền l&agrave; giống Claudius phế vật như vậy, thuộc về loại kia ngồi ăn rồi chờ chết hạ vị Thi&ecirc;n Long Nh&acirc;n.<br />\r\nLại cẩn thận quan s&aacute;t dưới c&aacute;i n&agrave;y thủy triều phản chiếu đi ra dung mạo, Claudius sờ l&ecirc;n cằm nghĩ nghĩ, cuối c&ugrave;ng gật gật đầu, c&oacute; ch&uacute;t rắm th&uacute;i cảm kh&aacute;i n&oacute;i: &quot;Từ nội t&igrave;nh đến xem, c&oacute; lẽ vẫn l&agrave; c&oacute; thể cứu, liền l&agrave; kh&iacute; chất n&agrave;y c&ugrave;ng d&aacute;ng người c&oacute; ch&uacute;t qu&aacute; phận. . . Chỉ cần c&oacute; thể đem hai điểm n&agrave;y cải biến, để c&aacute;i n&agrave;y th&acirc;n t&uacute;i da xứng với ta c&aacute;i kia anh tuấn lại anh tuấn linh hồn, nghĩ đến c&oacute; lẽ vẫn l&agrave; c&oacute; thể l&agrave;m được.&quot;<br />\r\nNguy&ecirc;n bản Claudius thuộc về dưới nhất vị Thi&ecirc;n Long Nh&acirc;n, ngồi ăn rồi chờ chết l&agrave; được rồi, muốn c&aacute;i g&igrave; nhan trị? Bởi vậy tăng th&ecirc;m c&aacute;i kia ngu ngốc v&ocirc; năng phế vật kh&iacute; chất, phối hợp với m&igrave;nh c&aacute;i kia ăn uống v&ocirc; độ nu&ocirc;i đi ra to&agrave;n th&acirc;n thịt thừa, dẫn đến Claudius trở th&agrave;nh một c&aacute;i nh&igrave;n qua liền mười phần l&ocirc;i th&ocirc;i buồn n&ocirc;n &#39;Mập mạp chết bầm&#39; .<br />\r\nNhưng l&agrave; trừ bỏ c&aacute;i n&agrave;y hậu thi&ecirc;n nh&acirc;n tố đến xem, chỉ nh&igrave;n c&aacute;i n&agrave;y ti&ecirc;n thi&ecirc;n nội t&igrave;nh, kh&ocirc;ng hề nghi ngờ, Claudius cũng l&agrave; rất ưu t&uacute; suất ca người kế tục, suy nghĩ kỹ một ch&uacute;t cũng thế, c&aacute;i n&agrave;y Thi&ecirc;n Long Nh&acirc;n l&agrave; c&aacute;i g&igrave; ch&oacute; đức hạnh? Nh&igrave;n thấy c&ocirc; nương xinh đẹp, liền muốn cường lấy về nh&agrave; l&agrave;m l&atilde;o b&agrave;, lại kh&ocirc;ng n&oacute;i c&oacute; đ&uacute;ng hay kh&ocirc;ng đồ chơi, ch&iacute; &iacute;t đ&atilde; chứng minh thẩm mỹ quan l&agrave; b&igrave;nh thường.<br />\r\nTiếp theo, những thế giới n&agrave;y c&aacute;c qu&yacute; tộc ăn uống d&ugrave;ng tất cả đều l&agrave; tốt nhất, t&aacute;m trăm năm đến, bao nhi&ecirc;u cũng sẽ cải biến một ch&uacute;t trong cơ thể &#39;Ưu t&uacute; gen&#39; a?<br />\r\nLại th&ecirc;m nguy&ecirc;n bản những người n&agrave;y ti&ecirc;n tổ liền l&agrave; Vương tộc, c&oacute; thể n&oacute;i trong cơ thể gen c&ograve;n l&agrave; rất kh&ocirc;ng tệ, tr&ecirc;n cơ bản loại kia trời sinh d&aacute;ng dấp để cho người ta kh&ocirc;ng vừa mắt đ&iacute;ch x&aacute;c rất &iacute;t người, tuyệt đại đa số đều l&agrave; bởi v&igrave; hậu thi&ecirc;n m&igrave;nh ăn uống v&ocirc; độ, ph&oacute;ng t&uacute;ng dục vọng, giày vò đi ra.<br />\r\nTừ tr&ecirc;n tổng hợp lại, Claudius cảm thấy chỉ cần m&igrave;nh cố gắng một ch&uacute;t, khắc chế trong cơ thể m&igrave;nh c&aacute;c loại r&aacute;c rưởi dục vọng, như vậy kh&ocirc;i phục d&aacute;ng người, loại trừ loại kia tự mang đầy mỡ cảm gi&aacute;c, biến th&agrave;nh một c&aacute;i đại suất ca c&aacute;i g&igrave;, c&oacute; lẽ vẫn l&agrave; c&oacute; thể l&agrave;m được.<br />\r\nD&ugrave; sao m&igrave;nh đại biểu đệ, Donquixote &middot; Doflamingo về sau như thế mạo liền rất suất kh&iacute; nha, ch&iacute; &iacute;t so c&aacute;i n&agrave;y Th&aacute;nh Địa &middot; Marijoa b&ecirc;n trong Thi&ecirc;n Long Nh&acirc;n nh&oacute;m đẹp trai hơn rất nhiều, c&aacute;i n&agrave;y tại Claudius xem ra, cũng l&agrave; bởi v&igrave; Doflamingo về sau ho&agrave;n cảnh lớn l&ecirc;n kh&aacute;c biệt, nếu l&agrave; vẫn như cũ để hắn tại MariJoa l&agrave;m c&aacute;i ngồi ăn rồi chờ chết Thi&ecirc;n Long Nh&acirc;n, vậy hắn đo&aacute;n chừng hội trưởng t&agrave;n.<br />\r\n&quot;Cuối c&ugrave;ng x&aacute;c định một điểm, ta Claudius sẽ tao ngộ tai nạn tr&ecirc;n biển, lưu lạc đến tận đ&acirc;y, cũng l&agrave; một trận &acirc;m mưu a, d&ugrave; sao ta vốn n&ecirc;n l&agrave; đ&atilde; chết mới đ&uacute;ng.&quot; Claudius trong mắt l&oacute;e l&ecirc;n một đạo phức tạp h&agrave;o quang, nhẹ giọng nỉ non n&oacute;i.</p>', '0', 'bat-dau-lien-la-thien-long-nhan', 1, '11', '2022-07-31 07:48:11', '2022-09-06 09:19:34'),
(3, '2', 'Ta tiền nhiệm là cái thiểu năng trí tuệ?', '<p>ởi v&igrave; c&aacute;i gọi l&agrave; nơi c&oacute; người, liền c&oacute; giang hồ, c&oacute; giang hồ, liền c&oacute; tranh đấu, tại Th&aacute;nh Địa &middot; Marijoa Thi&ecirc;n Long Nh&acirc;n quần thể b&ecirc;n trong, tự nhi&ecirc;n cũng l&agrave; như thế.<br />\r\nThi&ecirc;n Long Nh&acirc;n nh&oacute;m khinh thường tại c&ugrave;ng b&igrave;nh thường &#39;D&acirc;n đen&#39; nh&oacute;m c&aacute;i g&igrave; tranh đoạt, bởi v&igrave; bọn họ kh&ocirc;ng cần đi c&ugrave;ng những người n&agrave;y tranh, muốn c&aacute;i g&igrave;, trực tiếp đi &#39;Cầm&#39; đến ch&iacute;nh l&agrave;, tại Thi&ecirc;n Long Nh&acirc;n kh&aacute;i niệm b&ecirc;n trong, bọn hắn l&agrave; thuộc về &#39;Thượng vị giả&#39;, bởi vậy kh&ocirc;ng cần tranh đấu, d&ugrave; sao những c&aacute;i kia bọn tiện d&acirc;n cũng kh&ocirc;ng d&aacute;m phản b&aacute;c bọn hắn, chẳng lẽ bọn tiện d&acirc;n c&ograve;n d&aacute;m đoạt bọn hắn Thi&ecirc;n Long Nh&acirc;n đồ vật kh&ocirc;ng th&agrave;nh?<br />\r\nCho n&ecirc;n n&oacute;i như vậy, Thi&ecirc;n Long Nh&acirc;n nh&oacute;m ở giữa tranh đấu, liền cực hạn tại Thi&ecirc;n Long Nh&acirc;n c&aacute;i giai tầng n&agrave;y b&ecirc;n trong, chỉ c&oacute; ngang h&agrave;ng cao qu&yacute;, mới coi l&agrave; bọn hắn &#39;Đối thủ&#39; ?<br />\r\nDonquixote &middot; Claudius tao ngộ, ngươi muốn n&oacute;i hắn cẩu huyết, vậy thật đ&uacute;ng l&agrave; tương đương cẩu huyết, chớ nh&igrave;n hắn hiện tại l&agrave; c&aacute;i hạ vị Thi&ecirc;n Long Nh&acirc;n, nhưng l&agrave; hắn th&acirc;n thể n&agrave;y song th&acirc;n vẫn l&agrave; rất lợi hại, ch&iacute;nh l&agrave; nguy&ecirc;n bản Donquixote gia tộc chủ nh&acirc;n, cho n&ecirc;n n&oacute;i hắn l&agrave; c&aacute;i Thi&ecirc;n Long Nh&acirc;n b&ecirc;n trong &#39;Quan nhị đại&#39; cũng kh&ocirc;ng sao.<br />\r\nChỉ bất qu&aacute; trời c&oacute; bất trắc phong v&acirc;n, người c&oacute; họa hề đ&aacute;n ph&uacute;c, cho d&ugrave; l&agrave; tự xưng l&agrave; thần Thi&ecirc;n Long Nh&acirc;n, cũng cuối c&ugrave;ng bất qu&aacute; l&agrave; c&aacute;i nh&acirc;n loại th&ocirc;i, l&agrave; nh&acirc;n loại, cũng chạy kh&ocirc;ng tho&aacute;t sinh l&atilde;o bệnh tử lu&acirc;n hồi, Claudius phụ mẫu cũng bởi v&igrave; một loại n&agrave;o đ&oacute; bệnh bất trị, tại ba năm trước đ&acirc;y qua đời.</p>', '<p>Bởi v&igrave; c&aacute;i gọi l&agrave; nơi c&oacute; người, liền c&oacute; giang hồ, c&oacute; giang hồ, liền c&oacute; tranh đấu, tại Th&aacute;nh Địa &middot; Marijoa Thi&ecirc;n Long Nh&acirc;n quần thể b&ecirc;n trong, tự nhi&ecirc;n cũng l&agrave; như thế.<br />\r\nThi&ecirc;n Long Nh&acirc;n nh&oacute;m khinh thường tại c&ugrave;ng b&igrave;nh thường &#39;D&acirc;n đen&#39; nh&oacute;m c&aacute;i g&igrave; tranh đoạt, bởi v&igrave; bọn họ kh&ocirc;ng cần đi c&ugrave;ng những người n&agrave;y tranh, muốn c&aacute;i g&igrave;, trực tiếp đi &#39;Cầm&#39; đến ch&iacute;nh l&agrave;, tại Thi&ecirc;n Long Nh&acirc;n kh&aacute;i niệm b&ecirc;n trong, bọn hắn l&agrave; thuộc về &#39;Thượng vị giả&#39;, bởi vậy kh&ocirc;ng cần tranh đấu, d&ugrave; sao những c&aacute;i kia bọn tiện d&acirc;n cũng kh&ocirc;ng d&aacute;m phản b&aacute;c bọn hắn, chẳng lẽ bọn tiện d&acirc;n c&ograve;n d&aacute;m đoạt bọn hắn Thi&ecirc;n Long Nh&acirc;n đồ vật kh&ocirc;ng th&agrave;nh?<br />\r\nCho n&ecirc;n n&oacute;i như vậy, Thi&ecirc;n Long Nh&acirc;n nh&oacute;m ở giữa tranh đấu, liền cực hạn tại Thi&ecirc;n Long Nh&acirc;n c&aacute;i giai tầng n&agrave;y b&ecirc;n trong, chỉ c&oacute; ngang h&agrave;ng cao qu&yacute;, mới coi l&agrave; bọn hắn &#39;Đối thủ&#39; ?<br />\r\nDonquixote &middot; Claudius tao ngộ, ngươi muốn n&oacute;i hắn cẩu huyết, vậy thật đ&uacute;ng l&agrave; tương đương cẩu huyết, chớ nh&igrave;n hắn hiện tại l&agrave; c&aacute;i hạ vị Thi&ecirc;n Long Nh&acirc;n, nhưng l&agrave; hắn th&acirc;n thể n&agrave;y song th&acirc;n vẫn l&agrave; rất lợi hại, ch&iacute;nh l&agrave; nguy&ecirc;n bản Donquixote gia tộc chủ nh&acirc;n, cho n&ecirc;n n&oacute;i hắn l&agrave; c&aacute;i Thi&ecirc;n Long Nh&acirc;n b&ecirc;n trong &#39;Quan nhị đại&#39; cũng kh&ocirc;ng sao.<br />\r\nChỉ bất qu&aacute; trời c&oacute; bất trắc phong v&acirc;n, người c&oacute; họa hề đ&aacute;n ph&uacute;c, cho d&ugrave; l&agrave; tự xưng l&agrave; thần Thi&ecirc;n Long Nh&acirc;n, cũng cuối c&ugrave;ng bất qu&aacute; l&agrave; c&aacute;i nh&acirc;n loại th&ocirc;i, l&agrave; nh&acirc;n loại, cũng chạy kh&ocirc;ng tho&aacute;t sinh l&atilde;o bệnh tử lu&acirc;n hồi, Claudius phụ mẫu cũng bởi v&igrave; một loại n&agrave;o đ&oacute; bệnh bất trị, tại ba năm trước đ&acirc;y qua đời.<br />\r\nKhi đ&oacute; Claudius c&ograve;n rất tuổi nhỏ, mới bảy tuổi m&agrave; th&ocirc;i, d&ugrave; sao hắn hiện nay cũng mới mười tuổi, căn bản kh&ocirc;ng c&oacute; khả năng tiếp nhận hắn lão tử vị tr&iacute;, trở th&agrave;nh Donquixote gia tộc chủ nh&acirc;n.<br />\r\nSau đ&oacute; Donquixote gia tộc, liền bị Doflamingo l&atilde;o cha, cũng ch&iacute;nh l&agrave; Donquixote &middot; Homing cho tiếp nhận, c&aacute;i n&agrave;y St.Homing thuộc về loại kia Thi&ecirc;n Long Nh&acirc;n b&ecirc;n trong dị loại, t&acirc;m địa thiện lương, cũng kh&ocirc;ng c&oacute; nhiều như vậy &yacute; nghĩ xấu xa, chỗ lấy đối đ&atilde;i Claudius c&aacute;i n&agrave;y &#39;Trước Thiếu chủ&#39; c&ograve;n l&agrave; rất kh&ocirc;ng tệ, ba năm n&agrave;y, đều đem Claudius coi như l&agrave; con của m&igrave;nh đồng dạng tại dưỡng dục.<br />\r\nL&uacute;c đầu vậy liền coi l&agrave;, nếu l&agrave; kh&ocirc;ng xảy ra chuyện g&igrave; ngo&agrave;i &yacute; muốn, Claudius hẳn l&agrave; c&oacute; thể tại St.Homing chiếu kh&aacute;n dưới, ngồi ăn rồi chờ chết đến trưởng th&agrave;nh, sau đ&oacute; cả đời l&agrave;m c&aacute;i tầm thường v&ocirc; vi, lại c&oacute; thể l&agrave;m mưa l&agrave;m gi&oacute;, hưởng thụ vốn c&oacute; hết thảy phế vật Thi&ecirc;n Long Nh&acirc;n, bất qu&aacute; giống như trong tr&iacute; nhớ, St.Homing l&agrave; dự định tại m&igrave;nh sau trưởng th&agrave;nh, đem vị tr&iacute; gia chủ một lần nữa giao trả lại cho m&igrave;nh.<br />\r\n&quot;Suy nghĩ kỹ một ch&uacute;t, loại n&agrave;y khi củi mục, nhưng lại c&oacute; thể hưởng thụ hết thảy nh&acirc;n sinh. . . Giống như cũng thật thoải m&aacute;i?&quot; Claudius cười cười, tự nhủ.<br />\r\nKh&ocirc;ng thể kh&ocirc;ng n&oacute;i, loại người n&agrave;y sinh, x&aacute;c thực cũng thật thoải m&aacute;i, muốn c&aacute;i g&igrave; c&oacute; c&aacute;i đ&oacute;, c&ograve;n kh&ocirc;ng cần phấn đấu, kh&ocirc;ng cần mơ mộng những c&aacute;i kia bực m&igrave;nh sự t&igrave;nh. . .<br />\r\nNhưng vấn đề l&agrave;. . . Kh&ocirc;ng biết c&oacute; phải hay kh&ocirc;ng l&agrave; thực chất b&ecirc;n trong c&oacute; loại kh&ocirc;ng hiểu &#39;Chủ nghĩa l&yacute; tưởng huyết mạch&#39;, Donquixote nhất tộc thỉnh thoảng liền sẽ xuất hiện một hai c&aacute;i tại Claudius xem ra, đầu &oacute;c c&oacute; vấn đề gia hỏa, thảm hại hơn ch&iacute;nh l&agrave;, b&acirc;y giờ Donquixote gia tộc chủ nh&acirc;n &middot; Donquixote &middot; St.Homing. . . Hắn liền l&agrave; một c&aacute;i dạng n&agrave;y đầu &oacute;c c&oacute; vấn đề gia hỏa.<br />\r\nGần nhất hơn một năm nay đến, c&oacute; thể l&agrave; bởi v&igrave; trước đ&oacute; hắn mang theo vợ con của m&igrave;nh nh&oacute;m c&ugrave;ng đi Bắc Hải lữ h&agrave;nh nguy&ecirc;n nh&acirc;n, kiến thức rất nhiều c&aacute;i thế giới n&agrave;y phong thổ, để trong cơ thể hắn người chủ nghĩa l&yacute; tưởng huyết mạch bị k&iacute;ch hoạt l&ecirc;n.<br />\r\nC&aacute;i n&agrave;y tại Th&aacute;nh Địa &middot; Marijoa l&agrave;m tộc trưởng, khi đại l&atilde;o, khi Thi&ecirc;n Long Nh&acirc;n l&agrave;m thật tốt gia hỏa, đầu &oacute;c b&acirc;y giờ thiếu một c&acirc;y d&acirc;y cung, thỉnh thoảng liền sẽ nghĩ đến m&igrave;nh kh&ocirc;ng l&agrave;m Thi&ecirc;n Long Nh&acirc;n, muốn đi &#39;Hạ giới&#39; khi b&igrave;nh thường d&acirc;n ch&uacute;ng.<br />\r\nC&aacute;i n&agrave;y tại Thi&ecirc;n Long Nh&acirc;n giai tầng b&ecirc;n trong quả thực l&agrave; &#39;Đầu &oacute;c c&oacute; bệnh&#39; đại biểu a, bị h&oacute;a đi&ecirc;n cái chủng loại kia.<br />\r\nM&agrave; c&agrave;ng mấu chốt ch&iacute;nh l&agrave;, đang suy tư d&agrave;i đến thời gian một năm về sau, c&aacute;i n&agrave;y đầu &oacute;c c&oacute; bệnh gia hỏa, kh&ocirc;ng chỉ c&oacute; kh&ocirc;ng hề từ bỏ m&igrave;nh c&aacute;i kia suy nghĩ ấu tr&iacute;, ngược lại l&agrave; c&agrave;ng cảm thấy loại &yacute; nghĩ n&agrave;y rất c&oacute; t&iacute;nh khả thi.<br />\r\nBởi v&igrave; c&aacute;i gọi l&agrave; &#39;Ước mơ l&agrave; khoảng c&aacute;ch l&yacute; giải nhất xa x&ocirc;i cự ly&#39;, Homing gia hỏa n&agrave;y r&otilde; r&agrave;ng liền l&agrave; tại m&igrave;nh trong đầu qu&aacute; mức ước mơ c&aacute;i kia d&acirc;n ch&uacute;ng sinh sống, giống như v&ocirc; ưu v&ocirc; lự, chỉ cần l&agrave;m ruộng l&agrave; được rồi, ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; nghĩ qua t&igrave;nh huống thực tế, bất qu&aacute; ngược lại cũng kh&ocirc;ng phải l&agrave; kh&ocirc;ng thể l&yacute; giải, d&ugrave; sao từ xuất sinh l&ecirc;n liền đứng ở tr&ecirc;n đỉnh thế giới Thi&ecirc;n Long Nh&acirc;n nh&oacute;m, nếu l&agrave; c&oacute; thể l&yacute; giải d&acirc;n ch&uacute;ng khổ sở, vậy thật đ&uacute;ng l&agrave; c&oacute; quỷ.<br />\r\nCho n&ecirc;n c&aacute;i n&agrave;y khiến St.Homing sinh ra một loại &#39;Sao kh&ocirc;ng ăn thịt ch&aacute;o&#39; nực cười huyễn tưởng.<br />\r\nBởi vậy một năm qua n&agrave;y, St.Homing bắt đầu cho Donquixote gia tộc an b&agrave;i &#39;Đường lui&#39;, d&ugrave; sao hắn b&acirc;y giờ l&agrave; Donquixote gia tộc chủ nh&acirc;n nha, gia hỏa n&agrave;y bao nhi&ecirc;u cũng coi l&agrave; c&oacute; chút l&ograve;ng tr&aacute;ch nhiệm người, kh&ocirc;ng nghĩ lấy m&igrave;nh trực tiếp phủi m&ocirc;ng một c&aacute;i đi, bắt đầu an b&agrave;i giao tiếp l&ecirc;n c&oacute; quan hệ với Donquixote gia tộc c&aacute;c loại quyền lực c&ugrave;ng t&agrave;i ph&uacute; loại h&igrave;nh đồ vật.<br />\r\nL&uacute;c n&agrave;y, nguy&ecirc;n bản kh&ocirc;ng thế n&agrave;o thu h&uacute;t &#39;Claudius&#39; liền bị c&aacute;i n&agrave;y St.Homing cho lừa thảm rồi, d&ugrave; sao từ ph&aacute;p l&yacute; c&ugrave;ng huyết mạch nh&igrave;n lại, hắn Claudius l&agrave; thuộc về ch&iacute;nh thống Donquixote gia tộc người thừa kế.<br />\r\nM&agrave; hết lần n&agrave;y tới lần kh&aacute;c, Thi&ecirc;n Long Nh&acirc;n nh&oacute;m, coi trọng nhất liền l&agrave; huyết mạch, d&ugrave; sao đ&acirc;y cũng l&agrave; duy tr&igrave; lấy bọn hắn giai tầng căn bản lực lượng c&ugrave;ng chế độ m&agrave;.<br />\r\nNhư vậy, khi nguy&ecirc;n bản gia chủ St.Homing muốn rời khỏi Donquixote gia tộc l&uacute;c, Claudius liền bị đương nhi&ecirc;n đẩy ng&atilde; tr&ecirc;n đầu s&oacute;ng ngọn gi&oacute;.<br />\r\nSt.Homing thật đ&uacute;ng l&agrave; muốn đem Donquixote gia tộc một lần nữa giao cho Claudius, m&agrave; Claudius c&aacute;i n&agrave;y củi mục c&ograve;n một ch&uacute;t cũng kh&ocirc;ng c&oacute; cảm thấy c&oacute; c&aacute;i g&igrave; kh&ocirc;ng tốt, phi thường vui mừng tiếp nhận c&aacute;i n&agrave;y an b&agrave;i.<br />\r\nNghĩ đến đ&acirc;y, Claudius đ&atilde; cảm thấy v&ocirc; c&ugrave;ng im lặng, quả nhi&ecirc;n tiền th&acirc;n l&agrave; c&aacute;i ngu ngốc v&ocirc; năng phế vật a, hắn cũng kh&ocirc;ng nghĩ một ch&uacute;t, m&igrave;nh ngoại trừ c&aacute;i kia tr&ecirc;n người huyết mạch tương đối ch&iacute;nh thống b&ecirc;n ngo&agrave;i, c&ograve;n c&oacute; c&aacute;i g&igrave; năng lực c&oacute; thể ch&egrave;o chống hắn tiếp nhận Donquixote gia tộc tộc trưởng?<br />\r\nĐầu ti&ecirc;n tuổi đời n&agrave;y liền kh&ocirc;ng cho ph&eacute;p a, hắn Donquixote gia tộc nam nh&acirc;n cũng kh&ocirc;ng phải chết sạch, c&acirc;̀n ngươi c&aacute;i mười tuổi tiểu quỷ đi l&agrave;m l&atilde;o đại? Tiếp theo, ch&iacute;nh l&agrave; bởi v&igrave; l&agrave; tuổi t&aacute;c rất nhỏ, tăng th&ecirc;m phụ mẫu chết sớm, St.Homing lại l&agrave; c&aacute;i ngốc bạch ngọt, cho n&ecirc;n căn bản kh&ocirc;ng cho hắn Claudius lưu lại c&aacute;i g&igrave; hữu dụng th&agrave;nh vi&ecirc;n tổ chức, bởi vậy tại thế lực tr&ecirc;n phương diện, Claudius cũng l&agrave; một điểm căn cơ đều kh&ocirc;ng c&oacute; củi mục th&ocirc;i.<br />\r\nCuối c&ugrave;ng của cuối c&ugrave;ng, ngươi nếu l&agrave; c&aacute;i nh&acirc;n vật thi&ecirc;n t&agrave;i th&igrave; cũng th&ocirc;i đi, mọi người nếu l&agrave; c&oacute; thể tr&ocirc;ng thấy đi theo ngươi c&oacute; hi vọng, tương lai c&oacute; thể tại mười ch&iacute;n c&aacute;i trong gia tộc n&acirc;ng cao một bước c&aacute;i g&igrave;, n&oacute;i kh&ocirc;ng chừng cũng liền tạm thời ủy khuất một c&aacute;i, thừa nhận ngươi Claudius c&oacute; thể khi l&atilde;o đại rồi.<br />\r\nNhưng hết lần n&agrave;y tới lần kh&aacute;c, tiền th&acirc;n Claudius liền l&agrave; một c&aacute;i l&agrave;m g&igrave; c&aacute;i g&igrave; kh&ocirc;ng được, ăn c&aacute;i g&igrave; c&aacute;i g&igrave; kh&ocirc;ng dư thừa thật &middot; phế vật!</p>\r\n\r\n<p><br />\r\nNhư vậy, Donquixote gia tộc c&ograve;n lại những c&aacute;i kia Thi&ecirc;n Long Nh&acirc;n nh&oacute;m sẽ nghĩ như thế n&agrave;o? Muốn l&agrave; theo ch&acirc;n Claudius loại n&agrave;y v&ocirc; năng lại ngu ngốc tiểu quỷ c&ugrave;ng một chỗ lăn lộn, c&aacute;i n&agrave;y nguy&ecirc;n bản c&ograve;n tại MariJoa x&ecirc;́p hạng kh&ocirc;ng sai Donquixote gia tộc. . . Chỉ sợ kh&ocirc;ng cần hai ba năm, liền muốn đi mười ch&iacute;n gia tộc b&ecirc;n trong khi tầng dưới ch&oacute;t nhất Thi&ecirc;n Long Nh&acirc;n đi.<br />\r\nBởi vậy v&ocirc; luận l&agrave; v&igrave; vinh dự của gia tộc vẫn l&agrave; v&igrave; quyền lực của m&igrave;nh, d&ugrave; sao to&agrave;n bộ Donquixote trong gia tộc những c&aacute;i kia c&oacute; đầu &oacute;c người, đều l&agrave; &acirc;m thầm phản đối lấy để Claudius đến trở th&agrave;nh Donquixote gia tộc chủ nh&acirc;n.<br />\r\nĐiểm n&agrave;y, hiện tại Claudius rất l&yacute; giải, d&ugrave; sao nếu l&agrave; đổi lại hắn tới l&agrave;m lựa chọn, hắn cũng kh&ocirc;ng c&oacute; khả năng lựa chọn để loại phế vật n&agrave;y đến l&agrave;m gia chủ a!<br />\r\nNhưng hết lần n&agrave;y tới lần kh&aacute;c &#39;Người chủ nghĩa l&yacute; tưởng&#39; St.Homing c&aacute;i n&agrave;y ngốc bạch ngọt nh&igrave;n kh&ocirc;ng minh bạch đạo l&yacute; n&agrave;y, c&ograve;n cảm thấy m&igrave;nh đ&acirc;y l&agrave; phi thường ch&iacute;nh nghĩa lựa chọn, đem nguy&ecirc;n bản liền n&ecirc;n thuộc về Claudius quyền h&agrave;nh trả lại cho Claudius m&agrave; th&ocirc;i.<br />\r\nVề sau liền ph&aacute;t sinh đủ loại sự kiện, đầu ti&ecirc;n l&agrave; Claudius bị lừa lấy ăn nhầm bị l&agrave;m th&agrave;nh hoa quả salad Tr&aacute;i &Aacute;c Quỷ, trở th&agrave;nh Thi&ecirc;n Long Nh&acirc;n b&ecirc;n trong tr&ograve; cười.<br />\r\nNguy&ecirc;n bản đ&acirc;y l&agrave; đủ rồi, nhưng hết lần n&agrave;y tới lần kh&aacute;c St.Homing cũng kh&ocirc;ng c&oacute; bởi v&igrave; Claudius trở th&agrave;nh &#39;Tr&ograve; cười&#39; liền cải biến &yacute; nghĩ của m&igrave;nh, c&aacute;i n&agrave;y l&agrave;m cho những c&aacute;i kia kh&ocirc;ng nguyện &yacute; để Claudius trở th&agrave;nh Donquixote gia tộc tộc trưởng số &iacute;t c&oacute; đầu &oacute;c Thi&ecirc;n Long Nh&acirc;n nh&oacute;m c&agrave;ng th&ecirc;m l&agrave;m kh&oacute;.<br />\r\nNếu l&agrave; c&oacute; tuyển, bọn hắn kỳ thật cũng kh&ocirc;ng muốn hại c&aacute;i kia c&ugrave;ng thuộc về &#39;Hậu duệ của Thần&#39; &#39;Đồng b&agrave;o&#39;, bất qu&aacute; tại St.Homing chết đầu &oacute;c ph&iacute;a dưới, tại Claudius c&aacute;i kia kh&ocirc;ng hiểu thấu &#39;Cảm gi&aacute;c ưu việt&#39; ph&iacute;a dưới, bức đến bọn hắn kh&ocirc;ng thể kh&ocirc;ng thống hạ s&aacute;t thủ.<br />\r\nThế l&agrave; liền c&oacute; Claudius lần n&agrave;y tai nạn tr&ecirc;n biển sự kiện, ăn Tr&aacute;i &Aacute;c Quỷ, lại tao ngộ tai nạn tr&ecirc;n biển, nguy&ecirc;n bản Claudius vẫn l&agrave; c&aacute;i l&agrave;m g&igrave; c&aacute;i g&igrave; kh&ocirc;ng được củi mục, dựa theo những n&agrave;y lẽ thường đến xem, hắn hẳn phải chết kh&ocirc;ng nghi ngờ mới đ&uacute;ng chứ.<br />\r\nTr&ecirc;n thực tế, cũng quả thật l&agrave; như thế, Claudius rất xui xẻo chết tại lần n&agrave;y tai nạn tr&ecirc;n biển b&ecirc;n trong, sau đ&oacute; hắn lại đạt được tr&ugrave;ng sinh!<br />\r\nNgồi tại c&aacute;i n&agrave;y bờ biển tr&ecirc;n đ&aacute; ngầm, Claudius nhớ tới trước đ&acirc;y th&acirc;n trải qua cẩu huyết sự t&igrave;nh, liền v&ocirc; c&ugrave;ng im lặng, ph&agrave;m l&agrave; hỗn đản n&agrave;y c&oacute; thể c&oacute; chút tự m&igrave;nh hiểu lấy, chẳng phải bản th&acirc;n cảm gi&aacute;c tốt đẹp đi nguyện &yacute; l&agrave;m c&aacute;i n&agrave;y Donquixote gia tộc gia chủ, vậy hắn hiện tại đừng n&oacute;i sẽ tao ngộ tai nạn tr&ecirc;n biển chết ở chỗ n&agrave;y, thậm ch&iacute; đều kh&oacute; c&oacute; khả năng bị hố lấy ăn c&aacute;i kia Tr&aacute;i &Aacute;c Quỷ.<br />\r\nBất qu&aacute; việc đ&atilde; đến nước n&agrave;y, Claudius cũng kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o xoay xoay qua chỗ kh&aacute;c, chỉ c&oacute; thể suy nghĩ tại tương lai.<br />\r\nL&uacute;c n&agrave;y hắn th&acirc;n ở tại tr&ecirc;n hoang đảo, để ch&iacute;nh hắn tạo con thuyền lời n&oacute;i. . . Kỳ thật cũng kh&ocirc;ng phải l&agrave; kh&ocirc;ng được, nhưng vấn đề l&agrave; hắn cũng kh&ocirc;ng c&oacute; h&agrave;ng hải kinh nghiệm, chớ n&oacute;i chi l&agrave; tại c&aacute;i n&agrave;y hung hiểm thế giới mới b&ecirc;n trong h&agrave;ng hải, bởi vậy Claudius cho l&agrave; m&igrave;nh nếu l&agrave; gi&atilde;y dụa lấy cưỡng &eacute;p ra biển, sẽ chết c&agrave;ng nhanh một ch&uacute;t.<br />\r\nCho n&ecirc;n từ vừa mới bắt đầu, Claudius liền kh&ocirc;ng chuẩn bị tạo thuyền ra biển c&aacute;i g&igrave;, c&ograve;n lại cũng chỉ c&oacute; hai con đường tới chọn, con đường thứ nhất, ở chỗ n&agrave;y c&aacute;i g&igrave; cũng kh&ocirc;ng l&agrave;m, tiếp nhận c&aacute;i n&agrave;y đ&aacute;ng chết vận mệnh, thẳng đến ch&acirc;n ch&iacute;nh chết mất.<br />\r\nCon đường n&agrave;y cỗ th&acirc;n thể n&agrave;y b&ecirc;n tr&ecirc;n một nhiệm kỳ chủ nh&acirc;n đ&atilde; đi qua, nếu kh&ocirc;ng phải hắn ngồi ăn rồi chờ chết, m&igrave;nh cũng sẽ kh&ocirc;ng tu h&uacute; chiếm tổ chim kh&aacute;ch.<br />\r\nM&agrave; một con đường kh&aacute;c, liền l&agrave; ở chỗ n&agrave;y trước nghĩ biện ph&aacute;p sống s&oacute;t, sau đ&oacute; chờ cứu viện.<br />\r\nKh&ocirc;ng sai, d&ugrave; l&agrave; đ&acirc;y l&agrave; một trận &acirc;m mưu, nhưng Claudius d&ugrave; sao cũng l&agrave; Thi&ecirc;n Long Nh&acirc;n, liền xem như l&agrave;m cho gia tộc kh&aacute;c người đến xem, Donquixote gia tộc cũng tất nhi&ecirc;n sẽ ph&aacute;i người đến t&igrave;m kiếm cứu vớt hắn c&aacute;i n&agrave;y củi mục.<br />\r\nChỉ bất qu&aacute; c&oacute; lẽ thời gian n&agrave;y sẽ hơi k&eacute;o d&agrave;i một ch&uacute;t, thẳng đến k&eacute;o d&agrave;i đến những gia tộc kia b&ecirc;n trong đ&aacute;m người cho l&agrave; hắn Claudius tất nhưng đ&atilde; chết kh&ocirc;ng t&aacute;ng th&acirc;n chi địa về sau, mới c&oacute; h&agrave;nh động.<br />\r\nBất qu&aacute; c&aacute;i n&agrave;y như vậy đủ rồi, Claudius tự nhận l&agrave; m&igrave;nh c&oacute; lẽ vẫn l&agrave; c&oacute; thể chống đỡ cho đến l&uacute;c đ&oacute;, chỉ cần được ph&aacute;i tới đội cứu viện t&igrave;m được, hắn liền c&oacute; thể một lần nữa trở lại MariJoa.<br />\r\n&quot;Mẹ n&oacute;, chờ lão tử một lần nữa về tới MariJoa, chuyện thứ nhất liền l&agrave; tuy&ecirc;n bố c&aacute;i n&agrave;y Donquixote gia tộc tộc trưởng lão tử về sau kh&ocirc;ng l&agrave;m, người n&agrave;o th&iacute;ch l&agrave;m ai l&agrave;m!&quot; Claudius tức giận chửi bới n&oacute;i.<br />\r\nLấy Claudius hiện tại năng lực, coi như hắn đổi c&aacute;i linh hồn, cũng kh&ocirc;ng c&oacute; khả năng một c&aacute;i liền đạt được ủng hộ, trở th&agrave;nh Donquixote gia tộc tộc trưởng.<br />\r\nCho n&ecirc;n c&ugrave;ng n&oacute; đi tranh đoạt c&aacute;i n&agrave;y, c&ograve;n kh&ocirc;ng bằng cứ thế từ bỏ, thay c&aacute;i mạch suy nghĩ, thay c&aacute;i chiến lược đ&acirc;u.<br />\r\nVề phần muốn n&oacute;i Claudius đối với những c&aacute;i kia hố chết th&acirc;n thể n&agrave;y chủ nh&acirc;n đời trước đ&aacute;m gia hỏa c&oacute; c&aacute;i g&igrave; hận &yacute;. . . &Acirc;n, khoan h&atilde;y n&oacute;i, quả nhi&ecirc;n l&agrave; một ch&uacute;t cũng kh&ocirc;ng c&oacute;, d&ugrave; sao ch&iacute;nh hắn đều chướng mắt c&aacute;i n&agrave;y tiền nhiệm.<br />\r\nClaudius l&agrave; tương đối phải thiết thực gia hỏa, hắn cũng sẽ kh&ocirc;ng bởi v&igrave; tiếp nhận th&acirc;n thể của ngươi, liền đem ngươi trước đ&oacute; những c&aacute;i kia &#39;Nợ&#39; tất cả đều tiếp tục chống đỡ, thề muốn v&igrave; trước đ&acirc;y th&acirc;n chủ nh&acirc;n b&aacute;o th&ugrave; c&aacute;i g&igrave;, kh&ocirc;ng tồn tại. . .<br />\r\nĐương nhi&ecirc;n, nếu l&agrave; tại c&aacute;i n&agrave;y về sau, c&ograve;n c&oacute; người ra tay với hắn lời n&oacute;i. . . &Acirc;n, ta Claudius th&iacute;ch nhất đối những c&aacute;i kia tự nhận l&agrave; c&oacute; năng lực đ&aacute;m gia hỏa xuất thủ.<br />\r\n&quot;Nha, b&acirc;y giờ nghĩ nhiều như vậy cũng v&ocirc; dụng, vẫn l&agrave; trước hết nghĩ muốn l&agrave;m sao t&agrave;i năng tại cứu viện tiến đến trước đ&oacute; sống s&oacute;t a. . . S&aacute;ch, Thi&ecirc;n Long Nh&acirc;n d&atilde; ngoại cầu sinh? Đ&uacute;ng l&agrave; mẹ n&oacute; th&uacute; vị. . .&quot; Claudius tự giễu cười cười n&oacute;i.</p>', '0', 'ta-tien-nhiem-la-cai-thieu-nang-tri-tue', 1, '7', '2022-07-31 07:49:30', '2022-09-06 09:20:19');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(4, '3', 'King Engine?', '<p>Hoang đảo l&agrave; ch&ocirc;̃ t&ocirc;́t, mặc d&ugrave; vị tr&iacute; chỗ thế giới mới, bất qu&aacute; h&ograve;n đảo n&agrave;y l&agrave; c&aacute;i xu&acirc;n đảo, trong v&ograve;ng một năm bốn m&ugrave;a như m&ugrave;a xu&acirc;n, kh&iacute; hậu &ocirc;n nhuận, kh&ocirc;ng tồn tại c&aacute;i g&igrave; lạnh n&oacute;ng giao thế loại h&igrave;nh, bởi vậy cũng l&agrave;m cho Claudius tại tr&ecirc;n c&aacute;i đảo n&agrave;y lẫn v&agrave;o tương đối bu&ocirc;ng lỏng.<br />\r\nL&uacute;c n&agrave;y ở tr&ecirc;n c&aacute;i đảo n&agrave;y, một c&aacute;i to lớn heo rừng ch&iacute;nh đang đi&ecirc;n cuồng chạy trốn, c&aacute;i kia vốn cũng kh&ocirc;ng c&oacute; bao nhi&ecirc;u tr&iacute; tuệ &aacute;nh mắt b&ecirc;n trong, v&acirc;̣y mà tr&agrave;n đầy thần sắc sợ h&atilde;i.<br />\r\nĐừng nh&igrave;n được gọi l&agrave; &#39;Heo&#39;, tr&ecirc;n thực tế heo rừng l&agrave; một loại tại d&atilde; ngoại cường h&atilde;n v&ocirc; c&ugrave;ng sinh vật, to lớn răng nanh, nặng nề da l&ocirc;ng, mạnh mẽ hữu lực cơ bắp, th&acirc;n thể to lớn, những yếu tố n&agrave;y tổ hợp lại, đủ để khiến heo rừng th&agrave;nh v&igrave; thực vật li&ecirc;n b&ecirc;n trong thượng tầng giai cấp.</p>', '<p>Hoang đảo l&agrave; ch&ocirc;̃ t&ocirc;́t, mặc d&ugrave; vị tr&iacute; chỗ thế giới mới, bất qu&aacute; h&ograve;n đảo n&agrave;y l&agrave; c&aacute;i xu&acirc;n đảo, trong v&ograve;ng một năm bốn m&ugrave;a như m&ugrave;a xu&acirc;n, kh&iacute; hậu &ocirc;n nhuận, kh&ocirc;ng tồn tại c&aacute;i g&igrave; lạnh n&oacute;ng giao thế loại h&igrave;nh, bởi vậy cũng l&agrave;m cho Claudius tại tr&ecirc;n c&aacute;i đảo n&agrave;y lẫn v&agrave;o tương đối bu&ocirc;ng lỏng.<br />\r\nL&uacute;c n&agrave;y ở tr&ecirc;n c&aacute;i đảo n&agrave;y, một c&aacute;i to lớn heo rừng ch&iacute;nh đang đi&ecirc;n cuồng chạy trốn, c&aacute;i kia vốn cũng kh&ocirc;ng c&oacute; bao nhi&ecirc;u tr&iacute; tuệ &aacute;nh mắt b&ecirc;n trong, v&acirc;̣y mà tr&agrave;n đầy thần sắc sợ h&atilde;i.<br />\r\nĐừng nh&igrave;n được gọi l&agrave; &#39;Heo&#39;, tr&ecirc;n thực tế heo rừng l&agrave; một loại tại d&atilde; ngoại cường h&atilde;n v&ocirc; c&ugrave;ng sinh vật, to lớn răng nanh, nặng nề da l&ocirc;ng, mạnh mẽ hữu lực cơ bắp, th&acirc;n thể to lớn, những yếu tố n&agrave;y tổ hợp lại, đủ để khiến heo rừng th&agrave;nh v&igrave; thực vật li&ecirc;n b&ecirc;n trong thượng tầng giai cấp.<br />\r\nNhất l&agrave; l&uacute;c n&agrave;y ch&iacute;nh đang đi&ecirc;n cuồng chạy trốn heo rừng, h&igrave;nh thể to lớn, đều so ra m&agrave; vượt kiếp trước Claudius đ&atilde; thấy con voi, rất r&otilde; r&agrave;ng, loại sinh vật n&agrave;y, liền l&agrave; One Piece thế giới b&ecirc;n trong biến chủng, c&aacute;i thế giới n&agrave;y rất kỳ qu&aacute;i, c&aacute;c loại sinh vật đều c&oacute; to to nhỏ nhỏ biến dị, c&oacute; ch&uacute;t rất b&igrave;nh thường, c&ugrave;ng kiếp trước kh&ocirc;ng kh&aacute;c nhau ch&uacute;t n&agrave;o, c&oacute; ch&uacute;t liền mười phần qu&aacute; mức, tỉ như kia c&aacute;i g&igrave; Hải Vương, Zunisha loại h&igrave;nh sinh vật, h&igrave;nh thể to lớn đến l&agrave;m cho người kh&ocirc;ng thể tưởng tượng.<br />\r\nLiền xem như nh&acirc;n loại loại sinh vật n&agrave;y, trong thế giới n&agrave;y, cũng hoặc nhiều hoặc &iacute;t tồn tại một ch&uacute;t biến h&oacute;a, c&aacute;i kia ch&iacute;nh l&agrave; h&igrave;nh thể cao lớn nh&acirc;n loại rất nhiều, liền lấy Claudius tiểu biểu đệ &middot; Donquixote &middot; Doflamingo đến xem, tiểu tử n&agrave;y chỉ cần ph&aacute;t dục ho&agrave;n to&agrave;n, vậy sau n&agrave;y th&acirc;n cao thỏa thỏa ba m&eacute;t c&acirc;́t bước, vững v&agrave;ng Đại tướng cấp th&acirc;n cao.<br />\r\nM&agrave; Doflamingo loại n&agrave;y th&acirc;n cao, ở c&aacute;i thế giới n&agrave;y, cũng ch&iacute;nh l&agrave; rất thường gặp biểu hiện, cũng kh&ocirc;ng phải c&aacute;i g&igrave; dị loại, so với hắn cao c&agrave;ng l&agrave; c&oacute; khối người.<br />\r\nClaudius cảm thấy m&igrave;nh nếu l&agrave; c&oacute; thể hảo hảo r&egrave;n luyện, đầy đủ ph&aacute;t dục, đừng nh&igrave;n hiện tại l&agrave; c&aacute;i b&eacute;o đ&ocirc;n, nhưng ng&agrave;y sau hẳn l&agrave; cũng kh&ocirc;ng thể so với Doflamingo phải k&eacute;m.<br />\r\nBất qu&aacute; đ&acirc;y đều l&agrave; n&oacute;i sau, l&uacute;c n&agrave;y c&aacute;i n&agrave;y con lợn rừng v&igrave; c&aacute;i g&igrave; đang đi&ecirc;n cuồng chạy trốn? Đ&oacute; l&agrave; bởi v&igrave; n&oacute; gặp gần nhất c&aacute;i n&agrave;y hai tuần đến, ở tr&ecirc;n đảo ho&agrave;nh h&agrave;nh b&aacute; đạo, đem sư tử l&atilde;o hổ loại h&igrave;nh b&aacute; chủ tất cả đều xử l&yacute; nh&acirc;n loại tiểu th&iacute; h&agrave;i.<br />\r\nKh&ocirc;ng sai, l&uacute;c n&agrave;y Claudius liền đi theo c&aacute;i n&agrave;y heo rừng đằng sau, thở hồng hộc truy k&iacute;ch lấy, hai tuần thời gian, ngược lại để Claudius c&aacute;i kia nguy&ecirc;n bản b&eacute;o l&ugrave;n chắc nịch th&acirc;n cao gầy kh&ocirc;ng &iacute;t, mặc d&ugrave; vẫn như cũ l&agrave; c&oacute; thể nh&igrave;n ra một ch&uacute;t đầy mỡ c&ugrave;ng b&eacute;o tốt, nhưng bao nhi&ecirc;u cũng c&oacute; ch&uacute;t cơ bắp tr&agrave;n đầy tư thế.<br />\r\nTừ tr&agrave;ng diện nh&igrave;n lại, đ&uacute;ng l&agrave; c&oacute; ch&uacute;t buồn cười, một c&aacute;i mười mấy tuổi đại nh&acirc;n loại nhỏ b&eacute; tiểu quỷ, đang tại đuổi theo một c&aacute;i như ngọn n&uacute;i lớn nhỏ cường tr&aacute;ng heo rừng chạy nhanh, m&agrave; c&aacute;i kia heo rừng tựa hồ căn bản kh&ocirc;ng c&oacute; muốn quay đầu xử l&yacute; tiểu quỷ kia &yacute; nghĩ, chỉ l&agrave; tại trong sự sợ h&atilde;i liều mạng chạy trốn, quả thực c&oacute; ch&uacute;t buồn cười c&ugrave;ng nực cười.<br />\r\n&quot;Ngươi c&aacute;i n&agrave;y heo. . . Kh&ocirc;ng sai biệt lắm. . . Cũng. . . N&ecirc;n ngừng a?&quot; Claudius một b&ecirc;n thở hổn hển, một b&ecirc;n nh&igrave;n xem ph&iacute;a trước c&aacute;i kia ph&iacute;a sau v&ecirc;́t thương ch&ocirc;̀ng ch&acirc;́t, kh&ocirc;ng ngừng chảy m&aacute;u, nhưng như cũ đang liều mạng phi nước đại heo rừng, c&oacute; ch&uacute;t bất đắc dĩ chửi bới n&oacute;i.<br />\r\nLại chạy theo hai bước, cảm gi&aacute;c m&igrave;nh đ&atilde; nhanh muốn đến cực hạn, Claudius chậm r&atilde;i đ&atilde; ngừng lại c&aacute;i kia truy đuổi b&ocirc;̣ pháp, tiếp lấy h&iacute;t s&acirc;u một hơi, &aacute;nh mắt biến c&oacute; ch&uacute;t trở n&ecirc;n th&acirc;m thu&yacute;.<br />\r\nC&ugrave;ng n&agrave;y c&ugrave;ng l&uacute;c, tim của hắn đập cũng bắt đầu tăng nhanh, lấy Claudius l&agrave;m trung t&acirc;m, chung quanh hai ba m&eacute;t phạm vi b&ecirc;n trong, tựa hồ cũng c&oacute; thể nghe thấy c&aacute;i kia nhịp tim ch&iacute;nh đang kh&ocirc;ng ngừng gia tốc thanh &acirc;m.<br />\r\n&#39;Phanh phanh phanh phanh&#39;, m&atilde;nh liệt cảm gi&aacute;c tiết tấu phảng phất nhịp trống đồng dạng tại Claudius tim g&otilde; vang lấy, một gi&acirc;y sau, Claudius bước ch&acirc;n lần nữa bước động, chỉ bất qu&aacute; c&ugrave;ng l&uacute;c trước so s&aacute;nh, tốc độ của hắn nhanh gấp bội, c&aacute;i kia nhỏ b&eacute; th&acirc;n ảnh, đuổi theo tr&ecirc;n mặt đất tản m&aacute;t vết m&aacute;u, phi thường nhanh ch&oacute;ng liền đuổi kịp c&aacute;i kia ch&iacute;nh đang liều mạng phi nước đại heo rừng.<br />\r\nC&aacute;i kia heo rừng dư quang tựa hồ cũng quan s&aacute;t được sau lưng c&aacute;i kia t&ecirc;n tiểu quỷ lại lần nữa đuổi theo tới th&acirc;n ảnh, &aacute;nh mắt b&ecirc;n trong vẻ sợ h&atilde;i lại s&acirc;u một ch&uacute;t.<br />\r\nNhưng l&agrave; thời gian d&agrave;i chạy, lệnh thương thế của n&oacute; kh&ocirc;ng ngừng tăng th&ecirc;m, miệng vết thương chảy xu&ocirc;i m&aacute;u tươi cũng c&oacute; ch&uacute;t qua nhiều, dẫn đến n&oacute; lực lượng trong cơ thể cũng tại từ từ suy kiệt, phi nước đại bước ch&acirc;n cũng chậm lại.<br />\r\nNhưng m&agrave;, c&agrave;ng l&agrave; d&atilde; th&uacute; bị thương, th&igrave; c&agrave;ng hung t&agrave;n, tựa hồ l&agrave; &yacute; thức được m&igrave;nh đ&atilde; trốn kh&ocirc;ng tho&aacute;t, c&aacute;i kia to lớn heo rừng bỗng nhi&ecirc;n thắng gấp một c&aacute;i, lấy kh&ocirc;ng ph&ugrave; hợp th&acirc;n h&igrave;nh to lớn linh xảo b&ocirc;̣ pháp, ngạnh sinh sinh tr&ecirc;n mặt đất tới c&aacute;i 360 độ tr&ocirc;i đi lượn v&ograve;ng.<br />\r\n&quot;Ta dựa v&agrave;o, heo rừng th&agrave;nh tinh c&ograve;n học biết l&aacute;i xe?&quot; Claudius c&oacute; ch&uacute;t kinh ngạc nh&igrave;n một m&agrave;n n&agrave;y, trong l&ograve;ng kh&ocirc;ng khỏi thầm nghĩ.<br />\r\nC&aacute;i kia heo rừng tựa hồ cũng l&agrave; kh&ocirc;ng th&egrave;m đếm xỉa, d&ugrave;ng hết to&agrave;n lực, bốn v&oacute; tăng tốc độ, to lớn lại tr&agrave;n đầy lực s&aacute;t thương răng nanh hướng ph&iacute;a trước một đỉnh, thẳng vội v&agrave;ng liền hướng ph&iacute;a Claudius đ&aacute;nh tới.<br />\r\n&quot;V&ugrave;ng vẫy gi&atilde;y chết sao? Bất qu&aacute; kh&ocirc;ng d&ugrave;ng a. . .&quot; Claudius nở nụ cười, nhẹ giọng nỉ non n&oacute;i.<br />\r\nTrước ở c&aacute;i kia heo rừng va chạm m&agrave; đến trong nh&aacute;y mắt, Claudius cũng động, ch&acirc;n kế tiếp ph&aacute;t lực, hướng ph&iacute;a heo rừng tr&ecirc;n đầu liền nh&agrave;o tới.<br />\r\n&quot;Tử Vong Cầm B&atilde;o!&quot; Claudius khẽ qu&aacute;t một tiếng n&oacute;i.<br />\r\nMột gi&acirc;y sau, chỉ thấy c&aacute;i kia to lớn heo rừng một đầu đem Claudius đụng c&aacute;i vỡ n&aacute;t, th&acirc;n ảnh đều h&oacute;a th&agrave;nh hư v&ocirc;, m&agrave; ngay sau đ&oacute;, heo rừng tựa hồ gặp c&aacute;i g&igrave; phi thường kh&oacute; chịu t&igrave;nh huống đồng dạng, chạy vọt về ph&iacute;a trước chạy mấy bước về sau, th&acirc;n h&igrave;nh lảo đảo ng&atilde; xuống đất b&ecirc;n tr&ecirc;n, nện l&ecirc;n một mảng lớn tro bụi, tiếp lấy tứ chi bắt đầu gi&atilde;y dụa co quắp, đầu cũng tr&ecirc;n mặt đất kh&ocirc;ng ngừng cuồn cuộn lấy, giống như muốn đem thứ g&igrave; hất ra đồng dạng.<br />\r\nBất qu&aacute; những n&agrave;y gi&atilde;y dụa, đều kh&ocirc;ng c&oacute; thể tạo được hiệu quả, theo thời gian tr&ocirc;i qua, con n&agrave;y to lớn heo rừng gi&atilde;y dụa lực đạo cũng dần dần nhỏ đi, l&uacute;c n&agrave;y ở c&aacute;i kia heo rừng c&aacute;i mũi phụ cận, Claudius th&acirc;n ảnh ch&iacute;nh đang chậm r&atilde;i th&agrave;nh h&igrave;nh!<br />\r\nKh&ocirc;ng sai, Claudius c&aacute;i n&agrave;y kh&ocirc;ng may h&agrave;i tử mặc d&ugrave; l&agrave; c&aacute;i tứ chi kh&ocirc;ng cần, ngũ cốc kh&ocirc;ng ph&acirc;n &#39;Thiểu năng tr&iacute; tuệ Thi&ecirc;n Long Nh&acirc;n&#39;, nhưng cũng may m&agrave; hắn thiểu năng tr&iacute; tuệ, để hắn ăn Tr&aacute;i &Aacute;c Quỷ l&agrave;m th&agrave;nh salad, d&ugrave; l&agrave; chỉ c&oacute; một ngụm, cũng đủ rồi.<br />\r\nTừ đ&oacute; về sau, Claudius liền trở th&agrave;nh một t&ecirc;n Tr&aacute;i &Aacute;c Quỷ năng lực giả, nhắc tới năng lực, kh&ocirc;ng biết c&oacute; phải hay kh&ocirc;ng l&agrave; xuất ph&aacute;t từ Thi&ecirc;n Long Nh&acirc;n cao ngạo, liền xem như d&ugrave;ng để hại đồng tộc đồ chơi, cũng muốn d&ugrave;ng cao đoan h&agrave;ng mới được, nếu kh&ocirc;ng kh&ocirc;ng thể cho thấy bọn hắn cao qu&yacute; đồng dạng.<br />\r\nKh&ocirc;ng sai, vi&ecirc;n n&agrave;y Tr&aacute;i &Aacute;c Quỷ l&agrave; hệ Logia Tr&aacute;i &Aacute;c Quỷ, từ trong tr&iacute; nhớ đến xem, l&agrave; thuộc về hiếm thấy kh&iacute; thể loại hệ Logia Tr&aacute;i &Aacute;c Quỷ, được xưng l&agrave; &#39;Nitơ Nitơ no Mi&#39; .<br />\r\nClaudius tiền nhiệm l&agrave; c&aacute;i kh&ocirc;ng học thức dế nhũi, tự nhi&ecirc;n kh&ocirc;ng minh bạch c&aacute;i n&agrave;y tr&aacute;i c&acirc;y năng lực l&agrave; thứ đồ g&igrave;, cũng kh&ocirc;ng biết c&aacute;i kia kh&ocirc;ng hiểu kh&iacute; thể l&agrave; c&aacute;i g&igrave;, bởi vậy cũng đối với c&aacute;i n&agrave;y tr&aacute;i c&acirc;y kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o khai ph&aacute;t, ho&agrave;n to&agrave;n liền l&agrave; bỏ mặc trạng th&aacute;i, bất qu&aacute; tỉ mỉ nghĩ lại, cũng l&agrave; ph&ugrave; hợp hắn củi mục Thi&ecirc;n Long Nh&acirc;n t&aacute;c phong m&agrave;.<br />\r\nThế nhưng l&agrave; b&acirc;y giờ Claudius cũng kh&ocirc;ng đồng dạng, hắn l&agrave; tiếp thụ qua gi&aacute;o dục bắt buộc, c&ugrave;ng gi&aacute;o dục cao đẳng thế kỷ mới &#39;Người l&agrave;m c&ocirc;ng t&aacute;c văn ho&aacute;&#39;, c&aacute;i kh&aacute;c kh&ocirc;ng n&oacute;i nhiều, c&aacute;i n&agrave;y &#39;Nitơ Nitơ no Mi&#39; năng lực, hắn c&ograve;n c&oacute; thể l&yacute; giải, n&oacute;i một c&aacute;ch đơn giản, liền l&agrave; kh&iacute; nitơ m&agrave;.<br />\r\nC&aacute;i đồ chơi n&agrave;y c&oacute; thể n&oacute;i l&agrave; ở khắp mọi nơi, trong kh&ocirc;ng kh&iacute; liền c&oacute;, kh&iacute; nitơ chiếm so c&ograve;n rất lớn, chiếm hữu kh&iacute; quyển 78% điểm nhi&ecirc;̀u, l&agrave; kh&iacute; trơ.<br />\r\nĐương nhi&ecirc;n, đ&acirc;y ch&iacute;nh l&agrave; đơn giản ch&uacute;t hiểu biết, muốn n&oacute;i khai ph&aacute;t c&aacute;i g&igrave;, Claudius cho đến nay cũng kh&ocirc;ng c&oacute; lại khai ph&aacute;t đ&acirc;u, bất qu&aacute; chỉ cần c&oacute; thể đem c&aacute;i n&agrave;y hệ Logia Tr&aacute;i &Aacute;c Quỷ cơ bản hiệu lực ph&aacute;t huy ra, c&aacute;i kia tại c&aacute;i n&agrave;y nhỏ tr&ecirc;n hoang đảo l&agrave;m c&aacute;i thổ b&aacute; vương c&ograve;n l&agrave; ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; vấn đề.<br />\r\nĐầu ti&ecirc;n tr&ecirc;n đảo n&agrave;y m&atilde;nh th&uacute; căn bản kh&ocirc;ng c&oacute; khả năng sẽ sử dụng Haki lực lượng, điều n&agrave;y sẽ đưa đến Claudius ho&agrave;n to&agrave;n kh&ocirc;ng cần c&acirc;n nhắc c&oacute; thể hay kh&ocirc;ng bị k&iacute;ch thương vấn đề, nguy&ecirc;n tố h&oacute;a liền c&oacute; thể giải quyết hết thảy.<br />\r\nTiếp theo, kh&ocirc;ng phải Claudius khoe khoang, d&ugrave; sao cho tới b&acirc;y giờ, ch&iacute;nh hắn đều kh&ocirc;ng nghĩ ra đến, c&aacute;i n&agrave;y Nitơ Nitơ no Mi sẽ bị c&aacute;i qu&aacute;i g&igrave; khắc chế, cho n&ecirc;n tạm thời kh&ocirc;ng cần để &yacute; c&aacute;i g&igrave; nguy&ecirc;n tố tương khắc đồ vật, đột nhi&ecirc;n xem x&eacute;t, năng lực n&agrave;y nếu l&agrave; kh&ocirc;ng c&oacute; Haki c&aacute;i g&igrave; đến chống lại, c&ograve;n giống như rất v&ocirc; địch?<br />\r\nCuối c&ugrave;ng, xử l&yacute; c&aacute;i n&agrave;y heo rừng thủ đoạn, cũng l&agrave; hệ Logia năng lực một loại cơ sở ứng dụng, căn bản kh&ocirc;ng cần Claudius đến ph&iacute; t&acirc;m tư khai ph&aacute;t c&aacute;i g&igrave;, trực tiếp một c&aacute;i &#39;Cầm &ocirc;m&#39;, &ocirc;m lấy c&aacute;i n&agrave;y heo rừng c&aacute;i mũi, tiếp lấy đem th&acirc;n thể của m&igrave;nh mở ra, d&ugrave;ng nồng đậm lại v&ocirc; h&igrave;nh kh&iacute; nitơ đem kh&ocirc;ng kh&iacute; chung quanh tất cả đều đ&egrave; &eacute;p gạt ra, h&igrave;nh th&agrave;nh một loại kh&iacute; nitơ mười phần kh&ocirc;ng gian, sau đ&oacute; c&aacute;i n&agrave;y heo rừng liền tự nhi&ecirc;n m&agrave; vậy bởi v&igrave; kh&ocirc;ng c&aacute;ch n&agrave;o hấp thụ đến trong kh&ocirc;ng kh&iacute; dưỡng kh&iacute;, cuối c&ugrave;ng thiếu dưỡng ngạt thở m&agrave; chết rồi.<br />\r\nL&agrave;m xong đ&acirc;y hết thảy về sau, Claudius mới chậm r&atilde;i kh&ocirc;i phục được b&igrave;nh thường trạng th&aacute;i, sờ l&ecirc;n cằm nh&igrave;n một ch&uacute;t c&aacute;i n&agrave;y heo rừng, kh&oacute;e miệng hơi vểnh l&ecirc;n, nhẹ giọng tự nhủ: &quot;Tổng thể m&agrave; n&oacute;i, ta c&aacute;i n&agrave;y xuy&ecirc;n qua bắt đầu c&ograve;n l&agrave; rất kh&ocirc;ng tệ nha, mặc d&ugrave; c&oacute; ch&uacute;t cẩu huyết, bất qu&aacute; c&aacute;i n&agrave;y Thi&ecirc;n Long Nh&acirc;n th&acirc;n phận l&agrave; thực sự, tiếp theo, c&ograve;n tự mang một c&aacute;i hệ Logia Tr&aacute;i &Aacute;c Quỷ năng lực. . . Tiếp xuống chỉ phải thật tốt đặt nền m&oacute;ng, hẳn l&agrave; c&oacute; thể trong c&aacute;i thế giới n&agrave;y ph&aacute;t huy điểm t&aacute;c dụng, cải biến thứ g&igrave;. . .&quot;<br />\r\nVừa n&oacute;i, Claudius một b&ecirc;n đem b&ecirc;n h&ocirc;ng treo đoản đao lấy xuống, c&aacute;i n&agrave;y đoản đao c&oacute; chút nước Wano v&otilde; sĩ đao cảm gi&aacute;c, nhưng l&agrave; lại ngắn rất nhiều, tựa hồ chỉ c&oacute; b&igrave;nh thường th&aacute;i đao hai phần ba d&agrave;i, bất qu&aacute; c&aacute;i n&agrave;y l&agrave;m c&ocirc;ng ngược lại l&agrave; tương đương tinh xảo, c&oacute; một loại đại tượng sư xuất phẩm kh&iacute; chất, m&agrave;u trắng tr&ecirc;n chu&ocirc;i đao bao v&acirc;y lấy tinh t&ecirc;́ tỉ mỉ sợi tơ đường v&acirc;n, vỏ đao nh&igrave;n qua cũng l&agrave; giản dị tự nhi&ecirc;n, chỉ l&agrave; hơi t&ocirc; điểm m&ocirc;̣t chút hoa văn m&agrave; th&ocirc;i.</p>\r\n\r\n<p><br />\r\nĐ&acirc;y cũng kh&ocirc;ng phải l&agrave; Claudius c&aacute;i n&agrave;y Thi&ecirc;n Long Nh&acirc;n từ nơi n&agrave;o vơ v&eacute;t tới đeo sức, m&agrave; l&agrave; Claudius xuy&ecirc;n qua m&agrave; đến ba c&aacute;i g&oacute;i qu&agrave; thứ nhất.<br />\r\nKh&ocirc;ng sai, l&agrave;m người xuy&ecirc;n việt, Claudius l&agrave;m sao c&oacute; thể kh&ocirc;ng c&oacute; điểm &#39;Vốn liếng&#39; đ&acirc;u? Nắm kh&ocirc;ng biết l&agrave; c&aacute;i g&igrave; thần minh vẫn l&agrave; y&ecirc;u qu&aacute;i ph&uacute;c, t&oacute;m lại hắn tại xuy&ecirc;n qua đến c&aacute;i thế giới n&agrave;y về sau, cũng thu được một ch&uacute;t &#39;Ban thưởng&#39; ?<br />\r\nC&aacute;i n&agrave;y thanh đoản đao, t&ecirc;n l&agrave; &#39;Shinsō&#39;, lại giải th&iacute;ch một ch&uacute;t, liền l&agrave; trong truyền thuyết Zanpakutou, l&agrave; Bleach thế giới b&ecirc;n trong, Tinh Linh đ&igrave;nh Tam phi&ecirc;n đội đội trưởng &middot; Ichimaru Gin trong tay bội đao.<br />\r\nB&acirc;y giờ xem như rơi v&agrave;o Claudius trong tay, bất qu&aacute; c&acirc;y đao n&agrave;y giống như tại xuy&ecirc;n qua thế giới thời điểm, cũng bị Thế Giới chi lực sửa một c&aacute;i, cũng kh&ocirc;ng c&oacute; Bleach thế giới b&ecirc;n trong cường lực như vậy, nhưng cũng l&agrave; hiếm c&oacute; vũ kh&iacute; tốt.<br />\r\nVẫn như cũ l&agrave; c&oacute; thể d&agrave;i c&oacute; thể ngắn, tốc độ cực nhanh, chỉ bất qu&aacute; co v&agrave;o tốc độ kh&ocirc;ng c&oacute; gấp năm trăm lần vận tốc &acirc;m thanh khoa trương như vậy th&ocirc;i, Claudius th&ocirc;ng qua kh&ocirc;ng ngừng th&iacute; nghiệm, tổng kết một c&aacute;i, Shinsō xạ tốc b&acirc;y giờ cũng ch&iacute;nh l&agrave; nhiều nhất ba lượng lần tốc độ &acirc;m thanh kh&ocirc;ng dậy nổi.<br />\r\nC&aacute;i n&agrave;y ba lượng lần vận tốc &acirc;m thanh, nghe mặc d&ugrave; rất nhanh, bất qu&aacute; cũng ch&iacute;nh l&agrave; vẻn vẹn đối tại b&igrave;nh thường người m&agrave; n&oacute;i xong, s&uacute;ng k&iacute;p b&ecirc;n trong, đạn xạ tốc kh&ocirc;ng sai biệt lắm c&ugrave;ng vận tốc &acirc;m thanh tương đương, cho n&ecirc;n c&aacute;i n&agrave;y Shinsō tốc độ, cũng ch&iacute;nh l&agrave; đạn xạ tốc ba lượng lần th&ocirc;i, đối ph&oacute; người b&igrave;nh thường, tự nhi&ecirc;n l&agrave; như c&oacute; thần trợ, nhưng l&agrave; đối với cao thủ tới n&oacute;i, giống như cũng liền như thế?<br />\r\nMặc d&ugrave; bị Thế Giới chi lực sửa đổi qua sau uy lực giảm nhiều, bất qu&aacute; Claudius ngược lại l&agrave; vốn kh&ocirc;ng c&oacute; để &yacute;, một c&aacute;i c&oacute; thể d&agrave;i c&oacute; thể ngắn, kh&ocirc;ng thể ph&aacute; vỡ bội đao đ&atilde; rất tốt được kh&ocirc;ng? Hắn lại kh&ocirc;ng định cả một đời dựa v&agrave;o đao ăn cơm, cho n&ecirc;n đối với c&aacute;i n&agrave;y ngược lại l&agrave; vui vẻ tiếp nhận.<br />\r\nTừ trong vỏ đao r&uacute;t ra Shinsō đến về sau, Claudius tại c&aacute;i kia to lớn heo rừng tr&ecirc;n th&acirc;n mở mấy c&aacute;i khoa trương vết thương, đ&acirc;y l&agrave; đang lấy m&aacute;u, nếu l&agrave; kh&ocirc;ng đem thịt n&agrave;y b&ecirc;n trong m&aacute;u đặt sạch sẽ, bắt đ&acirc;̀u ăn sẽ c&oacute; một cỗ l&agrave;m cho người cảm thấy buồn n&ocirc;n m&ugrave;i h&ocirc;i thối, Claudius tại trước mấy ng&agrave;y kinh nghiệm chưa đủ t&igrave;nh huống dưới, đ&atilde; chịu qua c&aacute;i n&agrave;y khổ, cho n&ecirc;n v&igrave; m&igrave;nh cảm gi&aacute;c c&ugrave;ng vị gi&aacute;c, nhiều &iacute;t vẫn l&agrave; hạ điểm c&ocirc;ng phu thả lấy m&aacute;u tốt.<br />\r\nShinsō l&agrave; c&aacute;i thứ nhất g&oacute;i qu&agrave; lớn, m&agrave; c&aacute;i kia c&aacute;i thứ hai g&oacute;i qu&agrave; lớn, Claudius ngược lại l&agrave; cảm gi&aacute;c v&ocirc; c&ugrave;ng c&oacute; &yacute; tứ, đ&oacute; ch&iacute;nh l&agrave; hắn c&aacute;i kia c&oacute; thể đủ tự do lệnh tim đập rộn l&ecirc;n năng lực, tục xưng --- King Engine!</p>', '0', 'king-engine', 1, '3', '2022-07-31 07:50:46', '2022-08-01 02:40:15'),
(5, '4', 'Tìm kiếm St.Claudius?', '<p>C&aacute;i gọi l&agrave; King Engine, c&aacute;i n&agrave;y Haki t&ecirc;n tuổi đến từ One-Punch Man thế giới b&ecirc;n trong, c&aacute;i kia được xưng l&agrave; &#39;Tr&ecirc;n mặt đất mạnh nhất nam nh&acirc;n&#39; King trước đưa kỹ năng, n&oacute;i l&agrave; một khi hắn tiến v&agrave;o loại trạng th&aacute;i n&agrave;y, chẳng kh&aacute;c n&agrave;o l&agrave; muốn b&atilde;o nổi, một gi&acirc;y sau l&agrave; c&oacute; thể đem địch nh&acirc;n của hắn ch&ugrave;y th&agrave;nh thịt n&aacute;t c&aacute;i g&igrave;.<br />\r\nTr&ecirc;n thực tế, c&aacute;i n&agrave;y King Engine liền l&agrave; c&aacute;i &#39;Tr&ograve; cười&#39; m&agrave; th&ocirc;i, tr&ecirc;n mặt đất mạnh nhất nam nh&acirc;n, tr&ecirc;n bản chất liền l&agrave; c&aacute;i nhặt nhạnh chỗ tốt Vương, thuộc về mở ra Haoushoku vận kh&iacute; nam nh&acirc;n, đặt ở One Piece thế giới, đơn giản c&ugrave;ng Buggy đại thần kh&oacute; ph&acirc;n cao thấp.<br />\r\nKing Engine hiệu quả liền l&agrave; nhịp tim kh&ocirc;ng ngừng gia tốc m&agrave; th&ocirc;i, King liền l&agrave; dựa v&agrave;o lấy c&aacute;i n&agrave;y &#39;Năng lực&#39;, bị thần thoại đến &#39;Tr&ecirc;n mặt đất mạnh nhất&#39; t&igrave;nh trạng.<br />\r\nL&uacute;c n&agrave;y Claudius lấy được năng lực n&agrave;y cũng l&agrave; kh&ocirc;ng sai biệt lắm, liền l&agrave; nhịp tim kh&ocirc;ng ngừng gia tốc th&ocirc;i, ngay từ đầu, khi lấy được năng lực n&agrave;y thời điểm, l&agrave;m một c&aacute;i phải thiết thực nam nh&acirc;n, Claudius c&ograve;n v&ocirc; c&ugrave;ng bất đắc dĩ, đ&acirc;y kh&ocirc;ng phải k&eacute;o con b&ecirc; sao?<br />\r\nC&oacute; thể hay kh&ocirc;ng cho điểm kh&aacute;c như thế xốc nổi năng lực a? Thực sự kh&ocirc;ng được, ta cũng nguyện &yacute; từ bỏ ta c&aacute;i n&agrave;y một đầu t&uacute; lệ t&oacute;c v&agrave;ng, biến th&agrave;nh c&aacute;i t&ecirc;n trọc, ta cũng c&oacute; thể tiếp nhận a!</p>', '<p>C&aacute;i gọi l&agrave; King Engine, c&aacute;i n&agrave;y Haki t&ecirc;n tuổi đến từ One-Punch Man thế giới b&ecirc;n trong, c&aacute;i kia được xưng l&agrave; &#39;Tr&ecirc;n mặt đất mạnh nhất nam nh&acirc;n&#39; King trước đưa kỹ năng, n&oacute;i l&agrave; một khi hắn tiến v&agrave;o loại trạng th&aacute;i n&agrave;y, chẳng kh&aacute;c n&agrave;o l&agrave; muốn b&atilde;o nổi, một gi&acirc;y sau l&agrave; c&oacute; thể đem địch nh&acirc;n của hắn ch&ugrave;y th&agrave;nh thịt n&aacute;t c&aacute;i g&igrave;.<br />\r\nTr&ecirc;n thực tế, c&aacute;i n&agrave;y King Engine liền l&agrave; c&aacute;i &#39;Tr&ograve; cười&#39; m&agrave; th&ocirc;i, tr&ecirc;n mặt đất mạnh nhất nam nh&acirc;n, tr&ecirc;n bản chất liền l&agrave; c&aacute;i nhặt nhạnh chỗ tốt Vương, thuộc về mở ra Haoushoku vận kh&iacute; nam nh&acirc;n, đặt ở One Piece thế giới, đơn giản c&ugrave;ng Buggy đại thần kh&oacute; ph&acirc;n cao thấp.<br />\r\nKing Engine hiệu quả liền l&agrave; nhịp tim kh&ocirc;ng ngừng gia tốc m&agrave; th&ocirc;i, King liền l&agrave; dựa v&agrave;o lấy c&aacute;i n&agrave;y &#39;Năng lực&#39;, bị thần thoại đến &#39;Tr&ecirc;n mặt đất mạnh nhất&#39; t&igrave;nh trạng.<br />\r\nL&uacute;c n&agrave;y Claudius lấy được năng lực n&agrave;y cũng l&agrave; kh&ocirc;ng sai biệt lắm, liền l&agrave; nhịp tim kh&ocirc;ng ngừng gia tốc th&ocirc;i, ngay từ đầu, khi lấy được năng lực n&agrave;y thời điểm, l&agrave;m một c&aacute;i phải thiết thực nam nh&acirc;n, Claudius c&ograve;n v&ocirc; c&ugrave;ng bất đắc dĩ, đ&acirc;y kh&ocirc;ng phải k&eacute;o con b&ecirc; sao?<br />\r\nC&oacute; thể hay kh&ocirc;ng cho điểm kh&aacute;c như thế xốc nổi năng lực a? Thực sự kh&ocirc;ng được, ta cũng nguyện &yacute; từ bỏ ta c&aacute;i n&agrave;y một đầu t&uacute; lệ t&oacute;c v&agrave;ng, biến th&agrave;nh c&aacute;i t&ecirc;n trọc, ta cũng c&oacute; thể tiếp nhận a!<br />\r\nD&ugrave; sao ngay từ đầu, Claudius chỉ coi c&aacute;i n&agrave;y c&oacute; thể lệnh tim đập rộn l&ecirc;n năng lực l&agrave; c&aacute;i &#39;Trang bức&#39; d&ugrave;ng năng lực, cũng kh&ocirc;ng cảm thấy nương tựa theo vận kh&iacute; của m&igrave;nh, c&oacute; thể dựa v&agrave;o năng lực n&agrave;y trở th&agrave;nh One Piece thế giới &#39;Tr&ecirc;n mặt đất mạnh nhất nam nh&acirc;n&#39; c&aacute;i g&igrave;.<br />\r\nBất qu&aacute; tại thực tế sử dụng th&iacute; nghiệm mấy lần về sau, Claudius ph&aacute;t hiện năng lực n&agrave;y vẫn c&oacute; ch&uacute;t lợi hại, nhịp tim kh&ocirc;ng ngừng gia tốc, tất nhi&ecirc;n tăng cường huy&ecirc;́t dịch tại thể nội lưu th&ocirc;ng tốc độ, c&ugrave;ng l&uacute;c hẳn l&agrave; cũng c&oacute; thể tăng cường tạo m&aacute;u c&ocirc;ng năng vận chuyển.<br />\r\nTại mấy lần nghi&ecirc;n cứu c&ugrave;ng th&iacute; nghiệm b&ecirc;n trong, c&aacute;i kh&aacute;c kh&ocirc;ng n&oacute;i đến, Claudius ch&iacute; &iacute;t x&aacute;c định một sự kiện, c&aacute;i kia ch&iacute;nh l&agrave; một khi mở ra &#39;King Engine&#39;, để nhịp tim bắt đầu gia tăng tốc độ về sau, hắn đ&uacute;ng l&agrave; c&oacute; thể tiến v&agrave;o một loại &#39;Bạo tẩu&#39; trạng th&aacute;i b&ecirc;n trong, xưng l&agrave; &#39;Cuồng bạo trạng th&aacute;i&#39; cũng kh&ocirc;ng sao.<br />\r\nC&oacute; thể sử dụng King Engine, trong khoảng thời gian ngắn, đề cao th&acirc;n thể của m&igrave;nh tố chất, vừa rồi c&aacute;i kia đuổi theo heo rừng l&uacute;c sở dụng gia tốc, ch&iacute;nh l&agrave; ở trong đ&oacute; hiệu quả thứ nhất.<br />\r\nChỉ bất qu&aacute; đặt ở hiện tại, lấy Claudius b&acirc;y giờ tố chất th&acirc;n thể c&ugrave;ng năng lực, nhiều nhất cũng chỉ c&oacute; thể l&agrave;m đến ph&acirc;n thượng n&agrave;y, bất qu&aacute; tiếp theo có th&ecirc;̉ khai ph&aacute;t c&ugrave;ng truy đến c&ugrave;ng khả năng, c&ograve;n l&agrave; rất cao.<br />\r\nMột khi muốn l&agrave; th&acirc;n thể của m&igrave;nh tố chất đi theo, lực lượng, tốc độ loại h&igrave;nh đều l&ecirc;n đi, như vậy th&ocirc;ng qua King Engine đột nhi&ecirc;n đến c&aacute;i bộc ph&aacute;t, kh&ocirc;ng chừng liền c&oacute; thể trở th&agrave;nh một c&aacute;i đại s&aacute;t chi&ecirc;u, đem nguy&ecirc;n bản thực lực kh&ocirc;ng k&eacute;m g&igrave; ch&iacute;nh m&igrave;nh địch nh&acirc;n cưỡng &eacute;p nghiền &eacute;p một c&aacute;i loại h&igrave;nh.<br />\r\nNếu l&agrave; tại phối hợp hơn mấy c&acirc;u trang bức lời kịch, tỉ như &#39;Ta muốn bắt đầu chăm ch&uacute;&#39;, &#39;Xem ra ngươi l&agrave; đ&aacute;ng gi&aacute; ta chăm ch&uacute; đối đ&atilde;i đối thủ&#39;, &#39;Nghĩ kh&ocirc;ng ra ngươi c&oacute; thể đem ta bức đến t&igrave;nh cảnh như thế&#39; như vậy lời n&oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\nChậc chậc, bức c&aacute;ch một c&aacute;i liền tăng l&ecirc;n rất nhiều a?<br />\r\nV&igrave; vậy đối với c&aacute;i n&agrave;y &#39;King Engine&#39; năng lực, Claudius trước mắt vẫn l&agrave; rất h&agrave;i l&ograve;ng, v&ocirc; luận l&agrave; tại thực tế c&ocirc;ng dụng b&ecirc;n tr&ecirc;n, hay l&agrave; tại trang bức c&ocirc;ng dụng b&ecirc;n tr&ecirc;n, có th&ecirc;̉ khai ph&aacute;t tiềm lực c&ograve;n l&agrave; rất cao, về sau nhất định phải thật tốt nghi&ecirc;n cứu suy nghĩ một ch&uacute;t.<br />\r\nVề phần c&aacute;i n&agrave;y cái cu&ocirc;́i cùng người xuy&ecirc;n việt g&oacute;i qu&agrave; lớn, th&igrave; l&agrave; một c&aacute;i t&ecirc;n l&agrave; &#39;Reiji Maigo&#39; năng lực, tựa hồ l&agrave; tới từ c&aacute;i g&igrave; Shakugan no Shana thế giới, bất qu&aacute; Claudius đối với c&aacute;i n&agrave;y ngược lại l&agrave; kh&ocirc;ng c&oacute; c&aacute;i g&igrave; hi&ecirc;̉u rõ.<br />\r\nBất qu&aacute; cũng may trong đầu của hắn l&agrave; c&oacute; một c&aacute;i s&aacute;ch thuyết minh, &iacute;t nhiều khiến hắn c&ograve;n c&oacute; thể biết l&agrave; c&aacute;i chuyện g&igrave; xảy ra.<br />\r\nC&aacute;i n&agrave;y Reiji Maigo, &yacute; vị &#39;C&oacute; thể l&agrave;m cho thời gian lạc đường tại 0 giờ&#39;, đơn giản điểm tới n&oacute;i, liền l&agrave; c&oacute; thể để người sử dụng tại 0 điểm thời khắc, kh&ocirc;i phục to&agrave;n bộ tồn tại chi lực.<br />\r\nC&aacute;i n&agrave;y tồn tại chi lực c&aacute;i g&igrave;, tr&ecirc;n c&aacute;i thế giới n&agrave;y đương nhi&ecirc;n kh&ocirc;ng khả năng sẽ c&oacute;, Claudius cũng v&igrave; vậy m&agrave; khốn nhiễu v&agrave;i ng&agrave;y, về sau th&ocirc;ng qua một ch&uacute;t th&iacute; nghiệm c&ugrave;ng nghi&ecirc;n cứu, cuối c&ugrave;ng l&agrave; hơi hiểu r&otilde; năng lực n&agrave;y một ch&uacute;t chỗ d&ugrave;ng.<br />\r\nN&oacute;i trắng ra l&agrave;, c&oacute; điểm giống l&agrave; Hokage thế giới b&ecirc;n trong &#39;&Acirc;m Phong Ấn&#39; cảm gi&aacute;c, mặc d&ugrave; kh&ocirc;ng c&oacute; c&aacute;i g&igrave; tồn tại chi lực, nhưng l&agrave; c&oacute; thể đem trong v&ograve;ng một ng&agrave;y c&ograve;n lại những tinh lực kia, thể lực loại h&igrave;nh đồ vật, trữ tồn tại c&aacute;i n&agrave;y Reiji Maigo b&ecirc;n trong.<br />\r\nMột khi c&oacute; cần, liền c&oacute; thể tại 0 giờ mở ra năng lực n&agrave;y, căn cứ chứa đựng tại Reiji Maigo b&ecirc;n trong thể lực loại h&igrave;nh lượng, đến kh&ocirc;i phục trạng th&aacute;i của m&igrave;nh.<br />\r\nD&ugrave; sao cũng phải tới n&oacute;i, xem ra đến b&acirc;y giờ, cũng kh&ocirc;ng t&iacute;nh l&agrave; c&aacute;i cỡ n&agrave;o thứ lợi hại, nhưng ng&agrave;y sau c&oacute; lẽ c&oacute; thể ph&aacute;t huy ra một ch&uacute;t t&aacute;c dụng rất lớn? Tỉ như đ&aacute;nh đ&aacute;nh l&acirc;u d&agrave;i loại h&igrave;nh? Lại hoặc l&agrave; d&ugrave;ng để l&agrave;m một ch&uacute;t xấu hổ sự t&igrave;nh?<br />\r\n&quot;Cũng đ&uacute;ng vậy a, ta Claudius n&oacute;i thế n&agrave;o cũng l&agrave; Thi&ecirc;n Long Nh&acirc;n, thế giới qu&yacute; tộc, c&aacute;i kia lấy được mười c&aacute;i t&aacute;m c&aacute;i l&atilde;o b&agrave; loại h&igrave;nh, ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; vấn đề chứ? Thậm ch&iacute; c&ograve;n hợp l&yacute; hợp ph&aacute;p, đến l&uacute;c đ&oacute; nếu l&agrave; thận ti&ecirc;u hao, th&acirc;n thể kh&ocirc;ng ki&ecirc;n tr&igrave; nổi, mở Reiji Maigo c&aacute;i g&igrave;. . . Chậc chậc, ngẫm lại đều rất k&iacute;ch th&iacute;ch a, &aacute;c chiến đến b&igrave;nh minh giống như ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; vấn đề?&quot; Claudius một b&ecirc;n chờ lấy c&aacute;i kia heo rừng m&aacute;u tươi chảy hết, một b&ecirc;n sờ l&ecirc;n cằm tưởng tượng lấy dựa v&agrave;o Reiji Maigo năng lực n&agrave;y mỹ hảo tương lai.<br />\r\nC&aacute;c loại c&aacute;i n&agrave;y heo rừng m&aacute;u tươi đều tại một b&ecirc;n kh&aacute;c hội tụ th&agrave;nh một c&aacute;i vũng m&aacute;u nhỏ về sau, Claudius mới từ m&igrave;nh c&aacute;i n&agrave;y trong huyễn tưởng thu hồi suy nghĩ, tiếp lấy một b&ecirc;n d&ugrave;ng Shinsō cắt c&aacute;i kia cảm gi&aacute;c tương đối tốt vị tr&iacute; khối thịt, vừa c&oacute; ch&uacute;t kh&oacute; chịu nỉ non n&oacute;i: &quot;N&oacute;i đến, từ ta gặp nạn đến b&acirc;y giờ, đều c&oacute; hai tuần thời gian a? Những người kia kh&ocirc;ng sai biệt lắm cũng n&ecirc;n để đội cứu viện đến ứng ph&oacute; ứng ph&oacute; việc phải l&agrave;m đi? D&ugrave; sao đổi lại l&agrave; ta lời n&oacute;i, cũng sẽ kh&ocirc;ng cho l&agrave; ta c&aacute;i n&agrave;y tiền nhiệm củi mục c&oacute; thể tại tao ngộ tai nạn tr&ecirc;n biển về sau, tự lực c&aacute;nh sinh sống hai tuần thời gian l&acirc;u a. . .&quot;</p>\r\n\r\n<p><br />\r\nTại một chiếc to lớn qu&acirc;n hạm boong thuyền, l&uacute;c n&agrave;y một người trung ni&ecirc;n nam nh&acirc;n người kho&aacute;c ch&iacute;nh nghĩa &aacute;o kho&aacute;c, miệng b&ecirc;n trong cắn một điếu xi g&agrave;, mang theo một cặp k&iacute;nh m&aacute;t, hai tay &ocirc;m ở trước ngực, im ắng nh&igrave;n qua v&ugrave;ng biển n&agrave;y ph&iacute;a trước.<br />\r\nHắn c&oacute; một đầu m&agrave;u t&iacute;m nhạt lộn xộn t&oacute;c ngắn, th&acirc;n h&igrave;nh mười phần cường tr&aacute;ng, mặc d&ugrave; c&oacute; ch&uacute;t thu liễm, nhưng l&agrave; trong mơ hồ, vẫn như cũ c&oacute; thể từ tr&ecirc;n người hắn cảm gi&aacute;c được một cỗ kh&iacute; thế cường h&atilde;n.<br />\r\nC&aacute;i n&agrave;y nam nh&acirc;n, ch&iacute;nh l&agrave; hiện nay Hải Qu&acirc;n bản bộ Đại tướng &middot; Black Arm &middot; Zephyr, muốn n&oacute;i Zephyr gia hỏa n&agrave;y, cả đời c&oacute; thể n&oacute;i l&agrave; bi tr&aacute;ng v&ocirc; c&ugrave;ng, th&acirc;n l&agrave; Đại tướng, lại l&ograve;ng mang nh&acirc;n &aacute;i, tại thảo phạt Hải tặc c&aacute;c loại to to nhỏ nhỏ v&ocirc; số chiến tranh b&ecirc;n trong, chưa từng c&oacute; ch&acirc;n ch&iacute;nh giết chết qua một t&ecirc;n Hải tặc, cũng bởi vậy được xưng l&agrave; kh&ocirc;ng giết Đại tướng.<br />\r\nNhưng cũng ch&iacute;nh v&igrave; hắn phần n&agrave;y nh&acirc;n &aacute;i chi t&acirc;m, đưa đến cắt cỏ kh&ocirc;ng thể trừ tận gốc, tại sau n&agrave;y một ng&agrave;y b&ecirc;n trong, hắn y&ecirc;u dấu vợ con nh&oacute;m bị đến đ&acirc;y b&aacute;o th&ugrave; hung t&agrave;n Hải tặc nh&oacute;m giết hại.<br />\r\nSau đ&oacute; t&acirc;m tro &yacute; lạnh Zephyr từ đi Hải Qu&acirc;n bản bộ Đại tướng chức tr&aacute;ch, đem nguy&ecirc;n bản hứng th&uacute; trở th&agrave;nh chủ nghiệp, trở th&agrave;nh Hải Qu&acirc;n bản bộ sĩ quan trại huấn luyện đạo sư, mang ra ngo&agrave;i rất nhiều ưu t&uacute; học sinh, c&oacute; thể n&oacute;i hậu kỳ Hải Qu&acirc;n bản bộ đại đa số tướng lĩnh, đều l&agrave; hắn một tay bồi dưỡng ra.<br />\r\nC&aacute;i n&agrave;y về sau, hắn lại tao ngộ một ch&uacute;t l&agrave;m cho người cảm thấy bi thương sự t&igrave;nh, cuối c&ugrave;ng đối Ch&iacute;nh Phủ Thế Giới c&ugrave;ng Hải qu&acirc;n cảm nhận được tuyệt vọng, dứt kho&aacute;t ki&ecirc;n quyết rời đi Hải qu&acirc;n, th&agrave;nh lập &#39;NEO Hải qu&acirc;n&#39;, tiếp lấy muốn hủy diệt thế giới mới, cuối c&ugrave;ng chết tại đương nhi&ecirc;̣m Hải qu&acirc;n Đại tướng Borsalino trong tay, kết th&uacute;c m&igrave;nh c&aacute;i kia bi thảm nhưng lại mười phần oanh liệt một đời.<br />\r\nĐương nhi&ecirc;n, vậy cũng l&agrave; về sau có khả năng chuyện sẽ xảy ra, b&acirc;y giờ Zephyr c&ograve;n kh&ocirc;ng c&oacute; tao ngộ nhiều như vậy bi thương sự t&igrave;nh đ&acirc;u, hắn hiện tại c&oacute; thể n&oacute;i l&agrave; hăng h&aacute;i.<br />\r\nL&uacute;c n&agrave;y hắn dẫn đầu hạm đội, tại chấp h&agrave;nh nhiệm vụ n&oacute;i đơn giản cũng rất đơn giản, n&oacute;i phức tạp, cũng l&agrave; phức tạp, c&aacute;i kia ch&iacute;nh l&agrave; t&igrave;m kiếm hai tuần nhiều trước, tại v&ugrave;ng biển n&agrave;y b&ecirc;n trong tao ngộ tai nạn tr&ecirc;n biển Thi&ecirc;n Long Nh&acirc;n &middot; Donquixote &middot; Claudius th&aacute;nh!<br />\r\nKỳ thật Zephyr rất kh&ocirc;ng muốn đến tham dự loại chuyện n&agrave;y, bởi v&igrave; rất r&otilde; r&agrave;ng, Thi&ecirc;n Long Nh&acirc;n gặp nạn bị tr&igrave; ho&atilde;n hai tuần thời gian mới sưu cứu, c&aacute;i n&agrave;y ph&iacute;a sau khẳng định l&agrave; c&oacute; MariJoa b&ecirc;n trong những người kia &acirc;m mưu, l&agrave;m một t&ecirc;n Hải qu&acirc;n Đại tướng, đừng nh&igrave;n người ở b&ecirc;n ngo&agrave;i nh&oacute;m trước mặt phong quang v&ocirc; c&ugrave;ng, nhưng tại MariJoa trong mắt những người kia, cũng bất qu&aacute; l&agrave; c&aacute;i &#39;C&oacute; ch&uacute;t tiền đồ&#39; gia hỏa th&ocirc;i.<br />\r\nBởi vậy tham dự v&agrave;o loại chuyện n&agrave;y ở trong đến, phong hiểm t&iacute;nh c&ograve;n l&agrave; rất cao, nhưng vấn đề l&agrave; b&acirc;y giờ Hải Qu&acirc;n bản bộ, chỉ c&oacute; hai vị Đại tướng, c&aacute;i kia đ&aacute;ng chết Garp n&oacute;i c&aacute;i g&igrave; cũng kh&ocirc;ng nguyện &yacute; thăng nhiệm Đại tướng, Sengoku t&ecirc;n kia c&ograve;n c&oacute; một số c&ograve;n lại chuyện trọng yếu phải xử l&yacute;.<br />\r\nHết lần n&agrave;y tới lần kh&aacute;c c&aacute;i n&agrave;y Thi&ecirc;n Long Nh&acirc;n nếu l&agrave; xảy ra chuyện, nhất định phải vận dụng nhất cao quy c&aacute;ch lực lượng tới thu thập cục diện rối rắm, cho n&ecirc;n v&ocirc; luận như thế n&agrave;o an b&agrave;i, đều phải c&oacute; một vị Đại tướng đi ra tọa trấn mới được, bởi vậy c&aacute;i n&agrave;y cục diện rối rắm, cuối c&ugrave;ng vẫn l&agrave; rơi v&agrave;o Zephyr tr&ecirc;n đầu.<br />\r\nCoi như Zephyr nghĩ đến những chuyện n&agrave;y thời điểm, một c&aacute;i tuổi trẻ lại c&oacute; ch&uacute;t lười biếng tiếng n&oacute;i từ ph&iacute;a sau hắn c&aacute;ch đ&oacute; kh&ocirc;ng xa truyền đến.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n&quot;Zephyr l&atilde;o sư, c&ograve;n đang suy nghĩ c&aacute;i kia Thi&ecirc;n Long Nh&acirc;n sự t&igrave;nh sao? Ta cảm thấy ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; tất yếu lo lắng như vậy, tr&igrave; ho&atilde;n hai tuần thời gian tai nạn tr&ecirc;n biển cứu viện, liền xem như đặt ở tương đối lợi hại gia hỏa tr&ecirc;n th&acirc;n, đo&aacute;n chừng cũng đều sớm bị trong biển c&aacute; mập nh&oacute;m ăn c&aacute;i hết sạch, c&agrave;ng đừng đề cập l&agrave;. . . Những c&aacute;i kia cao qu&yacute; Thi&ecirc;n Long Nh&acirc;n, chỗ ta xem ra lần n&agrave;y t&ecirc;n l&agrave; cứu viện nhiệm vụ, tr&ecirc;n thực tế, cũng bất qu&aacute; l&agrave; c&aacute;i chạy theo h&igrave;nh thức ch&iacute;nh trị nhiệm vụ a?&quot;<br />\r\nNghe thanh &acirc;m n&agrave;y, Zephyr Đại tướng xoay người qua đến, nh&igrave;n tho&aacute;ng qua ph&iacute;a sau m&igrave;nh gia hoả kia, kh&oacute;e miệng kh&ocirc;ng khỏi đ&atilde; phủ l&ecirc;n một v&ograve;ng &yacute; cười, hắn hai năm trước tại mới khai trương Hải qu&acirc;n sĩ quan trong trại huấn luyện bằng v&agrave;o hứng th&uacute; l&agrave;m đoạn thời gian huấn luyện vi&ecirc;n, cũng v&igrave; vậy m&agrave; kh&aacute;m ph&aacute; mấy mầm mống tốt, b&ecirc;n trong một c&aacute;i, liền l&agrave; b&acirc;y giờ c&aacute;i n&agrave;y đ&atilde; tr&ecirc;n vai treo Thiếu tướng qu&acirc;n h&agrave;m gia hỏa, Kuzan.<br />\r\n&quot;Ngươi n&oacute;i kh&ocirc;ng sai đ&acirc;u, Kuzan, chuyến n&agrave;y vốn l&agrave; c&oacute; lấy cực lớn khả năng sẽ tay kh&ocirc;ng m&agrave; về, bởi vậy n&oacute;i n&oacute; l&agrave; cứu viện nhiệm vụ, tr&ecirc;n thực tế l&agrave; chạy theo h&igrave;nh thức ch&iacute;nh trị nhiệm vụ cũng kh&ocirc;ng sai, thế nhưng l&agrave;. . . Sợ l&agrave; sợ, sau khi trở về, những c&aacute;i kia cao cao tại thượng Thi&ecirc;n Long Nh&acirc;n đại l&atilde;o gia, sẽ đem St.Claudius chết, đẩy tại ch&uacute;ng ta Hải Qu&acirc;n bản bộ cứu viện bất lực cớ b&ecirc;n tr&ecirc;n, đến l&uacute;c đ&oacute;. . . Coi như c&oacute; nỗi khổ kh&ocirc;ng n&oacute;i được a.&quot; Zephyr c&oacute; ch&uacute;t bất đắc dĩ lắc đầu, mở miệng n&oacute;i ra.<br />\r\nL&uacute;c n&agrave;y coi như tuổi trẻ Kuzan hiển nhi&ecirc;n l&agrave; kh&ocirc;ng nghĩ tới chuyện n&agrave;y, hơi kinh ngạc nh&igrave;n một ch&uacute;t Zephyr về sau, mở miệng hỏi: &quot;C&aacute;i kia muốn l&agrave;m sao a? Bọn hắn hẳn l&agrave; sẽ kh&ocirc;ng kh&ocirc;ng n&oacute;i l&yacute; lẽ như vậy a?&quot;<br />\r\n&quot;Ha ha ha, chờ ngươi ng&agrave;y n&agrave;o trở th&agrave;nh Đại tướng, c&ugrave;ng những người kia tiếp x&uacute;c nhiều hơn, liền sẽ r&otilde; r&agrave;ng, bọn hắn những c&aacute;i kia Thi&ecirc;n Long Nh&acirc;n đại l&atilde;o gia, thật đ&uacute;ng l&agrave; liền l&agrave; kh&ocirc;ng n&oacute;i l&yacute; lẽ như vậy. . .&quot; Zephyr cười ha hả mở miệng đối với m&igrave;nh c&aacute;i n&agrave;y đệ tử n&oacute;i ra.<br />\r\nHơi hơi dừng một ch&uacute;t về sau, Zephyr hoạt động một ch&uacute;t bả vai, nh&igrave;n về ph&iacute;a mặt biển, nhẹ giọng mở miệng n&oacute;i ra: &quot;Cho n&ecirc;n n&oacute;i, cho d&ugrave; l&agrave; chạy theo h&igrave;nh thức ch&iacute;nh trị nhiệm vụ, cũng phải tận lực l&agrave;m đến ho&agrave;n mỹ, kh&ocirc;ng cần cho những c&aacute;i kia đại l&atilde;o gia lưu lại nhược điểm g&igrave;, mặc d&ugrave; c&aacute;i n&agrave;y St.Claudius t&aacute;m ch&iacute;n phần mười đ&atilde; t&aacute;ng th&acirc;n biển rộng, nhưng ch&uacute;ng ta kh&ocirc;ng thể &ocirc;m cái ý nghĩ này h&agrave;nh động, muốn lấy hắn c&ograve;n c&oacute; thể c&ograve;n sống l&agrave; điều kiện ti&ecirc;n quyết m&agrave; tiến h&agrave;nh sưu cứu, chỉ c&oacute; dạng n&agrave;y, t&agrave;i năng tận lực giảm bớt tương lai c&oacute; thể sẽ vấn đề xuất hiện.&quot;<br />\r\nKuzan gia hỏa n&agrave;y sờ lấy m&igrave;nh c&aacute;i kia c&oacute; ch&uacute;t xo&atilde; tung t&oacute;c nghĩ nghĩ, tiếp lấy gật gật đầu n&oacute;i: &quot;Quả nhi&ecirc;n ta c&ograve;n qu&aacute; trẻ đ&acirc;u, l&atilde;o sư ng&agrave;i n&oacute;i c&oacute; đạo l&yacute;, như vậy ch&uacute;ng ta hiện giai đoạn muốn l&agrave;m g&igrave;?&quot;<br />\r\n&quot;Trong v&ugrave;ng biển n&agrave;y, chỉ c&oacute; một t&ograve;a hoang đảo, nếu như, ta n&oacute;i l&agrave; nếu như, c&oacute; một phần vạn tỷ lệ, vị kia St.Claudius c&ograve;n sống, hắn chỉ c&oacute; thể l&agrave; xuất hiện tại c&aacute;i hoang đảo kia b&ecirc;n tr&ecirc;n, cho n&ecirc;n việc cấp b&aacute;ch, l&agrave; tại triển khai mặt biển lục so&aacute;t c&ugrave;ng l&uacute;c, lấy tốc độ nhanh nhất đến h&ograve;n đảo kia, b&ecirc;n tr&ecirc;n đi t&igrave;m một ch&uacute;t c&oacute; hay kh&ocirc;ng St.Claudius tung t&iacute;ch.&quot; Zephyr Đại tướng nhẹ giọng mở miệng n&oacute;i ra.<br />\r\nKuzan nghe vậy gật gật đầu, sau đ&oacute; tại hơi suy tư một trận m&agrave; về sau, ngẩng đầu nh&igrave;n về ph&iacute;a Zephyr Đại tướng, nhẹ giọng hỏi: &quot;Như vậy l&atilde;o sư, nếu như ng&agrave;i n&oacute;i c&aacute;i kia &#39;Một phần vạn&#39; thật ph&aacute;t sinh nữa nha? Ta nh&igrave;n &yacute; tứ ph&iacute;a tr&ecirc;n giống như cũng kh&ocirc;ng hi vọng St.Claudius c&ograve;n sống a. . .&quot;<br />\r\n&quot;Chớ n&oacute;i nữa, Kuzan.&quot; Zephyr d&ugrave;ng hết sức nghi&ecirc;m t&uacute;c &aacute;nh mắt nh&igrave;n về ph&iacute;a Kuzan, ngăn lại lời của hắn.<br />\r\nHơi hơi dừng một ch&uacute;t về sau, Zephyr vỗ vỗ Kuzan bả vai, khuy&ecirc;n bảo n&oacute;i: &quot;Kuzan, ngươi c&ograve;n trẻ, c&oacute; rất nhiều chuyện đều kh&ocirc;ng minh bạch, nhưng l&agrave; chỉ c&oacute; một điểm, ta hi vọng ngươi c&oacute; thể nhớ kỹ, c&aacute;i kia ch&iacute;nh l&agrave; &iacute;t c&ugrave;ng những c&aacute;i kia Thi&ecirc;n Long Nh&acirc;n nh&oacute;m tr&uacute;ng v&agrave;o quan hệ, c&oacute; lẽ ph&iacute;a tr&ecirc;n x&aacute;c thực c&oacute; người kh&ocirc;ng hy vọng hắn c&ograve;n sống, nhưng ch&uacute;ng ta chỉ cần l&agrave;m tốt ch&uacute;ng ta chuyện n&ecirc;n l&agrave;m l&agrave; đủ rồi, đừng nghĩ nhiều như vậy v&ocirc; &iacute;ch, liền xem như c&oacute; một phần vạn x&aacute;c suất, St.Claudius c&ograve;n sống, như vậy ta liền an an ổn ổn đem hắn đưa về MariJoa ch&iacute;nh l&agrave;. . . R&otilde; chưa?&quot;<br />\r\nKuzan đối với hắn Zephyr l&atilde;o sư rất t&ocirc;n trọng, bởi vậy tại Zephyr n&oacute;i ra những lời n&agrave;y về sau, Kuzan cũng kh&ocirc;ng c&oacute; lại c&oacute; c&aacute;i g&igrave; dư thừa &yacute; nghĩ, rất nghi&ecirc;m t&uacute;c gật gật đầu, mở miệng n&oacute;i ra: &quot;Ta đ&atilde; biết, l&atilde;o sư, ta sẽ nhớ kỹ dạy bảo của ng&agrave;i.&quot;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:285px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'tim-kiem-stclaudius', 1, '3', '2022-07-31 07:51:48', '2022-08-01 02:40:16');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(6, '5', 'Hải Viên lịch cuối năm 1490?', '<p>Khi c&aacute;i kia hoang đảo xuất hiện tại qu&acirc;n hạm trong tầm mắt chi l&uacute;c, Zephyr Đại tướng c&ugrave;ng Kuzan biểu lộ đều l&agrave; sửng sốt một ch&uacute;t, bởi v&igrave; bọn họ nh&igrave;n thấy c&aacute;i kia tr&ecirc;n hoang đảo lại c&oacute; kh&oacute;i đen d&acirc;ng l&ecirc;n, c&aacute;i n&agrave;y đại biểu cho c&oacute; người ở ph&iacute;a tr&ecirc;n nh&oacute;m lửa a!<br />\r\n&quot;Kh&ocirc;ng phải đ&acirc;u? Chẳng lẽ lại thật đ&uacute;ng l&agrave; bắt gặp c&aacute;i kia một phần vạn x&aacute;c suất?&quot; Kuzan c&oacute; ch&uacute;t kh&oacute; tin nhẹ giọng nỉ non n&oacute;i.<br />\r\nZephyr &aacute;nh mắt l&oacute;e l&ecirc;n một c&aacute;i, sau đ&oacute; mở miệng n&oacute;i ra: &quot;C&oacute; lẽ l&agrave; thật bắt gặp, nhanh, mệnh lệnh qu&acirc;n hạm gia tốc!&quot;<br />\r\n&quot;C&oacute; khả năng hay kh&ocirc;ng l&agrave; s&eacute;t đ&aacute;nh c&aacute;i g&igrave;? Ta cảm gi&aacute;c liền xem như s&eacute;t đ&aacute;nh đốt l&ecirc;n trong n&uacute;i c&acirc;y cối. . . X&aacute;c suất cũng so St.Claudius c&ograve;n sống tỷ lệ muốn cao a. . .&quot; Kuzan ch&eacute;p miệng một c&aacute;i, mở miệng hỏi.<br />\r\n&quot;Mặc kệ l&agrave; c&aacute;i g&igrave;, cũng n&ecirc;n đi l&ecirc;n xem một ch&uacute;t!&quot; Zephyr gật gật đầu nhanh ch&oacute;ng mở miệng n&oacute;i ra.<br />\r\nSau đ&oacute; c&aacute;i n&agrave;y to lớn qu&acirc;n hạm liền lấy tốc độ cực nhanh hướng ph&iacute;a c&aacute;i kia hoang đảo phương hướng chạy tới.<br />\r\nL&uacute;c n&agrave;y ở tr&ecirc;n đỉnh n&uacute;i, Claudius một b&ecirc;n nướng c&aacute;i kia mang về to lớn thịt heo rừng khối, một b&ecirc;n từ đảo n&agrave;y b&ecirc;n tr&ecirc;n điểm cao nhất quan s&aacute;t đến bốn ph&iacute;a hải vực, tại hắn chỗ &aacute;nh mắt nh&igrave;n tới, cũng ph&aacute;t hiện mấy chiếc qu&acirc;n hạm ch&iacute;nh đang nhanh ch&oacute;ng hướng về b&ecirc;n n&agrave;y l&aacute;i tới.<br />\r\nCắn một c&aacute;i m&ugrave;i vị đ&oacute; phi thường kh&ocirc;ng mỹ vị thịt heo rừng, Claudius nhẹ giọng thở d&agrave;i một hơi, nỉ non n&oacute;i: &quot;Ai, kh&ocirc;ng uổng c&ocirc;ng ta đợi l&acirc;u như vậy, những t&ecirc;n khốn kiếp kia nh&oacute;m rốt cục bỏ được ph&aacute;i người đến &#39;Cứu ta&#39; sao? Lại hoặc l&agrave; n&oacute;i. . . Ph&aacute;i người đến ch&iacute;nh thức x&aacute;c định ta tin chết?&quot;</p>', '<p>Khi c&aacute;i kia hoang đảo xuất hiện tại qu&acirc;n hạm trong tầm mắt chi l&uacute;c, Zephyr Đại tướng c&ugrave;ng Kuzan biểu lộ đều l&agrave; sửng sốt một ch&uacute;t, bởi v&igrave; bọn họ nh&igrave;n thấy c&aacute;i kia tr&ecirc;n hoang đảo lại c&oacute; kh&oacute;i đen d&acirc;ng l&ecirc;n, c&aacute;i n&agrave;y đại biểu cho c&oacute; người ở ph&iacute;a tr&ecirc;n nh&oacute;m lửa a!<br />\r\n&quot;Kh&ocirc;ng phải đ&acirc;u? Chẳng lẽ lại thật đ&uacute;ng l&agrave; bắt gặp c&aacute;i kia một phần vạn x&aacute;c suất?&quot; Kuzan c&oacute; ch&uacute;t kh&oacute; tin nhẹ giọng nỉ non n&oacute;i.<br />\r\nZephyr &aacute;nh mắt l&oacute;e l&ecirc;n một c&aacute;i, sau đ&oacute; mở miệng n&oacute;i ra: &quot;C&oacute; lẽ l&agrave; thật bắt gặp, nhanh, mệnh lệnh qu&acirc;n hạm gia tốc!&quot;<br />\r\n&quot;C&oacute; khả năng hay kh&ocirc;ng l&agrave; s&eacute;t đ&aacute;nh c&aacute;i g&igrave;? Ta cảm gi&aacute;c liền xem như s&eacute;t đ&aacute;nh đốt l&ecirc;n trong n&uacute;i c&acirc;y cối. . . X&aacute;c suất cũng so St.Claudius c&ograve;n sống tỷ lệ muốn cao a. . .&quot; Kuzan ch&eacute;p miệng một c&aacute;i, mở miệng hỏi.<br />\r\n&quot;Mặc kệ l&agrave; c&aacute;i g&igrave;, cũng n&ecirc;n đi l&ecirc;n xem một ch&uacute;t!&quot; Zephyr gật gật đầu nhanh ch&oacute;ng mở miệng n&oacute;i ra.<br />\r\nSau đ&oacute; c&aacute;i n&agrave;y to lớn qu&acirc;n hạm liền lấy tốc độ cực nhanh hướng ph&iacute;a c&aacute;i kia hoang đảo phương hướng chạy tới.<br />\r\nL&uacute;c n&agrave;y ở tr&ecirc;n đỉnh n&uacute;i, Claudius một b&ecirc;n nướng c&aacute;i kia mang về to lớn thịt heo rừng khối, một b&ecirc;n từ đảo n&agrave;y b&ecirc;n tr&ecirc;n điểm cao nhất quan s&aacute;t đến bốn ph&iacute;a hải vực, tại hắn chỗ &aacute;nh mắt nh&igrave;n tới, cũng ph&aacute;t hiện mấy chiếc qu&acirc;n hạm ch&iacute;nh đang nhanh ch&oacute;ng hướng về b&ecirc;n n&agrave;y l&aacute;i tới.<br />\r\nCắn một c&aacute;i m&ugrave;i vị đ&oacute; phi thường kh&ocirc;ng mỹ vị thịt heo rừng, Claudius nhẹ giọng thở d&agrave;i một hơi, nỉ non n&oacute;i: &quot;Ai, kh&ocirc;ng uổng c&ocirc;ng ta đợi l&acirc;u như vậy, những t&ecirc;n khốn kiếp kia nh&oacute;m rốt cục bỏ được ph&aacute;i người đến &#39;Cứu ta&#39; sao? Lại hoặc l&agrave; n&oacute;i. . . Ph&aacute;i người đến ch&iacute;nh thức x&aacute;c định ta tin chết?&quot;<br />\r\nCũng kh&ocirc;ng c&oacute; c&oacute; động t&aacute;c g&igrave;, Claudius vẫn như cũ l&agrave; ngồi tại c&aacute;i n&agrave;y tr&ecirc;n đỉnh n&uacute;i, mặc d&ugrave; gặp rủi ro, nhưng hắn n&oacute;i thế n&agrave;o cũng l&agrave; Thi&ecirc;n Long Nh&acirc;n nha, kh&ocirc;ng cần thiết tr&aacute;ch tr&aacute;ch h&ocirc; h&ocirc; chạy đi ngh&ecirc;nh đ&oacute;n trước đ&oacute; tới cứu viện qu&acirc;n hạm, chỉ cần ngồi ở chỗ n&agrave;y chờ bọn hắn tới gặp m&igrave;nh như vậy đủ rồi, mặc d&ugrave; loại h&agrave;nh vi n&agrave;y nh&igrave;n qua c&oacute; ch&uacute;t ngạo mạn, nhưng c&aacute;i n&agrave;y mới l&agrave; ph&ugrave; hợp Thi&ecirc;n Long Nh&acirc;n th&acirc;n phận h&agrave;nh vi m&agrave;.<br />\r\nBởi vậy Claudius vẫn như cũ l&agrave; mười phần b&igrave;nh tĩnh ngồi ở chỗ n&agrave;y, cũng kh&ocirc;ng c&oacute; lại đi ăn c&aacute;i kia l&agrave;m cho người ng&aacute;n thịt heo rừng, d&ugrave; sao chỉ cần b&ecirc;n tr&ecirc;n qu&acirc;n hạm, lại thế n&agrave;o cũng c&oacute; thể ăn v&agrave;o so m&ugrave;i vị kia phải tốt thức ăn a.<br />\r\nHải qu&acirc;n qu&acirc;n hạm nhanh ch&oacute;ng đ&atilde; tới hoang đảo n&agrave;y b&ecirc;n bờ biển b&ecirc;n tr&ecirc;n, sau đ&oacute; Zephyr Đại tướng mang theo Kuzan c&ugrave;ng một đo&agrave;n hải binh nh&oacute;m nhanh ch&oacute;ng đặt ch&acirc;n l&ecirc;n to&agrave; n&agrave;y hoang đảo, hướng ph&iacute;a c&aacute;i kia đang tại bốc l&ecirc;n kh&oacute;i đen tr&ecirc;n đỉnh n&uacute;i chạy tới.<br />\r\nChờ đến đỉnh n&uacute;i, tầm mắt kho&aacute;ng đạt chi l&uacute;c, Zephyr Đại tướng rốt cục nh&igrave;n thấy m&igrave;nh chuyến n&agrave;y nhiệm vụ mục ti&ecirc;u, đ&oacute; l&agrave; một c&aacute;i nh&igrave;n qua mười phần th&acirc;n ảnh chật vật, nhưng kh&ocirc;ng hề nghi ngờ, ch&iacute;nh l&agrave; c&aacute;i kia tao ngộ tai nạn tr&ecirc;n biển hai tuần thời gian l&acirc;u St.Claudius!<br />\r\nC&aacute;c loại tới gần c&aacute;i n&agrave;y St.Claudius thời điểm, Zephyr cũng chậm dần bước tiến của m&igrave;nh, hắn kỳ thật trong l&ograve;ng cũng l&agrave; rất &#39;Hư&#39;, c&aacute;i n&agrave;y St.Claudius mặc d&ugrave; hắn chưa thấy qua, nhưng l&agrave; cũng đ&atilde; được nghe n&oacute;i, d&ugrave; sao cũng l&agrave; ăn Tr&aacute;i &Aacute;c Quỷ Thi&ecirc;n Long Nh&acirc;n nha, nhiều &iacute;t vẫn l&agrave; c&oacute; ch&uacute;t tin đồn.<br />\r\nC&aacute;c loại truyền ng&ocirc;n b&ecirc;n trong, đều n&oacute;i c&aacute;i n&agrave;y St.Claudius mặc d&ugrave; tuổi c&ograve;n nhỏ, nhưng lại thỏa thỏa ch&iacute;nh l&agrave; c&aacute;i phế vật, bản sự kh&ocirc;ng lớn, t&iacute;nh t&igrave;nh ngược lại l&agrave; rất lớn, bọn hắn những n&agrave;y Hải qu&acirc;n thời gian qua đi hai tuần mới tới cứu viện, kh&ocirc;ng biết vị n&agrave;y Thi&ecirc;n Long Nh&acirc;n đại l&atilde;o gia t&acirc;m l&yacute; c&oacute; bao nhi&ecirc;u hỏa kh&iacute; đ&acirc;u, đợi l&aacute;t nữa kh&ocirc;ng chừng liền phải tao ngộ một ch&uacute;t l&agrave;m cho người chuyện t&igrave;nh kh&ocirc;ng vui.</p>\r\n\r\n<p><br />\r\nTrong l&ograve;ng c&oacute; ch&uacute;t thấp thỏm đi ra ph&iacute;a trước, nh&igrave;n xem c&aacute;i kia ch&iacute;nh tại xem kỹ m&igrave;nh St.Claudius, Zephyr nuốt một ngụm nước bọt, nhẹ giọng mở miệng n&oacute;i ra: &quot;St.Claudius, để ng&agrave;i chịu khổ, Hải Qu&acirc;n bản bộ Đại tướng &middot; Zephyr cố &yacute; suất hạm đội trước tới cứu viện, gặp ng&agrave;i an khang, trong l&ograve;ng kh&ocirc;ng thắng mừng rỡ.&quot;<br />\r\nTại Zephyr thấy được Claudius thời điểm, Claudius tự nhi&ecirc;n cũng l&agrave; nh&igrave;n thấy Zephyr, vừa v&agrave;o mắt, đ&atilde; cảm thấy gia hỏa n&agrave;y nh&igrave;n rất quen mắt, trong l&ograve;ng cũng đại kh&aacute;i c&oacute; ch&uacute;t suy đo&aacute;n, quả nhi&ecirc;n, thật đ&uacute;ng l&agrave; c&aacute;i kia đại danh đỉnh đỉnh Hải qu&acirc;n Đại tướng &middot; Zephyr a!<br />\r\nZephyr n&oacute;i đến lời n&agrave;y thời điểm, cũng đem eo th&acirc;n của m&igrave;nh mười phần cung k&iacute;nh cong xuống dưới, l&agrave;m Hải qu&acirc;n Đại tướng, Ch&iacute;nh Phủ Thế Giới nhiều &iacute;t vẫn l&agrave; sẽ cho ch&uacute;t mặt mũi, Thi&ecirc;n Long Nh&acirc;n nh&oacute;m tự nhi&ecirc;n cũng l&agrave; như thế, cho n&ecirc;n ngược lại cũng kh&ocirc;ng cần bọn hắn gặp được liền quỳ xuống.<br />\r\nBất qu&aacute; tại Zephyr b&ecirc;n người Thiếu tướng Kuzan c&ugrave;ng c&ograve;n lại một đ&aacute;m Hải qu&acirc;n c&aacute;c binh sĩ nhưng liền kh&ocirc;ng c&oacute; loại đ&atilde;i ngộ n&agrave;y, trong l&ograve;ng mặc d&ugrave; c&oacute; ch&uacute;t kh&oacute; chịu, nhưng Kuzan vẫn l&agrave; c&uacute;i đầu xuống, chuẩn bị đi c&aacute;i đại lễ.<br />\r\nNgay tại l&uacute;c c&aacute;i n&agrave;y l&uacute;c, Claudius nhẹ giọng mở miệng n&oacute;i ra: &quot;Tốt, tướng t&aacute; c&oacute; thể miễn, kh&ocirc;ng cần đa lễ.&quot;<br />\r\nHắn c&aacute;i n&agrave;y vừa mới n&oacute;i xong, Zephyr c&ugrave;ng Kuzan đều sửng sốt một ch&uacute;t, c&ograve;n c&oacute; một số Hải qu&acirc;n sĩ quan cấp gi&aacute;o nh&oacute;m cũng đều ng&acirc;y dại, về phần c&ograve;n lại những tiểu binh kia, ngược lại l&agrave; rất cung k&iacute;nh t&eacute; quỵ tr&ecirc;n đất, một bộ rất t&ocirc;n k&iacute;nh Claudius bộ d&aacute;ng.<br />\r\nNh&igrave;n xem một m&agrave;n n&agrave;y, Claudius trong l&ograve;ng cũng l&agrave; thật phức tạp, mặc d&ugrave; trong tr&iacute; nhớ x&aacute;c thực cũng thường xuy&ecirc;n c&oacute; người cho hắn quỳ xuống thỉnh an c&aacute;i g&igrave;, nhưng n&oacute;i trắng ra l&agrave;, vậy cũng chẳng qua l&agrave; k&yacute; ức m&agrave; th&ocirc;i, ch&acirc;n ch&iacute;nh tr&agrave;ng diện hắn lần n&agrave;y c&ograve;n l&agrave; lần đầu ti&ecirc;n gặp, với lại những c&aacute;i kia quỳ xuống đều l&agrave; tr&ecirc;n biển cả thi h&agrave;nh ch&iacute;nh nghĩa, giơ cao l&ecirc;n ch&iacute;nh nghĩa đại kỳ Hải qu&acirc;n a, từ c&aacute;i n&agrave;y cũng c&oacute; thể nh&igrave;n ra đến đám hải qu&acirc;n tại Thi&ecirc;n Long Nh&acirc;n trước mặt l&agrave; đến cỡ n&agrave;o biệt khuất, đồng dạng, cũng c&oacute; thể nh&igrave;n ra Thi&ecirc;n Long Nh&acirc;n l&agrave; đến cỡ n&agrave;o ngạo mạn.<br />\r\nVề phần miễn đi Kuzan đ&aacute;m người đại lễ, Claudius trong l&ograve;ng cũng l&agrave; c&oacute; c&acirc;n đ&ograve;n, bọn hắn cho m&igrave;nh đi c&aacute;i đại lễ, ngoại trừ hơi c&oacute; ch&uacute;t mừng thầm b&ecirc;n ngo&agrave;i, m&igrave;nh cũng phải kh&ocirc;ng đến chỗ tốt g&igrave;, kh&ocirc;ng chỉ c&oacute; kh&ocirc;ng chiếm được chỗ tốt, c&ograve;n biết để Kuzan trong l&ograve;ng bọn họ sinh ra ch&uacute;t biệt khuất.<br />\r\nM&agrave; Kuzan loại n&agrave;y c&oacute; tiềm lực gia hỏa, tương lai kh&ocirc;ng c&oacute; g&igrave; bất ngờ xảy ra, nhất định l&agrave; sẽ đi hướng Hải Qu&acirc;n Đại tướng vị tr&iacute;, Claudius bao nhi&ecirc;u đối với hắn vẫn c&ograve;n c&oacute; ch&uacute;t thưởng thức, c&ugrave;ng n&oacute; để hắn cho m&igrave;nh quỳ xuống, trong l&ograve;ng sinh ra biệt khuất cảm gi&aacute;c, c&ograve;n kh&ocirc;ng bằng &acirc;m thầm b&aacute;n tốt đ&acirc;u, về sau gặp mặt cũng sẽ kh&ocirc;ng như vậy l&uacute;ng t&uacute;ng m&agrave;.<br />\r\nNh&igrave;n xem Zephyr c&ugrave;ng Kuzan c&aacute;i kia c&oacute; ch&uacute;t vẻ mặt kinh ngạc, Claudius kho&aacute;t kho&aacute;t tay, tiếp tục mở miệng n&oacute;i ra: &quot;Tốt, bản điện hạ rất cảm tạ c&aacute;c ngươi c&oacute; thể t&igrave;m tới to&agrave; n&agrave;y hoang đảo, trước tới cứu ta, c&aacute;i n&agrave;y đ&aacute;ng chết địa phương, ta một gi&acirc;y đồng hồ đều kh&ocirc;ng nghĩ ở l&acirc;u, ph&iacute;a trước dẫn đường, ta muốn về MariJoa!&quot;<br />\r\nKh&ocirc;ng chỉ c&oacute; kh&ocirc;ng c&oacute; bị kh&oacute; xử c&ugrave;ng chửi mắng c&aacute;i g&igrave;, ngược lại Claudius c&ograve;n mười phần t&iacute;nh t&igrave;nh tốt mi&ecirc;̃n đi bọn hắn lễ, điều n&agrave;y thực để Zephyr c&aacute;i n&agrave;y đ&atilde; l&agrave;m tốt cần trải qua hỏng b&eacute;t t&acirc;m sự Hải qu&acirc;n Đại tướng trong l&ograve;ng c&oacute; ch&uacute;t kỳ qu&aacute;i.<br />\r\nBất qu&aacute; đ&atilde; c&aacute;i kia St.Claudius đ&atilde; l&ecirc;n tiếng, hắn cũng kh&ocirc;ng d&aacute;m nhiều tr&igrave; ho&atilde;n c&aacute;i g&igrave;, cấp tốc gật đầu, mở miệng n&oacute;i ra: &quot;C&ograve;n xin St.Claudius đi theo ta, ch&uacute;ng ta Hải qu&acirc;n nhất định sẽ đem hết to&agrave;n lực, hộ tống điện hạ ng&agrave;i trở lại MariJoa.&quot;<br />\r\nVừa n&oacute;i, Zephyr c&ograve;n liếc một c&aacute;i c&aacute;i kia đ&atilde; nướng ch&iacute;n thịt heo rừng khối, quan s&aacute;t một c&aacute;i ho&agrave;n cảnh chung quanh, tựa hồ c&ograve;n c&oacute; rất nhiều xương cốt, xem ra trong khoảng thời gian n&agrave;y đến, vị n&agrave;y Thi&ecirc;n Long Nh&acirc;n đại l&atilde;o gia v&acirc;̣y mà thật tại to&agrave; n&agrave;y tr&ecirc;n hoang đảo tự lực c&aacute;nh sinh?<br />\r\n&quot;Lu&ocirc;n cảm gi&aacute;c vị n&agrave;y St.Claudius c&ugrave;ng trong đồn đ&atilde;i h&igrave;nh tượng kh&ocirc;ng giống nhau lắm? Cũng kh&ocirc;ng c&oacute; Thi&ecirc;n Long Nh&acirc;n tr&ecirc;n người loại kia ngang ngược c&agrave;n rỡ cảm gi&aacute;c a. . .&quot; Kuzan tại trong l&ograve;ng cũng l&agrave; nghĩ như vậy n&oacute;i.</p>\r\n\r\n<p><br />\r\nX&aacute;c thực, Claudius trong l&ograve;ng một gi&acirc;y đồng hồ đều kh&ocirc;ng nghĩ tại to&agrave; n&agrave;y tr&ecirc;n hoang đảo ở l&acirc;u, hắn hiện tại chỉ muốn tranh thủ thời gian trở lại MariJoa, sau đ&oacute; hưởng thụ một ch&uacute;t trong tr&iacute; nhớ cái chủng loại kia xa hoa v&ocirc; c&ugrave;ng sinh hoạt.<br />\r\nRất nhanh, tại Claudius leo l&ecirc;n qu&acirc;n hạm về sau, tại Zephyr mệnh lệnh dưới, Hải qu&acirc;n hạm đội lấy tốc độ cực nhanh bắt đầu hướng ph&iacute;a Red Line vị tr&iacute; đi tới, mục ti&ecirc;u cho đến Th&aacute;nh Địa &middot; Marijoa.<br />\r\nC&ugrave;ng l&uacute;c, Hải Qu&acirc;n bản bộ c&ugrave;ng Th&aacute;nh Địa &middot; Marijoa cũng đều nhận được Zephyr Den Den Mushi li&ecirc;n lạc, c&oacute; quan hệ với hai tuần trước đ&oacute; tao ngộ tai nạn tr&ecirc;n biển Thi&ecirc;n Long Nh&acirc;n &middot; St.Claudius được th&agrave;nh c&ocirc;ng cứu viện tin tức cũng nhanh ch&oacute;ng truyền đến người li&ecirc;n quan chờ trong lỗ tai.<br />\r\nNhưng ph&agrave;m l&agrave; biết như thế c&aacute;i sự t&igrave;nh người, liền kh&ocirc;ng c&oacute; một c&aacute;i n&agrave;o kh&ocirc;ng cảm thấy kinh ngạc.<br />\r\n&quot;&Acirc;n, kh&ocirc;ng sai, người sống s&oacute;t chỉ c&oacute; St.Claudius một người, chuyện cụ thể, chờ ta về tới Marineford về sau, lại c&ugrave;ng ngươi tốt nhất n&oacute;i một ch&uacute;t đi, Sengoku.&quot; Zephyr đối Den Den Mushi nhẹ giọng n&oacute;i ra, tiếp lấy cũng kh&ocirc;ng đợi Sengoku b&ecirc;n kia đ&aacute;p lại c&aacute;i g&igrave;, hắn liền mười phần tự nhi&ecirc;n treo g&atilde;y mất Den Den Mushi.<br />\r\nL&uacute;c n&agrave;y ở c&aacute;i n&agrave;y trong khoang thuyền Kuzan ngược lại l&agrave; hết sức t&ograve; m&ograve; mở miệng hỏi: &quot;Zephyr l&atilde;o sư, lu&ocirc;n cảm gi&aacute;c vị n&agrave;y St.Claudius c&ugrave;ng theo như đồn đại h&igrave;nh tượng kh&ocirc;ng qu&aacute; tương xứng hợp a?&quot;<br />\r\nZephyr sờ l&ecirc;n c&aacute;i cằm, nghe Kuzan lời n&oacute;i gật gật đầu, sau đ&oacute; mở miệng n&oacute;i ra: &quot;Đ&uacute;ng l&agrave; c&oacute; ch&uacute;t kh&ocirc;ng giống nhau lắm, c&ugrave;ng ta tiếp x&uacute;c qua những c&aacute;i kia Thi&ecirc;n Long Nh&acirc;n nh&oacute;m đều kh&ocirc;ng qu&aacute; đồng dạng, bất qu&aacute;. . . C&oacute; lẽ l&agrave; bởi v&igrave; tao ngộ lần n&agrave;y đại nạn, bao nhi&ecirc;u đối với hắn c&oacute; ch&uacute;t ảnh hưởng a? Cho n&ecirc;n mới sẽ. . .&quot;<br />\r\nHơi hơi dừng một ch&uacute;t về sau, Zephyr lắc đầu, nở nụ cười, mở miệng n&oacute;i ra: &quot;Ống nhiều như vậy l&agrave;m c&aacute;i g&igrave;? Chỉ cần đem St.Claudius đưa về MariJoa, n&oacute;i kh&ocirc;ng chừng về sau cũng sẽ kh&ocirc;ng gặp lại, cho n&ecirc;n đừng suy nghĩ nhiều.&quot;<br />\r\nKuzan nghe vậy cũng l&agrave; gật gật đầu, nở nụ cười, mở miệng n&oacute;i ra: &quot;Tựa như l&agrave; chuyện như vậy a, ha ha ha, bất qu&aacute; chỉ l&agrave; bởi v&igrave; rất &iacute;t nh&igrave;n thấy Thi&ecirc;n Long Nh&acirc;n, cho n&ecirc;n ta mới c&oacute; thể như vậy hiếu kỳ m&agrave;.&quot;<br />\r\n&quot;Lần n&agrave;y l&agrave; ngoại lệ, chờ sau n&agrave;y ngươi thật gặp những c&aacute;i kia. . . Đại l&atilde;o gia, n&oacute;i kh&ocirc;ng chừng ngươi liền rốt cuộc kh&ocirc;ng muốn nh&igrave;n thấy Thi&ecirc;n Long Nh&acirc;n.&quot; Zephyr cười cười, mở miệng n&oacute;i ra.<br />\r\nC&ugrave;ng n&agrave;y c&ugrave;ng l&uacute;c, tại qu&acirc;n hạm một c&aacute;i khoang thuyền b&ecirc;n trong, Claudius ch&iacute;nh đem ch&iacute;nh m&igrave;nh nửa người cua trong bồn tắm, hưởng thụ lấy đ&atilde; l&acirc;u an nh&agrave;n.<br />\r\nMặc d&ugrave; tại c&aacute;i kia đ&aacute;ng chết tr&ecirc;n hoang đảo, Claudius cũng c&oacute; thể t&igrave;m tới nguồn nước thanh tẩy m&igrave;nh, bất qu&aacute; kia c&aacute;i g&igrave; tắm rửa vật dụng, hắn liền xem như đầu &oacute;c muốn ph&aacute; cũng chế, bởi vậy c&aacute;i n&agrave;y vừa l&ecirc;n thuyền, chịu đủ tr&ecirc;n th&acirc;n &ocirc; uế Claudius chuyện thứ nhất liền l&agrave; đến tắm rửa.<br />\r\n&quot;Zephyr c&ugrave;ng Kuzan đều tại. . . Nha, lần n&agrave;y ngược lại l&agrave; an t&acirc;m, c&oacute; hai người bọn họ, l&agrave;m sao cũng c&oacute; thể đem ta an an to&agrave;n to&agrave;n đưa về MariJoa.&quot; Claudius nhẹ giọng nỉ non một c&acirc;u n&oacute;i.<br />\r\nMặc d&ugrave; cũng kh&ocirc;ng c&oacute; c&ugrave;ng Kuzan n&oacute;i một c&acirc;u, bất qu&aacute; Claudius vẫn l&agrave; l&acirc;̀n đ&acirc;̀u ti&ecirc;n liền nhận ra Kuzan th&acirc;n ảnh, d&ugrave; sao c&aacute;i kia th&acirc;n cao liền đ&atilde; rất c&oacute; đặc th&ugrave; m&agrave;.</p>\r\n\r\n<p><br />\r\nĐắc &yacute; ng&acirc;m nước tắm, đem trong khoảng thời gian n&agrave;y mệt mỏi hơi h&oacute;a giải một lần về sau, Claudius đứng dậy, đi tới ph&ograve;ng tắm trước gương, nh&igrave;n một ch&uacute;t m&igrave;nh bộ d&aacute;ng.<br />\r\n&quot;Bao nhi&ecirc;u so hai tuần trước vẫn c&ograve;n c&oacute; ch&uacute;t biến h&oacute;a m&agrave;.&quot; Claudius sờ l&ecirc;n tr&ecirc;n bụng m&igrave;nh thịt thừa, nhẹ giọng n&oacute;i nhỏ.<br />\r\nNếu như n&oacute;i hai tuần trước, m&igrave;nh vẫn l&agrave; một c&aacute;i đầy người thịt mỡ, đầy mỡ v&ocirc; c&ugrave;ng củi mục Thi&ecirc;n Long Nh&acirc;n, l&uacute;c n&agrave;y mặc d&ugrave; vẫn c&ograve;n c&oacute; ch&uacute;t thịt thừa c&aacute;i g&igrave;, nhưng &iacute;t ra tinh kh&iacute; thần nh&igrave;n qua kh&ocirc;ng c&oacute; như vậy l&agrave;m cho người ch&aacute;n gh&eacute;t, th&acirc;n h&igrave;nh cũng x&aacute;c thực gầy g&ograve; rất nhiều, đồng thời tr&ecirc;n c&aacute;nh tay c&ugrave;ng tr&ecirc;n b&agrave;n ch&acirc;n cũng phong ph&uacute; một ch&uacute;t bắp thịt cảm gi&aacute;c.<br />\r\n&quot;Trở về mới hảo hảo r&egrave;n luyện một ch&uacute;t, hẳn l&agrave; c&oacute; thể tho&aacute;t khỏi &#39;Đầy mỡ&#39; hai chữ n&agrave;y.&quot; Claudius cười cười tự nhủ.<br />\r\nMặc v&agrave;o đám hải qu&acirc;n đặc biệt v&igrave; m&igrave;nh chuẩn bị lộng lẫy quần &aacute;o, Claudius cảm gi&aacute;c c&oacute; chút rộng r&atilde;i, bất qu&aacute; ngược lại cũng b&igrave;nh thường, d&ugrave; sao c&aacute;i n&agrave;y l&agrave; m&igrave;nh trước đ&oacute; h&igrave;nh thể l&uacute;c m&atilde; h&agrave;o m&agrave;.<br />\r\nĐi ra ph&ograve;ng tắm, tất cả nh&igrave;n thấy m&igrave;nh Hải qu&acirc;n c&aacute;c binh sĩ đều mười phần cung k&iacute;nh c&uacute;i đầu, đ&acirc;y l&agrave; Claudius ch&agrave;o hỏi, kh&ocirc;ng phải đ&ecirc;́n quỳ xuống một mảnh a?<br />\r\nTại một t&ecirc;n Hải qu&acirc;n binh sĩ dẫn đầu dưới, Claudius rất nhanh liền đi tới gian ph&ograve;ng của m&igrave;nh, khoan h&atilde;y n&oacute;i, cho d&ugrave; l&agrave; tại qu&acirc;n hạm b&ecirc;n tr&ecirc;n, gian ph&ograve;ng kia cũng thật lớn, bố tr&iacute; cũng v&ocirc; cùng thoải mái.<br />\r\n&quot;Sẽ kh&ocirc;ng phải l&agrave; đem trước đ&oacute; kh&ocirc;ng đạt được g&igrave; hai tuần thời gian tất cả đều cầm tới lắp đặt thiết bị chiếc qu&acirc;n hạm n&agrave;y đi?&quot; Claudius ở trong l&ograve;ng o&aacute;n thầm dưới.<br />\r\nSau đ&oacute; hắn cũng kh&ocirc;ng quay đầu lại đối sau lưng c&aacute;i kia đứng ở ngo&agrave;i cửa Hải qu&acirc;n n&oacute;i ra: &quot;Đi chuẩn bị cho ta phong ph&uacute; cơm trưa, sau đ&oacute; mời Zephyr Đại tướng đến đ&acirc;y c&ugrave;ng ta chung tiến cơm trưa.&quot;<br />\r\nVị kia Hải qu&acirc;n binh sĩ nghe vậy căn bản vốn kh&ocirc;ng d&aacute;m c&oacute; bất kỳ dị nghị g&igrave;, nhanh ch&oacute;ng đ&aacute;p lại n&oacute;i: &quot;L&agrave;, St.Claudius, xin ng&agrave;i chờ một ch&uacute;t, ta c&aacute;i n&agrave;y đi truyền đạt &yacute; ch&iacute; của ng&agrave;i.&quot;<br />\r\nClaudius gật gật đầu, c&aacute;c loại c&aacute;i kia Hải qu&acirc;n binh sĩ sau khi đi, hắn đem tr&ecirc;n b&agrave;n đ&atilde; ngược lại t&ocirc;́t ấm một trận m&agrave; ch&eacute;n tr&agrave; n&acirc;ng l&ecirc;n, nhẹ nh&agrave;ng uống một ngụm tr&agrave; n&oacute;ng, nỉ non n&oacute;i: &quot;Hải Vi&ecirc;n lịch cuối năm 1490. . . Mặc d&ugrave; ta cũng coi l&agrave; biết được một &iacute;t chuyện, tuy nhi&ecirc;n &iacute;t nhiều vẫn l&agrave; muốn c&ugrave;ng Zephyr lại tr&ograve; chuyện ch&uacute;t, x&aacute;c nhận x&aacute;c nhận mới được a.&quot;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:368px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'hai-vien-lich-cuoi-nam-1490', 1, '5', '2022-07-31 07:53:10', '2022-08-01 02:40:19'),
(7, '6', 'Cùng Zephyr Đại tướng chung tiến cơm trưa!', '<p>điểm n&agrave;y b&ecirc;n tr&ecirc;n, Claudius rất biết r&otilde; nguy&ecirc;n bản xưng b&aacute; bi&ecirc;̉n cả mạnh nhất băng hải tặc &middot; Rocks băng hải tặc đ&atilde; tại mấy năm trước God Valley chiến dịch b&ecirc;n trong, bị Roger c&ugrave;ng Garp hai người li&ecirc;n thủ cho đ&aacute;nh ng&atilde;, thuyền trưởng Rocks đ&atilde; nghỉ cơm, sau đ&oacute; Rocks băng hải tặc b&ecirc;n tr&ecirc;n c&aacute;c lộ đại l&atilde;o l&uacute;c n&agrave;y đều đ&atilde; bay một m&igrave;nh.<br />\r\nHiện nay nếu l&agrave; Claudius nhớ kh&ocirc;ng lầm, Big Mom băng hải tặc đ&atilde; th&agrave;nh lập một đoạn thời gian, Kaido t&ecirc;n kia cũng l&agrave;m c&aacute;i băng hải tặc B&aacute;ch Th&uacute;, R&acirc;u Trắng c&agrave;ng l&agrave; danh tiếng đang nổi, đ&atilde; bắt đầu thu con trai.<br />\r\nBất qu&aacute; trừ bỏ bọn hắn b&ecirc;n ngo&agrave;i, Golden Lion &middot; Shiki gia hỏa n&agrave;y b&acirc;y giờ mới gọi uy phong b&aacute;t diện, đ&atilde; g&acirc;y dựng một c&aacute;i khổng lồ Hải tặc đại hạm đội, được xưng l&agrave; &#39;Hải tặc Đ&ocirc; đ&ocirc;́c&#39;, xem như thế giới mới b&ecirc;n trong trước mắt uy thế mạnh nhất băng hải tặc.<br />\r\nLại n&oacute;i tiếp c&ograve;n c&oacute; rất nhiều lợi hại Hải tặc, tỉ như xuất th&acirc;n Ka no Kuni Đại hải tặc &middot; Ouchoku, vơ v&eacute;t v&ocirc; số t&agrave;i bảo John thuyền trưởng c&aacute;c loại.<br />\r\nM&agrave; những n&agrave;y danh chấn bi&ecirc;̉n cả đ&aacute;m gia hỏa, tr&ecirc;n cơ bản tất cả đều l&agrave; từ Rocks băng hải tặc b&ecirc;n trong đi ra bay một m&igrave;nh.<br />\r\nĐương nhi&ecirc;n tại bọn hắn b&ecirc;n ngo&agrave;i, c&ograve;n c&oacute; đi tới tinh binh lộ tuyến băng hải tặc Roger, bất qu&aacute; băng hải tặc Roger tương đối kỳ qu&aacute;i, những n&agrave;y Đại hải tặc nh&oacute;m tr&ecirc;n cơ bản đều l&agrave; tại thế giới mới b&ecirc;n trong tiến h&agrave;nh tranh b&aacute;, m&agrave; băng hải tặc Roger lại l&agrave; khắp thế giới tại chạy loạn, c&oacute; đ&ocirc;i khi sẽ ở thế giới mới hiện th&acirc;n, c&oacute; đ&ocirc;i khi lại sẽ ở Tứ Hải xuất hiện, kh&ocirc;ng biết bọn hắn b&acirc;y giờ đang l&agrave;m g&igrave; đ&acirc;u.<br />\r\nBất qu&aacute; những chuyện n&agrave;y tạm thời c&ugrave;ng hắn một c&aacute;i mười tuổi lớn tiểu th&iacute; h&agrave;i kh&ocirc;ng c&oacute; quan hệ g&igrave;, Claudius hiện nay ngoại trừ cho&agrave;ng một c&aacute;i Thi&ecirc;n Long Nh&acirc;n hổ &aacute;o kho&aacute;c bằng da, c&ograve;n lại đều l&agrave; muốn c&aacute;i g&igrave; kh&ocirc;ng c&oacute; g&igrave;, m&igrave;nh cũng kh&ocirc;ng c&oacute; năng lực c&ugrave;ng thực lực, muốn khuấy gi&oacute; nổi mưa, c&ograve;n k&eacute;m một ch&uacute;t đ&acirc;u, c&aacute;i kh&aacute;c kh&ocirc;ng n&oacute;i nhiều, trước hết nghĩ muốn về đến MariJoa về sau, thế n&agrave;o mới c&oacute; thể đứng ổn c&aacute;i g&oacute;t ch&acirc;n mới l&agrave; đ&uacute;ng l&yacute;.<br />\r\nM&agrave; căn cứ Claudius suy t&iacute;nh, kh&ocirc;ng c&oacute; g&igrave; bất ngờ xảy ra, nhiều nhất lại c&oacute; non nửa năm, b&acirc;y</p>', '<p>Hải Vi&ecirc;n lịch cuối năm 1490, nhưng thật ra l&agrave; một c&aacute;i tương đối c&oacute; &yacute; tứ thời gian điểm, tại thời điểm n&agrave;y b&ecirc;n tr&ecirc;n, Claudius rất biết r&otilde; nguy&ecirc;n bản xưng b&aacute; bi&ecirc;̉n cả mạnh nhất băng hải tặc &middot; Rocks băng hải tặc đ&atilde; tại mấy năm trước God Valley chiến dịch b&ecirc;n trong, bị Roger c&ugrave;ng Garp hai người li&ecirc;n thủ cho đ&aacute;nh ng&atilde;, thuyền trưởng Rocks đ&atilde; nghỉ cơm, sau đ&oacute; Rocks băng hải tặc b&ecirc;n tr&ecirc;n c&aacute;c lộ đại l&atilde;o l&uacute;c n&agrave;y đều đ&atilde; bay một m&igrave;nh.<br />\r\nHiện nay nếu l&agrave; Claudius nhớ kh&ocirc;ng lầm, Big Mom băng hải tặc đ&atilde; th&agrave;nh lập một đoạn thời gian, Kaido t&ecirc;n kia cũng l&agrave;m c&aacute;i băng hải tặc B&aacute;ch Th&uacute;, R&acirc;u Trắng c&agrave;ng l&agrave; danh tiếng đang nổi, đ&atilde; bắt đầu thu con trai.<br />\r\nBất qu&aacute; trừ bỏ bọn hắn b&ecirc;n ngo&agrave;i, Golden Lion &middot; Shiki gia hỏa n&agrave;y b&acirc;y giờ mới gọi uy phong b&aacute;t diện, đ&atilde; g&acirc;y dựng một c&aacute;i khổng lồ Hải tặc đại hạm đội, được xưng l&agrave; &#39;Hải tặc Đ&ocirc; đ&ocirc;́c&#39;, xem như thế giới mới b&ecirc;n trong trước mắt uy thế mạnh nhất băng hải tặc.<br />\r\nLại n&oacute;i tiếp c&ograve;n c&oacute; rất nhiều lợi hại Hải tặc, tỉ như xuất th&acirc;n Ka no Kuni Đại hải tặc &middot; Ouchoku, vơ v&eacute;t v&ocirc; số t&agrave;i bảo John thuyền trưởng c&aacute;c loại.<br />\r\nM&agrave; những n&agrave;y danh chấn bi&ecirc;̉n cả đ&aacute;m gia hỏa, tr&ecirc;n cơ bản tất cả đều l&agrave; từ Rocks băng hải tặc b&ecirc;n trong đi ra bay một m&igrave;nh.<br />\r\nĐương nhi&ecirc;n tại bọn hắn b&ecirc;n ngo&agrave;i, c&ograve;n c&oacute; đi tới tinh binh lộ tuyến băng hải tặc Roger, bất qu&aacute; băng hải tặc Roger tương đối kỳ qu&aacute;i, những n&agrave;y Đại hải tặc nh&oacute;m tr&ecirc;n cơ bản đều l&agrave; tại thế giới mới b&ecirc;n trong tiến h&agrave;nh tranh b&aacute;, m&agrave; băng hải tặc Roger lại l&agrave; khắp thế giới tại chạy loạn, c&oacute; đ&ocirc;i khi sẽ ở thế giới mới hiện th&acirc;n, c&oacute; đ&ocirc;i khi lại sẽ ở Tứ Hải xuất hiện, kh&ocirc;ng biết bọn hắn b&acirc;y giờ đang l&agrave;m g&igrave; đ&acirc;u.<br />\r\nBất qu&aacute; những chuyện n&agrave;y tạm thời c&ugrave;ng hắn một c&aacute;i mười tuổi lớn tiểu th&iacute; h&agrave;i kh&ocirc;ng c&oacute; quan hệ g&igrave;, Claudius hiện nay ngoại trừ cho&agrave;ng một c&aacute;i Thi&ecirc;n Long Nh&acirc;n hổ &aacute;o kho&aacute;c bằng da, c&ograve;n lại đều l&agrave; muốn c&aacute;i g&igrave; kh&ocirc;ng c&oacute; g&igrave;, m&igrave;nh cũng kh&ocirc;ng c&oacute; năng lực c&ugrave;ng thực lực, muốn khuấy gi&oacute; nổi mưa, c&ograve;n k&eacute;m một ch&uacute;t đ&acirc;u, c&aacute;i kh&aacute;c kh&ocirc;ng n&oacute;i nhiều, trước hết nghĩ muốn về đến MariJoa về sau, thế n&agrave;o mới c&oacute; thể đứng ổn c&aacute;i g&oacute;t ch&acirc;n mới l&agrave; đ&uacute;ng l&yacute;.<br />\r\nM&agrave; căn cứ Claudius suy t&iacute;nh, kh&ocirc;ng c&oacute; g&igrave; bất ngờ xảy ra, nhiều nhất lại c&oacute; non nửa năm, b&acirc;y giờ hắn Donquixote gia tộc gia chủ &middot; St.Homing liền muốn mang theo m&igrave;nh đại biểu đệ Doflamingo c&ugrave;ng tiểu biểu đệ Rosinante c&ograve;n c&oacute; l&atilde;o b&agrave; nh&oacute;m tiến đến Bắc Hải hưởng thụ &#39;Nh&acirc;n gian cực lạc&#39;.<br />\r\nC&ugrave;ng tr&ecirc;n đại dương bao la những c&aacute;i kia ph&acirc;n tranh so s&aacute;nh, chuyện n&agrave;y mới l&agrave; li&ecirc;n quan đến Claudius ch&iacute;nh m&igrave;nh vận mệnh sự kiện trọng đại a, ch&iacute; &iacute;t tại Homing gia hỏa n&agrave;y trước khi đi, Claudius phải đem c&oacute; li&ecirc;n quan tới ch&iacute;nh m&igrave;nh c&ugrave;ng Donquixote giữa gia tộc những n&agrave;y ph&aacute; sự xử l&yacute; sạch sẽ mới được a.<br />\r\nBất qu&aacute; chuyện n&agrave;y n&oacute;i đến cũng kh&ocirc;ng phải kh&oacute; khăn dường n&agrave;o, trước mắt c&oacute; hai c&aacute;i phương ph&aacute;p c&oacute; thể lựa chọn, phương ph&aacute;p thứ nhất, liền l&agrave; Claudius đem Homing thuyết phục, để gia hỏa n&agrave;y đừng lại tiếp tục l&agrave;m mộng ban ng&agrave;y của hắn, liền xem như l&agrave;m Thi&ecirc;n Long Nh&acirc;n, cũng muốn cước đạp thực địa nha, kh&ocirc;ng c&oacute; chuyện l&agrave;m đi c&aacute;i g&igrave; &#39;Nh&acirc;n gian&#39; ? Một năm về sau, c&ograve;n kh&ocirc;ng phải muốn kh&oacute;c sướt mướt trở về, kết quả bị cự tuyệt, cuối c&ugrave;ng l&atilde;o b&agrave; b&ecirc;n ngo&agrave;i bệnh chết, nhi tử đem m&igrave;nh giết, x&aacute;ch đầu đến MariJoa?<br />\r\nTốt sinh sinh một c&aacute;i thế giới qu&yacute; tộc Thi&ecirc;n Long Nh&acirc;n gia tộc, ngạnh sinh sinh bị c&aacute;i n&agrave;y đầu &oacute;c c&oacute; vấn đề gia hỏa cho chơi trở th&agrave;nh nh&acirc;n gian thảm kịch, thật sự l&agrave; kh&ocirc;ng biết n&ecirc;n n&oacute;i hắn Homing c&aacute;i g&igrave; tốt.<br />\r\nBất qu&aacute; c&aacute;i lựa chọn n&agrave;y rất kh&oacute; khăn, Homing gia hỏa n&agrave;y đ&uacute;ng l&agrave; c&oacute; ch&uacute;t ngốc bạch ngọt, đối Claudius cũng coi như kh&ocirc;ng tệ, nhưng l&agrave; thật muốn n&oacute;i hai người c&oacute; c&aacute;i g&igrave; đặc biệt tốt t&igrave;nh cảm, c&ograve;n kh&ocirc;ng đến mức, đương nhi&ecirc;n, chủ thể nguy&ecirc;n nh&acirc;n l&agrave; bởi v&igrave; nguy&ecirc;n bản Claudius căn bản l&agrave; kh&ocirc;ng c&oacute; nghĩ qua muốn c&ugrave;ng Homing l&agrave;m tốt quan hệ thế n&agrave;o loại h&igrave;nh, hắn liền l&agrave; c&aacute;i trong đầu tất cả đều l&agrave; ph&acirc;n ngớ ngẩn m&agrave; th&ocirc;i, bằng kh&ocirc;ng, quan hệ muốn l&agrave; phi thường th&acirc;n mật, Homing cũng kh&ocirc;ng đến mức bị những người kia tr&igrave; ho&atilde;n hai tuần mới biết được Claudius tao ngộ tai nạn tr&ecirc;n biển c&aacute;i g&igrave;.<br />\r\nBởi vậy c&oacute; thể thấy được, Claudius c&ugrave;ng Homing quan hệ trong đ&oacute;, cũng ch&iacute;nh l&agrave; loại kia coi như hai tuần đều kh&ocirc;ng tin tức, cũng sẽ kh&ocirc;ng c&oacute; c&aacute;i g&igrave; kỳ qu&aacute;i t&igrave;nh trạng.<br />\r\nLại th&ecirc;m Homing người n&agrave;y đầu &oacute;c mười phần ngay thẳng, muốn thuyết phục hắn người cũng kh&ocirc;ng phải l&agrave; kh&ocirc;ng c&oacute;, ch&iacute; &iacute;t Donquixote trong gia tộc người đại đa số đều tại phản đối hắn c&aacute;i n&agrave;y ảo tưởng kh&ocirc;ng thực tế, nhưng hắn nhưng như cũ rời đi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\nBởi vậy Claudius cũng kh&ocirc;ng cảm thấy m&igrave;nh một c&aacute;i mười tuổi tiểu th&iacute; h&agrave;i liền c&oacute; thể thay đổi St.Homing &yacute; nghĩ, cho n&ecirc;n c&aacute;i n&agrave;y lựa chọn thứ nhất, Claudius căn bản cũng kh&ocirc;ng suy t&iacute;nh.<br />\r\nC&aacute;i n&agrave;y lựa chọn thứ hai, liền rất đơn giản, Claudius sở dĩ bị nhằm v&agrave;o, tr&ecirc;n bản chất nguy&ecirc;n nh&acirc;n liền l&agrave; khi St.Homing thật phải đi về sau, hắn rất c&oacute; thể bị đẩy l&ecirc;n c&aacute;i n&agrave;y Donquixote gia tộc l&atilde;o đại vị tr&iacute; b&ecirc;n tr&ecirc;n đi.<br />\r\nSau đ&oacute; lấy bản l&atilde;nh của hắn c&ugrave;ng l&agrave;m người, c&ograve;n c&oacute; kia tuổi ấu tiểu kh&ocirc;ng may, Donquixote trong gia tộc người th&ocirc;ng minh nh&oacute;m căn bản vốn kh&ocirc;ng nguyện &yacute; để hắn th&agrave;nh v&igrave; con d&ecirc; đầu đ&agrave;n n&agrave;y.<br />\r\nCho n&ecirc;n mới sẽ bị li&ecirc;n tiếp nhằm v&agrave;o, l&agrave;m ra một ch&uacute;t &acirc;m mưu tới, bởi vậy Claudius chỉ cần tại sau khi trở về, từ tr&ecirc;n căn bản giải quyết c&aacute;i n&agrave;y m&acirc;u thuẫn, như vậy Claudius cảm thấy những n&agrave;y Thi&ecirc;n Long Nh&acirc;n nh&oacute;m, hẳn l&agrave; cũng sẽ kh&ocirc;ng lại đến c&ugrave;ng ch&iacute;nh m&igrave;nh c&aacute;i n&agrave;y tiểu th&iacute; h&agrave;i kh&ocirc;ng qua được.<br />\r\nD&ugrave; sao Thi&ecirc;n Long Nh&acirc;n số lượng cũng kh&ocirc;ng nhiều, nếu như kh&ocirc;ng c&oacute; tất yếu, cũng sẽ kh&ocirc;ng n&aacute;o ra đến loại n&agrave;y &#39;Thủ t&uacute;c tương t&agrave;n&#39; sự t&igrave;nh.<br />\r\nNghĩ tới đ&acirc;y, Claudius đ&atilde; quyết định chủ &yacute;, trở lại MariJoa về sau, liền tranh thủ thời gian rũ sạch m&igrave;nh c&ugrave;ng Donquixote gia tộc gia chủ vị tr&iacute; n&agrave;y quan hệ trong đ&oacute;, tr&aacute;nh đi c&aacute;i n&agrave;y danh tiếng a.<br />\r\nVừa ăn m&oacute;n điểm t&acirc;m ngọt, vừa nghĩ những chuyện n&agrave;y, sau đ&oacute; một trận m&agrave; tiếng đập cửa liền vang l&ecirc;n, đem Claudius suy nghĩ từ những &yacute; nghĩ n&agrave;y b&ecirc;n trong k&eacute;o ra ngo&agrave;i.<br />\r\nNgẩng đầu nh&igrave;n một ch&uacute;t cửa ph&ograve;ng phương hướng, Claudius nhẹ giọng mở miệng đ&aacute;p: &quot;V&agrave;o đi.&quot;<br />\r\nL&uacute;c n&agrave;y ở ngo&agrave;i cửa chờ Zephyr Đại tướng khi nghe thấy b&ecirc;n trong truyền đến thanh &acirc;m về sau, h&iacute;t s&acirc;u một hơi, tiếp lấy một thanh mở cửa ph&ograve;ng ra, sau đ&oacute; hắn đ&atilde; nh&igrave;n thấy l&uacute;c n&agrave;y đ&atilde; ngồi ở c&aacute;i kia xa hoa trước b&agrave;n ăn, đang tại ăn điểm t&acirc;m St.Claudius.<br />\r\nZephyr ngược lại l&agrave; kh&ocirc;ng c&oacute; để &yacute; vị n&agrave;y Thi&ecirc;n Long Nh&acirc;n đại l&atilde;o gia đều kh&ocirc;ng chờ m&igrave;nh đến đ&acirc;u, liền đ&atilde; ăn được m&oacute;n điểm t&acirc;m ngọt, d&ugrave; sao người ta c&oacute; thể k&ecirc;u m&igrave;nh tới ăn cơm, giống như từ Thi&ecirc;n Long Nh&acirc;n g&oacute;c độ đến xem, đều l&agrave; rất &#39;Để &yacute; m&igrave;nh&#39;?<br />\r\nBất qu&aacute; Zephyr cũng kh&ocirc;ng c&oacute; nghĩ đến thừa cơ &ocirc;m c&aacute;i đ&ugrave;i c&aacute;i g&igrave;, hắn c&ograve;n kh&ocirc;ng phải như vậy kh&ocirc;ng tiết th&aacute;o người, đồng dạng, giống như vị n&agrave;y St.Claudius t&igrave;nh cảnh cũng kh&ocirc;ng thế n&agrave;o thật sao.<br />\r\n&quot;St.Claudius, c&aacute;m ơn ng&agrave;i mời.&quot; Zephyr c&oacute; ch&uacute;t xoay người b&aacute;i một c&aacute;i n&oacute;i.<br />\r\nClaudius thấy thế cười cười, kho&aacute;t kho&aacute;t tay n&oacute;i ra: &quot;Bất kể n&oacute;i thế n&agrave;o, Zephyr Đại tướng ngươi cũng coi l&agrave; &acirc;n nh&acirc;n cứu mạng của ta, cho n&ecirc;n bản điện hạ &acirc;n chuẩn ngươi từ nay về sau, gặp ta kh&ocirc;ng phải l&agrave;m lễ.&quot;<br />\r\nZephyr sửng sốt một ch&uacute;t, bất qu&aacute; nếu c&oacute; thể c&oacute; c&aacute;i n&agrave;y chuyện tốt, hắn tự nhi&ecirc;n cũng vui vẻ tiếp nhận, miễn cho vừa thấy mặt liền muốn c&uacute;i đầu, hắn chịu được, c&aacute;i n&agrave;y eo cũng chịu kh&ocirc;ng được a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\nBởi vậy Zephyr nở nụ cười, mở miệng n&oacute;i ra: &quot;Vậy thật đ&uacute;ng l&agrave; đa tạ St.Claudius.&quot;<br />\r\n&quot;Kh&ocirc;ng cần phải kh&aacute;ch kh&iacute;, mời ngồi đi.&quot; Claudius kho&aacute;t kho&aacute;t tay sau mở miệng n&oacute;i ra.<br />\r\nZephyr cũng gật gật đầu, th&acirc;n l&agrave; Đại tướng, hắn cũng kh&ocirc;ng c&oacute; qu&aacute; nhiều c&acirc;u nệ, rất nhanh liền ngồi ở Claudius đối diện.<br />\r\nTiếp lấy hai người liền hơi h&agrave;n huy&ecirc;n v&agrave;i c&acirc;u, xem như k&eacute;o gần lại một c&aacute;i khoảng c&aacute;ch, th&ocirc;ng qua những n&agrave;y đơn giản đối thoại, ngược lại l&agrave; lệnh Zephyr t&acirc;m b&ecirc;n trong v&ocirc; c&ugrave;ng kinh ngạc, bởi v&igrave; hắn ph&aacute;t hiện vị n&agrave;y St.Claudius tựa hồ c&ugrave;ng theo như đồn đại h&igrave;nh tượng rất kh&ocirc;ng tương xứng, với lại r&otilde; r&agrave;ng nh&igrave;n qua mới l&agrave; c&aacute;i mười tuổi lớn tiểu quỷ m&agrave; th&ocirc;i, nhưng trong lời n&oacute;i, nhưng thật giống như rất th&agrave;nh thục bộ d&aacute;ng?<br />\r\nL&agrave;m Hải Qu&acirc;n bản bộ Đại tướng, Zephyr cũng coi l&agrave; tiếp x&uacute;c qua c&aacute;c lộ ngưu quỷ x&agrave; thần, nhất l&agrave; &aacute;nh mắt của hắn c&ograve;n cực k&igrave; tốt, nh&igrave;n người rất chuẩn, bằng kh&ocirc;ng th&igrave; cũng sẽ kh&ocirc;ng ở ng&agrave;y sau bồi dưỡng được nhiều như vậy Hải qu&acirc;n ưu t&uacute; sĩ quan.<br />\r\nBởi vậy Zephyr c&oacute; thể nh&igrave;n ra, trước mắt vị n&agrave;y Thi&ecirc;n Long Nh&acirc;n, cũng kh&ocirc;ng phải l&agrave; giả vờ cái chủng loại kia th&agrave;nh thục, cũng kh&ocirc;ng phải giả vờ tốt như vậy n&oacute;i chuyện, tựa hồ t&iacute;nh c&aacute;ch của hắn đ&uacute;ng l&agrave; kh&ocirc;ng c&oacute; theo như đồn đại như vậy ương ngạnh?<br />\r\nBất qu&aacute; đ&acirc;y đối với Zephyr tới n&oacute;i, t&oacute;m lại l&agrave; chuyện tốt, trước khi hắn tới đều đ&atilde; l&agrave;m tốt muốn phục dịch phiền phức tiểu quỷ chuẩn bị, m&agrave; trước mắt t&ecirc;n tiểu quỷ n&agrave;y nếu l&agrave; kh&ocirc;ng c&oacute; như vậy l&agrave;m người đau đầu, hắn bao nhi&ecirc;u cũng c&oacute; thể thoải m&aacute;i một ch&uacute;t.<br />\r\nCứ như vậy, giữa hai người bầu kh&ocirc;ng kh&iacute; cũng từ từ trầm tĩnh lại, kh&ocirc;ng c&oacute; nhiều như vậy c&acirc;u nệ, ngẫu nhi&ecirc;n c&ograve;n c&oacute; thể nghe thấy Zephyr cởi mở tiếng cười truyền đến.<br />\r\n&quot;Đ&uacute;ng, Zephyr Đại tướng. . .&quot; Claudius vừa ăn cơm trưa, một b&ecirc;n ngẩng đầu l&ecirc;n, nh&igrave;n về ph&iacute;a Zephyr n&oacute;i ra.<br />\r\nZephyr bưng l&ecirc;n chuy&ecirc;n m&ocirc;n chuẩn bị cho hắn ch&eacute;n rượu, uống một ngụm rượu đỏ về sau, nh&igrave;n về ph&iacute;a Claudius mở miệng hỏi: &quot;St.Claudius, c&oacute; dặn d&ograve; g&igrave; sao?&quot;<br />\r\n&quot;Kh&ocirc;ng, ta trước đ&oacute; mới n&oacute;i, kh&ocirc;ng cần kh&aacute;ch kh&iacute; như vậy, cũng kh&ocirc;ng c&oacute; dặn d&ograve; g&igrave;, ch&iacute;nh l&agrave; ta đột nhi&ecirc;n nghĩ đến, ngươi n&oacute;i thế n&agrave;o cũng l&agrave; Hải Qu&acirc;n bản bộ Đại tướng, như vậy đối với c&aacute;i n&agrave;y tr&ecirc;n đại dương bao la cố sự c&oacute; lẽ vẫn l&agrave; hiểu rất r&otilde; a? Ta lần n&agrave;y ra biển, cũng l&agrave; muốn nhiều hiểu r&otilde; một ch&uacute;t c&aacute;i thế giới n&agrave;y, chỉ bất qu&aacute;. . . Xem ra thật đ&aacute;ng tiếc, ta lữ tr&igrave;nh tại một mảnh hỏng b&eacute;t b&ecirc;n trong kết th&uacute;c.&quot; Claudius cười cười, nhẹ giọng mở miệng n&oacute;i ra.<br />\r\nHơi hơi dừng một ch&uacute;t về sau, Claudius liền n&oacute;i lần nữa: &quot;Bất qu&aacute; ta với c&aacute;i thế giới n&agrave;y hứng th&uacute; ngược lại l&agrave; kh&ocirc;ng c&oacute; suy giảm, nếu l&agrave; dễ d&agrave;ng m&agrave; n&oacute;i, c&oacute; thể hay kh&ocirc;ng mời Zephyr Đại tướng n&oacute;i cho ta một ch&uacute;t b&acirc;y giờ tr&ecirc;n thế giới n&agrave;y c&oacute; c&aacute;i g&igrave; chuyện th&uacute; vị kh&ocirc;ng c&oacute;? Lại hoặc l&agrave; n&oacute;i c&aacute;i g&igrave; người th&uacute; vị?&quot;<br />\r\nNghe Claudius những lời n&agrave;y, Zephyr cười cười, mở miệng n&oacute;i ra: &quot;Đ&atilde; St.Claudius ng&agrave;i c&oacute; hứng th&uacute; nghe ta n&oacute;i n&oacute;i c&aacute;i n&agrave;y ch&uacute;t, như vậy ta liền b&ecirc;u xấu.&quot;<br />\r\nThẳng đến c&aacute;i n&agrave;y l&uacute;c, Zephyr mới xem như ph&aacute;t hiện vị n&agrave;y St.Claudius tr&ecirc;n người một ch&uacute;t b&igrave;nh thường hiện tượng, đối với cố sự c&ugrave;ng thế giới cảm thấy rất hứng th&uacute;, l&uacute;c n&agrave;y mới ph&ugrave; hợp một đứa b&eacute; biểu hiện m&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\nVừa ăn, một b&ecirc;n kể, Claudius thỉnh thoảng c&ograve;n biết xen v&agrave;o đặt c&acirc;u hỏi một ch&uacute;t cổ qu&aacute;i kỳ lạ vấn đề, m&agrave; Zephyr đều l&agrave; rất c&oacute; ki&ecirc;n nhẫn tại giảng giải lấy.<br />\r\nNắm vị n&agrave;y Hải qu&acirc;n Đại tướng ph&uacute;c, Claudius cuối c&ugrave;ng l&agrave; với c&aacute;i thế giới n&agrave;y c&agrave;ng th&ecirc;m khắc s&acirc;u hi&ecirc;̉u rõ m&ocirc;̣t chút, tr&ecirc;n đại thể c&ugrave;ng những g&igrave; m&igrave;nh biết kh&ocirc;ng c&oacute; g&igrave; sai biệt, b&acirc;y giờ thế giới mặc d&ugrave; ph&acirc;n loạn, nhưng Hải qu&acirc;n c&ugrave;ng Ch&iacute;nh Phủ Thế Giới coi như che đậy được tr&agrave;ng diện, những c&aacute;i kia lợi hại đ&aacute;m gia hỏa, tr&ecirc;n cơ bản cũng tại thế giới mới b&ecirc;n trong pha trộn lấy, bao qu&aacute;t Tứ Hải c&ugrave;ng Grand Line nửa trước đoạn ở b&ecirc;n trong, Ch&iacute;nh Phủ Thế Giới đối những địa phương n&agrave;y, đều c&oacute; rất mạnh quản khống cường độ.<br />\r\n&quot;Th&igrave; ra l&agrave; thế, c&aacute;i thế giới n&agrave;y đ&atilde; vậy c&ograve;n qu&aacute; th&uacute; vị, xem ra một mực th&acirc;m cư MariJoa ta, quả thật c&oacute; ch&uacute;t kiến thức n&ocirc;ng cạn, bi&ecirc;̉n cả vậy m&agrave; như thế rộng lớn. . .&quot; Claudius cười ha hả mở miệng n&oacute;i ra.<br />\r\nZephyr nh&igrave;n trước mắt tiểu gia hỏa n&agrave;y n&oacute;i lời c&ograve;n vẻ nho nh&atilde;, l&agrave;m cho hắn cũng cảm thấy c&oacute; ch&uacute;t kỳ qu&aacute;i, tốt trong khoảng thời gian n&agrave;y tiếp x&uacute;c, cũng l&agrave;m cho hắn hiểu được trước mắt c&aacute;i n&agrave;y nhỏ Thi&ecirc;n Long Nh&acirc;n c&oacute; ch&uacute;t đặc biệt, bao nhi&ecirc;u cũng coi l&agrave; quen thuộc, ngược lại l&agrave; kh&ocirc;ng c&oacute; qu&aacute; mức để &yacute; loại n&agrave;y kh&oacute; chịu cảm gi&aacute;c.<br />\r\n&quot;St.Claudius nếu l&agrave; cảm thấy hứng th&uacute;, về sau c&oacute; thể đi th&ecirc;m Tứ Hải đi đi, c&ugrave;ng thế giới mới kh&aacute;c biệt, Tứ Hải hải vực kh&iacute; hậu vẫn l&agrave; rất an ổn, t&iacute;nh nguy hiểm cũng nhỏ rất nhiều.&quot; Zephyr cười cười, mở miệng đề nghị.<br />\r\n&quot;Đ&uacute;ng vậy a, lu&ocirc;n lu&ocirc;n muốn bao nhi&ecirc;u đi đi tốt.&quot; Claudius gật gật đầu n&oacute;i, hơi hơi dừng một ch&uacute;t về sau, Claudius vừa nh&igrave;n về ph&iacute;a Zephyr, mở miệng n&oacute;i ra: &quot;Bất qu&aacute; v&ocirc; luận l&agrave; ở đ&acirc;u b&ecirc;n trong, t&oacute;m lại vẫn c&ograve;n c&oacute; ch&uacute;t nguy hiểm kh&ocirc;ng phải sao? Tứ Hải cũng được, MariJoa cũng được.&quot;<br />\r\nNghe Claudius lời n&oacute;i n&agrave;y, Zephyr &aacute;nh mắt hơi rụt rụt, cũng kh&ocirc;ng c&oacute; nhanh ch&oacute;ng đ&aacute;p lại c&aacute;i g&igrave;.<br />\r\nClaudius cũng nở nụ cười, tiếp tục mở miệng n&oacute;i ra: &quot;Ta nghe n&oacute;i Zephyr Đại tướng ngươi đối với bồi dưỡng tuổi trẻ đám hải qu&acirc;n rất c&oacute; hứng th&uacute;? Tại hai năm trước thời điểm, c&ograve;n tham dự g&acirc;y dựng Hải qu&acirc;n sĩ quan trại huấn luyện?&quot;<br />\r\nZephyr ngược lại l&agrave; c&oacute; ch&uacute;t kỳ qu&aacute;i Claudius sẽ biết chuyện của m&igrave;nh, bất qu&aacute; nghĩ lại, đ&acirc;y cũng kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; bảo mật đồ vật, với lại người ta thế nhưng l&agrave; Thi&ecirc;n Long Nh&acirc;n, như thế ưa th&iacute;ch nghe chuyện xưa lời n&oacute;i, n&oacute;i kh&ocirc;ng chừng l&agrave; tại MariJoa nghe ai n&oacute;i qua đ&acirc;u?<br />\r\nCho n&ecirc;n cũng kh&ocirc;ng c&oacute; che lấp c&aacute;i g&igrave;, Zephyr gật gật đầu, mở miệng n&oacute;i ra: &quot;Đ&uacute;ng l&agrave; may mắn tham dự qua.&quot;<br />\r\n&quot;Như vậy, chắc hẳn Zephyr Đại tướng đối với dạy bảo người trẻ tuổi rất c&oacute; &yacute; nghĩ của m&igrave;nh a? Nếu như c&oacute; thể m&agrave; n&oacute;i, ta muốn từ Zephyr Đại tướng nơi n&agrave;y học ch&uacute;t g&igrave;, th&ocirc;ng qua lần n&agrave;y tao ngộ, ta cũng coi l&agrave; minh bạch, d&ugrave; l&agrave; ta l&agrave; Thi&ecirc;n Long Nh&acirc;n, nhưng như cũ chẳng phải an to&agrave;n, người cuối c&ugrave;ng c&oacute; khả năng dựa v&agrave;o, cũng chỉ c&oacute; m&igrave;nh th&ocirc;i.&quot; Claudius nhẹ giọng mở miệng n&oacute;i ra.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:434px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'cung-zephyr-dai-tuong-chung-tien-com-trua', 1, '3', '2022-07-31 07:53:50', '2022-08-01 02:20:15');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(8, '1', 'Quỷ dị chung cư', '<p>L&yacute; Ẩn chợt mở mắt.</p>\r\n\r\n<p>Tr&aacute;i tim của hắn, giờ ph&uacute;t n&agrave;y giống như lửa đốt đau đớn kh&ocirc;ng th&ocirc;i.</p>\r\n\r\n<p>Lại. . . Tới rồi sao?</p>\r\n\r\n<p>Lần n&agrave;y, l&agrave; đến phi&ecirc;n ta?</p>\r\n\r\n<p>L&yacute; Ẩn chống đ lấy ngồi dậy, vặn ra đầu giường đ&egrave;n b&agrave;n. Giờ ph&uacute;t n&agrave;y, tr&aacute;i tim kịch liệt đau đớn, cuối c&ugrave;ng l&agrave; hơi kh&ocirc;i phục m&ocirc;̣t chút.</p>\r\n\r\n<p>Hắn xuống giường, mang d&eacute;p, tiếp theo, đi ra ph&ograve;ng ngủ, đi tới ph&iacute;a ngo&agrave;i ph&ograve;ng kh&aacute;ch, đem đ&egrave;n mở ra.</p>\r\n\r\n<p>Hắn thấy được vật kia.</p>\r\n\r\n<p>Ph&ograve;ng kh&aacute;ch tuyết trắng tr&ecirc;n v&aacute;ch tường, giờ ph&uacute;t n&agrave;y, th&igrave;nh l&igrave;nh xuất hiện một nh&oacute;m ho&agrave;n to&agrave;n do m&aacute;u tươi tạo th&agrave;nh chữ! N&agrave;y cực độ quỷ dị tr&agrave;ng cảnh, nếu l&agrave; b&igrave;nh thường người, tại n&agrave;y nửa đ&ecirc;m thời khắc nh&igrave;n thấy, đều sẽ dọa đến hồn phi ph&aacute;ch t&aacute;n.</p>\r\n\r\n<p>Nhưng L&yacute; Ẩn lại kh&ocirc;ng c&oacute; phản ứng g&igrave;.</p>\r\n\r\n<p>&quot;Lại đến phi&ecirc;n ta. . .&quot;</p>\r\n\r\n<p>Hắn cẩn thận nh&igrave;n về ph&iacute;a h&agrave;ng chữ kia.</p>\r\n\r\n<p>&quot;Năm 2010 ngày mùng 7 tháng 6 - ngày mùng 7 tháng 7, đi X thị ngoại &ocirc; th&agrave;nh phố U Thủy th&ocirc;n, cũng cư trụ đ&acirc;̀y n&agrave;y một th&aacute;ng thời gian.&quot;</p>\r\n\r\n<p>H&agrave;ng chữ bằng m&aacute;u quỷ dị n&agrave;y, tại L&yacute; Ẩn sau khi xem xong, bỗng nhi&ecirc;n bắt đầu trở n&ecirc;n mơ hồ l&ecirc;n, m&aacute;u thế m&agrave; bắt đầu giống như l&agrave; bị v&aacute;ch tường hấp thu đi v&agrave;o, từ từ. . . Biến mất.</p>\r\n\r\n<p>U Thủy th&ocirc;n. . .</p>\r\n\r\n<p>L&yacute; Ẩn nhớ kỹ c&aacute;i t&ecirc;n n&agrave;y.</p>\r\n\r\n<p>C&ograve;n muốn trở về ngủ, nhưng l&agrave; đ&atilde; kh&ocirc;ng thể n&agrave;o ngủ. Hắn dứt kho&aacute;t cũng đem đ&egrave;n của ph&ograve;ng kh&aacute;ch mở ra, r&oacute;t một ch&eacute;n tr&agrave;, uống. Hắn kh&ocirc;ng c&oacute; th&oacute;i quen h&uacute;t thuốc, cho n&ecirc;n n&oacute;i cũng chỉ c&oacute; thể uống tr&agrave;. Mặc d&ugrave; c&agrave; ph&ecirc; c&oacute; lẽ hiệu quả c&agrave;ng tốt hơn , nhưng L&yacute; Ẩn lu&ocirc;n cảm thấy, thưởng thức hương trà thời điểm, t&acirc;m t&igrave;nh dễ d&agrave;ng b&igrave;nh tĩnh trở lại.</p>\r\n\r\n<p>Xem trong nước tr&agrave; rọi ra mặt m&igrave;nh, L&yacute; Ẩn nắm chặt ch&eacute;n tr&agrave;.</p>\r\n\r\n<p>Chuyển v&agrave;o c&aacute;i n&agrave;y chung cư cư trụ, đ&atilde;. . . Gần một năm.</p>\r\n\r\n<p>Mặc d&ugrave; một năm tựa hồ kh&ocirc;ng l&acirc;u lắm, nhưng L&yacute; Ẩn lại cảm gi&aacute;c, phảng phất vượt qua v&ocirc; số năm.</p>', '<p>L&yacute; Ẩn chợt mở mắt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tr&aacute;i tim của hắn, giờ ph&uacute;t n&agrave;y giống như lửa đốt đau đớn kh&ocirc;ng th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lại. . . Tới rồi sao?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lần n&agrave;y, l&agrave; đến phi&ecirc;n ta?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn chống đỡ lấy ngồi dậy, vặn ra đầu giường đ&egrave;n b&agrave;n. Giờ ph&uacute;t n&agrave;y, tr&aacute;i tim kịch liệt đau đớn, cuối c&ugrave;ng l&agrave; hơi kh&ocirc;i phục m&ocirc;̣t chút.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn xuống giường, mang d&eacute;p, tiếp theo, đi ra ph&ograve;ng ngủ, đi tới ph&iacute;a ngo&agrave;i ph&ograve;ng kh&aacute;ch, đem đ&egrave;n mở ra.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn thấy được vật kia.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ph&ograve;ng kh&aacute;ch tuyết trắng tr&ecirc;n v&aacute;ch tường, giờ ph&uacute;t n&agrave;y, th&igrave;nh l&igrave;nh xuất hiện một nh&oacute;m ho&agrave;n to&agrave;n do m&aacute;u tươi tạo th&agrave;nh chữ! N&agrave;y cực độ quỷ dị tr&agrave;ng cảnh, nếu l&agrave; b&igrave;nh thường người, tại n&agrave;y nửa đ&ecirc;m thời khắc nh&igrave;n thấy, đều sẽ dọa đến hồn phi ph&aacute;ch t&aacute;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng L&yacute; Ẩn lại kh&ocirc;ng c&oacute; phản ứng g&igrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Lại đến phi&ecirc;n ta. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn cẩn thận nh&igrave;n về ph&iacute;a h&agrave;ng chữ kia.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Năm 2010 ngày mùng 7 tháng 6 - ngày mùng 7 tháng 7, đi X thị ngoại &ocirc; th&agrave;nh phố U Thủy th&ocirc;n, cũng cư trụ đ&acirc;̀y n&agrave;y một th&aacute;ng thời gian.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&agrave;ng chữ bằng m&aacute;u quỷ dị n&agrave;y, tại L&yacute; Ẩn sau khi xem xong, bỗng nhi&ecirc;n bắt đầu trở n&ecirc;n mơ hồ l&ecirc;n, m&aacute;u thế m&agrave; bắt đầu giống như l&agrave; bị v&aacute;ch tường hấp thu đi v&agrave;o, từ từ. . . Biến mất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>U Thủy th&ocirc;n. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn nhớ kỹ c&aacute;i t&ecirc;n n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&ograve;n muốn trở về ngủ, nhưng l&agrave; đ&atilde; kh&ocirc;ng thể n&agrave;o ngủ. Hắn dứt kho&aacute;t cũng đem đ&egrave;n của ph&ograve;ng kh&aacute;ch mở ra, r&oacute;t một ch&eacute;n tr&agrave;, uống. Hắn kh&ocirc;ng c&oacute; th&oacute;i quen h&uacute;t thuốc, cho n&ecirc;n n&oacute;i cũng chỉ c&oacute; thể uống tr&agrave;. Mặc d&ugrave; c&agrave; ph&ecirc; c&oacute; lẽ hiệu quả c&agrave;ng tốt hơn , nhưng L&yacute; Ẩn lu&ocirc;n cảm thấy, thưởng thức hương trà thời điểm, t&acirc;m t&igrave;nh dễ d&agrave;ng b&igrave;nh tĩnh trở lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xem trong nước tr&agrave; rọi ra mặt m&igrave;nh, L&yacute; Ẩn nắm chặt ch&eacute;n tr&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chuyển v&agrave;o c&aacute;i n&agrave;y chung cư cư trụ, đ&atilde;. . . Gần một năm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mặc d&ugrave; một năm tựa hồ kh&ocirc;ng l&acirc;u lắm, nhưng L&yacute; Ẩn lại cảm gi&aacute;c, phảng phất vượt qua v&ocirc; số năm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;y thứ hai, &aacute;nh b&igrave;nh minh vừa l&oacute; rạng thời điểm, nằm ở ph&ograve;ng kh&aacute;ch trước b&agrave;n ăn L&yacute; Ẩn tỉnh lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tr&ecirc;n người hắn chỉ mặc một kiện đồ ngủ đơn bạc, đắp một điều thảm l&ocirc;ng, như vậy ngủ một giấc, cũng kh&ocirc;ng biết c&oacute; thể hay kh&ocirc;ng cảm mạo.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trước b&agrave;n ch&eacute;n tr&agrave;, c&ograve;n s&oacute;t lại tr&agrave; cũng đ&atilde; ho&agrave;n to&agrave;n l&agrave;m lạnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn xem xem ph&ograve;ng kh&aacute;ch đồng hồ treo tường, đ&atilde;. . . Buổi s&aacute;ng sáu giờ r&ocirc;̀i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn dụi dụi con mắt, lập tức đi rửa mặt, mặc chỉnh tề, sau đ&oacute;, ch&iacute;nh m&igrave;nh cho m&igrave;nh l&agrave;m một bữa ăn s&aacute;ng, chi&ecirc;n một trứng, lại đem trứng gia nhập h&ocirc;m qua mua cắt miếng b&aacute;nh m&igrave; b&ecirc;n trong kẹp tốt, b&ocirc;i một điểm salad tương, liền xem như h&ocirc;m nay điểm t&acirc;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn kỳ thật rất tinh th&ocirc;ng nấu nướng, chủ yếu l&agrave; trường kỳ một m&igrave;nh sinh hoạt, cho n&ecirc;n nhất định phải học tập tr&ugrave; nghệ, thời gian d&agrave;i, thế m&agrave; nấu được một tay đồ ăn ngon.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cắn trong miệng tự chế sandwich, lại lấy ra l&ograve; vi ba b&ecirc;n trong sữa b&ograve;, L&yacute; Ẩn lật ra lịch ng&agrave;y, t&iacute;nh to&aacute;n th&aacute;ng, cũng ch&iacute;nh l&agrave; th&aacute;ng 6 an b&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn l&agrave; một internet tiểu thuyết gia, hơn nữa tại tr&ecirc;n mạng cũng rất nổi danh, trước mắt đ&atilde; c&ugrave;ng một nh&agrave; trang web tiểu thuyết k&yacute; kết, cho n&ecirc;n ng&agrave;y thường chỉ cần ở nh&agrave; chuy&ecirc;n t&acirc;m g&otilde; chữ, thu lấy tiền th&ugrave; lao l&agrave; c&oacute; thể.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kh&ocirc;ng c&oacute; vấn đề qu&aacute; lớn.&quot; Hắn cắn trong miệng sandwich, n&oacute;i: &quot;Th&aacute;ng 6 chỉ cần mang theo Laptop , ấn l&uacute;c đổi mới tiểu thuyết l&agrave; c&oacute; thể. &Acirc;n. . . Chỉ l&agrave; kh&ocirc;ng biết đến l&uacute;c đ&oacute; sẽ ph&aacute;t sinh t&igrave;nh huống g&igrave;.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tiếp theo, hắn bu&ocirc;ng xuống lịch ng&agrave;y, nhai nuốt trong miệng b&aacute;nh m&igrave; c&ugrave;ng trứng tr&aacute;ng, siết chặt song quyền.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuyệt đối phải c&ograve;n sống. . . C&ograve;n sống về tới đ&acirc;y đến!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mặc ho&agrave;n tất sau, hắn đi ra cửa ph&ograve;ng, nhưng căn bản kh&ocirc;ng c&oacute; kh&oacute;a lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn chỗ ở l&agrave; tại lầu bốn ph&ograve;ng 404. Tương đương điềm xấu một số ph&ograve;ng, nhưng, đ&acirc;y cũng kh&ocirc;ng phải l&agrave; L&yacute; Ẩn lựa chọn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đi tới chung cư lầu một. Lầu một kh&ocirc;ng c&oacute; hộ gia đ&igrave;nh, lại l&agrave; một c&ugrave;ng loại nhà khách cự đại lễ đường. Lễ đường c&aacute;c nơi đều trưng b&agrave;y ghế s&ocirc; pha c&ugrave;ng c&aacute;i b&agrave;n, cung cấp người nghỉ ngơi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; ở trong đ&oacute; một chiếc sofa b&ecirc;n tr&ecirc;n, ngồi ba người.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ba người kia, nh&igrave;n thấy L&yacute; Ẩn hướng b&ecirc;n n&agrave;y đi tới, đều ph&acirc;n ph&acirc;n đứng dậy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bọn họ theo thứ tự l&agrave; một người cao, đeo k&iacute;nh &acirc;u phục thanh ni&ecirc;n, c&ugrave;ng một đội mũ, bộ d&aacute;ng thanh t&uacute; tiểu hỏa tử, c&ugrave;ng với một mặc m&agrave;u xanh v&aacute;y, khu&ocirc;n mặt phi thường đ&aacute;ng y&ecirc;u xinh đẹp thiếu nữ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;C&aacute;c ngươi. . . C&aacute;c ngươi v&aacute;ch tường, đều xuất hiện h&agrave;ng chữ bằng m&aacute;u kia đi?&quot; L&yacute; Ẩn trầm ng&acirc;m một l&aacute;t, hỏi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ba người đều kh&ocirc;ng hẹn m&agrave; c&ugrave;ng gật gật đầu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bọn họ, cũng đều l&agrave; t&ograve;a chung cư n&agrave;y hộ gia đ&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Th&igrave; ra l&agrave; thế, như vậy ch&iacute; &iacute;t lần n&agrave;y c&oacute; bốn người l&agrave;m bạn.&quot; L&yacute; Ẩn nhẹ nh&agrave;ng thở ra, cũng c&ugrave;ng nhau ngồi xuống.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bốn người thần sắc, đều rất ngưng trọng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;L&yacute; Ẩn. . .&quot; Kia &aacute;o xanh xinh đẹp thiếu nữ sắc mặt phi thường lo &acirc;u n&oacute;i: &quot;Thật. . . Kh&ocirc;ng c&oacute; chuyện g&igrave; sao? Lần n&agrave;y, thế m&agrave; muốn ch&uacute;ng ta nghỉ ngơi một th&aacute;ng? Hơn nữa c&ograve;n l&agrave; tại rời xa th&agrave;nh thị v&ugrave;ng ngoại th&agrave;nh th&ocirc;n x&oacute;m?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đ&uacute;ng vậy a,&quot; đeo k&iacute;nh &acirc;u phục thanh ni&ecirc;n cũng l&agrave; n&oacute;i ra: &quot;Ta điều tra bản đồ, X thị ngoại &ocirc; th&agrave;nh phố lưng tựa đại sơn, phi thường hoang vu vắng vẻ, liền ngay cả c&aacute;i g&igrave; hi vọng c&ocirc;ng tr&igrave;nh đều kh&ocirc;ng c&oacute; c&ugrave;ng nơi đ&oacute; sản sinh li&ecirc;n hệ, ho&agrave;n to&agrave;n l&agrave; lạc hậu v&ugrave;ng n&uacute;i th&ocirc;n trang a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Trung Quốc n&ocirc;ng th&ocirc;n c&oacute; nhiều lắm.&quot; L&yacute; Ẩn cũng rất l&agrave; trấn định: &quot;Giống như thế ho&agrave;n to&agrave;n c&ugrave;ng th&agrave;nh thị t&aacute;ch rời khốn c&ugrave;ng lạc hậu th&ocirc;n trang, cũng tuyệt đối kh&ocirc;ng phải số &iacute;t. Chớ suy nghĩ qu&aacute; nhiều, d&ugrave; sao, cũng kh&ocirc;ng phải lần đầu ti&ecirc;n.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nhưng l&agrave;. . . Ta c&ograve;n l&agrave; v&ocirc; c&ugrave;ng. . .&quot; Kia đeo k&iacute;nh &acirc;u phục thanh ni&ecirc;n c&ograve;n muốn n&oacute;i tiếp c&aacute;i g&igrave;, kia đội mũ tiểu hỏa tử ngăn trở hắn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bầu kh&ocirc;ng kh&iacute; một ch&uacute;t lại lần nữa sa v&agrave;o trong trầm mặc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lại qua rất l&acirc;u, cũng kh&ocirc;ng c&oacute; người lại xuất hiện, đo&aacute;n chừng, lần n&agrave;y trong nh&agrave; v&aacute;ch tường xuất hiện chữ bằng m&aacute;u, cũng liền bốn người bọn họ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nhanh 7h.&quot; L&yacute; Ẩn xem đồng hồ, n&oacute;i: &quot;Ba người c&aacute;c ngươi đi trước đi l&agrave;m đi, ta tiếp tục ở đ&acirc;y đợi, nếu c&oacute; th&agrave;nh vi&ecirc;n mới, ta lại gọi điện thoại cho c&aacute;c ngươi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn trấn định c&ugrave;ng cơ tr&iacute;, vẫn lu&ocirc;n khiến mấy người bọn họ rất an t&acirc;m, thế l&agrave;, ba người cũng đều ph&acirc;n ph&acirc;n đứng l&ecirc;n, rời khỏi chung cư.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vừa rồi, L&yacute; Ẩn đ&atilde; d&ugrave;ng di động l&ecirc;n mạng, nhưng l&agrave; vẫn lu&ocirc;n kh&ocirc;ng c&oacute; tra ra U Thủy th&ocirc;n nơi n&agrave;y. Bất qu&aacute; nếu n&oacute;i l&agrave; tại X thị ngoại &ocirc; th&agrave;nh phố, rồi sẽ t&igrave;m được.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nếu như chỉ dựa v&agrave;o những đầu mối n&agrave;y t&igrave;m kh&ocirc;ng được, h&agrave;ng chữ bằng m&aacute;u kia liền sẽ chỉ thị, c&agrave;ng th&ecirc;m x&aacute;c thực địa chỉ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; đến ngày mùng 7 tháng 6, bọn họ nhất định phải tiến v&agrave;o U Thủy th&ocirc;n. Cũng tại ngày mùng 7 tháng 7 trước kia, tuyệt đối kh&ocirc;ng thể rời khỏi kia.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuyệt đối kh&ocirc;ng thể. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thời gian như thoi đưa, rất nhanh, đến ng&agrave;y 6 th&aacute;ng 6.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Giờ ph&uacute;t n&agrave;y, l&agrave; tại hạ buổi trưa 2: 30 ph&acirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dốc đứng tr&ecirc;n sơn đạo, L&yacute; Ẩn, hộ tống ba người kia, từng bước từng bước tiến l&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngọn n&uacute;i n&agrave;y, t&ecirc;n l&agrave; Hắc &Ocirc; sơn, m&agrave; kia U Thủy th&ocirc;n, trải qua lặp đi lặp lại kiểm chứng, x&aacute;c nhận liền tại n&agrave;y t&ograve;a Sơn T&acirc;y b&ecirc;n cạnh. Thế n&uacute;i hiểm yếu, c&ugrave;ng ngoại giới giao th&ocirc;ng phi thường kh&ocirc;ng tiện, th&ocirc;n c&ugrave;ng ngoại giới cơ hồ ngăn c&aacute;ch, trải qua tự cấp tự t&uacute;c sinh hoạt, ngay cả thuỷ điện đều kh&ocirc;ng c&oacute;, n&agrave;y c&oacute; thể n&oacute;i l&agrave; tương đương c&ugrave;ng khốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia đeo k&iacute;nh &acirc;u phục thanh ni&ecirc;n, t&ecirc;n l&agrave; Tần Thủ Thi&ecirc;n, hắn l&agrave; một b&aacute;o nhỏ ph&oacute;ng vi&ecirc;n, bởi v&igrave; thường xuy&ecirc;n chạy ở b&ecirc;n ngo&agrave;i tin tức, cho n&ecirc;n cũng l&agrave; kh&ocirc;ng t&iacute;nh qu&aacute; mệt mỏi. Nhưng hai người kh&aacute;c, kia đội mũ tiểu hỏa tử La Hằng Vi&ecirc;m, c&ugrave;ng kia m&agrave;u xanh v&aacute;y xinh đẹp thiếu nữ Diệp Khả H&acirc;n, đều l&agrave; ng&agrave;y b&igrave;nh thường ngồi ph&ograve;ng l&agrave;m việc phổ th&ocirc;ng nh&acirc;n vi&ecirc;n văn ph&ograve;ng, đi mấy c&acirc;y số đường n&uacute;i, gi&agrave;y đều m&agrave;i hỏng, bọn họ đều cảm gi&aacute;c dị thường mỏi mệt. Nhưng l&agrave;, ai cũng kh&ocirc;ng d&aacute;m nghỉ ngơi. Nhất định phải mau ch&oacute;ng trước khi trời tối, đến U Thủy th&ocirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong tay cầm la b&agrave;n c&ugrave;ng bản đồ L&yacute; Ẩn, nh&igrave;n qua thật kh&ocirc;ng c&oacute; l&agrave;m sao mệt nhọc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;L&yacute;. . . L&yacute; Ẩn, &quot; La Hằng Vi&ecirc;m thở kh&ocirc;ng ra hơi hỏi: &quot;Trả, c&ograve;n bao l&acirc;u nữa mới đến a, ta, ch&acirc;n của ta đều nhanh muốn g&atilde;y. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi muốn ngừng liền cứ việc ngừng hảo, &quot; L&yacute; Ẩn xem la b&agrave;n, ngay cả đầu cũng kh&ocirc;ng quay lại n&oacute;i: &quot;Chỉ cần ngươi kh&ocirc;ng sợ chết, ta cũng kh&ocirc;ng quan trọng vứt xuống ngươi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Sao. . . Sao lại thế!&quot; La Hằng Vi&ecirc;m rất r&otilde; r&agrave;ng L&yacute; Ẩn c&aacute; t&iacute;nh, kh&ocirc;ng. . . Kỳ thật, mỗi người đều l&agrave; dạng n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tại c&aacute;i kia chung cư sinh hoạt người, cuối c&ugrave;ng đều sẽ trở n&ecirc;n coi thường người kh&aacute;c sinh mệnh. La Hằng Vi&ecirc;m v&agrave;o ở kia chung cư, cũng liền thời gian nửa năm m&agrave; th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lại đi qua một đỉnh n&uacute;i, L&yacute; Ẩn tinh thần phấn chấn, nh&igrave;n về ph&iacute;a ch&acirc;n n&uacute;i, n&oacute;i: &quot;Mau nh&igrave;n! Đến!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia quả nhi&ecirc;n l&agrave; một rất lớn th&ocirc;n trang. Mặc d&ugrave; xem l&ecirc;n rất tan hoang, bất qu&aacute; ph&ograve;ng ốc dựng được thật kh&ocirc;ng c&oacute; trong tưởng tượng như vậy keo kiệt, nh&igrave;n một c&aacute;i, rộng r&atilde;i thổ địa b&ecirc;n tr&ecirc;n đắp mấy trăm t&ograve;a nh&agrave; lầu, trong đ&oacute; c&ograve;n c&oacute; hai tầng lầu nh&agrave; lầu, nh&agrave; lầu c&ugrave;ng nh&agrave; lầu ở giữa trồng từng d&atilde;y ruộng l&uacute;a, c&ograve;n c&oacute; thể nh&igrave;n thấy rất nhiều người vất vả cần c&ugrave; canh t&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đến nơi n&agrave;y, vấn đề liền kh&ocirc;ng lớn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sau đ&oacute;, bốn người đều hưng phấn hướng ph&iacute;a dưới n&uacute;i tiến đến.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tiến v&agrave;o U Thủy th&ocirc;n. . . Như vậy th&igrave; chẳng kh&aacute;c g&igrave; l&agrave; ho&agrave;n th&agrave;nh kia chữ bằng m&aacute;u chỉ thị.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng c&oacute; người, c&oacute; thể vi phạm kia chữ bằng m&aacute;u chỉ lệnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đi đến ch&acirc;n n&uacute;i, bỗng nhi&ecirc;n L&yacute; Ẩn ch&uacute; &yacute; tới, một g&aacute;nh hai th&ugrave;ng nước, tướng mạo t&uacute; lệ n&ocirc;ng gia nữ h&agrave;i trải qua, thế l&agrave; lập tức gọi lại n&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Vị c&ocirc; nương n&agrave;y. . .&quot; L&yacute; Ẩn đi l&ecirc;n trước hỏi: &quot;Nơi n&agrave;y. . . L&agrave; U Thủy th&ocirc;n kh&ocirc;ng sai đi?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia n&ocirc;ng gia nữ h&agrave;i xem l&ecirc;n đại kh&aacute;i mười bảy mười t&aacute;m tuổi, một đ&ocirc;i mắt đại đại, rất l&agrave; m&ecirc; người.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đúng, kh&ocirc;ng sai. C&aacute;c ngươi. . .&quot; Kia n&ocirc;ng gia nữ h&agrave;i xem L&yacute; Ẩn, kh&ocirc;ng hiểu hỏi: &quot;Xin hỏi. . . Ngươi l&agrave;. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ch&uacute;ng ta l&agrave; từ K thị đến du lịch, đi rất d&agrave;i đường n&uacute;i, nghĩ đến trong l&agrave;ng nghỉ ch&acirc;n một ch&uacute;t.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nữ h&agrave;i lập tức hưng phấn n&oacute;i: &quot;Thật kh&oacute; được, thế m&agrave; lại c&oacute; người trong th&agrave;nh đến a. Ta gọi A T&uacute;, c&aacute;c ngươi đi theo ta, ta mang c&aacute;c ngươi đi Vương đại th&uacute;c tiệm cơm đi, Vương đại th&uacute;c gia m&igrave; chay hương vị thật phi thường tốt a, đều l&agrave; d&ugrave;ng nước giếng l&agrave;m.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nước giếng. . . Th&ocirc;n n&agrave;y, quả nhi&ecirc;n kh&ocirc;ng c&oacute; nước m&aacute;y sao?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hơn nữa, vừa rồi nh&igrave;n ra xa thời điểm, cũng x&aacute;c thực kh&ocirc;ng c&oacute; ph&aacute;t hiện cột điện. Xem ra th&ocirc;n n&agrave;y quả nhi&ecirc;n tương đương lạc hậu a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia gọi A T&uacute; n&ocirc;ng gia nữ h&agrave;i cũng l&agrave; rất nhiệt t&igrave;nh, L&yacute; Ẩn bọn họ liền đi theo n&agrave;ng đi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c n&agrave;y, L&yacute; Ẩn tầm mắt, trong l&uacute;c v&ocirc; t&igrave;nh, dời về ph&iacute;a A T&uacute; sau lưng chọn một c&aacute;i th&ugrave;ng nước. Nước trong th&ugrave;ng kia xem l&ecirc;n rất l&agrave; thanh tịnh, kh&ocirc;ng biết l&agrave; nước giếng vẫn l&agrave; nước suối.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;. . . L&yacute; Ẩn th&acirc;n thể, tại l&uacute;c n&agrave;y lại giống như l&agrave; giống như bị chạm điện chấn trụ!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia th&ugrave;ng nước dưới đ&aacute;y, th&igrave;nh l&igrave;nh, xuất hiện một trương v&ocirc; c&ugrave;ng trắng bệch, hai mắt v&ocirc; đồng nữ nh&acirc;n gương mặt!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khu&ocirc;n mặt n&agrave;y xuất hiện cực kỳ đột ngột, l&agrave;m cho người căn bản đều phản ứng kh&ocirc;ng kịp!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn nh&aacute;y mắt r&uacute;t lui mấy bước, tim đập loạn kh&ocirc;ng th&ocirc;i. Sau đ&oacute; lại đi qua nh&igrave;n về ph&iacute;a kia th&ugrave;ng nước. . . Như cũ l&agrave; một th&ugrave;ng phổ th&ocirc;ng thanh thủy m&agrave; th&ocirc;i, kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o chỗ dị thường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;, L&yacute; Ẩn lại phi thường r&otilde; r&agrave;ng, vừa rồi hắn nh&igrave;n thấy, tuyệt kh&ocirc;ng phải hoa mắt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A. . . A T&uacute; c&ocirc; nương. . .&quot; L&yacute; Ẩn cường h&agrave;nh khắc chế nội t&acirc;m sợ h&atilde;i, n&oacute;i: &quot;Ta, ch&uacute;ng ta vẫn l&agrave; kh&ocirc;ng l&agrave;m phiền ngươi. Xem ngươi g&aacute;nh th&ugrave;ng nước cũng rất bận.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngay sau đ&oacute; cũng kh&ocirc;ng đợi A T&uacute; đ&aacute;p lại, hắn liền nhanh ch&oacute;ng quay đầu, sải bước đi hướng ba c&aacute;i kia đồng bạn, cho bọn họ ra hiệu một &aacute;nh mắt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ba người lập tức ngầm hiểu, vội v&agrave;ng c&ugrave;ng L&yacute; Ẩn rời khỏi A T&uacute;. A T&uacute; th&igrave; l&agrave; lăng lăng đợi tại chỗ, kh&ocirc;ng r&otilde; địa phương n&agrave;o đắc tội bọn họ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đi ở trong th&ocirc;n, bọn họ gương mặt lạ đưa tới rất nhiều người ch&uacute; &yacute;. Người trong th&ocirc;n đều nghi hoặc xem bọn họ, n&agrave;y Hắc &Ocirc; sơn cũng kh&ocirc;ng phải c&aacute;i g&igrave; danh thắng cổ t&iacute;ch, tại sao c&oacute; thể c&oacute; du kh&aacute;ch đến đ&acirc;y?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Diệp Khả H&acirc;n thấp giọng hỏi thăm L&yacute; Ẩn: &quot;L&yacute;. . . L&yacute; Ẩn ti&ecirc;n sinh, vừa rồi. . . Ngươi đ&atilde;, nh&igrave;n thấy, &#39;Vật kia&#39; rồi?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn giữ im lặng, chỉ l&agrave; gật đầu l&agrave;m trả lời.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mặc d&ugrave; sớm biết, tiến v&agrave;o U Thủy th&ocirc;n, tuyệt đối kh&ocirc;ng thể n&agrave;o b&igrave;nh an v&ocirc; sự vượt qua một th&aacute;ng, nhưng l&agrave;, kh&ocirc;ng nghĩ tới vừa mới bước v&agrave;o mảnh n&agrave;y th&ocirc;n, vẫn l&agrave; tại giữa ban ng&agrave;y, liền. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn nh&igrave;n về ph&iacute;a đỉnh đầu bầu trời, chỉ cảm thấy Th&aacute;i Dương tựa hồ cũng đang dần dần bị đ&aacute;m m&acirc;y che lấp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lần n&agrave;y. . . Bốn người bọn họ, có th&ecirc;̉ c&oacute; mấy, sống đến ngày mùng 7 tháng 7, trở lại K thị kia chung cư đi đ&acirc;u?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một năm. . . Chuyện như vậy, L&yacute; Ẩn kinh lịch rất nhiều lần.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn c&oacute; v&agrave;i lần, đều su&yacute;t nữa chết đi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Su&yacute;t nữa chết tại &quot;Những vật kia&quot; tr&ecirc;n tay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Những vật kia&quot;, l&agrave; từ đ&acirc;u tới, v&igrave; sao lại xuất hiện, kh&ocirc;ng c&oacute; người biết được, nhưng l&agrave; ở khắp mọi nơi, c&oacute; thể tại bất luận c&aacute;i g&igrave; địa phương , bất kỳ c&aacute;i g&igrave; thời gian, đưa ngươi v&agrave;o chỗ chết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; ở c&aacute;i thế giới n&agrave;y, duy nhất c&oacute; thể n&eacute; tr&aacute;nh &quot;Những vật kia&quot; địa phương, ch&iacute;nh l&agrave; tại K thị trong t&ograve;a chung cư kia. Chỉ c&oacute; ở nơi đ&oacute;, bọn họ tuyệt đối c&oacute; thể cam đoan an to&agrave;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;y, ch&iacute;nh l&agrave; trở th&agrave;nh kia t&ograve;a chung cư hộ gia đ&igrave;nh sau, tuyệt đối kh&ocirc;ng c&aacute;ch n&agrave;o vi phạm quy tắc.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:1684px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'quy-di-chung-cu', 3, '1233123', '2022-07-31 08:04:09', '2022-07-31 08:04:09'),
(9, '2', 'Tiến vào', '<p>Hết thảy đều l&agrave; từ một năm trước bắt đầu từ ng&agrave;y đ&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&acirc;y giờ trở về nhớ tới, đều cảm gi&aacute;c l&agrave; một trận &aacute;c mộng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đại học tốt nghiệp sau, L&yacute; Ẩn kh&ocirc;ng để &yacute; phụ mẫu phản đối, dứt kho&aacute;t trở th&agrave;nh một internet tiểu thuyết gia. Đương nhi&ecirc;n, tuyệt đối kh&ocirc;ng phải l&agrave; bởi v&igrave; ước mơ g&igrave;, l&yacute; tưởng c&aacute;c loại nguy&ecirc;n nh&acirc;n, thuần t&uacute;y cũng chỉ l&agrave; l&agrave;m một chuyến n&agrave;y m&igrave;nh c&oacute; thể kiếm tiền th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Viết tiểu thuyết mạng l&agrave;m chức vị ch&iacute;nh, l&agrave; c&oacute; rất &iacute;t, m&agrave; L&yacute; Ẩn ch&iacute;nh l&agrave; một. Kỳ thật hắn tiền nhuận b&uacute;t thu nhập cũng kh&ocirc;ng thể t&iacute;nh qu&aacute; cao, cũng chỉ c&oacute; thể miễn cưỡng duy tr&igrave; sinh hoạt. Hơn nữa, tương lai hắn t&aacute;c phẩm mới cũng chưa chắc c&oacute; thể tiếp tục được hoan ngh&ecirc;nh. C&aacute;i ni&ecirc;n đại n&agrave;y, c&oacute; mới nới cũ độc giả c&oacute; nhiều lắm, huống chi l&agrave; tiểu thuyết mạng loại n&agrave;y thức ăn nhanh văn học, vỏn vẹn chỉ l&agrave; mọi người vật điều h&ograve;a th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn một lần cũng rất hoang mang. Lu&ocirc;n cảm gi&aacute;c, nh&acirc;n sinh tương lai rất kh&ocirc;ng c&oacute; phương hướng. Tại K thị một người thu&ecirc; ph&ograve;ng ở, mỗi ng&agrave;y mới vừa dậy liền mở ra m&aacute;y vi t&iacute;nh g&otilde; chữ truyền l&ecirc;n, thường ng&agrave;y mua sắm đều căn bản l&agrave; th&ocirc;ng qua internet, quả thực liền biến th&agrave;nh &quot;Trạch nam&quot;.</p>', '<p>Hết thảy đều l&agrave; từ một năm trước bắt đầu từ ng&agrave;y đ&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&acirc;y giờ trở về nhớ tới, đều cảm gi&aacute;c l&agrave; một trận &aacute;c mộng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đại học tốt nghiệp sau, L&yacute; Ẩn kh&ocirc;ng để &yacute; phụ mẫu phản đối, dứt kho&aacute;t trở th&agrave;nh một internet tiểu thuyết gia. Đương nhi&ecirc;n, tuyệt đối kh&ocirc;ng phải l&agrave; bởi v&igrave; ước mơ g&igrave;, l&yacute; tưởng c&aacute;c loại nguy&ecirc;n nh&acirc;n, thuần t&uacute;y cũng chỉ l&agrave; l&agrave;m một chuyến n&agrave;y m&igrave;nh c&oacute; thể kiếm tiền th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Viết tiểu thuyết mạng l&agrave;m chức vị ch&iacute;nh, l&agrave; c&oacute; rất &iacute;t, m&agrave; L&yacute; Ẩn ch&iacute;nh l&agrave; một. Kỳ thật hắn tiền nhuận b&uacute;t thu nhập cũng kh&ocirc;ng thể t&iacute;nh qu&aacute; cao, cũng chỉ c&oacute; thể miễn cưỡng duy tr&igrave; sinh hoạt. Hơn nữa, tương lai hắn t&aacute;c phẩm mới cũng chưa chắc c&oacute; thể tiếp tục được hoan ngh&ecirc;nh. C&aacute;i ni&ecirc;n đại n&agrave;y, c&oacute; mới nới cũ độc giả c&oacute; nhiều lắm, huống chi l&agrave; tiểu thuyết mạng loại n&agrave;y thức ăn nhanh văn học, vỏn vẹn chỉ l&agrave; mọi người vật điều h&ograve;a th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn một lần cũng rất hoang mang. Lu&ocirc;n cảm gi&aacute;c, nh&acirc;n sinh tương lai rất kh&ocirc;ng c&oacute; phương hướng. Tại K thị một người thu&ecirc; ph&ograve;ng ở, mỗi ng&agrave;y mới vừa dậy liền mở ra m&aacute;y vi t&iacute;nh g&otilde; chữ truyền l&ecirc;n, thường ng&agrave;y mua sắm đều căn bản l&agrave; th&ocirc;ng qua internet, quả thực liền biến th&agrave;nh &quot;Trạch nam&quot;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn viết nhiều số đều l&agrave; chiến tranh đề t&agrave;i tiểu thuyết, mặc d&ugrave; bản th&acirc;n hắn căn bản kh&ocirc;ng phải hiểu rất r&otilde; chiến tranh. M&agrave; tiểu thuyết của hắn tại tr&ecirc;n mạng, nh&acirc;n kh&iacute; cũng ng&agrave;y c&agrave;ng trượt xuống. Th&aacute;ng n&agrave;y, đặt mua tiểu thuyết chương tiết người c&agrave;ng l&uacute;c c&agrave;ng &iacute;t, một th&aacute;ng thu nhập vẫn chưa tới tám trăm khối. Đừng n&oacute;i l&agrave; sinh hoạt h&agrave;ng ng&agrave;y, liền ngay cả tiền thu&ecirc; nh&agrave; đều muốn th&agrave;nh vấn đề.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lu&ocirc;n cảm gi&aacute;c, nh&acirc;n sinh chạy tới một đoạn rất quẫn b&aacute;ch con đường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;y đ&oacute;. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn tại K thị tr&ecirc;n một con phố bu&ocirc;n b&aacute;n dạo bước đi, b&igrave;nh thường l&uacute;c n&agrave;y, hắn đều l&agrave; ở nh&agrave; đổi mới tiểu thuyết, nhưng l&agrave; b&acirc;y giờ. . . Hắn lại tại c&acirc;n nhắc sau n&agrave;y nh&acirc;n sinh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&iacute;nh m&igrave;nh cuối c&ugrave;ng vẫn l&agrave; qu&aacute; mức ng&acirc;y thơ, mặc d&ugrave; sớm dự liệu được sẽ c&oacute; một ng&agrave;y n&agrave;y, nhưng l&agrave; cho rằng chỉ cần qua một đoạn ng&agrave;y, c&oacute; nhất định dự trữ, kia c&oacute; thể đầu tư cổ phiếu, tương lai n&oacute;i kh&ocirc;ng chừng c&ograve;n c&oacute; thể ch&iacute;nh m&igrave;nh lập nghiệp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;, sự thật căn bản cũng kh&ocirc;ng c&oacute; đơn thuần như vậy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đi t&igrave;m việc l&agrave;m sao? Hắn kh&ocirc;ng c&oacute; bất cứ c&ocirc;ng t&aacute;c tư lịch, hiện tại c&aacute;i g&igrave; đơn vị kh&ocirc;ng giảng cứu kinh nghiệm? Vừa tiến v&agrave;o x&atilde; hội sinh vi&ecirc;n, nhiều v&ocirc; số kể, căn bản liền sẽ kh&ocirc;ng bị đặc biệt coi trọng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Niết tr&ecirc;n tay một phần t&igrave;m việc tuần san, L&yacute; Ẩn l&ocirc;ng m&agrave;y c&agrave;ng nhăn c&agrave;ng chặt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đi đi, hắn đ&atilde; bất tri bất gi&aacute;c tiến v&agrave;o một x&atilde; khu b&ecirc;n trong, tiến v&agrave;o một ng&otilde; nhỏ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Vẫn l&agrave;. . . Đi trước t&igrave;m c&ocirc;ng việc đi, &quot; L&yacute; Ẩn thực sự l&agrave;m kh&ocirc;ng được về đến nh&agrave; đi cầu phụ mẫu, cho d&ugrave; liều cũng phải trước vượt qua nan quan mới được.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>D&ugrave; sao. . . Th&aacute;ng n&agrave;y tiền thu&ecirc; nh&agrave; lại khất nợ, chủ thu&ecirc; nh&agrave; cũng chỉ c&oacute; đem ch&iacute;nh m&igrave;nh đuổi ra ngo&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c trước từ trong nh&agrave; dời ra ngo&agrave;i sống một m&igrave;nh, cũng l&agrave; bởi v&igrave; c&ugrave;ng phụ mẫu l&yacute; niệm kh&ocirc;ng hợp, nghĩ c&oacute; một ng&agrave;y ch&iacute;nh m&igrave;nh c&oacute; th&agrave;nh tựu lại đi về nh&agrave;. Nhưng l&agrave;. . . H&ocirc;m nay lại trở th&agrave;nh như vậy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đi đi, hắn bỗng nhi&ecirc;n chợt cảm gi&aacute;c ph&iacute;a sau lưng truyền đến một trận &yacute; lạnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn đem nằm ngang ở trước mặt t&igrave;m việc tuần san lấy ra, m&agrave; tại dưới th&aacute;i dương, chiếu rọi ở tr&ecirc;n mặt đất, c&aacute;i b&oacute;ng của m&igrave;nh. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lại như cũ đem kia phần t&igrave;m việc tuần san cầm tại trước mặt!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tiếp theo, liền chỉ thấy hắn c&aacute;i b&oacute;ng, ph&aacute;t sinh c&agrave;ng đ&aacute;ng sợ biến h&oacute;a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn, r&otilde; r&agrave;ng hiện tại cũng kh&ocirc;ng nh&uacute;c nh&iacute;ch, nhưng l&agrave;, dưới ch&acirc;n c&aacute;i b&oacute;ng lại bắt đầu di động! C&aacute;i b&oacute;ng, tho&aacute;t ly L&yacute; Ẩn dưới ch&acirc;n, tr&ecirc;n mặt đất hướng ph&iacute;a trước mắt ng&otilde; nhỏ g&oacute;c rẽ di động m&agrave; đi!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn lập tức sững sờ ngay tại chỗ, qua mấy gi&acirc;y mới phản ứng lại!</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn giờ ph&uacute;t n&agrave;y, kh&ocirc;ng c&oacute; c&aacute;i b&oacute;ng!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bất chấp tất cả, hắn l&uacute;c ấy đầu &oacute;c một mảnh trống rỗng, chỉ l&agrave; chạy như bay, dọc theo c&aacute;i b&oacute;ng kia chạy trốn phương hướng! M&agrave; tại n&agrave;y ngoằn ngo&egrave;o trong ng&otilde; nhỏ, c&aacute;i b&oacute;ng tốc độ di động thật nhanh, L&yacute; Ẩn cũng l&agrave; hao tốn kh&iacute; lực rất lớn mới đuổi kịp n&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Rốt cục, lại xuy&ecirc;n qua một ng&otilde; nhỏ, L&yacute; Ẩn nh&igrave;n thấy c&aacute;i b&oacute;ng tiến v&agrave;o một khối phi thường trống trải đất trống. Khối n&agrave;y đất trống trung ương. . . C&oacute; một t&ograve;a phi thường cao chung cư.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia t&ograve;a chung cư, th&ocirc; sơ giản lược nh&igrave;n qua hẳn l&agrave; sẽ kh&ocirc;ng thấp hơn hai mươi tầng. Chung cư chiếm diện t&iacute;ch cũng rất lớn, ước chừng c&oacute; hơn hai trăm m&eacute;t vu&ocirc;ng, mặt tường l&agrave; m&agrave;u trắng, ngoại l&acirc;u tr&ecirc;n mặt th&igrave; l&agrave; từng d&atilde;y chỉnh tề ban c&ocirc;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; t&ograve;a chung cư n&agrave;y, c&oacute; một cực k&igrave; quỷ dị địa phương.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện tại l&agrave; giữa trưa, Th&aacute;i Dương trực tiếp chiếu xuống, m&agrave; n&agrave;y cự đại chung cư. . . Tr&ecirc;n mặt đất, thế m&agrave; kh&ocirc;ng c&oacute; c&aacute;i b&oacute;ng!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave;m sao c&oacute; thể?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn liều mạng dụi dụi con mắt, hắn m&atilde;nh liệt ho&agrave;i nghi m&igrave;nh l&agrave; đang nằm mơ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave; cảnh tượng trước mắt lại l&agrave; kh&ocirc;ng thể nghi ngờ sự thật.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chung cư lầu một chỗ chỗ lối v&agrave;o, l&agrave; một cửa xoay. M&agrave; c&aacute;i b&oacute;ng của m&igrave;nh, th&igrave; tại tr&ecirc;n mặt đất, di động đến kia cửa xoay chỗ, tr&ecirc;n mặt đất tự động xuy&ecirc;n v&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Giờ n&agrave;y khắc n&agrave;y, khi c&ograve;n b&eacute; nghe rất nhiều chuyện ma, to&agrave;n bộ đều tại L&yacute; Ẩn trong đầu kh&ocirc;i phục l&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn l&uacute;c n&agrave;y khoảng c&aacute;ch t&ograve;a chung cư n&agrave;y cũng bất qu&aacute; liền ba mươi m&eacute;t khoảng c&aacute;ch, m&agrave; kia t&ograve;a nh&igrave;n như phổ th&ocirc;ng chung cư, lại phảng phất l&agrave; một muốn ăn thịt người qu&aacute;i th&uacute;, bất cứ l&uacute;c n&agrave;o cũng sẽ đem hắn th&ocirc;n phệ hết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng, kh&ocirc;ng biết thế n&agrave;o, hắn c&ograve;n hướng t&ograve;a chung cư n&agrave;y đi qua.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Phảng phất, l&agrave; c&aacute;i g&igrave;, đang triệu ho&aacute;n ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đi đến kia cửa xoay trước, L&yacute; Ẩn th&ograve; tay ra, th&ocirc;i động kia cửa xoay, đi v&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; đập v&agrave;o mi mắt, l&agrave; một rộng r&atilde;i đại đường. Dưới đất l&agrave; trơn b&oacute;ng đ&aacute; cẩm thạch, tr&ecirc;n trần nh&agrave; treo thủy tinh đ&egrave;n treo, đại đường b&ecirc;n tr&aacute;i c&oacute; thể nh&igrave;n thấy thang lầu, m&agrave; ph&iacute;a b&ecirc;n phải th&igrave; c&oacute; ba bộ thang m&aacute;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chỉ thấy c&aacute;i b&oacute;ng của hắn, tr&ecirc;n mặt đất bay tới một t&ograve;a thang m&aacute;y trước, ngay sau đ&oacute; kia cửa thang m&aacute;y mở ra, c&aacute;i b&oacute;ng bay v&agrave;o sau, cửa thang m&aacute;y liền đ&oacute;ng lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn lập tức chạy như bay đến cửa thang m&aacute;y trước, khẩn trương xem thang m&aacute;y tầng lầu biểu hiện b&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thang m&aacute;y, ngừng đến lầu bốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn vội v&agrave;ng chạy đến mặt kh&aacute;c một bộ thang m&aacute;y trước, nhấn xuống c&aacute;i n&uacute;t, cửa thang m&aacute;y chậm r&atilde;i mở ra, hắn th&igrave; một bước xa vọt v&agrave;o , ấn xuống lầu bốn c&aacute;i n&uacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo số tầng lầu l&ecirc;n cao, L&yacute; Ẩn t&acirc;m cũng bị treo l&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;i b&oacute;ng của ta. . . C&aacute;i b&oacute;ng của ta. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn trong l&ograve;ng cường liệt cảm gi&aacute;c đến, nếu như mất đi c&aacute;i b&oacute;ng, &yacute; nghĩa chuyện đ&aacute;ng sợ thế n&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Rốt cục, đến lầu bốn, cửa thang m&aacute;y mở ra nh&aacute;y mắt, hắn lập tức đi ra ngo&agrave;i, đứng tại một h&agrave;nh lang b&ecirc;n tr&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;i kia h&agrave;nh lang b&ecirc;n tr&ecirc;n, tia s&aacute;ng cũng rất sung t&uacute;c, nhưng l&agrave; c&agrave;ng l&agrave; đi về ph&iacute;a trước, liền c&agrave;ng c&oacute; một loại s&acirc;u thẳm cảm gi&aacute;c. H&agrave;nh lang hai b&ecirc;n, c&oacute; từng c&aacute;nh cửa. Tr&ecirc;n cửa đều ghi ch&uacute; số cửa ph&ograve;ng, &quot;411&quot;, &quot;410&quot;, &quot;412&quot; . . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; khi L&yacute; Ẩn đi đến &quot;404&quot; thất cổng l&uacute;c, bỗng nhi&ecirc;n, chỉ thấy c&aacute;i b&oacute;ng của hắn, từ căn ph&ograve;ng kia chỗ khe cửa bay ra, lại về tới L&yacute; Ẩn dưới ch&acirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng m&agrave;, c&aacute;i b&oacute;ng như cũ l&agrave;m L&yacute; Ẩn kh&ocirc;ng c&oacute; l&agrave;m động t&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;i b&oacute;ng tr&ecirc;n tay phải, tựa hồ trảo thứ g&igrave;, nhưng kh&ocirc;ng c&aacute;ch n&agrave;o thấy r&otilde; r&agrave;ng. Ngay sau đ&oacute;, c&aacute;i b&oacute;ng liền đem vật kia, nh&eacute;t v&agrave;o c&aacute;i b&oacute;ng trong t&uacute;i quần. Xem như xong động t&aacute;c n&agrave;y sau, hai tay của n&oacute; liền rủ xuống, kh&ocirc;ng nh&uacute;c nh&iacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn thử thăm d&ograve; huy động l&ecirc;n c&aacute;nh tay phải, l&agrave;m hắn ngo&agrave;i &yacute; muốn ch&iacute;nh l&agrave;, c&aacute;i b&oacute;ng cũng c&ugrave;ng l&agrave;m l&ecirc;n huy động tay phải động t&aacute;c đến!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;i b&oacute;ng. . . Quy vị rồi?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c n&agrave;y, hắn đem tay phải, th&ograve; v&agrave;o vừa rồi c&aacute;i b&oacute;ng bỏ đồ vật kia t&uacute;i quần, m&ograve; tới, một vật cứng rắn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&oacute; l&agrave; một thanh ch&igrave;a kho&aacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&igrave;a kho&aacute; xem l&ecirc;n cũng rất phổ th&ocirc;ng, chỉ l&agrave; ph&iacute;a tr&ecirc;n khắc &quot;404&quot; con số n&agrave;y. L&yacute; Ẩn bởi vậy nh&igrave;n m&igrave;nh b&ecirc;n cạnh kia phiến ph&ograve;ng 404 cửa, run rẩy, đem ch&igrave;a kho&aacute; cắm v&agrave;o trong lỗ kh&oacute;a, nhẹ nh&agrave;ng vừa chuyển động, cửa, liền mở ra.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn đi v&agrave;o ph&ograve;ng 404 b&ecirc;n trong.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đập v&agrave;o mắt chỗ, l&agrave; một tương đối lớn ph&ograve;ng kh&aacute;ch, trưng b&agrave;y b&agrave;n tr&agrave;, ghế s&ocirc; pha, tủ quần &aacute;o, TV, thậm ch&iacute; TV c&ograve;n c&oacute; k&egrave;m theo karaoke, lập thể &acirc;m hưởng, tựa hồ vẫn l&agrave; tr&ecirc;n thị trường tương đương qu&yacute; một c&aacute;i. M&agrave; TV b&ecirc;n cạnh, ch&iacute;nh l&agrave; một loạt cửa sổ s&aacute;t đất, b&ecirc;n ngo&agrave;i li&ecirc;n tiếp ban c&ocirc;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nh&igrave;n kỹ một phen, c&ograve;n c&oacute; k&egrave;m theo hai gian ph&ograve;ng ngủ, thư ph&ograve;ng, ph&ograve;ng vệ sinh c&ugrave;ng ph&ograve;ng bếp. Hơn nữa gian ph&ograve;ng trang ho&agrave;ng được phi thường tốt, gia cụ đều rất mới, hơn nữa đa số l&agrave; h&agrave;ng hiệu. Gian ph&ograve;ng ước chừng bảy t&aacute;m chục m&eacute;t vu&ocirc;ng, lấy trước mắt gi&aacute; ph&ograve;ng m&agrave; n&oacute;i, c&aacute;i ph&ograve;ng n&agrave;y liền xem như ph&ograve;ng secondhand, cũng tuyệt đối tiện nghi kh&ocirc;ng đến nơi n&agrave;o đi. Ch&iacute; &iacute;t. . . Lấy ch&iacute;nh m&igrave;nh trước mắt thu nhập, đối ph&ograve;ng ốc như vậy căn bản l&agrave; theo kh&ocirc;ng kịp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;i n&agrave;y. . . Rốt cuộc l&agrave; chuyện g&igrave; xảy ra?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi. . . Ngươi l&agrave;, mới tới hộ gia đ&igrave;nh?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn nghe được thanh &acirc;m n&agrave;y lập tức quay đầu qua xem x&eacute;t, đập v&agrave;o mi mắt l&agrave; một mặc m&agrave;u xanh v&aacute;y xinh đẹp thiếu nữ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi. . . Ngươi l&agrave; ai?&quot; L&yacute; Ẩn lập tức tr&agrave;n ngập cảnh gi&aacute;c hỏi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi, ngươi đừng sợ, &quot; thiếu nữ vội v&agrave;ng kho&aacute;t kho&aacute;t tay, n&oacute;i: &quot;Ta, ta giống như ngươi. . . Cũng đều l&agrave; c&aacute;i b&oacute;ng đột nhi&ecirc;n tự động chạy v&agrave;o c&aacute;i n&agrave;y chung cư, sau đ&oacute;, liền biến th&agrave;nh c&aacute;i n&agrave;y chung cư hộ gia đ&igrave;nh. Ngươi, cũng cầm đến ch&igrave;a kho&aacute; đi?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn giật m&igrave;nh, ngay sau đ&oacute; cầm ch&igrave;a kh&oacute;a, nhanh ch&acirc;n đi hướng thiếu nữ kia, hỏi: &quot;Đến c&ugrave;ng l&agrave; chuyện g&igrave; xảy ra? C&aacute;i n&agrave;y. . . T&ograve;a chung cư n&agrave;y, rốt cuộc. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nếu ngươi l&agrave; mới hộ gia đ&igrave;nh, căn cứ th&ocirc;ng lệ, ta dẫn ngươi đi thấy ch&uacute;ng ta lầu trưởng đi. Hắn l&agrave; ch&uacute;ng ta nhất tr&iacute; đề cử, ngươi đi gặp hắn về sau, hắn liền sẽ n&oacute;i cho ngươi t&igrave;nh huống, tiếp sẽ đem ngươi đến đăng k&yacute; xuống tới.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;L&acirc;u. . . Lầu trưởng?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;T&ograve;a chung cư n&agrave;y trước mắt hết thảy ở ba mươi ch&iacute;n người, tăng th&ecirc;m ngươi ch&iacute;nh l&agrave; bốn mươi người. Tất cả ch&uacute;ng ta. . . To&agrave;n bộ đều l&agrave;, cường h&agrave;nh bị t&ograve;a chung cư n&agrave;y tr&oacute;i buộc, cũng kh&ocirc;ng được kh&ocirc;ng phải vẫn ở chỗ n&agrave;y.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn lập tức giận t&iacute;m mặt: &quot;Ngươi. . . N&oacute;i đ&ugrave;a c&aacute;i g&igrave;? Ta l&agrave;m sao c&oacute; thể ở tại nơi n&agrave;y địa phương quỷ qu&aacute;i!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thiếu nữ kia cười khổ m&agrave; n&oacute;i: &quot;Kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o a. . . Ta, ta l&uacute;c ấy vừa tiến v&agrave;o t&ograve;a chung cư n&agrave;y thời điểm cũng rất sợ h&atilde;i, nhưng l&agrave;. . . Thật kh&ocirc;ng c&oacute; biện ph&aacute;p. B&oacute;ng d&aacute;ng của ch&uacute;ng ta một khi tiến v&agrave;o t&ograve;a chung cư n&agrave;y, được trao cho chiếc ch&igrave;a kh&oacute;a kia, liền đại biểu trở th&agrave;nh chung cư hộ gia đ&igrave;nh, như thế. . . Cũng chạy kh&ocirc;ng tho&aacute;t t&ograve;a chung cư n&agrave;y.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&oacute;i đến đ&acirc;y, thiếu nữ kia cũng l&agrave; một mặt đau đớn biểu cảm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kh&ocirc;ng. . . Kh&ocirc;ng thể trốn tho&aacute;t?&quot; L&yacute; Ẩn giờ ph&uacute;t n&agrave;y c&agrave;ng ng&agrave;y c&agrave;ng cảm gi&aacute;c t&igrave;nh huống trở n&ecirc;n quỷ dị, hỏi: &quot;V&igrave; sao kh&ocirc;ng thể chạy trốn?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Qu&aacute; khứ, cũng từng c&oacute; bị chọn l&agrave;m hộ gia đ&igrave;nh, nhưng l&agrave;, lại kh&ocirc;ng ở tại trong căn hộ người. Người như vậy. . . Liền sẽ bị c&aacute;i b&oacute;ng của m&igrave;nh giết chết.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Cái . . . C&aacute;i g&igrave;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn lập tức trợn mắt h&aacute; hốc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi. . . N&oacute;i đ&ugrave;a sao?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi kh&ocirc;ng phải nh&igrave;n thấy kh&ocirc;ng? C&aacute;i b&oacute;ng kia đ&atilde; bị c&aacute;i n&agrave;y chung cư ho&agrave;n to&agrave;n thao t&uacute;ng chi phối. Ng&agrave;y b&igrave;nh thường, c&aacute;i b&oacute;ng ho&agrave;n to&agrave;n c&oacute; thể do ch&iacute;nh ngươi t&ugrave;y &yacute; khống chế c&ugrave;ng chi phối, nhưng l&agrave;. . . Nếu như ngươi l&agrave;m vi phạm quy tắc h&agrave;nh vi, c&aacute;i b&oacute;ng liền sẽ thao t&uacute;ng ch&iacute;nh ngươi th&acirc;n thể đi tự s&aacute;t. Ta v&agrave;o ở c&aacute;i n&agrave;y chung cư hai năm. . . Thấy qua rất nhiều, bởi v&igrave; vi phạm chung cư quy tắc, bị c&aacute;i b&oacute;ng thao t&uacute;ng m&agrave; tự s&aacute;t tử vong người.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Người, thế m&agrave; tr&aacute;i lại bị c&aacute;i b&oacute;ng thao t&uacute;ng?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hoang đường như vậy kh&ocirc;ng trải qua, nhưng l&agrave; L&yacute; Ẩn lại kh&ocirc;ng c&aacute;ch n&agrave;o phản b&aacute;c. Bởi v&igrave;, hắn vừa rồi nhưng liền thấy tận mắt ch&iacute;nh m&igrave;nh c&aacute;i b&oacute;ng dị thường biến h&oacute;a a!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn bắt đầu cảm gi&aacute;c được t&ograve;a chung cư n&agrave;y, ho&agrave;n to&agrave;n si&ecirc;u việt hắn thường thức nhận tri.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;C&aacute;i n&agrave;y. . . T&ograve;a chung cư n&agrave;y. . . L&agrave; ai kiến tạo? Rốt cuộc, tại sao muốn lựa chọn ch&uacute;ng ta l&agrave;m hộ gia đ&igrave;nh?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thiếu nữ kia lắc đầu, n&oacute;i: &quot;Ta kh&ocirc;ng biết. . . Hai năm qua ta cũng suy tư rất nhiều lần, nhưng l&agrave; ta đều kh&ocirc;ng thể l&yacute; giải, thật kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o l&yacute; giải! T&ograve;a chung cư n&agrave;y, bản th&acirc;n li&ecirc;̀n là v&ocirc; ph&aacute;p l&yacute; giải ! Bất qu&aacute;, chỉ c&oacute; một điểm c&oacute; thể khẳng định. Ch&uacute;ng ta kh&ocirc;ng c&aacute;ch n&agrave;o nh&igrave;n thấy người kiến tạo chung cư n&agrave;y, m&agrave; đối với người kh&ocirc;ng phải hộ gia đ&igrave;nh m&agrave; n&oacute;i, chung cư cũng l&agrave; kh&ocirc;ng tồn tại ở tr&ecirc;n thế giới n&agrave;y.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi. . . Lời n&agrave;y của ngươi l&agrave; c&oacute; &yacute; g&igrave;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi, c&oacute; phải hay kh&ocirc;ng xuy&ecirc;n qua một ng&otilde; nhỏ đến đ&acirc;y?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đ&uacute;ng. . . Đ&uacute;ng a. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kỳ thật, kia căn bản ch&iacute;nh l&agrave; điều ng&otilde; cụt. Chỉ c&oacute; người bị chọn l&agrave;m hộ gia đ&igrave;nh, mới c&oacute; thể th&ocirc;ng qua ng&otilde; hẻm kia, tiến v&agrave;o c&aacute;i n&agrave;y chung cư. Chung cư chiếm đoạt c&oacute; khối n&agrave;y đất trống, cũng l&agrave; căn bản kh&ocirc;ng tồn tại. L&agrave; căn bản kh&ocirc;ng tồn tại thổ địa a!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kh&ocirc;ng. . . Kh&ocirc;ng tồn tại?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn xem rơi ngo&agrave;i cửa sổ ban c&ocirc;ng, b&ecirc;n ngo&agrave;i, san s&aacute;t nối tiếp nhau nh&agrave; cao tầng th&igrave;nh l&igrave;nh c&oacute; thể thấy được.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ch&uacute;ng ta nh&igrave;n thấy thế giới b&ecirc;n ngo&agrave;i, nhưng l&agrave;, thế giới b&ecirc;n ngo&agrave;i l&agrave; nh&igrave;n kh&ocirc;ng thấy ch&uacute;ng ta.&quot; Thiếu nữ nh&igrave;n ra L&yacute; Ẩn nghi hoặc, hướng hắn giải th&iacute;ch: &quot;Chờ ngươi rời khỏi chung cư, đi ra ng&otilde; hẻm kia, ngươi cũng sẽ căn bản kh&ocirc;ng nh&igrave;n thấy chung cư. Người kh&ocirc;ng phải hộ gia đ&igrave;nh, căn bản kh&ocirc;ng biết tr&ecirc;n thế giới n&agrave;y tồn tại c&aacute;i n&agrave;y chung cư.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng, t&ograve;a chung cư n&agrave;y l&agrave; x&aacute;c thực tồn tại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hơn nữa một khi tiến v&agrave;o, chẳng kh&aacute;c n&agrave;o bước l&ecirc;n một điều cực k&igrave; hung hiểm khủng bố con đường kh&ocirc;ng lối về!</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:3175px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'tien-vao', 3, '4234', '2022-07-31 08:06:30', '2022-07-31 08:06:30');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(10, '3', 'Chữ bằng máu quy tắc', '<p>T&ograve;a chung cư n&agrave;y tồn tại lịch sử, nghe n&oacute;i đ&atilde; kh&oacute; m&agrave; t&iacute;nh to&aacute;n. M&agrave; tại t&ograve;a chung cư n&agrave;y ở đ&acirc;y qua người, cũng l&agrave; tương đương nhiều. Mấy chục năm xuống tới, c&aacute;c hộ gia đ&igrave;nh ở giữa đều c&oacute; ăn &yacute; c&ugrave;ng kinh nghiệm, minh bạch đến n&ecirc;n l&agrave;m c&aacute;i g&igrave;, kh&ocirc;ng n&ecirc;n l&agrave;m c&aacute;i g&igrave;. Như thế n&agrave;o đối đầu ch&iacute;nh m&igrave;nh c&oacute; lợi, như thế n&agrave;o l&agrave;m đối với m&igrave;nh bất lợi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cho n&ecirc;n, mỗi khi c&oacute; hộ gia đ&igrave;nh mới chuyển v&agrave;o đến, đều sẽ c&oacute; l&atilde;o hộ gia đ&igrave;nh tiến đến n&oacute;i cho hắn biết tỉ mỉ ch&uacute; &yacute; hạng mục. Nếu như n&oacute;i kh&ocirc;ng n&oacute;i r&otilde; r&agrave;ng, liền c&oacute; khả năng x&uacute;c phạm cấm kỵ, chết kh&ocirc;ng c&oacute; chỗ ch&ocirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; cơ bản nhất quy tắc c&oacute; hai điều. M&agrave; một khi trở th&agrave;nh chung cư hộ gia đ&igrave;nh, như vậy, n&agrave;y hai điều quy tắc, v&ocirc; luận như thế n&agrave;o đều muốn tu&acirc;n thủ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thứ nhất, cũng l&agrave; cơ bản nhất, trừ chữ bằng m&aacute;u chỉ thị muốn ra ngo&agrave;i t&igrave;nh huống b&ecirc;n ngo&agrave;i, tuyệt đối kh&ocirc;ng thể li&ecirc;n tục rời khỏi chung cư thời gian vượt qua bốn mươi t&aacute;m giờ. Một khi vi phạm điều quy tắc n&agrave;y, liền sẽ chết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thứ hai, trở th&agrave;nh hộ gia đ&igrave;nh sau, ch&iacute;nh m&igrave;nh chỗ ở gian ph&ograve;ng, v&aacute;ch tường xuất hiện chữ bằng m&aacute;u sau, nhất định phải tuyệt đối tu&acirc;n thủ chữ bằng m&aacute;u chỉ thị, nếu như c&oacute; ch&uacute;t vi phạm, cũng sẽ chết.</p>', '<p>&nbsp;</p>\r\n\r\n<p>T&ograve;a chung cư n&agrave;y tồn tại lịch sử, nghe n&oacute;i đ&atilde; kh&oacute; m&agrave; t&iacute;nh to&aacute;n. M&agrave; tại t&ograve;a chung cư n&agrave;y ở đ&acirc;y qua người, cũng l&agrave; tương đương nhiều. Mấy chục năm xuống tới, c&aacute;c hộ gia đ&igrave;nh ở giữa đều c&oacute; ăn &yacute; c&ugrave;ng kinh nghiệm, minh bạch đến n&ecirc;n l&agrave;m c&aacute;i g&igrave;, kh&ocirc;ng n&ecirc;n l&agrave;m c&aacute;i g&igrave;. Như thế n&agrave;o đối đầu ch&iacute;nh m&igrave;nh c&oacute; lợi, như thế n&agrave;o l&agrave;m đối với m&igrave;nh bất lợi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cho n&ecirc;n, mỗi khi c&oacute; hộ gia đ&igrave;nh mới chuyển v&agrave;o đến, đều sẽ c&oacute; l&atilde;o hộ gia đ&igrave;nh tiến đến n&oacute;i cho hắn biết tỉ mỉ ch&uacute; &yacute; hạng mục. Nếu như n&oacute;i kh&ocirc;ng n&oacute;i r&otilde; r&agrave;ng, liền c&oacute; khả năng x&uacute;c phạm cấm kỵ, chết kh&ocirc;ng c&oacute; chỗ ch&ocirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; cơ bản nhất quy tắc c&oacute; hai điều. M&agrave; một khi trở th&agrave;nh chung cư hộ gia đ&igrave;nh, như vậy, n&agrave;y hai điều quy tắc, v&ocirc; luận như thế n&agrave;o đều muốn tu&acirc;n thủ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thứ nhất, cũng l&agrave; cơ bản nhất, trừ chữ bằng m&aacute;u chỉ thị muốn ra ngo&agrave;i t&igrave;nh huống b&ecirc;n ngo&agrave;i, tuyệt đối kh&ocirc;ng thể li&ecirc;n tục rời khỏi chung cư thời gian vượt qua bốn mươi t&aacute;m giờ. Một khi vi phạm điều quy tắc n&agrave;y, liền sẽ chết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thứ hai, trở th&agrave;nh hộ gia đ&igrave;nh sau, ch&iacute;nh m&igrave;nh chỗ ở gian ph&ograve;ng, v&aacute;ch tường xuất hiện chữ bằng m&aacute;u sau, nhất định phải tuyệt đối tu&acirc;n thủ chữ bằng m&aacute;u chỉ thị, nếu như c&oacute; ch&uacute;t vi phạm, cũng sẽ chết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn tận mắt nh&igrave;n đến qua kh&ocirc;ng tin n&agrave;y hai điều quy tắc, m&agrave; kh&ocirc;ng để &yacute; tới chỉ thị chữ bằng m&aacute;u, rời khỏi chung cư hộ gia đ&igrave;nh. M&agrave; những c&aacute;i kia hộ gia đ&igrave;nh, sau n&agrave;y cũng to&agrave;n bộ đều cực k&igrave; quỷ dị tử vong.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đều kh&ocirc;ng ngoại lệ!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Diệp Khả H&acirc;n, l&agrave; ph&ograve;ng 403 hộ gia đ&igrave;nh, cho n&ecirc;n l&agrave; L&yacute; Ẩn s&aacute;t v&aacute;ch h&agrave;ng x&oacute;m. M&agrave; La Hằng Vi&ecirc;m th&igrave; l&agrave; ph&ograve;ng 507 hộ gia đ&igrave;nh, hắn đ&atilde; ở nửa năm, chấp h&agrave;nh qua hai lần chỉ thị chữ bằng m&aacute;u đều sống tiếp được, kia Tần Thủ Thi&ecirc;n th&igrave; l&agrave; ở hơn ch&iacute;n th&aacute;ng, thi h&agrave;nh ba lần chỉ thị chữ bằng m&aacute;u.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chỉ thị chữ bằng m&aacute;u , b&igrave;nh thường l&agrave; chỉ định đi đặc biệt địa điểm, l&agrave;m một ch&uacute;t đặc biệt h&agrave;nh vi, m&agrave; trong đoạn thời gian n&agrave;y, tất nhi&ecirc;n sẽ ph&aacute;t sinh ra rất nhiều quỷ dị v&ocirc; c&ugrave;ng hiện tượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thường thường, c&ograve;n chưa &yacute; thức được, liền đ&atilde; chết rồi. Thậm ch&iacute; chết thời điểm, đều c&ograve;n kh&ocirc;ng c&aacute;ch n&agrave;o &yacute; thức được m&igrave;nh đ&atilde; chết rồi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nghe l&ecirc;n tương đương kh&ocirc;ng thể tưởng tượng nổi, thậm ch&iacute; c&oacute; thể n&oacute;i kh&oacute; c&oacute; thể tưởng tượng, nhưng. . . Lại l&agrave; sự thật kh&ocirc;ng thể chối c&atilde;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn l&uacute;c ấy, cũng l&agrave; hao tốn thời gian rất l&acirc;u, mới ho&agrave;n to&agrave;n tiếp nhận đ&acirc;y hết thảy. D&ugrave; sao, bất th&igrave;nh l&igrave;nh hết thảy, qu&aacute; mức kh&ocirc;ng thực tế. Nhưng l&agrave;, hắn l&uacute;c ấy, kh&ocirc;ng c&oacute; dũng kh&iacute; d&aacute;m rời khỏi chung cư vượt qua bốn mươi t&aacute;m giờ thời gian. D&ugrave; sao, loại kia h&agrave;nh vi c&ugrave;ng tự s&aacute;t kh&ocirc;ng kh&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cuối c&ugrave;ng, hắn hướng chung cư c&aacute;i kh&aacute;c c&aacute;c hộ gia đ&igrave;nh mượn &iacute;t tiền, trả sạch tiền thu&ecirc; nh&agrave;, tiến v&agrave;o t&ograve;a chung cư n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong căn hộ c&aacute;c hộ gia đ&igrave;nh, tr&ecirc;n cơ bản đều giống như hắn, l&agrave; bởi v&igrave; c&aacute;i b&oacute;ng tự động tiến v&agrave;o t&ograve;a chung cư n&agrave;y, đuổi theo c&aacute;i b&oacute;ng qu&aacute; tr&igrave;nh b&ecirc;n trong tiến v&agrave;o. M&agrave; tại t&ograve;a chung cư n&agrave;y b&ecirc;n trong cư trụ thời gian d&agrave;i nhất, ch&iacute;nh l&agrave; lầu trưởng, Hạ Uy&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hạ Uy&ecirc;n, l&agrave; một tuổi trẻ, mang theo k&iacute;nh mắt, nh&igrave;n qua rất nh&atilde; nhặn nho nh&atilde; một nam tử. M&agrave; hắn, cư trụ ở đ&acirc;y t&ograve;a chung cư thời gian, đ&atilde; vượt qua năm năm. Hắn n&oacute;i cho tất cả hộ gia đ&igrave;nh, nếu muốn ở c&aacute;i n&agrave;y trong căn hộ sống s&oacute;t, sinh cơ duy nhất, ch&iacute;nh l&agrave; c&oacute; thể tại quy định thời gian kết th&uacute;c sau, kịp thời trốn về trong căn hộ! V&ocirc; luận địa điểm chỉ định tồn tại c&aacute;i g&igrave;, đụng phải như thế n&agrave;o nguyền rủa, một khi trốn về trong căn hộ, liền c&oacute; thể ho&agrave;n to&agrave;n tho&aacute;t ly hết thảy nguy hiểm. Tiến v&agrave;o chung cư sau lại đi ra ngo&agrave;i, cũng sẽ kh&ocirc;ng cần lo lắng lại nhận tổn thương. Thẳng đến. . . Lần tiếp theo chữ bằng m&aacute;u xuất hiện mới th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một năm qua n&agrave;y, L&yacute; Ẩn tận mắt nh&igrave;n đến qua, bởi v&igrave; kh&ocirc;ng tin c&aacute;c hộ gia đ&igrave;nh, bị chọn l&agrave;m hộ gia đ&igrave;nh nhưng kh&ocirc;ng ở tại trong căn hộ người kết cục. Hắn đ&atilde; từng tiến đến thuyết phục những người kia lập tức chuyển v&agrave;o trong căn hộ ở, nhưng l&agrave; đối phương đều l&agrave; chẳng th&egrave;m ng&oacute; tới. Kết quả. . . Tại vượt qua bốn mươi t&aacute;m giờ đợi, L&yacute; Ẩn đều tận mắt nh&igrave;n thấy, b&oacute;ng của bọn hắn, từng ph&aacute;t sinh biến h&oacute;a, hoặc l&agrave; thao t&uacute;ng bọn họ nhảy lầu, hoặc l&agrave; lấy đao tự s&aacute;t, trong đ&oacute; thảm nhất một người, bị c&aacute;i b&oacute;ng thao t&uacute;ng chạy tới một ng&atilde; tư đường, bị một chiếc xe vận tải đương trường nghiền chết, biến th&agrave;nh một b&atilde;i thịt n&aacute;t. M&agrave; nh&igrave;n thấy một m&agrave;n kia về sau, L&yacute; Ẩn trọn thời gian ba th&aacute;ng nh&igrave;n thấy thịt đều sẽ muốn &oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chỉ thị chữ bằng m&aacute;u , b&igrave;nh thường đều l&agrave; từ dễ đến kh&oacute;. Lần thứ nhất chỉ thị chữ bằng m&aacute;u, độ nguy hiểm nhỏ nhất, sống s&oacute;t tỷ lệ cũng tương đối lớn, nhưng từ lần thứ hai bắt đầu, độ nguy hiểm c&ugrave;ng tr&igrave;nh độ quỷ dị cũng sẽ từng bước gia tăng, tiếp lần thứ ba. . . Lần thứ tư, đều l&agrave; muốn đem hết to&agrave;n lực mới c&oacute; thể may mắn sống s&oacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chỉ thị chữ bằng m&aacute;u, khoảng c&aacute;ch thời gian b&igrave;nh thường l&agrave; lấy th&aacute;ng l&agrave;m đơn vị, căn cứ l&atilde;o hộ gia đ&igrave;nh kinh nghiệm, chưa hề xuất hiện vượt qua nửa năm chưa tại to&agrave;n bộ chung cư tuy&ecirc;n bố chỉ thị chữ bằng m&aacute;u tiền lệ. M&agrave; gian ph&ograve;ng xuất hiện chỉ thị chữ bằng m&aacute;u, tr&aacute;i tim liền sẽ sinh ra cảm gi&aacute;c bỏng, như thế d&ugrave; cho kh&ocirc;ng trong ph&ograve;ng, cũng sẽ lập tức &yacute; thức được gian ph&ograve;ng của m&igrave;nh xuất hiện chữ bằng m&aacute;u. Một khi xem hết chữ bằng m&aacute;u cũng nhớ kỹ, chữ bằng m&aacute;u liền sẽ biến mất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chỉ thị chữ bằng m&aacute;u, xưa nay sẽ kh&ocirc;ng chỉ thị bọn họ kh&ocirc;ng c&aacute;ch n&agrave;o thực hiện h&agrave;nh vi, tỉ như gọi bọn họ đến tr&ecirc;n mặt trăng đi c&aacute;i n&agrave;y h&agrave;nh vi l&agrave; kh&ocirc;ng c&oacute;, đều l&agrave; vật l&yacute; b&ecirc;n tr&ecirc;n c&oacute; thể thực hiện h&agrave;nh vi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cuối c&ugrave;ng, căn cứ c&aacute;c hộ gia đ&igrave;nh cũ v&agrave;i chục năm nay kinh nghiệm, cho ra một c&aacute;i kết luận.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&oacute; ch&iacute;nh l&agrave;. . . Muốn rời khỏi t&ograve;a chung cư n&agrave;y, phương ph&aacute;p chỉ c&oacute; một.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&oacute; ch&iacute;nh l&agrave;. . . Li&ecirc;n tục ho&agrave;n th&agrave;nh mười lần chỉ thị chữ bằng m&aacute;u, c&ograve;n c&oacute; thể c&ograve;n sống, liền c&oacute; thể đạt được tự do, dời khỏi chung cư m&agrave; kh&ocirc;ng cần phải lo lắng sẽ chết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đương nhi&ecirc;n, &quot;Li&ecirc;n tục&quot; hai chữ căn bản ch&iacute;nh l&agrave; dư thừa, bởi v&igrave;, ho&agrave;n th&agrave;nh ti&ecirc;u chuẩn, vỏn vẹn chỉ l&agrave; c&oacute; thể sống trở lại chung cư đi, nếu như chết rồi, tự nhi&ecirc;n cũng liền kh&ocirc;ng c&aacute;ch n&agrave;o lại ho&agrave;n th&agrave;nh chỉ thị chữ bằng m&aacute;u kế tiếp. M&agrave; cứ như vậy người kia cư trụ gian ph&ograve;ng liền sẽ biến th&agrave;nh v&ocirc; chủ gian ph&ograve;ng, thẳng đến hộ gia đ&igrave;nh mới chuyển v&agrave;o đến, mới c&oacute; thể sinh ra chỉ thị chữ bằng m&aacute;u mới. Cho đến nay, hắn cũng đ&atilde; thi h&agrave;nh ba lần chỉ thị chữ bằng m&aacute;u, m&agrave; lần trước chấp h&agrave;nh chỉ thị chữ bằng m&aacute;u, cơ hồ c&oacute; thể n&oacute;i l&agrave; cửu tử nhất sinh! Chỉ thị nội dung rất đơn giản, tiến đến t&ograve;a n&agrave;o đ&oacute; vứt bỏ trường học lầu năm, đợi cho đ&ecirc;m đ&oacute; nửa đ&ecirc;m kh&ocirc;ng giờ liền t&iacute;nh chỉ thị kết th&uacute;c. L&uacute;c ấy, c&ugrave;ng hắn tiếp đến c&ugrave;ng một chỉ thị chữ bằng m&aacute;u, ngay cả hắn ở b&ecirc;n trong hết thảy s&aacute;u người. M&agrave; đ&ecirc;m đ&oacute; vừa đến nửa đ&ecirc;m kh&ocirc;ng giờ, tất cả mọi người vung ch&acirc;n l&ecirc;n muốn chạy trốn ra trường học. Nhưng m&agrave;. . . Mặc kệ đi tới chỗ n&agrave;o, bọn họ đều sẽ ph&aacute;t hiện l&agrave; ng&otilde; cụt! Kế tiếp, tại chạy trốn qu&aacute; tr&igrave;nh b&ecirc;n trong, bọn họ c&ograve;n ph&aacute;t hiện, b&ecirc;n người vốn hảo hảo đồng bạn, đột nhi&ecirc;n liền biến th&agrave;nh một bộ thi thể huyết nhục mơ hồ!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cuối c&ugrave;ng, L&yacute; Ẩn cắn răng, từ cửa sổ nhảy đến tr&ecirc;n một th&acirc;n c&acirc;y, lại b&ograve; xuống đi, sau đ&oacute; ngồi trong đ&oacute; một chết đi đồng bạn xe, đạp xuống lớn nhất ch&acirc;n ga trốn hướng chung cư!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn mặc d&ugrave; học qua l&aacute;i xe, nhưng l&agrave; cũng kh&ocirc;ng c&oacute; bằng l&aacute;i xe, chiếc xe kia lại tương đối kiểu mới, hắn l&uacute;c ấy cũng l&agrave; to&aacute;t hết mồ h&ocirc;i mới l&aacute;i về chung cư chỗ x&atilde; khu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;. . . Liền tại hắn l&uacute;c ấy l&uacute;c xuống xe, liền cảm gi&aacute;c được một b&agrave;n tay t&oacute;m hắn ch&acirc;n!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; l&uacute;c đ&oacute; hắn ng&atilde; tr&ecirc;n mặt đất, chỉ nh&igrave;n thấy. . . Một mặc đồng phục, sắc mặt trắng bệch tiểu h&agrave;i tử, th&acirc;n thể liền d&aacute;n tại xe dưới đ&aacute;y!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; L&yacute; Ẩn quyết định thật nhanh, l&uacute;c n&agrave;y đem ch&acirc;n từ gi&agrave;y b&ecirc;n trong r&uacute;t ra! Tiếp theo, liều mạng chạy v&agrave;o kia trong hẻm nhỏ!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn v&igrave; r&egrave;n luyện ch&iacute;nh m&igrave;nh chạy bộ tốc độ, cơ hồ mỗi một ng&agrave;y đều sẽ đi ph&ograve;ng tập thể thao &eacute;p buộc tự r&egrave;n luyện chạy bộ tốc độ. C&aacute;i kh&aacute;c hộ gia đ&igrave;nh cũng c&ugrave;ng hắn c&ugrave;ng nhau r&egrave;n luyện, đến cuối c&ugrave;ng, cơ hồ mỗi người đều r&egrave;n luyện ra đủ để c&ugrave;ng chạy cự li d&agrave;i vận động vi&ecirc;n c&oacute; thể so với tốc độ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng. . . V&ocirc; luận tốc độ lại nhanh, sau lưng học sinh kia bộ d&aacute;ng tiểu h&agrave;i tử, vẫn l&agrave; đang kh&ocirc;ng ngừng tiếp cận hắn! L&yacute; Ẩn mỗi lần quay đầu, đều sẽ tr&ocirc;ng thấy. . . Hắn đang tại tiếp cận ch&iacute;nh m&igrave;nh! Đ&aacute;ng sợ l&agrave;. . . Hắn chỉ l&agrave; rất đơn thuần đang bước đi, thấy thế n&agrave;o cũng kh&ocirc;ng thể sẽ l&agrave; có th&ecirc;̉ đuổi kịp ch&iacute;nh m&igrave;nh tốc độ!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cuối c&ugrave;ng, khi hắn chạy đến khoảng c&aacute;ch chung cư vỏn vẹn mười mét thời điểm, hắn đ&atilde; nh&igrave;n thấy Hạ Uy&ecirc;n đẳng một đ&aacute;m hộ gia đ&igrave;nh đ&atilde; tại cửa xoay nơi đ&oacute; chờ hắn!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nhanh a! L&yacute; Ẩn!&quot; Khi đ&oacute;, lu&ocirc;n lu&ocirc;n xem l&ecirc;n nh&atilde; nhặn nho nh&atilde; Hạ Uy&ecirc;n, liều mạng xả họng h&ocirc; to: &quot;Lại c&oacute; mười mét, ngươi liền an to&agrave;n! Nhanh a!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo sau n&agrave;y Hạ Uy&ecirc;n n&oacute;i, l&uacute;c ấy bọn họ nh&igrave;n thấy, kia quỷ, khoảng c&aacute;ch L&yacute; Ẩn khoảng c&aacute;ch. . . Kh&ocirc;ng đủ hai mét!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; kia cuối c&ugrave;ng mười mét, L&yacute; Ẩn l&agrave; liều mạng x&ocirc;ng lại, m&agrave; đồng thời ở nơi n&agrave;y, kia quỷ cũng đ&atilde; đem khoảng c&aacute;ch k&eacute;m giảm bớt trở về 0, đ&atilde; đem giang hai c&aacute;nh tay, muốn &ocirc;m chặt L&yacute; Ẩn!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;, Hạ Uy&ecirc;n bọn họ, đều kh&ocirc;ng thể đi ra ngo&agrave;i cứu hắn! D&ugrave; sao, rời khỏi chung cư, sinh mệnh an to&agrave;n liền rốt cuộc kh&ocirc;ng c&oacute; bất cứ bảo đảm!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; L&yacute; Ẩn tại cuối c&ugrave;ng kia một nh&aacute;y mắt, bỗng nhi&ecirc;n tốc độ lại một lần nữa bộc ph&aacute;t, nổi giận gầm l&ecirc;n một tiếng, ph&oacute;ng tới cửa xoay, chợt đẩy tiến đến! M&agrave; ở trong qu&aacute; tr&igrave;nh n&agrave;y, kia quỷ tiểu h&agrave;i, đ&atilde; đụng tới hắn hai b&ecirc;n phần eo!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn tiến v&agrave;o chung cư sau, kia quỷ, liền rốt cuộc v&ocirc; kế khả thi. Tại cửa ra v&agrave;o đứng sau một hồi, th&acirc;n thể h&oacute;a th&agrave;nh kh&oacute;i nhẹ, chậm r&atilde;i biến mất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sự hậu, L&yacute; Ẩn đem l&ecirc;n &aacute;o v&eacute;n l&ecirc;n sau, vạn phần hoảng sợ ph&aacute;t hiện, ch&iacute;nh m&igrave;nh phần eo hai b&ecirc;n, thế m&agrave;. . . Ri&ecirc;ng ph&acirc;̀n mình lưu lại một nho nhỏ m&agrave;u đen thủ ấn! Kia dấu tay, qua một đoạn thời gian rất d&agrave;i, mới ho&agrave;n to&agrave;n biến mất nh&igrave;n kh&ocirc;ng thấy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; tại lần kia sự kiện qua hai th&aacute;ng sau, cũng ch&iacute;nh l&agrave; hiện tại lần n&agrave;y chỉ thị.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đối với m&igrave;nh m&agrave; n&oacute;i, đ&acirc;y l&agrave; lần thứ tư chấp h&agrave;nh chỉ thị chữ bằng m&aacute;u.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuyệt đối. . . L&agrave; nguy hiểm tăng l&ecirc;n tới cực cao t&igrave;nh trạng!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&ocirc;n n&agrave;y so trong tưởng tượng lớn hơn rất nhiều.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bất qu&aacute;, c&ugrave;ng dự t&iacute;nh như vậy, kh&ocirc;ng tồn tại c&oacute; thể cung cấp người cư trụ kh&aacute;ch sạn. Đ&acirc;y cũng l&agrave; rất tự nhi&ecirc;n sự t&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đương nhi&ecirc;n, căn cứ chữ bằng m&aacute;u chỉ thị, nhất định phải &quot;Ở&quot; ở chỗ n&agrave;y. Rời khỏi U Thủy th&ocirc;n phạm vi, liền xem như vi phạm với chỉ thị chữ bằng m&aacute;u.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&ocirc;n n&agrave;y bị d&atilde;y n&uacute;i quấn quanh, chung quanh địa thế hiểm trở, c&oacute; một điều rất d&agrave;i d&ograve;ng s&ocirc;ng trải qua th&ocirc;n trang, trở th&agrave;nh nguồn nước tốt nhất cam đoan. C&oacute; nước n&agrave;y nguy&ecirc;n, liền c&oacute; thể tưới ti&ecirc;u hoa m&agrave;u, người trong th&ocirc;n cũng c&oacute; thể c&oacute; thể tại trong n&uacute;i lớn n&agrave;y vượt qua tự cấp tự t&uacute;c sinh hoạt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong ruộng trồng, đa số l&agrave; l&uacute;a nước. Thậm ch&iacute; một ch&uacute;t vẫn chưa tới mười tuổi tiểu h&agrave;i tử, cũng đi ch&acirc;n đất tại ruộng l&uacute;a b&ecirc;n trong hỗ trợ cấy mạ, mặt mũi tr&agrave;n đầy đều l&agrave; b&ugrave;n đất c&ugrave;ng nước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đi một đoạn đường sau, rốt cục một trung ni&ecirc;n nam nh&acirc;n đi tới, hỏi: &quot;Mấy vị ti&ecirc;n sinh. . . L&agrave; người trong th&agrave;nh?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Những h&agrave;i tử kia cũng đều rất hiếu k&igrave; xem L&yacute; Ẩn bọn họ, d&ugrave; sao, tại trong n&uacute;i lớn n&agrave;y, cũng kh&oacute; được nh&igrave;n thấy một người trong th&agrave;nh. Trong bọn họ rất nhiều người, căn bản chưa thấy qua đ&egrave;n điện, TV c&aacute;c loại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; L&yacute; Ẩn th&igrave; l&agrave; lờ mờ cảm gi&aacute;c được, trung ni&ecirc;n nam nh&acirc;n kia trong giọng n&oacute;i, tựa hồ bao h&agrave;m một ch&uacute;t địch &yacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nếu như c&aacute;c ngươi l&agrave; muốn phỏng vấn chuyện kia ph&oacute;ng vi&ecirc;n, liền lập tức cho ta rời khỏi!&quot; Trung ni&ecirc;n nam nh&acirc;n n&oacute;i đến đ&acirc;y, con mắt trừng rất lớn: &quot;Đều đ&atilde; nhiều năm như vậy, c&ograve;n d&acirc;y dưa kh&ocirc;ng chịu ph&oacute;ng a!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn t&acirc;m chợt m&aacute;y động, chuyện kia?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&oacute; thể hay kh&ocirc;ng, c&ugrave;ng lần n&agrave;y chỉ thị chữ bằng m&aacute;u c&oacute; li&ecirc;n quan g&igrave;?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn vội v&agrave;ng kho&aacute;t tay n&oacute;i: &quot;Kh&ocirc;ng phải, ti&ecirc;n sinh, ng&agrave;i hiểu lầm. . . Ch&uacute;ng ta, chỉ l&agrave; phổ th&ocirc;ng kh&aacute;ch du lịch, nghĩ đến ở nhờ tại qu&yacute; th&ocirc;n m&agrave; th&ocirc;i. Y&ecirc;n t&acirc;m, ch&uacute;ng ta sẽ trả tiền.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đầu năm nay, x&aacute;c thực c&oacute; một &iacute;t th&acirc;m ni&ecirc;n phượt thủ, kh&ocirc;ng dựa theo cơ quan du lịch du lịch tuyến đường tiến h&agrave;nh đi phượt, tiến v&agrave;o một ch&uacute;t hoang sơn d&atilde; lĩnh địa khu . Bất qu&aacute;, n&agrave;y Hắc &Ocirc; sơn bản th&acirc;n cũng kh&ocirc;ng t&iacute;nh l&agrave; g&igrave; điểm du lịch , b&igrave;nh thường cũng sẽ kh&ocirc;ng c&oacute; du kh&aacute;ch đến đ&acirc;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trung ni&ecirc;n nam nh&acirc;n hiển nhi&ecirc;n kh&ocirc;ng tin L&yacute; Ẩn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;N&oacute;i bậy! C&aacute;c ngươi vẫn l&agrave; nhanh đi cho ta đi! Đừng cho rằng ta kh&ocirc;ng biết dụng t&acirc;m của c&aacute;c ngươi!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trung ni&ecirc;n nam nh&acirc;n kia c&agrave;ng ng&agrave;y c&agrave;ng l&agrave; k&iacute;ch động, nhưng L&yacute; Ẩn rất r&otilde; r&agrave;ng. . . Hắn tuyệt đối kh&ocirc;ng thể rời khỏi th&ocirc;n n&agrave;y. Căn cứ chỉ thị chữ bằng m&aacute;u, hắn nhất định phải tại th&ocirc;n n&agrave;y nghỉ ngơi một th&aacute;ng thời gian, nếu kh&ocirc;ng. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn liền sẽ chết!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chữ bằng m&aacute;u chỉ thị l&agrave; tuyệt đối muốn tu&acirc;n thủ, L&yacute; Ẩn đối điểm n&agrave;y, đ&atilde; khắc cốt minh t&acirc;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Sự t&igrave;nh g&igrave; như vậy ồn a?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một tiếng n&oacute;i gi&agrave; nua vang l&ecirc;n, sau đ&oacute;, mọi người ph&acirc;n ph&acirc;n đẩy ra, một t&oacute;c bạc l&atilde;o nh&acirc;n, bị một đại kh&aacute;i hai mươi mấy tuổi c&ocirc; g&aacute;i trẻ tuổi đỡ lấy, chậm r&atilde;i đi tới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Th&ocirc;n trưởng. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Th&ocirc;n trưởng, bọn họ l&agrave; người trong th&agrave;nh, khẳng định l&agrave; hướng về ph&iacute;a L&yacute; Băng chuyện kia tới!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đ&uacute;ng! Khẳng định l&agrave; như thế!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&atilde;o nh&acirc;n xem l&ecirc;n, tựa hồ đ&atilde; hơn t&aacute;m mươi tuổi. Hắn run run rẩy rẩy đi hướng L&yacute; Ẩn bọn họ, n&oacute;i: &quot;Người trẻ tuổi. . . C&aacute;c ngươi, rốt cuộc l&agrave; v&igrave; c&aacute;i g&igrave; đến đ&acirc;y?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn liền vội v&agrave;ng tiến l&ecirc;n h&agrave;nh lễ, n&oacute;i: &quot;Xin ch&agrave;o, l&atilde;o th&ocirc;n trưởng. Ch&uacute;ng ta thuần t&uacute;y chỉ l&agrave; tiến v&agrave;o trong n&uacute;i n&agrave;y du ngoạn, nghĩ thể nghiệm n&ocirc;ng gia phong t&igrave;nh, cho n&ecirc;n mới nghĩ ở tại nơi n&agrave;y trong l&agrave;ng. Ngươi y&ecirc;n t&acirc;m, ch&uacute;ng ta sẽ kh&ocirc;ng ở ch&ugrave;a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ồ? Thể nghiệm n&ocirc;ng gia phong t&igrave;nh? Rất c&oacute; &yacute; tứ nha.&quot; L&atilde;o th&ocirc;n trưởng giống như l&agrave; đang tự hỏi c&aacute;i g&igrave;, sau đ&oacute; n&oacute;i: &quot;V&acirc;̣y thì t&ocirc;́t, c&aacute;c ngươi, liền ở đến nh&agrave; ta đi th&ocirc;i!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn sững sờ, người trưởng th&ocirc;n n&agrave;y. . . Thế m&agrave; như vậy sảng kho&aacute;i đ&aacute;p ứng?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Th&ocirc;n trưởng, ngươi. . . Ngươi l&agrave;m sao c&oacute; thể khiến n&agrave;y mấy người lai lịch kh&ocirc;ng r&otilde; đi nh&agrave; ngươi đ&acirc;u?&quot; Kia trung ni&ecirc;n nam nh&acirc;n lập tức n&oacute;i ra: &quot;Bọn họ khẳng định l&agrave; ph&oacute;ng vi&ecirc;n a!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kh&ocirc;ng quan trọng, &quot; kia l&atilde;o th&ocirc;n trưởng lại l&agrave; rất kho&aacute;t đạt n&oacute;i: &quot;U Thủy th&ocirc;n rất d&agrave;i thời gian kh&ocirc;ng c&oacute; kh&aacute;ch nh&acirc;n tới. Kh&oacute; được kh&aacute;ch tới, tại sao c&oacute; thể kh&ocirc;ng chi&ecirc;u đ&atilde;i đ&agrave;ng ho&agrave;ng đ&acirc;u?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&atilde;o th&ocirc;n trưởng lời n&oacute;i n&agrave;y, khiến L&yacute; Ẩn ho&agrave;i nghi, hẳn l&agrave; th&ocirc;n trưởng c&oacute; mưu đồ kh&aacute;c?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng, chỉ cần c&oacute; thể ở lại liền tốt, c&aacute;i kh&aacute;c. . . Đều đừng để &yacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&oacute;i thực ra, c&acirc;n nhắc đến th&aacute;ng sau ng&agrave;y 7 l&uacute;c rời đi chạy trốn vấn đề, L&yacute; Ẩn như cũ rất đau đầu. Lấy n&uacute;i n&agrave;y tới n&oacute;i, l&aacute;i xe so đi đường chậm hơn, cho n&ecirc;n chỉ c&oacute; thể đi bộ tho&aacute;t đi. Nhưng l&agrave; như thế hiểm trở thế n&uacute;i, nếu như bị truy đuổi. . . Bốn người bọn họ, c&oacute; thể c&oacute; mấy sống s&oacute;t? Lần trước, s&aacute;u hộ gia đ&igrave;nh, vỏn vẹn L&yacute; Ẩn một người sống s&oacute;t a!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cho n&ecirc;n, L&yacute; Ẩn quyết định l&agrave;m r&otilde;, rốt cuộc. . . Tại c&aacute;i n&agrave;y U Thủy th&ocirc;n b&ecirc;n trong, tồn tại t&agrave; &aacute;c lực lượng ch&acirc;n diện mục l&agrave; c&aacute;i g&igrave;, c&oacute; lẽ, c&oacute; thể t&igrave;m ra đối s&aacute;ch!</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:132.688px; top:17px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'chu-bang-mau-quy-tac', 3, '1233', '2022-07-31 08:07:37', '2022-07-31 08:07:37'),
(11, '1', 'Chiếc xe này là tôi thuê', '<p>Trước kia nghe n&oacute;i người một xui xẻo uống miếng nước th&ocirc;i cũng bị mắc kẻ răng c&ocirc; c&ograve;n kh&ocirc;ng tin, cho đến khi chiếc Dongfeng Peugeot* của c&ocirc; nhiệt t&igrave;nh m&agrave; h&ocirc;n l&ecirc;n chiếc xe h&agrave;o nho&aacute;ng Adorlp720-4 ph&iacute;a trước th&igrave; Hạ Chi T&igrave;nh rốt cuộc cũng tin rồi.<br />\r\n* Dongfeng Peugeot: một h&atilde;ng xe của Trung Quốc.<br />\r\nHạ Chi T&igrave;nh tắt m&aacute;y, xuống xe, cửa xe chỗ ghế l&aacute;i Lambhini được mở ra, một thanh ni&ecirc;n ch&acirc;n d&agrave;i bước xuống.<br />\r\nKhi người đ&agrave;n &ocirc;ng đi tới trước mặt c&ocirc;, Hạ Chi T&igrave;nh cảm thấy hai mắt của m&igrave;nh bị &aacute;nh đ&egrave;n ven đường ch&oacute;i dữ dội.<br />\r\nNgười đ&agrave;n &ocirc;ng liếc mắt tr&ecirc;n dưới đ&aacute;nh gi&aacute; c&ocirc; c&ugrave;ng với chiếc Dongfeng nhỏ n&agrave;y, n&oacute;i: &quot;Vị tiểu thư n&agrave;y, tr&aacute;ch nhiệm về sự cố đều do c&ocirc;, đối với điểm n&agrave;y, ch&uacute;ng ta hẳn l&agrave; đều kh&ocirc;ng c&oacute; &yacute; kiến g&igrave; phải kh&ocirc;ng?&quot;<br />\r\nThấy một người đ&agrave;n &ocirc;ng tập hợp đủ cả ba chữ cao ph&uacute; so&aacute;i, mức độ hấp dẫn so với sắc hương vị của mỹ thực quả thật giống nhau. Nếu đổi lại ng&agrave;y thường, Hạ Chi T&igrave;nh đ&atilde; sớm nổi m&aacute;u m&ecirc; trai rồi, nhưng hiện tại tưởng tượng đến tiếp theo phải bồi thường một khoản lớn, c&ocirc; chỉ muốn ngẩng mặt l&ecirc;n một g&oacute;c 45 độ nh&igrave;n đ&egrave;n rơi nước mắt.<br />\r\nĐầu năm nay, c&ocirc; ở tiểu khu Cẩm T&uacute; của trung t&acirc;m th&agrave;nh phố thu&ecirc; một căn nh&agrave; hai ph&ograve;ng ngủ v&agrave; một ph&ograve;ng kh&aacute;ch, th&aacute;ng trước lại vay tiền mua &ocirc; t&ocirc;, cho n&ecirc;n c&ocirc; đ&atilde; sớm l&agrave; một ph&uacute; b&agrave; nợ nần chồng chất rồi.<br />\r\nHạ Chi T&igrave;nh nh&igrave;n chiếc Lamborghini đ&atilde; n&aacute;t b&eacute;t, kh&oacute; khăn gật đầu, &quot;Chiếc xe đắt tiền như vậy, anh nhất định l&agrave; đ&atilde; mua bảo hiểm rồi phải kh&ocirc;ng?&quot;<br />\r\nĐ&ocirc;i mắt phượng s&acirc;u thẳm của người đ&agrave;n &ocirc;ng đảo l&ecirc;n tr&ecirc;n khu&ocirc;n mặt c&ocirc;, nh&uacute;n vai cười n&oacute;i: &quot;Chiếc xe n&agrave;y kh&ocirc;ng phải của t&ocirc;i.&quot;<br />\r\n&quot;Ồ, vậy đ&oacute; l&agrave; của bạn anh? Bạn của anh hẳn đ&atilde; mua bảo hiểm đầy đủ phải kh&ocirc;ng?&quot;<br />\r\n&quot;Đ&oacute; cũng kh&ocirc;ng phải l&agrave; của bạn t&ocirc;i. T&ocirc;i đ&atilde; thu&ecirc; chiếc xe n&agrave;y .&quot;<br />\r\nT&ocirc;i đ&atilde; thu&ecirc; chiếc xe n&agrave;y.<br />\r\nĐ&atilde; thu&ecirc;.</p>', '<p>Trước kia nghe n&oacute;i người một xui xẻo uống miếng nước th&ocirc;i cũng bị mắc kẻ răng c&ocirc; c&ograve;n kh&ocirc;ng tin, cho đến khi chiếc Dongfeng Peugeot* của c&ocirc; nhiệt t&igrave;nh m&agrave; h&ocirc;n l&ecirc;n chiếc xe h&agrave;o nho&aacute;ng Adorlp720-4 ph&iacute;a trước th&igrave; Hạ Chi T&igrave;nh rốt cuộc cũng tin rồi.<br />\r\n* Dongfeng Peugeot: một h&atilde;ng xe của Trung Quốc.<br />\r\nHạ Chi T&igrave;nh tắt m&aacute;y, xuống xe, cửa xe chỗ ghế l&aacute;i Lambhini được mở ra, một thanh ni&ecirc;n ch&acirc;n d&agrave;i bước xuống.<br />\r\nKhi người đ&agrave;n &ocirc;ng đi tới trước mặt c&ocirc;, Hạ Chi T&igrave;nh cảm thấy hai mắt của m&igrave;nh bị &aacute;nh đ&egrave;n ven đường ch&oacute;i dữ dội.<br />\r\nNgười đ&agrave;n &ocirc;ng liếc mắt tr&ecirc;n dưới đ&aacute;nh gi&aacute; c&ocirc; c&ugrave;ng với chiếc Dongfeng nhỏ n&agrave;y, n&oacute;i: &quot;Vị tiểu thư n&agrave;y, tr&aacute;ch nhiệm về sự cố đều do c&ocirc;, đối với điểm n&agrave;y, ch&uacute;ng ta hẳn l&agrave; đều kh&ocirc;ng c&oacute; &yacute; kiến g&igrave; phải kh&ocirc;ng?&quot;<br />\r\nThấy một người đ&agrave;n &ocirc;ng tập hợp đủ cả ba chữ cao ph&uacute; so&aacute;i, mức độ hấp dẫn so với sắc hương vị của mỹ thực quả thật giống nhau. Nếu đổi lại ng&agrave;y thường, Hạ Chi T&igrave;nh đ&atilde; sớm nổi m&aacute;u m&ecirc; trai rồi, nhưng hiện tại tưởng tượng đến tiếp theo phải bồi thường một khoản lớn, c&ocirc; chỉ muốn ngẩng mặt l&ecirc;n một g&oacute;c 45 độ nh&igrave;n đ&egrave;n rơi nước mắt.<br />\r\nĐầu năm nay, c&ocirc; ở tiểu khu Cẩm T&uacute; của trung t&acirc;m th&agrave;nh phố thu&ecirc; một căn nh&agrave; hai ph&ograve;ng ngủ v&agrave; một ph&ograve;ng kh&aacute;ch, th&aacute;ng trước lại vay tiền mua &ocirc; t&ocirc;, cho n&ecirc;n c&ocirc; đ&atilde; sớm l&agrave; một ph&uacute; b&agrave; nợ nần chồng chất rồi.<br />\r\nHạ Chi T&igrave;nh nh&igrave;n chiếc Lamborghini đ&atilde; n&aacute;t b&eacute;t, kh&oacute; khăn gật đầu, &quot;Chiếc xe đắt tiền như vậy, anh nhất định l&agrave; đ&atilde; mua bảo hiểm rồi phải kh&ocirc;ng?&quot;<br />\r\nĐ&ocirc;i mắt phượng s&acirc;u thẳm của người đ&agrave;n &ocirc;ng đảo l&ecirc;n tr&ecirc;n khu&ocirc;n mặt c&ocirc;, nh&uacute;n vai cười n&oacute;i: &quot;Chiếc xe n&agrave;y kh&ocirc;ng phải của t&ocirc;i.&quot;<br />\r\n&quot;Ồ, vậy đ&oacute; l&agrave; của bạn anh? Bạn của anh hẳn đ&atilde; mua bảo hiểm đầy đủ phải kh&ocirc;ng?&quot;<br />\r\n&quot;Đ&oacute; cũng kh&ocirc;ng phải l&agrave; của bạn t&ocirc;i. T&ocirc;i đ&atilde; thu&ecirc; chiếc xe n&agrave;y .&quot;<br />\r\nT&ocirc;i đ&atilde; thu&ecirc; chiếc xe n&agrave;y.<br />\r\nĐ&atilde; thu&ecirc;.<br />\r\n&quot;......&quot; Hạ Chi T&igrave;nh thật sự bị c&acirc;u n&oacute;i n&agrave;y l&agrave;m cho nghẹn họng, kh&ocirc;ng &iacute;t người giả vờ gi&agrave;u c&oacute;, nhưng đ&acirc;y l&agrave; lần đầu ti&ecirc;n thấy c&oacute; người giả vờ k&eacute;m như vậy.<br />\r\nNgười đ&agrave;n &ocirc;ng lấy ra chiếc Iphone 6*, chụp &quot;tạch tạch&quot; v&agrave;i tấm ảnh về ph&iacute;a hai chiếc xe, sau đ&oacute; đưa điện thoại v&agrave;o tay c&ocirc; v&agrave; n&oacute;i:&quot; Điền số điện thoại v&agrave; t&ecirc;n của c&ocirc;, sau khi sửa xe xong .&quot;<br />\r\n* kh&ocirc;ng biết 6 n&agrave;y l&agrave; g&igrave; v&igrave; trong bản gốc ghi l&agrave;&quot;男人拿出6&quot;, chắc l&agrave; n9 lấy ra iphone 6 để chụp ảnh n&ecirc;n m&igrave;nh ch&eacute;m đại nha =))<br />\r\nT&ocirc;i sẽ th&ocirc;ng b&aacute;o cho c&ocirc; đến bồi thường tiền.<br />\r\nĐến bồi thường.<br />\r\nBồi thường.<br />\r\nTiền.<br />\r\nHạ Chi T&igrave;nh siết chặt điện thoại với đ&ocirc;i mắt ngấn lệ, sợ sẩy tay một c&aacute;i lại phải đi bồi thường.<br />\r\nC&ocirc; c&oacute; một đồng nghiệp, th&aacute;ng trước kh&ocirc;ng cẩn thận l&agrave;m xước một chiếc Maserati, người ta v&ocirc; c&ugrave;ng h&agrave;o ph&oacute;ng vung tay l&ecirc;n m&agrave; kh&ocirc;ng cần đồng nghiệp của c&ocirc; bồi thường. Vừa rồi c&ocirc; c&oacute; ch&uacute;t hi vọng, hy vọng người đẹp trai gi&agrave;u c&oacute; trước mặt c&oacute; thể ph&oacute;ng kho&aacute;ng như vậy, ai biết c&ocirc; xui xẻo đến mức tưởng gặp phải r&ugrave;a v&agrave;ng, kh&ocirc;ng ngờ nh&igrave;n kỹ lại, mới ph&aacute;t hiện h&oacute;a ra l&agrave; mạ v&agrave;ng*!</p>\r\n\r\n<p><br />\r\n* Mạ v&agrave;ng nghĩa l&agrave; đ&aacute;nh b&oacute;ng, theo đuổi th&oacute;i hư danh.<br />\r\nKh&ocirc;ng c&oacute; tiền ngươi giả bộ cao ph&uacute; so&aacute;i l&agrave;m g&igrave;!<br />\r\nKh&ocirc;ng c&oacute; tiền ngươi thu&ecirc; Lamborghini l&agrave;m g&igrave;!<br />\r\nHạ Chi Thanh cảm thấy t&acirc;m hồn nhỏ b&eacute; mỏng manh của c&ocirc; đ&atilde; bị tổn thương rất nhiều. Sau khi trở lại ghế l&aacute;i, c&ocirc; kh&ocirc;ng nhịn được d&ugrave;ng đầu g&otilde; v&ocirc; lăng. Đ&uacute;ng l&agrave; năm hạn xui xẻo m&agrave; ! ! !<br />\r\nTh&aacute;ng trước l&uacute;c đi ngang qua một ng&ocirc;i ch&ugrave;a, một &quot;sư phụ&quot; dựng quầy h&agrave;ng b&ecirc;n đường đ&atilde; t&uacute;m lấy c&ocirc; v&agrave; n&oacute;i rằng ấn đường của c&ocirc; biến th&agrave;nh m&agrave;u đen, nếu kh&ocirc;ng h&oacute;a giải liền sẽ x&uacute;i quẩy, l&uacute;c ấy c&ocirc; phỉ nhổ đối phương một mặt, b&acirc;y giờ lại hối hận đến th&uacute;i ruột, sớm biết liền mua b&ugrave;a hộ mệnh của đại sư kia rồi.<br />\r\nTống An Thần nh&igrave;n ba chữ &quot;Hạ Chi T&igrave;nh&quot; tr&ecirc;n danh bạ điện thoại, kh&oacute;e miệng khẽ nhếch l&ecirc;n một v&ograve;ng cung mềm mại.<br />\r\nC&ocirc; g&aacute;i ngốc nghếch kia kh&ocirc;ng nhận ra anh, nhưng kh&ocirc;ng sao, Hạ Chi T&igrave;nh, một đời d&agrave;i như vậy, ch&uacute;ng ta c&oacute; thời gian.<br />\r\n1-【2】<br />\r\nC&ocirc; trở về nh&agrave; một c&aacute;ch phờ phạc, mở WeChat l&ecirc;n v&agrave; lặng lẽ nhấp v&agrave;o h&igrave;nh đại diện c&oacute; t&ecirc;n &quot;Sầu ri&ecirc;ng to ph&aacute;t nổ&quot;.<br />\r\nQuả t&aacute;o nhỏ ph&aacute;t nổ: Anh trai cao cấp, đẳng cấp, ngầu l&ograve;i đến tận trời của em ơi ~~[ h&ocirc;n gi&oacute; ][ h&ocirc;n gi&oacute; ], anh c&oacute; đ&oacute; kh&ocirc;ng?<br />\r\nSầu ri&ecirc;ng to ph&aacute;t nổ: N&oacute;i tiếng người!<br />\r\nQuả t&aacute;o nhỏ ph&aacute;t nổ: Em muốn mượn anh một &iacute;t tiền để ti&ecirc;u, cũng kh&ocirc;ng nhiều lắm, đại kh&aacute;i khoản 10 vạn đi.<br />\r\nSầu ri&ecirc;ng to ph&aacute;t nổ: Hai chữ + một k&yacute; hiệu.<br />\r\nQuả t&aacute;o nhỏ ph&aacute;t nổ: ?<br />\r\nSầu ri&ecirc;ng to ph&aacute;t nổ: Kh&ocirc;ng c&oacute;!<br />\r\nQuả t&aacute;o nhỏ ph&aacute;t nổ: Chết tiệt! Nh&acirc;n Chi Sơ, anh chẳng lẽ thấy chết kh&ocirc;ng cứu sao?<br />\r\n【 Tin nhắn đ&atilde; được gửi đi, nhưng đ&atilde; bị đối phương từ chối. 】<br />\r\nQuả t&aacute;o nhỏ ph&aacute;t nổ: ? C&oacute; &yacute; g&igrave;? Đừng n&oacute;i với em l&agrave; anh đ&atilde; chặn em rồi??<br />\r\n【 Tin nhắn đ&atilde; được gửi đi, nhưng đ&atilde; bị đối phương từ chối.】<br />\r\nQuả t&aacute;o nhỏ ph&aacute;t nổ: [ ph&aacute;t đi&ecirc;n ][ ph&aacute;t đi&ecirc;n ]<br />\r\n【 Tin nhắn đ&atilde; được gửi đi, nhưng đ&atilde; bị đối phương từ chối.】<br />\r\nHạ Chi T&igrave;nh lệ rơi đầy mặt m&agrave; n&eacute;m di động xuống, thật l&agrave; th&oacute;i đời bạc bẽo, l&ograve;ng người đổi thay! Canh g&agrave; t&acirc;m linh tr&ecirc;n mạng quả nhi&ecirc;n n&oacute;i kh&ocirc;ng c&oacute; sai, h&egrave;n mọn nhất l&agrave; cảm t&igrave;nh, lạnh lẽo nhất l&agrave; l&ograve;ng người.<br />\r\nTiền lương của Nh&acirc;n Chi Sơ l&agrave; gấp năm lần của c&ocirc;, tiền tiết kiệm b&ecirc;n trong thẻ ng&acirc;n h&agrave;ng đ&atilde; vượt qu&aacute; bảy chữ số, c&ograve;n c&aacute;c loại đầu tư sinh tiền nữa, cho c&ocirc; mượn ch&uacute;t tiền sẽ chết hả, sẽ chết hả, c&ocirc; cũng đ&acirc;u phải l&agrave; kh&ocirc;ng trả lại m&agrave; !<br />\r\nNh&acirc;n Chi Sơ, t&ecirc;n thật l&agrave; Hạ Chi Sơ, l&agrave; anh em sinh đ&ocirc;i được sinh ra trước c&ocirc; năm ph&uacute;t, từ nhỏ đ&atilde; th&iacute;ch bắt nạt c&ocirc; như một niềm vui trong cuộc sống, nhưng c&ocirc; đ&agrave;nh bất lực v&igrave; d&ugrave; l&agrave; IQ hay EQ, cả hai đều đấu kh&ocirc;ng lại t&ecirc;n tiểu biểu tạp* Hạ Chi Sơ n&agrave;y.<br />\r\n*小婊砸 (xiǎo biǎo z&aacute;) l&agrave; một lời mắng chửi nhưng kh&ocirc;ng c&oacute; &yacute; xấu, như từ &quot; bitch&quot; trong tiếng Anh.<br />\r\nHạ Chi T&igrave;nh ph&aacute;t đi&ecirc;n lăn qua lăn lại ở tr&ecirc;n giường, mục ti&ecirc;u cả đời của c&ocirc; ch&iacute;nh l&agrave; ngược chết t&ecirc;n tiểu biểu tạp Hạ Chi Sơ n&agrave;y, nếu kh&ocirc;ng thể ở tr&ecirc;n * ngược đến chết, vậy cũng phải ngược đến chết tinh thần của thằng nh&oacute;c đ&oacute;!<br />\r\nNg&agrave;y h&ocirc;m sau, Hạ Chi T&igrave;nh đi l&agrave;m với hai mắt gấu tr&uacute;c to chảng. H&ocirc;m nay, c&ocirc; c&oacute; hẹn một kh&aacute;ch h&agrave;ng để n&oacute;i về hợp đồng.<br />\r\nKh&aacute;ch h&agrave;ng l&agrave; một b&aacute;c g&aacute;i 50 tuổi, trừ b&eacute;o một ch&uacute;t, khỏe một ch&uacute;t, xấu một ch&uacute;t v&agrave; sở th&iacute;ch hơi lố, th&igrave; vẫn l&agrave; một người kh&ocirc;ng tồi, b&aacute;c g&aacute;i l&agrave; người c&oacute; l&ograve;ng nhiệt t&igrave;nh.<br />\r\nSau khi thương lượng xong hợp đồng cũng đ&atilde; đến giờ ăn trưa, c&ocirc; đ&agrave;nh phải lịch sự mời b&aacute;c g&aacute;i kh&aacute;ch h&agrave;ng c&ugrave;ng nhau ăn trưa. B&aacute;c g&aacute;i nửa c&acirc;u kh&aacute;ch s&aacute;o cũng kh&ocirc;ng n&oacute;i liền l&ocirc;i k&eacute;o c&ocirc; vui sướng đi xuống một nh&agrave; h&agrave;ng T&acirc;y cao cấp dưới lầu một, c&ocirc; bất chợt cảm thấy tim m&igrave;nh đau như cắt.<br />\r\nĂn được nửa bữa th&igrave; chu&ocirc;ng điện thoại reo, c&ocirc; nh&iacute;u m&agrave;y khi nh&igrave;n thấy d&ograve;ng chữ &quot;L&atilde;o Phật gia&quot; tr&ecirc;n m&agrave;n h&igrave;nh.<br />\r\nL&atilde;o Phật gia, l&agrave; mẹ của c&ocirc;, c&ograve;n gọi l&agrave; b&agrave; T&ocirc;.<br />\r\nC&ocirc; biết gần đ&acirc;y mẹ T&ocirc; lại đau l&ograve;ng, nguy&ecirc;n nh&acirc;n kh&ocirc;ng cần bấm đốt tay cũng đo&aacute;n ra được l&agrave; v&igrave; con d&acirc;u nh&agrave; d&igrave; Vương b&ecirc;n cạnh đ&atilde; sinh th&ecirc;m ch&aacute;u trai cho b&agrave;. Mẹ T&ocirc; mỗi ng&agrave;y ở nh&agrave; nhắc m&atilde;i ch&iacute;nh m&igrave;nh số khổ, sống hơn năm mươi năm vẫn l&agrave; mẹ người ta, trước sau kh&ocirc;ng c&oacute; cơ hội l&ecirc;n l&agrave;m b&agrave; người ta.<br />\r\nC&ocirc; thở d&agrave;i trong l&ograve;ng, cam chịu nhấn n&uacute;t trả lời, điện thoại di động của c&ocirc; sau một lần bị rơi c&oacute; ch&uacute;t kh&ocirc;ng b&igrave;nh thường, kh&ocirc;ng c&oacute; chức năng rảnh tay, cho n&ecirc;n c&ocirc; chẳng qua mới k&ecirc;u một tiếng &quot;mẹ ơi&quot;. Giọng n&oacute;i như cồng chi&ecirc;ng của mẹ T&ocirc; được ph&aacute;t ra từ điện thoại c&ocirc; &mdash;&mdash;<br />\r\n&quot;B&agrave; đ&acirc;y muốn &ocirc;m con rể!&quot;<br />\r\nMẹ T&ocirc; người l&ugrave;n nhưng giọng to, thường hay n&oacute;i mấy lời kinh người, Hạ Chi T&igrave;nh sớm đ&atilde; quen, chỉ l&agrave; b&aacute;c g&aacute;i kh&aacute;ch h&agrave;ng ngồi đối diện c&ocirc; r&otilde; r&agrave;ng giống như vừa ăn phải ruồi, ngay cả &aacute;nh mắt cũng c&oacute; ch&uacute;t thay đổi.<br />\r\nCon rể vs mẹ vợ.avi<br />\r\nThiệt l&agrave; hưng phấn qu&aacute; đi m&agrave;!<br />\r\nThực ra &yacute; mẹ T&ocirc; l&agrave;: Con mau nhanh gả chồng đi cho b&agrave;, b&agrave; đ&acirc;y muốn gặp con rể, b&agrave; đ&acirc;y muốn &ocirc;m ch&aacute;u ngoại.<br />\r\nNhưng sau khi bị mẹ T&ocirc; gia giảm theo c&aacute;ch n&agrave;y, m&ugrave;i vị ho&agrave;n to&agrave;n thay đổi, Hạ Chi T&igrave;nh c&oacute; ch&uacute;t k&iacute;ch động muốn rơi lệ.<br />\r\nC&uacute;p điện thoại mẹ T&ocirc;, c&ocirc; nh&igrave;n thấy &aacute;nh mắt đầy ẩn &yacute; của b&aacute;c g&aacute;i kh&aacute;ch h&agrave;ng, đ&agrave;nh phải giải th&iacute;ch: &quot;Mấy ng&agrave;y nay bố mẹ ch&aacute;u cũng ra sức giục cưới n&ecirc;n ch&aacute;u kh&ocirc;ng d&aacute;m về qu&ecirc; ăn Tết.&quot;<br />\r\n&quot;H&oacute;a ra Hạ tiểu thư c&ograve;n chưa c&oacute; kết h&ocirc;n &agrave;, ta c&ograve;n tưởng rằng......&quot; Kh&ocirc;ng biết v&igrave; sao b&aacute;c g&aacute;i kh&aacute;ch h&agrave;ng tr&ecirc;n mặt nh&igrave;n qua c&oacute; vẻ thất vọng, &quot;Bất qu&aacute; Hạ tiểu thư tr&ocirc;ng trẻ tuổi như vậy cũng kh&ocirc;ng cần phải gấp g&aacute;p l&agrave;m g&igrave;, mới hai hai, ba tuổi đi?&quot;<br />\r\nC&oacute; một khu&ocirc;n mặt trẻ con l&agrave; một lợi &iacute;ch như vậy, tốt nghiệp đ&atilde; bốn &ndash; năm năm, nh&igrave;n qua c&ograve;n giống như l&agrave; mới vừa tốt nghiệp, Hạ Chi T&igrave;nh hai mắt cười th&agrave;nh trăng non h&igrave;nh, &quot;N&agrave;o c&oacute;, cũng kh&ocirc;ng c&ograve;n trẻ.&quot;<br />\r\n&quot;Vậy l&agrave; bao nhi&ecirc;u tuổi?&quot; B&aacute;c g&aacute;i kh&aacute;ch h&agrave;ng cố hỏi đến c&ugrave;ng.<br />\r\n&quot;... Hai mươi s&aacute;u.&quot;<br />\r\n&quot;&quot;Ồ, hai mươi s&aacute;u quả thực kh&ocirc;ng c&ograve;n trẻ nữa. B&agrave; c&ocirc; của bạn của b&agrave; chị của đồng nghiệp ta c&oacute; đứa con trai cũng trạc tuổi ch&aacute;u, cũng l&agrave; c&aacute;i thừa đấu sĩ *, cũng chưa c&oacute; đối tượng, hay l&agrave; ta giới thiệu cho c&aacute;c ch&aacute;u l&agrave;m quen?&quot;<br />\r\n*Từ l&oacute;ng của giang cư mận b&ecirc;n Trung về c&aacute;c thanh ni&ecirc;n qu&aacute; lứa lỡ th&igrave; kết h&ocirc;n, độ tuổi n&agrave;y khoảng từ 25-27 tuổi. May mị vẫn c&ograve;n trẻ c&ograve;n muốn đi chơi =))<br />\r\n&quot;...... Kh&ocirc;ng cần đ&acirc;u ạ, như vậy th&igrave; hơi ngại.&quot; Dưới mệnh lệnh th&uacute;c giục như muốn mạng của mẹ T&ocirc;, mỗi khi nghe thấy hai từ &quot;xem mắt&quot; l&agrave; c&ocirc; liền cảm thấy đau đầu.<br />\r\nps: B&agrave; mới l&agrave; thừa đấu sĩ, cả c&ocirc;ng ty của b&agrave; đều l&agrave; thừa đấu sĩ!<br />\r\n&quot;Ch&aacute;u kh&ocirc;ng cần ngượng ng&ugrave;ng, trai lớn cưới vợ g&aacute;i lớn gả chồng, chuyện n&agrave;y quyết định vậy đi, chung th&acirc;n đại sự của Hạ tiểu thư đ&acirc;y ta nhận.&quot; B&aacute;c g&aacute;i kh&aacute;ch h&agrave;ng vỗ ngực ngực cam đoan.<br />\r\n&quot;......&quot; Gặp phải một b&aacute;c g&aacute;i kh&aacute;ch h&agrave;ng th&iacute;ch đi quản chuyện h&ocirc;n nh&acirc;n đại sự của người kh&aacute;c như thế n&agrave;y, c&ocirc; chỉ c&oacute; thể d&ugrave;ng c&acirc;u n&oacute;i nổi tiếng của Master Horse để diễn tả t&acirc;m trạng của m&igrave;nh l&uacute;c n&agrave;y &mdash;&mdash; tr&aacute;i tim t&ocirc;i gần như tan n&aacute;t.<br />\r\n1-【3】<br />\r\nHạ Chi T&igrave;nh quay đầu đi liền đem việc n&agrave;y qu&ecirc;n lu&ocirc;n, nhưng m&agrave; b&aacute;c g&aacute;i kh&aacute;ch h&agrave;ng người ta lại kh&ocirc;ng qu&ecirc;n, qua hai ng&agrave;y sau, thật sự gửi đến WeChat cho c&ocirc; phương thức li&ecirc;n hệ với nh&agrave; trai.<br />\r\nSau khi L&acirc;m Du biết chuyện liền mạnh mẽ x&uacute;i giục c&ocirc; đi gặp đối tượng xem mắt.<br />\r\nL&acirc;m Du hai mắt mắt lấp l&aacute;nh: &quot;Th&agrave; gϊếŧ nhầm c&ograve;n hơn bỏ s&oacute;t, biết đ&acirc;u l&agrave; một anh ch&agrave;ng gi&agrave;u c&oacute; đẹp trai nha.&quot;<br />\r\nHạ Chi T&igrave;nh trợn trắng mắt: &quot;Cậu c&oacute; nghe thấy người đẹp trai gi&agrave;u c&oacute; n&agrave;o m&agrave; đi xem mắt chưa hả?&quot;<br />\r\nL&acirc;m Du l&agrave; bạn c&ugrave;ng b&agrave;n hồi đại học của c&ocirc;, hiện tại l&agrave; khu&ecirc; mật ki&ecirc;m qu&acirc;n sư t&igrave;nh y&ecirc;u, c&aacute;i sau đương nhi&ecirc;n l&agrave; L&acirc;m Du tự phong.<br />\r\n&quot;C&ograve;n c&oacute; hơn một th&aacute;ng nữa l&agrave; đến tết rồi, kh&ocirc;ng phải cậu đ&atilde; đ&aacute;p ứng mẹ T&ocirc; nh&agrave; cậu năm nay sẽ mang bạn trai về hả?&quot;<br />\r\nC&aacute;i gọi l&agrave; đ&aacute;nh rắn đ&aacute;nh &quot;bảy tất&quot;, m&agrave; mẹ T&ocirc; ch&iacute;nh l&agrave; &quot;bảy tấc&quot; của Hạ Chi T&igrave;nh. Nhắc đến mẹ T&ocirc;, c&ocirc; muốn ngất xỉu, đ&agrave;nh phải th&agrave;nh thật add WeChat của người đ&agrave;n &ocirc;ng xem mắt n&agrave;y.<br />\r\nHai người tr&ograve; chuyện một ch&uacute;t, trừ bỏ khinh bỉ t&ecirc;n WeChat của hắn l&agrave; &quot;m&atilde;n địa thương&quot;*, Hạ Chi T&igrave;nh kh&ocirc;ng t&igrave;m được điều g&igrave; kh&aacute;c, v&igrave; vậy hai người đ&atilde; hẹn gặp nhau ở quảng trường Thời Đại v&agrave;o cuối tuần.<br />\r\n*满地伤: quả t&ecirc;n n&agrave;y thật l&agrave;m kh&oacute; em m&agrave;, c&aacute;c hạ n&agrave;o biết nghĩa của n&oacute; xin chỉ gi&aacute;o em với!<br />\r\nRất nhanh đ&atilde; đến cuối tuần.<br />\r\nDưới sự chỉ huy của L&acirc;m Du, Hạ Chi T&igrave;nh trang điểm nhẹ nh&agrave;ng, v&agrave;o ng&agrave;y m&ugrave;a đ&ocirc;ng năm độ m&agrave; mặc một bộ v&aacute;y ngắn, xinh đẹp động l&ograve;ng người liền đi xem mắt.<br />\r\nKhi tới quảng trường Thời Đại, người đ&agrave;n &ocirc;ng được xem mắt vẫn chưa đến, hắn gọi điện bảo c&oacute; việc n&ecirc;n đến trễ một ch&uacute;t, khoảng nửa tiếng nữa sẽ đến. C&ocirc; đ&agrave;nh phải đến qu&aacute;n c&agrave; ph&ecirc; gần đ&oacute; ngồi chờ.<br />\r\nHạ Chi T&igrave;nh cười như được m&ugrave;a bởi một mẫu chuyện cười tr&ecirc;n Weibo, bỗng nhi&ecirc;n một giọng n&oacute;i nũng nịu vang l&ecirc;n trước mặt c&ocirc; &mdash;&mdash; &quot;Ồ, kh&ocirc;ng phải t&ocirc;i hoa mắt chứ, đ&acirc;y kh&ocirc;ng phải l&agrave; đại mỹ nữ Hạ Chi T&igrave;nh hoa kh&ocirc;i của đại học ch&uacute;ng ta hay sao?&quot;<br />\r\nHạ Chi T&igrave;nh ngẩng đầu l&ecirc;n, đối diện với một khu&ocirc;n mặt thanh t&uacute; c&ugrave;ng với chiếc cằm nhọn như rắn, tuy rằng gương mặt kia sửa rất &iacute;t nhưng c&ocirc; liếc mắt một c&aacute;i vẫn nhận ra được.<br />\r\nĐ&oacute; l&agrave; Khương Vi, bạn c&ugrave;ng ph&ograve;ng hồi đại học của c&ocirc;, đ&atilde; từng th&acirc;n thiết đến mức hận kh&ocirc;ng sớm trở th&agrave;nh chị em tốt của nhau.<br />\r\n&quot;M&igrave;nh ngồi đ&acirc;y cậu kh&ocirc;ng phiền chứ?&quot; Đ&ocirc;i m&ocirc;i đỏ mọng của Khương Vi cong l&ecirc;n nở một nụ cười rạng rỡ.<br />\r\n&quot;C&ocirc; khi n&agrave;o th&igrave; trở n&ecirc;n lịch sự như vậy? Kh&ocirc;ng hỏi m&agrave; tự lấy chẳng phải l&agrave; sở trường của c&ocirc; sao?&quot; Hạ Chi T&igrave;nh nh&igrave;n Khương Vi, kh&oacute;e miệng k&eacute;o ra một v&ograve;ng cung giễu cợt.<br />\r\nKhương Vi k&eacute;o ghế đối diện c&ocirc; tự m&igrave;nh ngồi xuống, nh&igrave;n Hạ Chi T&igrave;nh từ tr&ecirc;n xuống dưới, cười n&oacute;i: &quot;Mấy năm kh&ocirc;ng gặp, t&iacute;nh t&igrave;nh của c&ocirc; cũng kh&ocirc;ng thay đổi, nếu năm đ&oacute; kh&ocirc;ng phải c&ocirc; quyết liệt như vậy, Lục Xuy&ecirc;n cũng sẽ kh&ocirc;ng chia tay c&ocirc;.&quot;</p>', '0', 'chiec-xe-nay-la-toi-thue', 4, '433', '2022-07-31 08:24:22', '2022-07-31 08:24:22');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(12, '2', 'Đại chiến đánh ghen', '<p>người kh&ocirc;ng cần mặt mũi quả nhi&ecirc;n l&agrave; thi&ecirc;n hạ v&ocirc; địch!<br />\r\nNăm đ&oacute; r&otilde; r&agrave;ng l&agrave; l&agrave;m tiểu tam l&eacute;n đ&agrave;o g&oacute;c tường của c&ocirc;, c&ugrave;ng t&ecirc;n tra nam Lục Xuy&ecirc;n phản bội lại c&ocirc;, giờ c&ograve;n c&oacute; mặt mũi đi tr&aacute;ch ngược lại c&ocirc;!<br />\r\nLục Xuy&ecirc;n l&agrave; đ&agrave;n anh kh&oacute;a tr&ecirc;n của bọn c&ocirc;, lớn l&ecirc;n cao r&aacute;o đẹp trai kh&ocirc;ng n&oacute;i, c&ograve;n rất t&agrave;i hoa, l&agrave; ch&agrave;ng trai v&agrave;ng trong l&agrave;ng đại học.<br />\r\nC&ocirc; v&agrave; Lục Xuy&ecirc;n gặp nhau trong một buổi sinh hoạt c&acirc;u lạc bộ. L&ecirc;n năm hai đại học, Lục Xuy&ecirc;n tỏ t&igrave;nh với c&ocirc;, l&uacute;c đ&oacute; Khương Vi n&oacute;i với c&ocirc; rằng nếu từ chối một nam sinh ưu t&uacute; như vậy sẽ bị s&eacute;t đ&aacute;nh đ&oacute;. V&igrave; kh&ocirc;ng muốn bị s&eacute;t đ&aacute;nh n&ecirc;n c&ocirc; đ&atilde; chấp nhận lời tỏ t&igrave;nh của Lục Xuy&ecirc;n, cả hai bắt đầu y&ecirc;u đương.<br />\r\nKh&ocirc;ng biết do mắt c&ocirc; bị m&ugrave; hay Khương Vi che giấu qu&aacute; th&acirc;m s&acirc;u, cho đến khi bắt được gian t&igrave;nh của bọn họ c&ocirc; mới biết được Khương Vi đ&atilde; th&iacute;ch Lục Xuy&ecirc;n ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.<br />\r\nBị cả bạn th&acirc;n v&agrave; bạn trai phản bội khiến c&ocirc; tức giận đến mức su&yacute;t mất tr&iacute;, đối mặt với lời tố c&aacute;o của c&ocirc;, Khương Vi &quot;đ&agrave;nh chấp nhận&quot;, tr&ecirc;n mặt kh&oacute;c l&oacute;c như mưa hoa.<br />\r\n&quot;Chi T&igrave;nh, xin lỗi, đều l&agrave; lỗi của tớ, cậu đ&aacute;nh tớ cũng được mắng tớ cũng được, chỉ l&agrave; tớ xin cậu, ng&agrave;n vạn lần đừng chia tay với Lục Xuy&ecirc;n, anh ấy thật sự rất y&ecirc;u cậu, kh&ocirc;ng thể kh&ocirc;ng c&oacute; cậu m&agrave;.&quot;</p>', '<p>2-【1】<br />\r\nSao lẩu thịt b&ograve; cay kh&ocirc;ng phỏng c&aacute;i đồ v&ocirc; li&ecirc;m sỉ n&agrave;y đi! C&acirc;y kh&ocirc;ng c&oacute; vỏ ắt hẳn phải chết, c&ograve;n người kh&ocirc;ng cần mặt mũi quả nhi&ecirc;n l&agrave; thi&ecirc;n hạ v&ocirc; địch!<br />\r\nNăm đ&oacute; r&otilde; r&agrave;ng l&agrave; l&agrave;m tiểu tam l&eacute;n đ&agrave;o g&oacute;c tường của c&ocirc;, c&ugrave;ng t&ecirc;n tra nam Lục Xuy&ecirc;n phản bội lại c&ocirc;, giờ c&ograve;n c&oacute; mặt mũi đi tr&aacute;ch ngược lại c&ocirc;!<br />\r\nLục Xuy&ecirc;n l&agrave; đ&agrave;n anh kh&oacute;a tr&ecirc;n của bọn c&ocirc;, lớn l&ecirc;n cao r&aacute;o đẹp trai kh&ocirc;ng n&oacute;i, c&ograve;n rất t&agrave;i hoa, l&agrave; ch&agrave;ng trai v&agrave;ng trong l&agrave;ng đại học.<br />\r\nC&ocirc; v&agrave; Lục Xuy&ecirc;n gặp nhau trong một buổi sinh hoạt c&acirc;u lạc bộ. L&ecirc;n năm hai đại học, Lục Xuy&ecirc;n tỏ t&igrave;nh với c&ocirc;, l&uacute;c đ&oacute; Khương Vi n&oacute;i với c&ocirc; rằng nếu từ chối một nam sinh ưu t&uacute; như vậy sẽ bị s&eacute;t đ&aacute;nh đ&oacute;. V&igrave; kh&ocirc;ng muốn bị s&eacute;t đ&aacute;nh n&ecirc;n c&ocirc; đ&atilde; chấp nhận lời tỏ t&igrave;nh của Lục Xuy&ecirc;n, cả hai bắt đầu y&ecirc;u đương.<br />\r\nKh&ocirc;ng biết do mắt c&ocirc; bị m&ugrave; hay Khương Vi che giấu qu&aacute; th&acirc;m s&acirc;u, cho đến khi bắt được gian t&igrave;nh của bọn họ c&ocirc; mới biết được Khương Vi đ&atilde; th&iacute;ch Lục Xuy&ecirc;n ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n.<br />\r\nBị cả bạn th&acirc;n v&agrave; bạn trai phản bội khiến c&ocirc; tức giận đến mức su&yacute;t mất tr&iacute;, đối mặt với lời tố c&aacute;o của c&ocirc;, Khương Vi &quot;đ&agrave;nh chấp nhận&quot;, tr&ecirc;n mặt kh&oacute;c l&oacute;c như mưa hoa.<br />\r\n&quot;Chi T&igrave;nh, xin lỗi, đều l&agrave; lỗi của tớ, cậu đ&aacute;nh tớ cũng được mắng tớ cũng được, chỉ l&agrave; tớ xin cậu, ng&agrave;n vạn lần đừng chia tay với Lục Xuy&ecirc;n, anh ấy thật sự rất y&ecirc;u cậu, kh&ocirc;ng thể kh&ocirc;ng c&oacute; cậu m&agrave;.&quot;<br />\r\nC&ocirc; bị d&aacute;ng vẻ em g&aacute;i tr&agrave; xanh n&agrave;y của Khương Vi gh&ecirc; tởm muốn ph&aacute;t đi&ecirc;n lu&ocirc;n, giơ tay định t&aacute;t c&ocirc; ta th&igrave; bị Lục Xuy&ecirc;n bắt lại, thấy hắn bảo vệ Khương Vi như vậy c&ocirc; c&agrave;ng tức giận, dồn hết sức t&aacute;t Lục Xuy&ecirc;n một c&uacute;, mặt hắn hằn l&ecirc;n vết tay.<br />\r\nLục Xuy&ecirc;n cũng ngay lập tức nổi đi&ecirc;n, vươn tay đẩy c&ocirc; ng&atilde; xuống đất, tr&ecirc;n mặt đất c&oacute; những mảnh vỡ của chiếc cốc, c&ocirc; ng&atilde; xuống đất, vết m&aacute;u chảy ra tr&ecirc;n tay.<br />\r\n&quot;Hạ Chi T&igrave;nh, c&ocirc; khi n&agrave;o th&igrave; trở th&agrave;nh người đ&agrave;n b&agrave; đanh đ&aacute; như vậy, sao c&ocirc; kh&ocirc;ng thể học được sự dịu d&agrave;ng hiểu chuyện như Vi Vi chứ, nếu như c&ocirc; c&oacute; được một nửa am hiểu l&ograve;ng người như Vi Vi th&igrave; đ&acirc;u c&oacute; t&igrave;nh cảnh như h&ocirc;m nay!&quot;<br />\r\nNg&agrave;y h&ocirc;m đ&oacute;, c&ocirc; ngồi dưới đất, nh&igrave;n vết m&aacute;u tr&ecirc;n tay, tr&aacute;i tim c&ocirc; run l&ecirc;n v&igrave; lạnh. Hạ Chi T&igrave;nh vuốt ve vết sẹo trong l&ograve;ng b&agrave;n tay, mỉm cười rồi l&ecirc; m&igrave;nh ra khỏi v&ograve;ng xo&aacute;y của k&yacute; ức, đ&atilde; l&acirc;u rồi c&ocirc; kh&ocirc;ng c&ograve;n nhớ về qu&aacute; khứ.<br />\r\nTrong phim &quot;Kh&ocirc;ng người l&aacute;i&quot; c&oacute; một c&acirc;u n&oacute;i: Kh&ocirc;ng sợ bị người kh&aacute;c t&iacute;nh to&aacute;n sau lưng, chỉ sợ khi nh&igrave;n lại sẽ thấy người t&iacute;nh to&aacute;n m&igrave;nh h&oacute;a ra lại l&agrave; người m&igrave;nh đ&atilde; từng tin tưởng rất s&acirc;u sắc. Một người l&agrave; bạn tốt của c&ocirc;, người kia l&agrave; mối t&igrave;nh đầu của c&ocirc;, sự phản bội của hai người thực sự khiến c&ocirc; tổn thương s&acirc;u đậm. V&igrave; vậy năm đ&oacute; ngay sau khi tốt nghiệp, c&ocirc; đ&atilde; rời khỏi th&agrave;nh phố A nơi c&ocirc; đ&atilde; sinh ra v&agrave; lớn l&ecirc;n, đến th&agrave;nh phố S một nơi ho&agrave;n to&agrave;n xa lạ để bắt đầu lại.<br />\r\nHiện tại c&ocirc; đ&atilde; bu&ocirc;ng bỏ rồi, l&uacute;c c&ograve;n trẻ ai m&agrave; kh&ocirc;ng gặp phải mấy t&ecirc;n cặn b&atilde; chứ? Thời gian đ&atilde; lấy đi diện mạo trẻ trung nhưng cũng lắng đọng lại những kinh nghiệm qu&yacute; b&aacute;u.<br />\r\nNếu l&agrave; b&acirc;y giờ, c&ocirc; nhất định sẽ kh&ocirc;ng c&ugrave;ng đ&ocirc;i cẩu nam nữ kia bắt gian rồi g&igrave; m&agrave; đ&aacute;nh ghen, chỉ l&agrave; l&uacute;c đ&oacute; vẫn c&ograve;n non trẻ, lần đầu ti&ecirc;n gặp phải t&igrave;nh huống như vậy liền bị k&iacute;ƈɦ ŧɦ&iacute;ƈɦ, bởi nỗi đau bị cả bạn b&egrave; v&agrave; bạn trai phản bội khiến c&ocirc; kh&ocirc;ng thể b&igrave;nh tĩnh nổi.<br />\r\nHạ Chi T&igrave;nh nhấp một ngụm c&agrave; ph&ecirc;, nh&igrave;n lại rồi mỉm cười: &quot;Kh&ocirc;ng phải chứ, nhiều năm như vậy, c&ocirc; vẫn c&ograve;n quen với t&ecirc;n cặn b&atilde; đ&oacute; hả? Nhưng cũng phải th&ocirc;i, tục ngữ n&oacute;i, kỹ nữ phối với ch&oacute;, thi&ecirc;n trường địa cửu, tiện nh&acirc;n phối với g&agrave;, gắn b&oacute; keo sơn. Hai người gom lại một chỗ, quả l&agrave; c&ocirc;ng đức v&ocirc; lượng, miễn cho ra ngo&agrave;i l&agrave;m hại x&atilde; hội.&quot;<br />\r\n&quot; C&ocirc;....!&quot; Khương Vi tức đến trợn cả mắt, miệng lưỡi sắc b&eacute;n của Hạ Chi T&igrave;nh thế n&agrave;y nằm ngo&agrave;i dự kiến của c&ocirc; ta, phải biết rằng năm đ&oacute; Hạ Chi T&igrave;nh chỉ biết lặp đi lặp lại m&agrave; mắng c&ocirc; ta l&agrave; hồ ly tinh.<br />\r\nNhưng ngay sau đ&oacute; c&ocirc; ta đ&atilde; kh&ocirc;i phục sức chiến đấu, Khương Vi nh&igrave;n xung quanh, nhướng m&agrave;y ch&acirc;m chọc: &quot;Ng&agrave;y cuối tuần trọng đại, c&ocirc; tự m&igrave;nh ngồi đ&acirc;y. Đừng n&oacute;i với t&ocirc;i l&agrave; c&ocirc; đến đ&acirc;y chỉ v&igrave; uống c&agrave; ph&ecirc; nha, kh&ocirc;ng lẽ sau khi chia tay Lục Xuy&ecirc;n c&ocirc; kh&ocirc;ng c&oacute; bạn trai hả?&quot;<br />\r\n&quot;......&quot; Hạ Chi T&igrave;nh chỉ cười kh&ocirc;ng n&oacute;i, cảm thấy đầu gối tr&uacute;ng một thương.</p>\r\n\r\n<p><br />\r\n&quot; Cũng đ&uacute;ng th&ocirc;i, người đ&agrave;n &ocirc;ng tốt như Lục Xuy&ecirc;n sao c&oacute; thể t&ugrave;y tiện m&agrave; t&igrave;m được, c&ocirc; kh&ocirc;ng phải đến đ&acirc;y để xem mắt đ&oacute; chứ?&quot;<br />\r\n&quot;.....&quot; Phốc! Hạ Chi T&igrave;nh cảm thấy bị tr&uacute;ng tiếp một thương v&agrave;o đầu gối b&ecirc;n kia.<br />\r\nC&oacute; c&acirc;u n&oacute;i như thế n&agrave;y, người tranh nhau khẩu kh&iacute; phật tranh nhau n&eacute;n hương, thật l&agrave; ch&uacute; muốn nhịn m&agrave; thiếm kh&ocirc;ng nhịn nổi!<br />\r\nĐ&aacute;nh trả!<br />\r\nPhải đ&aacute;nh trả!<br />\r\nC&ocirc; đặt ly c&agrave; ph&ecirc; xuống v&agrave; mỉm cười: &quot;Kh&ocirc;ng n&oacute;i đến việc Lục Xuy&ecirc;n l&agrave; một t&ecirc;n cặn b&atilde;. D&ugrave; anh ta c&oacute; tốt thế n&agrave;o đi chăng nữa th&igrave; c&ocirc; cũng chỉ đang nhặt thứ m&agrave; t&ocirc;i vứt bỏ. Bạn trai hiện tại của t&ocirc;i, d&ugrave; l&agrave; gia thế hay ngoại h&igrave;nh, cũng đủ n&eacute;m Lục Xuy&ecirc;n ra xa mấy d&atilde;y phố. Kh&ocirc;ng biết c&ocirc; ở trước mặt t&ocirc;i khoe khoang c&aacute;i nỗi g&igrave;?&quot;<br />\r\nTục ngữ n&oacute;i, đ&aacute;nh người kh&ocirc;ng đ&aacute;nh mặt, vạch trần người kh&ocirc;ng n&oacute;i r&otilde; khuyết điểm, chỉ l&agrave; đối ph&oacute; với em g&aacute;i tr&agrave; xanh Khương Vi đ&acirc;y phải l&agrave;m như vậy!<br />\r\nHạ Chi T&igrave;nh vả đến mặt Khương Vi vang bạch bạch. Khương Vi tức giận đến nỗi lớp trang điểm tr&ecirc;n mặt bị biến dạng. Đ&acirc;y l&agrave; c&aacute;i gai vĩnh viễn trong l&ograve;ng c&ocirc; ta, c&ocirc; ta v&agrave; Hạ Chi T&igrave;nh c&ugrave;ng nhau quen Lục Xuy&ecirc;n, nhưng trong mắt Lục Xuy&ecirc;n chỉ c&oacute; thể nh&igrave;n thấy Hạ Chi T&igrave;nh.<br />\r\n&quot; Vậy sao? Nếu ưu t&uacute; đến vậy, họp lớp Tết năm nay mang bạn trai của c&ocirc; đến cho mọi người xem xem.&quot;<br />\r\n&quot;......&quot; &Aacute;ch...... Giống như tự vả mặt m&igrave;nh, Hạ Chi T&igrave;nh hớp mạnh một ngụm c&agrave; ph&ecirc;.<br />\r\n&quot; Sao vậy? Sợ hả? Hay l&agrave; căn bản kh&ocirc;ng c&oacute; bạn trai đi? Ha hả...... Kho&aacute;c l&aacute;c cũng kh&ocirc;ng chuẩn bị kịch bản, thật đ&uacute;ng l&agrave; cho rằng đẹp trai gi&agrave;u c&oacute; như miếng b&aacute;nh ven đường, giơ tay ra t&ugrave;y tiện l&agrave; c&oacute; sao?&quot;<br />\r\nKhương Vi với một người bạn của c&ocirc; ta giống như g&agrave; mẹ m&agrave; cười khanh kh&aacute;ch, Hạ Chi T&igrave;nh hận kh&ocirc;ng thể lao l&ecirc;n x&eacute; n&aacute;t khu&ocirc;n mặt giả tạo của Khương Vi bằng axit hyaluronic.<br />\r\nHạ Chi T&igrave;nh cười lạnh: &quot; Đi th&igrave; đi, c&oacute; g&igrave; m&agrave; phải sợ, d&ugrave; sao l&agrave;m tiểu tam cũng kh&ocirc;ng phải l&agrave; t&ocirc;i!&quot;<br />\r\nKhương Vi nhếch miệng tức giận: &quot; C&ocirc;....bớt đắc &yacute; lại đi, t&ocirc;i chống mắt l&ecirc;n xem c&ocirc; c&oacute; thể mang đến loại mặt h&agrave;ng tốt g&igrave;!&quot;<br />\r\nSau khi Khương Vi rời đi, Hạ Chi T&igrave;nh c&agrave;ng nghĩ c&agrave;ng phiền muộn, kh&ocirc;ng nhịn được đập đầu xuống b&agrave;n, năm th&aacute;ng đọng lại c&aacute;i rắm &iacute;, c&ocirc; vẫn c&ograve;n ấu trĩ như vậy, sao lại kh&ocirc;ng nhịn được, giờ đi đ&acirc;u t&igrave;m một người y&ecirc;u gi&agrave;u c&oacute; v&agrave; đẹp trai đ&acirc;y! !<br />\r\nĐứng b&ecirc;n cạnh l&agrave; một phục vụ chứng kiến tận mắt trận chiến khiến m&aacute;u huyết s&ocirc;i tr&agrave;o n&agrave;y liền ph&aacute;t trực tiếp l&ecirc;n Tianya* nhận được đứng đầu cư d&acirc;n mạng.<br />\r\n*Một diễn đ&agrave;n internet phổ biến của Trung Quốc<br />\r\n2-【2】<br />\r\nNg&agrave;y cuối tuần đ&oacute;, Hạ Chi T&igrave;nh ngồi qu&aacute;n c&agrave; ph&ecirc; chờ tới s&aacute;u giờ, người đ&agrave;n &ocirc;ng xem mắt từ đầu đến cuối kh&ocirc;ng hề xuất hiện, điện thoại của anh ta lu&ocirc;n ở chế độ kh&ocirc;ng nhận cuộc gọi người lạ.<br />\r\n&quot;Loại t&igrave;nh huống n&agrave;y chỉ c&oacute; một loại khả năng.&quot; L&acirc;m Du một b&ecirc;n liếm Haagen-Dazs* một b&ecirc;n ph&acirc;n t&iacute;ch: &quot;Chắc hẳn c&oacute; bạn g&aacute;i cũ đang g&acirc;y rắc rối.&quot;<br />\r\n*Thương hiệu kem của Mỹ<br />\r\nHạ Chi T&igrave;nh vốn cười nhạo c&ocirc; l&agrave; một qu&acirc;n sư m&aacute;u ch&oacute;, ai m&agrave; biết lời n&oacute;i đ&oacute; trở th&agrave;nh sự thật!<br />\r\nChỉ l&agrave; người l&agrave;m phiền kh&ocirc;ng phải bạn g&aacute;i cũ, m&agrave; l&agrave; bạn trai cũ.<br />\r\nHai ng&agrave;y sau khi đi xem mắt người đ&agrave;n &ocirc;ng kia, quầy lễ t&acirc;n n&oacute;i rằng một người đ&agrave;n &ocirc;ng đang t&igrave;m c&ocirc; ở cổng c&ocirc;ng ty. Hạ Chi T&igrave;nh đi ra ngo&agrave;i v&agrave; nh&igrave;n thấy một người đ&agrave;n &ocirc;ng mặc quần b&oacute; đang đứng ở cửa với chai nước kho&aacute;ng tr&ecirc;n tay.<br />\r\n&quot; Xin hỏi anh l&agrave; ai? &quot; C&ocirc; bước l&ecirc;n ph&iacute;a trước, ch&agrave;o hỏi đối phương.<br />\r\nNhưng c&ocirc; chưa kịp dứt lời th&igrave; người b&ecirc;n kia đ&atilde; cầm chai nước kho&aacute;ng tr&ecirc;n tay dội về ph&iacute;a c&ocirc;, rồi su&yacute;t ch&uacute;t nữa d&ugrave;ng ng&oacute;n tay chọc v&agrave;o mũi c&ocirc;, gắt gao mắng: &quot;Đồ hồ li tinh v&ocirc; li&ecirc;m sỉ, c&ocirc; muốn chia rẽ t&igrave;nh cảm của t&ocirc;i với Tiểu H&uacute;c sao, người Tiểu H&uacute;c y&ecirc;u l&agrave; t&ocirc;i!&quot;<br />\r\nC&ocirc; g&aacute;i quầy lễ t&acirc;n sợ đến mức h&eacute;t l&ecirc;n, &quot;Người đ&acirc;u a, v&igrave; y&ecirc;u gϊếŧ người a, Hạ Chi T&igrave;nh bị người tạt axit n&egrave; !!!<br />\r\nHạ Chi T&igrave;nh bị người đ&agrave;n &ocirc;ng mặc quần b&oacute; l&agrave;m cho trở tay kh&ocirc;ng kịp, cũng may tr&ecirc;n mặt v&agrave; cơ thể kh&ocirc;ng c&oacute; cảm gi&aacute;c đau đớn g&igrave;, sau khi x&aacute;c định đối phương chỉ tạt nước kho&aacute;ng, l&ograve;ng c&ocirc; hơi b&igrave;nh tĩnh lại, nhưng khi nghe thấy tiếng la h&eacute;t của c&ocirc; g&aacute;i quầy lễ t&acirc;n, c&ocirc; kh&ocirc;ng khỏi cảm thấy ch&oacute;ng mặt!<br />\r\nNgười của c&ocirc;ng ty nhanh ch&oacute;ng tập trung xung quanh, một số đồng nghiệp nam ngay lập tức t&uacute;m lấy người đ&agrave;n &ocirc;ng mặc quần b&oacute; v&agrave; đ&egrave; anh ta xuống s&agrave;n.<br />\r\nC&ocirc; g&aacute;i ở quầy lễ t&acirc;n sau khi h&eacute;t l&ecirc;n liền nhanh tay lẹ mắt b&aacute;o cảnh s&aacute;t, cảnh s&aacute;t rất nhanh đ&atilde; đến.<br />\r\nDưới sự h&ograve;a giải của cảnh s&aacute;t, l&uacute;c n&agrave;y mới biết được h&oacute;a ra người đ&agrave;n &ocirc;ng mặc quần b&oacute; l&agrave; bạn tốt* của người đ&agrave;n &ocirc;ng trong buổi xem mắt đ&oacute;.<br />\r\n&nbsp; &nbsp;*Thường l&agrave; bạn tốt (con trai v&agrave; con trai) hoặc những người bạn n&oacute;i đ&ugrave;a nhau l&agrave; gay<br />\r\nNgười đ&agrave;n &ocirc;ng xem mắt đ&oacute; dưới &aacute;p lực từ gia đ&igrave;nh đ&atilde; c&ugrave;ng người đ&agrave;n &ocirc;ng mặc quần b&oacute; n&agrave;y chia tay, người đ&agrave;n &ocirc;ng xem mắt v&igrave; muốn người đ&agrave;n &ocirc;ng mặc quần b&oacute; chết t&acirc;m liền gạt n&oacute;i Hạ Chi T&igrave;nh l&agrave; bạn g&aacute;i của anh ta, hơn nữa c&ograve;n mang thai con của anh ta.<br />\r\nNgười đ&agrave;n &ocirc;ng mặc quần b&oacute; kh&ocirc;ng c&aacute;ch n&agrave;o chấp nhận nổi, v&igrave; để n&iacute;u k&eacute;o người đ&agrave;n &ocirc;ng xem mắt m&agrave; cắt tay tự tử. Đ&oacute; l&agrave; l&yacute; do tại sao ng&agrave;y cuối tuần đ&oacute; người đ&agrave;n &ocirc;ng xem mắt cho c&ocirc; leo c&acirc;y. Nhưng việc tự tử cũng kh&ocirc;ng l&agrave;m thay đổi được quyết t&acirc;m chia tay của người đ&agrave;n &ocirc;ng xem mắt đ&oacute;.<br />\r\nNgười đ&agrave;n &ocirc;ng mặc quần b&oacute; qu&aacute; tức giận thế l&agrave; l&eacute;n trộm xem điện thoại của người đ&agrave;n &ocirc;ng xem mắt sau đ&oacute; lấy được địa chỉ c&ocirc;ng ty của Hạ Chi T&igrave;nh liền chạy đến đ&aacute;nh ghen.<br />\r\nSau khi l&agrave;m r&otilde; mọi chuyện, Hạ Chi T&igrave;nh gần như n&ocirc;n ra m&aacute;u, đ&acirc;y r&otilde; r&agrave;ng l&agrave; nằm kh&ocirc;ng cũng bị ăn đạn m&agrave;!<br />\r\nViệc người đ&agrave;n &ocirc;ng xem mắt sử dụng c&ocirc; như một l&aacute; chắn khiến c&ocirc; v&ocirc; c&ugrave;ng kinh tởm, c&ocirc; kh&ocirc;ng khinh thường đồng t&iacute;nh nhưng lại khinh bỉ v&igrave; nối d&otilde;i t&ocirc;ng đường m&agrave; lừa kết h&ocirc;n đồng t&iacute;nh.<br />\r\nC&ocirc; gọi người đ&agrave;n &ocirc;ng xem mắt đến c&ocirc;ng ty, n&oacute;i r&otilde; r&agrave;ng trước mặt người đ&agrave;n &ocirc;ng quần b&oacute;, sau đ&oacute; để bọn họ c&uacute;t đi.<br />\r\nSau khi L&acirc;m Du biết chuyện n&agrave;y liền mắng t&ecirc;n xem mắt đ&oacute; một trận, một c&acirc;u n&oacute;i toạc ra thi&ecirc;n cơ: &quot;Tr&aacute;ch kh&ocirc;ng được WeChat của t&ecirc;n xem mắt đ&oacute; l&agrave; &quot;mặt đất bị thương&quot; h&oacute;a ra &aacute;m thị l&agrave; &quot;hoa c&uacute;c t&agrave;n&quot;.&quot;<br />\r\nHạ Chi T&igrave;nh im lặng.<br />\r\nSau khi mọi chuyện được giải quyết, Hạ Chi T&igrave;nh đ&atilde; đi l&agrave;m trở lại, nhưng khi vừa bước v&agrave;o c&ocirc;ng ty, c&ocirc; đ&atilde; cảm nhận được những &aacute;nh mắt h&aacute;o hức, đầy b&agrave;n t&aacute;n từ c&aacute;c đồng nghiệp, thậm ch&iacute; cả đồng nghiệp Tiểu Ni c&oacute; quan hệ tốt với c&ocirc; cũng kh&ocirc;ng n&eacute;n được l&ograve;ng b&aacute;t qu&aacute;i m&agrave; chạy đến hỏi c&ocirc;:<br />\r\n&quot;Bọn họ đang truyền nhau rằng em v&igrave; muốn gả đi m&agrave; kh&ocirc;ng tiếc c&ugrave;ng gay kết h&ocirc;n, chuyện n&agrave;y c&oacute; thật kh&ocirc;ng?&quot;<br />\r\n&quot;Tất nhi&ecirc;n kh&ocirc;ng phải l&agrave; sự thật, người đ&oacute; .... &quot;<br />\r\n&quot;Được rồi em kh&ocirc;ng cần phải giải th&iacute;ch, chị hiểu m&agrave;, nhưng chị khuy&ecirc;n em kh&ocirc;ng n&ecirc;n x&uacute;c động, đợi sau khi em kết h&ocirc;n, nếu một người đ&agrave;n &ocirc;ng kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o tr&ecirc;n giường ph&aacute;t huy t&aacute;c dụng của m&igrave;nh, vậy chi bằng kết h&ocirc;n với một c&aacute;i [ beep&mdash;&mdash;]*m&aacute;t xa l&agrave; vừa.&quot;<br />\r\n*&acirc;m thanh trẻ em kh&ocirc;ng n&ecirc;n nghe, m&agrave; c&aacute;i m&aacute;t xa n&agrave;y kh&ocirc;ng biết chị em n&agrave;o hiểu kh&ocirc;ng =))<br />\r\n&quot;......&quot; C&ocirc; nghẹn họng kh&ocirc;ng c&ograve;n lời để n&oacute;i, phụ nữ đ&atilde; kết h&ocirc;n khi n&oacute;i chuyện đều gọn g&agrave;ng dứt kho&aacute;t như kim ch&acirc;m v&agrave;o m&aacute;u như n&agrave;y sao?<br />\r\nHạ Chi T&igrave;nh y&ecirc;n lặng trở về chỗ ngồi v&agrave; cũng &acirc;m thầm đưa ra một quyết định, cuối tuần n&agrave;y c&ocirc; sẽ quay lại t&igrave;m &quot;sư phụ&quot; để h&oacute;a giải cuộc đời đầy ch&ocirc;ng gai của m&igrave;nh.<br />\r\nTuy nhi&ecirc;n v&agrave;o buổi chiều, c&ocirc;ng ty bất ngờ nổ ra một tin đồn kh&aacute;c hấp dẫn hơn, khiến mọi người đột ngột chuyển lực ch&uacute; &yacute;.<br />\r\nNăm sau, trụ sở b&ecirc;n Mỹ sẽ điều đến một vị gi&aacute;m đốc, nghe n&oacute;i rằng vị gi&aacute;m đốc n&agrave;y l&agrave; tuổi trẻ t&agrave;i cao.<br />\r\nC&oacute; tin đồn rằng gi&aacute;m đốc thật ra l&agrave; con trai của chủ tịch c&ocirc;ng ty, chủ tịch đ&atilde; cử anh ấy đến một c&ocirc;ng ty con ở Trung Quốc để r&egrave;n luyện, chuẩn bị cho việc kế nhiệm trong tương lai.<br />\r\nC&oacute; nguồn đ&aacute;ng tin cậy hơn n&oacute;i rằng ch&agrave;ng ph&uacute; nhị đại n&agrave;y ngọc thụ l&acirc;m phong, cử chỉ tự nhi&ecirc;n, phong lưu ph&oacute;ng kho&aacute;ng, kh&iacute; vũ hi&ecirc;n ngang, anh minh thần v&otilde;, d&ugrave; ưu t&uacute; như thế nhưng anh ấy vẫn kh&ocirc;ng ki&ecirc;u ngạo kh&ocirc;ng tự ti, đ&agrave;ng ho&agrave;ng đứng đắn, trước nay kh&ocirc;ng bao giờ v&igrave; bố m&igrave;nh l&agrave; chủ tịch m&agrave; t&ugrave;y &yacute; tr&ecirc;u chọc t&igrave;nh cảm phụ nữ, v&ocirc; c&ugrave;ng giữ m&igrave;nh trong sạch.<br />\r\nN&oacute;i một c&aacute;ch đơn giản - vị gi&aacute;m đốc tương lai n&agrave;y vừa gi&agrave;u c&oacute; lại c&ograve;n độc th&acirc;n.<br />\r\nNgay sau khi tin tức được lan truyền, n&oacute; c&oacute; sức c&ocirc;ng ph&aacute; mạnh như một vụ nổ bom hạt nh&acirc;n, v&agrave; to&agrave;n bộ phụ nữ trong c&ocirc;ng ty đ&atilde; n&aacute;o loạn. Nội bộ c&ocirc;ng ty li&ecirc;n tục đổ chu&ocirc;ng, li&ecirc;n tục bị soi, người đ&atilde; kết h&ocirc;n th&igrave; hối hận v&igrave; cưới sớm qu&aacute;, người chưa kết h&ocirc;n th&igrave; n&oacute;i muốn tổ chức đo&agrave;n đi H&agrave;n Quốc v&agrave;o m&ugrave;a xu&acirc;n để phẫu thuật thẩm mỹ.<br />\r\nM&ugrave;a xu&acirc;n sắp đến rồi, Hạ Chi T&igrave;nh nh&igrave;n c&aacute;c đồng nghiệp nữ l&ocirc;ng m&agrave;y kh&oacute;e mắt tr&agrave;n đầy gi&oacute; xu&acirc;n kh&ocirc;ng khỏi nghĩ đến con m&egrave;o m&agrave; c&ocirc; đ&atilde; nu&ocirc;i trước đ&acirc;y, mỗi khi m&ugrave;a xu&acirc;n đến n&oacute; lu&ocirc;n k&ecirc;u meo meo ch&agrave;o xu&acirc;n.<br />\r\nHạ Chi T&igrave;nh đối với vị si&ecirc;u cấp cao cấp ngầu l&ograve;i đến tận trời tổng gi&aacute;m đốc sắp đến n&agrave;y, cũng thực chờ mong, bởi nếu kh&ocirc;ng phải nhờ anh ta chuyện của c&ocirc; sao c&oacute; thể nhanh như vậy m&agrave; lắng xuống.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&mdash; QUẢNG C&Aacute;O &mdash;</p>', '0', 'dai-chien-danh-ghen', 4, '79', '2022-07-31 08:24:54', '2022-07-31 08:24:54'),
(13, '3', 'Thông báo thuê bạn trai', '<p>C&agrave;ng gần đến Tết, Hạ Chi T&igrave;nh c&agrave;ng buồn bực.<br />\r\nMẹ T&ocirc; biếи ŧɦ&aacute;i đem t&ecirc;n QQ đổi th&agrave;nh: đếm ngược 28 ng&agrave;y gặp con rể. Thật l&agrave; trần trụi m&agrave;, chi&ecirc;u thức bức h&ocirc;n kiểu im hơi lặng tiếng n&agrave;y quả thật l&agrave;m người phẫn nộ!<br />\r\nKẻ đ&aacute;ng ch&eacute;m ng&agrave;n lần Hạ Chi Sơ kh&ocirc;ng những chặn WeChat c&ocirc; m&agrave; c&ograve;n chặn lu&ocirc;n c&aacute;c phương thức li&ecirc;n lạc khiến c&ocirc; tức đi&ecirc;n m&agrave; cũng kh&ocirc;ng l&agrave;m g&igrave; được.<br />\r\nM&agrave; l&atilde;o cha c&ocirc; ng&agrave;y ng&agrave;y bận l&ecirc;n QQ WeChat show &acirc;n &aacute;i, căn bản kh&ocirc;ng đếm xỉa tới c&ocirc;, l&atilde;o cha c&ocirc; cả đời si m&ecirc; mẹ T&ocirc;, đến nay c&ograve;n chưa thức tỉnh lại.<br />\r\nHạ Chi T&igrave;nh ngẩng đầu một g&oacute;c 45 độ nh&igrave;n trần nh&agrave; m&agrave; rơi nước mắt, cả nh&agrave; c&ocirc; đều l&agrave; mấy dạng g&igrave; vậy a!<br />\r\nC&ocirc; phiền l&ograve;ng m&agrave; n&eacute;m điện thoại di động đi, xoay qua nh&igrave;n L&acirc;m Du đang cắn hạt dưa: &quot; Kh&ocirc;ng phải cậu lu&ocirc;n xưng l&agrave; chuy&ecirc;n gia t&igrave;nh y&ecirc;u hả? Mau nhanh nghĩ c&aacute;ch gi&uacute;p tớ n&ecirc;n l&agrave;m g&igrave; b&acirc;y giờ?&quot;</p>', '<p>C&agrave;ng gần đến Tết, Hạ Chi T&igrave;nh c&agrave;ng buồn bực.<br />\r\nMẹ T&ocirc; biếи ŧɦ&aacute;i đem t&ecirc;n QQ đổi th&agrave;nh: đếm ngược 28 ng&agrave;y gặp con rể. Thật l&agrave; trần trụi m&agrave;, chi&ecirc;u thức bức h&ocirc;n kiểu im hơi lặng tiếng n&agrave;y quả thật l&agrave;m người phẫn nộ!<br />\r\nKẻ đ&aacute;ng ch&eacute;m ng&agrave;n lần Hạ Chi Sơ kh&ocirc;ng những chặn WeChat c&ocirc; m&agrave; c&ograve;n chặn lu&ocirc;n c&aacute;c phương thức li&ecirc;n lạc khiến c&ocirc; tức đi&ecirc;n m&agrave; cũng kh&ocirc;ng l&agrave;m g&igrave; được.<br />\r\nM&agrave; l&atilde;o cha c&ocirc; ng&agrave;y ng&agrave;y bận l&ecirc;n QQ WeChat show &acirc;n &aacute;i, căn bản kh&ocirc;ng đếm xỉa tới c&ocirc;, l&atilde;o cha c&ocirc; cả đời si m&ecirc; mẹ T&ocirc;, đến nay c&ograve;n chưa thức tỉnh lại.<br />\r\nHạ Chi T&igrave;nh ngẩng đầu một g&oacute;c 45 độ nh&igrave;n trần nh&agrave; m&agrave; rơi nước mắt, cả nh&agrave; c&ocirc; đều l&agrave; mấy dạng g&igrave; vậy a!<br />\r\nC&ocirc; phiền l&ograve;ng m&agrave; n&eacute;m điện thoại di động đi, xoay qua nh&igrave;n L&acirc;m Du đang cắn hạt dưa: &quot; Kh&ocirc;ng phải cậu lu&ocirc;n xưng l&agrave; chuy&ecirc;n gia t&igrave;nh y&ecirc;u hả? Mau nhanh nghĩ c&aacute;ch gi&uacute;p tớ n&ecirc;n l&agrave;m g&igrave; b&acirc;y giờ?&quot;<br />\r\n&quot;Ếch ba ch&acirc;n kh&oacute; kiếm chứ đ&agrave;n &ocirc;ng hai ch&acirc;n th&igrave; c&oacute; đầy, trong đ&aacute;m anh em của cậu t&ugrave;y tiện chọn một người giả l&agrave;m bạn trai về nh&agrave; với cậu l&agrave; được rồi, so easy~&quot;<br />\r\nHạ Chi T&igrave;nh hai mắt lấp l&aacute;nh, &quot;Xin hỏi trong bọn họ c&oacute; ai cao hơn 1m78, đẹp trai giống như Ng&ocirc; Ngạn Tổ kh&ocirc;ng?&quot;<br />\r\nL&acirc;m Du cười lạnh một tiếng: &quot;Đẳng cấp so&aacute;i ca như Ng&ocirc; Ngạn Tổ c&ograve;n tồn tại để cậu lựa chọn hả? Thật l&agrave; too young too simple, chỉ c&oacute; dưa vẹo t&aacute;o nứt mới c&oacute; thể để người t&ugrave;y tiện chọn m&agrave; th&ocirc;i.&quot;<br />\r\nHạ Chi T&igrave;nh g&atilde;i đầu: &quot;Vậy tớ phải l&agrave;m c&aacute;i g&igrave; b&acirc;y giờ? T&ocirc; l&atilde;o th&aacute;i b&ecirc;n kia c&ograve;n dễ n&oacute;i, b&agrave; ấy đối với con rể tương lai y&ecirc;u cầu rất đơn giản th&ocirc; bạo, chỉ cần l&agrave; nam c&ograve;n l&agrave; được, vấn đề l&agrave; nếu tớ đưa ra dưa vẹo t&aacute;o nứt như thế đến l&uacute;c đ&oacute; khiến Khương Vi cười chết mất.&quot;<br />\r\n&quot;Cậu n&oacute;i đ&uacute;ng, như thế n&agrave;o cũng kh&ocirc;ng thể để con tr&agrave; xanh Khương Vi kia vừa &yacute; được!&quot; L&acirc;m Du &quot;Phốc&quot; phun ra vỏ hạt dưa, &quot;B&acirc;y giờ tr&ecirc;n mạng kh&ocirc;ng phải đang lưu h&agrave;nh thu&ecirc; bạn trai qua mạng sao? Hay l&agrave; cậu cũng l&ecirc;n đăng tin tuyển người thử?&quot;<br />\r\n&quot;Vậy c&oacute; ổn kh&ocirc;ng?&quot;<br />\r\n&quot;Kh&ocirc;ng biết, b&acirc;y giờ chỉ c&ograve;n nước đem ngựa chết chữa th&agrave;nh ngựa sống th&ocirc;i.&quot;<br />\r\nV&igrave; thế dưới sự x&uacute;i xục của qu&acirc;n sư m&aacute;u ch&oacute; L&acirc;m Du, Hạ Chi T&igrave;nh đ&atilde; đăng một b&agrave;i thu&ecirc; bạn trai tr&ecirc;n diễn đ&agrave;n c&aacute;c chủ sở hữu Beicheng, nội dung b&agrave;i đăng như sau:<br />\r\nThượng đế n&oacute;i rằng mỗi bạn nhỏ độc th&acirc;n kiếp trước ch&iacute;nh l&agrave; thi&ecirc;n thần bị g&atilde;y c&aacute;nh. Cuối năm quả l&agrave; cửa ải l&agrave;m kh&oacute; người độc th&acirc;n, v&igrave; ứng ph&oacute; với chuyện bức h&ocirc;n của l&atilde;o Phật gia trong nh&agrave; n&ecirc;n m&igrave;nh ở đ&acirc;y cần thu&ecirc; một người bạn trai:<br />\r\nY&ecirc;u cầu bề ngo&agrave;i: Cao tr&ecirc;n 1m78, đẹp trai cỡ Ng&ocirc; Ngạn Tổ, cơ bụng s&aacute;u m&uacute;i.<br />\r\nThời gian l&agrave;m việc: Từ giao thừa đến mồng bốn Tết, tổng cộng năm ng&agrave;y.<br />\r\nNội dung l&agrave;m việc: C&ugrave;ng ăn, c&ugrave;ng uống, c&ugrave;ng giả bộ đ&oacute;ng kịch.<br />\r\nTh&ugrave; lao c&ocirc;ng việc: Bao ăn, bao ở, bao v&eacute; m&aacute;y bay, cộng th&ecirc;m năm ngh&igrave;n đồng tiền hoa hồng.<br />\r\nĐi ngang qua xin đừng bỏ lỡ, nếu bạn l&agrave; một anh ch&agrave;ng đẹp trai, h&atilde;y nhấc điện thoại l&ecirc;n v&agrave; th&ecirc;m WeChat: xxx168 (quả t&aacute;o nhỏ ph&aacute;t nổ), t&ocirc;i đang chờ bạn tr&ecirc;n WeChat ~~ (* ^ __ ^ *) ~~<br />\r\nHạ Chi T&igrave;nh vốn đang &ocirc;m hy vọng rất lớn, chỉ l&agrave; b&agrave;i viết vừa đăng l&ecirc;n chưa tới nửa tiếng liền bị người kiểm duyệt x&oacute;a, người kiểm duyệt n&oacute;i rằng c&ocirc; vi phạm nội quy của hội đồng quản trị, c&ocirc; kh&ocirc;ng phục, tr&ecirc;n diễn đ&agrave;n nhiều b&agrave;i đăng giống nhau vậy sao hắn kh&ocirc;ng x&oacute;a, v&igrave; sao cố &yacute; x&oacute;a b&agrave;i đăng của c&ocirc;? Nhưng sau khi c&ocirc; gửi c&acirc;u n&agrave;y, ảnh đại diện của người kiểm duyệt chuyển sang m&agrave;u x&aacute;m, phải mất hai mươi ph&uacute;t sau anh ta mới trả lời: T&ocirc;i nh&igrave;n kh&oacute; chịu, kh&ocirc;ng được hả?<br />\r\nbỗng chốc lệ rơi đầy mặt, người một xui xẻo, đăng b&agrave;i viết th&ocirc;i m&agrave; cũng gặp phải người kiểm duyệt t&ugrave;y hứng như vậy.<br />\r\nNgười xui xẻo th&igrave; thấy nhiều rồi, nhưng xui đến thế n&agrave;y quả l&agrave; lần đầu thấy được, L&acirc;m Du ở tr&ecirc;n s&ocirc; pha cười đến chết đi sống lại, c&ocirc; tức giận n&eacute;m c&aacute;i gối bay sang.<br />\r\n3-【2】<br />\r\nĐ&atilde; hơn một tuần kể từ vụ va quẹt xe lần trước, cao ph&uacute; so&aacute;i giả kia chưa bao giờ gọi cho c&ocirc;. X Hạ Chi T&igrave;nh kh&ocirc;ng khỏi cảm thấy may mắn v&agrave; hy vọng b&ecirc;n kia sẽ kh&ocirc;ng cần c&ocirc; bồi thường, nhưng hiện thực lại một lần nữa nhắc nhở c&ocirc; rằng hai chữ &quot;may mắn&quot; kh&ocirc;ng thuộc về c&ocirc;.<br />\r\nH&ocirc;m nay cuối tuần, c&ocirc; đang nằm nghi&ecirc;ng ngả tr&ecirc;n s&ocirc; pha xem mấy b&agrave;i viết hot tr&ecirc;n Tianya, bỗng nhi&ecirc;n điện thoại reo l&ecirc;n hiện ra một d&atilde;y số lạ.<br />\r\n&quot;Alo, xin ch&agrave;o, cho hỏi ai vậy ạ?&quot;<br />\r\nB&ecirc;n kia r&otilde; r&agrave;ng dừng một ch&uacute;t, &quot;C&ocirc;...... Kh&ocirc;ng c&oacute; lưu số di động của t&ocirc;i?&quot;<br />\r\nC&ocirc; trả lời thẳng thừng v&agrave; r&otilde; r&agrave;ng, &quot;Kh&ocirc;ng.&quot;<br />\r\n&quot;Được rồi, rất tốt!&quot; Tống An Thần gần như nghiến răng nghiến lợi khi n&oacute;i c&acirc;u n&agrave;y.<br />\r\nNg&agrave;y h&ocirc;m đ&oacute; khi c&oacute; số điện thoại của c&ocirc;, anh đ&atilde; ghi nhớ rất kỹ, cả tuần liền đều cầm điện thoại bấm, tắt, tắt rồi bấm, sợ vội v&agrave;ng qu&aacute; lại g&acirc;y ấn tượng xấu cho đối phương, kh&ocirc;ng ngờ c&ocirc; ấy th&igrave; hay rồi, kh&ocirc;ng nhận ra giọng anh th&igrave; cũng th&ocirc;i đi, lại c&ograve;n kh&ocirc;ng chịu lưu số điện thoại của anh.<br />\r\nTừng đợt h&agrave;n kh&iacute; c&aacute;ch điện thoại ập v&agrave;o trước mặt, Hạ Chi T&igrave;nh nhịn kh&ocirc;ng được rụt rụt cổ, duỗi tay muốn uống miếng nước lấy b&igrave;nh tĩnh.<br />\r\n&quot;Hạ tiểu thư quả nhi&ecirc;n qu&yacute; nh&acirc;n hay qu&ecirc;n chuyện, bất qu&aacute; kh&ocirc;ng sao, t&ocirc;i nghĩ Hạ tiểu thư rất nhanh liền nhớ được bởi kh&ocirc;ng phải ai cũng c&oacute; cơ hội t&ocirc;ng v&agrave;o Lamborghini.&quot;<br />\r\n&quot;Phốc!&quot; Hạ Chi T&igrave;nh phun một ngụm nước l&ecirc;n tr&ecirc;n notebook, v&igrave; cứu lại notebook, c&ocirc; liền đem notebook từ tr&ecirc;n s&ocirc; pha lăn xuống ng&atilde; chổng v&oacute;, eo đạp mạnh v&agrave;o b&agrave;n tr&agrave;, đau đến c&ocirc; nhe răng nhếch miệng.<br />\r\n&quot;Tiếng g&igrave; vậy? Xảy ra chuyện g&igrave;?&quot;<br />\r\n&quot;Alo, c&ocirc; c&ograve;n c&oacute; đ&oacute; kh&ocirc;ng? Rốt cuộc xảy ra chuyện g&igrave;?&quot;<br />\r\n&quot;N&oacute;i chuyện, Hạ Chi T&igrave;nh!&quot; Điện thoại đầu b&ecirc;n kia truyền đến một tiếng vang thật lớn, sau đ&oacute; liền kh&ocirc;ng c&oacute; thanh &acirc;m g&igrave; nữa, Tống An Thần trong l&ograve;ng hiện l&ecirc;n ng&agrave;n vạn loại kh&ocirc;ng giả thiết kh&ocirc;ng tốt.<br />\r\nMột hồi l&acirc;u Hạ Chi T&igrave;nh mới từ tr&ecirc;n mặt đất b&ograve; dậy, nhặt di động tr&ecirc;n mặt đất l&ecirc;n, &quot;T&ocirc;i...... T&ocirc;i kh&ocirc;ng c&oacute; việc g&igrave;, chỉ l&agrave; t&eacute; ng&atilde; một c&aacute;i từ tr&ecirc;n s&ocirc; pha xuống.&quot;<br />\r\nTống An Thần dừng lại bước ch&acirc;n đang chạy, thở hổn hển, &quot;...... Ngu ngốc!&quot;<br />\r\n&quot;Hả, anh n&oacute;i c&aacute;i g&igrave;? T&ocirc;i vừa rồi nghe kh&ocirc;ng r&otilde;.&quot; Điện thoại c&ocirc; bị hư rồi giờ lại rớt th&ecirc;m một lần nữa c&agrave;ng hư nặng th&ecirc;m, nghe khi được khi mất.<br />\r\n&quot;Kh&ocirc;ng c&oacute; g&igrave;.&quot; Tống An Thần dựa v&aacute;ch tường ven đường, tr&aacute;i tim treo cao l&uacute;c n&agrave;y mới bu&ocirc;ng xuống, l&uacute;c n&agrave;y dừng lại mới ph&aacute;t hiện bất qu&aacute; thời gian ngắn ngủn chưa tới mười ph&uacute;t m&agrave; anh đ&atilde; ra một th&acirc;n mồ h&ocirc;i, kh&ocirc;ng biết do bị dọa hay l&agrave; do vừa rồi chạy qu&aacute; nhanh.<br />\r\n&quot;Ừm, c&aacute;i kia...... T&ocirc;i muốn biết ph&iacute; sửa xe hết bao nhi&ecirc;u?&quot;<br />\r\n&quot;Gặp mặt rồi n&oacute;i, nửa tiếng nữa gặp nhau ở qu&aacute;n c&agrave; ph&ecirc; C&#39;estlavie cạnh quảng trường Du Thi&ecirc;n.&quot;<br />\r\nC&uacute;p điện thoại, Tống An Thần ph&aacute;t hiện v&agrave;i người qua đường đều d&ugrave;ng &aacute;nh mắt qu&aacute;i dị nh&igrave;n qua ch&acirc;n anh, theo &aacute;nh mắt mọi người nh&igrave;n xuống ch&acirc;n mới ph&aacute;t hiện l&uacute;c n&atilde;y gấp qu&aacute; m&agrave; anh mang lu&ocirc;n d&eacute;p ở nh&agrave; chạy ra đường.<br />\r\nAnh cười giễu một tiếng, xoay người bước về.<br />\r\n3-【3】<br />\r\nHạ Chi T&igrave;nh ngồi nghệch tại chỗ, trong đầu thẩn thờ lặp đi lặp lại một con số ph&oacute;ng đại&mdash;&mdash; 50 vạn!<br />\r\nC&ocirc; biết ph&iacute; sửa si&ecirc;u xe rất đắt, cho n&ecirc;n l&uacute;c trước cắn răng m&agrave; mượn Nh&acirc;n Chi Sơ mười vạn, đ&oacute; l&agrave; đ&atilde; t&iacute;nh dư dả ra rồi, l&uacute;c đ&oacute; c&ocirc; c&ograve;n ng&acirc;y thơ nghĩ rằng ph&iacute; sửa xe mắc nhất bất qu&aacute; khoảng bảy vạn, l&uacute;c đ&oacute; c&ocirc; sẽ lấy lại tiền dư m&agrave; ti&ecirc;u x&agrave;i một ch&uacute;t sau đ&oacute; đổi điện thoại mới.<br />\r\nChỉ l&agrave; ai c&oacute; thể n&oacute;i cho c&ocirc; biết 50 vạn n&agrave;y rốt cuộc l&agrave; chuyện như thế n&agrave;o? Lamborghini th&igrave; sao, t&ocirc;ng rồi c&ograve;n kh&ocirc;ng phải chỉ l&agrave; một đống sắt vụn sao, dựa v&agrave;o c&aacute;i g&igrave; m&agrave; mắc như vậy?!<br />\r\nTống An Thần nh&igrave;n người ngồi ở đối diện hai mắt đăm đăm, vẻ mặt kinh ngạc đến ng&acirc;y người kia, kh&oacute;e miệng nhếch l&ecirc;n một nụ cười kh&ocirc;ng dễ thấy, anh ho một ch&uacute;t, kiềm chế &yacute; cười, mở miệng n&oacute;i: &quot;Hạ tiểu thư, chi ph&iacute; tr&ecirc;n h&oacute;a đơn đ&acirc;y c&ocirc; c&oacute; thể xem xem, c&ograve;n số tiền n&agrave;y c&ocirc; định khi n&agrave;o trả cho t&ocirc;i?&quot;<br />\r\nHạ Chi T&igrave;nh thật vất vả mới khiến mắt trở lại ti&ecirc;u cự, c&ocirc; y&ecirc;n lặng nh&igrave;n v&agrave;o tuấn nhan họa thủy kia của Tống An Thần, nhịn kh&ocirc;ng được ho&agrave;i nghi đ&acirc;y c&oacute; phải l&agrave; thủ đoạn lừa dối mới, &quot;Tống ti&ecirc;n sinh, t&ocirc;i muốn biết v&igrave; sao ph&iacute; sửa xe n&agrave;y đắt như vậy?&quot;<br />\r\n&quot;H&oacute;a đơn tr&ecirc;n tay Hạ tiểu thư đ&atilde; ghi kỹ c&agrave;ng tỉ mỉ từng điều khoản hạng mục.&quot;<br />\r\nHạ Ch&iacute; Thanh hất cằm, rầu rĩ: &quot;Nhưng...... T&ocirc;i l&agrave;m sao biết anh c&oacute; cố &yacute; ở tr&ecirc;n đ&oacute; sửa lại cho mắc hơn rồi l&agrave;m sao t&ocirc;i biết h&oacute;a đơn n&agrave;y c&oacute; thật hay kh&ocirc;ng.&quot;<br />\r\nTống An Thần đặt ly c&agrave; ph&ecirc; xuống, lạnh l&ugrave;ng nhếch m&eacute;p n&oacute;i: &quot;&Yacute; của Hạ tiểu thư l&agrave; t&ocirc;i đang lừa dối? Nếu l&agrave; &yacute; n&agrave;y, ngay b&acirc;y giờ Hạ tiểu thư c&oacute; thể b&aacute;o cảnh s&aacute;t.&quot;<br />\r\n&quot;T&ocirc;i...... T&ocirc;i kh&ocirc;ng c&oacute; &yacute; đ&oacute;, chỉ l&agrave; 50 vạn kh&ocirc;ng phải một con số nhỏ, t&ocirc;i d&ugrave; sao cũng phải biết r&otilde; r&agrave;ng.&quot; Giọng c&ocirc; c&agrave;ng n&oacute;i c&agrave;ng nhỏ, ho&agrave;n to&agrave;n kh&ocirc;ng giống kh&iacute; thế vừa rồi.<br />\r\nĐ&acirc;y l&agrave; kết quả từ nhỏ bị Hạ Chi Sơ ức hiếp, chỉ cần đối phương kh&iacute; thế cường một ch&uacute;t, c&ocirc; liền lập tức yếu đi.<br />\r\nTống An Thần mắt phượng đảo ở tr&ecirc;n mặt c&ocirc;, lời n&oacute;i ra đầy hư giả v&agrave; ch&acirc;n thực của Hạ Chi T&igrave;nh quả thực đ&aacute;ng y&ecirc;u, cực kỳ giống như thỏ con bị người bắt được, giương nanh m&uacute;a vuốt, kỳ thật kh&ocirc;ng c&oacute; một ch&uacute;t lực c&ocirc;ng k&iacute;ch.<br />\r\nAnh cố n&eacute;n &yacute; muốn cười, &quot;Hạ tiểu thư chẳng lẽ kh&ocirc;ng biết v&igrave; bảo đảm chất lượng, si&ecirc;u xe sau khi xảy ra va chạm sẽ đến chỗ 4s hoặc l&agrave; đổi hoặc l&agrave; sửa sao? Chiếc Aventador LP720-4 n&agrave;y l&agrave; phi&ecirc;n bản kỷ niệm Lamborghini th&agrave;nh lập 50 mươi năm. Số lượng giới hạn 100 chiếc tr&ecirc;n thế giới, gi&aacute; trong nước hơn 800 vạn. Vụ va chạm của Hạ tiểu thư v&agrave;o tuần trước đ&atilde; khiến th&acirc;n xe, cần gạt c&ugrave;ng với đ&egrave;n xe hư tổn kh&ocirc;ng giống nhau n&ecirc;n 50 vạn đ&atilde; l&agrave; gi&aacute; ưu đ&atilde;i sau khi giảm 20%.&quot;<br />\r\nKh&ocirc;ng chịu nổi, thật l&agrave; kh&ocirc;ng chịu nổi si&ecirc;u xe m&agrave;! 50 vạn thực chất l&agrave; đ&atilde; giảm 20% gi&aacute; ưu đ&atilde;i rồi!<br />\r\nHạ Chi T&igrave;nh c&agrave;ng nghe sắc mặt c&agrave;ng k&eacute;m, hận kh&ocirc;ng thể t&igrave;m miếng đậu hủ đ&acirc;m chết đi, c&ocirc; thật vất vả phấn đấu trở th&agrave;nh người c&oacute; xe c&oacute; nh&agrave; trong d&ograve;ng họ, chẳng lẽ v&igrave; va chạm như vậy c&ocirc; đ&agrave;nh phải b&aacute;n nh&agrave; b&aacute;n xe hay sao?<br />\r\n&quot;Kh&ocirc;ng biết Hạ tiểu thư nghe hiểu kh&ocirc;ng? Nếu Hạ tiểu thư c&ograve;n c&oacute; bất luận ho&agrave;i nghi g&igrave;, ch&uacute;ng ta c&oacute; thể mời c&ocirc;ng ty bảo hiểm đến tiến h&agrave;nh gi&aacute;m định chuy&ecirc;n nghiệp, đương nhi&ecirc;n ph&iacute; gi&aacute;m định l&agrave; do Hạ tiểu thư trả.&quot;<br />\r\n&quot;Kh&ocirc;ng cần kh&ocirc;ng cần.&quot; Hạ Chi T&igrave;nh lắc đầu như trống bỏi, &quot;Thực xin lỗi Tống ti&ecirc;n sinh, t&ocirc;i biết tr&aacute;ch nhiệm sự cố l&agrave; do t&ocirc;i, chỉ l&agrave;...... T&ocirc;i hiện tại kh&ocirc;ng c&oacute; nhiều tiền như vậy.&quot;<br />\r\nHạ Chi T&igrave;nh d&ugrave;ng sức m&agrave; dụi dụi đ&ocirc;i mắt, dụi đến đỏ bừng, cố &yacute; l&agrave;m cho ch&iacute;nh m&igrave;nh tr&ocirc;ng v&ocirc; tội một ch&uacute;t, nhu nhược đ&aacute;ng thương một ch&uacute;t.<br />\r\nCh&uacute;t t&acirc;m tư nhỏ n&agrave;y của Hạ Chi T&igrave;nh sao c&oacute; thể tr&aacute;nh được hai mắt Tống An Thần, anh c&uacute;i đầu, kh&oacute;e miệng khẽ giật rồi lại ngẩng đầu l&ecirc;n, tr&ecirc;n mặt bao l&ecirc;n một tầng sương gi&aacute;, &quot;Kh&ocirc;ng c&oacute; tiền? Vậy đ&acirc;y l&agrave; c&aacute;i g&igrave;?&quot;<br />\r\nTống An Sơ n&eacute;m tờ giấy cuối c&ugrave;ng trong t&uacute;i t&agrave;i liệu xuống b&agrave;n, Hạ Ch&iacute; Thanh đưa tay cầm l&ecirc;n, thoạt nh&igrave;n su&yacute;t nữa n&ocirc;n ra m&aacute;u! Đ&acirc;y kh&ocirc;ng phải l&agrave; th&ocirc;ng b&aacute;o thu&ecirc; bạn trai của c&ocirc; tr&ecirc;n diễn đ&agrave;n Beicheng sao? L&agrave;m thế n&agrave;o anh ta c&oacute; thể nh&igrave;n thấy n&oacute;? V&agrave; tại sao anh ta lại in n&oacute; ra?<br />\r\n&quot;Kh&ocirc;ng c&oacute; tiền bồi thường, lại c&oacute; tiền đi thu&ecirc; bạn trai, Hạ tiểu thư thật nghĩ rằng người kh&aacute;c đều l&agrave; đồ ngốc sao?&quot;<br />\r\n&quot;Kh&ocirc;ng phải, Tống ti&ecirc;n sinh anh nghe t&ocirc;i giải th&iacute;ch......&quot;<br />\r\n&quot;Kh&ocirc;ng cần! T&ocirc;i đ&atilde; chịu đủ những lời n&oacute;i dối của Hạ tiểu thư rồi, ch&uacute;ng ta gặp nhau tại t&ograve;a đi!&quot; N&eacute;m xuống một c&acirc;u như vậy, Tống An Thần đứng l&ecirc;n muốn đi.<br />\r\nVị tr&iacute; bọn họ ngồi rất gần cửa, mắt thấy Tống An Thần muốn đi ra cửa, Hạ Chi T&igrave;nh nhanh ch&oacute;ng đứng dậy, lon ton đi hai bước, đưa tay ra, &quot;Kh&ocirc;ng! Tống ti&ecirc;n sinh, đừng đi m&agrave;, h&atilde;y nghe t&ocirc;i giải th&iacute;ch!&quot;<br />\r\n&quot;Bu&ocirc;ng ra!&quot; Giọng anh nghe một tia ẩn nhẫn.<br />\r\n&quot;Đừng m&agrave;, Tống ti&ecirc;n sinh, anh cho t&ocirc;i th&ecirc;m cơ hội đi, t&ocirc;i......&quot;<br />\r\n&quot;Hạ Chi T&igrave;nh, sao c&ocirc; lại kh&ocirc;ng e lệ như vậy!&quot; Tống An Thần thở d&agrave;i, lại mở miệng, giọng điệu ngọt như tẩm mật, &quot;Ngoan, bu&ocirc;ng ra, đang c&oacute; nhiều người nh&igrave;n đấy.&quot;<br />\r\nHạ Chi T&igrave;nh vốn định phản b&aacute;c lại rằng c&ocirc; khi n&agrave;o kh&ocirc;ng e lệ, c&ograve;n chưa kịp mở miệng đ&atilde; bị c&acirc;u n&oacute;i tiếp theo của anh l&agrave;m cho sợ h&atilde;i, theo &aacute;nh mắt anh c&uacute;i đầu vừa nh&igrave;n thấy kh&ocirc;ng khỏi 囧, c&ocirc; r&otilde; r&agrave;ng l&agrave; đi bắt tay &aacute;o anh, sao lại biến th&agrave;nh quần anh rồi &gt;_</p>', '0', 'thong-bao-thue-ban-trai', 4, '87', '2022-07-31 08:25:23', '2022-07-31 08:25:23');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(14, '4', 'Ông tổ phúc hắc', '<p>Hạ Chi T&igrave;nh c&uacute;i đầu, kh&ocirc;ng ngừng khuấy ly c&agrave; ph&ecirc; tr&ecirc;n b&agrave;n.<br />\r\nTống An Thần nh&igrave;n v&agrave;nh tai Hạ Chi T&igrave;nh đỏ bừng, đ&ocirc;i mắt phượng d&agrave;i hẹp lộ ra nụ cười kh&oacute; lường: &quot;Thật l&agrave; kh&ocirc;ng thể tr&ocirc;ng mặt m&agrave; bắt h&igrave;nh dong, kh&ocirc;ng nghĩ tới Hạ tiểu thư lại l&agrave; người nhiệt t&igrave;nh như vậy.&quot;<br />\r\n&quot;......&quot; Hạ Chi T&igrave;nh rơi nước mắt, anh nhấn mạnh hai chữ &quot;nhiệt t&igrave;nh&quot; như thế, kh&ocirc;ng phải l&agrave; đang ch&acirc;m biếm c&ocirc; hay sao? C&ocirc; thật l&agrave; oan uổng m&agrave;, c&ocirc; kh&ocirc;ng biết tại sao lại nắm nhầm, nếu cố chấp t&igrave;m một c&aacute;i l&yacute; do, chỉ c&oacute; thể n&oacute;i l&agrave; lỗi do ch&ecirc;nh lệch chiều cao!<br />\r\n(Ch&ecirc;nh lệch chiều cao: đều tr&aacute;ch t&ocirc;i sao.)<br />\r\nAnh xem thời gian rồi n&oacute;i: &quot;Hạ tiểu thư kh&ocirc;ng phải c&oacute; chuyện muốn giải th&iacute;ch sao? Vậy tốt nhất nhanh l&ecirc;n, t&ocirc;i l&aacute;t nữa c&ograve;n c&oacute; chuyện kh&aacute;c.&quot;<br />\r\nHạ Chi T&igrave;nh ngẩng đầu, vừa vặn nh&igrave;n đến đồng hồ tr&ecirc;n tay Tống An Thần kh&ocirc;ng khỏi l&iacute;u lưỡi, tr&ecirc;n tay anh ta mang ch&iacute;nh l&agrave; đồng hồ Patek Philippe*, nghe n&oacute;i chiếc rẻ nhất ở nh&agrave; c&ocirc; cũng đ&atilde; hơn 10 vạn, được xem l&agrave; biểu tượng của giới qu&yacute; tộc, c&ocirc; kh&ocirc;ng khỏi sinh ra t&ograve; m&ograve; đối với th&acirc;n phận của người đ&agrave;n &ocirc;ng trước mắt n&agrave;y.<br />\r\n*Đồng hồ của n9 n&egrave; đẹp gh&ecirc; :3</p>', '<p>4-【1】<br />\r\nHạ Chi T&igrave;nh c&uacute;i đầu, kh&ocirc;ng ngừng khuấy ly c&agrave; ph&ecirc; tr&ecirc;n b&agrave;n.<br />\r\nTống An Thần nh&igrave;n v&agrave;nh tai Hạ Chi T&igrave;nh đỏ bừng, đ&ocirc;i mắt phượng d&agrave;i hẹp lộ ra nụ cười kh&oacute; lường: &quot;Thật l&agrave; kh&ocirc;ng thể tr&ocirc;ng mặt m&agrave; bắt h&igrave;nh dong, kh&ocirc;ng nghĩ tới Hạ tiểu thư lại l&agrave; người nhiệt t&igrave;nh như vậy.&quot;<br />\r\n&quot;......&quot; Hạ Chi T&igrave;nh rơi nước mắt, anh nhấn mạnh hai chữ &quot;nhiệt t&igrave;nh&quot; như thế, kh&ocirc;ng phải l&agrave; đang ch&acirc;m biếm c&ocirc; hay sao? C&ocirc; thật l&agrave; oan uổng m&agrave;, c&ocirc; kh&ocirc;ng biết tại sao lại nắm nhầm, nếu cố chấp t&igrave;m một c&aacute;i l&yacute; do, chỉ c&oacute; thể n&oacute;i l&agrave; lỗi do ch&ecirc;nh lệch chiều cao!<br />\r\n(Ch&ecirc;nh lệch chiều cao: đều tr&aacute;ch t&ocirc;i sao.)<br />\r\nAnh xem thời gian rồi n&oacute;i: &quot;Hạ tiểu thư kh&ocirc;ng phải c&oacute; chuyện muốn giải th&iacute;ch sao? Vậy tốt nhất nhanh l&ecirc;n, t&ocirc;i l&aacute;t nữa c&ograve;n c&oacute; chuyện kh&aacute;c.&quot;<br />\r\nHạ Chi T&igrave;nh ngẩng đầu, vừa vặn nh&igrave;n đến đồng hồ tr&ecirc;n tay Tống An Thần kh&ocirc;ng khỏi l&iacute;u lưỡi, tr&ecirc;n tay anh ta mang ch&iacute;nh l&agrave; đồng hồ Patek Philippe*, nghe n&oacute;i chiếc rẻ nhất ở nh&agrave; c&ocirc; cũng đ&atilde; hơn 10 vạn, được xem l&agrave; biểu tượng của giới qu&yacute; tộc, c&ocirc; kh&ocirc;ng khỏi sinh ra t&ograve; m&ograve; đối với th&acirc;n phận của người đ&agrave;n &ocirc;ng trước mắt n&agrave;y.<br />\r\n*Đồng hồ của n9 n&egrave; đẹp gh&ecirc; :3<br />\r\nTiếp theo, dưới &aacute;nh mắt s&aacute;ng quắc của Tống An Thần, c&ocirc; đem sự việc tại sao lại thu&ecirc; bạn trai tiền căn hậu quả n&oacute;i thẳng ra.<br />\r\nTống An Thần nhẹ nh&agrave;ng g&otilde; ng&oacute;n tay l&ecirc;n s&ocirc; pha, &quot;Cho n&ecirc;n n&oacute;i, v&igrave; đối ph&oacute; với cha mẹ v&agrave; bạn học n&ecirc;n Hạ tiểu thư mới thu&ecirc; bạn trai?&quot;<br />\r\nHạ Chi T&igrave;nh gật đầu như gi&atilde; tỏi, c&ocirc; hiện tại c&oacute; ch&uacute;t hối hận, gi&aacute; như kh&ocirc;ng n&ecirc;n đi xem mắt ở qu&aacute;n c&agrave; ph&ecirc; đ&oacute;, gi&aacute; như kh&ocirc;ng n&ecirc;n c&ugrave;ng điếm tr&agrave; xanh Khương Vi đấu khẩu, gi&aacute; như...... Nếu tr&ecirc;n đời n&agrave;y c&oacute; gi&aacute; như th&igrave; thế giới đ&acirc;u c&oacute; chiến tranh l&agrave;m g&igrave;.<br />\r\n&quot;H&oacute;a ra l&agrave; c&oacute; chuyện như vậy. Năm nay t&ocirc;i 26 tuổi, cao 1m83, nặng 80kg, cơ bụng t&aacute;m m&uacute;i, Hạ tiểu thư xem t&ocirc;i c&oacute; th&iacute;ch hợp kh&ocirc;ng?&quot;<br />\r\n&quot;Phốc...... Khụ khụ......&quot; C&ocirc; sặc một ngụm c&agrave; ph&ecirc; trong họng, sặc đến ho khan, &quot;Tống ti&ecirc;n sinh, anh đang tr&ecirc;u t&ocirc;i hả?&quot;<br />\r\nTống An Thần đưa qua một tờ khăn giấy, &quot;Kh&ocirc;ng phải, t&ocirc;i rất nghi&ecirc;m t&uacute;c, t&ocirc;i vừa l&uacute;c đang rảnh, muốn kiếm th&ecirc;m thu nhập, chẳng lẽ Hạ tiểu thư cảm thấy t&ocirc;i kh&ocirc;ng th&iacute;ch hợp?&quot;<br />\r\nHạ Chi T&igrave;nh tiếp nhận khăn giấy, xoa xoa kh&oacute;e miệng, &quot;T&ocirc;i kh&ocirc;ng c&oacute; &yacute; đ&oacute;, chỉ l&agrave; Tống ti&ecirc;n sinh giống như người c&oacute; tiền, chắc anh chướng mắt ch&uacute;t tiền hoa hồng n&agrave;y phải kh&ocirc;ng?&quot;<br />\r\nTống An Thần cười cười, &quot;T&ocirc;i chưa từng n&oacute;i l&agrave; m&igrave;nh c&oacute; tiền, kh&ocirc;ng biết Hạ tiểu thư ở đ&acirc;u biết được vậy?&quot;<br />\r\nHạ Chi T&igrave;nh chỉ chỉ cổ tay của anh, người c&oacute; thể mua nổi đồng hồ Patek Philippe sao c&oacute; thể ngh&egrave;o? Hơn nữa, tuy n&oacute;i rằng anh ta l&agrave; thu&ecirc; Lamborghini, nhưng chi ph&iacute; thu&ecirc; xe đủ để mua một chiếc Chery QQ* rồi.<br />\r\n*Xe Chery QQ<br />\r\nTống An Thần xoay chuyển đồng hồ tr&ecirc;n cổ tay, bộ dạng như bỗng nhi&ecirc;n tỉnh ngộ n&oacute;i: &quot;H&oacute;a ra c&ocirc; n&oacute;i c&aacute;i n&agrave;y.&quot;<br />\r\nC&ocirc; gật đầu, bỗng nhi&ecirc;n nhớ tới việc bị nghẹn lần trước, kh&ocirc;ng nhịn hỏi nhiều một c&acirc;u: &quot;Đồng hồ n&agrave;y kh&ocirc;ng phải l&agrave; anh thu&ecirc; chứ?&quot; C&ocirc; đ&atilde; nghe qua thu&ecirc; xe thu&ecirc; quần &aacute;o, nhưng chưa từng nghe qua thu&ecirc; đồng hồ.<br />\r\nTống An Thần lắc đầu, &quot;Kh&ocirc;ng phải thu&ecirc;, l&agrave; h&agrave;ng ch&iacute;nh h&atilde;ng tr&ecirc;n Taobao, ng&agrave;y 11-11 giảm gi&aacute; 999 đồng.&quot;<br />\r\n&quot;......&quot; Năm trước c&ocirc; cũng mua c&aacute;i đồng hồ 999 đồng nguy&ecirc;n seal, đồng hồ Patek Philippe n&agrave;y l&uacute;c giảm gi&aacute; cũng đ&atilde; hơn một vạn nh&acirc;n d&acirc;n tệ. Vậy đ&acirc;y r&otilde; r&agrave;ng l&agrave; đồng hồ fake, m&agrave; c&ograve;n kh&ocirc;ng bằng loại superfake!<br />\r\nSo&aacute;i ca, anh rốt cuộc l&agrave; c&oacute; bao nhi&ecirc;u hư vinh hả, người kh&ocirc;ng c&oacute; tiền th&igrave; mua h&agrave;ng ch&iacute;nh h&atilde;ng rẻ một ch&uacute;t cũng được m&agrave;, sao lại đi mua h&agrave;ng nh&aacute;i của h&atilde;ng nổi tiếng l&agrave;m g&igrave;?<br />\r\nTống An Thần nh&igrave;n Hạ Chi T&igrave;nh bị nghẹn chết khiếp, co r&uacute;t kh&oacute;e miệng, quay lại chủ đề n&oacute;i: &quot;Kh&ocirc;ng biết &yacute; Hạ tiểu thư như thế n&agrave;o? Nếu Hạ tiểu thư cảm thấy được, t&ocirc;i sẽ giảm gi&aacute; cho tiểu thư c&ograve;n 4000/năm ng&agrave;y.&quot;<br />\r\n&quot;C&oacute; thể c&oacute; thể, đương nhi&ecirc;n c&oacute; thể.&quot; Tr&ecirc;n b&agrave;i viết c&ocirc; đăng tiền thu&ecirc; l&agrave; 5000/năm ng&agrave;y, giờ anh ta lại tự nguyện giảm 1000, c&ocirc; tự nhi&ecirc;n l&agrave; vui mừng rồi. Hơn nữa trừ bỏ th&oacute;i giả vờ hư vinh của anh ta th&igrave; vẻ ngo&agrave;i ho&agrave;n mỹ n&agrave;y vượt xa tưởng tượng, &quot;C&ograve;n việc ph&iacute; sửa xe......&quot;<br />\r\n&quot;Về việc ph&iacute; sửa xe, nếu Hạ tiểu thư hiện tại kh&ocirc;ng thể trả được, vậy ghi c&aacute;i giấy nợ coi như mượn tiền t&ocirc;i, c&ocirc; cảm thấy như thế n&agrave;o?&quot;<br />\r\nNhư thế n&agrave;o? Đương nhi&ecirc;n l&agrave; kh&ocirc;ng được rồi! 50 vạn a, trừ đi khoản trả tiền nh&agrave; tiền xe ra, tiền lương c&ograve;n dư lại nếu như c&ocirc; kh&ocirc;ng ăn kh&ocirc;ng uống trong hai mươi năm may ra trả được.<br />\r\nC&ocirc; trưng ra khu&ocirc;n mặt đ&aacute;ng thương: &quot;Anh c&oacute; thể giảm gi&aacute; cho t&ocirc;i kh&ocirc;ng?&quot;<br />\r\nAnh tỏ vẻ ki&ecirc;n quyết v&agrave; lạnh l&ugrave;ng: &quot;Kh&ocirc;ng được, một ph&acirc;n cũng kh&ocirc;ng thể thiếu!&quot;<br />\r\nThấy chi&ecirc;u giả bộ đ&aacute;ng thương n&agrave;y cũng v&ocirc; dụng, c&ocirc; dứt kho&aacute;t v&ocirc; lại m&agrave; n&oacute;i: &quot;Tuy rằng sự cố lần n&agrave;y l&agrave; lỗi của t&ocirc;i, nhưng m&agrave; một b&agrave;n tay vỗ kh&ocirc;ng ra tiếng, Tống ti&ecirc;n sinh lẽ n&agrave;o kh&ocirc;ng biết xấu hổ m&agrave; cũng kh&ocirc;ng chịu một ch&uacute;t tr&aacute;ch nhiệm sao? 50 vạn với t&ocirc;i m&agrave; n&oacute;i qu&aacute; nhiều, t&ocirc;i chỉ c&oacute; thể trả một nửa, nhiều hơn th&igrave; kh&ocirc;ng c&oacute;. Hơn nữa t&ocirc;i phải nhắc nhở Tống ti&ecirc;n sinh anh một c&acirc;u, sự cố đụng xe ng&agrave;y đ&oacute; ch&uacute;ng ta kh&ocirc;ng b&aacute;o cảnh s&aacute;t, nếu l&agrave; muốn nh&aacute;o nhau ra t&ograve;a, đối với anh chưa chắc c&oacute; lợi.&quot;<br />\r\nKh&ocirc;ng phải c&ocirc; v&ocirc; lại, thật sự l&agrave; ức hiếp người ngh&egrave;o qu&aacute; đ&aacute;ng m&agrave;!<br />\r\n&quot;&Ocirc;, c&ocirc; thật sự nghĩ vậy sao?&quot; Tống An Thần từ trong t&uacute;i lấy ra một chiếc b&uacute;t quơ quơ trước mặt c&ocirc;, cười lạnh n&oacute;i: &quot;C&acirc;y b&uacute;t n&agrave;y đ&atilde; ghi lại to&agrave;n bộ cuộc tr&ograve; chuyện của ch&uacute;ng ta từ l&uacute;c gặp nhau đến giờ, bao gồm c&acirc;u n&oacute;i thừa nhận tr&aacute;ch nhiệm của c&ocirc; l&uacute;c n&atilde;y, kh&ocirc;ng biết Hạ tiểu thư cảm thấy n&oacute; kh&ocirc;ng c&oacute; lợi sao?&quot;<br />\r\n&quot;......&quot; C&ocirc; đ&atilde; từng nghĩ rằng Hạ Chi Sơ đ&atilde; l&agrave; người gian tr&aacute; xảo quyệt nhất tr&ecirc;n đời, nhưng b&acirc;y giờ c&ocirc; ph&aacute;t hiện m&igrave;nh sai rồi, Hạ Chi Sơ so với người đ&agrave;n &ocirc;ng trước mắt n&agrave;y quả thực l&agrave; ph&ugrave; thủy nhỏ gặp ph&ugrave; thủy lớn m&agrave;, người đ&agrave;n &ocirc;ng trước mặt n&agrave;y mới l&agrave; &ocirc;ng tổ ph&uacute;c hắc!<br />\r\nNh&igrave;n thấy khu&ocirc;n mặt như bị s&eacute;t đ&aacute;nh của Hạ Chi T&igrave;nh, kh&oacute;e miệng Tống An Thần nhếch l&ecirc;n một độ cong s&aacute;ng lạn.<br />\r\n&Aacute;nh nắng xuy&ecirc;n qua kẽ hở giữa những đ&aacute;m m&acirc;y chiếu xuống mặt đất lốm đốm, loang lổ, b&oacute;ng lo&aacute;ng những tia nắng, ngo&agrave;i cửa sổ c&agrave;nh l&aacute; xanh tươi, m&ugrave;a xu&acirc;n đến thật rồi.<br />\r\n4-【2】<br />\r\nMỗi lần đến thứ hai, sếp c&ocirc; giống như bơm m&aacute;u g&agrave;, mở họp, tổng kết, l&agrave;m kế hoạch, cả một ng&agrave;y n&agrave;y, Hạ Chi T&igrave;nh bận xoay tr&ograve;n như con quay, kh&oacute; khăn lắm mới được hết giờ l&agrave;m, c&ocirc; chộp vội t&uacute;i x&aacute;ch chạy như bay.<br />\r\nĐến nh&agrave; h&agrave;ng cũng kịp l&uacute;c L&acirc;m Du gọi lẩu t&ocirc;m chua cay c&ugrave;ng với c&aacute; hầm ớt đem l&ecirc;n, c&ocirc; vội v&agrave;ng ngồi xuống gia nhập chiến trường, hai người kh&ocirc;ng m&agrave;ng h&igrave;nh tượng, ăn v&ocirc; c&ugrave;ng vui vẻ, cay v&ocirc; c&ugrave;ng sảng kho&aacute;i.<br />\r\nSau một hồi gi&oacute; cuốn m&acirc;y t&agrave;n, L&acirc;m Du ợ một c&aacute;i, xỉa răng, mở miệng n&oacute;i: &quot;N&oacute;i đi, ng&agrave;y h&ocirc;m qua rốt cuộc đ&atilde; xảy ra chuyện g&igrave;?&quot;<br />\r\nC&ocirc; vốn đang v&eacute;t đĩa dưa chua c&ograve;n dư lại, nghe thấy L&acirc;m Du hỏi bỗng nhi&ecirc;n mất hết hứng ăn. C&ocirc; lấy khăn giấy lau đi sa tế d&iacute;nh ở kh&oacute;e m&ocirc;i, đem chuyện xảy ra h&ocirc;m qua một năm một mười m&agrave; kể cho L&acirc;m Du nghe.<br />\r\nL&acirc;m Du vứt bỏ tăm xỉa răng, vẻ mặt như xem kẻ ngốc, &quot;Cho n&ecirc;n n&oacute;i, h&ocirc;m qua cậu kh&ocirc;ng những k&yacute; giấy nợ bồi thường 50 vạn m&agrave; c&ograve;n đem chứng minh thư cho người ta?&quot;<br />\r\nHạ Chi T&igrave;nh yếu ớt bảo vệ t&ocirc;n nghi&ecirc;m ch&iacute;nh m&igrave;nh: &quot;Kh&ocirc;ng phải tớ ngốc, m&agrave; l&agrave; kẻ địch qu&aacute; xảo quyệt, cậu kh&ocirc;ng biết anh ta xảo quyệt thế n&agrave;o đ&acirc;u, l&uacute;c bắt đầu đ&atilde; chuẩn bị sẵn b&uacute;t ghi &acirc;m!&quot;<br />\r\nL&acirc;m Du khịt mũi coi thường lời phản b&aacute;c của c&ocirc;, &quot;Cậu đương nhi&ecirc;n kh&ocirc;ng ngốc, m&agrave; l&agrave; ngu! Xui xẻo kh&ocirc;ng thể tr&aacute;ch x&atilde; hội, mệnh khổ kh&ocirc;ng thể tr&aacute;ch ch&iacute;nh phủ, ch&iacute;nh cậu ngu như vậy c&ograve;n c&oacute; mặt mũi đi tr&aacute;ch người kh&aacute;c th&ocirc;ng minh? Đến học sinh tiểu học c&ograve;n biết chứng minh thư kh&ocirc;ng thể đưa cho người lạ, Hạ Chi T&igrave;nh cậu ra b&ecirc;n ngo&agrave;i kh&ocirc;ng cần n&oacute;i l&agrave; bạn của tớ.&quot;<br />\r\nC&ocirc; rớt nước mắt, &quot;Kh&ocirc;ng phải tớ bị &eacute;p buộc sao! Anh ta n&oacute;i sợ tớ bỏ trốn, nếu tớ kh&ocirc;ng đưa chứng minh thư cho anh ta, anh ta sẽ đi b&aacute;o cảnh s&aacute;t sau đ&oacute; c&ugrave;ng nhau ra t&ograve;a.&quot;<br />\r\nL&acirc;m Du vẻ mặt r&egrave;n sắt kh&ocirc;ng th&agrave;nh th&eacute;p, &quot;Nếu sau khi đụng xe cậu liền b&aacute;o ngay cho cảnh s&aacute;t để họ xử l&yacute; th&igrave; đ&acirc;u c&oacute; cục diện như ng&agrave;y h&ocirc;m nay!&quot;<br />\r\nC&ocirc; mới vừa biết l&aacute;i xe chưa đến một th&aacute;ng, l&uacute;c ấy va chạm xe cả người liền hoảng l&ecirc;n, l&agrave;m g&igrave; c&ograve;n nghĩ nhiều đến như vậy.<br />\r\nC&ocirc; duỗi tay qua b&agrave;n nắm lấy tay của L&acirc;m Du, &quot;B&acirc;y giờ tớ n&ecirc;n l&agrave;m g&igrave;?&quot;<br />\r\nL&acirc;m Du gh&eacute;t bỏ m&agrave; r&uacute;t tay về, &quot;Thật t&igrave;nh! Chẳng lẽ anh cậu thấy chết kh&ocirc;ng cứu?&quot;<br />\r\nNhắc đến Hạ Chi Sơ, c&ocirc; liền giận s&ocirc;i m&aacute;u, &quot;Đừng nhắc đến c&aacute;i t&ecirc;n khỉ gi&oacute; ấy! Hắn chặn hết c&aacute;c li&ecirc;n lạc của tớ rồi, nghe bố tớ n&oacute;i hắn c&ograve;n đang nghỉ ph&eacute;p ở ch&acirc;u &Acirc;u k&igrave;a.&quot;<br />\r\nNgười phục vụ tiến đến dọn b&agrave;n đồ ăn đ&atilde; hết sạch đi, L&acirc;m Du vẻ mặt xin lỗi n&oacute;i: &quot;Xin lỗi nha tớ cũng kh&ocirc;ng gi&uacute;p cậu được.&quot;<br />\r\n&quot;Chuyện của tớ cậu kh&ocirc;ng cần lo lắng.&quot; C&ocirc; xua tay n&oacute;i, sau đ&oacute; r&oacute;t nước v&agrave;o hai c&aacute;i ly, &quot;Thật ra cậu, anh ta...... vẫn kh&ocirc;ng muốn gặp cậu hả?&quot;<br />\r\nL&acirc;m Du cười khổ một tiếng, nhẹ nh&agrave;ng m&agrave; lắc đầu, &quot;Ba năm qua đến d&aacute;ng vẻ anh ấy tr&ocirc;ng như thế n&agrave;o tớ đều qu&ecirc;n rồi.&quot;<br />\r\nNh&agrave; h&agrave;ng tiếng người ồn &agrave;o, Hạ Chi T&igrave;nh nh&igrave;n sườn mặt L&acirc;m Du, kh&ocirc;ng biết l&agrave;m g&igrave; để an ủi.<br />\r\nNăm L&acirc;m Du tốt nghiệp đại học, một m&igrave;nh chạy đến T&acirc;y Tạng du lịch, tr&ecirc;n đường gặp phải đ&aacute;m du c&ocirc;n với &yacute; đồ quấy rối, một nam sinh đi ngang qua đ&atilde; cứu c&ocirc; ấy, trong l&uacute;c vật lộn với đ&aacute;m kẻ xấu đ&atilde; bị đ&acirc;m tr&uacute;ng xương tủy ở thắt lưng dẫn tới th&acirc;n dưới bị liệt.<br />\r\nNam sinh t&ecirc;n Thẩm Ch&iacute; Viễn, đều giống nhau v&igrave; ch&uacute;c mừng tốt nghiệp m&agrave; chạy tới T&acirc;y Tạng du lịch. Thẩm Ch&iacute; Viễn l&agrave; con trai độc nhất trong nh&agrave;, v&igrave; chữa trị cho anh ta m&agrave; hai nh&agrave; đ&atilde; dốc hết sức lực nhưng vẫn như cũ kh&ocirc;ng c&aacute;ch n&agrave;o l&agrave;m anh ta một lần nữa đứng l&ecirc;n. L&acirc;m Du tự tr&aacute;ch v&ocirc; c&ugrave;ng cho n&ecirc;n chủ động g&aacute;nh v&aacute;c tr&aacute;ch nhiệm chiếu cố Thẩm Ch&iacute; Viễn, trong qu&aacute; tr&igrave;nh chăm s&oacute;c, hai người l&acirc;u ng&agrave;y sinh t&igrave;nh.<br />\r\nL&acirc;m Du b&agrave;y tỏ thẳng thắng với cha mẹ về t&igrave;nh y&ecirc;u n&agrave;y, lại kh&ocirc;ng nghĩ rằng bị họ kịch liệt phản đối, bọn họ cho rằng L&acirc;m Du v&igrave; &aacute;y n&aacute;y m&agrave; muốn d&ugrave;ng nửa đời c&ograve;n lại bồi thường đối phương, L&acirc;m Du theo ch&acirc;n bọn họ giải th&iacute;ch ch&iacute;nh m&igrave;nh kh&ocirc;ng phải bởi v&igrave; &aacute;y n&aacute;y mới y&ecirc;u Thẩm Ch&iacute; Viễn, nhưng bố L&acirc;m mẹ L&acirc;m n&oacute;i g&igrave; cũng kh&ocirc;ng đồng &yacute;.<br />\r\nV&igrave; phản đối họ ở b&ecirc;n nhau, bọn họ đ&atilde; chạy đến Thẩm gia n&aacute;o loạn, kh&ocirc;ng biết bọn họ c&ugrave;ng Thẩm Ch&iacute; Viễn n&oacute;i g&igrave; đ&oacute;, sau lần đ&oacute;, Thẩm Ch&iacute; Viễn đơn phương quyết định chia tay, hơn nữa v&ocirc; luận c&ocirc; ấy cầu xin như thế n&agrave;o, anh ta đều kh&ocirc;ng muốn gặp c&ocirc;.<br />\r\nTuy rằng kh&ocirc;ng thể gặp mặt, L&acirc;m Du vẫn l&agrave; dứt kho&aacute;t quyết định ở lại th&agrave;nh phố của anh ta, hơn nữa mỗi th&aacute;ng đ&uacute;ng hạn đem một nửa tiền lương gửi tới t&agrave;i khoản Thẩm Ch&iacute; Viễn, kỳ thật gia cảnh Thẩm gia kh&ocirc;ng tồi, căn bản kh&ocirc;ng thiếu ch&uacute;t tiền ấy của L&acirc;m Du, hơn nữa l&uacute;c trước L&acirc;m gia đều dứt kho&aacute;t bồi thường một lần lu&ocirc;n cho Thẩm gia, nhưng L&acirc;m Du vẫn đều ki&ecirc;n tr&igrave; mỗi th&aacute;ng l&agrave;m như vậy.<br />\r\nL&acirc;m Du n&oacute;i, &quot;Tớ biết nhiều người bảo tớ khờ, nhưng đ&acirc;y l&agrave; c&aacute;ch duy nhất gi&uacute;p tớ giữ li&ecirc;n lạc với anh ấy.&quot;<br />\r\nC&ocirc; hỏi, &quot;Ki&ecirc;n tr&igrave; trong v&ocirc; vọng như vậy, cậu muốn ki&ecirc;n tr&igrave; đến khi n&agrave;o?&quot;<br />\r\nL&acirc;m Du lắc đầu, &quot;Kh&ocirc;ng biết, chỉ cần t&agrave;i khoản kia vẫn c&ograve;n một ng&agrave;y, tớ vẫn sẽ ki&ecirc;n tr&igrave;.&quot;<br />\r\nTrong nh&agrave; h&agrave;ng l&uacute;c n&agrave;y vang l&ecirc;n b&agrave;i h&aacute;t &quot;Chuyện của mai sau&quot; của Phạm Vĩ Kỳ v&agrave; Trương Thiều H&agrave;m:<br />\r\n......<br />\r\nVẫn lu&ocirc;n ki&ecirc;n tr&igrave; đối với t&igrave;nh y&ecirc;u của ch&iacute;nh m&igrave;nh<br />\r\nNhư gi&oacute; kh&ocirc;ng do g&igrave; m&agrave; thổi<br />\r\nAi y&ecirc;u ai kh&ocirc;ng c&oacute; c&aacute;i gọi l&agrave; đ&uacute;ng hay sai<br />\r\nMặc kệ thời gian ch&uacute;ng ta ở b&ecirc;n nhau c&oacute; bao nhi&ecirc;u nhấp nh&ocirc;<br />\r\nTa kh&ocirc;ng d&aacute;m đi chứng thực hai chữ y&ecirc;u em<br />\r\nKh&ocirc;ng phải ch&iacute;nh m&igrave;nh rụt r&egrave; cũng kh&ocirc;ng phải chế giễu<br />\r\nNgười kh&aacute;c đều đang n&oacute;i ta kỳ thật rất v&ocirc; tri<br />\r\nT&igrave;nh cảm như vậy lại bị nhận định l&agrave;m c&agrave;n<br />\r\nTa rất kh&ocirc;ng phục<br />\r\n......<br />\r\n*B&agrave;i nay hay lắm mn nghe thử&nbsp;<br />\r\nHạ Chi T&igrave;nh đem tầm mắt từ tr&ecirc;n mặt L&acirc;m Du nh&igrave;n sang ngo&agrave;i cửa sổ, c&ocirc; từ trước đến nay cho rằng, t&igrave;nh y&ecirc;u như người uống nước, ấm lạnh tự biết, hạnh ph&uacute;c hay kh&ocirc;ng, c&oacute; đ&aacute;ng gi&aacute; hay kh&ocirc;ng, chỉ c&oacute; người trong cuộc mới c&oacute; tư c&aacute;ch đi b&igrave;nh luận, cho n&ecirc;n chuyện của L&acirc;m Du v&agrave; Thẩm Ch&iacute; Viễn, c&ocirc; kh&ocirc;ng c&oacute; &yacute; kiến, c&ocirc; chỉ bầu bạn b&ecirc;n cạnh m&agrave; th&ocirc;i.&nbsp;</p>', '0', 'ong-to-phuc-hac', 4, '656', '2022-07-31 08:25:55', '2022-07-31 08:25:55'),
(16, '4', 'Nước đâu ?', '<p>&yacute; Ẩn c&ograve;n nhớ r&otilde;, ra đến ph&aacute;t trước, Hạ Uy&ecirc;n đặc biệt cho hắn một lời khuy&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Lần thứ tư chỉ thị chữ bằng m&aacute;u bắt đầu, nguy hiểm, thường thường ở v&agrave;o trong l&uacute;c v&ocirc; h&igrave;nh. Muốn s&ocirc;́ng sót, liền muốn quan s&aacute;t, cẩn thận, t&igrave;m kiếm ra một loại n&agrave;o đ&oacute; quy luật, d&ugrave; cho lại nhỏ b&eacute; mất tự nhi&ecirc;n cũng kh&ocirc;ng thể bu&ocirc;ng tha. Đ&atilde; rất l&acirc;u kh&ocirc;ng c&oacute; c&oacute; thể sống qua ba lần chỉ thị chữ bằng m&aacute;u người, cho n&ecirc;n ta hi vọng ngươi c&oacute; thể sống s&oacute;t.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; hắn đặc biệt nhấn mạnh m&ocirc;̣t đi&ecirc;̉m là &mdash;&mdash;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ng&agrave;n vạn kh&ocirc;ng thể tin tưởng bất luận kẻ n&agrave;o lời n&oacute;i, thậm ch&iacute;, ngay cả m&igrave;nh đều kh&ocirc;ng thể ho&agrave;n to&agrave;n tin tưởng. Bởi v&igrave;, con mắt của ngươi sẽ lừa gạt ngươi, c&oacute; lẽ ngươi r&otilde; r&agrave;ng đi tại v&aacute;ch n&uacute;i cheo leo b&ecirc;n tr&ecirc;n, nhưng ngươi lại cảm gi&aacute;c ch&iacute;nh m&igrave;nh tr&ecirc;n thực tế tại bằng phẳng mặt đất.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn nhớ kỹ những lời n&agrave;y, một khắc, cũng kh&ocirc;ng d&aacute;m bu&ocirc;ng lỏng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; th&ocirc;n trưởng lần n&agrave;y mời, th&igrave; l&agrave; một cực lớn &quot;Mất tự nhi&ecirc;n&quot; .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ta l&agrave; bổn th&ocirc;n th&ocirc;n trưởng, họ Trương.&quot; Kia l&atilde;o th&ocirc;n trưởng bị c&ocirc; g&aacute;i trẻ kia đỡ lấy, mang L&yacute; Ẩn bốn người bọn họ, hướng ch&iacute;nh hắn ph&ograve;ng đi đến.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Trương th&ocirc;n trưởng, &quot; Diệp Khả H&acirc;n trước một bước đưa ra nghi vấn: &quot;V&igrave; sao. . . Ngươi nguyện &yacute; để ch&uacute;ng ta ở lại?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ha ha, &quot; th&ocirc;n trưởng kia lại l&agrave; cười thần b&iacute;, n&oacute;i: &quot;Ta c&ugrave;ng những c&aacute;i kia chưa khai h&oacute;a l&atilde;o cổ đổng kh&ocirc;ng giống. U Thủy th&ocirc;n kh&ocirc;ng thể vĩnh viễn đều tại trong n&uacute;i lớn n&agrave;y bảo thủ ph&aacute;t triển. Chớ khẩn trương. C&aacute;c ngươi muốn ở bao l&acirc;u cũng được đi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đỡ lấy hắn c&ocirc; g&aacute;i trẻ kia vội n&oacute;i: &quot;Gia gia, c&aacute;i n&agrave;y. . . Thật được kh&ocirc;ng? Ng&agrave;y mai, lại vừa vặn, l&agrave; Băng Nhi ng&agrave;y giỗ a, ở thời đi&ecirc;̉m này. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Băng Nhi ng&agrave;y giỗ?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn t&acirc;m một lộp bộp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;y mai. . . Ch&iacute;nh l&agrave; căn cứ chung cư chỉ thị chữ bằng m&aacute;u, ch&iacute;nh thức tại U Thủy th&ocirc;n bắt đầu cư trụ ng&agrave;y. M&agrave; kia Băng Nhi, ch&iacute;nh l&agrave; vừa rồi nhắc tới &quot;L&yacute; Băng&quot; sao?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lại vừa l&uacute;c l&agrave; ng&agrave;y giỗ. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nếu như n&oacute;i l&agrave; tr&ugrave;ng hợp, đ&aacute;nh chết hắn hắn cũng kh&ocirc;ng tin.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&ocirc;n trưởng ph&ograve;ng ở cũng t&iacute;nh l&agrave; kh&iacute; ph&aacute;i, lại c&oacute; ch&uacute;t &iacute;t nh&agrave; kiểu t&acirc;y hương vị, c&oacute; ba tầng lầu cao, ngoại l&acirc;u mặt đều l&agrave; xi măng cốt th&eacute;p, kh&ocirc;ng giống với c&aacute;i kh&aacute;c ph&ograve;ng ốc đa số l&agrave; b&igrave;nh thường xi măng ph&ograve;ng ốc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>D&ugrave; sao cũng kh&ocirc;ng phải kh&aacute;ch kh&iacute; thời điểm, L&yacute; Ẩn bọn họ cũng liền tiến v&agrave;o th&ocirc;n trưởng gia.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đương nhi&ecirc;n, th&ocirc;n trưởng cũng rất cẩn thận, mang bọn họ đi gian ph&ograve;ng của m&igrave;nh, sau khi ngồi xuống, khiến hắn c&aacute;i kia t&ocirc;n nữ rời đi trước, đ&oacute;ng kỹ cửa lại, tiếp theo, hỏi: &quot;Như vậy, c&oacute; thể n&oacute;i cho ta t&ecirc;n của c&aacute;c ngươi sao?&quot;</p>', '<p>L&yacute; Ẩn c&ograve;n nhớ r&otilde;, ra đến ph&aacute;t trước, Hạ Uy&ecirc;n đặc biệt cho hắn một lời khuy&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Lần thứ tư chỉ thị chữ bằng m&aacute;u bắt đầu, nguy hiểm, thường thường ở v&agrave;o trong l&uacute;c v&ocirc; h&igrave;nh. Muốn s&ocirc;́ng sót, liền muốn quan s&aacute;t, cẩn thận, t&igrave;m kiếm ra một loại n&agrave;o đ&oacute; quy luật, d&ugrave; cho lại nhỏ b&eacute; mất tự nhi&ecirc;n cũng kh&ocirc;ng thể bu&ocirc;ng tha. Đ&atilde; rất l&acirc;u kh&ocirc;ng c&oacute; c&oacute; thể sống qua ba lần chỉ thị chữ bằng m&aacute;u người, cho n&ecirc;n ta hi vọng ngươi c&oacute; thể sống s&oacute;t.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; hắn đặc biệt nhấn mạnh m&ocirc;̣t đi&ecirc;̉m là &mdash;&mdash;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ng&agrave;n vạn kh&ocirc;ng thể tin tưởng bất luận kẻ n&agrave;o lời n&oacute;i, thậm ch&iacute;, ngay cả m&igrave;nh đều kh&ocirc;ng thể ho&agrave;n to&agrave;n tin tưởng. Bởi v&igrave;, con mắt của ngươi sẽ lừa gạt ngươi, c&oacute; lẽ ngươi r&otilde; r&agrave;ng đi tại v&aacute;ch n&uacute;i cheo leo b&ecirc;n tr&ecirc;n, nhưng ngươi lại cảm gi&aacute;c ch&iacute;nh m&igrave;nh tr&ecirc;n thực tế tại bằng phẳng mặt đất.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn nhớ kỹ những lời n&agrave;y, một khắc, cũng kh&ocirc;ng d&aacute;m bu&ocirc;ng lỏng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; th&ocirc;n trưởng lần n&agrave;y mời, th&igrave; l&agrave; một cực lớn &quot;Mất tự nhi&ecirc;n&quot; .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ta l&agrave; bổn th&ocirc;n th&ocirc;n trưởng, họ Trương.&quot; Kia l&atilde;o th&ocirc;n trưởng bị c&ocirc; g&aacute;i trẻ kia đỡ lấy, mang L&yacute; Ẩn bốn người bọn họ, hướng ch&iacute;nh hắn ph&ograve;ng đi đến.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Trương th&ocirc;n trưởng, &quot; Diệp Khả H&acirc;n trước một bước đưa ra nghi vấn: &quot;V&igrave; sao. . . Ngươi nguyện &yacute; để ch&uacute;ng ta ở lại?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ha ha, &quot; th&ocirc;n trưởng kia lại l&agrave; cười thần b&iacute;, n&oacute;i: &quot;Ta c&ugrave;ng những c&aacute;i kia chưa khai h&oacute;a l&atilde;o cổ đổng kh&ocirc;ng giống. U Thủy th&ocirc;n kh&ocirc;ng thể vĩnh viễn đều tại trong n&uacute;i lớn n&agrave;y bảo thủ ph&aacute;t triển. Chớ khẩn trương. C&aacute;c ngươi muốn ở bao l&acirc;u cũng được đi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đỡ lấy hắn c&ocirc; g&aacute;i trẻ kia vội n&oacute;i: &quot;Gia gia, c&aacute;i n&agrave;y. . . Thật được kh&ocirc;ng? Ng&agrave;y mai, lại vừa vặn, l&agrave; Băng Nhi ng&agrave;y giỗ a, ở thời đi&ecirc;̉m này. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Băng Nhi ng&agrave;y giỗ?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn t&acirc;m một lộp bộp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;y mai. . . Ch&iacute;nh l&agrave; căn cứ chung cư chỉ thị chữ bằng m&aacute;u, ch&iacute;nh thức tại U Thủy th&ocirc;n bắt đầu cư trụ ng&agrave;y. M&agrave; kia Băng Nhi, ch&iacute;nh l&agrave; vừa rồi nhắc tới &quot;L&yacute; Băng&quot; sao?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lại vừa l&uacute;c l&agrave; ng&agrave;y giỗ. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nếu như n&oacute;i l&agrave; tr&ugrave;ng hợp, đ&aacute;nh chết hắn hắn cũng kh&ocirc;ng tin.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&ocirc;n trưởng ph&ograve;ng ở cũng t&iacute;nh l&agrave; kh&iacute; ph&aacute;i, lại c&oacute; ch&uacute;t &iacute;t nh&agrave; kiểu t&acirc;y hương vị, c&oacute; ba tầng lầu cao, ngoại l&acirc;u mặt đều l&agrave; xi măng cốt th&eacute;p, kh&ocirc;ng giống với c&aacute;i kh&aacute;c ph&ograve;ng ốc đa số l&agrave; b&igrave;nh thường xi măng ph&ograve;ng ốc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>D&ugrave; sao cũng kh&ocirc;ng phải kh&aacute;ch kh&iacute; thời điểm, L&yacute; Ẩn bọn họ cũng liền tiến v&agrave;o th&ocirc;n trưởng gia.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đương nhi&ecirc;n, th&ocirc;n trưởng cũng rất cẩn thận, mang bọn họ đi gian ph&ograve;ng của m&igrave;nh, sau khi ngồi xuống, khiến hắn c&aacute;i kia t&ocirc;n nữ rời đi trước, đ&oacute;ng kỹ cửa lại, tiếp theo, hỏi: &quot;Như vậy, c&oacute; thể n&oacute;i cho ta t&ecirc;n của c&aacute;c ngươi sao?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>D&ugrave; sao cũng l&agrave; người lai lịch kh&ocirc;ng r&otilde;, th&ocirc;n trưởng cũng muốn l&agrave;m l&agrave;m r&otilde; r&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn hồi đ&aacute;p: &quot;Ta gọi L&yacute; Ẩn, vị n&agrave;y l&agrave; Tần Thủ Thi&ecirc;n, La Hằng Vi&ecirc;m, Diệp Khả H&acirc;n. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngay l&uacute;c n&agrave;y, bỗng nhi&ecirc;n cửa mở, đi tới một t&oacute;c h&uacute;i cua người trẻ tuổi, lạnh l&ugrave;ng qu&eacute;t mắt L&yacute; Ẩn bọn họ, đối th&ocirc;n trưởng n&oacute;i: &quot;Gia gia. . . Quả nhi&ecirc;n, ta nghe người trong th&ocirc;n n&oacute;i, ngươi mang đ&aacute;m người kh&ocirc;ng r&otilde; lai lịch muốn v&agrave;o ở đến, l&agrave; thật?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A Vũ! L&agrave;m sao như vậy kh&ocirc;ng biết lễ ph&eacute;p!&quot; Trương th&ocirc;n trưởng lập tức cả giận n&oacute;i: &quot;Ta mời người n&agrave;o đến, c&ograve;n muốn ngươi ph&ecirc; chuẩn kh&ocirc;ng th&agrave;nh!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gọi l&agrave; A Vũ người trẻ tuổi lại kh&ocirc;ng nhượng bộ ch&uacute;t n&agrave;o, n&oacute;i: &quot;Gia gia ngươi nghĩ mời người n&agrave;o l&agrave; ngươi tự do, nhưng ta nhưng nh&igrave;n kh&ocirc;ng quen đ&aacute;m n&agrave;y kh&ocirc;ng đứng đắn người chạy trong l&agrave;ng đến!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lập tức, t&iacute;nh t&igrave;nh tương đối gấp La Hằng Vi&ecirc;m tức giận trong l&ograve;ng, lập tức đứng l&ecirc;n, n&oacute;i: &quot;Ngươi, ngươi n&oacute;i ai kh&ocirc;ng đứng đắn!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn cũng lập tức đứng l&ecirc;n, thấp giọng n&oacute;i: &quot;Ngươi c&oacute; lầm hay kh&ocirc;ng! Hiện tại l&agrave; so đo loại chuyện như vậy thời điểm sao?&quot; Sau đ&oacute; bồi khu&ocirc;n mặt tươi cười n&oacute;i: &quot;Vị ti&ecirc;n sinh n&agrave;y, ch&uacute;ng ta l&agrave; tại thể nghiệm n&ocirc;ng gia phong t&igrave;nh, tuyệt đối kh&ocirc;ng c&oacute; &aacute;c &yacute;, ở lại một th&aacute;ng, ch&uacute;ng ta liền sẽ rời khỏi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Một th&aacute;ng?&quot; Kia gọi A Vũ người trẻ tuổi giận t&iacute;m mặt, n&oacute;i: &quot;C&ograve;n muốn ở thời gian d&agrave;i như vậy? C&uacute;t cho ta! N&oacute;i cho ngươi, c&aacute;i g&igrave; nh&aacute;o quỷ, đều l&agrave; chột dạ người n&oacute;i, kia L&yacute; Băng l&agrave; tự s&aacute;t, cục c&ocirc;ng an đều kết &aacute;n! C&ograve;n c&oacute; người mượn quỷ thần m&agrave; n&oacute;i, tin đồn thất thiệt!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tự s&aacute;t?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&acirc;y ngược lại l&agrave; khiến L&yacute; Ẩn c&oacute; ch&uacute;t ngo&agrave;i &yacute; muốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;, n&agrave;y như cũ rất khiến người ta cảm thấy khẩn trương. Chuyện ma ai cũng nh&igrave;n qua, tự s&aacute;t người biến th&agrave;nh quỷ, cũng l&agrave; rất thường gặp s&aacute;o lộ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A Vũ trợn mắt nh&igrave;n nhau, lại khiến L&yacute; Ẩn c&agrave;ng th&ecirc;m x&aacute;c định. . .&quot;Nh&aacute;o quỷ&quot;, kh&ocirc;ng phải c&aacute;i g&igrave; tin đồn thất thiệt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Tốt, A Vũ!&quot; Trương th&ocirc;n trưởng đem gậy chống nặng nề m&agrave; g&otilde; một ch&uacute;t mặt đất, n&oacute;i: &quot;Ngươi cho ta trở về ph&ograve;ng đi! L&yacute; ti&ecirc;n sinh bọn họ ch&iacute;nh l&agrave; của ta kh&aacute;ch nh&acirc;n, ngươi c&oacute; thể n&agrave;o đối kh&aacute;ch nh&acirc;n v&ocirc; lễ!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Hừ!&quot; A Vũ nhưng căn bản kh&ocirc;ng rảnh để &yacute;, n&oacute;i: &quot;Gia gia, ngươi thật đ&uacute;ng l&agrave; khiến bọn họ ở một th&aacute;ng? Ngươi biết lai lịch của bọn hắn?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trương th&ocirc;n trưởng vung vẩy l&ecirc;n gậy chống, liền hướng kia A Vũ đ&aacute;nh tới! Người sau vội v&agrave;ng kh&ocirc;ng kịp chuẩn bị, bả vai nặng nề m&agrave; g&otilde; một c&aacute;i!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Cho ta trở về ph&ograve;ng đi!&quot; Trương th&ocirc;n trưởng một b&ecirc;n nổi giận, bỗng nhi&ecirc;n, lại bắt đầu ho khan.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn rất l&agrave; kh&ocirc;ng hiểu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&igrave; mấy người bọn họ người kh&ocirc;ng quen biết, đ&aacute;nh ch&aacute;u của m&igrave;nh? Người trưởng th&ocirc;n n&agrave;y rốt cuộc t&iacute;nh to&aacute;n g&igrave;?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Mất tự nhi&ecirc;n&quot; tr&igrave;nh độ, c&agrave;ng ng&agrave;y c&agrave;ng m&atilde;nh liệt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia A Vũ th&igrave; l&agrave; đối L&yacute; Ẩn bọn họ trợn mắt nh&igrave;n, n&oacute;i: &quot;Xem c&aacute;c ngươi đắc &yacute; tới khi n&agrave;o!&quot; Tiếp theo, liền vọt ra khỏi ph&ograve;ng, kết quả, c&ugrave;ng một trung ni&ecirc;n nữ tử đụng đầy c&otilde;i l&ograve;ng!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A Vũ! Ngươi l&agrave;m c&aacute;i g&igrave; vậy a!&quot; Kia trung ni&ecirc;n nữ tử che lấy c&aacute;i tr&aacute;n, n&oacute;i: &quot;Như vậy va đụng lung tung!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Mẹ, ch&iacute;nh ngươi đến hỏi gia gia đi!&quot; Sau đ&oacute;, A Vũ liền liền x&ocirc;ng ra ngo&agrave;i, kia trung ni&ecirc;n nữ nh&acirc;n cũng lập tức đuổi theo.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Khiến c&aacute;c vị ch&ecirc; cười, &quot; Trương th&ocirc;n trưởng lập tức đ&oacute;ng cửa lại, n&oacute;i: &quot;C&aacute;c vị c&aacute;i g&igrave; cũng đừng để &yacute;, cứ việc ở lại ch&iacute;nh l&agrave;, ở lại ch&iacute;nh l&agrave;.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn cảm gi&aacute;c được, tựa hồ Trương th&ocirc;n trưởng mời bọn họ đến, c&oacute; dụng &yacute; g&igrave; tồn tại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave; c&aacute;i g&igrave; đ&acirc;y?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trương th&ocirc;n trưởng trong nh&agrave;, hết thảy ở bốn người, ch&iacute;nh l&agrave; hắn, nữ nhi Trương Anh Lan, cũng ch&iacute;nh l&agrave; vừa rồi kia trung ni&ecirc;n nữ nh&acirc;n, c&ugrave;ng với ch&aacute;u trai Trương Hồng Vũ, t&ocirc;n nữ Trương Tố Nguyệt, cũng ch&iacute;nh l&agrave; kia c&ocirc; g&aacute;i trẻ tuổi. M&agrave; th&ocirc;n trưởng con rể, th&igrave; tại ba năm trước đ&acirc;y qua đời.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lấy A Vũ bầu kh&ocirc;ng kh&iacute;, L&yacute; Ẩn cho rằng, mọi người c&ugrave;ng nhau d&ugrave;ng cơm thật sự l&agrave; kh&ocirc;ng th&iacute;ch hợp, cho n&ecirc;n đề nghị, cơm tối bọn họ liền thi&ecirc;n vị giải quyết, kh&ocirc;ng cần l&agrave;m phiền th&ocirc;n trưởng. D&ugrave; sao nhằm đề ph&ograve;ng vạn nhất c&oacute; chuyện g&igrave; xảy ra, cũng mang đại lượng đồ ăn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khiến L&yacute; Ẩn ngo&agrave;i &yacute; muốn ch&iacute;nh l&agrave;. . . Th&ocirc;n trưởng thật kh&ocirc;ng c&oacute; ki&ecirc;n tr&igrave;, nghe hắn n&oacute;i như vậy, cũng liền đồng &yacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&ecirc;m đ&atilde; khuya.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nước chảy xiết theo th&aacute;c nước tr&uacute;t xuống, A T&uacute; th&igrave; nước chảy, hướng ph&iacute;a th&aacute;c nước đi đến.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Băng Nhi tỷ tỷ. . .&quot; N&agrave;ng giờ ph&uacute;t n&agrave;y, mặt mũi tr&agrave;n đầy đều l&agrave; nước mắt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; c&ugrave;ng một thời gian, tại nh&agrave; trưởng th&ocirc;n phụ cận, liền ở kia buổi s&aacute;ng kh&oacute; xử L&yacute; Ẩn bọn họ trung ni&ecirc;n nam nh&acirc;n. Hắn gọi Di&ecirc;m Hạo Thi&ecirc;n, l&agrave; trong l&agrave;ng một người rất h&agrave;o sảng, cũng tương đối c&oacute; nh&acirc;n vọng. Trước mắt, vẫn l&agrave; độc th&acirc;n một người sống một m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Th&ocirc;n trưởng đang suy nghĩ g&igrave; đấy. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c n&agrave;y, đ&atilde; sắp đến nửa đ&ecirc;m 0 giờ, nhưng l&agrave;, Di&ecirc;m Hạo Thi&ecirc;n như cũ kh&oacute; m&agrave; ch&igrave;m v&agrave;o giấc ngủ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo l&yacute; thuyết cũng sắp m&ugrave;a hạ, nhưng l&agrave; hắn lại cảm gi&aacute;c to&agrave;n th&acirc;n r&eacute;t run, cho n&ecirc;n nhiều đắp một c&aacute;i chăn. Nhưng cho d&ugrave; như thế, vẫn l&agrave; cảm gi&aacute;c co r&uacute;m lại ph&aacute;t run.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bỗng nhi&ecirc;n, hắn cảm gi&aacute;c trong chăn, ẩm ướt. Ban sơ c&ograve;n kh&ocirc;ng c&oacute; qu&aacute; để &yacute;, nhưng đến sau n&agrave;y, cảm gi&aacute;c to&agrave;n bộ chăn mền phảng phất bị ng&acirc;m ở trong nước!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Di&ecirc;m Hạo Thi&ecirc;n lập tức ngồi dậy v&eacute;n chăn l&ecirc;n, cả c&aacute;i giường kh&ocirc;ng biết khi n&agrave;o, ho&agrave;n to&agrave;n bị nước thấm ướt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn lập tức ngồi dậy, đương hai ch&acirc;n chạm đến s&agrave;n nh&agrave; thời điểm. . . Cũng l&agrave; ng&acirc;m ở trong nước!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Sao. . . Chuyện g&igrave; xảy ra?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn bắt đầu kinh nghi bất định l&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gian ph&ograve;ng rất tối, bởi v&igrave; kh&ocirc;ng c&oacute; đ&egrave;n điện, hắn căn bản v&ocirc; ph&aacute;p ph&acirc;n r&otilde; phương hướng, chỉ c&oacute; thể lục lọi tiến l&ecirc;n. Bởi v&igrave; thời gian d&agrave;i sinh hoạt ở nơi n&agrave;y, đ&atilde; rất quen thuộc, cho n&ecirc;n cũng kh&ocirc;ng c&oacute; đụng v&agrave;o gia cụ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&igrave;m t&ograve;i đến ngăn k&eacute;o, ph&aacute;t hiện ph&iacute;a tr&ecirc;n cũng đều l&agrave; nước. K&eacute;o ra ngăn k&eacute;o, lấy ra đ&egrave;n dầu hỏa c&ugrave;ng một hộp di&ecirc;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sau khi đốt đ&egrave;n, hắn hơi tho&aacute;ng an t&acirc;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Giờ ph&uacute;t n&agrave;y, đ&atilde; qua nửa đ&ecirc;m kh&ocirc;ng giờ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thời gian, tiến v&agrave;o ng&agrave;y 7 th&aacute;ng 6.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Di&ecirc;m Hạo Thi&ecirc;n đi ra ph&ograve;ng ngủ, đi tới b&ecirc;n ngo&agrave;i xem x&eacute;t, ph&aacute;t hiện mặt đất nước đọng kh&aacute; l&agrave; nghi&ecirc;m trọng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Trời mưa to rồi?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;, ngo&agrave;i cửa sổ r&otilde; r&agrave;ng l&agrave; trăng s&aacute;ng nh&ocirc; l&ecirc;n cao, nơi n&agrave;o c&oacute; mảy may mưa xuống dấu hiệu?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Rất nhanh hắn cảm gi&aacute;c được, nước l&agrave; từ ph&iacute;a tr&ecirc;n trần nh&agrave; nhỏ xuống tới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Di&ecirc;m Hạo Thi&ecirc;n giơ l&ecirc;n đ&egrave;n dầu hỏa, xem x&eacute;t, tr&ecirc;n trần nh&agrave;, quả nhi&ecirc;n l&agrave; c&oacute; nước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng. . . Hắn nh&igrave;n thấy kia cảnh tượng đồng thời, lập tức bị dọa đến hồn phi ph&aacute;ch t&aacute;n!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sao. . . L&agrave;m sao c&oacute; thể?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuyết trắng tr&ecirc;n trần nh&agrave;, thế m&agrave;, để lại từng d&atilde;y dấu ch&acirc;n! Do nước tạo th&agrave;nh dấu ch&acirc;n!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dấu ch&acirc;n kia, c&oacute; thể thấy r&otilde; r&agrave;ng năm ng&oacute;n ch&acirc;n, thử hỏi người có khả năng treo ngược tr&ecirc;n trần nh&agrave; đi đường sao?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Di&ecirc;m Hạo Thi&ecirc;n lấy lại b&igrave;nh tĩnh, cắn chặt m&ocirc;i, xem kia mấy trần nh&agrave; b&ecirc;n tr&ecirc;n dấu ch&acirc;n, thế m&agrave; k&eacute;o d&agrave;i đến trong ph&ograve;ng bếp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mặc d&ugrave; phi thường sợ h&atilde;i, nhưng Di&ecirc;m Hạo Thi&ecirc;n vẫn l&agrave; muốn đi xem. C&oacute; lẽ, c&oacute; thể t&igrave;m tới c&aacute;i g&igrave; giải th&iacute;ch, l&yacute; giải loại n&agrave;y hiện tượng quỷ dị.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn run rẩy, dần dần đi hướng ph&ograve;ng bếp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ph&ograve;ng bếp cũng kh&ocirc;ng phải l&agrave; rất lớn, ho&agrave;n to&agrave;n c&oacute; thể nh&igrave;n một c&aacute;i kh&ocirc;ng s&oacute;t g&igrave;, căn bản kh&ocirc;ng chỗ c&oacute; thể giấu người.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trừ. . . Kia tủ ch&eacute;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; tr&ecirc;n trần nh&agrave; dấu ch&acirc;n, thế m&agrave; ch&iacute;nh l&agrave; k&eacute;o d&agrave;i đến tủ ch&eacute;n ph&iacute;a tr&ecirc;n, sau đ&oacute;, di động đến tủ ch&eacute;n chỗ dựa v&agrave;o tr&ecirc;n v&aacute;ch tường!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kh&ocirc;ng, kh&ocirc;ng thể n&agrave;o.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Di&ecirc;m Hạo Thi&ecirc;n giơ cao l&ecirc;n đ&egrave;n dầu hỏa, chậm r&atilde;i đi hướng tủ ch&eacute;n. Mỗi một bước, đều d&egrave; chừng cẩn thận.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Rốt cục đi đến tủ ch&eacute;n ph&iacute;a trước thời điểm, hắn d&ugrave;ng tay tr&aacute;i, bắt lấy tủ ch&eacute;n cửa, h&iacute;t thở s&acirc;u một ch&uacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Sẽ kh&ocirc;ng. . . Kh&ocirc;ng c&oacute; quan hệ g&igrave; với ta. . . L&yacute; Băng, ngươi l&agrave; tự s&aacute;t, kh&ocirc;ng phải ta hại ngươi, ngươi, ngươi đừng đến t&igrave;m ta. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sau đ&oacute;, hắn chợt đem tủ ch&eacute;n cửa, ho&agrave;n to&agrave;n mở ra!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&egrave;n dầu hỏa chiếu rọi, &aacute;nh v&agrave;o hắn tầm mắt ch&iacute;nh l&agrave; &mdash;&mdash;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&agrave;y ra được chỉnh chỉnh tề tề từng b&aacute;t c&ugrave;ng th&igrave;a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hết thảy đều rất b&igrave;nh thường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;H&ocirc; &mdash;&mdash; &quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Di&ecirc;m Hạo Thi&ecirc;n giờ ph&uacute;t n&agrave;y cảm gi&aacute;c to&agrave;n th&acirc;n đều muốn hư tho&aacute;t, bất qu&aacute; cuối c&ugrave;ng l&agrave; bu&ocirc;ng lỏng xuống tới. Quả nhi&ecirc;n, l&agrave; ch&iacute;nh m&igrave;nh dọa ch&iacute;nh m&igrave;nh a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave; tr&ecirc;n trần nh&agrave; dấu ch&acirc;n, l&agrave; chuyện g&igrave; xảy ra? Chẳng lẽ l&agrave; ai đ&ugrave;a &aacute;c? Nhưng l&agrave; trong l&agrave;ng kh&ocirc;ng c&oacute; người c&ugrave;ng ch&iacute;nh m&igrave;nh c&oacute; kh&uacute;c mắc a. C&ograve;n c&oacute;, vỏn vẹn những c&aacute;i kia dấu ch&acirc;n nước đọng, c&oacute; thể t&iacute;ch lấy nhiều như vậy nước sao?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vẫn l&agrave;, trước ti&ecirc;n đem nước đều m&uacute;c ra ngo&agrave;i đi, nếu kh&ocirc;ng, căn bản kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o ngủ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thế l&agrave;, hắn liền đem tủ ch&eacute;n cửa kh&eacute;p lại, quyết định trước cầm chậu rửa mặt đi m&uacute;c nước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Liền tại tủ ch&eacute;n cửa sắp đ&oacute;ng lại s&aacute;t na. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một kh&ocirc;ng c&oacute; ch&uacute;t huyết sắc n&agrave;o tay, thế m&agrave; từ tủ ch&eacute;n b&ecirc;n trong duỗi ra, đem cửa chống!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Di&ecirc;m Hạo Thi&ecirc;n c&ograve;n kh&ocirc;ng kịp phản ứng, lại c&oacute; một b&agrave;n tay duỗi ra, một phen b&oacute;p lấy hắn cổ, ngay sau đ&oacute;, tủ ch&eacute;n đại m&ocirc;n mở ra, Di&ecirc;m Hạo Thi&ecirc;n th&acirc;n thể, liền như vậy bị thu v&agrave;o tủ ch&eacute;n b&ecirc;n trong!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tủ ch&eacute;n cửa nặng nề m&agrave; đ&oacute;ng lại, hồi l&acirc;u, đều kh&ocirc;ng c&oacute; phản ứng. M&agrave; những c&aacute;i kia nước đọng, th&igrave; giống như bị hấp thu, x&ocirc;ng v&agrave;o mặt đất, biến mất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một trận gi&oacute; thổi tới, tủ ch&eacute;n cửa lại lần nữa bị thổi ra, b&ecirc;n trong, như cũ chỉ l&agrave; b&agrave;y ra được chỉnh chỉnh tề tề b&aacute;t, c&ugrave;ng th&igrave;a.</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:4941px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'nuoc-dau', 3, '566', '2022-07-31 08:28:50', '2022-07-31 08:28:50');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(17, '5', 'A Tú là ai', '<p>L&yacute; Ẩn nh&igrave;n ngo&agrave;i cửa sổ s&aacute;ng tỏ hạo nguyệt, tại đồng hồ đeo tay kim đồng hồ vượt qua nửa đ&ecirc;m kh&ocirc;ng giờ thời đi&ecirc;̉m &mdash;&mdash; liền mang &yacute; nghĩa, bọn họ đ&atilde; bị tr&oacute;i buộc ở trong th&ocirc;n n&agrave;y. Chỉ c&oacute; một th&aacute;ng sau, đến ng&agrave;y 8 th&aacute;ng 7 nửa đ&ecirc;m kh&ocirc;ng giờ, mới c&oacute; thể rời khỏi n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một th&aacute;ng. . . Thật đ&uacute;ng l&agrave; d&agrave;i đến hiếm thấy a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lấy đ&atilde; từng chữ bằng m&aacute;u chỉ lệnh m&agrave; n&oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn c&ograve;n r&otilde; r&agrave;ng nhớ kỹ. . . Diệp Khả H&acirc;n lần thứ nhất mang ch&iacute;nh m&igrave;nh, đi gặp Hạ Uy&ecirc;n t&igrave;nh cảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ở tại ph&ograve;ng 1006 Hạ Uy&ecirc;n, l&uacute;c ấy cho L&yacute; Ẩn ấn tượng đầu ti&ecirc;n ch&iacute;nh l&agrave; &mdash;&mdash; kh&ocirc;ng thể n&agrave;o? Hắn ch&iacute;nh l&agrave; tại c&aacute;i n&agrave;y khủng bố chỗ ở bốn năm người (một năm trước Hạ Uy&ecirc;n cư trụ thời gian l&agrave; bốn năm), thật hay giả?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mang theo ph&oacute; mắt kiếng kh&ocirc;ng gọng, mặc ủi thẳng thớm &acirc;u phục, d&aacute;ng người thon d&agrave;i, khu&ocirc;n mặt nho nh&atilde; hiền l&agrave;nh, xem l&ecirc;n. . . L&agrave;m sao cũng v&ocirc; ph&aacute;p c&ugrave;ng L&yacute; Ẩn trong suy nghĩ lầu trưởng vẽ l&ecirc;n ngang bằng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi l&agrave; hộ gia đ&igrave;nh mới?&quot; Hạ Uy&ecirc;n khi đ&oacute;, rất dễ d&agrave;ng liền tiếp nhận hắn, tựa hồ đ&atilde; tập m&atilde;i th&agrave;nh th&oacute;i quen.</p>', '<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn nh&igrave;n ngo&agrave;i cửa sổ s&aacute;ng tỏ hạo nguyệt, tại đồng hồ đeo tay kim đồng hồ vượt qua nửa đ&ecirc;m kh&ocirc;ng giờ thời đi&ecirc;̉m &mdash;&mdash; liền mang &yacute; nghĩa, bọn họ đ&atilde; bị tr&oacute;i buộc ở trong th&ocirc;n n&agrave;y. Chỉ c&oacute; một th&aacute;ng sau, đến ng&agrave;y 8 th&aacute;ng 7 nửa đ&ecirc;m kh&ocirc;ng giờ, mới c&oacute; thể rời khỏi n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một th&aacute;ng. . . Thật đ&uacute;ng l&agrave; d&agrave;i đến hiếm thấy a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lấy đ&atilde; từng chữ bằng m&aacute;u chỉ lệnh m&agrave; n&oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hắn c&ograve;n r&otilde; r&agrave;ng nhớ kỹ. . . Diệp Khả H&acirc;n lần thứ nhất mang ch&iacute;nh m&igrave;nh, đi gặp Hạ Uy&ecirc;n t&igrave;nh cảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ở tại ph&ograve;ng 1006 Hạ Uy&ecirc;n, l&uacute;c ấy cho L&yacute; Ẩn ấn tượng đầu ti&ecirc;n ch&iacute;nh l&agrave; &mdash;&mdash; kh&ocirc;ng thể n&agrave;o? Hắn ch&iacute;nh l&agrave; tại c&aacute;i n&agrave;y khủng bố chỗ ở bốn năm người (một năm trước Hạ Uy&ecirc;n cư trụ thời gian l&agrave; bốn năm), thật hay giả?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mang theo ph&oacute; mắt kiếng kh&ocirc;ng gọng, mặc ủi thẳng thớm &acirc;u phục, d&aacute;ng người thon d&agrave;i, khu&ocirc;n mặt nho nh&atilde; hiền l&agrave;nh, xem l&ecirc;n. . . L&agrave;m sao cũng v&ocirc; ph&aacute;p c&ugrave;ng L&yacute; Ẩn trong suy nghĩ lầu trưởng vẽ l&ecirc;n ngang bằng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi l&agrave; hộ gia đ&igrave;nh mới?&quot; Hạ Uy&ecirc;n khi đ&oacute;, rất dễ d&agrave;ng liền tiếp nhận hắn, tựa hồ đ&atilde; tập m&atilde;i th&agrave;nh th&oacute;i quen.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đ&uacute;ng vậy, Hạ ti&ecirc;n sinh, ta. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;C&aacute;i g&igrave; cũng đừng n&oacute;i.&quot; Hạ Uy&ecirc;n lập tức khiến hắn trong ph&ograve;ng ngồi xuống, sau đ&oacute; n&oacute;i với Diệp Khả H&acirc;n: &quot;Khả H&acirc;n, đi th&ocirc;ng tri mọi người đến người mới sự t&igrave;nh đi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khả H&acirc;n khi đ&oacute; xem Hạ Uy&ecirc;n, mặt kh&ocirc;ng khỏi giống quả t&aacute;o ch&iacute;n, thế m&agrave; đều c&oacute; ch&uacute;t kh&ocirc;ng d&aacute;m nh&igrave;n thẳng hắn. Tiếp theo, ngại ng&ugrave;ng gật gật đầu, đi ra ngo&agrave;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hạ Uy&ecirc;n gian ph&ograve;ng, c&ugrave;ng L&yacute; Ẩn kh&ocirc;ng ch&ecirc;nh lệch nhiều . Bất qu&aacute;, so s&aacute;nh dưới, bố tr&iacute; được c&agrave;ng th&ecirc;m mộc mạc một điểm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi đừng qu&aacute; khẩn trương, &quot; Hạ Uy&ecirc;n mỉm cười n&oacute;i: &quot;Lần thứ nhất tiến v&agrave;o c&aacute;i n&agrave;y chung cư người, đều sẽ kh&ocirc;ng biết l&agrave;m sao, m&agrave; khi ta n&oacute;i cho hắn biết ch&acirc;n tướng sau, đều sẽ c&oacute; một loại thế giới quan sụp đổ cảm gi&aacute;c, rất kh&oacute; tiếp nhận c&ugrave;ng tin tưởng.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ta. . . Ta đến b&acirc;y giờ, đối đ&acirc;y hết thảy đều cảm gi&aacute;c l&agrave; rơi v&agrave;o trong sương m&ugrave;.&quot; L&yacute; Ẩn vịn c&aacute;i tr&aacute;n, n&oacute;i: &quot;Hạ ti&ecirc;n sinh. . . Ta, thật muốn một mực ở chỗ n&agrave;y?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Uống c&agrave; ph&ecirc; vẫn l&agrave; tr&agrave;?&quot; Hạ Uy&ecirc;n lại kh&ocirc;ng trực tiếp trả lời, m&agrave; l&agrave; từ trong tủ đứng lấy ra một gốm sứ ly, n&oacute;i: &quot;Ta chỗ n&agrave;y c&oacute; H&agrave;ng Ch&acirc;u Vũ Tiền Long Tỉnh, c&agrave; ph&ecirc;, cũng đều l&agrave; Colombia c&ugrave;ng Brazil nhập khẩu c&agrave; ph&ecirc;, cha mẹ ta ở nước ngo&agrave;i gửi cho ta.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;C&agrave; ph&ecirc; a. . . Ta vẫn lu&ocirc;n cảm gi&aacute;c loại vật n&agrave;y c&ugrave;ng thuốc Đ&ocirc;ng y kh&ocirc;ng c&oacute; kh&aacute;c biệt.&quot; L&yacute; Ẩn cười khổ m&agrave; n&oacute;i: &quot;&Acirc;n, tr&agrave; tốt, đ&uacute;ng, kh&ocirc;ng muốn th&ecirc;m cẩu kỷ tử.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Tốt. Muốn hay kh&ocirc;ng th&ecirc;m điểm hoa c&uacute;c?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;C&oacute; thể.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hạ Uy&ecirc;n nh&igrave;n qua cũng liền hai bốn hai lăm tuổi, so L&yacute; Ẩn hơi lớn một ch&uacute;t, nhưng l&agrave; l&agrave;m việc lại rất l&atilde;o luyện, hắn pha tr&agrave; xong sau, th&acirc;n thiết đưa n&oacute; đưa cho L&yacute; Ẩn, tiếp theo, n&oacute;i ra: &quot;Kế tiếp ta lời n&oacute;i, ngươi tận khả năng đi tin tưởng đi, ta c&oacute; thể hướng ngươi đảm bảo, ta tuyệt đối sẽ kh&ocirc;ng n&oacute;i dối . Bất qu&aacute;, ngươi c&oacute; thể tiếp nhận bao nhi&ecirc;u, ta cũng kh&ocirc;ng c&oacute; x&aacute;c thực cam đoan.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ta, ta minh bạch.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tiếp theo, Hạ Uy&ecirc;n liền đem hết thảy n&oacute;i cho L&yacute; Ẩn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi n&oacute;i xong hết thảy, L&yacute; Ẩn biểu cảm đ&atilde; l&agrave; kinh ngạc đến tột đỉnh. Hắn, thực sự kh&ocirc;ng tiếp thụ được quỷ dị như vậy thuyết ph&aacute;p.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng. . . Đ&acirc;y lại l&agrave; thật. Hắn đ&atilde; thử qua đi đến ng&otilde; nhỏ b&ecirc;n ngo&agrave;i xem nơi n&agrave;y, quả nhi&ecirc;n. . . Chung cư biến mất v&ocirc; tung v&ocirc; ảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nơi n&agrave;y, x&aacute;c thực l&agrave; một v&ocirc; ph&aacute;p l&yacute; giải dị thường kiến tr&uacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ta hiểu ngươi b&acirc;y giờ t&acirc;m t&igrave;nh, nhưng. . . Ta nhất định phải n&oacute;i cho ngươi. . . Rời khỏi c&aacute;i n&agrave;y chung cư vượt qua bốn mươi t&aacute;m giờ liền sẽ chết. Nếu như ngươi kh&ocirc;ng tin, ta c&oacute; thể cho ngươi xem một điểm video. Ta v&igrave; c&oacute; thể l&agrave;m cho người tin tưởng điểm n&agrave;y, trước kia đ&atilde; từng. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ta tin.&quot; L&yacute; Ẩn lại l&agrave; rất dễ d&agrave;ng tiếp nhận điểm n&agrave;y, n&oacute;i tiếp đi: &quot;Như vậy. . . C&aacute;c ngươi, tại những c&aacute;i kia chỉ thị chữ bằng m&aacute;u địa điểm, đều gặp. . . Gặp. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đ&uacute;ng thế.&quot; Hạ Uy&ecirc;n n&oacute;i đến đ&acirc;y, sắc mặt cũng biến th&agrave;nh c&oacute; ch&uacute;t t&aacute;i nhợt: &quot;Thật đ&aacute;ng tiếc, ta từ tiến v&agrave;o c&aacute;i n&agrave;y chung cư bắt đầu, liền rốt cuộc kh&ocirc;ng phải kẻ v&ocirc; thần.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Duy t&acirc;m. . . tồn tại?&quot; L&yacute; Ẩn giờ ph&uacute;t n&agrave;y tay cũng bắt đầu kh&ocirc;ng ngừng ph&aacute;t run l&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hạ Uy&ecirc;n đẩy tr&ecirc;n sống mũi k&iacute;nh mắt, n&oacute;i: &quot;Ta, c&oacute; thể sống cho tới h&ocirc;m nay, quay đầu qu&aacute; khứ, quả thực ch&iacute;nh l&agrave; &aacute;c mộng a. Vẻn vẹn chỉ l&agrave; hồi ức một ch&uacute;t, ta cũng ho&agrave;i nghi ch&iacute;nh m&igrave;nh l&uacute;c ấy l&agrave; thế n&agrave;o gắng gượng qua tới.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn l&uacute;c n&agrave;y, đưa ra một c&aacute;i nghi vấn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi mới vừa n&oacute;i. .. B&igrave;nh thường trong căn hộ kh&ocirc;ng c&oacute; chỉ thị chữ bằng m&aacute;u khoảng c&aacute;ch vượt qua nửa năm, như vậy. . . C&ugrave;ng một hộ gia đ&igrave;nh, chỉ thị chữ bằng m&aacute;u có khả năng khoảng c&aacute;ch bao l&acirc;u?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;N&agrave;y rất kh&oacute; n&oacute;i.&quot; Hạ Uy&ecirc;n tiếp tục n&oacute;i ra: &quot;Ban sơ sẽ tương đối nhanh, c&aacute;ch mấy th&aacute;ng liền sẽ c&oacute; một lần mới chữ bằng m&aacute;u xuất hiện. Nhưng l&agrave;, c&agrave;ng l&agrave; đến đằng sau, khoảng c&aacute;ch thời gian cũng liền c&agrave;ng d&agrave;i. Ta đ&atilde; ho&agrave;n th&agrave;nh năm lần chỉ thị chữ bằng m&aacute;u, m&agrave; lần trước chỉ thị chữ bằng m&aacute;u, khoảng c&aacute;ch hiện tại đ&atilde; sắp muốn qua một năm.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kia. . . Chẳng phải l&agrave; n&oacute;i kh&ocirc;ng tuy&ecirc;n bố chỉ thị dưới t&igrave;nh huống, ch&uacute;ng ta nhất định phải một mực ở lại đi?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đ&uacute;ng. Kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o. Mười lần chỉ thị chữ bằng m&aacute;u liền c&oacute; thể rời đi thuyết ph&aacute;p, cũng l&agrave; ta vừa v&agrave;o ở thời điểm, l&atilde;o hộ gia đ&igrave;nh n&oacute;i cho ta. M&agrave; những hộ gia đ&igrave;nh cũ kia sau n&agrave;y, cũng đều chết rồi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn rất r&otilde; r&agrave;ng. . . Chỉ sợ ch&iacute;nh m&igrave;nh sau n&agrave;y, cũng muốn tại c&aacute;i n&agrave;y trong căn hộ, sinh hoạt rất d&agrave;i một đoạn ng&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tiếp theo, Hạ Uy&ecirc;n dẫn hắn quen biết trong c&aacute;c chung cư c&aacute;c bạn h&agrave;ng x&oacute;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cho L&yacute; Ẩn mang đến khắc s&acirc;u ấn tượng c&oacute; trở xuống mấy người, ở tại ph&ograve;ng 706 Hoa Li&ecirc;n Th&agrave;nh c&ugrave;ng Y Mang vợ chồng, đ&ocirc;i vợ chồng kia đều rất trẻ tuổi, tựa hồ vừa mới đại học tốt nghiệp, bọn họ so L&yacute; Ẩn sớm một năm v&agrave;o ở c&aacute;i n&agrave;y chung cư, c&ugrave;ng hắn rất n&oacute;i chuyện rất l&agrave; hợp &yacute;; c&ograve;n c&oacute; hai người, cũng l&agrave;m cho hắn kh&oacute; m&agrave; qu&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một, l&agrave; ở tại ph&ograve;ng 502 Đường Văn Sơn, c&ograve;n c&oacute; một, l&agrave; ở tại ph&ograve;ng 402 Odagiri Sachiko.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đường Văn Sơn, l&agrave; cực k&igrave; &acirc;m trầm kiệm lời, hai mắt cực k&igrave; băng l&atilde;nh thanh ni&ecirc;n, nh&igrave;n thấy bất luận kẻ n&agrave;o, đều l&agrave; một bộ đối phương c&ugrave;ng hắn l&agrave; kh&ocirc;ng đội trời chung cừu nh&acirc;n biểu cảm; m&agrave; Odagiri Sachiko l&agrave; một từ Nhật Bản đến Trung Quốc du học sinh vi&ecirc;n, ti&ecirc;́ng Trung n&oacute;i đến phi thường tốt, m&agrave; n&agrave;ng th&igrave; l&agrave; rất tỉnh t&aacute;o, b&igrave;nh tĩnh biểu cảm, khiến người ta cảm thấy nh&igrave;n kh&ocirc;ng thấu n&agrave;ng. M&agrave; Sachiko n&agrave;ng cho L&yacute; Ẩn lưu lại khắc s&acirc;u ấn tượng một nguy&ecirc;n nh&acirc;n kh&aacute;c l&agrave;. . . Dung mạo của n&agrave;ng đẹp đến khiến L&yacute; Ẩn giật nảy m&igrave;nh, l&agrave; ti&ecirc;u chuẩn Gothic loli.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; tại L&yacute; Ẩn bắt đầu tại trong căn hộ cư trụ sau, tự nhi&ecirc;n gặp thường đến th&acirc;n l&agrave; s&aacute;t v&aacute;ch h&agrave;ng x&oacute;m Odagiri Sachiko, ban sơ hắn l&agrave; hi vọng c&oacute; thể c&ugrave;ng n&agrave;y mấy c&oacute; kinh nghiệm hộ gia đ&igrave;nh hảo hảo giao tiếp. Diệp Khả H&acirc;n phi thường b&igrave;nh dị gần gũi, nhưng Odagiri Sachiko, cho người cảm gi&aacute;c lu&ocirc;n lu&ocirc;n rất kh&ocirc;ng thể tiếp cận, b&igrave;nh thường l&uacute;c kh&ocirc;ng c&oacute; chuyện g&igrave; l&agrave;m, n&agrave;ng đều l&agrave; bưng lấy một bản tiếng Nhật bản &laquo; Truyện kể Genji &raquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn nhớ kỹ, h&ocirc;m qua l&uacute;c ra cửa, hắn c&ograve;n c&ugrave;ng Diệp Khả H&acirc;n đặc biệt đi Odagiri Sachiko nh&agrave; tạm biệt. M&agrave; n&agrave;ng, cũng như cũ l&agrave; như vậy băng l&atilde;nh, từ đầu đến cuối, cũng kh&ocirc;ng c&oacute; n&oacute;i qua một c&acirc;u.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngược lại l&agrave; Hoa Li&ecirc;n Th&agrave;nh vợ chồng rất nhiệt t&igrave;nh, li&ecirc;n tục nhắc nhở bọn họ phải cẩn thận cẩn thận, nhất định phải c&ograve;n sống trở về. N&agrave;y khiến L&yacute; Ẩn rất cảm động, một năm xuống tới, cảm t&igrave;nh giữa nhau cũng rất s&acirc;u.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mọi người. . . D&ugrave; sao đều l&agrave; gi&uacute;p đỡ lẫn nhau, sống đến b&acirc;y giờ!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nghĩ tới đ&acirc;y, L&yacute; Ẩn trăm mối cảm x&uacute;c ngổn ngang, xem xem b&ecirc;n cạnh ngủ say La Hằng Vi&ecirc;m c&ugrave;ng Tần Thủ Thi&ecirc;n. C&ugrave;ng với, kh&ocirc;ng ngừng c&ugrave;ng s&acirc;u ngủ đấu tranh Diệp Khả H&acirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Khả H&acirc;n, chịu đựng đi, ch&uacute;ng ta nhất định phải c&oacute; hai người tỉnh mới c&oacute; thể.&quot; L&yacute; Ẩn n&oacute;i ra: &quot;Ngươi ki&ecirc;n tr&igrave; một ch&uacute;t nữa đi, nhạ, lại uống điểm tr&agrave; đi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ta kh&ocirc;ng sao. . .&quot; N&agrave;ng mặc d&ugrave; n&oacute;i như vậy, nhưng vẫn l&agrave; ng&aacute;p lớn: &quot;Ta, ta kh&ocirc;ng thể ngủ.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn thế l&agrave; đ&agrave;nh phải c&ugrave;ng n&agrave;ng nhiều n&oacute;i chuyện, khiến n&agrave;ng tốt tập trung tinh thần: &quot;Kia, ch&uacute;ng ta n&oacute;i chuyện đi. Khả H&acirc;n, ngươi. . . Th&iacute;ch Hạ Uy&ecirc;n đ&uacute;ng kh&ocirc;ng?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A, đ&uacute;ng a. . . Hả? A, kh&ocirc;ng kh&ocirc;ng kh&ocirc;ng kh&ocirc;ng, ta kh&ocirc;ng phải. . .&quot; Khả H&acirc;n vội v&agrave;ng kho&aacute;t tay, n&oacute;i: &quot;Ta, ta kh&ocirc;ng phải c&ugrave;ng Hạ Uy&ecirc;n. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Diệp Khả H&acirc;n c&oacute; thể sống đến b&acirc;y giờ, c&ugrave;ng Hạ Uy&ecirc;n tồn tại c&oacute; ch&uacute;t &iacute;t quan hệ. Ba năm, n&agrave;ng quả thực l&agrave; chống đến hiện tại.&quot;Hạ. . . Hạ Uy&ecirc;n hắn, &quot; Diệp Khả H&acirc;n giờ ph&uacute;t n&agrave;y sắc mặt trở n&ecirc;n đỏ bừng: &quot;Hắn chưa hẳn để &yacute; ta a. Huống chi, hiện tại, đệ nhất c&acirc;n nhắc ch&iacute;nh l&agrave; vấn đề sinh tồn a, ta, nghĩ cũng kh&ocirc;ng d&aacute;m nghĩ những thứ n&agrave;y. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn cười n&oacute;i: &quot;Ch&uacute;ng ta nhất định c&oacute; thể c&ugrave;ng nhau c&ograve;n sống rời đi c&aacute;i kia đ&aacute;ng chết chung cư. . . Nhất định!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Diệp Khả H&acirc;n nghe thấy L&yacute; Ẩn c&acirc;u n&oacute;i n&agrave;y, cũng tho&aacute;ng an t&acirc;m một ch&uacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Hằng vi&ecirc;m hắn a, t&iacute;nh c&aacute;ch cũng tương đối xung động, ta ch&iacute;nh l&agrave; tương đối lo lắng hắn.&quot; L&yacute; Ẩn quay đầu lại xem ngủ say La Hằng Vi&ecirc;m, n&oacute;i: &quot;Hắn cũng coi như rất đ&aacute;ng thương, phụ mẫu đều mất sau một m&igrave;nh đến K thị sinh hoạt, kh&ocirc;ng nghĩ tới gặp được chuyện như vậy.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đúng v&acirc;̣y a. . .&quot; Diệp Khả H&acirc;n gật gật đầu, n&oacute;i: &quot;C&ograve;n c&oacute; thủ thi&ecirc;n hắn cũng l&agrave;. . . Bởi v&igrave; vợ ngoại t&igrave;nh m&agrave; ly h&ocirc;n, một m&igrave;nh một người sa s&uacute;t tại đ&ecirc;m khuya khắp nơi lắc lư, uống rượu mua say, kết quả c&aacute;i b&oacute;ng liền bị. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tần Thủ Thi&ecirc;n trước kia c&ugrave;ng Diệp Khả H&acirc;n tiếp đến qua c&ugrave;ng một lần chỉ thị chữ bằng m&aacute;u, một lần kia chỉ c&oacute; ba người, kết quả một người kh&aacute;c chết rồi, chỉ c&oacute; Tần Thủ Thi&ecirc;n c&ugrave;ng Diệp Khả H&acirc;n sống s&oacute;t. Cho n&ecirc;n, Diệp Khả H&acirc;n đối Tần Thủ Thi&ecirc;n cũng c&oacute; m&atilde;nh liệt đồng bạn &yacute; thức, rất hi vọng hắn c&oacute; thể sống s&oacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ng&agrave;y thứ hai. . . Rốt cục đến.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;N&oacute;i như vậy, L&yacute; ti&ecirc;n sinh c&ugrave;ng Diệp tiểu thư c&ograve;n kh&ocirc;ng c&oacute; rời giường?&quot; S&aacute;ng sớm, Trương th&ocirc;n trưởng liền đến &acirc;n cần thăm hỏi bọn họ, m&agrave; La Hằng Vi&ecirc;m th&igrave; trả lời hắn L&yacute; Ẩn c&ugrave;ng Diệp Khả H&acirc;n rất mệt mỏi, c&ograve;n tại nghỉ ngơi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kh&ocirc;ng c&oacute; quan hệ, &quot; Trương th&ocirc;n trưởng bỗng nhi&ecirc;n nh&igrave;n chung quanh một phen, xem bốn bề vắng lặng, liền thấp giọng hỏi La Hằng Vi&ecirc;m: &quot;La ti&ecirc;n sinh a, nơi n&agrave;y cũng kh&ocirc;ng c&oacute; người kh&aacute;c, ngươi liền n&oacute;i cho ta đi. . . Ngươi l&atilde;o bản, lúc nào đến a?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A? L&atilde;o bản?&quot; La Hằng Vi&ecirc;m ng&acirc;y ngẩn, hỏi: &quot;C&aacute;i g&igrave; l&atilde;o bản?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ha ha, c&ograve;n giả a. Ta bi&ecirc;́t l&atilde;o bản của c&aacute;c ngươi dặn d&ograve; qua c&aacute;c ngươi, nhất định phải giả bộ giống, kh&ocirc;ng quan hệ. Ta đ&atilde; c&ugrave;ng người trong th&ocirc;n th&ocirc;ng qua kh&iacute;, bọn họ c&ograve;n kh&ocirc;ng đến mức d&aacute;m kh&ocirc;ng nh&igrave;n mặt mũi của ta. C&aacute;c ngươi t&ugrave;y tiện tại th&ocirc;n c&aacute;c nơi xem, ta tuyệt đối kh&ocirc;ng c&oacute; &yacute; kiến.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ta. . . Ta kh&ocirc;ng r&otilde;. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đ&uacute;ng đ&uacute;ng đ&uacute;ng, ngươi kh&ocirc;ng r&otilde;, kh&ocirc;ng r&otilde; l&agrave; được rồi. Y&ecirc;n t&acirc;m đi, c&aacute;c ngươi ở tại trong l&agrave;ng khoảng thời gian n&agrave;y, ta tuyệt đối sẽ hảo hảo chi&ecirc;u đ&atilde;i c&aacute;c ngươi, l&atilde;o bản của c&aacute;c ngươi đ&atilde; c&ugrave;ng ta li&ecirc;n hệ tốt nha.&quot;</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xem Trương th&ocirc;n trưởng kia &acirc;n cần như vậy bộ d&aacute;ng, La Hằng Vi&ecirc;m bắt đầu &yacute; thức được, vị trưởng th&ocirc;n n&agrave;y, tựa hồ hiểu lầm sự t&igrave;nh g&igrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;, tựa hồ cũng l&agrave; bởi v&igrave; kia &quot;L&atilde;o bản&quot;, th&ocirc;n mới khiến cho bọn họ lưu lại, thậm ch&iacute; đối với m&igrave;nh ch&aacute;u trai ruột động thủ, hơn nữa c&ograve;n đối bọn họ nhiệt t&igrave;nh như vậy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Như vậy. . . Tạm thời liền để hắn tiếp tục tiếp tục hiểu lầm đi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Gia gia!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trương Tố Nguyệt bỗng nhi&ecirc;n vội v&atilde; chạy tới, thở kh&ocirc;ng ra hơi n&oacute;i: &quot;Gia gia, ph&aacute;t sinh đại sự. . . Hạo, Hạo Thi&ecirc;n th&uacute;c hắn, kh&ocirc;ng thấy!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Hạo Thi&ecirc;n?&quot; Trương th&ocirc;n trưởng lập tức nhướn m&agrave;y, ngay sau đ&oacute; n&oacute;i với La Hằng Vi&ecirc;m: &quot;La ti&ecirc;n sinh. . . Ta đi trước xử l&yacute; một &iacute;t chuyện.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kh&ocirc;ng thấy&quot;, đối với hết thảy đều cực k&igrave; mẫn cảm La Hằng Vi&ecirc;m tới n&oacute;i, c&aacute;i n&agrave;y thật sự l&agrave; rất dễ d&agrave;ng dẫn tới li&ecirc;n hệ từ ngữ. L&yacute; Ẩn lặp đi lặp lại khuy&ecirc;n bảo hắn , bất kỳ c&aacute;i g&igrave; &quot;Mất tự nhi&ecirc;n&quot; đều nhất định muốn tuyệt đối quan t&acirc;m. Bởi vậy, hắn lập tức n&oacute;i ra: &quot;Th&ocirc;n trưởng, ta, c&ugrave;ng ngươi c&ugrave;ng đi xem xem đi!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chảy xiết trước th&aacute;c nước, A T&uacute; cởi xuống vớ gi&agrave;y, đem ch&acirc;n ng&acirc;m ở trong nước, dao động hai ch&acirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A T&uacute;. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;ng lập tức quay đầu qua xem x&eacute;t, ch&iacute;nh l&agrave; n&agrave;ng s&aacute;t v&aacute;ch h&agrave;ng x&oacute;m, từ nhỏ thanh mai tr&uacute;c m&atilde; lớn l&ecirc;n Lương Nh&acirc;n B&acirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tướng mạo phi thường tuấn t&uacute; Lương Nh&acirc;n B&acirc;n, c&ugrave;ng A T&uacute; một mực l&agrave; phi thường th&acirc;n mật. A T&uacute; sau khi cha mẹ mất, minh x&aacute;c đưa ra hi vọng A T&uacute; gả cho Nh&acirc;n B&acirc;n &yacute; tưởng . Bất qu&aacute;, A T&uacute; cho rằng phụ mẫu giữ đạo hiếu l&agrave;m l&yacute; do, một mực k&eacute;o h&ocirc;n sự, n&ocirc;ng th&ocirc;n người, d&ugrave; sao so người trong th&agrave;nh c&oacute; c&agrave;ng nhiều quan niệm bảo thủ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;. . . L&yacute; Băng sự t&igrave;nh ph&aacute;t sinh sau, A T&uacute; liền đối Nh&acirc;n B&acirc;n cũng k&iacute;nh nhi viễn chi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;H&ocirc;m nay l&agrave; Băng Nhi tỷ tỷ ng&agrave;y giỗ, &quot; A T&uacute; quay đầu đi, n&oacute;i: &quot;Ta sẽ b&aacute;i tế n&agrave;ng. Mặc kệ người trong th&ocirc;n cỡ n&agrave;o ki&ecirc;ng kị n&agrave;ng, ta tuyệt đối sẽ kh&ocirc;ng qu&ecirc;n ng&agrave;y n&agrave;y.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Tội g&igrave; khổ như thế chứ? A T&uacute;, v&igrave; L&yacute; Băng ngươi liền muốn từ trước đến nay ta như vậy bất ho&agrave; xuống dưới? N&agrave;ng đều đ&atilde; chết, ta c&ograve;n c&oacute; thể l&agrave;m thế n&agrave;o?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ta kh&ocirc;ng muốn lại nh&igrave;n thấy ngươi.&quot; A T&uacute; tiếp tục lạnh l&ugrave;ng n&oacute;i: &quot;Ngươi lại tới, ta liền kh&ocirc;ng kh&aacute;ch kh&iacute;!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lương Nh&acirc;n B&acirc;n vẻ mặt đau khổ, n&oacute;i: &quot;Được. . . Được rồi. . . D&ugrave; sao h&ocirc;m nay ng&agrave;y n&agrave;y, c&ugrave;ng ngươi n&oacute;i c&aacute;i g&igrave; cũng v&ocirc; dụng. Nhưng l&agrave;, A T&uacute;, ngươi cần phải nhớ kỹ, ta đối với ngươi, thủy chung l&agrave; thật l&ograve;ng!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nh&acirc;n B&acirc;n rời khỏi sau, A T&uacute; c&uacute;i người xuống, m&uacute;c nước rửa rửa mặt. M&agrave; ngay l&uacute;c n&agrave;y, n&agrave;ng từ giữa ng&oacute;n tay, thấy được. . . Phản chiếu tại tr&ecirc;n nước, kh&ocirc;ng phải mặt của n&agrave;ng, m&agrave; l&agrave; mặt kh&aacute;c một nữ nh&acirc;n!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;ng chậm r&atilde;i lấy tay ra, tr&ecirc;n mặt nước, như cũ chiếu ch&iacute;nh n&agrave;ng mặt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Băng Nhi tỷ tỷ. . .&quot; A T&uacute; lại một ch&uacute;t cũng kh&ocirc;ng c&oacute; sợ h&atilde;i, n&oacute;i: &quot;Ta bi&ecirc;́t, ta bi&ecirc;́t ngươi rất hận. . . Liền t&iacute;nh biến th&agrave;nh như vậy, ngươi như cũ l&agrave; ta Băng Nhi tỷ tỷ. Ta, sẽ kh&ocirc;ng sợ ngươi, bởi v&igrave; ta tin tưởng ngươi tuyệt đối sẽ kh&ocirc;ng tổn thương ta. Ngươi cứ việc đi l&agrave;m đi. Đem l&uacute;c trước người bức tử ngươi, to&agrave;n bộ đều giết chết. . . To&agrave;n bộ. . .&quot;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:132.688px; top:22px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'a-tu-la-ai', 3, '66', '2022-07-31 08:30:02', '2022-07-31 08:30:02'),
(19, '1', 'Thứ nhất chương Trước mộ phần đối thoại', '<p>Gi&oacute; thu hiu quạnh, sắc trời h&ocirc;n m&ecirc; đ&iacute;ch d&atilde; ngoại, một t&ograve;a kh&ocirc; trước mộ phần, th&acirc;n thước bạch &aacute;o d&agrave;i đ&iacute;ch mười tuổi thiếu ni&ecirc;n ngồi xếp bằng, trong tay dẫn theo một bầu rượu, thường thường ngửa đầu tự ẩm, thần sắc lặng im lại tịch khổ vạn phần.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi tới ho&agrave;ng h&ocirc;n, m&agrave;n trời dần tối, theo xa xa lại đi tới một người, nho nh&atilde; thanh sam, trong tay đồng dạng dẫn theo một c&aacute;i rượu hồ l&ocirc;, đi v&agrave;o trước mộ phần y&ecirc;n lặng c&uacute;i đầu, theo sau c&ugrave;ng kia &aacute;o trắng thiếu ni&ecirc;n s&oacute;ng vai m&agrave; ngồi, thở d&agrave;i một tiếng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Phụng Hiếu, thệ người đ&atilde; hĩ, bảo trọng th&acirc;n thể a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; kia mười tuổi thiếu ni&ecirc;n lại th&aacute;nh th&oacute;t cười, lại qu&aacute;n tiếp theo khẩu rượu thủy, biến mất b&ecirc;n miệng rượu t&iacute;ch, lắc đầu kh&ocirc;ng n&oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng ai biết giờ ph&uacute;t n&agrave;y hắn trong l&ograve;ng trừ bỏ tang mẫu chi đau ở ngo&agrave;i, c&ograve;n ẩn h&agrave;m rất nhiều kh&ocirc;ng muốn người biết đ&iacute;ch b&iacute; mật, m&agrave; n&agrave;y b&iacute; mật, lại l&agrave;m cho hắn đau đầu vạn phần, nội t&acirc;m gi&atilde;y dụa kh&ocirc;ng th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave;m một c&aacute;i xuy&ecirc;n qua nh&acirc;n, đường thịnh l&agrave; may mắn đ&iacute;ch, nhưng cũng l&agrave; kh&ocirc;ng hay ho đ&iacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng hay ho ch&iacute;nh l&agrave; hắn xuy&ecirc;n qua tới rồi Đ&ocirc;ng H&aacute;n những năm cuối, may mắn ch&iacute;nh l&agrave; hắn xuy&ecirc;n qua tới rồi địa linh nh&acirc;n kiệt đ&iacute;ch To&aacute;nh Xuy&ecirc;n, trở th&agrave;nh trời sinh kỳ t&agrave;i được xưng thi&ecirc;n t&agrave;i đ&iacute;ch Qu&aacute;ch Gia, khả kh&ocirc;ng may hiện giờ mới mười tuổi đ&iacute;ch Qu&aacute;ch Gia đ&atilde; muốn th&agrave;nh c&ocirc; nhi, lại ở lịch sử thượng kh&ocirc;ng đến bốn mươi tuổi liền qua đời.</p>', '<p>Gi&oacute; thu hiu quạnh, sắc trời h&ocirc;n m&ecirc; đ&iacute;ch d&atilde; ngoại, một t&ograve;a kh&ocirc; trước mộ phần, th&acirc;n thước bạch &aacute;o d&agrave;i đ&iacute;ch mười tuổi thiếu ni&ecirc;n ngồi xếp bằng, trong tay dẫn theo một bầu rượu, thường thường ngửa đầu tự ẩm, thần sắc lặng im lại tịch khổ vạn phần.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi tới ho&agrave;ng h&ocirc;n, m&agrave;n trời dần tối, theo xa xa lại đi tới một người, nho nh&atilde; thanh sam, trong tay đồng dạng dẫn theo một c&aacute;i rượu hồ l&ocirc;, đi v&agrave;o trước mộ phần y&ecirc;n lặng c&uacute;i đầu, theo sau c&ugrave;ng kia &aacute;o trắng thiếu ni&ecirc;n s&oacute;ng vai m&agrave; ngồi, thở d&agrave;i một tiếng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Phụng Hiếu, thệ người đ&atilde; hĩ, bảo trọng th&acirc;n thể a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; kia mười tuổi thiếu ni&ecirc;n lại th&aacute;nh th&oacute;t cười, lại qu&aacute;n tiếp theo khẩu rượu thủy, biến mất b&ecirc;n miệng rượu t&iacute;ch, lắc đầu kh&ocirc;ng n&oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng ai biết giờ ph&uacute;t n&agrave;y hắn trong l&ograve;ng trừ bỏ tang mẫu chi đau ở ngo&agrave;i, c&ograve;n ẩn h&agrave;m rất nhiều kh&ocirc;ng muốn người biết đ&iacute;ch b&iacute; mật, m&agrave; n&agrave;y b&iacute; mật, lại l&agrave;m cho hắn đau đầu vạn phần, nội t&acirc;m gi&atilde;y dụa kh&ocirc;ng th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave;m một c&aacute;i xuy&ecirc;n qua nh&acirc;n, đường thịnh l&agrave; may mắn đ&iacute;ch, nhưng cũng l&agrave; kh&ocirc;ng hay ho đ&iacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng hay ho ch&iacute;nh l&agrave; hắn xuy&ecirc;n qua tới rồi Đ&ocirc;ng H&aacute;n những năm cuối, may mắn ch&iacute;nh l&agrave; hắn xuy&ecirc;n qua tới rồi địa linh nh&acirc;n kiệt đ&iacute;ch To&aacute;nh Xuy&ecirc;n, trở th&agrave;nh trời sinh kỳ t&agrave;i được xưng thi&ecirc;n t&agrave;i đ&iacute;ch Qu&aacute;ch Gia, khả kh&ocirc;ng may hiện giờ mới mười tuổi đ&iacute;ch Qu&aacute;ch Gia đ&atilde; muốn th&agrave;nh c&ocirc; nhi, lại ở lịch sử thượng kh&ocirc;ng đến bốn mươi tuổi liền qua đời.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mẫu th&acirc;n qua đời sau tự rước tự Phụng Hiếu đ&iacute;ch Qu&aacute;ch Gia ở trước mộ phần c&ocirc; tọa ba ng&agrave;y, cả ng&agrave;y uống rượu, thiếu ni&ecirc;n phấn chấn đ&iacute;ch oai h&ugrave;ng hiện giờ lại tho&aacute;ng như thất vọng kh&ocirc;ng chịu nổi đ&iacute;ch lưu d&acirc;n, rất c&oacute; cam chịu đ&iacute;ch xu thế.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trầm mặc hồi l&acirc;u, Qu&aacute;ch Gia sắc mặt lạnh l&ugrave;ng địa quay đầu nh&igrave;n ph&iacute;a b&ecirc;n cạnh lớn tuổi hắn đ&iacute;ch thanh ni&ecirc;n, n&oacute;i: &quot;Ch&iacute; T&agrave;i, ta phụ chết sớm, ta mẫu ngậm đắng nuốt cay nu&ocirc;i nấng ta mười năm, c&ocirc; nhi quả phụ sống nương tựa lẫn nhau, trong nh&agrave; điền bất qu&aacute; trăm mẫu, ốc bất qu&aacute; sổ gian, v&agrave;ng bạc ngọc v&agrave; tơ lụa sớm d&ugrave;ng hết, gia đạo bần h&agrave;n đến tận đ&acirc;y, lại c&ograve;n c&oacute; tiết tiểu đồ đệ mơ ước r&igrave;nh, bức tử ta mẫu kh&ocirc;ng n&oacute;i, c&ograve;n muốn đem ta đuổi ra To&aacute;nh Xuy&ecirc;n, Ch&iacute; T&agrave;i, ngươi n&oacute;i, ta Qu&aacute;ch Phụng Hiếu c&oacute; phải hay kh&ocirc;ng n&ecirc;n tắm rửa tịnh thủ, nh&acirc;m n&agrave;y ho&agrave;nh h&agrave;nh ngang ngược, mặc cho x&acirc;m lược?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i b&ugrave;i ng&ugrave;i thở d&agrave;i sau, động dung đạo: &quot;Phụng Hiếu, việc n&agrave;y x&eacute;t đến c&ugrave;ng l&agrave; ngươi đ&iacute;ch gia sự, ta kh&ocirc;ng n&ecirc;n xen mồm, nhưng ngươi đ&atilde; c&ugrave;ng ta c&ocirc;ng bằng, ta cũng liền n&oacute;i thoải m&aacute;i . Ngươi phụ ở Qu&aacute;ch gia l&agrave; thứ ra người ấy lại thi&ecirc;n cư To&aacute;nh Xuy&ecirc;n, rời xa trong gia tộc t&acirc;m, ở nh&agrave; trong tộc kh&ocirc;ng c&oacute; quyền v&ocirc; thế, hiện giờ tao n&agrave;y đại họa, mặc d&ugrave; đang,ở ngươi trong miệng l&agrave; Qu&aacute;ch gia mưu nh&agrave; ngươi sản, bức b&aacute;ch ngươi mẫu tử, nhưng ở ngo&agrave;i nh&acirc;n trong mắt, n&agrave;y cũng thật ch&iacute;nh l&agrave; Qu&aacute;ch gia ch&iacute;nh m&igrave;nh chuyện nhi, n&oacute;i đến để, ngươi tr&ecirc;n người chảy xu&ocirc;i đ&iacute;ch huyết mạch vẫn l&agrave; Qu&aacute;ch gia đ&iacute;ch, n&oacute;i c&aacute;ch kh&aacute;c, ngươi phụ lưu lại đ&iacute;ch, Qu&aacute;ch gia ch&aacute;nh chủ tự nhi&ecirc;n c&oacute; xử tr&iacute; chi quyền. Bất qu&aacute; ngươi y&ecirc;n t&acirc;m, việc n&agrave;y ta đ&atilde; cho biết, b&aacute;o cho Văn Nhược, Văn Nhược viện thủ trợ ngươi, từ nay về sau v&ocirc; hoạn.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia ngửa mặt l&ecirc;n trời cười ha ha, lại lộ th&ecirc; lương &yacute;.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Văn Nhược trợ ta? Tu&acirc;n gia một m&ocirc;n anh h&agrave;o, To&aacute;nh Xuy&ecirc;n danh m&ocirc;n, danh sĩ đại tộc trong l&uacute;c đ&oacute; sao lại v&ocirc; lợi khởi can qua, tu&acirc;n gia trợ ta bất qu&aacute; l&agrave; l&agrave;m cho kia Qu&aacute;ch gia s&agrave;i lang như vậy dừng tay m&agrave; th&ocirc;i, nếu mỗ thi&ecirc;n ta kh&ocirc;ng người che chở, Qu&aacute;ch gia chẳng phải l&agrave; vừa muốn t&aacute;i khởi l&ograve;ng xấu xa? Ta Qu&aacute;ch Phụng Hiếu chẳng lẽ chung thứ nhất sinh phải nhờ bao che vu nh&acirc;n? Ch&iacute; T&agrave;i, ngươi c&oacute; biết ta muốn n&oacute;i c&aacute;i g&igrave; sao kh&ocirc;ng?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i khu&ocirc;n mặt nghi&ecirc;m t&uacute;c, trầm giọng hỏi: &quot;G&igrave; n&oacute;i?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cầm trong tay bầu rượu hướng kh&ocirc;ng trung ra sức n&eacute;m đi, t&ecirc; thanh r&iacute;t g&agrave;o đạo: &quot;Thế gian h&agrave;o m&ocirc;n giai c&aacute; m&egrave; một lứa, tội &aacute;c chồng chất tội lỗi chồng chất, n&agrave;y &aacute;c kh&ocirc;ng trừ, thi&ecirc;n hạ v&ocirc; th&aacute;i b&igrave;nh!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hảm ho&agrave;n lời n&agrave;y, Qu&aacute;ch Gia tho&aacute;t lực b&igrave;nh thường ngưỡng mặt ng&atilde; v&agrave;o trước mộ phần tr&ecirc;n cỏ, sắc mặt đột nhi&ecirc;n trầm tĩnh như thường, ch&iacute;nh l&agrave; đ&ocirc;i mắt giữa d&ograve;ng chuyển một cỗ ai phẫn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xuy&ecirc;n qua đến Đ&ocirc;ng H&aacute;n những năm cuối đ&iacute;ch đường thịnh từ trước đ&iacute;ch &yacute; tưởng thập phần đơn giản, lịch sử thượng đ&iacute;ch Qu&aacute;ch Gia trời sinh kỳ t&agrave;i, kỳ mưu chồng chất liệu sự như thần, hắn c&oacute; lẽ kh&ocirc;ng thể trở th&agrave;nh qu&acirc;n sự thi&ecirc;n t&agrave;i đ&iacute;ch c&aacute;i kia Qu&aacute;ch Gia, lại bằng v&agrave;o đối từ nay về sau trăm năm thi&ecirc;n hạ đại thế đi hướng r&otilde; như l&ograve;ng b&agrave;n tay, &iacute;t nhất c&oacute; thể l&agrave;m một c&aacute;i mưu lược thượng thừa đ&iacute;ch mưu sĩ, đ&atilde;i thi&ecirc;n hạ đại biến l&agrave; l&uacute;c, dấn th&acirc;n v&agrave;o minh chủ, tự khả cam đoan cả đời vinh hoa ph&uacute; qu&yacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ch&iacute; T&agrave;i, hiện giờ thi&ecirc;n tai kh&ocirc;ng ngừng, trong triều ngoại th&iacute;ch hoạn quan chuy&ecirc;n quyền g&acirc;y th&agrave;nh nh&acirc;n họa, Thần Ch&acirc;u đại địa người chết đ&oacute;i đầy đất, lưu d&acirc;n t&ugrave;y &yacute; c&oacute; thể thấy được, khả danh m&ocirc;n vọng tộc đều đang l&agrave;m c&aacute;i g&igrave;? Độn h&oacute;a đầu cơ t&iacute;ch trữ, gi&agrave;nh m&oacute;n l&atilde;i kếch s&ugrave;, thế gia trong v&ograve;ng đọc đủ thứ thi thư người lại đang l&agrave;m c&aacute;i g&igrave;? Thượng kh&ocirc;ng tư đền nợ nước an bang, hạ kh&ocirc;ng l&agrave;m y&ecirc;n ổn tạo ph&uacute;c một phương d&acirc;n ch&uacute;ng việc, trăm phương ngh&igrave;n kế v&agrave;o triều l&agrave;m quan cũng bất qu&aacute; l&agrave; v&igrave; gia tộc gi&agrave;nh lớn nhất tiện nghi, hiện giờ thi&ecirc;n tử c&ocirc;ng nhi&ecirc;n b&aacute;n quan thụ tước, c&ograve;n đ&acirc;y l&agrave; mất nước hiện ra!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i mi ti&ecirc;m một chọn, trầm giọng qu&aacute;t: &quot;Phụng Hiếu n&oacute;i cẩn thận!&quot; Theo sau hắn tả hữu tuần tra một phen, may mắn nơi đ&acirc;y hoang vắng ti&ecirc;n c&oacute; người t&iacute;ch, nếu kh&ocirc;ng Qu&aacute;ch Gia lời ấy nếu l&agrave; truyền đi ra ngo&agrave;i, tất nhi&ecirc;n l&agrave; g&acirc;y tr&ecirc;n th&acirc;n!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ha hả, Hi Ch&iacute; T&agrave;i c&ograve;n sợ ta Qu&aacute;ch Phụng Hiếu n&oacute;i thật ra sao kh&ocirc;ng? Hi Ch&iacute; T&agrave;i c&ograve;n sợ ta một c&aacute;i mười tuổi tiểu nhi n&oacute;i ra ngươi trong l&ograve;ng đăm chi&ecirc;u sao kh&ocirc;ng? Thi&ecirc;n tử ngu ngốc, triều thần v&ocirc; năng, địa phương quyền qu&yacute; cảnh th&aacute;i b&igrave;nh giả tạo, đ&aacute;ng thương thi&ecirc;n hạ ng&agrave;n vạn lần d&acirc;n ch&uacute;ng sống tạm độ nhật, vong đi, c&ugrave;ng với k&eacute;o d&agrave;i hơi t&agrave;n, kh&ocirc;ng bằng đại loạn l&uacute;c sau thi&ecirc;n hạ đại trị.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia trong l&ograve;ng buồn khổ, &aacute;p lực hồi l&acirc;u đ&iacute;ch t&igrave;nh cảm kh&ocirc;ng tự chủ được ph&aacute;t tiết đi ra, hắn đẳng kh&ocirc;ng được , hắn đẳng kh&ocirc;ng kịp bốn năm sau đ&iacute;ch khởi nghĩa Ho&agrave;ng C&acirc;n, hắn đẳng kh&ocirc;ng kịp ch&iacute;n năm về sau đ&iacute;ch Đổng Tr&aacute;c nhập Lạc Dương, đẳng kh&ocirc;ng kịp mười năm sau Quan Đ&ocirc;ng qu&acirc;n phạt đổng, đẳng kh&ocirc;ng kịp loạn thế gian h&ugrave;ng T&agrave;o Mạnh Đức khởi binh Trần Lưu , nếu l&agrave; thật sự đẳng minh chủ hiện thế, hắn liền c&ograve;n muốn nhịn nữa mười năm, khả qu&aacute; khứ đ&iacute;ch mười năm, hắn đ&atilde; muốn nhẫn đắc qu&aacute; nhiều , thế gia đệ tử đ&iacute;ch khi dễ c&ugrave;ng xem thường, h&agrave;o m&ocirc;n quyền qu&yacute; đ&iacute;ch &aacute;p b&aacute;ch, tới rồi hiện giờ, mẫu th&acirc;n đau khổ trong l&ograve;ng m&agrave; chết, l&acirc;m chung tiền nắm chặt ch&iacute;nh m&igrave;nh đ&iacute;ch hai tay, kh&oacute;c kh&ocirc;ng th&agrave;nh tiếng, lệ như chảy ra, cuối c&ugrave;ng chết kh&ocirc;ng nhắm mắt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia biết, mẫu th&acirc;n kh&ocirc;ng thể bu&ocirc;ng ch&iacute;nh m&igrave;nh, tại đ&acirc;y thế đạo b&ecirc;n trong ch&iacute;nh m&igrave;nh như thế n&agrave;o trữ h&agrave;ng đi xuống l&agrave; c&aacute;i đ&iacute;ch th&acirc;n tới chung tiền đều kh&ocirc;ng thể bu&ocirc;ng đ&iacute;ch trong l&ograve;ng tảng đ&aacute; lớn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Phụng Hiếu, ta biết ngươi &yacute;, cũng biết n&agrave;y thế đạo gian nan, đại h&aacute;n bốn trăm năm tới rồi h&ocirc;m nay, nếu n&oacute;i vận số đ&atilde; hết, ta l&agrave; kh&ocirc;ng tin, nếu l&agrave; c&oacute; minh qu&acirc;n đăng cơ, lấy l&ocirc;i đ&igrave;nh chi thế chỉnh đốn triều cương, qu&eacute;t sạch gian nịnh, lớn như vậy h&aacute;n vẫn l&agrave; c&oacute; thể cứu chữa đ&iacute;ch. Khả đương kim thi&ecirc;n tử ch&iacute;nh trực tr&aacute;ng ni&ecirc;n, dưới gối hai tử c&ograve;n ch&iacute;nh l&agrave; h&agrave;i đồng, n&agrave;y ph&aacute;p, chỉ sợ l&agrave; ta si t&acirc;m vọng tưởng .&quot; Hi Ch&iacute; T&agrave;i lắc đầu qu&aacute;n hạ mồm to rượu thủy, tr&ecirc;n mặt m&acirc;y m&ugrave; che phủ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thi&ecirc;n tử ch&iacute;nh trực tr&aacute;ng ni&ecirc;n kh&ocirc;ng giả, khả kh&ocirc;ng hiểu l&yacute; lẽ như lợn, cả ng&agrave;y trầm m&ecirc; nữ sắc, lại tham luyến t&agrave;i vật, quanh năm suốt th&aacute;ng xuống dưới, th&acirc;n thể ng&agrave;y c&agrave;ng sa s&uacute;t, kh&ocirc;ng ra mười năm đ&atilde; đem xuống mồ vi an.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đương nhi&ecirc;n, đ&acirc;y l&agrave; Qu&aacute;ch Gia trong l&ograve;ng đăm chi&ecirc;u, kh&ocirc;ng thể tuy&ecirc;n chi vu khẩu, d&ugrave; sao thi&ecirc;n tử ở ho&agrave;ng cung b&ecirc;n trong đ&iacute;ch sở t&aacute;c sở vi, th&acirc;n thể khỏe mạnh trạng huống, đều kh&ocirc;ng phải l&agrave; bọn họ n&agrave;y đ&oacute; b&igrave;nh d&acirc;n d&acirc;n ch&uacute;ng c&oacute; thể biết đến.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ch&iacute; T&agrave;i ngươi tố c&oacute; đại t&agrave;i, nếu l&agrave; tầm thường cả đời m&agrave; kh&ocirc;ng chỗ n&agrave;o vi, chỉ sợ ngươi ch&iacute;nh m&igrave;nh sẽ kh&ocirc;ng t&acirc;m cam đi.&quot; Qu&aacute;ch Gia nh&igrave;n l&ecirc;n tấm m&agrave;n đen hạ đ&iacute;ch bầu trời đ&ecirc;m, tinh thần dầy đặc, x&aacute;n nếu hạ hoa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i cười khổ lắc đầu, s&aacute;p thanh đạo: &quot;Ngươi ta đều l&agrave; h&agrave;n m&ocirc;n xuất th&acirc;n, Phụng Hiếu ngươi mặc d&ugrave; c&ograve;n trẻ, nhưng t&agrave;i thắng vu ta mấy lần, l&agrave; ta b&igrave;nh sinh tối k&iacute;nh nể c&ugrave;ng tự h&agrave;o đ&iacute;ch bằng hữu, người hiểu ta phi Phụng Hiếu mạc ch&uacute;c, ta ngay cả kh&ocirc;ng cam l&ograve;ng đầy ngập t&agrave;i hoa ch&igrave;m v&agrave;o, nhưng thời sự như thế, kh&ocirc;ng thể nề h&agrave; a. Một phi h&agrave;o m&ocirc;n vọng tộc, cử hiếu li&ecirc;m kh&oacute; hơn l&ecirc;n trời, hai v&ocirc; cự tư chi ph&uacute;, kia Đ&ocirc;ng Đ&ocirc; Lạc Dương minh m&atilde; yết gi&aacute; đ&iacute;ch quan chức, mong muốn m&agrave; kh&ocirc;ng thể th&agrave;nh. Mặc d&ugrave; muốn v&igrave; quan một phương tạo ph&uacute;c thương sinh linh, cũng l&agrave; l&ograve;ng c&oacute; dư m&agrave; lực kh&ocirc;ng đủ a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&oacute; lẽ đối lịch sử m&agrave; n&oacute;i, vội v&agrave;ng mười t&aacute;i l&uacute;c sau đ&oacute; l&agrave; thi&ecirc;n hạ đại loạn l&agrave; l&uacute;c, nhưng giờ n&agrave;y khắc n&agrave;y, đối Qu&aacute;ch Gia m&agrave; n&oacute;i, mười năm, vẫn l&agrave; một c&aacute;i xa xa kh&ocirc;ng hẹn đ&iacute;ch thời gian, nghĩ đến đ&acirc;y, Qu&aacute;ch Gia theo Hi Ch&iacute; T&agrave;i trong tay đoạt qu&aacute; rượu hồ l&ocirc;, qu&aacute;n tiếp theo khẩu l&uacute;c sau, n&oacute;i: &quot;Hiện giờ thi&ecirc;n hạ mất nước hiện ra đ&atilde; lộ, ngươi ta hai người chỉ đợi thời cơ đ&atilde; đến, liền c&oacute; d&ugrave;ng v&otilde; nơi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i dở kh&oacute;c dở cười, chỉ v&agrave;o Qu&aacute;ch Gia khuy&ecirc;n giải an ủi đạo: &quot;Phụng Hiếu, rượu l&agrave; mặc tr&agrave;ng độc dược, ngươi như thế c&ograve;n trẻ liền tham luyến vật ấy, ng&agrave;n vạn lần phải cẩn thận th&acirc;n thể của ch&iacute;nh m&igrave;nh a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia mạt sạch sẽ miệng, tựa tiếu phi tiếu địa nh&igrave;n thấy Hi Ch&iacute; T&agrave;i n&oacute;i: &quot;Rượu kh&ocirc;ng rời th&acirc;n đ&iacute;ch Hi Ch&iacute; T&agrave;i chẳng lẽ cũng biết rượu l&agrave; mặc tr&agrave;ng độc dược? Chớ kh&ocirc;ng phải l&agrave; sợ ta uống hết của ngươi rượu ngon t&agrave;i ra lời ấy? Ha ha ha, nếu l&agrave; Văn Nhược nghe xong lời n&agrave;y, chỉ sợ tuyệt kh&ocirc;ng tin tưởng.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&ugrave;ng Qu&aacute;ch Gia đấu v&otilde; mồm chỉ c&oacute; thể l&agrave; kh&ocirc;ng thể nề h&agrave; đ&iacute;ch Hi Ch&iacute; T&agrave;i kh&ocirc;ng n&oacute;i g&igrave; m&agrave; chống đỡ, nghĩ nghĩ l&uacute;c sau đột nhi&ecirc;n biểu t&igrave;nh nghi&ecirc;m t&uacute;c địa n&oacute;i: &quot;Ng&agrave;y h&ocirc;m trước ta b&aacute;i phỏng Văn Nhược khi, nam dương danh sĩ H&agrave; Ngung vừa mới ở tu&acirc;n gia l&agrave;m kh&aacute;ch, ngươi đo&aacute;n H&agrave; Ngung nh&igrave;n thấy Văn Nhược khi n&oacute;i g&igrave; đ&oacute;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia tiếp lời đ&atilde; n&oacute;i: &quot;Vương t&aacute; t&agrave;i!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i mặt lộ vẻ vẻ mặt, l&uacute;ng ta l&uacute;ng t&uacute;ng hỏi han: &quot;Ngươi ch&iacute;nh l&agrave; theo nơi kh&aacute;c nghe n&oacute;i việc n&agrave;y?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia lắc đầu đạo: &quot;N&agrave;y ba ng&agrave;y ta canh giữ ở mẫu th&acirc;n trước mộ phần, nửa bước chưa ly, ngươi ta hai người đều l&agrave; thế nh&acirc;n trong mắt cao ngạo đồ đệ, ng&agrave;y thường c&ugrave;ng c&ugrave;ng lui tới đ&iacute;ch bằng hữu c&oacute; thể đếm được tr&ecirc;n đầu ng&oacute;n tay, m&agrave; ở l&uacute;c n&agrave;y c&oacute; thể đến thăm ta Qu&aacute;ch Phụng Hiếu đ&iacute;ch, trừ ngươi ra Hi Ch&iacute; T&agrave;i, chỉ sợ chỉ c&oacute; Tu&acirc;n Văn Nhược , m&agrave; nghe ngươi ng&ocirc;n, tu&acirc;n gia c&oacute; kh&aacute;ch, Văn Nhược tự nhi&ecirc;n kh&ocirc;ng c&oacute; phương tiện rời đi, ai c&oacute; thể cho ta mật b&aacute;o cho biết, b&aacute;o cho tu&acirc;n gia đ&atilde; xảy ra c&aacute;i g&igrave;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&acirc;m tư nhanh quay ngược trở lại bất qu&aacute; một c&aacute;i chớp mắt trong l&uacute;c đ&oacute;, Hi Ch&iacute; T&agrave;i cũng biết Qu&aacute;ch Gia lời n&oacute;i phi hư, trong l&ograve;ng kinh nghi c&agrave;ng s&acirc;u, hỏi: &quot;Vậy ngươi như thế n&agrave;o sao biết được H&agrave; Ngung lời n&oacute;i?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia kh&ocirc;ng đ&aacute;p hỏi lại: &quot;Văn Nhược t&agrave;i, kh&ocirc;ng xứng vương t&aacute; chi thực?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i &aacute; khẩu kh&ocirc;ng trả lời được, trầm mặc thật l&acirc;u sau sau thở d&agrave;i: &quot;Văn Nhược x&aacute;c thực c&oacute; vương t&aacute; t&agrave;i, H&agrave; Ngung tuệ nh&atilde;n thức nh&acirc;n, lời ấy kh&ocirc;ng giả. Xa muốn l&agrave;m năm ngươi bất qu&aacute; s&aacute;u tuổi chi đồng tiện c&ugrave;ng ta c&ugrave;ng Văn Nhược n&oacute;i thoải m&aacute;i cổ kim, thường thường ngữ ra kinh người khiến người tỉnh ngộ, Văn Nhược lời n&oacute;i cử chỉ c&oacute; thể n&oacute;i phong c&aacute;ch qu&yacute; ph&aacute;i, kiến thức bất ph&agrave;m, vương t&aacute; t&agrave;i, đ&uacute;ng mức, chỉ tiếc, đ&aacute;ng tiếc...&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Chỉ tiếc anh h&ugrave;ng kh&ocirc;ng đất dụng v&otilde; a. D&ugrave; c&oacute; vương t&aacute; t&agrave;i, nếu v&ocirc; minh chủ, kinh thi&ecirc;n vĩ địa t&agrave;i lại như thế n&agrave;o?&quot; Qu&aacute;ch Gia khinh cười n&oacute;i, n&agrave;y trong chốc l&aacute;t hắn đ&atilde; muốn điều chỉnh tốt t&acirc;m t&igrave;nh, b&aacute;n hay n&oacute;i giỡn đứng l&ecirc;n, lại đều l&agrave; đại lời n&oacute;i thật.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i im lặng kh&ocirc;ng n&oacute;i g&igrave;, đ&uacute;ng vậy, Tu&acirc;n &Uacute;c t&agrave;i hoa cao, To&aacute;nh Xuy&ecirc;n trong v&ograve;ng khủng c&oacute; thể cập người, ai cũng xem qua tiền bạn tri kỉ bạn tốt Qu&aacute;ch Gia, khả xuất th&acirc;n sĩ tộc đ&iacute;ch Tu&acirc;n Văn Nhược c&ograve;n đền nợ nước kh&ocirc;ng cửa, huống chi l&agrave; bần h&agrave;n đệ tử Qu&aacute;ch Phụng Hiếu đ&acirc;u? Thương thi&ecirc;n bất c&ocirc;ng, v&igrave; sao cho bọn hắn một th&acirc;n t&agrave;i hoa rồi lại l&agrave;m cho bọn họ sinh kh&ocirc;ng ph&ugrave;ng khi đ&acirc;u?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i trong l&ograve;ng trung thở d&agrave;i thở ngắn, Qu&aacute;ch Gia lại đứng l&ecirc;n gi&atilde;n ra g&acirc;n cốt, b&aacute;n ra từng bước, dưới ch&acirc;n lảo đảo su&yacute;t nữa ng&atilde; quỵ, may mắn Hi Ch&iacute; T&agrave;i tay mắt lanh lẹ một phen đỡ lấy, cười khổ m&agrave; n&oacute;i: &quot;Phụng Hiếu a, về sau n&agrave;y rượu a, vẫn l&agrave; &iacute;t d&iacute;nh tuyệt vời.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia đẩy ra Hi Ch&iacute; T&agrave;i, ha ha cười, ba bước lay động, năm bước nho&aacute;ng l&ecirc;n một c&aacute;i địa hướng gia đi đến, vừa đi vừa cất cao giọng n&oacute;i:</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhĩ căn đắc th&iacute;nh cầm sơ sướng, t&acirc;m địa vong ky tửu b&aacute;n h&agrave;m</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhược sử khải kỳ ki&ecirc;m giải t&uacute;y, ứng ng&ocirc;n tứ nhạc bất ng&ocirc;n tam (b&agrave;i thơ post nguy&ecirc;n văn, cv ra thấy kỳ qu&aacute;)</p>\r\n\r\n<hr />', '0', 'thu-nhat-chuong-truoc-mo-phan-doi-thoai', 5, '56547', '2022-08-03 02:40:46', '2022-09-29 14:01:37');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(20, '2', 'Đệ nhị chương tiếp tế dân chạy nạn', '<p>Về đến nh&agrave; trung ph&ograve;ng ốc sơ s&agrave;i sau đ&iacute;ch Qu&aacute;ch Gia c&ugrave;ng Hi Ch&iacute; T&agrave;i uống say b&aacute;n t&uacute;c, đồng th&aacute;p m&agrave; mi&ecirc;n, thẳng đến h&ocirc;m sau buổi trưa t&agrave;i từ từ chuyển tỉnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cổ đại cồn độ tinh khiết kh&ocirc;ng cao, say rượu l&uacute;c sau cũng kh&ocirc;ng nhiều lắm di chứng, Qu&aacute;ch Gia đứng dậy sau rửa mặt sửa sang lại một phen ra gia m&ocirc;n, vừa mới nh&igrave;n đến tr&ecirc;n đường d&ograve;ng người bắt đầu khởi động, xanh xao v&agrave;ng vọt quần &aacute;o r&aacute;ch n&aacute;t đ&iacute;ch lưu d&acirc;n chung quanh khất thực, cảnh tượng v&ocirc; c&ugrave;ng th&ecirc; thảm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đang ở Qu&aacute;ch Gia ngẩn người hết sức, một vị diện mạo anh tuấn thần quang nội liễm đ&iacute;ch lam sam thanh ni&ecirc;n đi tới Qu&aacute;ch Gia trước mặt, n&oacute;i hỏi: &quot;Phụng Hiếu suy nghĩ c&aacute;i g&igrave;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia bừng tỉnh bừng tỉnh, quay đầu vừa thấy, thở d&agrave;i: &quot;Văn Nhược, h&ocirc;m nay lại tới nữa nhiều &iacute;t lưu d&acirc;n?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Phong thần tuấn l&atilde;ng đ&iacute;ch lam sam thanh ni&ecirc;n đ&uacute;ng l&agrave; Tu&acirc;n &Uacute;c, hắn sắc mặt ngưng trọng địa n&oacute;i: &quot;Chỉ sợ kh&ocirc;ng dưới một vạn. Trung Nguy&ecirc;n mấy năm li&ecirc;n tục thi&ecirc;n tai kh&ocirc;ng ngừng, năm nay lại ph&ugrave;ng đại hạn, thu hoạch chỉ sợ kh&ocirc;ng đủ năm rồi ba th&agrave;nh, rất nhiều địa phương đ&iacute;ch d&acirc;n ch&uacute;ng thực kh&ocirc;ng c&oacute; kết quả ph&uacute;c, &aacute;o r&aacute;ch quần manh, t&igrave;nh cảnh gian nan a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cười lạnh n&oacute;i: &quot;Kh&ocirc;ng biết h&agrave;o m&ocirc;n đại hộ gia đ&iacute;ch kho l&uacute;a l&yacute; đ&ecirc;m qua lại lạn mấy đấu lương thực đ&acirc;u.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c mặc kh&ocirc;ng l&ecirc;n tiếng kh&ocirc;ng n&oacute;i g&igrave; m&agrave; chống đỡ, hắn biết Qu&aacute;ch Gia đối thế gia c&oacute; th&agrave;nh kiến, kh&ocirc;ng, kh&ocirc;ng thể n&oacute;i l&agrave; th&agrave;nh kiến, phải n&oacute;i Qu&aacute;ch Gia đối thế gia đ&iacute;ch c&aacute;i nh&igrave;n nhất ch&acirc;m kiến huyết, chẳng qua đổi l&agrave;m người b&ecirc;n ngo&agrave;i đến xem ch&iacute;nh l&agrave; căm th&ugrave; .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hai người đang về tới Qu&aacute;ch Gia trong nh&agrave;, vừa l&uacute;c Hi Ch&iacute; T&agrave;i cũng đ&atilde; tỉnh lại, cũng ở trong ph&ograve;ng đ&aacute;nh đ&agrave;n, Qu&aacute;ch Gia Tu&acirc;n &Uacute;c cũng kh&ocirc;ng quấy rầy, tĩnh tọa hai b&ecirc;n, nhắm mắt nghe.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:1884px; top:579px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p>Về đến nh&agrave; trung ph&ograve;ng ốc sơ s&agrave;i sau đ&iacute;ch Qu&aacute;ch Gia c&ugrave;ng Hi Ch&iacute; T&agrave;i uống say b&aacute;n t&uacute;c, đồng th&aacute;p m&agrave; mi&ecirc;n, thẳng đến h&ocirc;m sau buổi trưa t&agrave;i từ từ chuyển tỉnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cổ đại cồn độ tinh khiết kh&ocirc;ng cao, say rượu l&uacute;c sau cũng kh&ocirc;ng nhiều lắm di chứng, Qu&aacute;ch Gia đứng dậy sau rửa mặt sửa sang lại một phen ra gia m&ocirc;n, vừa mới nh&igrave;n đến tr&ecirc;n đường d&ograve;ng người bắt đầu khởi động, xanh xao v&agrave;ng vọt quần &aacute;o r&aacute;ch n&aacute;t đ&iacute;ch lưu d&acirc;n chung quanh khất thực, cảnh tượng v&ocirc; c&ugrave;ng th&ecirc; thảm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đang ở Qu&aacute;ch Gia ngẩn người hết sức, một vị diện mạo anh tuấn thần quang nội liễm đ&iacute;ch lam sam thanh ni&ecirc;n đi tới Qu&aacute;ch Gia trước mặt, n&oacute;i hỏi: &quot;Phụng Hiếu suy nghĩ c&aacute;i g&igrave;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia bừng tỉnh bừng tỉnh, quay đầu vừa thấy, thở d&agrave;i: &quot;Văn Nhược, h&ocirc;m nay lại tới nữa nhiều &iacute;t lưu d&acirc;n?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Phong thần tuấn l&atilde;ng đ&iacute;ch lam sam thanh ni&ecirc;n đ&uacute;ng l&agrave; Tu&acirc;n &Uacute;c, hắn sắc mặt ngưng trọng địa n&oacute;i: &quot;Chỉ sợ kh&ocirc;ng dưới một vạn. Trung Nguy&ecirc;n mấy năm li&ecirc;n tục thi&ecirc;n tai kh&ocirc;ng ngừng, năm nay lại ph&ugrave;ng đại hạn, thu hoạch chỉ sợ kh&ocirc;ng đủ năm rồi ba th&agrave;nh, rất nhiều địa phương đ&iacute;ch d&acirc;n ch&uacute;ng thực kh&ocirc;ng c&oacute; kết quả ph&uacute;c, &aacute;o r&aacute;ch quần manh, t&igrave;nh cảnh gian nan a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cười lạnh n&oacute;i: &quot;Kh&ocirc;ng biết h&agrave;o m&ocirc;n đại hộ gia đ&iacute;ch kho l&uacute;a l&yacute; đ&ecirc;m qua lại lạn mấy đấu lương thực đ&acirc;u.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c mặc kh&ocirc;ng l&ecirc;n tiếng kh&ocirc;ng n&oacute;i g&igrave; m&agrave; chống đỡ, hắn biết Qu&aacute;ch Gia đối thế gia c&oacute; th&agrave;nh kiến, kh&ocirc;ng, kh&ocirc;ng thể n&oacute;i l&agrave; th&agrave;nh kiến, phải n&oacute;i Qu&aacute;ch Gia đối thế gia đ&iacute;ch c&aacute;i nh&igrave;n nhất ch&acirc;m kiến huyết, chẳng qua đổi l&agrave;m người b&ecirc;n ngo&agrave;i đến xem ch&iacute;nh l&agrave; căm th&ugrave; .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hai người đang về tới Qu&aacute;ch Gia trong nh&agrave;, vừa l&uacute;c Hi Ch&iacute; T&agrave;i cũng đ&atilde; tỉnh lại, cũng ở trong ph&ograve;ng đ&aacute;nh đ&agrave;n, Qu&aacute;ch Gia Tu&acirc;n &Uacute;c cũng kh&ocirc;ng quấy rầy, tĩnh tọa hai b&ecirc;n, nhắm mắt nghe.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i sở đạn chi nhạc hi &acirc;m hướng c&ugrave;ng, đại kh&iacute; ở ngo&agrave;i c&agrave;ng hiển b&agrave;ng bạc chi thế, lệnh người nghe nội t&acirc;m k&iacute;ch động, &yacute; ch&iacute; chiến đấu sục s&ocirc;i, ngưng l&acirc;u kh&ocirc;ng ti&ecirc;u tan, Tu&acirc;n &Uacute;c lại trong l&ograve;ng trung khen: nghe thấy Ch&iacute; T&agrave;i sở tấu chi nhạc, đ&atilde; biết &yacute; ch&iacute; ba phần, h&ugrave;ng t&acirc;m to lớn, ch&iacute; kh&iacute; cao, nếu d&ugrave;ng chi thoả đ&aacute;ng, kh&ocirc;ng thể số lượng!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đương cuối c&ugrave;ng một c&aacute;i tiếng đ&agrave;n lạc định l&uacute;c sau, b&ecirc;n trong thật l&acirc;u kh&ocirc;ng tiếng động, thật l&acirc;u sau, Qu&aacute;ch Gia t&agrave;i c&ugrave;ng Tu&acirc;n &Uacute;c đồng ra một hơi, b&igrave;nh phục quyết t&acirc;m trung tr&agrave;o d&acirc;ng, Hi Ch&iacute; T&agrave;i cũng cầm lấy quy&ecirc;n bố ch&agrave; lau hai tay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ba người tương giao bất luận t&ocirc;n trưởng, chẳng ph&acirc;n biệt được gi&aacute; cả thế n&agrave;o, chỉ tại thổ lộ t&igrave;nh cảm, bởi vậy thiếu gi&acirc;y l&aacute;t nịnh h&oacute;t đ&iacute;ch t&aacute;n thưởng, lễ nghi phiền phức lại kh&ocirc;ng để ở trong l&ograve;ng, đ&oacute; l&agrave; như thế, t&agrave;i l&agrave;m cho ba người t&igrave;nh nghĩa kh&oacute; được.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bưng l&ecirc;n ba ch&eacute;n nước ch&egrave; xanh đ&atilde;i kh&aacute;ch, Qu&aacute;ch Gia hướng Tu&acirc;n &Uacute;c hỏi: &quot;Văn Nhược n&agrave;y đến, g&acirc;y n&ecirc;n chuyện g&igrave;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c thẹn trong l&ograve;ng, sắc mặt xấu hổ địa n&oacute;i: &quot;Phụng Hiếu tang mẫu l&uacute;c sau, nh&agrave; của ta trung c&oacute; kh&aacute;ch kh&ocirc;ng tiện ra ngo&agrave;i, n&agrave;y đến, một l&agrave; thỉnh tội. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&oacute;i c&ograve;n chưa n&oacute;i ho&agrave;n, Qu&aacute;ch Gia liền cười đ&aacute;nh gảy hắn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Văn Nhược kh&ocirc;ng cần như thế, ta biết ngươi t&acirc;m &yacute; c&oacute; thể.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c mặt lộ vẻ cười khổ, tiếp tục n&oacute;i: &quot;Thứ hai th&ocirc;i, ta phụ ra mặt c&ugrave;ng Qu&aacute;ch gia chu to&agrave;n, từ nay về sau Qu&aacute;ch gia tất sẽ kh&ocirc;ng t&aacute;i sinh sự t&igrave;nh, ngo&agrave;i ra, t&aacute;i tặng ruộng tốt ng&agrave;n mẫu lấy an ủi của ngươi tang mẫu chi đau.&quot;</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia nghe xong, cười ha ha đạo: &quot;Ruộng tốt ng&agrave;n mẫu? Qu&aacute;ch gia thật sự l&agrave; khinh người qu&aacute; đ&aacute;ng, nh&agrave; của ta trung bần h&agrave;n, cũng kh&ocirc;ng n&ocirc; bộc, ng&agrave;n mẫu nơi, người n&agrave;o đến canh?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i c&ugrave;ng Tu&acirc;n &Uacute;c cũng đồng dạng cười khổ kh&ocirc;ng th&ocirc;i, n&agrave;y phải thay đổi người kh&aacute;c, chỉ sợ &iacute;t nhất đắc cao hứng nửa ng&agrave;y đi, tốt xấu cũng l&agrave; ruộng tốt ng&agrave;n mẫu, khả trước mặt n&agrave;y d&ugrave; sao cũng l&agrave; c&oacute; thi&ecirc;n t&agrave;i nhanh tr&iacute; đ&iacute;ch Qu&aacute;ch Gia, trong nh&aacute;y mắt liền hiểu r&otilde; trong đ&oacute; huyền cơ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kia Phụng Hiếu, &yacute; tứ của ngươi đ&acirc;u?&quot; Tu&acirc;n &Uacute;c kh&ocirc;ng &ocirc;n kh&ocirc;ng hỏa hỏi han, hắn đ&atilde; biết Qu&aacute;ch Gia c&oacute; đối s&aacute;ch, hắn thậm ch&iacute; nghĩ tới ch&iacute;nh m&igrave;nh nếu l&agrave; bị v&acirc;y Qu&aacute;ch Gia đ&iacute;ch lập trường n&ecirc;n như thế n&agrave;o l&agrave;m, đơn giản ch&iacute;nh l&agrave; b&aacute;n địa, mấu chốt ch&iacute;nh l&agrave; b&aacute;n cho ai.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia c&ograve;n chưa trả lời, Hi Ch&iacute; T&agrave;i đ&atilde; muốn giận dữ đạo: &quot;Qu&aacute;ch gia đứng đầu đ&aacute;ng giận đến cực điểm, nhưng lại dục l&agrave;m nhục Phụng Hiếu! Thật giận!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c c&ugrave;ng Qu&aacute;ch Gia liếc nhau, &yacute; cười rất đậm, Qu&aacute;ch Gia trong l&ograve;ng s&aacute;ng sủa, lại hay l&agrave; hỏi đạo: &quot;Ch&iacute; T&agrave;i lời ấy &yacute; g&igrave;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i phiết mắt Qu&aacute;ch Gia sau cười mắng: &quot;Qu&aacute;ch Phụng Hiếu ngươi trong l&ograve;ng so với ai kh&aacute;c đều hiểu được, Qu&aacute;ch gia l&agrave;m như vậy đơn giản l&agrave; cho ngươi qua tay sẽ đem n&agrave;y đất vườn b&aacute;n cho bọn họ đổi lấy tiền t&agrave;i, To&aacute;nh Xuy&ecirc;n tuấn kiệt giai n&oacute;i ngươi Qu&aacute;ch Phụng Hiếu thanh cao ki&ecirc;u ngạo, hiện giờ ngươi nếu l&agrave; b&aacute;n địa đổi tiền, tất bị người b&ecirc;n ngo&agrave;i nhạo b&aacute;ng. Ngươi b&aacute;n c&ograve;n Qu&aacute;ch gia, c&ograve;n đ&acirc;y l&agrave; đại sỉ! B&aacute;n c&ugrave;ng người b&ecirc;n ngo&agrave;i, cũng lạc người kh&aacute;c mượn cớ, bị người nhục nh&atilde;, nếu ngươi kh&ocirc;ng b&aacute;n nơi đ&acirc;y, ruộng tốt hoang phế, kia lại người b&ecirc;n ngo&agrave;i chế ngạo chi theo, n&oacute;i ngươi Qu&aacute;ch Phụng Hiếu bất qu&aacute; một lời trẻ con đầy tớ nhỏ, c&oacute; thể ng&ocirc;n thiện biện lại chỉ c&oacute; thể n&oacute;i bốc n&oacute;i ph&eacute;t, ruộng tốt ng&agrave;n mẫu thượng kh&ocirc;ng thể tr&igrave;, g&igrave; đ&agrave;m mặt kh&aacute;c?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ha ha ha ha. . .&quot; Qu&aacute;ch Gia c&ugrave;ng Tu&acirc;n &Uacute;c cất tiếng cười to, việc n&agrave;y bọn họ ba người trong l&ograve;ng đều đ&atilde; nghĩ đến, lợi v&agrave; hại lợi hại sớm t&iacute;nh trước kỹ c&agrave;ng, kh&oacute; xử kh&ocirc;ng ở c&oacute; kh&ocirc;ng nh&igrave;n ra n&agrave;y ruộng tốt ng&agrave;n mẫu đ&iacute;ch d&atilde; t&acirc;m chỗ,nơi, m&agrave; l&agrave; như thế n&agrave;o ứng đối mới l&agrave; nan đề.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Phụng Hiếu trong l&ograve;ng k&yacute; c&oacute; đối s&aacute;ch, sao kh&ocirc;ng n&oacute;i ra? Việc n&agrave;y ta c&agrave;ng nghĩ đều kh&oacute; c&oacute; thể nghĩ ra một c&aacute;i vạn to&agrave;n chi s&aacute;ch.&quot; Tu&acirc;n &Uacute;c nh&igrave;n thẳng Qu&aacute;ch Gia, nh&igrave;n hắn c&oacute; g&igrave; thượng s&aacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hi Ch&iacute; T&agrave;i đồng dạng cũng thập phần t&ograve; m&ograve;, kỳ mưu chồng chất đ&iacute;ch Qu&aacute;ch Phụng Hiếu như thế n&agrave;o h&oacute;a giải n&agrave;y nan đề.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&iacute; định thần nh&agrave;n đ&iacute;ch Qu&aacute;ch Gia trầm ng&acirc;m sau một l&uacute;c l&acirc;u mở miệng đạo: &quot;Văn Nhược, ta mặc d&ugrave; tuổi nhỏ, nhưng ngươi biết l&ograve;ng ta, l&uacute;c n&agrave;y, ta c&oacute; một c&aacute;i y&ecirc;u cầu qu&aacute; đ&aacute;ng, hy vọng Văn Nhược sẽ gi&uacute;p ta một lần.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&ograve; m&ograve; đ&iacute;ch Tu&acirc;n &Uacute;c thử nghĩ: chẳng lẽ Phụng Hiếu phải ng&agrave;n mẫu ruộng tốt b&aacute;n c&ugrave;ng ta? Kh&ocirc;ng, như vậy giống nhau hội lạc d&acirc;n cư thực, kia? Tặng c&ugrave;ng ta? Như thế c&aacute;i biện ph&aacute;p, nhưng cũng kh&oacute; n&eacute;n từ từ ch&uacute;ng khẩu mắng hắn Qu&aacute;ch Phụng Hiếu leo l&ecirc;n chi ngại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Phụng Hiếu c&oacute; g&igrave; kh&oacute; xử cứ việc ng&ocirc;n chi, đủ khả năng việc, ta tất kh&ocirc;ng chối từ.&quot; Tu&acirc;n &Uacute;c thiệt t&igrave;nh n&oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia nghĩ nghĩ l&uacute;c sau n&oacute;i: &quot;Thỉnh Văn Nhược cho ta mượn ba trăm hộ nh&agrave; một năm mạng sống chi lương.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c c&ugrave;ng Hi Ch&iacute; T&agrave;i đầy mặt nghi hoặc vẻ, hai người cũng chưa đo&aacute;n trước đến Qu&aacute;ch Gia sở phun n&oacute;i như vậy đều kh&ocirc;ng phải l&agrave; bọn họ sở liệu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;N&agrave;y, n&agrave;y thật v&ocirc; phương, việc n&agrave;y ta khả l&agrave;m chủ, bất qu&aacute;, Phụng Hiếu c&oacute; kh&ocirc;ng đem ngươi trong l&ograve;ng đăm chi&ecirc;u n&oacute;i thẳng ra, l&ograve;ng ta trung thập phần t&ograve; m&ograve; Phụng Hiếu suy nghĩ của ngươi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c một ngụm đ&aacute;p ứng, ch&iacute;nh l&agrave; c&ugrave;ng Hi Ch&iacute; T&agrave;i giống nhau, mặt lộ vẻ suy tư, trong l&ograve;ng đại kỳ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cười n&oacute;i: &quot;Qu&aacute;ch gia bất qu&aacute; l&agrave; liệu định ta v&ocirc; c&agrave;y ruộng người th&ocirc;i, chỉ cần giải quyết n&agrave;y nan đề, c&ograve;n ph&aacute;t sầu c&aacute;i g&igrave;?&quot;</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lời vừa n&oacute;i ra, Hi Ch&iacute; T&agrave;i c&ugrave;ng Tu&acirc;n &Uacute;c ngầm hiểu, trong đầu liền nghĩ tới khất thực no bụng đ&iacute;ch d&acirc;n chạy nạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ba người kh&ocirc;ng hề ng&ocirc;n ngữ, nh&igrave;n nhau sau một l&uacute;c l&acirc;u, bỗng nhi&ecirc;n c&ugrave;ng k&ecirc;u l&ecirc;n cười to, Hi Ch&iacute; T&agrave;i lại sau khi cười xong vỗ tay khen: &quot;Phụng Hiếu đi n&agrave;y việc thiện, một khả l&agrave;m cho Qu&aacute;ch gia sở đồ thất bại, hai khả t&iacute;ch đức tể d&acirc;n ch&uacute;ng, thật sự l&agrave; c&ocirc;ng đức v&ocirc; lượng.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; Qu&aacute;ch Gia trong l&ograve;ng sở đồ cũng kh&ocirc;ng chỉ Hi Ch&iacute; T&agrave;i sở giảng như vậy &iacute;t, về phần c&agrave;ng nhiều, ng&agrave;y sau gặp mặt sẽ hiểu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia mặc d&ugrave; c&ugrave;ng Tu&acirc;n &Uacute;c l&agrave; bạn tri kỉ bạn tốt, t&aacute; lương một chuyện cũng trịnh trọng v&ocirc; c&ugrave;ng, lập hạ chứng từ, đồng &yacute; l&agrave;m chứng, ước định ba năm trong v&ograve;ng theo giai đoạn trả lại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một th&aacute;ng l&uacute;c sau, Qu&aacute;ch Gia danh nghĩa đ&iacute;ch ruộng tốt ng&agrave;n mẫu hơn nữa bổn gia ph&iacute;a trước c&oacute; trăm mẫu nơi c&ugrave;ng đ&atilde; c&oacute; lao động trồng trọt, m&agrave; ở cự đồng ruộng c&aacute;ch đ&oacute; kh&ocirc;ng xa đ&iacute;ch rừng c&acirc;y giữ cũng kiến nổi l&ecirc;n đơn sơ đ&iacute;ch cư thất, trong đ&oacute; lớn nhất đ&iacute;ch một gian nh&agrave; gỗ b&ecirc;n trong, truyền ra lang lảnh đọc s&aacute;ch thanh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đứng ở ngo&agrave;i ph&ograve;ng đ&iacute;ch Tu&acirc;n &Uacute;c tr&ecirc;n mặt b&igrave;nh tĩnh, trong l&ograve;ng lại kh&oacute; c&oacute; thể tự chế địa phập phồng kh&ocirc;ng chừng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Phụng Hiếu, thực thi&ecirc;n t&agrave;i cũng!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&iacute;nh m&igrave;nh thượng bất qu&aacute; mười tuổi đ&iacute;ch thiếu ni&ecirc;n, lại d&aacute;m dạy thư dục nh&acirc;n, tại đ&acirc;y đơn sơ đ&iacute;ch nh&agrave; gỗ b&ecirc;n trong gi&aacute;o h&oacute;a bần d&acirc;n người ấy, cũng kh&ocirc;ng biết n&agrave;y gần trăm đ&iacute;ch đứa b&eacute; tương lai c&oacute; kh&ocirc;ng b&aacute;o đ&aacute;p Qu&aacute;ch Phụng Hiếu đ&iacute;ch nổi khổ t&acirc;m!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia vừa mới n&oacute;i xong kh&oacute;a đi ra nh&agrave; gỗ, vừa vặn b&iacute;nh kiến Tu&acirc;n &Uacute;c c&ugrave;ng Hi Ch&iacute; T&agrave;i, Hi Ch&iacute; T&agrave;i l&agrave; ứng với Qu&aacute;ch Gia chi y&ecirc;u, tiến đến giảng b&agrave;i, d&ugrave; sao hắn cũng l&agrave; lẻ loi một m&igrave;nh, v&ocirc; khi&ecirc;n v&ocirc; quải, n&agrave;y bồi dưỡng nh&acirc;n t&agrave;i việc cũng c&ugrave;ng hắn &yacute;, vui vẻ nhận.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng thật ra Tu&acirc;n &Uacute;c xuất th&acirc;n sĩ tộc, kh&ocirc;ng c&oacute; khả năng bu&ocirc;ng d&aacute;ng người đến l&agrave;m n&agrave;y bị h&agrave;o m&ocirc;n vọng tộc sở nhạo b&aacute;ng việc, th&acirc;n phận c&oacute; hạn, thật sự kh&ocirc;ng thể nề h&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Tiểu th&aacute;i c&ocirc;ng, thao thao bất tuyệt vừa l&ecirc;n ngọ, chỉ sợ miệng kh&ocirc; đi, đến, tiểu th&aacute;i c&ocirc;ng thỉnh uống nước.&quot; Tu&acirc;n &Uacute;c tr&ecirc;u ghẹo địa đệ thượng một ch&eacute;n nước, trong mắt lộ vẻ chế nhạo vẻ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia hồi hắn một c&aacute;i xem thường, o&aacute;n thầm kh&ocirc;ng th&ocirc;i: c&aacute;i kia ai ng&agrave;n đao đ&iacute;ch l&atilde;o đạo!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mấy năm tiền c&oacute; c&aacute;i l&atilde;o đạo c&ugrave;ng Qu&aacute;ch Gia c&oacute; gặp mặt một lần, xưng Qu&aacute;ch Gia l&agrave; khương th&aacute;i c&ocirc;ng chuyển thế, liền c&oacute; tiểu th&aacute;i c&ocirc;ng đ&iacute;ch danh hiệu, vốn thời gian đ&atilde; l&acirc;u, sớm kh&ocirc;ng người hỏi thăm, khả gần nhất tiếp tế lưu d&acirc;n, d&acirc;n ch&uacute;ng trong l&uacute;c đ&oacute; Qu&aacute;ch Gia thanh danh đại t&aacute;o, liền c&oacute; người hiểu chuyện th&acirc;m lấy Qu&aacute;ch Gia chuyện cũ, &yacute; đồ hơn giải Qu&aacute;ch Gia một &iacute;t, v&igrave; thế, n&agrave;y tiểu th&aacute;i c&ocirc;ng đ&iacute;ch danh hiệu cũng đ&atilde; bị trở m&igrave;nh đi ra, hiện tại khen ngược, hắn tiếp tế đ&iacute;ch ba trăm hộ lưu d&acirc;n đều xưng hắn vi tiểu th&aacute;i c&ocirc;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Phụng Hiếu, n&agrave;y đ&oacute; ngươi đều l&agrave; nghĩ như thế n&agrave;o đi ra đ&iacute;ch? Ta Tu&acirc;n &Uacute;c mấy năm gần đ&acirc;y coi như l&agrave; kiến thức rộng r&atilde;i , kinh đ&ocirc; Lạc Dương đại hiền tr&iacute; tuệ người khi rảnh rỗi c&oacute; mặt đ&agrave;m đ&iacute;ch cơ hội, ch&iacute;nh l&agrave; c&oacute; thể nghĩ ra n&agrave;y đ&oacute; đ&iacute;ch, chỉ c&oacute; ngươi Qu&aacute;ch Phụng Hiếu a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c chỉ ch&iacute;nh l&agrave; Qu&aacute;ch Gia k&ecirc;u n&agrave;y bảy t&aacute;m tuổi đứa nhỏ sở dụng đ&iacute;ch phương ph&aacute;p, biết chữ d&ugrave;ng gh&eacute;p vần, c&oacute; nghĩa d&ugrave;ng c&ocirc;ng thức từ từ, m&agrave; n&agrave;y đ&oacute;, đều l&agrave;m cho Tu&acirc;n &Uacute;c c&ugrave;ng Hi Ch&iacute; T&agrave;i tấm tắc lấy l&agrave;m kỳ, n&agrave;y chăm học hảo hỏi đ&iacute;ch bọn nhỏ kh&ocirc;ng ra một th&aacute;ng liền c&oacute; thể thức ng&agrave;n tự, lại c&oacute; thể suy một ra ba, học thức tiến độ mau đ&iacute;ch kinh người.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cười khổ n&oacute;i: &quot;Văn Nhược ngươi đừng n&oacute;i l&agrave; n&oacute;i m&aacute;t , dạy học dục nh&acirc;n, g&aacute;nh nặng đường xa, tuy rằng Ch&iacute; T&agrave;i thường n&oacute;i ta c&oacute; tiệp k&iacute;nh c&oacute; thể đem n&agrave;y đ&oacute; đứa nhỏ dạy th&agrave;nh đại t&agrave;i, khả học hải v&ocirc; nhai, cho d&ugrave; ngươi c&ugrave;ng ta hơn nữa Ch&iacute; T&agrave;i ba người buộc c&ugrave;ng nhau, lại kh&ocirc;ng d&aacute;m n&oacute;i biết r&otilde; thi&ecirc;n hạ học thức? Hiện tại n&agrave;y đ&oacute; đứa nhỏ ho&agrave;i cảm ơn chi t&acirc;m chăm chỉ khắc khổ, ta chỉ hy vọng tương lai bọn họ &aacute;o cơm kh&ocirc;ng lo khi đừng sinh giải đ&atilde;i bại lại chi t&acirc;m.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c l&yacute; giải địa điểm gật đầu, đang muốn n&oacute;i c&aacute;i g&igrave; nữa đ&iacute;ch thời điểm, đ&atilde; thấy xa xa đi tới một ti&ecirc;n phong đạo cốt đ&iacute;ch l&atilde;o nh&acirc;n, chỉ thấy l&atilde;o nh&acirc;n một tay gi&uacute;p đỡ tuyết trắng ch&ograve;m r&acirc;u, một b&ecirc;n tấm tắc lấy l&agrave;m kỳ, &aacute;nh mắt ở đồng ruộng c&ugrave;ng l&acirc;m bi&ecirc;n đ&iacute;ch ruộng đất v&agrave; nh&agrave; cửa đổi tới đổi lui.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia ch&iacute;nh l&agrave; lơ đ&atilde;ng địa theo Tu&acirc;n &Uacute;c đ&iacute;ch tầm mắt ngắm qu&aacute; khứ, kết quả vẻ mặt chấn động, vội v&agrave;ng địa hướng tới kia l&atilde;o nh&acirc;n chạy qu&aacute; khứ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c th&acirc;n thủ dục ngăn lại Qu&aacute;ch Gia để hỏi hiểu được, chậm đi từng bước, trong tay thất bại, t&aacute;i vừa thấy, nhất thời trợn mắt h&aacute; hốc mồm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chỉ thấy Qu&aacute;ch Gia trực tiếp lẻn đến kia b&aacute;n trăm l&atilde;o nh&acirc;n trước mặt, th&acirc;n thủ t&uacute;m trụ l&atilde;o nh&acirc;n tuyết trắng đ&iacute;ch ch&ograve;m r&acirc;u, hung tợn địa n&oacute;i: &quot;Tử l&atilde;o đạo, ta nhớ ngươi muốn chết!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia l&atilde;o đạo đau đắc nhe răng nhếch miệng, kh&oacute;e mắt đều tr&agrave;o ra nước mắt , vội v&agrave;ng cầu xin tha thứ đạo: &quot;Ai ai ai, ta n&oacute;i tiểu hữu, ngươi điểm nhẹ nhi, điểm nhẹ nhi, nếu quải niệm l&atilde;o phu, v&igrave; sao phải l&agrave;m kh&oacute; dễ vu l&atilde;o phu a?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia thủ kh&ocirc;ng bu&ocirc;ng k&iacute;nh, nộ kh&iacute; đằng đằng địa n&oacute;i: &quot;Ngươi giả danh lừa bịp cho d&ugrave; , v&igrave; sao năm đ&oacute; muốn n&oacute;i ta l&agrave; khương th&aacute;i c&ocirc;ng chuyển thế? Hiện tại gặp người đ&atilde; k&ecirc;u ta tiểu th&aacute;i c&ocirc;ng, ngươi cũng biết th&aacute;i c&ocirc;ng &yacute; g&igrave;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Th&aacute;i c&ocirc;ng b&igrave;nh thường l&agrave; chỉ tiểu cha, ta gia, Qu&aacute;ch Gia tổng bị người như vậy k&ecirc;u, tự nhi&ecirc;n kh&ocirc;ng ki&ecirc;n nhẫn , huống hồ d&ugrave;ng hắn đ&iacute;ch &yacute; tưởng ch&iacute;nh l&agrave;, tương lai t&igrave;m vợ đều th&agrave;nh vấn đề ! Bằng kh&ocirc;ng nh&agrave; g&aacute;i vừa thấy mặt, trước gọi một tiếng: tiểu th&aacute;i c&ocirc;ng! Qu&aacute;ch Gia l&agrave;m sao b&acirc;y giờ? Ứng với xuống dưới sao kh&ocirc;ng? Kia t&aacute;n g&aacute;i kh&ocirc;ng diễn , kh&ocirc;ng duy&ecirc;n cớ v&ocirc; cớ th&agrave;nh trưởng bối . Kh&ocirc;ng ứng với xuống dưới sao kh&ocirc;ng? Lại rất kh&ocirc;ng lễ ph&eacute;p , ấn tượng đầu ti&ecirc;n sẽ ph&aacute; hủy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; sự t&igrave;nh ngọn nguồn, đ&uacute;ng l&agrave; trước mặt n&agrave;y nh&igrave;n qua ti&ecirc;n phong đạo cốt đ&iacute;ch l&atilde;o đạo nhi!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chỉ nghe kia l&atilde;o đạo tiếp tục cầu xin tha thứ: &quot;Tiểu hữu, ngươi bu&ocirc;ng tay đi, l&atilde;o phu n&agrave;y ch&ograve;m r&acirc;u cần phải bị ngươi bạt hết! L&atilde;o đạo Tả Từ trước cho ngươi bồi c&aacute;i kh&ocirc;ng phải, ch&uacute;ng ta c&oacute; c&aacute;i g&igrave; n&oacute;i, trước bu&ocirc;ng tay n&oacute;i sau khỏe?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tả Từ? Qu&aacute;ch Gia sửng sốt, tr&ecirc;n tay đ&iacute;ch k&iacute;nh nhi liền t&ugrave;ng , Tả Từ l&atilde;o đạo lập tức ngẩng th&acirc;n m&igrave;nh ngẩng đầu ưỡn ngực, một tay vuốt r&acirc;u, biến th&agrave;nh cao nh&acirc;n phong phạm</p>', '0', 'de-nhi-chuong-tiep-te-dan-chay-nan', 5, '546547', '2022-08-03 02:42:04', '2022-09-29 14:01:41');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(21, '3', 'Đệ tam chương thiên mệnh đã sửa', '<p>Tả Từ l&atilde;o đạo mặc d&ugrave; đang,ở tam quốc lịch sử trung cũng kh&ocirc;ng c&oacute; lưu lại đại ph&oacute;ng th&agrave;nh tựu xuất sắc chuyện t&iacute;ch, cũng tuyệt đối l&agrave; một c&aacute;i thần b&iacute; kh&oacute; lường ch&iacute;nh l&agrave; nh&acirc;n vật.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vốn tưởng rằng n&agrave;y hạc ph&aacute;t đồng nhan đ&iacute;ch l&atilde;o đạo bất qu&aacute; l&agrave; c&aacute;i giả danh lừa bịp đồ đệ, kh&ocirc;ng nghĩ tới đ&aacute;nh bậy đ&aacute;nh bạ đụng phải trong truyền thuyết ch&iacute;nh l&agrave; nh&acirc;n vật, Qu&aacute;ch Gia cũng kh&ocirc;ng d&acirc;y dưa , lập tức ở ruộng đất v&agrave; nh&agrave; cửa ngoại đ&iacute;ch d&agrave;i trước b&agrave;n c&ugrave;ng Tu&acirc;n &Uacute;c c&ugrave;ng Tả Từ đồng tịch m&agrave; ngồi, d&acirc;ng nước ch&egrave; xanh đ&atilde;i kh&aacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c đối Tả Từ thập phần t&ograve; m&ograve;, hắn cũng biết Qu&aacute;ch Gia tiểu th&aacute;i c&ocirc;ng danh hiệu đ&iacute;ch lai lịch, vẫn hy vọng gặp một lần n&agrave;y nước ngo&agrave;i cao nh&acirc;n, d&ugrave; sao c&oacute; thể ở Qu&aacute;ch Gia bảy tuổi l&agrave; l&uacute;c liền d&otilde;ng dạc địa n&oacute;i Qu&aacute;ch Gia l&agrave; khương th&aacute;i c&ocirc;ng chuyển thế, ở Tu&acirc;n &Uacute;c trong mắt, l&atilde;o đạo cũng kh&ocirc;ng c&oacute; n&oacute;i qu&aacute; sự thật, lấy Qu&aacute;ch Gia t&agrave;i, c&oacute; thể so với khương th&aacute;i c&ocirc;ng, nếu l&agrave; ng&agrave;y sau c&oacute; &quot;Chu văn vương&quot; thỉnh hắn rời n&uacute;i phụ t&aacute;, đại sự khả kỳ!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; c&oacute; thể ở Qu&aacute;ch Gia ấu linh l&agrave; l&uacute;c liền nh&igrave;n ra Qu&aacute;ch Gia t&agrave;i người, sao lại l&agrave; hời hợt đồ đệ tầm thường hạng người?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kh&ocirc;ng biết l&atilde;o nh&acirc;n gia tuổi bao nhi&ecirc;u? Ta xem ngươi sắc mặt hồng nhuận giống như thanh ni&ecirc;n, rồi lại t&oacute;c trắng xo&aacute;, trong l&ograve;ng thật sự t&ograve; m&ograve; đ&iacute;ch nhanh, nếu c&oacute; ch&uacute;t mạo phạm, c&ograve;n thỉnh thứ lỗi.&quot; Tu&acirc;n &Uacute;c đ&atilde; b&aacute;i thi lễ, xem như trước ti&ecirc;n bồi tội .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&iacute;nh uống tr&agrave; đ&iacute;ch Tả Từ một ngụm thủy văng l&ecirc;n đi ra ngo&agrave;i, sang ở, ho khan mấy tiếng l&uacute;c sau t&agrave;i mạt sạch sẽ miệng, ở Qu&aacute;ch Gia c&ugrave;ng Tu&acirc;n &Uacute;c nghi hoặc đ&iacute;ch trong &aacute;nh mắt chật vật địa tr&aacute;nh đi tầm mắt, khẩu kh&iacute; đ&ocirc;ng cứng địa thấp giọng n&oacute;i: &quot;L&atilde;o phu vừa qua khỏi m&agrave; đứng chi năm.&quot;</p>\r\n\r\n<div id=\"gtx-trans\" style=\"left:-177px; position:absolute; top:38px\">\r\n<div class=\"gtx-trans-icon\">&nbsp;</div>\r\n</div>\r\n\r\n<div class=\"ddict_btn\" style=\"left:222.984px; top:38px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '<p>Tả Từ l&atilde;o đạo mặc d&ugrave; đang,ở tam quốc lịch sử trung cũng kh&ocirc;ng c&oacute; lưu lại đại ph&oacute;ng th&agrave;nh tựu xuất sắc chuyện t&iacute;ch, cũng tuyệt đối l&agrave; một c&aacute;i thần b&iacute; kh&oacute; lường ch&iacute;nh l&agrave; nh&acirc;n vật.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vốn tưởng rằng n&agrave;y hạc ph&aacute;t đồng nhan đ&iacute;ch l&atilde;o đạo bất qu&aacute; l&agrave; c&aacute;i giả danh lừa bịp đồ đệ, kh&ocirc;ng nghĩ tới đ&aacute;nh bậy đ&aacute;nh bạ đụng phải trong truyền thuyết ch&iacute;nh l&agrave; nh&acirc;n vật, Qu&aacute;ch Gia cũng kh&ocirc;ng d&acirc;y dưa , lập tức ở ruộng đất v&agrave; nh&agrave; cửa ngoại đ&iacute;ch d&agrave;i trước b&agrave;n c&ugrave;ng Tu&acirc;n &Uacute;c c&ugrave;ng Tả Từ đồng tịch m&agrave; ngồi, d&acirc;ng nước ch&egrave; xanh đ&atilde;i kh&aacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c đối Tả Từ thập phần t&ograve; m&ograve;, hắn cũng biết Qu&aacute;ch Gia tiểu th&aacute;i c&ocirc;ng danh hiệu đ&iacute;ch lai lịch, vẫn hy vọng gặp một lần n&agrave;y nước ngo&agrave;i cao nh&acirc;n, d&ugrave; sao c&oacute; thể ở Qu&aacute;ch Gia bảy tuổi l&agrave; l&uacute;c liền d&otilde;ng dạc địa n&oacute;i Qu&aacute;ch Gia l&agrave; khương th&aacute;i c&ocirc;ng chuyển thế, ở Tu&acirc;n &Uacute;c trong mắt, l&atilde;o đạo cũng kh&ocirc;ng c&oacute; n&oacute;i qu&aacute; sự thật, lấy Qu&aacute;ch Gia t&agrave;i, c&oacute; thể so với khương th&aacute;i c&ocirc;ng, nếu l&agrave; ng&agrave;y sau c&oacute; &quot;Chu văn vương&quot; thỉnh hắn rời n&uacute;i phụ t&aacute;, đại sự khả kỳ!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; c&oacute; thể ở Qu&aacute;ch Gia ấu linh l&agrave; l&uacute;c liền nh&igrave;n ra Qu&aacute;ch Gia t&agrave;i người, sao lại l&agrave; hời hợt đồ đệ tầm thường hạng người?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Kh&ocirc;ng biết l&atilde;o nh&acirc;n gia tuổi bao nhi&ecirc;u? Ta xem ngươi sắc mặt hồng nhuận giống như thanh ni&ecirc;n, rồi lại t&oacute;c trắng xo&aacute;, trong l&ograve;ng thật sự t&ograve; m&ograve; đ&iacute;ch nhanh, nếu c&oacute; ch&uacute;t mạo phạm, c&ograve;n thỉnh thứ lỗi.&quot; Tu&acirc;n &Uacute;c đ&atilde; b&aacute;i thi lễ, xem như trước ti&ecirc;n bồi tội .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&iacute;nh uống tr&agrave; đ&iacute;ch Tả Từ một ngụm thủy văng l&ecirc;n đi ra ngo&agrave;i, sang ở, ho khan mấy tiếng l&uacute;c sau t&agrave;i mạt sạch sẽ miệng, ở Qu&aacute;ch Gia c&ugrave;ng Tu&acirc;n &Uacute;c nghi hoặc đ&iacute;ch trong &aacute;nh mắt chật vật địa tr&aacute;nh đi tầm mắt, khẩu kh&iacute; đ&ocirc;ng cứng địa thấp giọng n&oacute;i: &quot;L&atilde;o phu vừa qua khỏi m&agrave; đứng chi năm.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; đứng chi năm? Ba mươi m&agrave; đứng! N&agrave;y l&atilde;o đạo hiện giờ t&agrave;i ba mươi tuổi xuất đầu? Kia như thế n&agrave;o t&oacute;c to&agrave;n bộ trắng? Liền ngay cả ch&ograve;m r&acirc;u đều l&agrave; bạch đ&iacute;ch! L&uacute;c n&agrave;y đại c&oacute; chất lượng như vậy cao đ&iacute;ch nhuộm t&oacute;c tề?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c c&ugrave;ng Qu&aacute;ch Gia biểu t&igrave;nh kinh ngạc, Qu&aacute;ch Gia lại trực tiếp hỏi han: &quot;L&atilde;o đạo, ngươi n&agrave;y n&oacute;i dối n&oacute;i đ&iacute;ch rất kh&ocirc;ng xoay ngang đi? Ngươi ba mươi xuất đầu? Lừa ai a, ngươi đừng n&oacute;i ngươi sinh hạ đến lớn l&ecirc;n mao ch&iacute;nh l&agrave; bạch đ&iacute;ch! C&ograve;n c&oacute;, ngươi đều tự xưng l&atilde;o đạo l&atilde;o phu , ta chỉ nghe người ta c&oacute; đem ch&iacute;nh m&igrave;nh n&oacute;i c&ograve;n trẻ đ&iacute;ch, chưa thấy qua ba mươi trang t&aacute;m mươi đ&iacute;ch.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia ng&ocirc;n ngữ th&ocirc; bỉ, Tu&acirc;n &Uacute;c thấy nhưng kh&ocirc;ng thể tr&aacute;ch, nhưng cũng nhận thức đồng Qu&aacute;ch Gia n&agrave;y c&aacute;ch n&oacute;i, chỉ nh&igrave;n Tả Từ như thế n&agrave;o biện giải.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thần sắc xấu hổ đ&iacute;ch Tả Từ thở d&agrave;i một tiếng, tẫn hiển bất đắc dĩ vẻ, giải th&iacute;ch đạo: &quot;L&atilde;o phu ta tuổi nhỏ t&ugrave;y nước ngo&agrave;i cao nh&acirc;n học nghệ, thiếu ni&ecirc;n khi liền thường thường đ&ecirc;m xem hiện tượng thi&ecirc;n văn, ph&aacute;t hiện đại h&aacute;n vương triều vận số đ&atilde; suy, biết r&otilde; loạn thế bu&ocirc;ng xuống, v&agrave;o đời nếu cầu quan lớn tất như l&yacute; miếng băng mỏng, nếu vơ v&eacute;t của cải cầu ph&uacute; qu&yacute; khủng chết oan chết uổng, toại nhập trụ trời sơn dốc l&ograve;ng học đạo cầu trường sinh, nề h&agrave; kh&ocirc;ng bao l&acirc;u v&agrave;o n&uacute;i, hơn mười t&aacute;i quang &acirc;m trong nh&aacute;y mắt qu&aacute;, &ocirc; ph&aacute;t nhiễm sương, ngẫu nhi&ecirc;n xuống n&uacute;i du lịch khi vi thế nh&acirc;n giải th&iacute;ch nghi hoặc xu c&aacute;t tị hung khi, thế nh&acirc;n giai xưng ta vi t&ocirc;n trưởng l&atilde;o người, cửu nhi cửu chi, l&atilde;o phu cũng th&agrave;nh th&oacute;i quen.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c vẻ mặt vẻ mặt, c&ograve;n c&oacute; bực n&agrave;y thần kỳ chuyện t&igrave;nh? Ở trong n&uacute;i dốc l&ograve;ng học đạo, học học, t&oacute;c to&agrave;n bộ trắng? Bất qu&aacute; tr&ecirc;n đời kỳ văn dị sự nhiều, thường c&oacute; phi thường để &yacute; c&oacute; thể độ việc, lấy n&agrave;y l&atilde;o đạo đ&iacute;ch tuệ nh&atilde;n thức nh&acirc;n bổn sự, nhưng thật ra tin bảy chữ b&aacute;t ph&acirc;n.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; một b&ecirc;n đ&iacute;ch Qu&aacute;ch Gia tắc giao tr&aacute;i tim tư đặt ở địa phương kh&aacute;c, l&uacute;c n&agrave;y hắn trong l&ograve;ng thầm nghĩ: chẳng lẽ hiện tượng thi&ecirc;n văn thật đ&uacute;ng l&agrave; đ&iacute;ch c&ugrave;ng thi&ecirc;n hạ đại thế c&oacute; li&ecirc;n hệ? Tả Từ xem hiện tượng thi&ecirc;n văn liền c&oacute; thể liệu định thi&ecirc;n hạ xu thế, hơn nữa Tả Từ học đạo ph&iacute;a trước đ&iacute;ch băn khoăn cũng kh&ocirc;ng giả, loạn thế b&ecirc;n trong, chức vị? Ngoạn ch&iacute;nh trị kh&ocirc;ng phải người b&igrave;nh thường c&oacute; thể c&oacute; khả năng đ&iacute;ch, đ&acirc;y l&agrave; bắt người mệnh l&agrave;m tiền vốn đ&iacute;ch đại sinh &yacute;, bồi trong lời n&oacute;i, ch&iacute;nh m&igrave;nh đ&iacute;ch mệnh đi tong xem như tốt , nếu l&agrave; đ&aacute;p thượng một nh&agrave; gi&agrave; trẻ thậm ch&iacute; bộ tộc mọi người, th&igrave; phải l&agrave; diệt tộc họa. Việc bu&ocirc;n b&aacute;n? Loạn thế sinh &yacute; nan l&agrave;m, lưu d&acirc;n nổi l&ecirc;n bốn ph&iacute;a v&agrave;o rừng l&agrave;m cướp vi khấu đ&iacute;ch kh&ocirc;ng ở số &iacute;t, sơn tặc dồ bậy bạ lại mỗi ng&agrave;y v&agrave;o nh&agrave; cướp của th&agrave;nh cố định c&ocirc;ng t&aacute;c, loạn thế b&ecirc;n trong, kẻ c&oacute; tiền hoặc l&agrave; l&agrave; đợi l&agrave;m thịt sơn dương, hoặc l&agrave; l&agrave; qu&acirc;n phiệt đ&iacute;ch tự động đề khoản cơ, hơi c&oacute; v&ocirc; &yacute;, chỉ sợ cũng l&agrave; kh&ocirc;ng được chết gi&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bởi vậy c&oacute; thể thấy được, Tả Từ bổn sự kh&ocirc;ng nhỏ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đương biết người n&agrave;y l&agrave; Tả Từ l&uacute;c sau, Qu&aacute;ch Gia liền sớm thu hồi khinh thị chi t&acirc;m, v&igrave; thế cũng đem đề t&agrave;i dẫn v&agrave;o ch&iacute;nh đề.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Tả l&atilde;o đạo, vậy ngươi v&igrave; sao lại đ&acirc;y nơi n&agrave;y? Hay l&agrave; ba năm trước đ&acirc;y hại ta kh&ocirc;ng đủ, hiện giờ c&ograve;n muốn lửa ch&aacute;y đổ th&ecirc;m dầu?&quot; Qu&aacute;ch Gia sắc mặt kh&ocirc;ng tốt, thật sự l&agrave; bị tiểu th&aacute;i c&ocirc;ng h&agrave;ng đầu hại s&acirc;u.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tả Từ buồn bực kh&ocirc;ng th&ocirc;i, trong l&ograve;ng thầm nghĩ: nếu l&agrave; người b&ecirc;n ngo&agrave;i đắc khương th&aacute;i c&ocirc;ng t&ecirc;n đầu, tất nhi&ecirc;n vui sướng nhảy nh&oacute;t, n&agrave;y ngoan đồng như thế n&agrave;o như thế m&acirc;u thuẫn?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trăm tư kh&ocirc;ng được n&agrave;y giải đ&iacute;ch Tả Từ cũng lười lại đi nghĩ muốn trong đ&oacute; duy&ecirc;n cớ, thản ng&ocirc;n đạo: &quot;L&atilde;o phu n&agrave;y đến, vốn l&agrave; muốn gặp một lần qu&aacute; thiện t&acirc;m đ&iacute;ch tiểu th&aacute;i c&ocirc;ng, hiện giờ th&ocirc;i, lại muốn c&ugrave;ng tiểu th&aacute;i c&ocirc;ng giao c&aacute;i bằng hữu.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Acirc;n? Tự động đưa l&ecirc;n m&ocirc;n? Qu&aacute;ch Gia trong l&ograve;ng kinh nghi, nghĩ thầm,rằng ta chỉ bất qu&aacute; l&agrave; tiếp tế mấy trăm hộ lưu d&acirc;n m&agrave; th&ocirc;i, lại kh&ocirc;ng c&oacute; xả đại kỳ tạo phản, ngươi chạy tới giao bằng hữu? Lời n&agrave;y nghe đứng l&ecirc;n c&oacute; ch&uacute;t điểm đầu nhập v&agrave;o chi ngại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Tả l&atilde;o đạo ngươi đến tột c&ugrave;ng an đ&iacute;ch c&aacute;i g&igrave; t&acirc;m tư, kh&ocirc;ng ngại n&oacute;i thẳng, giao bằng hữu? Ch&uacute;ng ta kh&ocirc;ng cần che lấp, ta ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; quyền, hai v&ocirc; thế, ngươi kh&ocirc;ng c&oacute; khả năng c&oacute; cầu vu ta, m&agrave; ngươi lại tị thế l&acirc;u d&agrave;i, đoạn kh&ocirc;ng c&oacute; khả năng dễ d&agrave;ng c&ugrave;ng ta c&oacute; c&ugrave;ng xuất hiện, rốt cuộc muốn l&agrave;m g&igrave;, n&oacute;i thẳng đi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tả Từ mặt đỏ tai hồng, bị Qu&aacute;ch Gia sang đắc c&oacute; ch&uacute;t nan kham, nhưng thật ra Tu&acirc;n &Uacute;c bất đắc dĩ địa than nhẹ một tiếng, đối n&agrave;y thẳng thắn đ&iacute;ch bạn tốt kh&ocirc;ng lời n&agrave;o để n&oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bất qu&aacute; Tả Từ thật cũng kh&ocirc;ng giận, d&ugrave; sao lấy hắn l&agrave;m việc t&aacute;c phong, ch&iacute;nh như Qu&aacute;ch Gia lời n&oacute;i, kh&ocirc;ng c&oacute; khả năng kh&ocirc;ng duy&ecirc;n cớ v&ocirc; cớ bỏ chạy đến giao hữu, hắn đ&iacute;ch x&aacute;c c&oacute; điều đồ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Một khi đ&atilde; như vậy, l&atilde;o phu liền thản ng&ocirc;n chi, tiểu th&aacute;i c&ocirc;ng, ng&agrave;y đ&oacute; l&atilde;o phu xem ngươi tướng mạo, n&oacute;i ngươi l&agrave; khương th&aacute;i c&ocirc;ng chuyển thế, n&agrave;y bất qu&aacute; một dụ, lấy ngươi t&agrave;i, ng&agrave;y sau kh&ocirc;ng thể số lượng, m&agrave; ngươi b&ecirc;n cạnh vị n&agrave;y bằng hữu, c&ugrave;ng ngươi ng&agrave;y đ&oacute; đ&iacute;ch tướng mạo, c&oacute; chữ b&aacute;t ph&acirc;n tương tự.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tả Từ n&oacute;i xong, Tu&acirc;n &Uacute;c trong l&ograve;ng kinh h&atilde;i, cảm th&aacute;n người n&agrave;y quả nhi&ecirc;n bổn sự kh&ocirc;ng tầm thường, m&agrave; Qu&aacute;ch Gia đồng dạng khiếp sợ, nguyệt tiền Tu&acirc;n &Uacute;c t&agrave;i đắc vương t&aacute; t&agrave;i t&ecirc;n, hiện giờ Tả Từ lại một lần nữa khẳng định c&aacute;i thuyết ph&aacute;p n&agrave;y, nếu n&oacute;i l&atilde;o đạo sẽ kh&ocirc;ng xem tướng, Qu&aacute;ch Gia hiện giờ ngược lại kh&ocirc;ng tin .</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;, nghi vấn đến đ&acirc;y, Qu&aacute;ch Gia nh&iacute;u m&agrave;y hỏi: &quot;Tả l&atilde;o đạo, ngươi n&oacute;i Văn Nhược c&ugrave;ng ta ng&agrave;y đ&oacute; tướng mạo c&oacute; chữ b&aacute;t ph&acirc;n tương tự, chẳng lẽ ta h&ocirc;m nay tướng mạo c&ugrave;ng ba năm trước đ&acirc;y c&oacute; điều bất đồng?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tả Từ do dự sau một l&uacute;c l&acirc;u trầm giọng n&oacute;i: &quot;L&atilde;o phu n&oacute;i thẳng, thỉnh tiểu hữu chớ tr&aacute;ch, năm đ&oacute; ta xem ngươi tướng mạo mặc d&ugrave; ng&agrave;y sau tiền đồ v&ocirc; lượng, đ&atilde; c&oacute; trung ni&ecirc;n chết non chi c&ugrave;ng, h&ocirc;m nay vừa thấy, tiểu hữu tướng mạo lại c&ugrave;ng ng&agrave;y đ&oacute; ho&agrave;n to&agrave;n bất đồng, l&atilde;o phu thức nh&acirc;n xem tướng v&ocirc; số, ngươi, cũng duy nhất một c&aacute;i mệnh số số mệnh đột nhi&ecirc;n chuyển biến người, l&atilde;o phu kh&aacute;n ph&aacute; thi&ecirc;n cơ lại kh&oacute; c&oacute; thể tham ph&aacute; trong đ&oacute; nguy&ecirc;n do, cố nghĩ muốn ở lại tiểu hữu b&ecirc;n người, nh&igrave;n xem l&agrave; l&atilde;o phu học nghệ kh&ocirc;ng tinh xem đi rồi mắt, vẫn l&agrave; h&ocirc;m nay hạ thực sự mệnh số v&ocirc; thường chi ngoại tộc.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&acirc;u cửa miệng đạo ba tuổi xem t&aacute;m mươi, phỏng chừng tướng mạo mệnh để &yacute; n&oacute;i đến cũng l&agrave; như thế đi, Qu&aacute;ch Gia ch&iacute;nh m&igrave;nh r&otilde; r&agrave;ng lịch sử thượng ch&iacute;nh m&igrave;nh đ&iacute;ch c&aacute;i g&igrave; mệnh, trước đầu Vi&ecirc;n Thiệu, sau đầu T&agrave;o Th&aacute;o, ba mươi t&aacute;m tuổi bệnh chết ở T&agrave;o Th&aacute;o chinh phạt &ocirc; ho&agrave;n đ&iacute;ch tr&ecirc;n đường, n&oacute;i l&agrave; trung ni&ecirc;n chết non, đ&iacute;ch x&aacute;c như thế.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave;m xuy&ecirc;n qua nh&acirc;n đ&iacute;ch Qu&aacute;ch Gia đ&atilde; biết thi&ecirc;n mệnh, tự nhi&ecirc;n kh&ocirc;ng cam l&ograve;ng bị vận mệnh b&agrave;i bố, kh&ocirc;ng bao l&acirc;u liền cần vu r&egrave;n luyện th&acirc;n thể, c&ograve;n nữa hắn cũng kh&ocirc;ng c&oacute; thể cả ng&agrave;y đọc s&aacute;ch nguy&ecirc;n l&yacute;, c&ugrave;ng bằng hữu t&aacute;n phiếm luận địa, ng&acirc;m gi&oacute; ngợi trăng rất nhiều, lại hiển buồn tẻ v&ocirc; c&ugrave;ng, v&igrave; thế mỗi ng&agrave;y l&agrave;m ch&uacute;t đơn giản đ&iacute;ch th&acirc;n thể r&egrave;n luyện đ&atilde; muốn th&agrave;nh cuộc sống th&oacute;i quen, gần nhất th&ocirc;i cường th&acirc;n kiện thể, kh&ocirc;ng cầu trường sinh, ch&iacute;ch nguyện trường thọ, thứ hai th&ocirc;i, cũng l&agrave; ph&aacute;i thời gian, nếu kh&ocirc;ng giải tr&iacute; hạng mục thiếu thốn đ&iacute;ch thời đại l&yacute;, hắn c&oacute; thể bị ch&iacute;nh m&igrave;nh bức đi&ecirc;n rồi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia nghe được ch&iacute;nh m&igrave;nh thi&ecirc;n mệnh đ&atilde; sửa, nhất thời vui mừng lộ r&otilde; tr&ecirc;n n&eacute;t mặt, thầm nghĩ ch&iacute;nh m&igrave;nh hẳn l&agrave; c&oacute; thể d&agrave;i thọ đi, m&agrave; Tu&acirc;n &Uacute;c lại mặt lộ vẻ vẻ mặt ngưng trọng, thử t&iacute;nh hỏi han: &quot;Xin hỏi đạo trưởng, hiện giờ Phụng Hiếu mặt c&ugrave;ng như thế n&agrave;o? C&oacute; kh&ocirc;ng bẩm b&aacute;o?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tả Từ trầm ng&acirc;m một l&aacute;t sau lắc đầu n&oacute;i: &quot;Hiện giờ tiểu th&aacute;i c&ocirc;ng tướng mạo ta đ&atilde; mất ph&aacute;p kh&aacute;n ph&aacute;, chỉ biết tiểu th&aacute;i c&ocirc;ng đương kim mệnh c&aacute;ch đ&atilde; qu&yacute; kh&ocirc;ng thể n&oacute;i.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tu&acirc;n &Uacute;c y&ecirc;n lặng gật đầu, qu&yacute; kh&ocirc;ng thể n&oacute;i bốn chữ đủ để kẻ kh&aacute;c sinh ra, ở trong l&ograve;ng hắn, qu&yacute; kh&ocirc;ng thể n&oacute;i &yacute; nghĩa Qu&aacute;ch Gia ng&agrave;y sau tất vị cực nh&acirc;n thần, tất l&agrave; phụ quốc trọng thần!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngẫm lại với, lấy Qu&aacute;ch Gia t&agrave;i, nếu c&oacute; ch&uacute;t minh chủ quen biết, tự nhi&ecirc;n như c&aacute; gặp nước tiền đồ kh&ocirc;ng thể số lượng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&iacute;nh l&agrave; Tu&acirc;n &Uacute;c lại xem nhẹ ba năm trước đ&acirc;y Tả Từ n&oacute;i Qu&aacute;ch Gia l&agrave; khương th&aacute;i c&ocirc;ng chuyển thế, bởi vậy m&agrave; n&oacute;i, th&agrave;nh tựu dĩ nhi&ecirc;n người phi thường c&oacute; thể đạt được, hiện giờ, mệnh số đại biến, cũng đ&atilde; nhi&ecirc;n qu&yacute; kh&ocirc;ng thể n&oacute;i, trong đ&oacute; huyền cơ, chỉ sợ người b&igrave;nh thường cũng kh&oacute; lấy hiểu thấu đ&aacute;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vuốt cằm l&agrave;m ra suy tư trạng đ&iacute;ch Qu&aacute;ch Gia đ&aacute;nh gi&aacute; Tả Từ, tả tiều hữu xem, l&agrave;m cho Tả Từ cả người kh&ocirc;ng thoải m&aacute;i, nhịn kh&ocirc;ng được mở miệng hỏi đạo: &quot;Tiểu th&aacute;i c&ocirc;ng v&igrave; sao như thế nh&igrave;n chằm chằm l&atilde;o phu?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia nh&uacute;n nh&uacute;n vai, bĩu m&ocirc;i đạo: &quot;Tả l&atilde;o đạo, ngươi nếu n&oacute;i phải ở lại ta b&ecirc;n người, ta đ&acirc;u, tự nhi&ecirc;n l&agrave; hoan ngh&ecirc;nh chi tới, ch&iacute;nh l&agrave; đ&acirc;u, ta nơi n&agrave;y kh&ocirc;ng dưỡng người rảnh rỗi, ngươi mặc d&ugrave; c&oacute; thấy r&otilde; thi&ecirc;n địa huyền diệu đ&iacute;ch bổn sự, khả ở ta nơi n&agrave;y v&ocirc; dụng a, nếu kh&ocirc;ng ngươi l&agrave;m thầy b&oacute;i như thế n&agrave;o? Ở To&aacute;nh Xuy&ecirc;n giả danh lừa bịp tốt xấu cũng c&oacute; thể hỗn khẩu cơm ăn.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tức giận hai mắt trắng d&atilde; đ&iacute;ch Tả Từ vỗ &aacute;n dựng l&ecirc;n, n&oacute;i: &quot;L&atilde;o phu ta hiểu được cũng kh&ocirc;ng chỉ n&agrave;y đ&oacute;, tuy n&oacute;i vũ văn lộng mặc kh&ocirc;ng được, binh ph&aacute;p thao lược cũng kh&ocirc;ng hiểu, nhưng l&atilde;o phu ta một th&acirc;n v&otilde; nghệ kh&ocirc;ng d&aacute;m n&oacute;i thi&ecirc;n hạ v&ocirc; địch, nhưng cũng kh&ocirc;ng phải tầm thường đ&iacute;ch vũ phu c&oacute; thể so s&aacute;nh, c&ograve;n nữa, thi&ecirc;n văn địa l&yacute;, &Acirc;m Dương Ngũ H&agrave;nh, ta kh&ocirc;ng chỗ n&agrave;o kh&ocirc;ng tinh, ta ở lại nơi n&agrave;y, đều kh&ocirc;ng phải l&agrave; ăn nhờ ở đậu, sống tạm độ nhật, ta tự h&agrave;nh giải quyết.&quot;</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ai ai ai, đừng n&oacute;ng giận, đừng n&oacute;ng giận, ngươi một phen tuổi , b&igrave;nh tĩnh b&igrave;nh tĩnh. Tả l&atilde;o đạo, ngươi xem như vậy như thế n&agrave;o, ngươi đ&acirc;u, liền tạm thời ở ta nơi n&agrave;y trụ hạ, sau đ&oacute; ta liền dẫn người cho ngươi dựng một khu nh&agrave; nh&agrave; gỗ, về sau đ&acirc;u, ăn cơm loại n&agrave;y việc nhỏ kh&ocirc;ng nhọc ngươi quan t&acirc;m, ngươi mỗi ng&agrave;y nh&agrave;n rỗi cũng l&agrave; nh&agrave;n rỗi, kh&ocirc;ng bằng ngươi chọn lựa mấy đứa nhỏ đương đồ đệ, dạy hắn nh&oacute;m v&otilde; nghệ, mặt kh&aacute;c ngươi đổng thi&ecirc;n văn địa l&yacute;, &Acirc;m Dương Ngũ H&agrave;nh, ngươi xem, ta nơi n&agrave;y ba trăm hộ người ta phải dựa v&agrave;o trồng trọt m&agrave; sống, ngươi kh&ocirc;ng c&oacute; việc g&igrave; gi&aacute;o dạy hắn nh&oacute;m n&agrave;y đ&oacute; tri thức, tương lai cũng tốt c&oacute; thể dự ph&ograve;ng thi&ecirc;n tai, miễn cho thu hoạch kh&ocirc;ng tốt , ch&uacute;ng ta đều đi theo đ&oacute;i bụng, ngươi xem thế n&agrave;o?&quot; Qu&aacute;ch Gia cười tủm tỉm địa nh&igrave;n Tả Từ, Tu&acirc;n &Uacute;c ở một b&ecirc;n trợn mắt h&aacute; hốc mồm, h&ocirc;m nay lại thấy thức Qu&aacute;ch Gia đ&iacute;ch một kh&aacute;c mặt, hiện tại đ&iacute;ch Qu&aacute;ch Gia, quả thực giống một ch&iacute;ch tiểu hồ ly.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tả Từ vừa định mở miệng cự tuyệt, lại quay đầu tưởng tượng, ở tại chỗ n&agrave;y quan s&aacute;t Qu&aacute;ch Gia chỉ sợ nhu l&acirc;u d&agrave;i l&agrave; l&uacute;c, cả ng&agrave;y kh&ocirc;ng c&oacute; việc g&igrave; quả thật nh&agrave;m ch&aacute;n, Qu&aacute;ch Gia đ&iacute;ch đề nghị cũng coi như c&ugrave;ng hắn ăn uống, v&igrave; thế, Tả Từ gật đầu đ&aacute;p ứng rồi xuống dưới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia đang ở dạy đ&iacute;ch h&agrave;i đồng tuổi nhỏ người bất qu&aacute; năm tuổi, lớn tuổi người bất qu&aacute; t&aacute;m tuổi, tổng số ch&iacute;n mươi bảy người, Qu&aacute;ch Gia theo dạy bọn họ bắt đầu liền gi&aacute;o huấn tương th&acirc;n tương &aacute;i đ&iacute;ch l&yacute; niệm, gần trăm đ&iacute;ch h&agrave;i đồng lấy huynh đệ tương xứng, thực tắc đồng tịch, tẩm tắc đồng th&aacute;p, gặp được vấn đề cộng đồng giải quyết, gặp được kh&oacute; khăn gi&uacute;p đỡ cho nhau, hơn một th&aacute;ng xuống dưới, n&agrave;y đ&oacute; kh&aacute;c họ huynh đệ trong l&uacute;c đ&oacute; &iacute;t c&oacute; tranh c&atilde;i, c&agrave;ng nhiều b&agrave;y biện ra một loại ăn &yacute; c&ugrave;ng t&igrave;nh nghĩa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cứ việc Qu&aacute;ch Gia c&ugrave;ng Hi Ch&iacute; T&agrave;i tận hết sức lực địa dạy bọn họ, cho bọn hắn gi&aacute;o huấn ti&ecirc;n tiến đ&iacute;ch tri thức l&yacute; niệm, nhưng giống nhau thước dưỡng trăm dạng nh&acirc;n, mỗi người ai cũng c&oacute; sở trường ri&ecirc;ng, bởi vậy, ở học thức thượng đ&iacute;ch ngộ t&iacute;nh c&ugrave;ng thi&ecirc;n ph&uacute; tại đ&acirc;y nh&oacute;m người trung c&oacute; thực trực quan đ&iacute;ch phản ứng, c&oacute; đứa nhỏ một ch&uacute;t tức th&ocirc;ng, c&oacute; đứa nhỏ m&aacute;y m&oacute;c kh&ocirc;ng được n&agrave;y &yacute;, cũng bởi vậy, Qu&aacute;ch Gia hy vọng n&agrave;y đ&oacute; đứa nhỏ c&ugrave;ng thi triển sở trường, hắn ch&acirc;n ch&iacute;nh chờ mong ch&iacute;nh l&agrave; n&agrave;y đ&oacute; đứa nhỏ cuối c&ugrave;ng c&oacute; thể xuất hiện mấy thương nh&acirc;n, ch&iacute;nh kh&aacute;ch, v&otilde; tướng từ từ, m&agrave; kh&ocirc;ng phải thuần một sắc đ&iacute;ch học giả.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>T&ugrave;y theo t&agrave;i năng tới đ&acirc;u m&agrave; dạy đ&iacute;ch &yacute; tưởng kh&ocirc;ng c&oacute; vấn đề, ch&iacute;nh l&agrave; Qu&aacute;ch Gia một kh&ocirc;ng nhiều như vậy tinh lực, hai thật sự l&agrave; hắn sở học hữu hạn, ch&iacute;nh m&igrave;nh c&ograve;n ng&acirc;y thơ, đ&agrave;m g&igrave; gi&aacute;o h&oacute;a người kh&aacute;c? Hắn nhưng thật ra nghĩ muốn bồi dưỡng mấy v&otilde; tướng, khả hắn nhiều lắm c&oacute; thể đ&aacute;nh một bộ qu&acirc;n thể quyền th&ocirc;i, n&agrave;y ngoạn &yacute; l&ecirc;n ngựa lấy thượng binh kh&iacute;, ho&agrave;n to&agrave;n kh&ocirc;ng phải một c&aacute;i con đường th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vừa l&uacute;c, Tả Từ xuất hiện , l&atilde;o sư c&ograve;n c&oacute; .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng cầu Tả Từ c&oacute; thể dạy dỗ vạn nh&acirc;n địch đ&iacute;ch tuyệt thế m&atilde;nh tướng, &iacute;t nhất trăm người địch ng&agrave;n nh&acirc;n địch đ&iacute;ch v&otilde; tướng đi, kia Qu&aacute;ch Gia ngủ đều c&oacute; thể cười tỉnh.</p>', '0', 'de-tam-chuong-thien-menh-da-sua', 5, '568', '2022-08-03 02:43:24', '2022-09-29 14:01:42'),
(22, '4', 'Đệ tứ chương thiên hạ đại thế', '<p>Khoảng c&aacute;ch Qu&aacute;ch Gia tiếp tế d&acirc;n chạy nạn đ&atilde; qua hai năm, c&ocirc;ng nguy&ecirc;n 182 năm, Qu&aacute;ch Gia nh&acirc;n quảng thi việc thiện m&agrave; thanh danh thước khởi vu To&aacute;nh Xuy&ecirc;n, tiểu th&aacute;i c&ocirc;ng đ&iacute;ch danh vọng ở d&acirc;n ch&uacute;ng b&ecirc;n trong phi sĩ tộc quan gia c&oacute; khả năng đợi đến, m&agrave; hiện giờ, Qu&aacute;ch Gia dạy đ&iacute;ch đứa nhỏ đ&atilde; muốn vượt qua hai trăm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mưa ph&ugrave;n k&eacute;o d&agrave;i, đồng ruộng giữ đ&iacute;ch nh&agrave; gỗ trung tương đối ngồi chồm hỗm hai c&aacute;i tuấn dật thiếu ni&ecirc;n, trong đ&oacute; th&acirc;n thước bạch &aacute;o d&agrave;i đ&iacute;ch thiếu ni&ecirc;n đ&uacute;ng l&agrave; Qu&aacute;ch Gia, hiện giờ hắn đ&atilde; muốn mau mười ba tuổi, theo h&agrave;ng năm tập v&otilde;, th&acirc;n h&igrave;nh cao lớn, chưa n&oacute;i tới uy vũ cương m&atilde;nh, lại ở anh tuấn b&ecirc;n trong kh&ocirc;ng mất dương mới vừa vẻ đẹp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cấp đối diện đ&iacute;ch thiếu ni&ecirc;n tự m&igrave;nh ch&acirc;m tr&agrave;, rồi sau đ&oacute; nh&igrave;n ph&iacute;a ngo&agrave;i cửa sổ vũ cảnh, than nhẹ một tiếng đạo: &quot;Nguy&ecirc;n Trực, ngươi ta quen biết đ&atilde; c&oacute; một năm, n&agrave;y một năm trung, ngươi tổng hỏi ta thi&ecirc;n hạ đại thế, ta cuối c&ugrave;ng ngậm miệng kh&ocirc;ng đ&aacute;p, h&ocirc;m nay, ngươi ta kh&ocirc;ng bằng n&oacute;i thoải m&aacute;i, được?&quot;</p>', '<p>Khoảng c&aacute;ch Qu&aacute;ch Gia tiếp tế d&acirc;n chạy nạn đ&atilde; qua hai năm, c&ocirc;ng nguy&ecirc;n 182 năm, Qu&aacute;ch Gia nh&acirc;n quảng thi việc thiện m&agrave; thanh danh thước khởi vu To&aacute;nh Xuy&ecirc;n, tiểu th&aacute;i c&ocirc;ng đ&iacute;ch danh vọng ở d&acirc;n ch&uacute;ng b&ecirc;n trong phi sĩ tộc quan gia c&oacute; khả năng đợi đến, m&agrave; hiện giờ, Qu&aacute;ch Gia dạy đ&iacute;ch đứa nhỏ đ&atilde; muốn vượt qua hai trăm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mưa ph&ugrave;n k&eacute;o d&agrave;i, đồng ruộng giữ đ&iacute;ch nh&agrave; gỗ trung tương đối ngồi chồm hỗm hai c&aacute;i tuấn dật thiếu ni&ecirc;n, trong đ&oacute; th&acirc;n thước bạch &aacute;o d&agrave;i đ&iacute;ch thiếu ni&ecirc;n đ&uacute;ng l&agrave; Qu&aacute;ch Gia, hiện giờ hắn đ&atilde; muốn mau mười ba tuổi, theo h&agrave;ng năm tập v&otilde;, th&acirc;n h&igrave;nh cao lớn, chưa n&oacute;i tới uy vũ cương m&atilde;nh, lại ở anh tuấn b&ecirc;n trong kh&ocirc;ng mất dương mới vừa vẻ đẹp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cấp đối diện đ&iacute;ch thiếu ni&ecirc;n tự m&igrave;nh ch&acirc;m tr&agrave;, rồi sau đ&oacute; nh&igrave;n ph&iacute;a ngo&agrave;i cửa sổ vũ cảnh, than nhẹ một tiếng đạo: &quot;Nguy&ecirc;n Trực, ngươi ta quen biết đ&atilde; c&oacute; một năm, n&agrave;y một năm trung, ngươi tổng hỏi ta thi&ecirc;n hạ đại thế, ta cuối c&ugrave;ng ngậm miệng kh&ocirc;ng đ&aacute;p, h&ocirc;m nay, ngươi ta kh&ocirc;ng bằng n&oacute;i thoải m&aacute;i, được?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bưng ch&eacute;n tr&agrave; đang muốn nước uống đ&iacute;ch Từ Thứ tinh thần chấn động, thần sắc nhảy nh&oacute;t r&otilde; r&agrave;ng, bu&ocirc;ng ch&eacute;n tr&agrave; hướng Qu&aacute;ch Gia chắp tay thi lễ, hư t&acirc;m đạo: &quot;Phụng Hiếu dạy ta.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia trước mặt người n&agrave;y đ&oacute; l&agrave; Từ Thứ Từ Nguy&ecirc;n Trực, hiện tại bất qu&aacute; mười s&aacute;u tuổi tả hữu đ&iacute;ch Từ Thứ tuy rằng c&ograve;n trẻ, cũng đ&atilde; c&oacute; kh&ocirc;ng k&eacute;m đ&iacute;ch danh kh&iacute;, nhưng l&agrave; hắn đ&iacute;ch danh kh&iacute; kh&ocirc;ng ở sĩ l&acirc;m, m&agrave; ở giang hồ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thiếu ni&ecirc;n khi liền y&ecirc;u th&iacute;ch kết giao du hiệp h&agrave;o kiệt đ&iacute;ch Từ Thứ một th&acirc;n v&otilde; nghệ hơn xa tầm thường vũ phu, lập ch&iacute; trừ bạo an d&acirc;n, cứu khốn ph&ograve; nguy, ẩn ẩn c&oacute; tận diệt thi&ecirc;n hạ bất b&igrave;nh sự đ&iacute;ch ch&iacute; hướng, du lịch tứ phương khi l&agrave;m kh&ocirc;ng &iacute;t đại kho&aacute;i nh&acirc;n t&acirc;m đ&iacute;ch hiệp nghĩa việc, cũng bởi vậy th&agrave;nh chừng nổi tiếng đ&iacute;ch thiếu ni&ecirc;n hiệp sĩ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng theo thời gian chuyển dời, Từ Thứ biết bằng bản th&acirc;n vũ lực nh&igrave;n trời hạ thương sinh linh m&agrave; n&oacute;i ch&iacute;nh l&agrave; như muối bỏ biển, dục cứu thi&ecirc;n hạ, c&ograve;n phải học an bang định quốc tr&igrave; thi&ecirc;n hạ đ&iacute;ch học vấn, bởi vậy hắn độc th&acirc;n tiến đến To&aacute;nh Xuy&ecirc;n học ở trường, lại ở To&aacute;nh Xuy&ecirc;n học viện lọt v&agrave;o vắng vẻ c&ugrave;ng xem thường, sĩ tộc đệ tử tự nhi&ecirc;n xem thường hắn như vậy đ&iacute;ch giang hồ nghĩa sĩ, m&agrave; hắn đ&iacute;ch xuất th&acirc;n cũng l&agrave;m cho rất nhiều danh sĩ đại hiền tiều kh&ocirc;ng hơn mắt, tự nhi&ecirc;n đối hắn cũng liền chẳng quan t&acirc;m .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong l&ograve;ng mất m&aacute;c đến cực điểm đ&iacute;ch Từ Thứ được nghe To&aacute;nh Xuy&ecirc;n tiểu th&aacute;i c&ocirc;ng chi nh&acirc;n đức t&agrave;i đức s&aacute;ng suốt liền đến b&aacute;i phỏng, ở học đường nh&agrave; gỗ ngoại nghe nửa ng&agrave;y đ&iacute;ch học vấn sau liền vội vội v&agrave;ng vội địa phải b&aacute;i Qu&aacute;ch Gia vi sư, Qu&aacute;ch Gia đ&acirc;u, ch&iacute;nh ki&ecirc;ng kị l&agrave;m người trưởng bối việc, v&igrave; thế đem Từ Thứ dẫn cho thỏa đ&aacute;ng hữu, cộng đồng nghi&ecirc;n cứu học vấn, như thế, Từ Thứ liền lưu tại Qu&aacute;ch Gia nơi n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nguy&ecirc;n Trực, ngươi n&oacute;i đương kim thi&ecirc;n hạ thế đạo như thế n&agrave;o?&quot; Qu&aacute;ch Gia &aacute;nh mắt nh&igrave;n ngo&agrave;i cửa sổ mưa ph&ugrave;n, trong l&ograve;ng lại suy nghĩ: hai năm qu&aacute; khứ, c&ograve;n c&oacute; hai năm Trương Gi&aacute;c sẽ khởi sự , khả khoảng c&aacute;ch ch&acirc;n ch&iacute;nh đ&iacute;ch thi&ecirc;n hạ đại loạn anh h&ugrave;ng xuất hiện lớp lớp đ&iacute;ch thời đại, c&ograve;n c&oacute; t&aacute;m năm, ta thật sự, thật sự, đẳng kh&ocirc;ng kịp .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ trầm tư một l&aacute;t sau sắc mặt ngưng trọng địa n&oacute;i: &quot;Thế đạo n&oacute;ng lạnh, người chết đ&oacute;i khắp nơi, d&acirc;n ch&uacute;ng dịch tử c&ugrave;ng thực, v&ocirc; c&ugrave;ng th&ecirc; thảm.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;V&igrave; sao? Đại h&aacute;n cũng tằng thịnh cực nhất thời, v&igrave; sao k&eacute;o d&agrave;i bốn trăm năm đ&iacute;ch vương triều cơ nghiệp tới rồi h&ocirc;m nay, ng&agrave;n dặm giang sơn lại vỡ n&aacute;t?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia đ&iacute;ch &aacute;nh mắt vẫn nh&igrave;n ngo&agrave;i cửa sổ, khẩu kh&iacute; b&igrave;nh thản, m&agrave; Từ Thứ c&ograve;n lại l&agrave; tức giận bất b&igrave;nh địa n&oacute;i: &quot;N&agrave;y căn nguy&ecirc;n một l&agrave; thi&ecirc;n tử kh&ocirc;ng hiểu l&yacute; lẽ, t&ugrave;y &yacute; ngoại th&iacute;ch hoạn quan chuy&ecirc;n quyền, triều cương hỗn loạn, ch&iacute;nh lệnh kh&ocirc;ng r&otilde;, đủ loại quan lại ngồi kh&ocirc;ng ăn b&aacute;m, chỉ lo tranh quyền đoạt lợi, gi&agrave;nh bản th&acirc;n chi tư, tr&iacute; thi&ecirc;n hạ d&acirc;n ch&uacute;ng sinh tử vu kh&ocirc;ng để &yacute;, thật l&agrave; l&agrave;m nh&acirc;n thất vọng đau khổ nột. Thứ hai căn nguy&ecirc;n, thứ cho rằng ch&iacute;nh l&agrave; thế gia m&ocirc;n phiệt họa, thế gia vọng tộc đều c&oacute; thi&ecirc;n kiến b&egrave; ph&aacute;i, nh&acirc;n tư phế c&ocirc;ng, tr&iacute; đạo nghĩa kh&ocirc;ng để &yacute;, thấy chết m&agrave; kh&ocirc;ng cứu được, vắt hết &oacute;c &aacute;p bức d&acirc;n ch&uacute;ng, đi bước một đem d&acirc;n ch&uacute;ng bức hướng tuyệt lộ, nếu bọn họ c&oacute; một tia thương hại chi t&acirc;m, nh&acirc;n đức chi t&acirc;m, như vậy hiện giờ đ&iacute;ch cục diện sẽ kh&ocirc;ng hội như thế .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia l&uacute;c n&agrave;y đem &aacute;nh mắt thu trở về, chuyển hướng Từ Thứ, ch&uacute; mục hắn sau một hồi khẽ cười n&oacute;i: &quot;Nguy&ecirc;n Trực ngươi tổng n&oacute;i ch&iacute;nh m&igrave;nh kh&ocirc;ng bao l&acirc;u kh&iacute; văn học v&otilde;, ch&iacute;nh l&agrave; một giới m&atilde;ng phu, hiện giờ ngươi lời n&oacute;i, đ&atilde; kh&ocirc;ng tầm thường sĩ tử c&oacute; thể nghĩ đến đ&iacute;ch . Bất qu&aacute;, ta c&ograve;n nghĩ muốn bổ sung một c&aacute;i kh&aacute;ch quan nh&acirc;n tố, thi&ecirc;n tai, ta mặc d&ugrave; cư To&aacute;nh Xuy&ecirc;n g&oacute;c, nhưng cũng nghe n&oacute;i năm gần đ&acirc;y c&aacute;c nơi thi&ecirc;n tai kh&ocirc;ng ngừng, hoặc ph&ugrave;ng đại hạn, kinh năm v&ocirc; vũ, hoặc tao lũ lụt, vạn khoảnh đồng ruộng hủy hoại chỉ trong chốc l&aacute;t, nếu l&agrave; kh&ocirc;ng c&oacute; thi&ecirc;n tai, hiện giờ đ&iacute;ch cục diện tuyệt kh&ocirc;ng hội thảm tuyệt như vậy.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Thi&ecirc;n tai? Chẳng lẽ thi&ecirc;n cũng muốn vong đại h&aacute;n sao kh&ocirc;ng?&quot; Từ Thứ th&igrave; th&agrave;o tự n&oacute;i, thần sắc biến ảo, kh&ocirc;ng biết hắn trong l&ograve;ng suy nghĩ c&aacute;i g&igrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia tiếp tục hỏi: &quot;Nếu Nguy&ecirc;n Trực đ&atilde; muốn biết thế đạo kh&ocirc;ng c&oacute; đ&iacute;ch căn nguy&ecirc;n, như vậy xin hỏi, Nguy&ecirc;n Trực c&oacute; g&igrave; kế s&aacute;ch thần kỳ khả cứu thi&ecirc;n hạ?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ ngẩng đầu c&ugrave;ng Qu&aacute;ch Gia đối diện, thở d&agrave;i một tiếng, lắc đầu đạo: &quot;Hiện giờ chi thế, đại h&aacute;n vương triều nguy ngập nguy cơ, nếu phải tr&igrave; thi&ecirc;n hạ, phi minh qu&acirc;n kh&ocirc;ng thể, qu&eacute;t sạch triều cương, sửa trị thế gia, chỉ c&oacute; qu&acirc;n minh thần hiền, ch&iacute;nh lệnh hiểu r&otilde;, lại trị thanh minh. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ kh&ocirc;ng c&oacute; tiếp tục n&oacute;i tiếp, bởi v&igrave; hắn biết, đ&acirc;y đều l&agrave; kh&ocirc;ng c&oacute; khả năng đ&iacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia gật gật đầu xem như nhận thức đồng n&agrave;y c&aacute;ch n&oacute;i, bất qu&aacute; hắn vẫn đang hỏi: &quot;Như vậy y Nguy&ecirc;n Trực theo như lời, h&ocirc;m nay hạ chẳng phải l&agrave; kh&ocirc;ng cứu?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ trầm ng&acirc;m thật l&acirc;u sau, đ&uacute;ng l&agrave; vẫn c&ograve;n khẽ cắn m&ocirc;i n&oacute;i: &quot;Thứ nhớ r&otilde; Phụng Hiếu tằng ng&ocirc;n, tự ho&agrave;n đế tới nay, triều cương hủ bại, d&acirc;n ch&uacute;ng lầm than, đương kim thi&ecirc;n tử linh đế lại b&aacute;n quan thụ tước, c&ograve;n đ&acirc;y l&agrave; mất nước hiện ra, thứ th&acirc;m chấp nhận. Hiện giờ t&igrave;nh thế, nếu phải thi&ecirc;n hạ đại trị, tất trước đại loạn, loạn thế nếu tới, thi&ecirc;n hạ anh h&ugrave;ng chắc chắn quật khởi tứ phương, tần thất n&agrave;y lộc, quần h&ugrave;ng cộng trục, đến l&uacute;c đ&oacute;, minh chủ đắc giang sơn lại vừa thi&ecirc;n hạ đại trị.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;y phi&ecirc;n n&oacute;i c&oacute; thể n&oacute;i đại nghịch bất đạo, bất qu&aacute; cũng đại lời n&oacute;i thật, c&oacute; thấy xa đ&iacute;ch c&oacute; thể t&agrave;i hạng người tất nhi&ecirc;n đều c&oacute; thể nh&igrave;n ra điểm n&agrave;y, khả Qu&aacute;ch Gia mấy năm nay c&ugrave;ng người t&aacute;n phiếm luận địa, ch&acirc;n ch&iacute;nh c&oacute; thể n&oacute;i thoải m&aacute;i người, đơn giản chỉ c&oacute; hai người, Hi Ch&iacute; T&agrave;i c&ugrave;ng Từ Thứ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gần nhất hai người giai xuất th&acirc;n h&agrave;n m&ocirc;n, hiện giờ qu&acirc;n muội thần nịnh, đền nợ nước kh&ocirc;ng cửa, đối bọn họ m&agrave; n&oacute;i, nếu thật muốn thi&ecirc;n hạ đại loạn, tất nhi&ecirc;n t&igrave;m kiếm minh chủ đầu chi, về phần h&aacute;n vương triều vong c&ugrave;ng kh&ocirc;ng vong, kia nhưng thật ra tiếp theo . Thứ hai Qu&aacute;ch Gia hiện giờ cũng coi như To&aacute;nh Xuy&ecirc;n danh sĩ, l&agrave;m việc kh&ocirc;ng cần cẩn thận chặt chẽ nhưng cũng phải tr&aacute;nh cho họa l&agrave; từ ở miệng m&agrave; ra, đối mặt một kh&aacute;c bạn tốt Tu&acirc;n &Uacute;c, Qu&aacute;ch Gia l&agrave; trăm triệu sẽ kh&ocirc;ng n&oacute;i ra đại h&aacute;n đem vong trong lời n&oacute;i, tu&acirc;n gia lịch đại h&aacute;n thần, thế chịu ho&agrave;ng &acirc;n, ngay cả Tu&acirc;n &Uacute;c nh&igrave;n ra được đại h&aacute;n kh&iacute; suy, cũng kh&ocirc;ng d&aacute;m hướng tới mất nước phương diện suy nghĩ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia hỏi lại: &quot;Nếu thi&ecirc;n hạ đại loạn, thế gia đem ở loạn thế như thế n&agrave;o sinh tồn?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;y vừa hỏi, nhưng thật ra l&agrave;m cho Từ Thứ l&acirc;m v&agrave;o trầm tư, bất qu&aacute; cần c&ugrave; th&ocirc;ng minh đ&iacute;ch Từ Thứ rất nhanh liền để &yacute; thanh &yacute; nghĩ, trầm giọng đ&aacute;p: &quot;Thi&ecirc;n hạ nếu loạn, tất nhi&ecirc;n bạo d&acirc;n nổi l&ecirc;n bốn ph&iacute;a, thế gia tuy mạnh nhưng cũng khủng c&oacute; lật &uacute;p chi nguy, đến l&uacute;c đ&oacute;, thế gia tất nhi&ecirc;n gi&uacute;p đỡ h&ugrave;ng chủ, lệnh trong nh&agrave; c&oacute; thức chi sĩ dấn th&acirc;n v&agrave;o minh chủ trướng hạ hiệu lực, gần nhất khả bảo gia tộc an nguy, thứ hai nếu l&agrave; bọn họ nh&igrave;n tr&uacute;ng đ&iacute;ch minh chủ thật c&oacute; thể được thi&ecirc;n hạ, như vậy, thế gia tất nhi&ecirc;n c&agrave;ng hơn v&atilde;ng t&iacute;ch.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nguy&ecirc;n Trực lời n&oacute;i tự tự ch&acirc;u ngọc, loạn thế anh h&ugrave;ng nếu phải c&oacute; một phen l&agrave;m, binh m&atilde; thuế ruộng, mưu thần v&otilde; tướng, quyền sở hửu th&agrave;nh tr&igrave; đều l&agrave; tất kh&ocirc;ng thể thiếu đ&iacute;ch điều kiện, m&agrave; h&agrave;o m&ocirc;n vọng tộc khả cung thuế ruộng chi&ecirc;u binh m&atilde;i m&atilde;, thế gia đệ tử cũng phi giai b&igrave;nh thường tầm thường hạng người, loạn thế b&ecirc;n trong, thế gia đ&iacute;ch t&aacute;c dụng, kh&ocirc;ng thể khinh thường.&quot; Qu&aacute;ch Gia cho ... nữa Từ Thứ ng&atilde; một ly tr&agrave;, nhẹ nh&agrave;ng đem ch&eacute;n đổ l&ecirc;n hắn đ&iacute;ch trước mặt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ hốt hiện khu&ocirc;n mặt u sầu, chua s&oacute;t đạo: &quot;Đương kim loạn thi&ecirc;n hạ người, thế gia chiếm thứ nhất, ng&agrave;y sau thủ thi&ecirc;n hạ, thế gia cũng chiếm thứ nhất, kh&ocirc;ng biết n&agrave;y đến tột c&ugrave;ng l&agrave; ph&uacute;c hay l&agrave; họa.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vừa dứt lời, Từ Thứ rồi lại giọng căm hận đạo: &quot;To&aacute;nh Xuy&ecirc;n gi&agrave;u c&oacute; v&agrave; đ&ocirc;ng đ&uacute;c nơi, địa linh nh&acirc;n kiệt, h&agrave;o m&ocirc;n đại tộc cũng kh&ocirc;ng ở số &iacute;t, ng&agrave;y trước mấy nh&agrave; đại tộc li&ecirc;n danh l&ecirc;n lớp giảng b&agrave;i triều đ&igrave;nh mật c&aacute;o Phụng Hiếu dự trữ nu&ocirc;i dưỡng tử sĩ &yacute; đồ g&acirc;y rối, nếu kh&ocirc;ng c&oacute; tu&acirc;n gia ra mặt ở Lạc Dương cao thấp chuẩn bị vi Phụng Hiếu giải v&acirc;y, chỉ sợ Phụng Hiếu h&ocirc;m nay đ&atilde; đại họa l&acirc;m đầu, bởi vậy c&oacute; thể thấy được, thế gia l&ograve;ng dạ hẹp, n&agrave;y t&acirc;m gian &aacute;c, kh&oacute; chứa ta bối.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đối với việc n&agrave;y, Qu&aacute;ch Gia lại ch&iacute;nh l&agrave; nhẹ nh&agrave;ng cười, cũng kh&ocirc;ng để ở trong l&ograve;ng, hắn biết mấy năm gần đ&acirc;y nh&acirc;n hắn ki&ecirc;u ngạo m&agrave; đắc tội đ&iacute;ch nh&acirc;n kh&ocirc;ng ở số &iacute;t, To&aacute;nh Xuy&ecirc;n học viện nhiều lần y&ecirc;u hắn tiến đến đều bị lời n&oacute;i dịu d&agrave;ng xin miễn, thế gia đệ tử lời n&oacute;i c&oacute; ch&uacute;t bất m&atilde;n, m&agrave; hắn tiếp tế d&acirc;n chạy nạn ở d&acirc;n ch&uacute;ng trung dựng đứng uy vọng tắc lại l&agrave;m cho kh&ocirc;ng &iacute;t To&aacute;nh Xuy&ecirc;n đại hộ &aacute;m sinh ghen gh&eacute;t.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy rằng ở mặt ngo&agrave;i dường như kh&ocirc;ng c&oacute; việc g&igrave;, nhưng l&agrave; Qu&aacute;ch Gia trong l&ograve;ng lại đem thế gia sắc mặt xem đ&iacute;ch nhất thanh nhị sở, cũng &acirc;m thầm hạ quyết t&acirc;m, nếu l&agrave; ng&agrave;y sau thi&ecirc;n hạ đại loạn, đối n&agrave;y đ&oacute; thế gia tất kh&ocirc;ng thể nh&acirc;n từ nương tay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&ocirc;m nay c&ugrave;ng Từ Thứ luận thi&ecirc;n hạ đại thế, kh&ocirc;ng hy vọng đề t&agrave;i xả xa, v&igrave; thế Qu&aacute;ch Gia n&oacute;i: &quot;Nguy&ecirc;n Trực, vừa rồi ngươi ta lời ti&ecirc;n đo&aacute;n loạn thế bu&ocirc;ng xuống, như vậy ta liền hỏi đ&iacute;ch t&aacute;i cẩn thận một &iacute;t, h&ocirc;m nay hạ như thế n&agrave;o loạn đ&iacute;ch đứng l&ecirc;n? Loạn thế thủy vu khi n&agrave;o?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;N&agrave;y n&agrave;y n&agrave;y, Phụng Hiếu, thứ t&agrave;i sơ học thiển, h&aacute; c&oacute; thể dự đo&aacute;n được ng&agrave;y sau việc? H&ocirc;m nay c&oacute; thể vọng ng&ocirc;n ng&agrave;y sau thi&ecirc;n hạ đại thế cũng Phụng Hiếu dạy chi c&ocirc;ng, nếu Phụng Hiếu vui l&ograve;ng chỉ gi&aacute;o, thứ v&ocirc; c&ugrave;ng cảm k&iacute;ch.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ n&oacute;i xong chắp tay c&uacute;i đầu, một bộ khi&ecirc;m tốn thỉnh gi&aacute;o đ&iacute;ch bộ d&aacute;ng, đ&acirc;y l&agrave; l&agrave;m cho Qu&aacute;ch Gia kh&ocirc;ng thể nề h&agrave; chuyện t&igrave;nh, hắn nhưng thật ra hy vọng bằng hữu trong l&uacute;c đ&oacute; hiền ho&agrave; một ch&uacute;t, khả nhiều lần dặn d&ograve; Từ Thứ cũng chỉ l&agrave; v&ocirc; c&ocirc;ng m&agrave; phản.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia đứng dậy đến ph&iacute;a trước cửa sổ, nh&igrave;n l&ecirc;n h&ocirc;i m&ocirc;ng m&ocirc;ng đ&iacute;ch kh&ocirc;ng trung, hỏi: &quot;Ngươi cũng biết Trương Gi&aacute;c người n&agrave;o?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ sửa sang lại &yacute; nghĩ sau đ&aacute;p: &quot;Nghe đồn người n&agrave;y năm mới truyền gi&aacute;o Trung Nguy&ecirc;n c&aacute;c nơi, thi ph&ugrave; thủy ch&uacute; ngữ giải bệnh cứu người, phương bắc đến Trung Nguy&ecirc;n c&aacute;c ch&acirc;u đều c&oacute; thể được nghe người n&agrave;y thiện danh, th&aacute;i b&igrave;nh đạo hơn mười trong năm t&iacute;n đồ trăm vạn, thậm ch&iacute; kh&ocirc;ng &iacute;t quan lại đều đối th&aacute;i b&igrave;nh đạo t&ocirc;n s&ugrave;ng đầy đủ.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Vậy ngươi n&oacute;i Trương Gi&aacute;c người n&agrave;y nếu phản, triều đ&igrave;nh đương như thế n&agrave;o?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Tất ph&aacute;i trọng binh ti&ecirc;u diệt chi! Trương Gi&aacute;c tất bại! Ch&iacute;nh l&agrave;, Trương Gi&aacute;c v&igrave; sao hội phản? Hắn d&ugrave; c&oacute; t&iacute;n đồ trăm vạn, khả kia đều l&agrave; tay kh&ocirc;ng tấc sắt đ&iacute;ch b&igrave;nh d&acirc;n d&acirc;n ch&uacute;ng, c&ugrave;ng qu&acirc;n đội ch&iacute;nh quy so s&aacute;nh với, quả thực kh&ocirc;ng chịu nổi một k&iacute;ch a.&quot; Từ Thứ tuy rằng khẩu thượng kh&ocirc;ng tin, nhưng trong l&ograve;ng nhưng cũng đả khởi cổ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Người n&agrave;y được xưng đại hiền lương sư, t&iacute;n đồ trải rộng Trung Nguy&ecirc;n cập phương bắc c&aacute;c ch&acirc;u, nếu l&agrave; đăng cao một h&ocirc;, chỉ sợ hơn ph&acirc;n nửa c&aacute;i giang sơn đều phải loạn đi l&ecirc;n!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia đạm cười trở lại chỗ ngồi ngồi hạ, nhẹ giọng đạo: &quot;Ngươi nếu c&oacute; thể thu k&yacute;, thanh, u, dương, duyện, kinh, từ, dự t&aacute;m ch&acirc;u nơi đ&iacute;ch d&acirc;n t&acirc;m, lại c&oacute; trăm vạn chi ch&uacute;ng nghe ngươi cống hiến, ngươi chẳng lẽ sẽ kh&ocirc;ng c&oacute; kh&aacute;c t&acirc;m tư? Ngươi ta giai ra h&agrave;n m&ocirc;n, nếu l&agrave; sinh ở t&igrave;nh h&igrave;nh tai nạn nặng nhất đ&iacute;ch ch&acirc;u quận trung, chỉ sợ sống tạm đều nan, nếu đại hiền lương sư cứu ngươi ta hai người chi mệnh, chẳng lẽ kh&ocirc;ng sẽ v&igrave; hắn hiệu lực sao kh&ocirc;ng? Trăm vạn b&igrave;nh d&acirc;n trung c&oacute; t&agrave;i chi sĩ kh&ocirc;ng phải số &iacute;t, Trương Gi&aacute;c dưới trướng đủ người t&agrave;i ba, th&aacute;i b&igrave;nh đạo truyền gi&aacute;o gần hai mươi năm, ph&aacute;t triển tới rồi h&ocirc;m nay t&igrave;nh trạng n&agrave;y, Trương Gi&aacute;c c&ograve;n cam t&acirc;m l&agrave;m một c&aacute;i t&ocirc;n gi&aacute;o đứng đầu sao kh&ocirc;ng? Mặc d&ugrave; hắn cam t&acirc;m, hắn b&ecirc;n người đ&iacute;ch nh&acirc;n chẳng lẽ hội cam t&acirc;m? Theo t&igrave;nh thế c&agrave;ng ng&agrave;y c&agrave;ng nghi&ecirc;m trọng, th&aacute;i b&igrave;nh đạo mưu phản đ&atilde; l&agrave; t&ecirc;n đ&atilde; tr&ecirc;n d&acirc;y kh&ocirc;ng thể kh&ocirc;ng ph&aacute;t.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ nghe được kinh hồn t&aacute;ng đảm, c&aacute;i tr&aacute;n chảy ra mồ h&ocirc;i lạnh, xoa xoa l&uacute;c sau l&ograve;ng c&ograve;n sợ h&atilde;i địa n&oacute;i: &quot;Phụng Hiếu lời ấy nếu trung, đại h&aacute;n vương triều khủng c&oacute; lật &uacute;p chi nguy a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia lại lắc đầu n&oacute;i: &quot;Ngươi cũng n&oacute;i, Trương Gi&aacute;c tất bại, đại h&aacute;n vương triều tuy rằng kh&iacute; suy, lại c&ograve;n chưa tới sụp đổ đ&iacute;ch n&ocirc;ng nỗi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ mặt lộ vẻ nghi hoặc, chần chờ hỏi han: &quot;Kia Trương Gi&aacute;c nếu bại, khắp thi&ecirc;n hạ đại thế c&oacute; g&igrave; quan hệ? Mấy năm nay mưu phản việc, cũng đ&atilde; xảy ra kh&ocirc;ng &iacute;t, Trương Gi&aacute;c mưu phản c&oacute; g&igrave; bất đồng?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia nhắm mắt đạo: &quot;Trương Gi&aacute;c nếu phản, nửa giang san đem chiến hỏa mấy ng&agrave;y liền, thi&ecirc;n tử nếu phải nhanh ch&oacute;ng ti&ecirc;u diệt phản tặc, triều đ&igrave;nh đ&iacute;ch binh đủ sao kh&ocirc;ng?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ ngầm hiểu, một ch&uacute;t tức th&ocirc;ng, thất thanh đạo: &quot;Chẳng lẽ thi&ecirc;n tử kh&ocirc;ng hiểu l&yacute; lẽ tới rồi hội tướng qu&acirc;n quyền ngoại ph&oacute;ng đ&iacute;ch n&ocirc;ng nỗi?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia trợn mắt t&aacute;n thưởng đạo: &quot;C&ugrave;ng người th&ocirc;ng minh n&oacute;i chuyện ch&iacute;nh l&agrave; thoải m&aacute;i, v&igrave; mau ch&oacute;ng dập tắt thanh thế lớn đ&iacute;ch tặc qu&acirc;n, thi&ecirc;n tử tất nhi&ecirc;n l&agrave;m cho c&aacute;c nơi quận thủ tăng cường qu&acirc;n bị ti&ecirc;u diệt tặc, đắc vu tư người bị hủy bởi tư, Trương Gi&aacute;c mặc d&ugrave; bại, lại dao động đại h&aacute;n căn cơ, ho&agrave;ng quyền giữ lạc, thi&ecirc;n hạ đại loạn, quần h&ugrave;ng cũng khởi đ&iacute;ch ng&agrave;y, kh&ocirc;ng xa .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ph&ograve;ng trong l&acirc;m v&agrave;o trầm mặc, Từ Thứ t&igrave;m thời gian rất l&acirc;u t&agrave;i b&igrave;nh phục hạ t&acirc;m t&igrave;nh k&iacute;ch động, nếu c&oacute; ch&uacute;t đăm chi&ecirc;u hỏi han: &quot;Ch&iacute;nh l&agrave; Trương Gi&aacute;c thật sự hội phản sao kh&ocirc;ng?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia kh&ocirc;ng đ&aacute;p hỏi lại: &quot;Nguy&ecirc;n Trực c&oacute; thể c&oacute; t&acirc;m tư c&ugrave;ng ta đi K&yacute; Ch&acirc;u Nghiệp Th&agrave;nh một du?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ sửng sốt, hỏi: &quot;Đi Nghiệp Th&agrave;nh l&agrave;m c&aacute;i g&igrave;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Trương Gi&aacute;c ở Nghiệp Th&agrave;nh.&quot;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:1063px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'de-tu-chuong-thien-ha-dai-the', 5, '9790', '2022-08-03 02:44:39', '2022-09-29 14:01:45');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(23, '5', 'Thứ năm chương rút dao tương trợ', '<p>Ở đi th&ocirc;ng Nghiệp Th&agrave;nh đ&iacute;ch tr&ecirc;n đường, Qu&aacute;ch Gia c&ugrave;ng Từ Thứ giục ngựa đồng h&agrave;nh, trong nh&agrave; c&ocirc;ng việc giai giao ph&oacute; Hi Ch&iacute; T&agrave;i c&ugrave;ng Tả Từ, điểm n&agrave;y nhưng thật ra l&agrave;m cho Qu&aacute;ch gia thập phần y&ecirc;n t&acirc;m, Hi Ch&iacute; T&agrave;i t&agrave;i hoa xuất ch&uacute;ng l&agrave;m việc cẩn thận, Tả Từ v&otilde; nghệ mạnh thế gian hiếm thấy, một văn một v&otilde; tọa trấn trong nh&agrave;, Qu&aacute;ch Gia so với ai kh&aacute;c đều an t&acirc;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mắt thấy Nghiệp Th&agrave;nh đ&atilde; muốn tiến v&agrave;o mi mắt, Qu&aacute;ch Gia lại giữ chặt d&acirc;y cương nh&iacute;u m&agrave;y quan vọng đường ph&iacute;a trước, Từ Thứ đ&atilde; ph&aacute;t hiện ph&iacute;a trước t&igrave;nh huống, một đ&aacute;m sơn tặc ch&iacute;nh v&acirc;y c&ocirc;ng ba gi&aacute; hoa lệ xe ngựa, ven đường đ&atilde; ng&atilde; xuống kh&ocirc;ng &iacute;t thi thể, huyết s&aacute;i đầy đất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nguy&ecirc;n Trực, ngươi h&agrave;o hiệp t&ecirc;n ta sớm c&oacute; nghe thấy, h&ocirc;m nay tr&ugrave;ng hợp gặp gỡ tặc phỉ s&aacute;t lược, sao kh&ocirc;ng theo ta thấy nh&igrave;n ngươi v&otilde; nghệ như thế n&agrave;o?&quot; Qu&aacute;ch Gia n&oacute;i xong đ&atilde; r&uacute;t ra b&ecirc;n h&ocirc;ng bảo kiếm, hiển nhi&ecirc;n muốn đ&iacute;ch th&acirc;n ra trận.</p>', '<p>Ở đi th&ocirc;ng Nghiệp Th&agrave;nh đ&iacute;ch tr&ecirc;n đường, Qu&aacute;ch Gia c&ugrave;ng Từ Thứ giục ngựa đồng h&agrave;nh, trong nh&agrave; c&ocirc;ng việc giai giao ph&oacute; Hi Ch&iacute; T&agrave;i c&ugrave;ng Tả Từ, điểm n&agrave;y nhưng thật ra l&agrave;m cho Qu&aacute;ch gia thập phần y&ecirc;n t&acirc;m, Hi Ch&iacute; T&agrave;i t&agrave;i hoa xuất ch&uacute;ng l&agrave;m việc cẩn thận, Tả Từ v&otilde; nghệ mạnh thế gian hiếm thấy, một văn một v&otilde; tọa trấn trong nh&agrave;, Qu&aacute;ch Gia so với ai kh&aacute;c đều an t&acirc;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Mắt thấy Nghiệp Th&agrave;nh đ&atilde; muốn tiến v&agrave;o mi mắt, Qu&aacute;ch Gia lại giữ chặt d&acirc;y cương nh&iacute;u m&agrave;y quan vọng đường ph&iacute;a trước, Từ Thứ đ&atilde; ph&aacute;t hiện ph&iacute;a trước t&igrave;nh huống, một đ&aacute;m sơn tặc ch&iacute;nh v&acirc;y c&ocirc;ng ba gi&aacute; hoa lệ xe ngựa, ven đường đ&atilde; ng&atilde; xuống kh&ocirc;ng &iacute;t thi thể, huyết s&aacute;i đầy đất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nguy&ecirc;n Trực, ngươi h&agrave;o hiệp t&ecirc;n ta sớm c&oacute; nghe thấy, h&ocirc;m nay tr&ugrave;ng hợp gặp gỡ tặc phỉ s&aacute;t lược, sao kh&ocirc;ng theo ta thấy nh&igrave;n ngươi v&otilde; nghệ như thế n&agrave;o?&quot; Qu&aacute;ch Gia n&oacute;i xong đ&atilde; r&uacute;t ra b&ecirc;n h&ocirc;ng bảo kiếm, hiển nhi&ecirc;n muốn đ&iacute;ch th&acirc;n ra trận.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ ha ha cười, chắp tay sảng kho&aacute;i đạo: &quot;Phụng Hiếu, thứ ta n&oacute;i thẳng, văn ta kh&ocirc;ng bằng ngươi, nhưng n&agrave;y v&otilde;, ta tất thắng ngươi kh&ocirc;ng ngừng một bậc! Ch&iacute;nh l&agrave; mao tặc, đối đ&atilde;i một l&aacute;t c&oacute; thể s&aacute;t lui.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dứt lời, Từ Thứ r&uacute;t kiếm giục ngựa, xung phong liều chết m&agrave; đi, Qu&aacute;ch Gia theo s&aacute;t Sau đ&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia hỏa sơn tặc ước chừng năm mươi nhiều người, mặc bố y, trong tay binh kh&iacute; cũng l&agrave; t&agrave;n ph&aacute; kh&ocirc;ng chịu nổi, v&acirc;y c&ocirc;ng xe ngựa chỉ dựa v&agrave;o người đ&ocirc;ng thế mạnh, thủ vệ xe ngựa đ&iacute;ch vệ sĩ kh&ocirc;ng đủ mười người nhưng cũng lực lượng ngang nhau, bởi vậy c&oacute; thể thấy được, n&agrave;y đẳng tặc phỉ, c&oacute; thể n&agrave;o kh&iacute; hậu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ c&ugrave;ng Qu&aacute;ch Gia kỵ m&atilde; s&aacute;t nhập đạo tặc b&ecirc;n trong, xuất kỳ bất &yacute; liền đều tự ch&eacute;m giết hai người lập uy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bang bang</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&aacute;u tươi kh&ocirc;ng trung, đầu người nhảy l&ecirc;n kh&ocirc;ng m&agrave; rơi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia giơ l&ecirc;n bảo kiếm, t&uacute;c mục lạnh l&ugrave;ng n&oacute;i: &quot;Nghiệp Th&agrave;nh đại qu&acirc;n sắp đuổi tới, ngươi đẳng c&ograve;n kh&ocirc;ng th&uacute;c thủ chịu tr&oacute;i!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đạo tặc nh&oacute;m trước bị Qu&aacute;ch Gia c&ugrave;ng Từ Thứ trong nh&aacute;y mắt liền s&aacute;t bốn người đ&iacute;ch uy thế sở kinh sợ, t&aacute;i vừa nghe Nghiệp Th&agrave;nh đ&iacute;ch quan binh muốn tới , nhất thời sợ tới mức mặt kh&ocirc;ng c&ograve;n ch&uacute;t m&aacute;u, t&egrave; ra quần địa chung quanh chạy trối chết đi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia c&ugrave;ng Từ Thứ xoay người xuống ngựa, nh&igrave;n nh&igrave;n chung quanh đ&iacute;ch t&igrave;nh cảnh, ph&aacute;t hiện đ&atilde; chết đ&iacute;ch đại hội đại biểu nh&acirc;n d&acirc;n to&agrave;n quốc nhiều đều l&agrave; mặc t&ocirc;i tớ trang phục đ&iacute;ch thanh tr&aacute;ng nam tử, nghĩ đến hẳn l&agrave; l&agrave; n&agrave;y ba gi&aacute; hoa lệ xe ngựa đ&iacute;ch chủ nh&acirc;n trong nh&agrave; t&ocirc;i tớ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Phụng Hiếu nhanh tr&iacute;, thứ kh&ocirc;ng kịp cũng.&quot; Từ Thứ mặt lộ vẻ vẻ xấu hổ, thiếu ni&ecirc;n du hiệp đ&iacute;ch trải qua l&agrave;m cho hắn ở vừa rồi thầm nghĩ ch&eacute;m giết, đ&atilde; thấy Qu&aacute;ch Gia một c&acirc;u liền lệnh b&aacute;n trăm cường đạo chạy trối chết, n&agrave;y đẳng bổn sự, xa luận v&otilde; lực giết người phải cao si&ecirc;u gấp trăm lần.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cười m&agrave; kh&ocirc;ng n&oacute;i, đ&atilde; thấy kia vừa rồi bảo vệ trung gian xe ngựa cũng chỉ huy thị vệ ngăn địch đ&iacute;ch c&ocirc;ng tử đi tới hắn đ&iacute;ch trước mặt, chỉ thấy kia c&ocirc;ng tử cẩm b&agrave;o hoa phục, dung tư thậm vĩ, mặc d&ugrave; cha vết thương nhẹ lại vẻ mặt trấn định, hắn hướng Qu&aacute;ch Gia c&ugrave;ng Từ Thứ trịnh trọng thi c&uacute;i đầu lễ, ngẩng đầu sau t&agrave;i cảm k&iacute;ch địa n&oacute;i: &quot;Đa tạ nhị vị huynh đ&agrave;i &acirc;n cứu mạng, tại hạ Ch&acirc;n Dự, chưa thỉnh gi&aacute;o nhị vị huynh đ&agrave;i?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;To&aacute;nh Xuy&ecirc;n Qu&aacute;ch Gia.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Từ Thứ.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia c&ugrave;ng Từ Thứ chắp tay đ&aacute;p lễ, l&uacute;c sau, Qu&aacute;ch Gia coi như nhớ tới c&aacute;i g&igrave;, hỏi: &quot;Ch&acirc;n Dự? Hay l&agrave; huynh đ&agrave;i l&agrave; trung sơn v&ocirc; cực ch&acirc;n gia người?&quot;</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự thật cũng kh&ocirc;ng ngo&agrave;i &yacute; muốn, ch&acirc;n gia ở H&agrave; Bắc đ&oacute; l&agrave; thanh danh kh&ocirc;ng k&eacute;m đ&iacute;ch gia tộc, v&igrave; thế khi&ecirc;m tốn đ&aacute;p: &quot;Đ&uacute;ng l&agrave;.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; Qu&aacute;ch Gia c&oacute; thể biết ch&acirc;n gia đơn giản l&agrave; hai c&aacute;i nguy&ecirc;n nh&acirc;n, thứ nhất l&agrave; ch&acirc;n gia ra một c&aacute;i ho&agrave;ng hậu, lạc thần ch&acirc;n mật. Thứ hai l&agrave; ch&acirc;n gia l&agrave; H&agrave; Bắc cự ph&uacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave; nghĩ lại tưởng tượng hiện giờ lạc thần ch&acirc;n mật phỏng chừng c&ograve;n tại từ trong bụng mẹ l&yacute; đ&acirc;u, đối mặt sắp đ&atilde; đến đ&iacute;ch loạn thế, ch&acirc;n gia tất nhi&ecirc;n phải đầu nhập v&agrave;o một phương chư hầu, nếu kh&ocirc;ng ch&acirc;n mật cũng sẽ kh&ocirc;ng đ&oacute; nhập vi&ecirc;n gia, trở th&agrave;nh Vi&ecirc;n Thiệu đ&iacute;ch con d&acirc;u, cho n&ecirc;n hiện tại Qu&aacute;ch Gia v&ocirc; t&acirc;m tư c&ugrave;ng Ch&acirc;n Dự ở trong n&agrave;y t&aacute;n phiếm luận địa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&igrave; thế Qu&aacute;ch Gia l&uacute;c n&agrave;y n&oacute;i: &quot;Nghiệp Th&agrave;nh gần ngay trước mắt, huynh đ&agrave;i khả mau ch&oacute;ng v&agrave;o th&agrave;nh tị nạn, c&ograve;n nữa huynh đ&agrave;i lần n&agrave;y thương vong kh&ocirc;ng nhỏ, n&oacute;i vậy c&ograve;n c&oacute; kh&ocirc;ng &iacute;t chuyện t&igrave;nh cần liệu l&yacute;, ch&uacute;ng ta như vậy đừng qu&aacute;, c&ograve;n nhiều thời gian, nếu l&agrave; hữu duy&ecirc;n, định đăng m&ocirc;n b&aacute;i phỏng.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ l&agrave; biết bọn họ việc n&agrave;y l&agrave; v&igrave; Trương Gi&aacute;c m&agrave; đến, cho n&ecirc;n l&yacute; giải Qu&aacute;ch Gia đ&iacute;ch thực hiện, v&igrave; thế hướng Ch&acirc;n Dự liền &ocirc;m quyền, xoay người l&ecirc;n ngựa, t&iacute;nh to&aacute;n c&ugrave;ng Qu&aacute;ch Gia chạy tới Nghiệp Th&agrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&iacute;nh l&agrave; Ch&acirc;n Dự lại che ở hai người trước ngựa, vẻ mặt th&agrave;nh khẩn địa n&oacute;i: &quot;Nhị vị huynh đ&agrave;i vu ta c&oacute; &acirc;n cứu mạng, nếu như thế rời đi, ta Ch&acirc;n Dự chẳng phải th&agrave;nh c&oacute; &acirc;n kh&ocirc;ng b&aacute;o đ&iacute;ch tiểu nh&acirc;n, nếu l&agrave; hai vị kh&ocirc;ng ch&ecirc; kh&iacute;, đối đ&atilde;i chỉnh đốn hảo gia sự, ch&uacute;ng ta v&agrave;o th&agrave;nh một tự, l&agrave;m cho ta tạ ơn qu&aacute; nhị vị.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cũng kh&ocirc;ng cảm k&iacute;ch, lắc đầu đạo: &quot;Gặp chuyện bất b&igrave;nh r&uacute;t dao tương trợ, ta đẳng đều kh&ocirc;ng phải l&agrave; thi &acirc;n b&aacute;o đ&aacute;p đồ đệ, h&ocirc;m nay c&oacute; duyến giải huynh đ&agrave;i chi v&acirc;y bất qu&aacute; nhấc tay chi lao, kh&ocirc;ng cần để ở trong l&ograve;ng, ta đẳng c&ograve;n c&oacute; chuyện quan trọng trong người, kh&ocirc;ng tiện ở l&acirc;u, như vậy đừng qu&aacute;, sau n&agrave;y c&ograve;n gặp lại.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự bất đắc dĩ, đ&agrave;nh phải tr&aacute;nh ra đường, nh&igrave;n Qu&aacute;ch Gia c&ugrave;ng Từ Thứ rời đi đ&iacute;ch b&oacute;ng d&aacute;ng, b&oacute;p cổ tay thở d&agrave;i kh&ocirc;ng th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo vừa rồi Qu&aacute;ch Gia c&ugrave;ng Từ Thứ giục ngựa đ&aacute;nh tới đ&iacute;ch cảnh tượng đến xem, n&agrave;y hai người v&otilde; nghệ rất cao, hơn nữa Qu&aacute;ch Gia một lời liền tr&aacute; lui tặc phỉ đ&iacute;ch t&agrave;i tr&iacute;, người n&agrave;y c&oacute; dũng c&oacute; mưu a, như thế thiếu ni&ecirc;n anh h&ugrave;ng lại đau thất kết giao đ&iacute;ch cơ hội, thật sự đ&aacute;ng tiếc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ca ca, vừa rồi vị kia hiệp sĩ kh&ocirc;ng phải n&oacute;i Nghiệp Th&agrave;nh ph&aacute;i đ&iacute;ch đại qu&acirc;n nhanh đến sao kh&ocirc;ng? Như thế n&agrave;o b&acirc;y giờ c&ograve;n kh&ocirc;ng c&oacute; b&oacute;ng d&aacute;ng đ&acirc;u?&quot; Xe ngựa đi ra một vị sắc mặt t&aacute;i nhợt đ&iacute;ch tuổi thanh xu&acirc;n c&ocirc; g&aacute;i, dung mạo đoan trang t&uacute; lệ, th&acirc;n nhiều m&agrave;u la quần, đủ để kinh diễm hậu thế.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự lắc đầu cười khổ đi đến ch&acirc;n khương b&ecirc;n người giải th&iacute;ch đạo: &quot;Đại muội, ngươi c&oacute; từng nh&igrave;n đến kia hai vị thiếu hiệp từ chỗ n&agrave;o m&agrave; đến?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ph&iacute;a nam a.&quot; Ch&acirc;n khương thốt ra, lập tức sửng sốt, t&aacute;i nhợt đ&iacute;ch ngọc diện hiện l&ecirc;n một tia ưu sầu, n&oacute;i tiếp: &quot;Nghiệp Th&agrave;nh ở ph&iacute;a bắc diện, chẳng lẽ, chẳng lẽ. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;L&agrave; nha, bọn họ hướng Nghiệp Th&agrave;nh m&agrave; đi, lại như thế n&agrave;o hội biết được Nghiệp Th&agrave;nh c&oacute; đại qu&acirc;n ra khỏi th&agrave;nh ti&ecirc;u diệt, kia Qu&aacute;ch Gia l&agrave; ở h&ugrave; dọa cường đạo đ&acirc;u.&quot; Ch&acirc;n Dự nh&igrave;n tuyệt trần m&agrave; đi đ&iacute;ch hai vị thiếu ni&ecirc;n, trong l&ograve;ng lại tiếc hận kh&ocirc;ng th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Hắn thật th&ocirc;ng minh a.&quot; Hoa qu&yacute; c&ocirc; g&aacute;i ch&acirc;n khương nhất thời lộ ra th&aacute;n phục vẻ, nhưng vừa thấy đến b&ecirc;n đường tử thi liền lại hoa dung thất sắc, lui về trong xe.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sắp tiến v&agrave;o Nghiệp Th&agrave;nh đ&iacute;ch Qu&aacute;ch Gia c&ugrave;ng Từ Thứ xuống ngựa đi bộ, đi ngang qua chỗ, nh&igrave;n thấy lưu d&acirc;n ở ngo&agrave;i th&agrave;nh tốp năm tốp ba hội tụ một đo&agrave;n, cầu xin qua đường nh&acirc;n đ&iacute;ch tiếp tế, kia cảnh tượng quả thật l&agrave; &aacute;o r&aacute;ch quần manh thực kh&ocirc;ng c&oacute; kết quả ph&uacute;c, kẻ kh&aacute;c động dung.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Phụng Hiếu, ta xem kia ch&acirc;n gia người ấy dục kết giao vu ngươi, v&igrave; sao ngươi ngược lại vội v&agrave;ng rời đi đ&acirc;u? Ngươi ta việc n&agrave;y tuy l&agrave; đến xem Trương Gi&aacute;c l&agrave;m việc như thế n&agrave;o, nhưng cũng kh&ocirc;ng phải thập phần cấp b&aacute;ch a.&quot; Từ Thứ hỏi ra ch&iacute;nh m&igrave;nh đ&iacute;ch nghi hoặc, ở hắn nghĩ đến, phi &aacute;c danh lan xa đồ đệ, kh&ocirc;ng đạo l&yacute; cự chi ng&agrave;n dậm a, huống hồ kia ch&acirc;n gia người ấy c&oacute; lễ c&oacute; độ, so với chi dĩ v&atilde;ng chứng kiến,thấy mua danh chuộc tiếng đ&iacute;ch di động khoa c&ocirc;ng tử bạn th&acirc;n phải xuất sắc rất nhiều .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia theo tr&ecirc;n người lấy ra đồng tiền đưa cho cửa th&agrave;nh ngoại đ&iacute;ch thủ vệ, c&ugrave;ng Từ Thứ đang v&agrave;o th&agrave;nh, vừa đi vừa giải th&iacute;ch đạo: &quot;Ta khủng kia ch&acirc;n gia c&oacute; mời ch&agrave;o &yacute;, c&ugrave;ng với đến l&uacute;c đ&oacute; mở miệng cự tuyệt, kh&ocirc;ng bằng rời xa thị phi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hai người dẫn ngựa đi ở ng&atilde; tư đường thượng, t&igrave;m kiếm ngủ lại đ&iacute;ch kh&aacute;ch điếm, lại bỗng nhi&ecirc;n nh&igrave;n thấy ven đường c&oacute; một c&ocirc; g&aacute;i b&aacute;n m&igrave;nh t&aacute;ng phụ.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Phi ma để tang đ&iacute;ch c&ocirc; g&aacute;i quỳ gối ven đường, trước mặt nằm thẳng một khối thi thể bị chiếu che dấu, gầy yếu đ&iacute;ch c&ocirc; g&aacute;i tuy rằng th&acirc;n thể đơn bạc, nhưng theo tướng mạo đến xem nhưng thật ra c&oacute; v&agrave;i phần tư sắc, th&acirc;n thể của hắn bi&ecirc;n hội tụ kh&ocirc;ng &iacute;t v&acirc;y xem người, nghị luận đều.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&acirc;y xem đ&aacute;m người bị người t&aacute;ch ra điều đạo, một vị quần &aacute;o đẹp đẽ qu&yacute; gi&aacute; đ&iacute;ch c&ocirc;ng tử ca đi v&agrave;o quỳ xuống đất c&ocirc; g&aacute;i trước mặt, cao thấp đ&aacute;nh gi&aacute; một phen, tr&ecirc;n mặt hiện ra tham luyến vẻ, lập tức mở miệng đạo: &quot;Ngươi theo ta hồi phủ, từ nay về sau đ&oacute; l&agrave; ta Vương gia thị nữ.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhất thời, c&ocirc;ng tử ca ph&iacute;a sau tho&aacute;t ra hai c&aacute;i n&ocirc; bộc, vượt qua che dấu thi thể đ&iacute;ch chiếu sẽ đi k&eacute;o kia mặc đồ tang đ&iacute;ch c&ocirc; g&aacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khả c&ocirc; g&aacute;i lại th&acirc;n m&igrave;nh co rụt lại n&eacute; qu&aacute; khứ, ai thanh đạo: &quot;Đa tạ c&ocirc;ng tử &yacute; tốt, thỉnh c&ocirc;ng tử cấp n&ocirc; tỳ trăm tiễn t&aacute;ng phụ, đối đ&atilde;i phụ an t&aacute;ng l&uacute;c sau, ta tất tận t&acirc;m hầu hạ c&ocirc;ng tử.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Hừ, kh&ocirc;ng biết tốt xấu, c&aacute;i n&agrave;y theo ta về nh&agrave; đi, ngươi phụ đ&iacute;ch hậu sự, đều c&oacute; nh&acirc;n chăm s&oacute;c, đừng kh&ocirc;ng t&aacute;n thưởng.&quot; Vương c&ocirc;ng tử vẻ mặt kh&ocirc;ng ki&ecirc;n nhẫn, huy phất tay &yacute; bảo n&ocirc; bộc đi l&ecirc;n t&iacute;nh to&aacute;n d&ugrave;ng sức mạnh đem n&agrave;y c&ocirc; g&aacute;i lược về nh&agrave; trung.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chung quanh d&acirc;n ch&uacute;ng giai lui về ph&iacute;a sau mấy bước tr&aacute;nh ra đường, vẻ mặt c&aacute;c kh&ocirc;ng giống nhau, tiếc hận, thống hận, tức giận từ từ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;y Vương gia l&agrave; Nghiệp Th&agrave;nh đại tộc, ng&agrave;y thường l&yacute; ho&agrave;nh h&agrave;nh ngang ngược qu&aacute;n , d&acirc;n ch&uacute;ng d&aacute;m giận m&agrave; kh&ocirc;ng n&oacute;i, sợ g&acirc;y tr&ecirc;n th&acirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khanh</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một tiếng kiếm ra khỏi vỏ, mọi người chỉ thấy trước mắt ng&acirc;n quang chợt l&oacute;e m&agrave; qua, một thiếu ni&ecirc;n đem kiếm ho&agrave;nh ở tại Vương gia c&ocirc;ng tử cổ chỗ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ nh&igrave;n thấy n&agrave;y Vương gia c&ocirc;ng tử như thế b&aacute; đạo, v&igrave; thế giận từ t&acirc;m sinh, k&egrave;m hai b&ecirc;n ở Vương gia c&ocirc;ng tử.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia c&ugrave;ng Từ Thứ từ đầu tới đu&ocirc;i đem sự t&igrave;nh thấy nhất thanh nhị sở, Vương gia sắc mặt cũng trong l&ograve;ng biết r&otilde; r&agrave;ng, nếu thật sự l&agrave; vi c&ocirc; g&aacute;i bỏ vốn t&aacute;ng phụ liệu l&yacute; hậu sự, như vậy c&ocirc; g&aacute;i nhập Vương gia vi n&ocirc; vi tỳ l&agrave; thi&ecirc;n kinh địa nghĩa, nhưng n&agrave;y Vương gia c&ocirc;ng tử chỉ sợ l&agrave; nghĩ muốn xu kh&ocirc;ng ra liền cường thưởng c&ocirc; g&aacute;i , Từ Thứ hảo b&ecirc;nh vực kẻ yếu, Qu&aacute;ch Gia cũng thống hận thị cường lăng nhược, n&agrave;y bất b&igrave;nh việc đương nhi&ecirc;n muốn xen v&agrave;o quan t&acirc;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chủ nh&acirc;n bị k&egrave;m hai b&ecirc;n, hai c&aacute;i n&ocirc; bộc tự nhi&ecirc;n kh&ocirc;ng d&aacute;m vọng động, v&igrave; thế lui trở về, c&aacute;ch ba bước hốt hoảng v&ocirc; thố địa nh&igrave;n Từ Thứ, m&agrave; Vương gia c&ocirc;ng tử c&ograve;n lại l&agrave; giả bộ một bộ trấn định tự nhi&ecirc;n đ&iacute;ch bộ d&aacute;ng, mắt l&eacute; nh&igrave;n Từ Thứ run giọng đạo: &quot;Nhữ l&agrave; người phương n&agrave;o? Chẳng phải biết ta l&agrave; Nghiệp Th&agrave;nh Vương gia Tam c&ocirc;ng tử? Nếu thương ta l&ocirc;ng t&oacute;c, ngươi tất chết kh&ocirc;ng c&oacute; chỗ ch&ocirc;n.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ vẻ mặt nghi&ecirc;m khắc, hừ lạnh một tiếng, quay đầu hướng Qu&aacute;ch Gia cung k&iacute;nh hỏi han: &quot;Thiếu gia, người n&agrave;y xử tr&iacute; như thế n&agrave;o?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia trong l&ograve;ng cười thầm, tr&ecirc;n mặt lại lạnh nhạt tự nhi&ecirc;n, ngầm hiểu đạo: &quot;H&ocirc;m nay cấp Nghiệp Th&agrave;nh Vương gia một c&aacute;i mặt mũi, thả hắn.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ tu&acirc;n lệnh, thu kiếm c&ograve;n sao, lui ra ph&iacute;a sau v&agrave;i bước đi đến Qu&aacute;ch Gia ph&iacute;a sau, sắc mặt thủy chung l&atilde;nh đạm như sương.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lần n&agrave;y Vương gia c&ocirc;ng tử kinh h&atilde;i kh&ocirc;ng th&ocirc;i, đ&aacute;nh gi&aacute; nổi l&ecirc;n Qu&aacute;ch Gia c&ugrave;ng Từ Thứ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hai người mặc kh&ocirc;ng t&iacute;nh l&agrave; đẹp đẽ qu&yacute; gi&aacute; lại tuấn t&uacute; lịch sự, phong thần tuấn l&atilde;ng, lưng đeo bảo kiếm, đầu mang khăn ch&iacute;t đầu, kh&iacute; độ mờ mờ ảo ảo, tuyệt đối l&agrave; anh h&ugrave;ng thiếu ni&ecirc;n đ&iacute;ch điển phạm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;y hai người c&aacute;i g&igrave; th&acirc;n phận? Vương gia c&ocirc;ng tử kinh nghi bất định, v&igrave; thế thật cẩn thận địa d&ograve; hỏi: &quot;Kh&ocirc;ng biết hai vị huynh đ&agrave;i t&ocirc;n t&iacute;nh đại danh?&quot;</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;L&agrave;m c&agrave;n! Nh&agrave; của ta thiếu gia tục danh khởi l&agrave; ngươi c&oacute; thể hỏi đ&iacute;ch? Thừa dịp nh&agrave; của ta thiếu gia c&ograve;n chưa thay đổi chủ &yacute;, chạy nhanh cổn.&quot; Từ Thứ lạnh giọng qu&aacute;t, &aacute;nh mắt lạnh như băng địa qu&eacute;t Vương gia c&ocirc;ng tử liếc mắt một c&aacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&iacute;nh trực l&uacute;c n&agrave;y, Ch&acirc;n Dự mang theo trong nh&agrave; xa m&atilde; tiến nhập Nghiệp Th&agrave;nh, đi ngang qua nơi n&agrave;y, theo Từ Thứ r&uacute;t kiếm bắt đầu thấy được hiện tại, giờ ph&uacute;t n&agrave;y, Ch&acirc;n Dự đi ra ph&iacute;a trước, hướng Qu&aacute;ch Gia l&agrave;m thi lễ sau n&oacute;i: &quot;Kh&ocirc;ng biết huynh đ&agrave;i cớ g&igrave; ? L&uacute;c n&agrave;y?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia nhưng thật ra đĩnh ngo&agrave;i &yacute; muốn Ch&acirc;n Dự đ&iacute;ch xuất hiện, bất qu&aacute; nh&igrave;n thấy Ch&acirc;n Dự trong mắt &yacute; cười, đ&atilde; biết đối phương thấy r&otilde; ch&iacute;nh m&igrave;nh ở phẫn trư ăn hổ, nếu cũng kh&ocirc;ng n&oacute;i g&igrave; ph&aacute;, đ&oacute; l&agrave; phải gi&uacute;p hắn một phen, cho n&ecirc;n cũng liền một trang rốt cuộc, ngẩng đầu thản nhi&ecirc;n n&oacute;i: &quot;Tr&ecirc;n đường đi gặp bất b&igrave;nh sự, ra tay gi&aacute;o huấn một ch&uacute;t n&agrave;y c&agrave;n rỡ đ&iacute;ch h&agrave;o m&ocirc;n đệ tử.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi!&quot; Vương gia c&ocirc;ng tử nghe Qu&aacute;ch Gia khẩu kh&iacute; tức giận đốn sinh, c&oacute; thể thấy được đến Ch&acirc;n Dự sau liền đ&egrave; &eacute;p xuống dưới, nghĩ lại tưởng tượng: ch&acirc;n gia đại thiếu gia huống hồ đối n&agrave;y thiếu ni&ecirc;n cung k&iacute;nh v&ocirc; c&ugrave;ng, chỉ sợ n&agrave;y thiếu ni&ecirc;n lai lịch kh&ocirc;ng nhỏ, vẫn l&agrave; &iacute;t nhạ tuyệt vời.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự c&ugrave;ng n&agrave;y Vương gia c&ocirc;ng tử l&agrave; cũ thức, lại lui tới cũng kh&ocirc;ng thường xuy&ecirc;n, hời hợt chi giao m&agrave; th&ocirc;i, hiện tại hắn đi v&agrave;o Vương gia c&ocirc;ng tử trước mặt thấp giọng n&oacute;i: &quot;Vương huynh, sớm đi rời đi đi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vương gia c&ocirc;ng tử lại kinh h&atilde;i kh&ocirc;ng th&ocirc;i: hay l&agrave; người n&agrave;y ngay cả ch&acirc;n gia đều c&oacute; ch&uacute;t ki&ecirc;ng kị?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kiềm chế kh&ocirc;ng được trong l&ograve;ng t&ograve; m&ograve;, Vương gia c&ocirc;ng tử thấp giọng hỏi đạo: &quot;Kẻ m&agrave; người n&agrave;o?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự ch&iacute;nh l&agrave; lắc đầu kh&ocirc;ng n&oacute;i, Vương gia c&ocirc;ng tử nhất thời sợ tới mức hồn phi ph&aacute;ch t&aacute;n - hồn v&iacute;a l&ecirc;n m&acirc;y, hắn l&agrave; đem Ch&acirc;n Dự lắc đầu &yacute; l&yacute; giải v&igrave; Ch&acirc;n Dự l&agrave; ở b&aacute;o cho hắn n&agrave;y nh&acirc;n Vương gia kh&ocirc;ng thể tr&ecirc;u v&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xin lỗi một tiếng sau, Vương gia c&ocirc;ng tử liền dẫn n&ocirc; bộc hốt hoảng rời đi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&atilde;i Vương gia c&ocirc;ng tử đi kh&ocirc;ng c&oacute; ảnh, Ch&acirc;n Dự, Qu&aacute;ch Gia, Từ Thứ ba người nh&igrave;n nhau cười ha ha đứng l&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đa tạ huynh đ&agrave;i, gia c&ograve;n c&oacute; một chuyện thỉnh huynh đ&agrave;i hỗ trợ, n&agrave;ng n&agrave;y b&aacute;n m&igrave;nh t&aacute;ng phụ, hiếu t&acirc;m khả gia, huynh đ&agrave;i nếu l&agrave; phương tiện, thỉnh thi lấy viện thủ, th&agrave;nh to&agrave;n n&agrave;ng một phen hiếu t&acirc;m.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia hướng Ch&acirc;n Dự l&agrave;m thi lễ, Ch&acirc;n Dự vội v&agrave;ng đ&aacute;p lễ l&uacute;c sau kh&ocirc;ng n&oacute;i hai lời liền l&agrave;m cho hạ nh&acirc;n lấy tiền t&agrave;i cho kia c&ocirc; g&aacute;i, rồi sau đ&oacute; t&aacute;i hướng Qu&aacute;ch Gia n&oacute;i: &quot;L&uacute;c trước huynh đ&agrave;i từng n&oacute;i hữu duy&ecirc;n tạm biệt, hiện giờ ngươi ta gặp gỡ vu n&agrave;y Nghiệp Th&agrave;nh, dự t&aacute;i thỉnh huynh đ&agrave;i đến ta phủ đệ một tự, mong rằng huynh đ&agrave;i kh&ocirc;ng cần chối từ.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia bất đắc dĩ, đối phương n&oacute;i đều n&oacute;i đến n&agrave;y ph&acirc;n thượng , nếu kh&ocirc;ng nể t&igrave;nh cũng kh&ocirc;ng c&oacute; thể đến vẽ mặt đ&iacute;ch tr&igrave;nh độ, v&igrave; thế đ&aacute;p ứng xuống dưới.</p>', '0', 'thu-nam-chuong-rut-dao-tuong-tro', 5, '434', '2022-08-03 02:45:51', '2022-08-03 02:45:51'),
(24, '6', 'Thứ sáu chương thiết ngọc thâu hương', '<p>Ch&acirc;n gia l&agrave; H&agrave; Bắc cự ph&uacute;, Nghiệp Th&agrave;nh trung c&oacute; sản nghiệp cũng kh&ocirc;ng ngạc nhi&ecirc;n, Ch&acirc;n Dự việc n&agrave;y đ&oacute; l&agrave; đến Nghiệp Th&agrave;nh đốc tra trong nh&agrave; cửa h&agrave;ng đ&iacute;ch kinh doanh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia c&ugrave;ng Từ Thứ theo Ch&acirc;n Dự tiến v&agrave;o ch&acirc;n gia phủ đệ, hơi chỉ nghỉ ngơi chỉnh đốn, tẩy đi phong trần sau đăng đường ngồi v&agrave;o vị tr&iacute;, b&atilde;i nhắm rượu yến đ&iacute;ch Ch&acirc;n Dự ngồi ở chủ vị giơ l&ecirc;n ch&eacute;n ph&acirc;n biệt hướng Qu&aacute;ch Gia c&ugrave;ng Từ Thứ một k&iacute;nh, th&agrave;nh khẩn đạo: &quot;Nhận được hai vị ngo&agrave;i th&agrave;nh &acirc;n cứu mạng, thỉnh m&atilde;n ẩm n&agrave;y ch&eacute;n.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ba người ngửa đầu uống cạn ch&eacute;n trung rượu, sau đ&oacute; liền n&oacute;i thoải m&aacute;i, Qu&aacute;ch Gia c&ugrave;ng Từ Thứ gặp Ch&acirc;n Dự th&aacute;i độ l&agrave;m người h&agrave;o sảng ch&acirc;n th&agrave;nh, ba người n&oacute;i chuyện với nhau b&ecirc;n trong c&oacute; ch&uacute;t đầu cơ, v&igrave; thế cũng để lại hạ ch&uacute; &yacute;, lời n&oacute;i rộng r&atilde;i luận cũng kh&ocirc;ng ki&ecirc;ng kị.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia c&ugrave;ng Từ Thứ kia ch&iacute;nh l&agrave; hung c&oacute; ch&iacute; lớn hạng người, lại thường xuy&ecirc;n c&ugrave;ng Hi Ch&iacute; T&agrave;i c&ugrave;ng Tu&acirc;n &Uacute;c đ&agrave;m luận thi&ecirc;n hạ đại sự, c&oacute; thể n&oacute;i đầy bụng t&agrave;i hoa sớm xa hướng thường nh&acirc;n.</p>', '<p>Ch&acirc;n gia l&agrave; H&agrave; Bắc cự ph&uacute;, Nghiệp Th&agrave;nh trung c&oacute; sản nghiệp cũng kh&ocirc;ng ngạc nhi&ecirc;n, Ch&acirc;n Dự việc n&agrave;y đ&oacute; l&agrave; đến Nghiệp Th&agrave;nh đốc tra trong nh&agrave; cửa h&agrave;ng đ&iacute;ch kinh doanh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia c&ugrave;ng Từ Thứ theo Ch&acirc;n Dự tiến v&agrave;o ch&acirc;n gia phủ đệ, hơi chỉ nghỉ ngơi chỉnh đốn, tẩy đi phong trần sau đăng đường ngồi v&agrave;o vị tr&iacute;, b&atilde;i nhắm rượu yến đ&iacute;ch Ch&acirc;n Dự ngồi ở chủ vị giơ l&ecirc;n ch&eacute;n ph&acirc;n biệt hướng Qu&aacute;ch Gia c&ugrave;ng Từ Thứ một k&iacute;nh, th&agrave;nh khẩn đạo: &quot;Nhận được hai vị ngo&agrave;i th&agrave;nh &acirc;n cứu mạng, thỉnh m&atilde;n ẩm n&agrave;y ch&eacute;n.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ba người ngửa đầu uống cạn ch&eacute;n trung rượu, sau đ&oacute; liền n&oacute;i thoải m&aacute;i, Qu&aacute;ch Gia c&ugrave;ng Từ Thứ gặp Ch&acirc;n Dự th&aacute;i độ l&agrave;m người h&agrave;o sảng ch&acirc;n th&agrave;nh, ba người n&oacute;i chuyện với nhau b&ecirc;n trong c&oacute; ch&uacute;t đầu cơ, v&igrave; thế cũng để lại hạ ch&uacute; &yacute;, lời n&oacute;i rộng r&atilde;i luận cũng kh&ocirc;ng ki&ecirc;ng kị.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia c&ugrave;ng Từ Thứ kia ch&iacute;nh l&agrave; hung c&oacute; ch&iacute; lớn hạng người, lại thường xuy&ecirc;n c&ugrave;ng Hi Ch&iacute; T&agrave;i c&ugrave;ng Tu&acirc;n &Uacute;c đ&agrave;m luận thi&ecirc;n hạ đại sự, c&oacute; thể n&oacute;i đầy bụng t&agrave;i hoa sớm xa hướng thường nh&acirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; Ch&acirc;n Dự ở c&ugrave;ng hai người đ&iacute;ch n&oacute;i chuyện với nhau trung c&agrave;ng ph&aacute;t ra k&iacute;nh nể kh&ocirc;ng th&ocirc;i, n&agrave;y hai người học thức uy&ecirc;n b&aacute;c lại thấy giải độc đ&aacute;o, đ&agrave;m cập thi&ecirc;n hạ thời sự lại nhất ch&acirc;m kiến huyết, ng&ocirc;n ngữ b&ecirc;n trong cũng c&oacute; ch&iacute; lớn, lập tức liền hướng Qu&aacute;ch Gia c&ugrave;ng Từ Thứ hai người thỉnh gi&aacute;o đạo: &quot;Phụng Hiếu c&ugrave;ng Nguy&ecirc;n Trực giai đại t&agrave;i, hiện giờ mặc d&ugrave; thi&ecirc;n hạ chưa loạn, cũng đ&atilde; c&oacute; loạn thế hiện ra, thỉnh nhị vị dạy ta như thế n&agrave;o bảo ta ch&acirc;n gia cao thấp ở loạn thế trữ h&agrave;ng chi đạo.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia c&ugrave;ng Từ Thứ nh&igrave;n nhau cười, trong l&ograve;ng thầm nghĩ: quả nhi&ecirc;n l&agrave; thế gia đệ tử, loạn thế trước cố nh&agrave; m&igrave;nh, nhưng đ&acirc;y l&agrave; nh&acirc;n chi thường t&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ nghĩ nghĩ l&uacute;c sau n&oacute;i: &quot;Nếu luận mưu t&iacute;nh s&acirc;u xa, ta kh&ocirc;ng bằng Phụng Hiếu, vẫn l&agrave; thỉnh Phụng Hiếu n&oacute;i một ch&uacute;t đi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự vẻ mặt khẩn trương địa nh&igrave;n Qu&aacute;ch Gia, trong &aacute;nh mắt chờ đợi &yacute; r&otilde; r&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cũng kh&ocirc;ng đ&aacute;p hỏi lại: &quot;Ch&acirc;n gia ở Nghiệp Th&agrave;nh ở ngo&agrave;i gặp được đ&iacute;ch thưởng phỉ, l&agrave; người phương n&agrave;o?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự mơ hồ suy nghĩ nghĩ muốn, sau đ&oacute; n&oacute;i: &quot;Ta xem n&agrave;y quần &aacute;o binh kh&iacute; c&ugrave;ng với v&otilde; nghệ, chỉ sợ đều l&agrave; lưu d&acirc;n đi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Lưu d&acirc;n v&igrave; sao v&agrave;o rừng l&agrave;m cướp vi khấu?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;C&ugrave;ng đường.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ch&acirc;n người sử dụng sao kh&ocirc;ng c&oacute; thể cho bọn họ một ngụm mạng sống chi cơm?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự nghẹn họng nh&igrave;n tr&acirc;n trối, thốt ra: &quot;Thi&ecirc;n hạ d&acirc;n đ&oacute;i dữ dội nhiều, ri&ecirc;ng l&agrave; K&yacute; Ch&acirc;u chỉ sợ cũng c&oacute; lưu d&acirc;n hơn mười vạn, ch&acirc;n gia mặc d&ugrave; t&aacute;ng gia bại sản, chỉ sợ cũng kh&oacute; c&oacute; thể chống đỡ a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bưng ch&eacute;n rượu tự ch&acirc;m tự ẩm đ&iacute;ch Qu&aacute;ch Gia cũng kh&ocirc;ng c&oacute; đi tiều đ&atilde; muốn sắc mặt đại biến đ&iacute;ch Ch&acirc;n Dự, lẩm bẩm: &quot;Đắc đạo đa trợ, người t&agrave;i muốn th&agrave;nh một phương b&aacute; nghiệp liền phải an một phương d&acirc;n t&acirc;m, h&ugrave;ng kiệt muốn th&agrave;nh thi&ecirc;n cổ đế nghiệp tất thu thi&ecirc;n hạ d&acirc;n t&acirc;m. Ch&acirc;n gia h&agrave;o ph&uacute; nổi tiếng H&agrave; Bắc, mặc d&ugrave; ng&agrave;y sau ph&uacute; khả địch quốc, nếu vi ph&uacute; bất nh&acirc;n, như vậy loạn thế b&ecirc;n trong c&oacute; năng lực sinh tồn bao l&acirc;u đ&acirc;u? Hiện giờ K&yacute; Ch&acirc;u d&acirc;n đ&oacute;i khắp nơi, v&agrave;o rừng l&agrave;m cướp vi khấu đ&iacute;ch thưởng phỉ, chiếm sơn vi vương đ&iacute;ch sơn tặc, sổ bất thắng sổ, m&agrave; ngươi ch&acirc;n gia tiễn nhiều lương chừng, ta nếu l&agrave; tặc, cũng tất mưu ngươi ch&acirc;n gia. Phản chi, ngươi ch&acirc;n gia nếu khẳng tiếp tế một phương d&acirc;n chạy nạn, quảng thi việc thiện thu một phương d&acirc;n t&acirc;m, mặc d&ugrave; loạn thế thậm ch&iacute;, bạo d&acirc;n nổi l&ecirc;n bốn ph&iacute;a, mưu ngươi ch&acirc;n gia người, tất trước c&acirc;n nhắc một phen, gần nhất nh&acirc;n phi cỏ c&acirc;y thục c&oacute; thể v&ocirc; t&igrave;nh, ch&acirc;n gia &acirc;n đức đ&atilde; x&acirc;m nhập d&acirc;n t&acirc;m, thứ hai ngang ngược cũng kh&ocirc;ng d&aacute;m vọng động ngươi ch&acirc;n gia mảy may, tại sao? Động ch&acirc;n gia l&agrave; c&ugrave;ng d&acirc;n ly t&acirc;m cử chỉ, hơi c&oacute; thấy xa đứng đầu liền sẽ kh&ocirc;ng đi nầy tự chịu diệt vong chi đạo.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Phụng Hiếu n&agrave;y phi&ecirc;n n&oacute;i c&oacute; thể n&oacute;i đại thiện, xưa nay th&agrave;nh nghiệp lớn người, tất l&agrave; d&acirc;n t&acirc;m sở hướng đứng đầu. D&acirc;n t&acirc;m thụt l&ugrave;i l&agrave; tự chịu diệt vong! Ha ha ha!&quot; Từ Thứ m&atilde;nh qu&aacute;n ba ch&eacute;n rượu, hưng phấn dị thường.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; Ch&acirc;n Dự c&ograve;n lại l&agrave; giật m&igrave;nh trụ sau một l&uacute;c l&acirc;u, bỗng nhi&ecirc;n vỗ &aacute;n dựng l&ecirc;n, n&acirc;ng ch&eacute;n đi v&agrave;o Qu&aacute;ch Gia trước mặt k&iacute;ch động địa n&oacute;i: &quot;Phụng Hiếu một lời bừng tỉnh người trong mộng na, dự k&iacute;nh ngươi một ly.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia n&acirc;ng ch&eacute;n c&ugrave;ng Ch&acirc;n Dự đồng ẩm l&uacute;c sau lạnh nhạt đạo: &quot;Nếu n&oacute;i đ&atilde; muốn n&oacute;i đến nước n&agrave;y, ta sẽ thấy nhiều lời v&agrave;i c&acirc;u.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Thỉnh giảng thỉnh giảng, c&ograve;n thỉnh Phụng Hiếu tri v&ocirc; bất ng&ocirc;n na.&quot; Ch&acirc;n Dự v&ocirc; c&ugrave;ng cao hứng địa ngồi trở lại chủ vị, chậm đợi Qu&aacute;ch Gia b&ecirc;n dưới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ đồng dạng t&ograve; m&ograve; địa nh&igrave;n thấy Qu&aacute;ch Gia, xem hắn c&ograve;n c&oacute; g&igrave; lời b&agrave;n cao kiến.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia d&ugrave; bận vẫn ung dung ăn mấy khẩu đồ ăn &aacute;p &aacute;p rượu, sau đ&oacute; mới n&oacute;i đạo: &quot;Vừa rồi ta lời n&oacute;i, ch&iacute;nh l&agrave; ch&acirc;n gia loạn thế tự bảo vệ m&igrave;nh chi đạo, khả loạn thế nếu tới, h&agrave;o kiệt cũng khởi cắt cứ một phương l&agrave; l&uacute;c, ch&acirc;n gia lại n&ecirc;n đi nơi n&agrave;o?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự lại một lần nữa mặt lộ vẻ vẻ mặt, m&agrave; Từ Thứ tắc sớm c&oacute; chuẩn bị t&acirc;m l&yacute;, ch&uacute;t kh&ocirc;ng c&oacute; bị Qu&aacute;ch Gia đ&iacute;ch ng&ocirc;n luận sở khiếp sợ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia n&oacute;i l&yacute; n&oacute;i ngoại đ&iacute;ch &yacute; tứ rất r&otilde; r&agrave;ng, quần h&ugrave;ng cắt cứ đ&iacute;ch cục diện một khi xuất hiện, vậy kh&ocirc;ng phải H&aacute;n Triều thi&ecirc;n hạ loạn, m&agrave; l&agrave; H&aacute;n Triều giang sơn vong .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự nghe thế loại đại nghịch bất đạo đ&iacute;ch ng&ocirc;n luận, nội t&acirc;m đ&iacute;ch khiếp sợ tột đỉnh, nhưng l&agrave; hắn chỉ c&oacute; thể theo Qu&aacute;ch Gia trong lời n&oacute;i đi xuống n&oacute;i, liền hỏi: &quot;Phụng Hiếu thỉnh n&oacute;i r&otilde;.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia thản nhi&ecirc;n cười n&oacute;i: &quot;Tần thất n&agrave;y lộc, quần h&ugrave;ng cộng trục, trước c&oacute; trần thắng ng&ocirc; quảng, rồi sau đ&oacute; c&oacute; cao tổ c&ugrave;ng b&aacute; vương. Nếu ch&acirc;n gia ở l&uacute;c ấy thế gian, cao tổ c&ugrave;ng b&aacute; vương thắng bại chưa ph&acirc;n l&agrave; l&uacute;c, ch&acirc;n gia trợ ai?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trợ ai? M&atilde; hậu ph&aacute;o tự nhi&ecirc;n l&agrave; trợ cao tổ lưu bang , ch&iacute;nh l&agrave; năm đ&oacute; đ&iacute;ch t&igrave;nh thế l&agrave; Hạng V&otilde; cơ hồ sẽ th&agrave;nh tựu đế vương chi nghiệp , hiện tại đặt m&igrave;nh v&agrave;o ho&agrave;n cảnh người kh&aacute;c đi tự hỏi, Ch&acirc;n Dự đ&iacute;ch đầu đ&atilde; muốn bắt đầu hỗn loạn, nhưng l&agrave; hắn nhưng thật ra biết Qu&aacute;ch Gia lời ấy &yacute; g&igrave;, nếu l&agrave; thật sự l&agrave; tới rồi quần h&ugrave;ng tranh ph&aacute;ch đ&iacute;ch cục diện, ch&acirc;n gia quả quyết kh&ocirc;ng thể bảo tr&igrave; trung lập, nếu kh&ocirc;ng t&acirc;n chủ đăng đỉnh ng&agrave;y, ch&acirc;n gia liền c&oacute; ngập đầu tai ương, m&agrave; ch&acirc;n gia nếu phải duy tr&igrave; một phương b&aacute; chủ, n&ecirc;n trợ ai mới l&agrave; mấu chốt, nếu l&agrave; to lớn duy tr&igrave; đ&iacute;ch h&ugrave;ng chủ bại trận, như vậy ch&acirc;n gia đồng dạng c&oacute; họa diệt m&ocirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đi con đường n&agrave;o? Đi con đường n&agrave;o? Ch&acirc;n Dự t&acirc;m thần đại loạn, trăm tư kh&ocirc;ng c&oacute; kết quả đ&iacute;ch t&igrave;nh h&igrave;nh hạ đ&agrave;nh phải xin gi&uacute;p đỡ địa nh&igrave;n Qu&aacute;ch Gia, hỏi: &quot;Phụng Hiếu, theo &yacute; kiến của ngươi, ta ch&acirc;n gia n&ecirc;n như thế n&agrave;o sống y&ecirc;n vu loạn thế lại vừa kh&ocirc;ng lo đ&acirc;u?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia nghe xong cười ha ha đạo: &quot;Loạn thế b&ecirc;n trong y&ecirc;n c&oacute; kh&ocirc;ng lo chi s&aacute;ch. Từ tranh gi&agrave;nh thi&ecirc;n hạ đ&iacute;ch h&ugrave;ng kiệt, hạ đến thực kh&ocirc;ng c&oacute; kết quả ph&uacute;c đ&iacute;ch d&acirc;n ch&uacute;ng, ai c&oacute; thể cam đoan ch&iacute;nh m&igrave;nh cả đời kh&ocirc;ng lo?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự bừng tỉnh lại đ&acirc;y, thở d&agrave;i một tiếng, th&agrave;nh như Qu&aacute;ch Gia lời n&oacute;i, thi&ecirc;n hạ đại loạn l&agrave; l&uacute;c, thi&ecirc;n mệnh c&ograve;n kh&oacute; d&ograve;, huống chi bọn họ n&agrave;y đ&oacute; qu&acirc;n cờ. Lập tức th&agrave;nh khẩn đ&iacute;ch đối Qu&aacute;ch Gia chắp tay n&oacute;i: &quot;Phụng Hiếu, ta biết ngươi đại t&agrave;i, nếu c&oacute; ch&uacute;t lời b&agrave;n cao kiến, nhưng thỉnh bẩm b&aacute;o.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia lắc đầu n&oacute;i: &quot;Ta v&ocirc; kế s&aacute;ch thần kỳ, chỉ c&oacute; thể thực sự cầu thị n&oacute;i c&ugrave;ng ngươi nghe, thi&ecirc;n hạ nếu loạn, l&uacute;c đầu, tất nhi&ecirc;n quần h&ugrave;ng cũng khởi, l&uacute;c n&agrave;y thi&ecirc;n mệnh sở về kh&oacute; c&oacute; thể đo&aacute;n trước, ch&acirc;n gia khả trung lập, tự bảo vệ m&igrave;nh kh&ocirc;ng lo, rồi sau đ&oacute; ki&ecirc;u h&ugrave;ng trổ hết t&agrave;i năng khi, ch&acirc;n gia khả đ&aacute;nh cuộc một keo , nếu trung, tắc ch&acirc;n gia chẳng những vinh hoa ph&uacute; qu&yacute;, cũng c&oacute; c&oacute; thể quyền khuynh một phương. Nếu kh&ocirc;ng tr&uacute;ng, ha hả.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự sau khi nghe xong thật l&acirc;u kh&ocirc;ng n&oacute;i, thần sắc sổ độ biến ảo, cuối c&ugrave;ng hướng Qu&aacute;ch Gia khom người c&uacute;i đầu, cảm k&iacute;ch đạo: &quot;Đa tạ Phụng Hiếu thản ng&ocirc;n. H&ocirc;m nay nghe qu&acirc;n buổi n&oacute;i chuyện, ta được lợi phỉ thiển, c&oacute; thể c&ugrave;ng nhị vị đại t&agrave;i tương giao, l&agrave; ta b&igrave;nh sinh chi hạnh, ng&agrave;y sau mong rằng Phụng Hiếu nhiều hơn chỉ gi&aacute;o, đến đến đến, h&ocirc;m nay một t&uacute;y phương hưu.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ba người uống thả cửa s&acirc;u v&ocirc; c&ugrave;ng đ&ecirc;m t&agrave;i đều tự trở về ph&ograve;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia bị hạ nh&acirc;n tặng tới kh&aacute;ch ph&ograve;ng sau nằm ở tr&ecirc;n giường trằn trọc ngủ kh&ocirc;ng được, gần nhất men say kh&ocirc;ng nặng, thứ hai theo Ch&acirc;n Dự trong miệng biết được hắn lần n&agrave;y tới Nghiệp Th&agrave;nh, muội muội cũng đồng h&agrave;nh, Qu&aacute;ch Gia t&acirc;m dương kh&oacute; nhịn, muốn gặp gặp n&agrave;y lạc thần đ&iacute;ch tỷ tỷ bộ dạng như thế n&agrave;o, d&ugrave; sao lạc thần năm tỷ muội nghe n&oacute;i đều l&agrave; quốc sắc thi&ecirc;n hương, h&ocirc;m nay c&oacute; duyến, nếu l&agrave; kh&ocirc;ng đồng nhất đổ phương dung, chẳng phải l&agrave; một c&aacute;i cọc chuyện ăn năn?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&igrave; thế theo tr&ecirc;n giường đứng l&ecirc;n đ&iacute;ch Qu&aacute;ch Gia l&eacute;n l&uacute;t địa ra kh&aacute;ch ph&ograve;ng, lảo đảo địa ở trong viện t&igrave;m đường, hắn cũng kh&ocirc;ng r&otilde; r&agrave;ng n&agrave;y ch&acirc;n gia thi&ecirc;n kim nghỉ ngơi ở đ&acirc;u, bất qu&aacute; dựa theo hắn đ&iacute;ch trinh th&aacute;m, lường trước tất nhi&ecirc;n l&agrave; ở đường sau ở chỗ s&acirc;u trong đ&igrave;nh viện b&ecirc;n trong.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>H&ocirc;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thở ph&agrave;o một hơi đ&iacute;ch Qu&aacute;ch Gia giấu ở một chỗ n&uacute;i giả mặt sau, nh&igrave;n thấy kia đ&aacute;nh ng&aacute;p tuần tra ban đ&ecirc;m đ&iacute;ch hạ nh&acirc;n cũng kh&ocirc;ng xa xa đi qua, mới từ n&uacute;i giả mặt sau tha đi ra, nh&igrave;n thấy kia đ&igrave;nh viện đại m&ocirc;n bị kh&oacute;a tr&aacute;i trụ, Qu&aacute;ch Gia tả tiều hữu xem, trong l&ograve;ng c&oacute; đối s&aacute;ch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lảo đảo đ&iacute;ch đi v&agrave;o tường bi&ecirc;n, đặt l&ecirc;n đầu tường, t&iacute;nh to&aacute;n xoay người nhảy v&agrave;o trong viện.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gi&oacute; lạnh thổi tới, cảm gi&aacute;c say tập nh&acirc;n, Qu&aacute;ch Gia gh&eacute; v&agrave;o đầu tường, trong miệng đ&ocirc; than thở nang địa lẩm bẩm: &quot;Kh&iacute; ta đi người, h&ocirc;m qua ng&agrave;y kh&ocirc;ng thể lưu. Loạn l&ograve;ng ta người, h&ocirc;m nay ng&agrave;y nhiều ưu phiền. Gi&oacute; mạnh ng&agrave;n dặm tặng thu nhạn, đối n&agrave;y c&oacute; thể h&agrave;m cao lầu. Bồng Lai văn vẻ Kiến An cốt, trung gian tiểu tạ ơn lại thanh ph&aacute;t. C&acirc;u ho&agrave;i dật hưng tr&aacute;ng tư phi, dục thượng thanh thi&ecirc;n l&atilde;m trăng s&aacute;ng. R&uacute;t đao đoạn thủy thủy c&agrave;ng lưu, n&acirc;ng ch&eacute;n ti&ecirc;u sầu sầu c&agrave;ng sầu. Nh&acirc;n sinh tr&ecirc;n đời kh&ocirc;ng xưng &yacute;, Minh triều ph&aacute;t ra lộng thuyền con.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;y mới vừa gi&agrave;u to rồi một ch&uacute;t rượu đi&ecirc;n đ&iacute;ch Qu&aacute;ch Gia hai tay chống đỡ trụ th&acirc;n thể t&iacute;nh to&aacute;n nhảy v&agrave;o trong viện khi, lại ngẩng đầu hướng trong viện nh&igrave;n l&ecirc;n khi, ng&acirc;y dại.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hạo khiết dưới &aacute;nh trăng, &aacute;o trắng la quần đ&iacute;ch c&ocirc; g&aacute;i duy&ecirc;n d&aacute;ng y&ecirc;u kiều, mắt ngọc m&agrave;y ng&agrave;i, quỳnh mũi phấn thần, ba ngh&igrave;n t&oacute;c đen phi ủng hộ hay phản đối t&acirc;m, bừng tỉnh nh&acirc;n gian ti&ecirc;n tử, m&agrave; giờ ph&uacute;t n&agrave;y c&ocirc; g&aacute;i ch&iacute;nh mở to hai mắt nh&igrave;n ngẩng đầu hướng Qu&aacute;ch Gia nh&igrave;n lại, trong mắt ẩn ẩn c&oacute; hoảng sợ &yacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ai, ta c&aacute;i kia, kh&ocirc;ng phải tặc.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ph&ugrave; ph&ugrave;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia thất kinh gian theo đầu tường ng&atilde; quỵ, ng&atilde; v&agrave;o trong viện, chật vật kh&ocirc;ng chịu nổi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi, ngươi l&agrave; ban ng&agrave;y đ&atilde; cứu ch&uacute;ng ta đ&iacute;ch thiếu hiệp?&quot; Ch&acirc;n Khương thấy r&otilde; Qu&aacute;ch Gia khu&ocirc;n mặt, nhất thời cảnh gi&aacute;c bu&ocirc;ng xuống một nửa, bất qu&aacute; n&agrave;y tr&egrave;o tường nhập viện cử chỉ, thấy thế n&agrave;o cũng kh&ocirc;ng nghĩ muốn l&agrave; người khi&ecirc;m tốn hội việc l&agrave;m.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đ&uacute;ng đ&uacute;ng đối, tại hạ Qu&aacute;ch Gia, đường đột giai nh&acirc;n . Ha hả, ngươi ng&agrave;n vạn lần đừng lộ ra, ta kh&ocirc;ng c&oacute; &aacute;c &yacute;, ch&iacute;nh l&agrave;, ch&iacute;nh l&agrave;, ha hả.&quot; Qu&aacute;ch Gia vỗ vỗ tr&ecirc;n người b&ugrave;n đất, một b&ecirc;n c&ograve;n cười giải th&iacute;ch , ở hắn xem ra kh&ocirc;ng g&igrave; c&ugrave;ng lắm th&igrave; đ&iacute;ch, chỉ cần kh&ocirc;ng đ&uacute;ng n&agrave;y c&ocirc; g&aacute;i động thủ động cước, vấn đề sẽ kh&ocirc;ng như vậy nghi&ecirc;m trọng, nhiều lắm ch&iacute;nh l&agrave; lạc c&aacute;i kh&ocirc;ng tốt đ&iacute;ch thanh danh th&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Chỉ l&agrave; c&aacute;i g&igrave;? Tuy rằng ngươi h&ocirc;m qua c&ugrave;ng ta gia c&oacute; &acirc;n, khả hiện tại sở đi việc thật sự khả nghi, ngươi nếu n&oacute;i kh&ocirc;ng n&ecirc;n lời c&aacute;i l&agrave;m cho ta tin phục đ&iacute;ch đ&aacute;p &aacute;n đi ra, ta gọi ca ca bắt ngươi đi gặp quan.&quot; Ch&acirc;n Khương b&aacute;n xoay người, một bộ bất cận nh&acirc;n t&igrave;nh đ&iacute;ch bộ d&aacute;ng, Qu&aacute;ch Gia nh&igrave;n kh&ocirc;ng thấy đ&iacute;ch b&ecirc;n mặt cười thượng, kh&oacute;e miệng lại nhẹ nh&agrave;ng thượng kiều.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia đau đầu kh&ocirc;ng th&ocirc;i, chạy nhanh giải th&iacute;ch đạo: &quot;Ta thực kh&ocirc;ng phải đến thiết ngọc th&acirc;u hương đ&iacute;ch, &acirc;n? Ngươi thật muốn nghe lời n&oacute;i thật?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thiết ngọc th&acirc;u hương? Người n&agrave;y c&agrave;n rở như vậy, rất đ&aacute;ng giận !</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Khương tức giận mặt cười đỏ bừng, trừng mắt Qu&aacute;ch Gia, n&oacute;i: &quot;Hừ, ngươi nếu kh&ocirc;ng n&oacute;i n&oacute;i thật, vậy chờ ăn quan nha đ&iacute;ch cờ-l&ecirc; đi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Do dự sau một l&uacute;c l&acirc;u, Qu&aacute;ch Gia đầu một c&uacute;i, ăn ngay n&oacute;i thật, đạo: &quot;Ta nghe ngươi huynh trưởng n&oacute;i trong nh&agrave; muội muội theo hắn đang tới rồi Nghiệp Th&agrave;nh, lại nghe thấy ch&acirc;n gia nữ tử đều c&oacute; bế nguyệt tu hoa chi dung, v&igrave; thế muốn trộm vọng liếc mắt một c&aacute;i, giải quyết xong t&acirc;m nguyện. Ngươi tin sao kh&ocirc;ng?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia l&agrave; thật kh&ocirc;ng nắm chắc a, nếu nếu đối phương nhận định hắn l&agrave; c&aacute;i h&aacute;i hoa tặc, kia n&agrave;y phi&ecirc;n n&oacute;i thật ch&iacute;nh l&agrave; ba tuổi tiểu nhi cũng kh&ocirc;ng t&iacute;n, đều biết đạo nh&acirc;n cha mẹ đắc đẹp, l&eacute;n l&uacute;t đến xem liếc mắt một c&aacute;i bước đi? Ai tin nột?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Giương mắt nh&igrave;n l&ecirc;n, đ&atilde; thấy Ch&acirc;n Khương đầy mặt đỏ l&ecirc;n vẻ, trong mắt lược xấu hổ &yacute;, n&agrave;ng n&eacute; tr&aacute;nh Qu&aacute;ch Gia &aacute;nh mắt, thấp giọng hỏi đạo: &quot;Vậy ngươi gặp được, hẳn l&agrave; thất vọng rồi đi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thất vọng? Qu&aacute;ch Gia lại ngẩn ngơ, lập tức bừng tỉnh đại ngộ, n&agrave;y tiểu nha đầu l&agrave; bởi v&igrave; vi ch&iacute;nh m&igrave;nh khoa n&agrave;ng xinh đẹp da mặt mỏng thẹn th&ugrave;ng đ&acirc;u, n&oacute;i chuyện khi&ecirc;m tốn cho n&ecirc;n n&oacute;i thất vọng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Gia t&acirc;m nguyện đ&atilde; xong, như vậy c&aacute;o từ.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia xoay người đ&atilde; muốn đi, cũng kh&ocirc;ng nghĩ muốn sau lưng c&oacute; người gọi lại hắn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ai, từ từ, vừa rồi ngươi ở tr&ecirc;n tường sở ng&acirc;m c&acirc;u thơ ta nghe được kh&ocirc;ng phải rất r&otilde; r&agrave;ng, c&oacute; thể t&aacute;i niệm một lần sao kh&ocirc;ng?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Acirc;n?</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:1112px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'thu-sau-chuong-thiet-ngoc-thau-huong', 5, '434', '2022-08-03 02:47:44', '2022-08-03 02:47:44');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(25, '7', 'Thứ bảy chương dẹp yên thế gia', '<p>H&ocirc;m sau s&aacute;ng sớm, Ch&acirc;n Dự theo trong l&uacute;c ngủ mơ tỉnh lại, hạ nh&acirc;n hầu hạ mặc quần &aacute;o rửa mặt, ch&iacute;nh sửa sang lại ăn mặc khi mở miệng hỏi đạo: &quot;Hai vị kh&aacute;ch qu&yacute; c&oacute; từng đứng dậy?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ở b&ecirc;n cạnh hậu đ&iacute;ch &aacute;o xanh gia n&ocirc; mặt lộ vẻ kh&oacute; xử địa đ&aacute;p: &quot;Vị kia Từ Thứ c&ocirc;ng tử đ&atilde; muốn đứng dậy, đang ở trong viện luyện kiếm, m&agrave; vị kia Qu&aacute;ch c&ocirc;ng tử, Qu&aacute;ch c&ocirc;ng tử. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Chuyện g&igrave; che che lấp dấu, n&oacute;i!&quot; Ch&acirc;n Dự nh&iacute;u m&agrave;y qu&aacute;t, mặt lộ vẻ kh&ocirc;ng hờn giận vẻ, trong l&ograve;ng cũng l&agrave; ở lo lắng, sợ chậm trễ Qu&aacute;ch Gia.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia hạ nh&acirc;n quỳ tr&ecirc;n mặt đất dập đầu đạo: &quot;Thỉnh Đại c&ocirc;ng tử thứ tội, đ&ecirc;m qua tiểu nh&acirc;n tuần tra ban đ&ecirc;m khi nghe được tiểu thư trong viện truyền đến đ&agrave;m tiếu thanh, tiểu nh&acirc;n trong l&ograve;ng sinh nghi liền ở đ&igrave;nh viện ngo&agrave;i cửa nghe l&eacute;n, lại ph&aacute;t hiện, ph&aacute;t hiện, ph&aacute;t hiện c&ugrave;ng tiểu thư đ&agrave;m tiếu người đ&uacute;ng l&agrave; Qu&aacute;ch c&ocirc;ng tử, về phần Qu&aacute;ch c&ocirc;ng tử như H&agrave; Tiến nhập tiểu thư trong đ&igrave;nh viện, tiểu nh&acirc;n kh&ocirc;ng biết.&quot;</p>', '<p>H&ocirc;m sau s&aacute;ng sớm, Ch&acirc;n Dự theo trong l&uacute;c ngủ mơ tỉnh lại, hạ nh&acirc;n hầu hạ mặc quần &aacute;o rửa mặt, ch&iacute;nh sửa sang lại ăn mặc khi mở miệng hỏi đạo: &quot;Hai vị kh&aacute;ch qu&yacute; c&oacute; từng đứng dậy?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ở b&ecirc;n cạnh hậu đ&iacute;ch &aacute;o xanh gia n&ocirc; mặt lộ vẻ kh&oacute; xử địa đ&aacute;p: &quot;Vị kia Từ Thứ c&ocirc;ng tử đ&atilde; muốn đứng dậy, đang ở trong viện luyện kiếm, m&agrave; vị kia Qu&aacute;ch c&ocirc;ng tử, Qu&aacute;ch c&ocirc;ng tử. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Chuyện g&igrave; che che lấp dấu, n&oacute;i!&quot; Ch&acirc;n Dự nh&iacute;u m&agrave;y qu&aacute;t, mặt lộ vẻ kh&ocirc;ng hờn giận vẻ, trong l&ograve;ng cũng l&agrave; ở lo lắng, sợ chậm trễ Qu&aacute;ch Gia.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia hạ nh&acirc;n quỳ tr&ecirc;n mặt đất dập đầu đạo: &quot;Thỉnh Đại c&ocirc;ng tử thứ tội, đ&ecirc;m qua tiểu nh&acirc;n tuần tra ban đ&ecirc;m khi nghe được tiểu thư trong viện truyền đến đ&agrave;m tiếu thanh, tiểu nh&acirc;n trong l&ograve;ng sinh nghi liền ở đ&igrave;nh viện ngo&agrave;i cửa nghe l&eacute;n, lại ph&aacute;t hiện, ph&aacute;t hiện, ph&aacute;t hiện c&ugrave;ng tiểu thư đ&agrave;m tiếu người đ&uacute;ng l&agrave; Qu&aacute;ch c&ocirc;ng tử, về phần Qu&aacute;ch c&ocirc;ng tử như H&agrave; Tiến nhập tiểu thư trong đ&igrave;nh viện, tiểu nh&acirc;n kh&ocirc;ng biết.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự sửng sốt, Qu&aacute;ch Gia c&ugrave;ng Ch&acirc;n Khương đ&ecirc;m khuya ở trong đ&igrave;nh viện hẹn h&ograve;? Hắn trong l&ograve;ng c&oacute; nghi hoặc c&ugrave;ng lo lắng, lớn tiếng hỏi: &quot;Việc n&agrave;y thật sao?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gia ph&oacute; sợ tới mức vội v&agrave;ng dập đầu, vội v&agrave;ng n&oacute;i: &quot;Thi&ecirc;n ch&acirc;n vạn x&aacute;c, những c&acirc;u l&agrave; thật. Tiểu nh&acirc;n lo lắng Qu&aacute;ch c&ocirc;ng tử đối tiểu thư g&acirc;y rối, v&igrave; thế ở ngo&agrave;i cửa vẫn thủ , trong viện khi th&igrave; truyền đến tiểu thư tiếng cười, khi th&igrave; truyền đến Qu&aacute;ch c&ocirc;ng tử ng&acirc;m xướng tiếng động, thẳng đến canh năm thi&ecirc;n Qu&aacute;ch c&ocirc;ng tử t&agrave;i tr&egrave;o tường xuất viện trở về kh&aacute;ch ph&ograve;ng, tiểu nh&acirc;n l&uacute;c ấy sợ h&atilde;i bị ph&aacute;t hiện, v&igrave; thế trốn v&agrave;o đ&igrave;nh viện ngoại đ&iacute;ch bụi hoa trung.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự sắc mặt vừa chậm, phất tay k&ecirc;u gia ph&oacute; đứng dậy, ăn mặc chỉnh tề sau mới đi ra ph&ograve;ng ngủ, c&agrave;ng nghĩ cũng muốn kh&ocirc;ng th&ocirc;ng Qu&aacute;ch Gia như thế n&agrave;o hội c&ugrave;ng đại muội c&oacute; c&ugrave;ng xuất hiện, theo l&yacute; thuyết đ&acirc;y l&agrave; hai người lần đầu gặp mặt, nhưng lại kh&ocirc;ng phải ch&iacute;nh thức trường hợp, v&igrave; sao hội chỉnh đ&ecirc;m n&oacute;i chuyện với nhau, c&ograve;n truyền ra hoan ca truyện cười?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hay l&agrave; Qu&aacute;ch Gia đối đại muội cố &yacute;?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nghĩ đến đ&acirc;y, Ch&acirc;n Dự dừng lại cước bộ, ch&agrave; x&aacute;t thủ suy nghĩ s&acirc;u xa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Luận tuổi, Qu&aacute;ch Gia c&ugrave;ng Ch&acirc;n Khương k&eacute;m kh&ocirc;ng lớn, Ch&acirc;n Khương lớn tuổi Qu&aacute;ch Gia hai tuổi, nhưng đều c&ograve;n ch&uacute;c tuổi nhỏ, nếu thực sự &yacute;, khả trước định ra việc h&ocirc;n nh&acirc;n. Luận diện mạo t&agrave;i hoa, Qu&aacute;ch Gia l&agrave; đương thời hiếm thấy đ&iacute;ch anh t&agrave;i tuấn ngạn, đại muội cũng l&agrave; đọc đủ thứ thi thư mỹ mạo v&ocirc; song đ&iacute;ch t&agrave;i nữ, c&oacute; thể n&oacute;i trai t&agrave;i g&aacute;i sắc. Luận gia thế, ch&acirc;n gia mặc d&ugrave; kh&ocirc;ng phải hậu duệ qu&yacute; tộc l&uacute;c sau, nhưng cũng l&agrave; h&agrave;o m&ocirc;n đại tộc, nhưng Qu&aacute;ch Gia cũng h&agrave;n m&ocirc;n xuất th&acirc;n, m&ocirc;n kh&ocirc;ng lo, hộ kh&ocirc;ng đ&uacute;ng, n&agrave;y chướng ngại giống như hồng c&acirc;u kh&oacute; c&oacute; thể vượt qua a.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự một l&ograve;ng một dạ muốn c&ugrave;ng Qu&aacute;ch Gia đ&aacute;p thượng quan hệ, c&ugrave;ng l&uacute;c l&agrave; bởi v&igrave; vi h&agrave;ng năm v&agrave;o Nam ra Bắc, kiến thức kh&ocirc;ng &iacute;t c&aacute;i gọi l&agrave; đ&iacute;ch vương c&ocirc;ng l&uacute;c sau, quyền qu&yacute; người ấy, đan luận t&agrave;i hoa c&oacute; thể thắng Qu&aacute;ch Gia người, &iacute;t chi lại &iacute;t, c&agrave;ng miễn b&agrave;n ch&iacute; hướng c&ugrave;ng thấy xa . Về phương diện kh&aacute;c, Ch&acirc;n Dự tự biết t&agrave;i hoa b&igrave;nh thường, nhưng hắn h&agrave;ng năm kinh thương b&ecirc;n ngo&agrave;i, kiến thức uy&ecirc;n b&aacute;c, n&agrave;y H&aacute;n Triều thi&ecirc;n hạ hiện giờ đ&atilde; l&agrave; bại vong chi tượng, loạn thế b&ecirc;n trong, nếu kh&ocirc;ng ai t&agrave;i b&agrave;y mưu t&iacute;nh kế, ch&acirc;n gia đ&iacute;ch tiền cảnh sẽ ảm đạm kh&ocirc;ng &aacute;nh s&aacute;ng, nếu c&oacute; Qu&aacute;ch Gia tương trợ, ch&acirc;n gia kh&ocirc;ng lo!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khả Qu&aacute;ch Gia đ&iacute;ch gia thế, gia thế. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự vỗ tay một c&aacute;i, bừng tỉnh bừng tỉnh b&agrave;n th&igrave; th&agrave;o lẩm bẩm: &quot;Nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave; Phụng Hiếu xuất th&acirc;n h&agrave;n m&ocirc;n, kh&ocirc;ng c&oacute; quyền v&ocirc; thế, nếu t&agrave;i c&aacute;n v&igrave; ta ch&acirc;n gia chi tế, chắc chắn to lớn phụ trợ. Phụ th&acirc;n chi tr&iacute;, định c&oacute; thể hiểu được c&aacute;i khổ của ta t&acirc;m!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khi tới sau giờ ngọ Qu&aacute;ch Gia t&agrave;i rời giường, rửa mặt mặc quần &aacute;o l&uacute;c sau đi v&agrave;o ch&iacute;nh đường, nh&igrave;n thấy Từ Thứ c&ugrave;ng Ch&acirc;n Dự đang ở ẩm tr&agrave; tự thoại, cười khổ tiến l&ecirc;n đối Ch&acirc;n Dự xin lỗi đạo: &quot;Gia đ&ecirc;m qua say rượu l&uacute;c sau bị ma quỷ &aacute;m ảnh, loạn nhập lệnh muội đ&igrave;nh viện b&ecirc;n trong, mong rằng thứ lỗi.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ vẻ mặt kinh ngạc, Qu&aacute;ch Gia tối h&ocirc;m qua c&ograve;n l&agrave;m bực n&agrave;y phong lưu sự? L&uacute;c n&agrave;y tr&ecirc;u đ&ugrave;a: &quot;To&aacute;nh Xuy&ecirc;n thi&ecirc;n t&agrave;i Qu&aacute;ch Phụng Hiếu cư nhi&ecirc;n vẫn l&agrave; c&aacute;i phong lưu l&atilde;ng tử, thứ h&ocirc;m nay xem như mở rộng ra nh&atilde;n giới . Tiểu th&aacute;i c&ocirc;ng, h&ocirc;m nay sau ngươi chỉ sợ vừa muốn nhiều đăng đồ tử đ&iacute;ch danh hiệu đi, ha ha.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hung tợn địa trừng mắt nh&igrave;n Từ Thứ liếc mắt một c&aacute;i, Qu&aacute;ch Gia biết r&otilde; l&uacute;c n&agrave;y kh&ocirc;ng kh&iacute; cứ việc coi như mở ra, nhưng h&agrave;o m&ocirc;n đại tộc coi trọng nề nếp gia đ&igrave;nh, tất nhi&ecirc;n sẽ kh&ocirc;ng khinh tha loại chuyện n&agrave;y, c&ugrave;ng với ng&agrave;y sau bị người n&oacute;i r&otilde; chỗ yếu, chẳng ch&iacute;nh m&igrave;nh h&agrave;o ph&oacute;ng thừa nhận qu&ecirc;n đi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự nhưng thật ra kh&ocirc;ng nghĩ tới Qu&aacute;ch Gia d&aacute;m l&agrave;m d&aacute;m chịu, t&iacute;nh t&igrave;nh thẳng thắn, trong l&ograve;ng tăng th&ecirc;m thưởng thức &yacute;, xua tay cười n&oacute;i: &quot;Yểu điệu thục nữ, qu&acirc;n tử hảo cầu. Nh&agrave; của ta đại muội mặc d&ugrave; xưng kh&ocirc;ng hơn thi&ecirc;n tư quốc sắc, nhưng cũng c&oacute; tri thức hiểu lễ nghĩa, dung mạo thượng giai, ta nếu l&agrave; Phụng Hiếu, cũng tất c&oacute; t&acirc;m gặp thượng một mặt.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cười khổ kh&ocirc;ng th&ocirc;i, Từ Thứ cũng l&agrave; sửng sốt, nghĩ lại tưởng tượng liền đo&aacute;n được Ch&acirc;n Dự đ&iacute;ch t&acirc;m tư, trong l&ograve;ng thở d&agrave;i: quả nhi&ecirc;n như Phụng Hiếu sở liệu, ch&acirc;n gia đ&atilde; c&oacute; mời ch&agrave;o &yacute;, lại thật kh&ocirc;ng ngờ đ&uacute;ng l&agrave; chi&ecirc;u tế như vậy rất nặng đ&iacute;ch thủ đoạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; t&acirc;m tư linh hoạt đ&iacute;ch Qu&aacute;ch Gia lại c&oacute; thể n&agrave;o kh&ocirc;ng biết Ch&acirc;n Dự n&oacute;i ngoại &yacute;, nhưng l&agrave; chỉ c&oacute; thể giả c&acirc;m vờ điếc sung hồ đồ, tả cố ng&ocirc;n hắn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Việc n&agrave;y đến Nghiệp Th&agrave;nh kh&ocirc;ng phải v&igrave; phong hoa tuyết nguyệt, bởi vậy Qu&aacute;ch Gia c&ugrave;ng Từ Thứ liền đưa ra c&aacute;o từ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự kinh ngạc hỏi han: &quot;Hay l&agrave; ta chậm trễ kh&ocirc;ng chu to&agrave;n, nhị vị v&igrave; sao n&oacute;ng l&ograve;ng rời đi?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia lắc đầu đạo: &quot;Ch&acirc;n gia đ&atilde;i kh&aacute;ch cực kỳ chu đ&aacute;o, ch&iacute;nh l&agrave; ta c&ugrave;ng với Nguy&ecirc;n Trực việc n&agrave;y Nghiệp Th&agrave;nh ch&iacute;nh l&agrave; du lịch, h&ocirc;m nay liền đi trong th&agrave;nh du l&atilde;m một phen, nếu c&oacute; ch&uacute;t nh&agrave;n hạ, lại đến ch&acirc;n phủ quấy rầy.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự biết r&otilde; n&agrave;y hai người c&oacute; chủ kiến, nếu đ&atilde; quyết định rời đi, tất kh&ocirc;ng thể cường lưu, v&igrave; thế tiếc hận đạo: &quot;Như thế, ta liền cung tặng nhị vị, nếu c&oacute; ch&uacute;t sở nhu nhưng thỉnh ng&ocirc;n chi, đủ khả năng việc, ta tuyệt kh&ocirc;ng chối từ. Ngo&agrave;i ra, &iacute;t ng&agrave;y nữa ta đem phản hồi trung sơn v&ocirc; cực, nếu nhị vị c&oacute; hạ, mời đến ch&acirc;n gia l&agrave;m kh&aacute;ch, ta tất qu&eacute;t dọn giường chiếu hoan ngh&ecirc;nh.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đi ra ch&acirc;n gia phủ đệ sau, Qu&aacute;ch Gia c&ugrave;ng Từ Thứ nắm m&atilde; hướng trong th&agrave;nh kh&aacute;ch điếm m&agrave; đi, tr&ecirc;n đường, Từ Thứ cảm th&aacute;n địa n&oacute;i: &quot;Ch&acirc;n gia tuy l&agrave; H&agrave; Bắc đại hộ, trong nh&agrave; đệ tử đức hạnh thượng giai, nếu thi&ecirc;n hạ thế gia giai như thế, d&acirc;n ch&uacute;ng cũng sẽ kh&ocirc;ng bị buộc thượng tuyệt lộ.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia c&ograve;n lại l&agrave; lạnh nhạt cười n&oacute;i: &quot;Đế vương c&oacute; minh qu&acirc;n c&ugrave;ng h&ocirc;n qu&acirc;n, thần tử c&oacute; hiền thần c&ugrave;ng nịnh thần, d&acirc;n ch&uacute;ng c&oacute; thuận d&acirc;n c&ugrave;ng bạo d&acirc;n, th&acirc;n phận c&ugrave;ng địa vị cũng kh&ocirc;ng thể quyết định n&agrave;y l&agrave;m việc t&aacute;c phong, cứu n&agrave;y căn nguy&ecirc;n, ch&iacute;nh l&agrave; nh&acirc;n t&iacute;nh sở tr&iacute;.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ gật đầu thụ gi&aacute;o, bội phục loại t&igrave;nh cảm du nhi&ecirc;n nhi sinh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đương Qu&aacute;ch Gia c&ugrave;ng Từ Thứ đang ở kh&aacute;ch điếm d&agrave;n xếp h&agrave;nh trang khi, ch&acirc;n gia biệt viện c&ocirc;ng ch&iacute;nh ở ch&ograve;i nghỉ m&aacute;t l&yacute; đọc s&aacute;ch đ&iacute;ch Ch&acirc;n Dự lại kinh ngạc địa nh&igrave;n thấy trước mặt chạy tới đ&iacute;ch đại muội, c&ograve;n chưa mở miệng hỏi, chỉ thấy Ch&acirc;n Khương mặt lộ vẻ h&aacute;o sắc hỏi han: &quot;Qu&aacute;ch Gia đi rồi?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đi rồi, một c&aacute;i canh giờ tiền đi đ&iacute;ch.&quot; Ch&acirc;n Dự thực ng&ocirc;n bẩm b&aacute;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Khương c&agrave;ng cấp, hỏi: &quot;Kia đại ca v&igrave; sao kh&ocirc;ng ngăn cản hắn a?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự cười khẽ, hỏi ngược lại: &quot;Qu&aacute;ch Gia l&agrave; ta ch&acirc;n gia kh&aacute;ch qu&yacute;, hắn nghĩ đến ta hoan ngh&ecirc;nh chi tới, hắn c&ograve;n muốn chạy ta cung tống xuất m&ocirc;n, ta v&igrave; sao phải ngăn đ&oacute;n hắn? Lại c&oacute; c&aacute;i g&igrave; l&yacute; do đưa hắn cường ở lại trong phủ?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Bởi v&igrave;, bởi v&igrave;. . . Hắn như thế n&agrave;o cứ như vậy đi rồi. . .&quot; Ch&acirc;n Khương lo lắng đ&iacute;ch thần sắc bỗng nhi&ecirc;n biến đổi, vạn phần buồn bả c&ugrave;ng mất m&aacute;c di động hiện tại xinh đẹp nho nh&atilde; đ&iacute;ch tr&ecirc;n mặt, ngồi ở ca ca b&ecirc;n người, tiện đ&agrave; thở d&agrave;i một tiếng, rầu rĩ kh&ocirc;ng vui.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự thấy n&agrave;ng n&agrave;y ph&uacute;c bộ d&aacute;ng, cũng l&agrave; cảm th&aacute;n Qu&aacute;ch Gia mị lực to lớn, bất qu&aacute; c&ugrave;ng đại muội gặp mặt một lần liền l&agrave;m cho đại muội như thế n&oacute;ng ruột n&oacute;ng gan, nghĩ đến cũng l&agrave;, người kia bộ dạng anh vĩ tuấn mỹ, t&agrave;i hoa xuất ch&uacute;ng, lại ph&oacute;ng đ&atilde;ng kh&ocirc;ng kềm chế được, như vậy đ&iacute;ch kỳ nam tử, nữ nh&acirc;n v&igrave; sao kh&ocirc;ng ưu &aacute;i?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;R&uacute;t đao đoạn thủy thủy c&agrave;ng lưu, n&acirc;ng ch&eacute;n ti&ecirc;u sầu sầu c&agrave;ng sầu, nh&acirc;n sinh tr&ecirc;n đời kh&ocirc;ng xưng &yacute;, Minh triều ph&aacute;t ra lộng thuyền con. Đại ca, ta bỗng nhi&ecirc;n nghĩ muốn như hắn theo như lời vậy tị thế ti&ecirc;u dao .&quot; Ch&acirc;n Khương đầu phiến diện, kh&oacute;e mắt tr&agrave;o ra một giọt nước mắt, t&acirc;m t&igrave;nh thập phần hạ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự kinh ngạc đạo: &quot;Đại muội, n&agrave;y thi l&agrave; ngươi l&agrave;m đ&iacute;ch?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Khương một bĩu m&ocirc;i, sinh hờn dỗi đạo: &quot;C&aacute;i kia Qu&aacute;ch Phụng Hiếu l&agrave;m đ&iacute;ch, to&agrave;n bộ thi l&agrave; như vậy.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự nghe xong ha ha cười n&oacute;i: &quot;Qu&aacute;ch Phụng Hiếu, thực kh&ocirc;ng hỗ thi&ecirc;n t&agrave;i t&ecirc;n, c&agrave;ng ki&ecirc;m m&agrave;u sắc đẹp đẽ phong lưu, tiểu th&aacute;i c&ocirc;ng thiện danh lan xa, như thế nam nhi, ta nếu l&agrave; nữ tử, tất phi người n&agrave;y kh&ocirc;ng lấy chồng.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Khương nghe ra đại ca đ&iacute;ch &yacute; tại ng&ocirc;n ngoại, xấu hổ đến đầy mặt đỏ bừng, rồi lại nghi hoặc đạo: &quot;Thi&ecirc;n t&agrave;i? Tiểu th&aacute;i c&ocirc;ng? Đ&acirc;y đều l&agrave; c&aacute;i g&igrave; a?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự cố &yacute; điếu n&agrave;ng ăn uống, kh&ocirc;ng ch&uacute;t để &yacute; đạo: &quot;Qu&aacute;ch Gia a, hắn kh&ocirc;ng n&oacute;i cho ngươi sao kh&ocirc;ng? Hắn ở To&aacute;nh Xuy&ecirc;n được xưng thi&ecirc;n t&agrave;i, lại c&oacute; tiểu th&aacute;i c&ocirc;ng đ&iacute;ch danh hiệu.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ai nha, đại ca, ngươi đừng cười ta , đuổi mau n&oacute;i cho ta biết đi.&quot; Ch&acirc;n Khương phe phẩy đại ca đ&iacute;ch c&aacute;nh tay cấp kh&oacute; dằn nổi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&acirc;n Dự cũng kh&ocirc;ng hay n&oacute;i giỡn , lập tức đem hắn biết đạo về Qu&aacute;ch Gia chuyện t&igrave;nh đều n&oacute;i cho Ch&acirc;n Khương.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nghiệp Th&agrave;nh phố x&aacute; sầm uất một gian tr&ecirc;n tửu l&acirc;u, Qu&aacute;ch Gia c&ugrave;ng Từ Thứ l&acirc;m song tương đối m&agrave; ngồi, nh&igrave;n l&acirc;u ngoại ng&atilde; tư đường thượng vội v&agrave;ng m&agrave; đến đ&iacute;ch quan binh n&acirc;ng đi b&ecirc;n đường g&oacute;c đ&iacute;ch hai cổ thi thể lại vội v&agrave;ng rời đi, Qu&aacute;ch Gia nhắm mắt thở d&agrave;i: &quot;Cửa son rượu thịt thối, lộ c&oacute; đ&ocirc;ng chết cốt.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ cũng l&agrave; cảm kh&aacute;i h&agrave;ng vạn h&agrave;ng ngh&igrave;n địa n&oacute;i: &quot;C&ograve;n như vậy đi xuống, người chết đ&oacute;i ng&agrave;n dậm, d&acirc;n ch&uacute;ng khi n&agrave;o mới c&oacute; thể cơm no &aacute;o ấm a.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ch&iacute;nh trực l&uacute;c n&agrave;y, ng&atilde; tư đường thượng đi tới một h&agrave;ng cẩm y hoa phục đ&iacute;ch c&ocirc;ng tử bạn th&acirc;n, bọn họ một b&ecirc;n hướng ven đường nhưng đồng tiền, một b&ecirc;n đ&aacute; văng chạy đến bọn họ b&ecirc;n người kiểm đồng tiền đ&iacute;ch d&acirc;n đ&oacute;i, một đường m&agrave; đến, cuồng tiếu kh&ocirc;ng ngừng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ trong l&ograve;ng sinh giận, vừa muốn đứng dậy lại bị Qu&aacute;ch Gia n&acirc;ng thủ ngăn lại, khẽ lắc đầu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&atilde;i đ&aacute;m kia c&ocirc;ng tử ca đi xa sau, Từ Thứ kh&ocirc;ng đ&agrave;nh l&ograve;ng đạo: &quot;Thế đạo kh&ocirc;ng c&oacute;, thế gia đệ tử giai tự so với người khi&ecirc;m tốn, lại đi như vậy v&ocirc; đức v&ocirc; nghĩa việc, thật sự thật giận.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia thở d&agrave;i một tiếng, n&oacute;i: &quot;Nguy&ecirc;n Trực, ngươi hiệp nghĩa t&acirc;m địa, ta đổng, khả ngươi v&igrave; sao đến To&aacute;nh Xuy&ecirc;n học ở trường? Bởi v&igrave; ngươi biết, ngươi một người s&aacute;t v&ocirc; c&ugrave;ng thi&ecirc;n hạ gian &aacute;c đồ đệ. H&ocirc;m nay ngươi ta chứng kiến,thấy, kh&ocirc;ng ngại thử nghĩ chi, vừa rồi n&agrave;y h&agrave;o m&ocirc;n đệ tử c&ugrave;ng d&acirc;n đ&oacute;i c&oacute; g&igrave; bất đồng? Theo &yacute; ta đến, n&agrave;y đ&oacute; gi&aacute; &aacute;o t&uacute;i cơm chẳng qua đầu một c&aacute;i hảo thai, sinh ở ph&uacute; qu&yacute; nh&agrave;, nếu bọn họ l&agrave; b&igrave;nh d&acirc;n d&acirc;n ch&uacute;ng trong lời n&oacute;i, lại như thế n&agrave;o ỷ thế hiếp người đ&acirc;u? M&agrave; ở bọn họ trong mắt đ&iacute;ch d&acirc;n đen nếu sinh ở vương hầu đem m&ocirc;n, th&agrave;nh tựu vị tất ngay tại bọn họ dưới, c&oacute; một c&acirc;u, ta từng đối Ch&iacute; T&agrave;i n&oacute;i qua, h&ocirc;m nay, ta đem những lời n&agrave;y n&oacute;i sau c&ugrave;ng ngươi nghe.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ t&ograve; m&ograve;, n&oacute;i: &quot;Thỉnh giảng.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia thản nhi&ecirc;n n&oacute;i: &quot;Thế gian h&agrave;o m&ocirc;n giai c&aacute; m&egrave; một lứa, tội &aacute;c chồng chất tội lỗi chồng chất, n&agrave;y &aacute;c kh&ocirc;ng trừ, thi&ecirc;n hạ v&ocirc; th&aacute;i b&igrave;nh!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ Thứ vẻ mặt chấn động, trầm trọng địa gật gật đầu, bất qu&aacute;, hắn lại nghi vấn đạo: &quot;Như ch&acirc;n gia như vậy gia đại nghiệp đại nhưng kh&ocirc;ng l&agrave;m &aacute;c đ&iacute;ch thế gia, lại như thế n&agrave;o?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia cười n&oacute;i: &quot;Chẳng lẽ ngươi đ&atilde; qu&ecirc;n, ta n&oacute;i rồi th&acirc;n phận c&ugrave;ng địa vị cũng kh&ocirc;ng thể quyết định n&agrave;y l&agrave;m việc t&aacute;c phong, cứu n&agrave;y căn nguy&ecirc;n ở nh&acirc;n t&iacute;nh. D&acirc;n ch&uacute;ng l&agrave;m &aacute;c, đều c&oacute; quan lại trừng trị, hiện giờ thế gia chi &aacute;c, lại kh&ocirc;ng người ước th&uacute;c, gian thần chi &aacute;c, cũng kh&ocirc;ng nh&acirc;n c&oacute; thể trị, đế vương chi &aacute;c, c&agrave;ng kh&ocirc;ng người khả trừ. Nếu c&oacute; một ng&agrave;y, ta c&oacute; năng lực trừ đế vương chi &aacute;c, tr&igrave; gian thần chi &aacute;c, đoạn thế gia chi &aacute;c, ta tất một hướng v&ocirc; địch.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ha ha ha ha, tiểu huynh đệ, nhĩ hảo đại đ&iacute;ch khẩu kh&iacute; a, ta đ&acirc;y hỏi ngươi, đế vương kh&ocirc;ng hiểu l&yacute; lẽ kh&ocirc;ng thay đổi, gian thần họa quốc kh&ocirc;ng ngừng, thế gia ho&agrave;nh h&agrave;nh kh&ocirc;ng ngừng, ngươi đương như thế n&agrave;o?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tửu l&acirc;u lầu hai vốn l&agrave; hai b&aacute;t kh&aacute;ch nh&acirc;n, một sắp đặt lại l&agrave; Qu&aacute;ch Gia c&ugrave;ng Từ Thứ, m&agrave; b&ecirc;n kia cũng ba nam nh&acirc;n, giờ ph&uacute;t n&agrave;y kia ba nam nh&acirc;n hướng Qu&aacute;ch Gia đi tới, cầm đầu người b&aacute;n trăm mấy tuổi, diện mạo th&ocirc; cuồng lại ẩn h&agrave;m chặt chẽ vẻ, tay cầm ch&iacute;n lễ trượng xoải bước m&agrave; đến, s&aacute;ng ngời hữu thần đ&iacute;ch &aacute;nh mắt tập trung ở tại Qu&aacute;ch Gia tr&ecirc;n người.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia thầm nghĩ trong l&ograve;ng: mắc c&acirc;u. Mặt ngo&agrave;i lại bất động thanh sắc, thản nhi&ecirc;n n&oacute;i: &quot;Đế vương kh&ocirc;ng hiểu l&yacute; lẽ kh&ocirc;ng thay đổi, h&agrave;nh th&iacute;ch vua! Gian thần họa quốc kh&ocirc;ng ngừng, trảm thần! Thế gia ho&agrave;nh h&agrave;nh kh&ocirc;ng ngừng, diệt m&ocirc;n!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Người nghe bốn người đều bị sắc mặt đại biến, cho d&ugrave; l&agrave; Từ Thứ cũng thật hấp một ngụm l&atilde;nh kh&iacute;, tim v&agrave; mật c&acirc;u chấn!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; kia đi v&agrave;o Qu&aacute;ch Gia b&ecirc;n người đ&iacute;ch trung ni&ecirc;n nh&acirc;n lại lắc đầu cười nhạo đạo: &quot;Tiểu huynh đệ, khẩu kh&iacute; kh&ocirc;ng nhỏ, ngươi biết h&ocirc;m nay tạ thế gia dữ dội nhiều? Nếu ngươi mỗi người diệt m&ocirc;n, giết ho&agrave;n sao kh&ocirc;ng?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia &aacute;nh mắt nh&igrave;n ph&iacute;a l&acirc;u ngoại ng&atilde; tư đường, n&oacute;i: &quot;Thi&ecirc;n hạ c&oacute; bao nhi&ecirc;u thế gia, ta kh&ocirc;ng biết, thi&ecirc;n hạ c&oacute; bao nhi&ecirc;u d&acirc;n ch&uacute;ng, ta cũng kh&ocirc;ng biết, đối với ngươi biết thi&ecirc;n hạ d&acirc;n ch&uacute;ng chi sổ, ng&agrave;n lần vạn lần hậu thế gia, c&ugrave;ng với l&agrave;m cho thi&ecirc;n hạ thế gia tai họa thi&ecirc;n hạ d&acirc;n ch&uacute;ng, chẳng dẹp y&ecirc;n thế gia, đến l&uacute;c đ&oacute;, t&acirc;n đ&iacute;ch thế gia đem theo d&acirc;n ch&uacute;ng trung trổ hết t&agrave;i năng, khi đ&oacute;, t&acirc;n đ&iacute;ch thế gia sinh tồn quy tắc cũng chắc chắn được xuất bản, người đương quyền nếu c&oacute; thể thấy r&otilde; vết xe đổ, như vậy t&acirc;n đ&iacute;ch thế gia c&aacute;ch sinh tồn đem sẽ kh&ocirc;ng cấp thế gia cơ hội tai họa d&acirc;n ch&uacute;ng.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Tiểu huynh đệ, chưa thỉnh gi&aacute;o cao t&iacute;nh đại danh?&quot; Kia tr&igrave; ch&iacute;n lễ trượng đ&iacute;ch trung ni&ecirc;n nam nh&acirc;n hướng Qu&aacute;ch Gia cung k&iacute;nh hỏi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Qu&aacute;ch Gia nghi&ecirc;m mặt n&oacute;i: &quot;To&aacute;nh Xuy&ecirc;n Qu&aacute;ch Gia.&quot;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:1207px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'thu-bay-chuong-dep-yen-the-gia', 5, '23123', '2022-08-03 03:52:04', '2022-08-03 03:52:04'),
(26, '1', 'Trấn Bắc Công Phủ tiểu trong suốt', '<p>Đại Tề đế quốc, Bắc Địa Th&agrave;nh bắc c&ocirc;ng phủ, hậu trạch nơi n&agrave;o đ&oacute; vắng vẻ tiểu viện.<br />\r\n<br />\r\nTia nắng ban mai hơi lộ ra, kh&ocirc;ng kh&iacute; kh&ocirc; lạnh c&oacute; ch&uacute;t hơi r&eacute;t lạnh, cả hậu trạch c&ocirc;ng phủ đều từ trong y&ecirc;n lặng tỉnh t&aacute;o lại, chỗ n&agrave;y nương tựa tường sau vắng vẻ tiểu viện cũng kh&ocirc;ng ngoại lệ.<br />\r\n<br />\r\n&quot;Tam thiếu gia, đ&atilde; dậy !&quot;<br />\r\n<br />\r\nHai c&aacute;i mười hai mười ba tuổi tiểu nha ho&agrave;n bận rộn kh&ocirc;ng ngừng, một mặt mũi tr&agrave;n đầy tinh minh mặt tr&ograve;n đ&agrave;n b&agrave; trung ni&ecirc;n đi đến đ&oacute;ng chặt ph&ograve;ng ngủ ch&iacute;nh cửa, vỗ nhẹ nhẹ đ&aacute;nh cửa gỗ k&ecirc;u.<br />\r\n<br />\r\n&quot;Biết đến L&yacute; Mụ, ta c&aacute;i n&agrave;y đi l&ecirc;n!&quot;<br />\r\n<br />\r\nTrong ph&ograve;ng truyền đến một đạo thiếu ni&ecirc;n lười biếng tiếng n&oacute;i, ngay sau đ&oacute; một rời giường mặc quần &aacute;o c&ograve;n c&oacute; xuống đất nhỏ vụn &acirc;m thanh truyền ra, chẳng qua một hồi tiếng bước ch&acirc;n vang l&ecirc;n, đ&oacute;ng chặt khắc hoa cửa gỗ mở ra.</p>', '<p>Đại Tề đế quốc, Bắc Địa Th&agrave;nh bắc c&ocirc;ng phủ, hậu trạch nơi n&agrave;o đ&oacute; vắng vẻ tiểu viện.<br />\r\n<br />\r\nTia nắng ban mai hơi lộ ra, kh&ocirc;ng kh&iacute; kh&ocirc; lạnh c&oacute; ch&uacute;t hơi r&eacute;t lạnh, cả hậu trạch c&ocirc;ng phủ đều từ trong y&ecirc;n lặng tỉnh t&aacute;o lại, chỗ n&agrave;y nương tựa tường sau vắng vẻ tiểu viện cũng kh&ocirc;ng ngoại lệ.<br />\r\n<br />\r\n&quot;Tam thiếu gia, đ&atilde; dậy !&quot;<br />\r\n<br />\r\nHai c&aacute;i mười hai mười ba tuổi tiểu nha ho&agrave;n bận rộn kh&ocirc;ng ngừng, một mặt mũi tr&agrave;n đầy tinh minh mặt tr&ograve;n đ&agrave;n b&agrave; trung ni&ecirc;n đi đến đ&oacute;ng chặt ph&ograve;ng ngủ ch&iacute;nh cửa, vỗ nhẹ nhẹ đ&aacute;nh cửa gỗ k&ecirc;u.<br />\r\n<br />\r\n&quot;Biết đến L&yacute; Mụ, ta c&aacute;i n&agrave;y đi l&ecirc;n!&quot;<br />\r\n<br />\r\nTrong ph&ograve;ng truyền đến một đạo thiếu ni&ecirc;n lười biếng tiếng n&oacute;i, ngay sau đ&oacute; một rời giường mặc quần &aacute;o c&ograve;n c&oacute; xuống đất nhỏ vụn &acirc;m thanh truyền ra, chẳng qua một hồi tiếng bước ch&acirc;n vang l&ecirc;n, đ&oacute;ng chặt khắc hoa cửa gỗ mở ra.<br />\r\n<br />\r\nMột th&acirc;n cẩm b&agrave;o hơi cũ kh&ocirc;ng phải mới, năm thước th&acirc;n cao t&oacute;c tai b&ugrave; x&ugrave;, khu&ocirc;n mặt non nớt nh&igrave;n chỉ c&oacute; mười tuổi khoảng tiểu thiếu ni&ecirc;n, đứng ở ph&iacute;a sau cửa tr&ecirc;n mặt cười h&igrave; h&igrave;.<br />\r\n<br />\r\n&quot;Tam thiếu năm mau trở lại ph&ograve;ng, thời tiết kh&ocirc; lạnh cảm lạnh cũng kh&ocirc;ng tốt chịu!&quot;<br />\r\n<br />\r\nL&yacute; Mụ một thanh k&eacute;o qua thiếu ni&ecirc;n tay, tiến v&agrave;o ph&ograve;ng ngủ ấm &aacute;p, khiến thiếu ni&ecirc;n ngồi xuống chừng cao cỡ nửa người trước gương đồng, cười n&oacute;i: &quot;Hoa hồng mau tới đ&acirc;y gi&uacute;p Tam thiếu gia sửa sang lại quan ph&aacute;t, kh&ocirc;ng phải vậy chờ đến phu nh&acirc;n c&aacute;i kia lại phải bị ph&ecirc;!&quot;<br />\r\n<br />\r\n&quot;Đến đến, liễu t&aacute;i trước hầu hạ Tam thiếu năm tẩy thấu, tiểu tỳ c&aacute;i n&agrave;y tới sửa sang lại ph&aacute;t quan!&quot;<br />\r\n<br />\r\nPh&iacute;a trước ở trong s&acirc;n nhỏ bận rộn kh&ocirc;ng nghỉ hai c&aacute;i tiểu nha ho&agrave;n, vội v&agrave;ng bu&ocirc;ng xuống trong tay c&ocirc;ng việc, một bưng bồn một cầm gấm b&agrave;y, vội v&atilde; v&agrave;o nh&agrave; gi&uacute;p đỡ thiếu ni&ecirc;n tẩy thấu, đồng thời xử l&yacute; rối bời t&oacute;c.<br />\r\n<br />\r\n&quot;Tam thiếu gia, ng&agrave;y h&ocirc;m qua phu nh&acirc;n nhận được Đại thiếu gia từ kinh th&agrave;nh đưa tới thư nh&agrave;, t&acirc;m t&igrave;nh tương đối kh&aacute;, h&ocirc;m nay kh&ocirc;ng cần ở phu nh&acirc;n nơi đ&oacute; đợi qu&aacute; l&acirc;u!&quot;<br />\r\n<br />\r\n&quot;Tối h&ocirc;m qua Tứ thiếu gia chơi đ&ugrave;a, lại rớt bể một bộ cung sứ đồ uống tr&agrave;, h&ocirc;m nay nhất định phải cẩn thận một ch&uacute;t, kh&ocirc;ng n&ecirc;n bị Triệu di nương d&ugrave;ng lời chụp v&agrave;o đi!&quot;<br />\r\n<br />\r\n&quot;Đại quản gia con trai trong qu&acirc;n đội lập được c&ocirc;ng cực khổ, gặp kh&ocirc;ng n&ecirc;n qu&ecirc;n ch&uacute;c mừng một tiếng!&quot;<br />\r\n<br />\r\n&quot;...&quot;<br />\r\n<br />\r\nTrần Anh ngồi ngay ngắn ở c&aacute;i ghế nhỏ, đối với những chuyện hư hỏng trong hậu trạch c&ocirc;ng phủ n&agrave;y thật kh&ocirc;ng c&oacute; hứng th&uacute; để &yacute; tới, chẳng qua l&agrave; đ&aacute;ng tiếc th&acirc;n phận của hắn kh&ocirc;ng cho ph&eacute;p.<br />\r\n<br />\r\nL&agrave;m một mẹ đẻ chết sớm con thứ c&ocirc;ng phủ, ở kh&ocirc;ng th&agrave;nh ni&ecirc;n xuất phủ đơn độc lập hộ ph&iacute;a trước, cơ bản kh&ocirc;ng c&oacute; g&igrave; qu&aacute; lớn tự chủ kh&ocirc;ng gian.<br />\r\n<br />\r\nMuốn sống được thoải m&aacute;i, vậy th&igrave; phải dựa theo quy củ của hậu trạch c&ocirc;ng phủ đ&agrave;ng ho&agrave;ng l&agrave;m người, bằng kh&ocirc;ng hậu quả tướng tướng l&agrave;m kh&ocirc;ng xong.<br />\r\n<br />\r\nMay mắn t&acirc;m l&yacute; của hắn thập phần th&agrave;nh thục, trong th&acirc;n thể tuổi nhỏ chứa một linh hồn của người xuy&ecirc;n việt, l&uacute;c n&agrave;y mới kh&ocirc;ng c&oacute; nhận lấy c&ocirc;ng phủ hậu viện tranh đấu li&ecirc;n lụy, b&igrave;nh an sống đến mười tuổi tuổi.<br />\r\n<br />\r\nKh&ocirc;ng phải vậy, c&ocirc;ng phủ vinh dự, th&acirc;n phận l&uacute;ng t&uacute;ng, hạ nh&acirc;n nịnh nọt, c&ugrave;ng ung dung thản nhi&ecirc;n lại ngoan lệ cực kỳ &aacute;p chế, tuyệt đối c&oacute; thể gọi một c&aacute;i kh&ocirc;ng c&oacute; mẹ đẻ che chở con thứ, xuất hiện nghi&ecirc;m trọng vấn đề t&acirc;m l&yacute;.<br />\r\n<br />\r\nHoặc l&agrave; l&ograve;ng dạ mất sạch cả ng&agrave;y kh&uacute;m n&uacute;m, hoặc l&agrave; t&acirc;m l&yacute; b&oacute;p m&eacute;o đầy c&otilde;i l&ograve;ng o&aacute;n hận.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><br />\r\nMay mắn...<br />\r\n<br />\r\n&quot;Tam thiếu gia, kh&ocirc;ng sai biệt lắm sau đ&oacute; đến l&uacute;c, vẫn l&agrave; sớm một ch&uacute;t đi trước chủ viện thỉnh an tốt!&quot;<br />\r\n<br />\r\nMắt nh&igrave;n b&ecirc;n ngo&agrave;i sắc trời, L&yacute; Mụ mượt m&agrave; tr&ecirc;n mặt lộ ra nh&egrave; nhẹ lo lắng, mở miệng nhắc nhở.<br />\r\n<br />\r\n&quot;Tốt a, ta c&aacute;i n&agrave;y động th&acirc;n!&quot;<br />\r\n<br />\r\nTrần Anh quan s&aacute;t tỉ mỉ gương đồng một c&aacute;i, nguy&ecirc;n bản t&oacute;c tai b&ugrave; x&ugrave; mặt mũi tr&agrave;n đầy lười biếng thiếu ni&ecirc;n, đ&atilde; biến th&agrave;nh đầu thắt tiểu quan đẹp trai.<br />\r\n<br />\r\nH&igrave; h&igrave; cười một tiếng, theo trong viện chỉ c&oacute; hai c&aacute;i tiểu nha ho&agrave;n ch&agrave;o hỏi &acirc;m thanh, liền dẫn L&yacute; Mụ hướng chủ viện đi.<br />\r\n<br />\r\nTrấn Bắc C&ocirc;ng l&agrave;m Đại Tề đế quốc tiếng tăm lừng lẫy qu&acirc;n đội đại l&atilde;o, b&agrave;n tay ba mươi vạn h&ugrave;ng binh v&ugrave;ng bi&ecirc;n cương cường lực chư hầu, phủ đệ tự nhi&ecirc;n mười phần rộng lớn, ốc x&aacute; li&ecirc;n mi&ecirc;n kh&iacute; thế h&ugrave;ng hồn, liền v&agrave; một t&ograve;a th&agrave;nh nhỏ kh&ocirc;ng sai biệt lắm.<br />\r\n<br />\r\nTrần Anh ở tiểu viện đến gần b&ecirc;n tường, c&ugrave;ng hậu trạch chủ viện tự nhi&ecirc;n c&aacute;ch xa nhau rất xa, lấy cước tr&igrave;nh của hắn, thẳng đường đi tới &iacute;t nhất được hao tốn nửa canh giờ.<br />\r\n<br />\r\nBảy tuổi ph&iacute;a trước cũng c&oacute; xe ngựa ngồi, chẳng qua l&agrave; bảy tuổi về sau liền kh&ocirc;ng c&oacute; chuyện tốt bực n&agrave;y.<br />\r\n<br />\r\nKh&ocirc;ng phải c&ocirc;ng phủ kh&ocirc;ng bỏ được, m&agrave; l&agrave; th&acirc;n l&agrave; v&ugrave;ng bi&ecirc;n cương trấn thủ chư hầu Trấn Bắc C&ocirc;ng, l&agrave;m ra hiếm thấy quyết định.<br />\r\n<br />\r\nN&oacute;i c&aacute;i g&igrave; con của hắn đập, sau đ&oacute; cũng phải cần ra chiến trường, từ nhỏ đ&atilde; được r&egrave;n luyện đi l&ecirc;n, miễn cho trưởng th&agrave;nh sau đ&oacute; l&agrave;m mất mặt hắn v&acirc;n v&acirc;n.<br />\r\n<br />\r\nCuối c&ugrave;ng,<br />\r\n<br />\r\nChịu tội cũng chỉ c&oacute; Trần Anh c&aacute;i n&agrave;y kh&ocirc;ng c&oacute; mẹ che chở con thứ.<br />\r\n<br />\r\nMột năm ba trăm s&aacute;u mươi ng&agrave;y, mặc kệ gió th&ocirc;̉i trời mưa vẫn l&agrave; tuyết lớn đầy trời, lại hoặc l&agrave; thời tiết n&oacute;ng bức người s&oacute;ng nhiệt cuồn cuộn, hắn đều phải tr&ocirc;ng quy củ đ&agrave;ng ho&agrave;ng đi đến chủ viện thỉnh an.<br />\r\n<br />\r\nL&agrave;m Trần Anh v&uacute; em, lại l&agrave; vong mẫu lưu lại duy nhất t&acirc;m ph&uacute;c, L&yacute; Mụ trong &acirc;m thầm tất nhi&ecirc;n l&agrave; kh&ocirc;ng &iacute;t thay Trần Anh minh bất b&igrave;nh, đ&aacute;ng tiếc kh&ocirc;ng c&oacute; g&igrave; chỗ d&ugrave;ng.<br />\r\n<br />\r\nN&agrave;ng c&oacute; thể l&agrave;m được, cũng chỉ c&oacute; thể gi&oacute; mặc gi&oacute;, mưa mặc mưa v&agrave; Trần Anh c&ugrave;ng nhau &#39;Chịu tội&#39;, đồng thời cũng gi&uacute;p đỡ giải quyết một ch&uacute;t hạ nh&acirc;n phương diện l&agrave;m phiền.<br />\r\n<br />\r\nTrần Anh cũng kh&ocirc;ng cảm thấy mệt mỏi, th&acirc;n thể thẳng tắp bước ch&acirc;n mười phần ổn định, h&iacute;t thở gi&agrave;u c&oacute; tiết tấu lồng ngực bụng chập tr&ugrave;ng rất c&oacute; quy luật, rất được c&ocirc;ng phủ cơ sở phương ph&aacute;p r&egrave;n luyện tinh t&uacute;y.<br />\r\n<br />\r\nN&oacute;i như thế n&agrave;o đều l&agrave; đứng đầu tướng m&ocirc;n thế gia, c&oacute; cực kỳ ổn thỏa cơ sở phương ph&aacute;p r&egrave;n luyện mười phần b&igrave;nh thường.<br />\r\n<br />\r\nHắn dứt kho&aacute;t đem đi đến chủ viện lộ tr&igrave;nh, xem như một loại r&egrave;n luyện.<br />\r\n<br />\r\nBa năm gi&oacute; mặc gi&oacute;, mưa mặc mưa huấn luyện, bộ n&agrave;y c&oacute; thể hoạt động quanh th&acirc;n hơn ph&acirc;n nửa g&acirc;n cốt cơ sở phương ph&aacute;p thổ nạp, đ&atilde; tạo th&agrave;nh bản năng của th&acirc;n thể.<br />\r\n<br />\r\nNửa canh giờ đi đến chủ cửa s&acirc;n vừa vặn, th&acirc;n thể kh&iacute; huyết g&acirc;n cốt chậm chạp hoạt động mở, c&aacute;i tr&aacute;n hơi tiết ra một tầng mỏng mồ h&ocirc;i.<br />\r\n<br />\r\n&quot;Linh Tước tỷ tỷ, mẫu th&acirc;n đi l&ecirc;n sao?&quot;<br />\r\n<br />\r\nNhận lấy L&yacute; Mụ đưa tới khăn lụa lau một c&aacute;i, hướng về ph&iacute;a đang ở cửa chủ viện đại nha ho&agrave;n cười hỏi.<br />\r\n<br />\r\n&quot;Ah xong, Tam thiếu gia tới a, tiểu tỳ cũng n&ecirc;n đi Hướng phu nh&acirc;n hồi bẩm!&quot;<br />\r\n<br />\r\nLinh Tước th&aacute;i độ kh&ocirc;ng mặn kh&ocirc;ng nhạt, lộ ra một kh&ocirc;ng n&oacute;i ra được l&agrave; lạnh vẫn l&agrave; n&oacute;ng l&ecirc;n ti&ecirc;u chuẩn nụ cười, xoay người quay trở về chủ trong nội viện đường.<br />\r\n<br />\r\nKh&ocirc;ng c&oacute; bị k&ecirc;u tiến v&agrave;o ph&ograve;ng b&ecirc;n cạnh ngồi xuống chờ, c&agrave;ng kh&ocirc;ng c&oacute; nước tr&agrave; điểm t&acirc;m d&acirc;ng l&ecirc;n, Trần Anh sớm đ&atilde; th&agrave;nh th&oacute;i quen, cũng l&agrave; sẽ kh&ocirc;ng c&ugrave;ng một chủ viện đại nha ho&agrave;n so đo, t&acirc;m t&iacute;nh vững v&agrave;ng cực k&igrave;.<br />\r\n<br />\r\nCũng ph&iacute;a sau L&yacute; Mụ trải qua l&acirc;u như vậy vẫn như cũ kh&ocirc;ng được xem mở, quay đầu lại vừa hay nh&igrave;n thấy trong mắt L&yacute; Mụ x&oacute;a nổi giận, khẽ lắc đầu ra hiệu n&agrave;ng kh&ocirc;ng n&ecirc;n lộ dấu vết.<br />\r\n<br />\r\nKh&ocirc;ng l&acirc;u, liền c&oacute; nha ho&agrave;n đem Trần Anh đưa v&agrave;o chủ viện hậu đường đại sảnh, Trấn Bắc C&ocirc;ng phu nh&acirc;n cũng l&agrave; phải trong miệng hắn &#39;Mẫu th&acirc;n&#39;, đ&atilde; mặc đầy đủ hết ngồi ngay ngắn ở chủ vị.<br />\r\n<br />\r\n&quot;H&agrave;i nhi hướng mẫu th&acirc;n thỉnh an!&quot;<br />\r\n<br />\r\nĐứng ở trung t&acirc;m sảnh ch&iacute;nh, Trần Anh hướng về ph&iacute;a mẹ cả đi c&aacute;i lẽ thỉnh an ti&ecirc;u chuẩn, cẩn thận tỉ mỉ gọi người t&igrave;m kh&ocirc;ng ra ch&uacute;t n&agrave;o mao bệnh.<br />\r\n<br />\r\nCứ việc chẳng qua l&agrave; c&ocirc;ng phủ một con thứ kh&ocirc;ng được coi trọng, c&oacute; thể n&ecirc;n học được thế gia lễ nghi v&agrave; một ch&uacute;t quy củ lại l&agrave; kh&ocirc;ng c&oacute; rơi xuống.<br />\r\n<br />\r\n&quot;Anh nhi tới a, ngồi xuống đợi l&aacute;t nữa c&ugrave;ng nhau d&ugrave;ng bữa!&quot;<br />\r\n<br />\r\nC&ocirc;ng tước phu nh&acirc;n vẻ mặt nh&agrave;n nhạt giọng n&oacute;i b&igrave;nh tĩnh, kho&aacute;t tay &aacute;o k&ecirc;u Trần Anh ngồi xuống một b&ecirc;n, kh&ocirc;ng c&oacute; hiển lộ ch&uacute;t n&agrave;o th&acirc;n mật dấu hiệu.<br />\r\n<br />\r\nTrần Anh đổ kh&ocirc;ng c&oacute; cảm thấy chịu lạnh nhạt, hắn một kh&ocirc;ng c&oacute; mẹ ruột chiếu cố con thứ, mẹ cả c&oacute; thể kh&ocirc;ng biểu hiện ra ch&aacute;n gh&eacute;t v&agrave; kh&ocirc;ng ki&ecirc;n nhẫn được nữa, đ&atilde; tương đương nể t&igrave;nh.<br />\r\n<br />\r\nDựa theo dĩ v&atilde;ng quen thuộc, t&igrave;m c&aacute;i kh&ocirc;ng đ&aacute;ng ch&uacute; &yacute; nơi hẻo l&aacute;nh ngồi xuống, mắt nh&igrave;n mũi mũi nh&igrave;n t&acirc;m l&agrave;m tiểu trong suốt.<br />\r\n<br />\r\nRất nhanh, đại sảnh nơi n&agrave;y n&aacute;o nhiệt l&ecirc;n.<br />\r\n<br />\r\nTrấn Bắc C&ocirc;ng c&oacute; ba vị tiểu thiếp, c&aacute;c n&agrave;ng h&igrave;nh như đ&atilde; hẹn, mang theo th&acirc;n sinh cốt nhục nhất nhất tới trước hướng về ph&iacute;a chủ mẫu thỉnh an.<br />\r\n<br />\r\nTrong đ&oacute; Triệu di nương một th&acirc;n hỏa hồng gấm v&oacute;c hoa dung nguyệt mạo, kh&iacute; chất kh&ocirc;ng tầm thường b&ecirc;n người mang theo c&ocirc;ng phủ thứ hạng thứ tư con thứ Trần H&ugrave;ng, so với Trần Anh nhỏ hơn một tuổi l&agrave; một tr&ograve;n vo tiểu mập mạp.<br />\r\n<br />\r\nNh&igrave;n c&aacute;i kia hiện ra b&oacute;ng lo&aacute;ng mặt b&eacute;o nhỏ, c&ograve;n c&oacute; ki&ecirc;u ho&agrave;nh tr&ecirc;n mặt mũi, hiển nhi&ecirc;n thời gian tr&ocirc;i qua tương đương tưới nhuần, t&iacute;nh t&igrave;nh cũng bị nu&ocirc;ng chiều được c&oacute; ch&uacute;t ngang ngược.<br />\r\n<br />\r\nPh&iacute;a sau Tiền di nương v&agrave; T&ocirc;n di nương đồng dạng phong th&aacute;i soi người, b&ecirc;n người theo c&ocirc;ng phủ x&ecirc;́p hạng thứ năm thứ nữ trần nh&atilde; c&ugrave;ng thứ hạng thứ s&aacute;u thứ nữ trần chi, tuổi đều ở bảy tuổi, tất cả đều l&agrave; dung mạo động l&ograve;ng người tiểu mỹ nữ.<br />\r\n<br />\r\nTrần Anh nhất nhất tiến l&ecirc;n lễ ra mắt, mẹ con Triệu di nương v&agrave; Trần H&ugrave;ng căn bản l&agrave; kh&ocirc;ng c&oacute; phản ứng, Tiền di nương v&agrave; T&ocirc;n di nương cũng khẽ gật đầu, dưới gối hai c&aacute;i tiểu nha đầu vội v&agrave;ng gi&ograve;n tiếng h&ocirc; &#39;Tam ca&#39;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\nVề sau, hắn lại tiếp tục uốn tại nơi hẻo l&aacute;nh kh&ocirc;ng phải tiếng hừ, hậu đường trong ch&iacute;nh sảnh n&aacute;o nhiệt c&ugrave;ng hắn kh&ocirc;ng hề c&oacute; một ch&uacute;t quan hệ.<br />\r\n<br />\r\nĐừng xem ba vị di nương đều l&agrave; th&acirc;n phận tiểu thiếp, c&oacute; thể sau lưng c&aacute;c n&agrave;ng gia thế cũng kh&ocirc;ng t&iacute;nh l&agrave; k&eacute;m, c&ocirc;ng phủ chủ mẫu đang kh&ocirc;ng c&oacute; xung đột lợi &iacute;ch dưới t&igrave;nh huống, cũng sẽ kh&ocirc;ng l&agrave;m qu&aacute; mức, cho mấy c&aacute;i khu&ocirc;n mặt tươi cười n&oacute;i chuyện phiếm một cũng coi như đuổi nh&agrave;m ch&aacute;n.<br />\r\n<br />\r\nChờ đến d&ugrave;ng đồ ăn s&aacute;ng, Trần Anh căn bản l&agrave; kh&ocirc;ng c&oacute; hướng ph&iacute;a trước tiếp cận, m&agrave; l&agrave; y&ecirc;n lặng k&iacute;nh bồi vị tr&iacute; thấp nhất, mọi cử động l&agrave; ti&ecirc;u chuẩn con em thế gia d&ugrave;ng bữa h&igrave;nh thức, lặng y&ecirc;n kh&ocirc;ng tiếng động xử l&yacute; hai b&aacute;t lớn cơm, lại đem trước mắt mấy b&agrave;n thức ăn mỹ vị qu&eacute;t tới một nửa, l&uacute;c n&agrave;y mới ăn t&aacute;m phần b&atilde;o m&atilde;n đủ để đũa xuống.<br />\r\n<br />\r\n&quot;Nha, nh&igrave;n Anh nhi cơm n&agrave;y đo, một hơi ăn hai b&aacute;t lớn!&quot;<br />\r\n<br />\r\nVốn cho rằng lại l&agrave; một b&igrave;nh thường s&aacute;ng sớm, kh&ocirc;ng ngờ Triệu di nương đột nhi&ecirc;n mở miệng bắt hắn l&agrave;m b&egrave;, cười duy&ecirc;n n&oacute;i: &quot;C&oacute; phải hay kh&ocirc;ng b&igrave;nh thường ở m&igrave;nh s&acirc;n nhỏ ăn đến kh&ocirc;ng tốt!&quot;<br />\r\n<br />\r\nN&oacute;i, kh&ocirc;ng để &yacute; người b&ecirc;n ngo&agrave;i vẻ mặt, nụ cười th&acirc;n mật đ&acirc;u c&ograve;n cũng c&oacute; trước l&atilde;nh đạm, &#39;Hảo t&acirc;m&#39; đề nghị: &quot;Ch&iacute;nh hảo di nương trong viện c&oacute; thể thi&ecirc;n vị, kh&ocirc;ng cần liền cho Anh nhi l&agrave;m nhiều một phần?&quot;<br />\r\n<br />\r\n&quot;Di nương hảo &yacute; t&acirc;m lĩnh!&quot;<br />\r\n<br />\r\nTrần Anh đứng dậy, khu&ocirc;n mặt chất ph&aacute;c lắc đầu n&oacute;i: &quot;Đầu bếp ph&ograve;ng đồ ăn liền rất mỹ vị, ta ăn đến rất vui vẻ!&quot;<br />\r\n<br />\r\nTriệu di nương sắc mặt lạnh l&ugrave;ng, c&ograve;n kh&ocirc;ng đợi n&agrave;ng mở miệng, c&ocirc;ng phủ chủ mẫu liền kh&ocirc;ng nhanh kh&ocirc;ng chậm n&oacute;i: &quot;Muội muội đa t&acirc;m, Anh nhi theo trong phủ hộ viện luyện v&otilde; đặt nền m&oacute;ng rất c&oacute; tiểu th&agrave;nh, ng&agrave;y thường ti&ecirc;u hao lớn hơn một ch&uacute;t ăn được nhiều một điểm rất b&igrave;nh thường!&quot;<br />\r\n<br />\r\nMột trận nhỏ tiểu phong ba như vậy trừ khử, d&ugrave;ng qua sau bữa ăn điểm t&acirc;m, thời gian về khoảng c&aacute;ch kh&oacute;a kh&ocirc;ng xa, Trần Anh đ&acirc;u ra đấy hướng về ph&iacute;a mẹ cả đưa ra c&aacute;o từ.<br />\r\n<br />\r\n&quot;Đi th&ocirc;i đi th&ocirc;i, v&agrave; h&ugrave;ng m&agrave; c&ugrave;ng nhau đi trước đi học!&quot;<br />\r\n<br />\r\nC&ocirc;ng phủ chủ mẫu kho&aacute;t tay &aacute;o, trực tiếp đem nghe được đi học liền n&aacute;o loạn tiểu t&iacute;nh t&igrave;nh Trần H&ugrave;ng tiểu mập mạp cũng cho đuổi.<br />\r\n<br />\r\nTrần Anh một mặt chất ph&aacute;c, mang theo mặt mũi tr&agrave;n đầy kh&ocirc;ng t&igrave;nh nguyện tiểu mập mạp đi đến đi học địa phương.<br />\r\n<br />\r\nTrấn Bắc C&ocirc;ng c&oacute; hai đ&iacute;ch hai thứ bốn c&aacute;i con trai, vừa kh&ocirc;ng c&oacute; huyết mạch th&acirc;n cận t&ocirc;ng tộc gi&uacute;p đỡ, cho n&ecirc;n đối với bốn c&aacute;i con trai gi&aacute;o dục thấy rất nặng, lớn như vậy một c&ocirc;ng phủ cũng kh&ocirc;ng phải &iacute;t hai c&aacute;i con thứ gi&aacute;o dục t&agrave;i nguy&ecirc;n.<br />\r\n<br />\r\nĐi học địa phương ở nhị m&ocirc;n phụ cận một chỗ lệch sảnh, l&atilde;o sư thế nhưng l&agrave; Bắc Địa một v&ugrave;ng tương đương nổi danh Nho gia m&ocirc;n sinh, ph&iacute;a trước mới dạy qua muốn đi gấp văn đường, đ&atilde; trưởng th&agrave;nh c&ocirc;ng phủ trưởng tử trần văn.<br />\r\n<br />\r\nXem s&aacute;ch tr&ecirc;n b&agrave;n trưng b&agrave;y được chỉnh chỉnh tề tề, lũy nổi l&ecirc;n chừng một đống kinh, sử, tử, tập, Trần Anh buồn bực sau khi chỉ muốn h&ocirc; lớn l&ecirc;n tiếng: Khổng Th&aacute;nh người ngưu bức!<br />\r\n<br />\r\nCũng kh&ocirc;ng phải hắn ch&aacute;n gh&eacute;t những n&agrave;y Nho gia học thuật kinh điển, m&agrave; l&agrave; thật sớm liền c&otilde;ng to&agrave;n, ch&iacute;nh l&agrave; c&aacute;c loại mọi người ch&uacute; th&iacute;ch đều một mực khắc s&acirc;u v&agrave;o n&atilde;o hải.<br />\r\n<br />\r\nN&oacute;i c&acirc;u kh&ocirc;ng kh&aacute;ch kh&iacute;, ch&iacute;nh l&agrave; dạy học ti&ecirc;n sinh c&oacute; hay kh&ocirc;ng hắn biết nhi&ecirc;̀u, vẫn l&agrave; c&aacute;i dấu hỏi.<br />\r\n<br />\r\nC&oacute; thể hắn kh&ocirc;ng n&oacute;i g&igrave;, giả trang ra m&ocirc;̣t b&ocirc;̣ ch&aacute;n gh&eacute;t học được bộ d&aacute;ng, ở ph&iacute;a trước sinh ra dạy học thời điểm suy nghĩ viển v&ocirc;ng, cẩn thận suy nghĩ trong đầu b&agrave;n tay v&agrave;ng đi.<br />\r\n<br />\r\nC&ograve;n tốt c&oacute; c&aacute;i c&agrave;ng kh&ocirc;ng chịu nổi Trần H&ugrave;ng đệm lưng, n&agrave;y mới khiến Trần Anh c&oacute; thể an tĩnh trang học được cặn b&atilde;, cho người ngo&agrave;i một loại kh&ocirc;ng c&oacute; học văn thi&ecirc;n ph&uacute; mỹ hảo ấn tượng.<br />\r\n<br />\r\nTruyện main b&aacute;, hậu cung, c&oacute; ch&uacute;t yy, diễn tả pk r&otilde; r&agrave;ng, văn phong kh&aacute; ổn, đ&atilde; ho&agrave;n th&agrave;nh</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:2639px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'tran-bac-cong-phu-tieu-trong-suot', 6, '565', '2022-08-03 03:55:12', '2022-08-03 03:55:12');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(27, '2', 'Trong khe hẹp cầu sinh tồn', '<p>&mdash; QUẢNG C&Aacute;O &mdash;</p>\r\n\r\n<p>Thường thường kh&ocirc;ng c&oacute; g&igrave; lạ mới l&agrave; đối với m&igrave;nh bảo vệ tốt nhất!<br />\r\n<br />\r\nTừ khi Trần Anh xuy&ecirc;n qua trọng sinh, c&ograve;n ở t&atilde; l&oacute;t l&uacute;c kiến thức qua c&ocirc;ng phủ nội trạch m&aacute;u tanh tranh đấu, liền quyết định s&aacute;ch lược tự vệ n&agrave;y.<br />\r\n<br />\r\nĐừng xem c&ocirc;ng phủ h&agrave;i tử kh&ocirc;ng &iacute;t, chừng hai đ&iacute;ch hai thứ bốn c&aacute;i nam h&agrave;i, c&ograve;n c&oacute; hai c&aacute;i thứ nữ liền cho rằng rất nhiều, hắn trọng sinh đến đ&acirc;y thời điểm tr&ecirc;n đầu nhưng c&ograve;n c&oacute; hai c&aacute;i con thứ huynh trưởng.<br />\r\n<br />\r\nKết quả, hắn c&ograve;n chưa tới hai tuổi, hai vị thứ huynh liền lần lượt bởi v&igrave; qua đời, liền th&acirc;n mẫu kh&ocirc;ng l&acirc;u cũng đi theo, trong đ&oacute; m&aacute;u tanh t&agrave;n khốc quả thực k&ecirc;u t&acirc;m hắn r&eacute;t lạnh.<br />\r\n<br />\r\nTrong l&uacute;c rảnh rỗi cẩn thận suy nghĩ, kết hợp một ch&uacute;t như c&oacute; như kh&ocirc;ng truyền ng&ocirc;n, cơ bản c&oacute; thể x&aacute;c định l&agrave; mẹ cả hạ thủ.<br />\r\n<br />\r\nHai vị thứ huynh ni&ecirc;n kỷ c&aacute;ch con trai trưởng qu&aacute; gần, bọn họ th&acirc;n mẫu lại c&oacute; ch&uacute;t được sủng &aacute;i, bị mẹ cả xem như uy hiếp giải quyết, chỉ đơn giản như vậy.<br />\r\n<br />\r\nSau đ&oacute; c&ocirc;ng phủ gi&oacute; &ecirc;m s&oacute;ng lặng kh&ocirc;ng ch&uacute;t rung động...<br />\r\n<br />\r\nTrần Anh ngược lại sẽ kh&ocirc;ng c&oacute; nguy hiểm như vậy, tuổi c&ugrave;ng con vợ cả huynh trưởng k&eacute;o đến rất lớn, vừa kh&ocirc;ng c&oacute; th&acirc;n mẫu ở sau lưng thu xếp, cũng sẽ kh&ocirc;ng đạt được c&ocirc;ng tước cha ruột ch&uacute;t n&agrave;o coi trọng.</p>', '<p>Thường thường kh&ocirc;ng c&oacute; g&igrave; lạ mới l&agrave; đối với m&igrave;nh bảo vệ tốt nhất!<br />\r\n<br />\r\nTừ khi Trần Anh xuy&ecirc;n qua trọng sinh, c&ograve;n ở t&atilde; l&oacute;t l&uacute;c kiến thức qua c&ocirc;ng phủ nội trạch m&aacute;u tanh tranh đấu, liền quyết định s&aacute;ch lược tự vệ n&agrave;y.<br />\r\n<br />\r\nĐừng xem c&ocirc;ng phủ h&agrave;i tử kh&ocirc;ng &iacute;t, chừng hai đ&iacute;ch hai thứ bốn c&aacute;i nam h&agrave;i, c&ograve;n c&oacute; hai c&aacute;i thứ nữ liền cho rằng rất nhiều, hắn trọng sinh đến đ&acirc;y thời điểm tr&ecirc;n đầu nhưng c&ograve;n c&oacute; hai c&aacute;i con thứ huynh trưởng.<br />\r\n<br />\r\nKết quả, hắn c&ograve;n chưa tới hai tuổi, hai vị thứ huynh liền lần lượt bởi v&igrave; qua đời, liền th&acirc;n mẫu kh&ocirc;ng l&acirc;u cũng đi theo, trong đ&oacute; m&aacute;u tanh t&agrave;n khốc quả thực k&ecirc;u t&acirc;m hắn r&eacute;t lạnh.<br />\r\n<br />\r\nTrong l&uacute;c rảnh rỗi cẩn thận suy nghĩ, kết hợp một ch&uacute;t như c&oacute; như kh&ocirc;ng truyền ng&ocirc;n, cơ bản c&oacute; thể x&aacute;c định l&agrave; mẹ cả hạ thủ.<br />\r\n<br />\r\nHai vị thứ huynh ni&ecirc;n kỷ c&aacute;ch con trai trưởng qu&aacute; gần, bọn họ th&acirc;n mẫu lại c&oacute; ch&uacute;t được sủng &aacute;i, bị mẹ cả xem như uy hiếp giải quyết, chỉ đơn giản như vậy.<br />\r\n<br />\r\nSau đ&oacute; c&ocirc;ng phủ gi&oacute; &ecirc;m s&oacute;ng lặng kh&ocirc;ng ch&uacute;t rung động...<br />\r\n<br />\r\nTrần Anh ngược lại sẽ kh&ocirc;ng c&oacute; nguy hiểm như vậy, tuổi c&ugrave;ng con vợ cả huynh trưởng k&eacute;o đến rất lớn, vừa kh&ocirc;ng c&oacute; th&acirc;n mẫu ở sau lưng thu xếp, cũng sẽ kh&ocirc;ng đạt được c&ocirc;ng tước cha ruột ch&uacute;t n&agrave;o coi trọng.<br />\r\n<br />\r\nCứ t&igrave;nh h&igrave;nh như vậy, ai cũng sẽ kh&ocirc;ng cho l&agrave; hắn đối với hai vị con vợ cả huynh trưởng c&oacute; uy hiếp.<br />\r\n<br />\r\nMuốn n&oacute;i vừa rồi xuy&ecirc;n qua trọng sinh l&uacute;c đ&oacute;, kh&ocirc;ng c&oacute; điểm l&agrave;m một phen đại sự h&ugrave;ng t&acirc;m tr&aacute;ng ch&iacute; kh&ocirc;ng thể n&agrave;o. Chẳng qua l&agrave; c&ocirc;ng phủ hậu trạch c&aacute;ch cục, c&ograve;n c&oacute; c&ocirc;ng tước cha ruột ngẫu nhi&ecirc;n xem ra l&atilde;nh đạm &aacute;nh mắt, n&agrave;o c&ograve;n d&aacute;m nổi l&ecirc;n c&aacute;i g&igrave; kế vặt?<br />\r\n<br />\r\nNh&igrave;n đến mức qu&aacute; nhiều, hiểu t&igrave;nh h&igrave;nh c&agrave;ng nhiều, một c&aacute;ch tự nhi&ecirc;n liền r&otilde; r&agrave;ng m&igrave;nh sinh tồn chi đạo, thường thường kh&ocirc;ng c&oacute; g&igrave; lạ vượt qua c&ocirc;ng phủ tuổi thơ v&agrave; thời kỳ thiếu ni&ecirc;n, mới l&agrave; tốt nhất lựa chọn.<br />\r\n<br />\r\nĐương nhi&ecirc;n, cũng kh&ocirc;ng biết l&agrave; xuy&ecirc;n qua nguy&ecirc;n cớ, vẫn l&agrave; b&agrave;n tay v&agrave;ng nguy&ecirc;n nh&acirc;n, đ&atilde; gặp qua l&agrave; kh&ocirc;ng qu&ecirc;n được v&agrave; qua tai kh&ocirc;ng qu&ecirc;n đều l&agrave; cơ bản kỹ năng, thường thường kh&ocirc;ng c&oacute; g&igrave; lạ chẳng qua l&agrave; biểu tượng.<br />\r\n<br />\r\nChỉ cần kh&ocirc;ng cho hậu trạch c&ocirc;ng phủ một đ&aacute;m nh&acirc;n vật thực quyền cảm nhận được uy hiếp, thời gian tr&ocirc;i qua vẫn l&agrave; tương đối thật tốt.<br />\r\n<br />\r\nN&oacute;i như thế n&agrave;o cũng l&agrave; c&ocirc;ng tước con thứ, Trấn Bắc C&ocirc;ng Phủ c&oacute; thể xưng b&agrave;ng bạc b&igrave;nh thường t&agrave;i nguy&ecirc;n, ho&agrave;n to&agrave;n đối hắn mở ra, c&oacute; thể để cho hắn ở cuộc sống b&igrave;nh thản b&ecirc;n trong, chậm r&atilde;i học được lượng lớn kiến thức v&agrave; c&aacute;c loại bản lĩnh.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><br />\r\nMột nửa canh giờ lớp học tr&ocirc;i qua rất nhanh, Trần H&ugrave;ng tiểu b&agrave;n tử n&agrave;y gắn hoan chạy mất, Trần Anh th&igrave; chất ph&aacute;c Hướng ti&ecirc;n sinh đi b&aacute;i biệt lễ, chờ lắc đầu li&ecirc;n tục ti&ecirc;n sinh rời khỏi, l&uacute;c n&agrave;y mới chậm r&atilde;i hướng về ph&iacute;a nh&agrave; m&igrave;nh tiểu viện đi.<br />\r\n<br />\r\nNếm qua ph&acirc;n lượng mười phần lại kh&ocirc;ng t&iacute;nh l&agrave; tinh sảo ăn trưa, khoan thai trong s&acirc;n độ bước ti&ecirc;u thực, tiểu nha ho&agrave;n hoa hồng tay n&acirc;ng một quyển tạp thư, đứng ở dưới m&aacute;i hi&ecirc;n xong tiếng l&atilde;ng đọc, &acirc;m thanh thanh th&uacute;y &ecirc;m tai cực kỳ.<br />\r\n<br />\r\nTrần Anh một bộ hững hờ bộ d&aacute;ng, thực tế hoa hồng chỗ đọc tạp thư nội dung, đ&atilde; xong to&agrave;n bộ ghi tạc trong l&ograve;ng kh&ocirc;ng k&eacute;m ch&uacute;t n&agrave;o.<br />\r\n<br />\r\nKh&ocirc;ng thể biểu hiện qu&aacute; mức, hắn cũng chỉ c&oacute; thể lấy như vậy phương thức hấp thụ kiến thức.<br />\r\n<br />\r\nNgười b&ecirc;n ngo&agrave;i cho rằng đ&acirc;y l&agrave; chơi đ&ugrave;a tiến h&agrave;nh, hơn nữa c&ograve;n l&agrave; kh&ocirc;ng v&agrave;o ch&iacute;nh đường tạp thư, tất nhi&ecirc;n l&agrave; xem như đ&agrave;m tiếu lơ đễnh, ngay cả nhũ mẫu L&yacute; Mụ v&agrave; hai c&aacute;i tiểu nha ho&agrave;n cũng như thế, thật l&agrave; một mỹ lệ hiểu lầm.<br />\r\n<br />\r\nAi cũng chưa từng biết được, từ ba tuổi vỡ l&ograve;ng sau tận lực g&acirc;y n&ecirc;n, tạo th&agrave;nh loại n&agrave;y &#39;Kh&ocirc;ng l&agrave;m việc đ&agrave;ng ho&agrave;ng&#39; thói quen, Trần Anh đ&atilde; đem Trấn Bắc C&ocirc;ng Phủ kh&ocirc;ng sai biệt lắm một phần mười t&agrave;ng thư, tất cả đều nhớ cho kỹ.<br />\r\n<br />\r\nTrong đ&oacute; ch&iacute;nh thống Nho gia điển tịch chỉ chiếm rất số &iacute;t điểm, c&ograve;n lại đều l&agrave; y n&ocirc;ng t&iacute;nh to&aacute;n bốc, thi&ecirc;n văn địa l&yacute;, truyền thuyết thần thoại, du lịch b&uacute;t k&yacute;, cơ sở v&otilde; học binh s&aacute;ch loại h&igrave;nh tạp thư.<br />\r\n<br />\r\nBởi v&igrave; trang giấy v&agrave; in ấn thuật xuất hiện cực kỳ sớm nguy&ecirc;n nh&acirc;n, từ thượng cổ đến nay truyền thừa thể hệ chưa hề đoạn tuyệt hoặc l&agrave; tuyệt tự, lưu giữ lại kiến thức c&oacute; thể tưởng tượng được cỡ n&agrave;o phong ph&uacute; bề bộn.<br />\r\n<br />\r\nTrấn Bắc C&ocirc;ng Phủ T&agrave;ng Thư L&acirc;u mười phần rộng lớn, thư tịch đ&ecirc;́n mười vạn s&aacute;ch, Trần Anh c&oacute; thể xem b&igrave;nh thường thư tịch đầy rẫy, nếu kh&ocirc;ng phải cố &yacute; đem những kia th&acirc;m ảo kh&oacute; hiểu thư tịch tạm thời để ở một b&ecirc;n, hắn suy nghĩ nhớ kỹ mấy vạn s&aacute;ch tạp thư nội dung kh&ocirc;ng hề dễ d&agrave;ng.<br />\r\n<br />\r\nMay mắn hắn kh&ocirc;ng c&oacute; tự m&igrave;nh lật xem, m&agrave; l&agrave; th&ocirc;ng qua b&ecirc;n người nha ho&agrave;n l&atilde;ng đọc. Người b&ecirc;n ngo&agrave;i cho l&agrave; hắn chẳng qua l&agrave; đang nghe xong chuyện xưa, thậm ch&iacute; đem nha ho&agrave;n l&atilde;ng đọc xem như b&agrave;i h&aacute;t ru con, chỉ coi đ&agrave;m tiếu lơ đễnh.<br />\r\n<br />\r\nKh&ocirc;ng phải vậy một ng&agrave;y mấy quyển thậm ch&iacute; mười mấy vốn đọc, kh&ocirc;ng phải xảy ra chuyện lớn kh&ocirc;ng thể, hắn cũng kh&ocirc;ng sống nổi đến trước mắt tuổi.<br />\r\n<br />\r\nB&ecirc;n người nha ho&agrave;n, cũng bởi v&igrave; l&atilde;ng đọc đại lượng thư tịch mở rộng tầm mắt, cơ bản kh&ocirc;ng c&oacute; ra c&aacute;i g&igrave; y&ecirc;u thi&ecirc;u th&acirc;n.<br />\r\n<br />\r\nCũng l&agrave; nhờ v&agrave;o đ&oacute;,<br />\r\n<br />\r\nTrần Anh kh&ocirc;ng chỉ c&oacute; hấp thụ đại lượng kiến thức, đồng thời cũng đối thế giới b&ecirc;n ngo&agrave;i c&oacute; đại kh&aacute;i hiểu.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><br />\r\n<br />\r\nC&aacute;c loại hoa hồng l&atilde;ng đọc xong một quyển tạp thư, hắn l&uacute;c n&agrave;y mới trở về ph&ograve;ng nghỉ trưa nửa canh giờ.<br />\r\n<br />\r\n&quot;Tam thiếu gia, n&ecirc;n đi luyện v&otilde; tr&agrave;ng!&quot;<br />\r\n<br />\r\nThời gian vừa đến, tiểu nha ho&agrave;n liễu t&aacute;i đem hắn tỉnh lại, rửa mặt sửa sang lại một phen chạy tới nhị m&ocirc;n c&aacute;ch đ&oacute; kh&ocirc;ng xa nhỏ luyện v&otilde; tr&agrave;ng.<br />\r\n<br />\r\nHừng hực dưới &aacute;nh mặt trời, đ&atilde; c&oacute; một vị th&acirc;n mang hộ viện trang phục đại h&aacute;n vạm vỡ chờ.<br />\r\n<br />\r\n&quot;Hồng hộ viện, ch&uacute;ng ta bắt đầu đi!&quot;<br />\r\n<br />\r\nTrần Anh cười khẽ gật đầu, hướng c&aacute;i kia cường tr&aacute;ng hộ viện n&oacute;i.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, cả người hắn tinh thần phấn chấn, ch&uacute;t n&agrave;o cũng kh&ocirc;ng c&oacute; buổi s&aacute;ng thỉnh an, c&ugrave;ng buổi s&aacute;ng đi học l&uacute;c uể oải suy sụp.<br />\r\n<br />\r\nBiểu hiện như vậy, tự nhi&ecirc;n cũng l&agrave; thủ đoạn tự vệ. Chẳng qua hắn x&aacute;c thực đối luyện v&otilde; tương đương cảm thấy hứng th&uacute;.<br />\r\n<br />\r\n&quot;Tam thiếu gia, vẫn quy củ cũ, đ&aacute;nh trước một chuyến quyền n&oacute;ng một ch&uacute;t g&acirc;n cốt!&quot;<br />\r\n<br />\r\nH&igrave;nh thể hung h&atilde;n Hồng hộ viện mặt mũi tr&agrave;n đầy nghi&ecirc;m t&uacute;c, gật đầu n&oacute;i thẳng.<br />\r\n<br />\r\nTrần Anh cũng kh&ocirc;ng c&ograve;n nhiều lời, tho&aacute;ng hoạt động một ch&uacute;t g&acirc;n cốt, th&acirc;n thể trầm xuống c&ugrave;ng nhau ở giữa quyền cước bay m&uacute;a, một bộ nhớ kỹ trong l&ograve;ng M&atilde;nh Hổ Quyền một mạch m&agrave; th&agrave;nh.<br />\r\n<br />\r\nChi&ecirc;u thức ti&ecirc;u chuẩn kh&ocirc;ng c&oacute; ch&uacute;t n&agrave;o sơ hở, h&iacute;t thở tiết tấu gi&agrave;u c&oacute; vận luật, chập tr&ugrave;ng nhảy vọt tựa như một đầu hổ con g&agrave;o th&eacute;t n&uacute;i rừng, h&ocirc; qu&aacute;t thanh &acirc;m ở nhỏ luyện v&otilde; tr&agrave;ng vừa đi vừa về truyền vang.<br />\r\n<br />\r\nC&aacute;c loại một bộ M&atilde;nh Hổ Quyền đ&aacute;nh xong thu thức, hắn đ&atilde; xong to&agrave;n th&acirc;n kh&ocirc; n&oacute;ng đầu đầy mồ h&ocirc;i.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><br />\r\n&quot;Tam thiếu gia cơ sở M&atilde;nh Hổ Quyền đ&atilde; tương đối th&agrave;nh thục, kế tiếp c&ograve;n l&agrave; đứng như cọc gỗ ngồi tr&ecirc;n ngựa đi!&quot;<br />\r\n<br />\r\nH&igrave;nh thể hung h&atilde;n Hồng hộ viện khẽ gật đầu, thần sắc tr&ecirc;n mặt vẫn kh&ocirc;ng c&oacute; bất kỳ biến h&oacute;a n&agrave;o, đi tới chỉ điểm Trần Anh ngồi tr&ecirc;n ngựa đứng như cọc gỗ, th&aacute;i độ l&atilde;nh đạm h&igrave;nh như l&agrave;m theo th&ocirc;ng lệ.<br />\r\n<br />\r\nCũng x&aacute;c thực chẳng qua l&agrave; l&agrave;m theo th&ocirc;ng lệ...<br />\r\n<br />\r\nTừ khi Trần Anh năm tuổi bắt đầu th&igrave; thầm lấy muốn luyện v&otilde;, Hồng hộ viện đ&atilde; l&agrave; hắn đời thứ ba dạy bảo v&otilde; sư, truyền thụ như trước vẫn l&agrave; M&atilde;nh Hổ Quyền như vậy cơ sở đo&aacute;n thể quyền ph&aacute;p.<br />\r\n<br />\r\nKh&ocirc;ng sai, từ năm tuổi luyện v&otilde; bắt đầu, đến b&acirc;y giờ ước chừng đ&atilde; c&oacute; năm năm, hắn c&ograve;n đang luyện một bộ c&ocirc;ng phủ cơ sở nhất đo&aacute;n thể quyền ph&aacute;p, chuy&ecirc;n thay cho hộ viện tu luyện loại đ&oacute;.<br />\r\n<br />\r\nTrong l&ograve;ng Trần Anh m&ocirc;n xong, đ&acirc;y cũng l&agrave; mẹ cả thủ đoạn.<br />\r\n<br />\r\nCứ việc Đại Tề đế quốc đ&atilde; c&oacute; văn đạo cường thịnh chi thế, c&oacute; thể Trấn Bắc C&ocirc;ng l&agrave;m Bắc Địa qu&acirc;n đội đệ nhất h&agrave;o h&ugrave;ng, đối với gia tộc con ch&aacute;u v&otilde; nghệ th&acirc;n thủ tương đương coi trọng.<br />\r\n<br />\r\nSắp th&agrave;nh năm con vợ cả Nhị huynh, từ nhỏ đ&atilde; biểu hiện ra cực kỳ xuất ch&uacute;ng luyện v&otilde; thi&ecirc;n ph&uacute;, tự nhi&ecirc;n l&agrave; Trấn Bắc C&ocirc;ng Phủ trong qu&acirc;n đội việc nh&acirc;n đức kh&ocirc;ng nhường ai người thừa kế.<br />\r\n<br />\r\nC&ocirc;ng tước cha ruột &yacute; nghĩ g&igrave; kh&ocirc;ng r&otilde; r&agrave;ng, nhưng Trần Anh hết sức r&otilde; r&agrave;ng, con vợ cả Nhị huynh ở mẹ cả trong mắt, ch&iacute;nh l&agrave; kế thừa c&ocirc;ng phủ trong qu&acirc;n thế lực kh&ocirc;ng c&oacute; hai nh&acirc;n tuyển.<br />\r\n<br />\r\nV&agrave; đi văn đường con vợ cả Đại huynh một văn một v&otilde;, một mực cầm giữ c&ocirc;ng phủ ở đời n&agrave;y b&ecirc;n trong tuyệt đại bộ phận t&agrave;i nguy&ecirc;n.<br />\r\n<br />\r\nDưới t&igrave;nh huống như vậy, đ&acirc;u c&ograve;n c&oacute; Trần Anh bao nhi&ecirc;u cơ hội ra mặt?<br />\r\n<br />\r\nMẹ cả kh&ocirc;ng c&oacute; đoạn mất hắn luyện v&otilde; con đường, c&ograve;n an b&agrave;i hộ viện truyền thụ cơ sở đo&aacute;n thể quyền ph&aacute;p, đ&atilde; rất đại độ, cứ việc năm năm đổi ba c&aacute;i hộ viện dạy bảo, một tr&igrave;nh độ so với một thấp, trước mắt Hồng hộ viện chẳng qua l&agrave; hộ viện b&ecirc;n trong t&acirc;n đinh, th&aacute;i điểu trong th&aacute;i điểu...<br />\r\n<br />\r\nTruyện main b&aacute;, hậu cung, c&oacute; ch&uacute;t yy, diễn tả pk r&otilde; r&agrave;ng, văn phong kh&aacute; ổn, đ&atilde; ho&agrave;n th&agrave;nh</p>', '0', 'trong-khe-hep-cau-sinh-ton', 6, '68', '2022-08-03 03:57:05', '2022-08-03 03:57:23'),
(28, '3', 'Phúc Vận Bảo Tháp xoát kinh nghiệm', '<p>Nắng gắt như lửa, kh&ocirc;ng ch&uacute;t ki&ecirc;ng kỵ huy s&aacute;i v&ocirc; tận nhiệt lượng.<br />\r\n<br />\r\nTrấn Bắc C&ocirc;ng Phủ nhị m&ocirc;n b&ecirc;n ngo&agrave;i nhỏ trong luyện v&otilde; trường, Trần Anh ngồi xổm ngựa đứng như cọc gỗ đầu đầy mồ h&ocirc;i, h&iacute;t thở k&eacute;o d&agrave;i gi&agrave;u c&oacute; tiết tấu, th&acirc;n thể hơi c&oacute; chập tr&ugrave;ng tựa như thật biến th&agrave;nh một thớt ngựa con c&acirc;u.<br />\r\n<br />\r\nNếu m&agrave; c&oacute; được v&otilde; học T&ocirc;ng Sư ở đ&acirc;y, khẳng định sẽ kinh ngạc với hắn l&uacute;c n&agrave;y trạng th&aacute;i.<br />\r\n<br />\r\nNgồi xổm ngựa đứng như cọc gỗ vốn l&agrave; m&ocirc; phỏng động vật cử chỉ phương thức r&egrave;n luyện, Trần Anh trước mắt mặc kệ l&agrave; h&iacute;t thở tiết tấu, vẫn l&agrave; g&acirc;n cốt vận động quy luật, lại hoặc l&agrave; th&acirc;n thể chập tr&ugrave;ng tần suất, gần như c&ugrave;ng thật ngựa con c&acirc;u giống như đ&uacute;c, loại n&agrave;y h&igrave;nh thần sẵn s&agrave;ng r&egrave;n luyện hiệu quả tốt nhất.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, c&aacute;ch đ&oacute; kh&ocirc;ng xa Hồng hộ viện tr&igrave;nh độ, căn bản nh&igrave;n kh&ocirc;ng ra ảo diệu trong đ&oacute;.<br />\r\n&nbsp;</p>', '<p>Nắng gắt như lửa, kh&ocirc;ng ch&uacute;t ki&ecirc;ng kỵ huy s&aacute;i v&ocirc; tận nhiệt lượng.<br />\r\n<br />\r\nTrấn Bắc C&ocirc;ng Phủ nhị m&ocirc;n b&ecirc;n ngo&agrave;i nhỏ trong luyện v&otilde; trường, Trần Anh ngồi xổm ngựa đứng như cọc gỗ đầu đầy mồ h&ocirc;i, h&iacute;t thở k&eacute;o d&agrave;i gi&agrave;u c&oacute; tiết tấu, th&acirc;n thể hơi c&oacute; chập tr&ugrave;ng tựa như thật biến th&agrave;nh một thớt ngựa con c&acirc;u.<br />\r\n<br />\r\nNếu m&agrave; c&oacute; được v&otilde; học T&ocirc;ng Sư ở đ&acirc;y, khẳng định sẽ kinh ngạc với hắn l&uacute;c n&agrave;y trạng th&aacute;i.<br />\r\n<br />\r\nNgồi xổm ngựa đứng như cọc gỗ vốn l&agrave; m&ocirc; phỏng động vật cử chỉ phương thức r&egrave;n luyện, Trần Anh trước mắt mặc kệ l&agrave; h&iacute;t thở tiết tấu, vẫn l&agrave; g&acirc;n cốt vận động quy luật, lại hoặc l&agrave; th&acirc;n thể chập tr&ugrave;ng tần suất, gần như c&ugrave;ng thật ngựa con c&acirc;u giống như đ&uacute;c, loại n&agrave;y h&igrave;nh thần sẵn s&agrave;ng r&egrave;n luyện hiệu quả tốt nhất.<br />\r\n<br />\r\nĐ&aacute;ng tiếc, c&aacute;ch đ&oacute; kh&ocirc;ng xa Hồng hộ viện tr&igrave;nh độ, căn bản nh&igrave;n kh&ocirc;ng ra ảo diệu trong đ&oacute;.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; ph&oacute;ng nh&atilde;n cả Trấn Bắc C&ocirc;ng Phủ, v&otilde; học T&ocirc;ng Sư số lượng cũng l&agrave; một c&aacute;i tay c&oacute; thể đếm được, trừ phi vận kh&iacute; đặc biệt kh&ocirc;ng tốt, kh&ocirc;ng phải vậy liền kh&ocirc;ng cần lo lắng bại lộ.<br />\r\n<br />\r\nThật sự cho rằng Trần Anh trang ng&acirc;y dại b&aacute;n cho&aacute;ng v&aacute;ng, hao tốn thời gian bảy năm t&iacute;ch lũy lượng lớn kiến thức l&agrave; giả hay sao?<br />\r\n<br />\r\nCơ sở v&otilde; học phương diện kiến thức, thậm ch&iacute; c&ograve;n c&oacute; tương quan y học nội dung, hắn kh&ocirc;ng n&oacute;i m&ocirc;n xong cũng l&agrave; như l&ograve;ng b&agrave;n tay.<br />\r\n<br />\r\nC&oacute; phong ph&uacute; l&yacute; luận kiến thức, tăng th&ecirc;m tự thể nghiệm hiệu quả, một bộ học được năm năm cơ sở M&atilde;nh Hổ Quyền, sớm đ&atilde; nghi&ecirc;n cứu triệt để.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; tới nguy&ecirc;n bộ đứng như cọc gỗ ngồi xổm ngựa, cũng c&oacute; thể th&ocirc;ng qua đối với ch&acirc;n thật ngựa nhỏ x&iacute;u quan s&aacute;t, kh&ocirc;ng ngừng điều chỉnh th&iacute;ch hợp tự th&acirc;n tiết tấu, thời gian năm năm đủ để đạt đến hiệu quả tốt nhất.<br />\r\n<br />\r\nKh&ocirc;ng ch&uacute;t n&agrave;o khoa trương, hắn đối với cơ sở M&atilde;nh Hổ Quyền nghi&ecirc;n cứu c&ugrave;ng r&egrave;n luyện, đ&atilde; đạt đến T&ocirc;ng Sư ti&ecirc;u chuẩn, chẳng qua l&agrave; đ&aacute;ng tiếc kh&ocirc;ng c&oacute; biện ph&aacute;p mở ra tiến một bước r&egrave;n luyện.<br />\r\n<br />\r\nKh&ocirc;ng phải l&agrave; kh&ocirc;ng c&oacute; phương ph&aacute;p r&egrave;n luyện, c&ocirc;ng phủ trong T&agrave;ng Thư L&acirc;u cơ sở v&otilde; nghệ phương ph&aacute;p r&egrave;n luyện đ&ocirc;ng đảo, đơn hắn biết cũng kh&ocirc;ng dưới mười mấy loại, trong đ&oacute; cũng c&oacute; M&atilde;nh Hổ Quyền tiến giai quyền ph&aacute;p, c&oacute; thể hắn kh&ocirc;ng d&aacute;m luyện a.<br />\r\n<br />\r\nThật sự cho rằng mẹ cả đối với hắn luyện v&otilde; tiến tr&igrave;nh, kh&ocirc;ng quan t&acirc;m hay sao?</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><br />\r\n<br />\r\nTrước mắt Trần Anh biểu hiện ra, chẳng qua l&agrave; v&ocirc; c&ugrave;ng b&igrave;nh thường luyện v&otilde; tư chất th&ocirc;i, đối với d&ugrave;ng v&otilde; lập th&acirc;n con vợ cả Nhị huynh kh&ocirc;ng c&oacute; nhiều uy hiếp.<br />\r\n<br />\r\nMột khi đột nhi&ecirc;n xuất hiện r&otilde; r&agrave;ng thay đổi, hắn sợ l&agrave; sống kh&ocirc;ng qu&aacute; nửa th&aacute;ng, đ&acirc;y l&agrave; mẹ cả kh&ocirc;ng muốn l&agrave;m qu&aacute; mức r&otilde; r&agrave;ng kết quả.<br />\r\n<br />\r\nSinh tồn ho&agrave;n cảnh ch&iacute;nh l&agrave; như vậy, kh&ocirc;ng cần thiết o&aacute;n trời tr&aacute;ch đất.<br />\r\n<br />\r\nChỉ cần nguyện &yacute;, th&ocirc;ng qua một &iacute;t đặc th&ugrave; phương ph&aacute;p r&egrave;n luyện, thực lực của hắn c&oacute; thể ở trong thời gian ngắn đột nhi&ecirc;n tăng mạnh, kiến thức ch&iacute;nh l&agrave; lực lượng lời n&agrave;y tuyệt đối bất giả.<br />\r\n<br />\r\nTrần Anh cũng đem luyện v&otilde;, trở th&agrave;nh ở thế giới n&agrave;y lập th&acirc;n gốc rễ!<br />\r\n<br />\r\nBởi v&igrave; kiến thức truyền thừa, từ Thượng Cổ thời đại chưa từng tuyệt tự nguy&ecirc;n nh&acirc;n, th&ocirc;ng qua bảy năm kh&ocirc;ng ngừng t&iacute;ch lũy, nhưng hắn l&agrave; biết được rất nhiều b&iacute; ẩn. Cứ việc tuyệt đại bộ phận người b&igrave;nh thường, sẽ chỉ đem những t&igrave;nh huống n&agrave;y xem như truyền kỳ cố sự.<br />\r\n<br />\r\nL&agrave; hắn biết, Thượng Cổ thời đại cần phải c&oacute; loại đ&oacute; c&oacute; thể h&aacute;t trăng bắt sao tồn tại cường ho&agrave;nh.<br />\r\n<br />\r\nChẳng qua l&agrave; chẳng biết tại sao, loại tồn tại n&agrave;y từ từ biến mất, thay v&agrave;o đ&oacute; ch&iacute;nh l&agrave; c&aacute;i gọi l&agrave; Luyện kh&iacute; sĩ, đồng dạng c&oacute; ph&aacute;p thuật thần th&ocirc;ng, chẳng qua l&agrave; uy năng nhỏ một ch&uacute;t c&aacute;i tầng cấp.<br />\r\n<br />\r\nVề sau Luyện kh&iacute; sĩ cũng từ từ ti&ecirc;u vong ở trong d&ograve;ng s&ocirc;ng lịch sử, chậm r&atilde;i do ch&acirc;n kh&iacute; v&otilde; giả trở th&agrave;nh lực lượng chủ lưu.<br />\r\n<br />\r\nCh&acirc;n kh&iacute; v&otilde; giả phong quang hơn ng&agrave;n năm từ từ xuống dốc, biến th&agrave;nh trước mắt nội lực c&ugrave;ng kh&iacute; huyết v&otilde; đạo c&ugrave;ng tồn tại h&igrave;nh thức.<br />\r\n<br />\r\nRất như l&agrave; từ thần thoại thời đại, chậm r&atilde;i biến th&agrave;nh thời đại mạt ph&aacute;p, đ&uacute;ng kh&ocirc;ng?<br />\r\n<br />\r\nNgười b&igrave;nh thường, hoặc l&agrave; một &iacute;t truyền thừa kh&ocirc;ng đủ tiểu qu&yacute; tộc c&oacute; thể sẽ cảm thấy một &iacute;t ghi lại, ch&iacute;nh l&agrave; chuyện thần thoại v&agrave; truyền kỳ cố sự, Trần Anh lại kh&ocirc;ng cho l&agrave; như vậy.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><br />\r\n<br />\r\nCứ việc kh&ocirc;ng c&oacute; người đ&atilde; n&oacute;i với hắn những n&agrave;y, c&oacute; thể th&ocirc;ng qua đại lượng văn tự ghi lại, c&oacute; thể ph&aacute;t hiện hệ thống sức mạnh biến h&oacute;a b&igrave;nh thường diễn biến, kh&ocirc;ng c&oacute; bao nhi&ecirc;u đột ngột chỗ.<br />\r\n<br />\r\nKiến thức của hắn trữ bị b&ecirc;n trong, thế nhưng l&agrave; bao gồm một &iacute;t cơ sở Luyện kh&iacute; sĩ tr&uacute;c cơ phương ph&aacute;p, c&ograve;n c&oacute; cơ sở ch&acirc;n kh&iacute; phương ph&aacute;p tu luyện, chẳng qua l&agrave; kh&ocirc;ng c&oacute; biện ph&aacute;p tu luyện m&agrave; th&ocirc;i.<br />\r\n<br />\r\nTrước mắt l&agrave; chủ chảy nội lực v&agrave; kh&iacute; huyết phương ph&aacute;p tu luyện, c&aacute;i trước rất giống tuyết sơn phi hồ c&ugrave;ng giai hệ thống sức mạnh, c&aacute;i sau lại l&agrave; tương đương ho&agrave;n thiện Nội Gia Quyền thể hệ.<br />\r\n<br />\r\nChẳng biết tại sao, ở phương thế giới n&agrave;y, chủ lưu nội kh&iacute; hệ thống tu luyện l&agrave;m cao cấp đo&aacute;n thể phương ph&aacute;p, kh&iacute; huyết hệ thống tu luyện lại l&agrave; đ&ecirc; giai luyện thể phương ph&aacute;p bị t&aacute;ch ra, đương nhi&ecirc;n đến cao giai cấp độ ch&iacute;nh l&agrave; trăm s&ocirc;ng đổ về một biển kết quả.<br />\r\n<br />\r\nTrần Anh luyện năm năm M&atilde;nh Hổ Quyền, ch&iacute;nh l&agrave; kh&iacute; huyết đo&aacute;n thể phương ph&aacute;p b&ecirc;n trong cơ sở quyền ph&aacute;p, căn bản l&agrave; t&iacute;nh kh&ocirc;ng được c&aacute;i g&igrave;.<br />\r\n<br />\r\nNhưng ch&iacute;nh l&agrave; như vậy, luyện đến chỗ cao th&acirc;m cũng đủ để mạnh g&acirc;n kiện xương, trở th&agrave;nh cao thủ Minh K&iacute;nh kh&ocirc;ng đ&aacute;ng kể.<br />\r\n<br />\r\nKh&ocirc;ng sai, nếu thế giới n&agrave;y c&oacute; ho&agrave;n thiện kh&iacute; huyết đo&aacute;n thể phương ph&aacute;p, tự nhi&ecirc;n c&oacute; Minh K&iacute;nh &Aacute;m K&iacute;nh H&oacute;a K&igrave;nh, c&ugrave;ng cương k&igrave;nh Đan K&iacute;nh ph&acirc;n chia, c&ograve;n c&oacute; luyện da luyện g&acirc;n luyện xương luyện tạng c&ugrave;ng tối cao tầng thứ luyện tủy phương ph&aacute;p.<br />\r\n<br />\r\nNghe n&oacute;i c&ocirc;ng tước cha ruột ch&iacute;nh l&agrave; cương k&igrave;nh T&ocirc;ng Sư, ở trong qu&acirc;n doanh lấy một địch trăm kh&ocirc;ng c&oacute; vấn đề.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; bởi v&igrave; biết được những n&agrave;y, tăng th&ecirc;m bản th&acirc;n x&aacute;c thực th&iacute;ch, mới đem luyện V&otilde; Đang l&agrave;m lập th&acirc;n gốc rễ, hao tốn đại lượng tinh lực ở c&aacute;i n&agrave;y cấp tr&ecirc;n.<br />\r\n<br />\r\nLấy hắn phong ph&uacute; l&yacute; luận kiến thức, c&ograve;n c&oacute; năm năm r&egrave;n luyện chi c&ocirc;ng, tiến v&agrave;o Minh K&iacute;nh cấp độ chẳng qua một c&aacute;i &yacute; niệm trong đầu chuyện, chỉ v&igrave; để tr&aacute;nh cho đưa tới mẹ cả ch&uacute; &yacute;, mới một mực đ&egrave; &eacute;p biểu hiện thường thường kh&ocirc;ng c&oacute; g&igrave; lạ.<br />\r\n<br />\r\nNgồi xổm nửa canh giờ trung b&igrave;nh tấn, lại luyện nửa canh giờ M&atilde;nh Hổ Quyền, c&aacute;i ng&agrave;y n&agrave;y r&egrave;n luyện kết th&uacute;c.<br />\r\n<br />\r\nH&igrave;nh thể hung h&atilde;n Hồng hộ viện n&oacute;i một tiếng trực tiếp đi người, Trần Anh mồ h&ocirc;i nhễ nhại mang theo khu&ocirc;n mặt phơi hồng đồng đồng tiểu nha ho&agrave;n liễu t&aacute;i quay trở về nh&agrave; m&igrave;nh tiểu viện.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><br />\r\n<br />\r\nRửa mặt qua đi sắc trời dần tối, nếm qua sung t&uacute;c lại kh&ocirc;ng tinh xảo cơm tối, hắn trong s&acirc;n độ bước ti&ecirc;u thực, hoa hồng lại bắt đầu l&atilde;ng đọc mới tạp thư.<br />\r\n<br />\r\nM&atilde;i cho đến trước khi ngủ, ước chừng nghe kh&ocirc;ng sai biệt lắm mười lăm vốn tạp thư l&atilde;ng đọc, hắn đ&acirc;y mới gọi l&agrave; đ&atilde; miệng đắng lưỡi kh&ocirc; hai c&aacute;i tiểu nha ho&agrave;n ngừng nghỉ ngơi.<br />\r\n<br />\r\nThời gian ch&iacute;nh l&agrave; như vậy phong ph&uacute; m&agrave; nh&agrave;m ch&aacute;n, nằm d&agrave;i tr&ecirc;n giường trước đem nghe được kiến thức &ocirc;n lại một lần, lại suy nghĩ một kh&iacute; huyết tu luyện chuyện, chờ đến mệt mỏi đ&aacute;nh tới l&uacute;c n&agrave;y mới theo bản năng tiến v&agrave;o minh tưởng trạng th&aacute;i.<br />\r\n<br />\r\nSau một khắc đột nhi&ecirc;n hai mắt mở to, kh&ocirc;ng biết c&oacute; phải hay kh&ocirc;ng l&agrave; lưu giữ ở thức hải b&agrave;n tay v&agrave;ng Ph&uacute;c Vận Bảo Th&aacute;p, truyền một c&aacute;i gọi hắn chấn phấn kh&ocirc;ng dứt tin tức: T&iacute;ch lũy kh&iacute; vận đủ để chống đỡ một lần cấp thấp thế giới xuy&ecirc;n qua!<br />\r\n<br />\r\nBảy tầng Ph&uacute;c Vận Bảo Th&aacute;p, c&ugrave;ng hắn xuy&ecirc;n qua trước trong tay thưởng thức cổ ngọc đi&ecirc;u khắc giống nhau như đ&uacute;c, chỉ c&oacute; điều từ mặt phẳng biến th&agrave;nh lập thể.<br />\r\n<br />\r\nC&ocirc;ng hiệu hay sao, cũng l&agrave; thi&ecirc;u đốt trong th&aacute;p g&oacute;p nhặt kh&iacute; vận xuy&ecirc;n qua thế giới ảo tưởng.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, Ph&uacute;c Vận Bảo Th&aacute;p ph&iacute;a tr&ecirc;n s&aacute;u tầng vẫn như cũ mờ đi, dưới đ&aacute;y tầng kia l&oacute;ng l&aacute;nh mịt mờ &aacute;nh s&aacute;ng, đ&atilde; g&oacute;p nhặt đủ xuy&ecirc;n qua cấp thấp thế giới kh&iacute; vận.<br />\r\n<br />\r\nLời n&oacute;i, hắn c&oacute; thể xuy&ecirc;n th&agrave;nh Đại Tề đế quốc Trấn Bắc C&ocirc;ng con thứ, thấy thế n&agrave;o cũng coi l&agrave; kh&iacute; vận kh&ocirc;ng k&eacute;m, &iacute;t nhất so với đ&atilde; xuy&ecirc;n qua đến nh&agrave; b&igrave;nh d&acirc;n b&aacute;ch t&iacute;nh c&ograve;n mạnh hơn nhiều.<br />\r\n<br />\r\nTuy rằng thời gian tr&ocirc;i qua thận trọng, khả năng đủ thu hoạch t&agrave;i nguy&ecirc;n, cũng kh&ocirc;ng phải b&igrave;nh thường tiểu thế gia con trai trưởng c&oacute; thể so,<br />\r\n<br />\r\nVề phần Ph&uacute;c Vận Bảo Th&aacute;p rốt cuộc l&agrave; một thứ đồ g&igrave;, Trần Anh kh&ocirc;ng biết cũng lười suy nghĩ, trước mắt hắn quan t&acirc;m nhất vẫn l&agrave; linh hồn xuy&ecirc;n qua, đ&acirc;y ch&iacute;nh l&agrave; hắn tho&aacute;t khỏi trước mắt l&uacute;ng t&uacute;ng t&igrave;nh cảnh, cơ hội tốt nhất.<br />\r\n<br />\r\n&Iacute;t nhất, đ&atilde; xuy&ecirc;n qua đến thế giới ảo tưởng sau, ho&agrave;n to&agrave;n c&oacute; thể đem trong l&ograve;ng t&iacute;ch lũy đ&ocirc;ng đảo &yacute; nghĩ, c&ograve;n c&oacute; kh&iacute; Huyết tu luyện phương ph&aacute;p hảo hảo thử một phen, cũng coi l&agrave; t&iacute;ch lũy kinh nghiệm...<br />\r\n<br />\r\nTruyện main b&aacute;, hậu cung, c&oacute; ch&uacute;t yy, diễn tả pk r&otilde; r&agrave;ng, văn phong kh&aacute; ổn, đ&atilde; ho&agrave;n th&agrave;nh</p>', '0', 'phuc-van-bao-thap-xoat-kinh-nghiem', 6, '5464', '2022-08-03 03:57:55', '2022-08-03 03:58:18'),
(29, '4', 'Đem ăn nhờ ở đậu tiến hành tới cùng', '<p>&quot;Tam thiếu gia, rời giường!&quot;<br />\r\n<br />\r\nTrung ni&ecirc;n b&agrave; tử th&ocirc; h&agrave;o tiếng n&oacute;i, đem vừa rồi th&ocirc;ng qua thi&ecirc;u đốt kh&iacute; vận, xuy&ecirc;n qua m&agrave; đến Trần Anh đ&aacute;nh thức,<br />\r\n<br />\r\nSau một khắc, t&uacute;c chủ k&yacute; ức đơn giản x&ocirc;ng l&ecirc;n đầu, kh&ocirc;ng khỏi lộ ra một cười khẽ.<br />\r\n<br />\r\nĐồng dạng l&agrave; con thứ c&ocirc;ng phủ, đồng dạng x&ecirc;́p hạng l&atilde;o tam, chỉ c&oacute; điều t&igrave;nh trạng k&eacute;m hắn nhiều.<br />\r\n<br />\r\nGiả T&ocirc;ng, Vinh Quốc Phủ nhất đẳng tướng qu&acirc;n Giả X&aacute; con thứ, người xưng &#39;T&ocirc;ng tam gia&#39;.<br />\r\n<br />\r\nKh&ocirc;ng sai, hắn đ&atilde; xuy&ecirc;n qua đến thế giới Hồng L&acirc;u Mộng, phụ th&acirc;n ở c&aacute;i kia &#39;Đen l&ocirc;ng m&agrave;y &ocirc; miệng&#39;, tiểu trong suốt b&ecirc;n trong tiểu trong suốt tr&ecirc;n người Giả T&ocirc;ng. (sau đ&oacute; lấy Giả T&ocirc;ng t&ecirc;n l&agrave; chủ)<br />\r\n<br />\r\n&quot;Tam thiếu gia mau mau đi l&ecirc;n, h&ocirc;m nay thế nhưng l&agrave; biểu tiểu thư tới lễ lớn, phu nh&acirc;n thật sớm liền ch&agrave;o hỏi, thiếu gia tốt nhất nhanh l&ecirc;n một ch&uacute;t đi!&quot;<br />\r\n<br />\r\nMột mặc t&iacute;nh kh&ocirc;ng được s&aacute;ng r&otilde;, nhưng cũng t&iacute;nh to&aacute;n tề chỉnh trung ni&ecirc;n b&agrave; tử, cũng l&agrave; phải Giả T&ocirc;ng nhũ mẫu L&yacute; thị trực tiếp đi đi qua, đem chăn v&eacute;n l&ecirc;n muốn k&eacute;o người.<br />\r\n&nbsp;</p>', '<p>&quot;Tam thiếu gia, rời giường!&quot;<br />\r\n<br />\r\nTrung ni&ecirc;n b&agrave; tử th&ocirc; h&agrave;o tiếng n&oacute;i, đem vừa rồi th&ocirc;ng qua thi&ecirc;u đốt kh&iacute; vận, xuy&ecirc;n qua m&agrave; đến Trần Anh đ&aacute;nh thức,<br />\r\n<br />\r\nSau một khắc, t&uacute;c chủ k&yacute; ức đơn giản x&ocirc;ng l&ecirc;n đầu, kh&ocirc;ng khỏi lộ ra một cười khẽ.<br />\r\n<br />\r\nĐồng dạng l&agrave; con thứ c&ocirc;ng phủ, đồng dạng x&ecirc;́p hạng l&atilde;o tam, chỉ c&oacute; điều t&igrave;nh trạng k&eacute;m hắn nhiều.<br />\r\n<br />\r\nGiả T&ocirc;ng, Vinh Quốc Phủ nhất đẳng tướng qu&acirc;n Giả X&aacute; con thứ, người xưng &#39;T&ocirc;ng tam gia&#39;.<br />\r\n<br />\r\nKh&ocirc;ng sai, hắn đ&atilde; xuy&ecirc;n qua đến thế giới Hồng L&acirc;u Mộng, phụ th&acirc;n ở c&aacute;i kia &#39;Đen l&ocirc;ng m&agrave;y &ocirc; miệng&#39;, tiểu trong suốt b&ecirc;n trong tiểu trong suốt tr&ecirc;n người Giả T&ocirc;ng. (sau đ&oacute; lấy Giả T&ocirc;ng t&ecirc;n l&agrave; chủ)<br />\r\n<br />\r\n&quot;Tam thiếu gia mau mau đi l&ecirc;n, h&ocirc;m nay thế nhưng l&agrave; biểu tiểu thư tới lễ lớn, phu nh&acirc;n thật sớm liền ch&agrave;o hỏi, thiếu gia tốt nhất nhanh l&ecirc;n một ch&uacute;t đi!&quot;<br />\r\n<br />\r\nMột mặc t&iacute;nh kh&ocirc;ng được s&aacute;ng r&otilde;, nhưng cũng t&iacute;nh to&aacute;n tề chỉnh trung ni&ecirc;n b&agrave; tử, cũng l&agrave; phải Giả T&ocirc;ng nhũ mẫu L&yacute; thị trực tiếp đi đi qua, đem chăn v&eacute;n l&ecirc;n muốn k&eacute;o người.<br />\r\n<br />\r\n&quot;Đừng đừng đừng, ta tự m&igrave;nh tới ta tự m&igrave;nh tới!&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng mặt xạm lại, vốn định như một l&agrave;n kh&oacute;i nhảy l&ecirc;n, kết quả kh&ocirc;ng c&oacute; trải qua r&egrave;n luyện bảy tuổi tiểu th&acirc;n bản kh&ocirc;ng nghe chỉ huy, cuối c&ugrave;ng vẫn l&agrave; bi phẫn đ&atilde; rơi v&agrave;o nhũ mẫu ma trảo.<br />\r\n<br />\r\n&quot;&Ocirc;i ta Tam thiếu gia, ngươi c&aacute;i n&agrave;y c&ograve;n l&agrave;m ki&ecirc;u đ&atilde; l&ecirc;n!&quot;<br />\r\n<br />\r\nNhũ mẫu L&yacute; thị tay ch&acirc;n lanh lẹ, hai ba lần liền thay Giả T&ocirc;ng đổi xong y phục, tức giận n&oacute;i: &quot;Tam thiếu gia ngươi c&oacute; phải ta một tay sữa, lớn !&quot;<br />\r\n<br />\r\n&quot;Biết đến L&yacute; Mụ, ta cũng n&ecirc;n đi th&aacute;i th&aacute;i c&aacute;i kia!&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng chạy như một l&agrave;n kh&oacute;i rơi mất, theo trong tr&iacute; nhớ đơn giản cảnh tượng, vắt ch&acirc;n l&ecirc;n cổ liền hướng mẹ cả H&igrave;nh phu nh&acirc;n s&acirc;n nhỏ chạy tới.<br />\r\n<br />\r\n&quot;Tam thiếu gia chờ ta một ch&uacute;t...&quot;<br />\r\n<br />\r\nTiểu trong suốt ch&iacute;nh l&agrave; tiểu trong suốt, tr&ecirc;n đường gặp nha ho&agrave;n b&agrave; tử, kh&ocirc;ng c&oacute; một cầm con mắt nh&igrave;n hắn, chớ n&oacute;i chi l&agrave; nhường đường ch&agrave;o hỏi.</p>\r\n\r\n<p><br />\r\nQuy củ n&agrave;y loạn, vẫn l&agrave; đường đường Nhất Đẳng Tương Qu&acirc;n Phủ hay sao?<br />\r\n<br />\r\nMuốn n&oacute;i t&igrave;nh cảnh, hắn ở Vinh Quốc Phủ c&ugrave;ng Trấn Bắc C&ocirc;ng Phủ đều kh&ocirc;ng kh&aacute;c mấy, c&oacute; thể Trấn Bắc C&ocirc;ng Phủ quy củ s&acirc;m nghi&ecirc;m, mặc kệ những hạ nh&acirc;n kia trong l&ograve;ng &yacute; nghĩ g&igrave;, mặt ngo&agrave;i cung k&iacute;nh một điểm kh&ocirc;ng k&eacute;m, Vinh Quốc Phủ nơi n&agrave;y liền chậc chậc...<br />\r\n<br />\r\nTrong tr&iacute; nhớ, Giả T&ocirc;ng ở Nhất Đẳng Tương Qu&acirc;n Phủ nơi n&agrave;o đ&oacute; vắng vẻ tiểu viện, ch&uacute;t n&agrave;y vẫn l&agrave; rất tốt.<br />\r\n<br />\r\nTh&ocirc;i đừng ch&eacute;m gi&oacute; c&aacute;i g&igrave; Nhất Đẳng Tương Qu&acirc;n Phủ l&agrave; từ Vinh Quốc Phủ v&ograve;ng c&aacute;i s&acirc;n nhỏ m&agrave; th&agrave;nh, thật so đo ph&aacute;p l&yacute; m&agrave; n&oacute;i, phủ tướng qu&acirc;n mới phải l&agrave; Vinh Quốc c&ocirc;ng phủ hạch t&acirc;m, c&aacute;i n&agrave;y kh&ocirc;ng cần thiết đề.<br />\r\n<br />\r\nMột đường chạy chậm, kh&ocirc;ng tới một dặm đ&atilde; l&agrave; thở hồng hộc đầu đầy mồ h&ocirc;i, th&acirc;n thể n&agrave;y đ&uacute;ng l&agrave; chẳng ra sao cả, xem ra cần phải mau sớm r&egrave;n luyện đi l&ecirc;n.<br />\r\n<br />\r\nỞ ch&iacute;nh ph&ograve;ng cửa, ch&iacute;nh hảo gặp H&igrave;nh phu nh&acirc;n t&acirc;m ph&uacute;c Vương Thiện Bảo Gia, vội v&agrave;ng l&ecirc;n tiếng ch&agrave;o hỏi vấn an.<br />\r\n<br />\r\n&quot;Th&aacute;i th&aacute;i, T&ocirc;ng ca nhi đến !&quot;<br />\r\n<br />\r\nH&igrave;nh phu nh&acirc;n ch&iacute;nh ph&ograve;ng, Vương Thiện Bảo Gia tiến đến nhắc nhở.<br />\r\n<br />\r\n&quot;Cho th&aacute;i th&aacute;i thỉnh an!&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng đi đến, thỉnh an thi lễ một điểm kh&ocirc;ng k&eacute;m, những nhũ mẫu L&yacute; thị n&agrave;y cũng kh&ocirc;ng c&oacute; thiếu đi dạy bảo, thật cũng kh&ocirc;ng một &iacute;t Hồng học nh&agrave; ph&acirc;n t&iacute;ch như vậy kh&ocirc;ng chịu nổi.<br />\r\n<br />\r\n&quot;H&ocirc;m nay biểu c&ocirc; nương v&agrave;o cửa, chẳng mấy chốc sẽ tới đại ph&ograve;ng b&ecirc;n n&agrave;y gặp ngươi l&atilde;o gia, đ&agrave;ng ho&agrave;ng ở ch&iacute;nh ph&ograve;ng đợi c&aacute;i n&agrave;o cũng kh&ocirc;ng cần đi!&quot;<br />\r\n<br />\r\nH&igrave;nh phu nh&acirc;n th&aacute;i độ l&atilde;nh đạm, ph&acirc;n ph&oacute; mấy c&acirc;u liền ở Vương Thiện Bảo Gia, c&ograve;n c&oacute; hai vị đại nha ho&agrave;n chen ch&uacute;c xuống rời khỏi.<br />\r\n<br />\r\nChậc chậc...<br />\r\n<br />\r\nThật đ&uacute;ng l&agrave; kh&ocirc;ng kh&aacute;ch kh&iacute;, một điểm mặt mũi cũng kh&ocirc;ng cho.<br />\r\n<br />\r\n&quot;Vị tỷ tỷ n&agrave;y, c&oacute; g&igrave; ăn hay kh&ocirc;ng, ta chưa d&ugrave;ng bữa!&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng cũng kh&ocirc;ng c&oacute; kh&aacute;ch kh&iacute;, trực tiếp ch&agrave;o hỏi b&ecirc;n cạnh nhị đẳng nha ho&agrave;n, muốn n&agrave;ng l&agrave;m ăn ch&uacute;t g&igrave; tới, bụng tức thời k&ecirc;u rột rột hai tiếng.</p>\r\n\r\n<p><br />\r\n<br />\r\nĐem lưu thủ ch&iacute;nh đường mấy c&aacute;i nha ho&agrave;n chọc cho cười khanh kh&aacute;ch, cũng kh&ocirc;ng c&oacute; thật kh&ocirc;ng th&egrave;m để &yacute; Giả T&ocirc;ng y&ecirc;u cầu, nếu hắn kh&oacute;c rống đi l&ecirc;n nhưng chịu tr&aacute;ch nhiệm kh&ocirc;ng dậy nổi.<br />\r\n<br />\r\nMột hơi ăn ba bàn tinh sảo điểm t&acirc;m nhỏ, l&uacute;c n&agrave;y mới đ&aacute;nh ợ một c&aacute;i ra cửa, căn bản l&agrave; kh&ocirc;ng thèm đ&ecirc;̉ ý trong ph&ograve;ng bọn nha ho&agrave;n vẻ giật m&igrave;nh.<br />\r\n<br />\r\nNhất Đẳng Tương Qu&acirc;n Phủ diện t&iacute;ch kh&ocirc;ng lớn, gian ph&ograve;ng lại l&agrave; kh&ocirc;ng &iacute;t, chủ yếu bị tiện nghi l&atilde;o tử Giả X&aacute; tiểu l&atilde;o b&agrave; nh&oacute;m chiếm, c&ograve;n lại l&agrave;m xanh ho&aacute; kh&ocirc;ng gian liền kh&ocirc;ng lớn.<br />\r\n<br />\r\nC&oacute; thể d&ugrave; l&agrave; như vậy, cả phủ tướng qu&acirc;n ch&iacute;nh đường c&aacute;i n&agrave;y một mảnh, vẫn như cũ bố tr&iacute; được tinh sảo kh&eacute;o l&eacute;o đường n&eacute;t độc đ&aacute;o, r&otilde; r&agrave;ng l&agrave; bỏ ra t&acirc;m tư.<br />\r\n<br />\r\nHắn liền chạy đạt đến trong hoa vi&ecirc;n ti&ecirc;u thực,<br />\r\n<br />\r\nDạo bước m&agrave; đi h&iacute;t thở gi&agrave;u c&oacute; tiết tấu, cẩn thận thử nghiệm vận chuyển kh&iacute; huyết, gia tốc đồ ăn ti&ecirc;u h&oacute;a hấp thu.<br />\r\n<br />\r\nCũng kh&ocirc;ng biết thế n&agrave;o nu&ocirc;i, Giả T&ocirc;ng bảy tuổi thể cốt c&oacute; ch&uacute;t kh&iacute; nhược, tạm thời c&ograve;n chưa th&iacute;ch hợp đứng trung b&igrave;nh tấn luyện kiến thức cơ bản, như vậy h&agrave;nh vi cũng c&oacute; ch&uacute;t đột ngột, vẫn l&agrave; trước ti&ecirc;n đem th&acirc;n thể điều l&yacute; thỏa đ&aacute;ng, lấy h&iacute;t thở kh&iacute; huyết vận chuyển r&egrave;n luyện l&agrave;m quan trọng.<br />\r\n<br />\r\nĐ&acirc;y ch&iacute;nh l&agrave; hắn kết hợp Luyện kh&iacute; sĩ cơ sở phương ph&aacute;p r&egrave;n luyện, lại tổng hợp kh&iacute; huyết vận chuyển nguy&ecirc;n l&yacute;, c&ograve;n c&oacute; kh&ocirc;ng &iacute;t cơ sở thuật thổ nạp, hao tốn kh&ocirc;ng sai biệt lắm thời gian một năm suy nghĩ ra được phương ph&aacute;p r&egrave;n luyện.<br />\r\n<br />\r\nH&iacute;t thở thổ nạp kết hợp b&igrave;nh thường động t&aacute;c, kh&ocirc;ng phải l&agrave;m người kh&aacute;c ch&uacute; &yacute; lại l&agrave; c&oacute; thể từ b&ecirc;n trong ra ngo&agrave;i tr&aacute;ng kiện thể ph&aacute;ch, tương đối th&iacute;ch hợp hắn ở Trấn Bắc C&ocirc;ng Phủ ho&agrave;n cảnh.<br />\r\n<br />\r\nChẳng qua l&agrave; một mực kh&ocirc;ng d&aacute;m thử, lỡ như nếu l&agrave; g&acirc;y ra rủi ro cũng kh&ocirc;ng phải n&oacute;i giỡn.<br />\r\n<br />\r\nTrước mắt t&igrave;nh huống kh&aacute;c biệt, ở Nhất Đẳng Tương Qu&acirc;n Phủ cũng kh&ocirc;ng tốt l&agrave;m được qu&aacute; mức đột ngột, lại l&agrave; c&oacute; thể thử một lần.<br />\r\n<br />\r\nCoi như xong thật xảy ra vấn đề, gh&ecirc; gớm về tới Trấn Bắc C&ocirc;ng Phủ chỗ thế giới, c&oacute; đường lui dưới t&igrave;nh huống tự nhi&ecirc;n c&oacute; dũng kh&iacute; thử.<br />\r\n<br />\r\nĐương nhi&ecirc;n, tốt nhất c&oacute; thể nhất cử th&agrave;nh c&ocirc;ng, đang kh&ocirc;ng c&oacute; g&oacute;p nhặt đầy đủ xuy&ecirc;n qua kh&iacute; vận ph&iacute;a trước, cưỡng &eacute;p quay trở về cũng kh&ocirc;ng biết sẽ ph&aacute;t sinh c&aacute;i g&igrave;.<br />\r\n<br />\r\nVề phần h&ocirc;m nay tới cửa L&acirc;m Đại Ngọc, hắn một điểm &yacute; nghĩ cũng kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nL&atilde;o th&aacute;i th&aacute;i chỗ Vinh Kh&aacute;nh Đường l&agrave; Vinh Quốc Phủ ch&acirc;n ch&iacute;nh hạch t&acirc;m, Giả T&ocirc;ng l&uacute;c n&agrave;y ngay cả v&agrave;o cửa tư c&aacute;ch cũng kh&ocirc;ng c&oacute;, coi như xong muốn đi nh&igrave;n c&aacute;i n&aacute;o nhiệt cũng kh&ocirc;ng được a.</p>\r\n\r\n<p><br />\r\nL&acirc;m Đại Ngọc lần đầu ti&ecirc;n tới cửa, tuyệt đối l&agrave; bị xem như kh&aacute;ch qu&yacute; chi&ecirc;u đ&atilde;i.<br />\r\n<br />\r\nĐừng xem Vương phu nh&acirc;n v&agrave; Vương Hi Phượng l&agrave;m m&ocirc;̣t chút mờ &aacute;m, nhưng cũng kh&oacute; thực hiện qu&aacute; mức.<br />\r\n<br />\r\nL&uacute;c n&agrave;y L&acirc;m Như Hải thế nhưng l&agrave; th&acirc;n cư yếu chức, c&oacute; tiền c&oacute; quyền lại l&agrave; thượng ho&agrave;ng t&acirc;m ph&uacute;c, thỏa thỏa quan trường tiềm lực, đồ đần mới c&oacute; thể tại l&uacute;c n&agrave;y tận lực chĩa mũi nhọn v&agrave;o đ&iacute;ch nữ.<br />\r\n<br />\r\nCũng Giả T&ocirc;ng tự th&acirc;n t&igrave;nh h&igrave;nh, kh&ocirc;ng phải rất l&agrave; kh&eacute;o a.<br />\r\n<br />\r\nTrong k&yacute; ức đơn giản, ăn mặc v&agrave;o đều b&igrave;nh thường, trong tay c&agrave;ng kh&ocirc;ng tiền bạc g&igrave;, theo n&oacute;i hắn coi như xong chẳng qua l&agrave; kh&ocirc;ng c&oacute; g&igrave; địa vị con thứ, cũng kh&ocirc;ng trở th&agrave;nh th&ecirc; thảm như thế đi.<br />\r\n<br />\r\nTừ đ&oacute;, thật ra th&igrave; rất dễ d&agrave;ng nh&igrave;n thấu Vinh Quốc Phủ vấn đề.<br />\r\n<br />\r\nNh&igrave;n thức ăn xuống đĩa, n&acirc;ng cao đạp thấp, nịnh nọt mặc d&ugrave; c&aacute;i n&agrave;o đều c&oacute;, lại đều kh&ocirc;ng c&oacute; Vinh Quốc Phủ hậu trạch như vậy đỏ tự nhi&ecirc;n.<br />\r\n<br />\r\nHồng l&acirc;u thức ăn ngon thế nhưng l&agrave; tương đối nổi danh, nhưng c&ugrave;ng Giả T&ocirc;ng ho&agrave;n to&agrave;n v&ocirc; duy&ecirc;n.<br />\r\n<br />\r\nTrong tr&iacute; nhớ, hắn ăn tr&ecirc;n cơ bản đều l&agrave; cơm tập thể, bởi v&igrave; tuổi nhỏ nguy&ecirc;n nh&acirc;n, mỗi bữa ăn một &iacute;t ch&eacute;n đậu hũ, một &iacute;t ch&eacute;n rau xanh, c&ograve;n c&oacute; một &iacute;t ch&eacute;n trứng hấp, lại th&ecirc;m một &iacute;t ch&eacute;n cơm, nhiều khi đều ăn kh&ocirc;ng hết l&atilde;ng ph&iacute;.<br />\r\n<br />\r\nĐổi lại trước mắt Giả T&ocirc;ng, ch&uacute;t n&agrave;y tử ph&acirc;n lượng chỉ đủ nh&eacute;t kẽ răng, mấu chốt chất b&eacute;o kh&ocirc;ng đủ dinh dưỡng kh&ocirc;ng đủ đều đều, hương vị cũng l&agrave; b&igrave;nh thường c&ugrave;ng thức ăn ngon kh&ocirc;ng c&oacute; bất kỳ quan hệ n&agrave;o.<br />\r\n<br />\r\nC&oacute; thể mẹ n&oacute;, muốn th&ecirc;m đồ ăn được lấy tiền hiếu k&iacute;nh đầu bếp nữ, phần của hắn lệ đều bị nhũ mẫu L&yacute; thị thấy gắt gao, căn bản kh&ocirc;ng c&oacute; khả năng hao ph&iacute; ở cải thiện cơm nước chất lượng.<br />\r\n<br />\r\nTản bộ một v&ograve;ng, hiển nhi&ecirc;n hắn suy nghĩ ra được h&iacute;t thở vận chuyển kh&iacute; huyết phương ph&aacute;p hiệu quả kh&ocirc;ng tệ, trong bụng b&aacute;nh ngọt đ&atilde; ti&ecirc;u h&oacute;a được kh&ocirc;ng sai biệt lắm, lại c&oacute; ăn đỡ đ&oacute;i cảm gi&aacute;c đ&oacute;i bụng.<br />\r\n<br />\r\nMẹ n&oacute;...<br />\r\n<br />\r\nDừng bước, nh&igrave;n về ph&iacute;a ch&iacute;nh viện tinh sảo kh&eacute;o l&eacute;o, &aacute;nh mắt lập tức trở n&ecirc;n ki&ecirc;n định: Xem ra, sau đ&oacute; được mặt dạn m&agrave;y d&agrave;y nhiều tới nơi n&agrave;y ăn nhờ ở đậu...<br />\r\n<br />\r\nTruyện main b&aacute;, hậu cung, c&oacute; ch&uacute;t yy, diễn tả pk r&otilde; r&agrave;ng, văn phong kh&aacute; ổn, đ&atilde; ho&agrave;n th&agrave;nh</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:679px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'dem-an-nho-o-dau-tien-hanh-toi-cung', 6, '72', '2022-08-03 04:01:27', '2022-08-03 04:01:27');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(30, '5', 'Quyền thế bình thường lợi dụng', '<p>H&igrave;nh phu nh&acirc;n ở ch&iacute;nh ph&ograve;ng<br />\r\n<br />\r\n&quot;B&aacute;i kiến L&acirc;m tỷ tỷ!&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng cười h&igrave; h&igrave; hướng L&acirc;m Đại Ngọc thi lễ, l&agrave;m cho tiểu c&ocirc; nương thật kh&ocirc;ng tốt &yacute; tứ.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; nh&agrave; Đại cữu cữu ngươi l&atilde;o tam, t&ecirc;n gọi Giả T&ocirc;ng, l&agrave; một khỉ con kh&ocirc;ng an phận, c&oacute; rảnh rỗi thời điểm kh&ocirc;ng ngại gọi l&ecirc;n chơi đ&ugrave;a!&quot;<br />\r\n<br />\r\nL&acirc;m Đại Ngọc vị kh&aacute;ch qu&yacute; n&agrave;y ở trước mặt, H&igrave;nh phu nh&acirc;n thần th&aacute;i tương đương hiền l&agrave;nh, đến kh&ocirc;ng c&oacute; để cho Giả T&ocirc;ng kh&ocirc;ng mặt mũi, chỉ l&agrave; giới thiệu thời điểm chưa qu&ecirc;n tổn thất c&acirc;u.<br />\r\n<br />\r\n&quot;T&ocirc;ng huynh đệ thật l&agrave; tinh thần!&quot;<br />\r\n<br />\r\nChe miệng cười khẽ, L&acirc;m Đại Ngọc t&acirc;m thần r&otilde; r&agrave;ng bu&ocirc;ng lỏng, giữa l&ocirc;ng m&agrave;y thiếu đi mấy phần ưu sầu, c&oacute; lẽ l&agrave; gặp được người đồng lứa phản ứng tự nhi&ecirc;n, lại hoặc l&agrave; nhận lấy Giả T&ocirc;ng cười đ&ugrave;a t&iacute; tửng kh&ocirc;ng đứng đắn l&acirc;y nhiễm.<br />\r\n<br />\r\n&quot;N&agrave;y, ta đ&acirc;y l&agrave; vừa rồi ăn no ở trong hoa vi&ecirc;n tản bộ một v&ograve;ng, tinh thần năng kh&ocirc;ng phải phấn chấn hay sao?&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng cười h&igrave; h&igrave; n&oacute;i: &quot;Cũng L&acirc;m tỷ tỷ tinh thần đầu kh&ocirc;ng tốt lắm a, c&oacute; phải hay kh&ocirc;ng đường đi mệt nhọc thể x&aacute;c tinh thần mệt mỏi?&quot;<br />\r\n<br />\r\nN&oacute;i giỡn, x&atilde; hội hiện đại đi m&aacute;y bay thừa đường sắt cao tốc v&atilde;ng lai Dương Ch&acirc;u c&ugrave;ng kinh th&agrave;nh, người trưởng th&agrave;nh cũng sẽ c&oacute; mệt mỏi cảm gi&aacute;c, huống chi đi thuyền hơn một th&aacute;ng lặn lội đường xa L&acirc;m Đại Ngọc?<br />\r\n<br />\r\nTuổi c&ograve;n nhỏ kh&ocirc;ng c&oacute; ng&atilde; bệnh, cũng đ&atilde; xem như th&acirc;n thể để tử kh&ocirc;ng tệ.<br />\r\n<br />\r\nHồng l&acirc;u kh&uacute;c dạo đầu đối với mi&ecirc;u tả mảnh mai th&aacute;i độ, đơn giản kh&ocirc;ng giải th&iacute;ch được.<br />\r\n<br />\r\n&quot;Ai nha, nh&igrave;n ta đều qu&ecirc;n Đại Ngọc đường đi mệt nhọc!&quot;<br />\r\n<br />\r\nH&igrave;nh phu nh&acirc;n kịp phản ứng, qu&eacute;t L&acirc;m Đại Ngọc một c&aacute;i x&aacute;c thực như vậy, vội v&agrave;ng h&ocirc;: &quot;Đi hỏi một ch&uacute;t l&atilde;o gia l&uacute;c n&agrave;o c&oacute; rảnh rỗi!&quot;<br />\r\n<br />\r\nKết quả Giả X&aacute; muốn c&ugrave;ng tiểu l&atilde;o b&agrave; uống rượu đ&ugrave;a nghịch vui vẻ, căn bản l&agrave; kh&ocirc;ng c&oacute; thời gian rỗi để &yacute; tới L&acirc;m Đại Ngọc c&ocirc; ch&aacute;u ngoại n&agrave;y, đương nhi&ecirc;n đ&aacute;p lời đại nha ho&agrave;n kh&ocirc;ng c&oacute; vờ ngớ ngẩn n&oacute;i được ngay thẳng như vậy.<br />\r\n<br />\r\nH&igrave;nh phu nh&acirc;n sắc mặt c&oacute; phần kh&oacute; coi, lại cũng chỉ c&oacute; thể gi&uacute;p đỡ ho&agrave; giải, đem L&acirc;m Đại Ngọc đưa đi nhị ph&ograve;ng b&ecirc;n kia.</p>', '<p>&nbsp;</p>\r\n\r\n<p>H&igrave;nh phu nh&acirc;n ở ch&iacute;nh ph&ograve;ng<br />\r\n<br />\r\n&quot;B&aacute;i kiến L&acirc;m tỷ tỷ!&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng cười h&igrave; h&igrave; hướng L&acirc;m Đại Ngọc thi lễ, l&agrave;m cho tiểu c&ocirc; nương thật kh&ocirc;ng tốt &yacute; tứ.<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; nh&agrave; Đại cữu cữu ngươi l&atilde;o tam, t&ecirc;n gọi Giả T&ocirc;ng, l&agrave; một khỉ con kh&ocirc;ng an phận, c&oacute; rảnh rỗi thời điểm kh&ocirc;ng ngại gọi l&ecirc;n chơi đ&ugrave;a!&quot;<br />\r\n<br />\r\nL&acirc;m Đại Ngọc vị kh&aacute;ch qu&yacute; n&agrave;y ở trước mặt, H&igrave;nh phu nh&acirc;n thần th&aacute;i tương đương hiền l&agrave;nh, đến kh&ocirc;ng c&oacute; để cho Giả T&ocirc;ng kh&ocirc;ng mặt mũi, chỉ l&agrave; giới thiệu thời điểm chưa qu&ecirc;n tổn thất c&acirc;u.<br />\r\n<br />\r\n&quot;T&ocirc;ng huynh đệ thật l&agrave; tinh thần!&quot;<br />\r\n<br />\r\nChe miệng cười khẽ, L&acirc;m Đại Ngọc t&acirc;m thần r&otilde; r&agrave;ng bu&ocirc;ng lỏng, giữa l&ocirc;ng m&agrave;y thiếu đi mấy phần ưu sầu, c&oacute; lẽ l&agrave; gặp được người đồng lứa phản ứng tự nhi&ecirc;n, lại hoặc l&agrave; nhận lấy Giả T&ocirc;ng cười đ&ugrave;a t&iacute; tửng kh&ocirc;ng đứng đắn l&acirc;y nhiễm.<br />\r\n<br />\r\n&quot;N&agrave;y, ta đ&acirc;y l&agrave; vừa rồi ăn no ở trong hoa vi&ecirc;n tản bộ một v&ograve;ng, tinh thần năng kh&ocirc;ng phải phấn chấn hay sao?&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng cười h&igrave; h&igrave; n&oacute;i: &quot;Cũng L&acirc;m tỷ tỷ tinh thần đầu kh&ocirc;ng tốt lắm a, c&oacute; phải hay kh&ocirc;ng đường đi mệt nhọc thể x&aacute;c tinh thần mệt mỏi?&quot;<br />\r\n<br />\r\nN&oacute;i giỡn, x&atilde; hội hiện đại đi m&aacute;y bay thừa đường sắt cao tốc v&atilde;ng lai Dương Ch&acirc;u c&ugrave;ng kinh th&agrave;nh, người trưởng th&agrave;nh cũng sẽ c&oacute; mệt mỏi cảm gi&aacute;c, huống chi đi thuyền hơn một th&aacute;ng lặn lội đường xa L&acirc;m Đại Ngọc?<br />\r\n<br />\r\nTuổi c&ograve;n nhỏ kh&ocirc;ng c&oacute; ng&atilde; bệnh, cũng đ&atilde; xem như th&acirc;n thể để tử kh&ocirc;ng tệ.<br />\r\n<br />\r\nHồng l&acirc;u kh&uacute;c dạo đầu đối với mi&ecirc;u tả mảnh mai th&aacute;i độ, đơn giản kh&ocirc;ng giải th&iacute;ch được.<br />\r\n<br />\r\n&quot;Ai nha, nh&igrave;n ta đều qu&ecirc;n Đại Ngọc đường đi mệt nhọc!&quot;<br />\r\n<br />\r\nH&igrave;nh phu nh&acirc;n kịp phản ứng, qu&eacute;t L&acirc;m Đại Ngọc một c&aacute;i x&aacute;c thực như vậy, vội v&agrave;ng h&ocirc;: &quot;Đi hỏi một ch&uacute;t l&atilde;o gia l&uacute;c n&agrave;o c&oacute; rảnh rỗi!&quot;<br />\r\n<br />\r\nKết quả Giả X&aacute; muốn c&ugrave;ng tiểu l&atilde;o b&agrave; uống rượu đ&ugrave;a nghịch vui vẻ, căn bản l&agrave; kh&ocirc;ng c&oacute; thời gian rỗi để &yacute; tới L&acirc;m Đại Ngọc c&ocirc; ch&aacute;u ngoại n&agrave;y, đương nhi&ecirc;n đ&aacute;p lời đại nha ho&agrave;n kh&ocirc;ng c&oacute; vờ ngớ ngẩn n&oacute;i được ngay thẳng như vậy.<br />\r\n<br />\r\nH&igrave;nh phu nh&acirc;n sắc mặt c&oacute; phần kh&oacute; coi, lại cũng chỉ c&oacute; thể gi&uacute;p đỡ ho&agrave; giải, đem L&acirc;m Đại Ngọc đưa đi nhị ph&ograve;ng b&ecirc;n kia.</p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Ừm, ngươi cái t&ecirc;n này kh&ocirc;ng trả lại được ỷ lại c&aacute;i n&agrave;y l&agrave;m c&aacute;i g&igrave;, đ&ograve;i đ&aacute;nh hay sao?&quot;<br />\r\n<br />\r\nGặp lại sau Giả T&ocirc;ng c&ograve;n dựa v&agrave;o kh&ocirc;ng đi, lập tức biến sắc, phất tay hừ lạnh n&oacute;i: &quot;Ngươi nhũ mẫu, chết ở đ&acirc;u?&quot;<br />\r\n<br />\r\n&quot;Th&aacute;i th&aacute;i, L&acirc;m tỷ tỷ nh&agrave; thế nhưng l&agrave; T&ocirc; Ch&acirc;u đại tộc, phụ th&acirc;n c&agrave;ng Dương Ch&acirc;u Tuần Di&ecirc;m Ngự Sử, ngươi cũng kh&ocirc;ng c&oacute; &yacute; nghĩ g&igrave; hay sao?&quot;<br />\r\n<br />\r\nĐối với H&igrave;nh phu nh&acirc;n th&aacute;i độ &aacute;c liệt v&agrave; giọng n&oacute;i, Giả T&ocirc;ng ho&agrave;n to&agrave;n kh&ocirc;ng th&egrave;m để &yacute;, liền xem như gi&oacute; b&ecirc;n tai, tiến l&ecirc;n mấy bước cười h&igrave; h&igrave; n&oacute;i: &quot;T&ugrave;y tiện bu&ocirc;n b&aacute;n mấy lần, đều l&agrave; bạc trắng b&oacute;ng a!&quot;<br />\r\n<br />\r\nSau đ&oacute; c&ograve;n phải ở H&igrave;nh phu nh&acirc;n nơi n&agrave;y ăn nhờ ở đậu, kh&ocirc;ng n&oacute;i trước đả hảo chi&ecirc;u h&ocirc; thế n&agrave;o th&agrave;nh?<br />\r\n<br />\r\nNếu biết được H&igrave;nh phu nh&acirc;n tham tiền hẹp h&ograve;i, vậy chĩa mũi nhọn v&agrave;o ch&uacute;t n&agrave;y l&agrave;m bố tr&iacute; th&ocirc;i, của người ph&uacute;c ta chuyện, l&agrave;m một điểm g&aacute;nh nặng trong l&ograve;ng cũng kh&ocirc;ng c&oacute; a.<br />\r\n<br />\r\n&quot;Ừm, n&oacute;i như thế n&agrave;o?&quot;<br />\r\n<br />\r\n&#39;Bạc trắng b&oacute;ng&#39; mấy chữ, lập tức dẫn động H&igrave;nh phu nh&acirc;n thần kinh nhạy cảm, kịp phản ứng hai mắt lấp l&aacute;nh, nh&igrave;n chằm chằm Giả T&ocirc;ng giọng n&oacute;i đều &ocirc;n h&ograve;a kh&ocirc;ng &iacute;t.<br />\r\n<br />\r\n&quot;Rất đơn giản!&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng b&igrave;nh ch&acirc;n như vại, thản nhi&ecirc;n n&oacute;i: &quot;Th&ocirc;ng qua L&acirc;m tỷ tỷ c&ugrave;ng L&acirc;m dượng c&oacute; li&ecirc;n lạc, th&aacute;i th&aacute;i ở Kim Lăng cần phải c&ograve;n c&oacute; th&acirc;n nh&acirc;n đi, t&igrave;m y&ecirc;n t&acirc;m hỗ trợ đưa tin lại đ&ograve;i mấy phần muối dẫn cũng kh&ocirc;ng phải vấn đề, chuyển tay ch&iacute;nh l&agrave; bạc trắng b&oacute;ng a!&quot;<br />\r\n<br />\r\nVề phần l&agrave;m như vậy phạm ph&aacute;p kh&ocirc;ng phải phạm ph&aacute;p, hắn căn bản l&agrave; kh&ocirc;ng c&oacute; để &yacute;.<br />\r\n<br />\r\nL&acirc;m Như Hải ổn thỏa Dương Ch&acirc;u Tuần Di&ecirc;m Ngự Sử nhiều năm, nếu ch&uacute;t chuyện nhỏ như vậy đều kh&ocirc;ng l&agrave;m được đến, sớm đ&atilde; bị người ăn đến xương vụn đều kh&ocirc;ng thừa.<br />\r\n<br />\r\nMấy phần muối dẫn lại c&oacute; thể đổi bao nhi&ecirc;u bạc?<br />\r\n<br />\r\nChẳng qua l&agrave; một tới tiền tiền thu th&ocirc;i, H&igrave;nh gia người nếu thật l&agrave; c&oacute; thể bằng n&agrave;y x&acirc;m nhập thương nh&acirc;n bu&ocirc;n muối h&agrave;ng ngũ, Giả T&ocirc;ng kh&ocirc;ng n&oacute;i được cao hơn nh&igrave;n bọn họ một c&aacute;i.<br />\r\n<br />\r\n&quot;Thật c&oacute; thể l&agrave;m như thế?&quot;</p>\r\n\r\n<p><br />\r\n<br />\r\nH&igrave;nh phu nh&acirc;n c&oacute; ch&uacute;t động t&acirc;m, lại c&oacute; ch&uacute;t chần chờ, thấp thỏm n&oacute;i: &quot;Sẽ kh&ocirc;ng ra vấn đề g&igrave; a?&quot;<br />\r\n<br />\r\nD&ugrave; sao xuất th&acirc;n Kim Lăng, đối với Dương Ch&acirc;u thương nh&acirc;n bu&ocirc;n muối nhiều chuyện c&oacute; nghe thấy.<br />\r\n<br />\r\n&quot;Chỉ cần kh&ocirc;ng tham lam, ch&iacute;nh l&agrave; v&igrave; L&acirc;m tỷ tỷ c&oacute; thể ở ch&uacute;ng ta phủ tr&ocirc;i qua c&agrave;ng thư th&aacute;i, L&acirc;m dượng cũng sẽ kh&ocirc;ng cự tuyệt, sẽ c&ograve;n gi&uacute;p đỡ an b&agrave;i thỏa đ&aacute;ng!&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng cười h&igrave; h&igrave; n&oacute;i: &quot;Đương nhi&ecirc;n, điều kiện ti&ecirc;n quyết l&agrave; th&aacute;i th&aacute;i c&oacute; thể thuyết phục L&acirc;m tỷ tỷ hỗ trợ, kh&ocirc;ng chừng nhị ph&ograve;ng b&ecirc;n kia cũng sẽ c&oacute; &yacute; nghĩ như vậy!&quot;<br />\r\n<br />\r\n&quot;Hừ, sẽ kh&ocirc;ng k&ecirc;u bọn họ được như &yacute; !&quot;<br />\r\n<br />\r\nNhấc l&ecirc;n nhị ph&ograve;ng,<br />\r\n<br />\r\nH&igrave;nh phu nh&acirc;n lập tức tinh thần phấn chấn chiến &yacute; ngang dương, c&aacute;i g&igrave; lo lắng đều vứt qua một b&ecirc;n, c&ograve;n c&oacute; bạc trắng b&oacute;ng hấp dẫn, lập tức l&agrave;m ra quyết định.<br />\r\n<br />\r\n&quot;Th&aacute;i th&aacute;i anh minh!&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng cười đ&ugrave;a t&iacute; tửng che giấu lương t&acirc;m khen c&acirc;u, ngắm nh&igrave;n b&ecirc;n ngo&agrave;i ng&atilde; về t&acirc;y &aacute;nh nắng, hướng về ph&iacute;a b&ecirc;n cạnh đại nha ho&agrave;n n&oacute;i: &quot;C&aacute;i n&agrave;y đến l&uacute;c n&agrave;o, thế n&agrave;o c&ograve;n kh&ocirc;ng l&ecirc;n bữa tối?&quot;<br />\r\n<br />\r\nThấy đại nha ho&agrave;n xem ra, H&igrave;nh phu nh&acirc;n l&uacute;c n&agrave;y t&acirc;m t&igrave;nh thật tốt, cũng kh&ocirc;ng c&oacute; so đo c&aacute;i g&igrave;, phất phất tay n&oacute;i: &quot;Nếu t&ocirc;ng m&agrave; đ&oacute;i bụng, vậy đi đầu bếp ph&ograve;ng bưng tới bữa tối đi!&quot;<br />\r\n<br />\r\nN&oacute;i xong, rất th&acirc;n mật vỗ vỗ Giả T&ocirc;ng nhỏ bả vai, mang theo Vương Thiện Bảo Gia, v&agrave; đại nha ho&agrave;n rời khỏi: &quot;Ta đi Vinh Kh&aacute;nh Đường b&ecirc;n kia, t&ocirc;ng m&agrave; m&igrave;nh trước d&ugrave;ng đi!&quot;<br />\r\n<br />\r\nC&aacute;c loại ra Nhất Đẳng Tương Qu&acirc;n Phủ chỗ s&acirc;n nhỏ, l&ecirc;n xe lu&ocirc;n cảm gi&aacute;c c&oacute; ch&uacute;t kh&ocirc;ng ổn, h&igrave;nh như bị dao động.<br />\r\n<br />\r\n&quot;Vương Thiện Bảo Gia, ngươi cho rằng c&aacute;i kia hồn tiểu tử n&oacute;i, c&oacute; khả năng hay kh&ocirc;ng l&agrave;m được?&quot;<br />\r\n<br />\r\nGiọng n&oacute;i c&oacute; ch&uacute;t do dự, sợ một phen chuyện tốt chẳng qua l&agrave; hư ảo.<br />\r\n<br />\r\n&quot;Th&aacute;i th&aacute;i, ta n&agrave;y cũng n&oacute;i kh&ocirc;ng ch&iacute;nh x&aacute;c!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<br />\r\nVương Thiện Bảo Gia cũng hơi muộn mất nghi, do dự n&oacute;i: &quot;Chẳng qua Tam gia n&oacute;i tới cũng kh&ocirc;ng phải kh&ocirc;ng c&oacute; c&oacute; l&yacute;, d&ugrave; sao cũng phải thử một ch&uacute;t xem sao, n&oacute;i như thế n&agrave;o cũng l&agrave; đầu t&agrave;i lộ!&quot;<br />\r\n<br />\r\n&quot;Ừm, cũng kh&ocirc;ng biết c&aacute;i kia hồn tiểu tử l&agrave;m sao biết những n&agrave;y !&quot;<br />\r\n<br />\r\nTrong l&ograve;ng H&igrave;nh phu nh&acirc;n c&oacute; ch&uacute;t an ủi, giống như l&agrave; tựa như nhớ tới c&aacute;i g&igrave;, trực tiếp ph&acirc;n ph&oacute; n&oacute;i: &quot;Ngươi trở về, hỏi một ch&uacute;t tiểu tử kia n&agrave;o biết được những n&agrave;y, hỏi r&otilde; r&agrave;ng!&quot;<br />\r\n<br />\r\nVương Thiện Bảo Gia ứng tiếng, đ&agrave;nh phải nửa đường chuyển Hồi tướng qu&acirc;n phủ chỗ s&acirc;n nhỏ, vừa hay nh&igrave;n thấy Giả T&ocirc;ng ăn như gió cu&ocirc;́n ăn đến miệng đầy b&oacute;ng lo&aacute;ng, kh&ocirc;ng lo được trong l&ograve;ng cổ qu&aacute;i vội v&agrave;ng hỏi l&ecirc;n, trong l&ograve;ng n&agrave;ng cũng x&aacute;c thực phạm v&agrave;o n&oacute;i thầm.<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;y c&oacute; c&aacute;i g&igrave; kỳ qu&aacute;i đ&acirc;u?&quot;<br />\r\n<br />\r\nTiện tay đem gặm được trụi lủi đ&ugrave;i g&agrave; xương n&eacute;m đi tr&ecirc;n b&agrave;n, lại kẹp khối nhỏ gi&ograve;n thịt lấp trong miệng, Giả T&ocirc;ng lơ đễnh n&oacute;i h&agrave;m hồ kh&ocirc;ng r&otilde;: &quot;Trong phủ c&aacute;c quản sự cũng kh&ocirc;ng c&oacute; &iacute;t tại b&ecirc;n ngo&agrave;i, mượn Vinh Quốc Phủ danh tiếng m&ograve; bạc, ta n&oacute;i bất qu&aacute; chỉ l&agrave; bắt chước th&ocirc;i, lại l&agrave; mượn L&acirc;m dượng quyền thế, c&oacute; g&igrave; gh&ecirc; gớm đ&acirc;u?&quot;<br />\r\n<br />\r\nVương Thiện Bảo Gia kh&ocirc;ng n&oacute;i, Giả T&ocirc;ng l&yacute; do n&agrave;y x&aacute;c thực đầy đủ, trong phủ c&aacute;i nh&oacute;m n&agrave;y thực quyền quản sự tại b&ecirc;n ngo&agrave;i dạng g&igrave;, n&agrave;ng thế nhưng l&agrave; hết sức r&otilde; r&agrave;ng, trong l&ograve;ng đ&atilde; sớm c&aacute;c loại ước ao ghen tị, dưới mắt c&oacute; cơ hội học thượng một thanh cũng l&agrave; kh&aacute; cao hứng.<br />\r\n<br />\r\nC&aacute;c loại n&agrave;ng từ mỹ hảo mơ m&agrave;ng b&ecirc;n trong hồi thần, muốn hỏi nữa vừa hỏi cụ thể chi tiết, Giả T&ocirc;ng đ&atilde; sớm ăn no đường chạy, chỉ để lại một b&agrave;n bừa bộn.<br />\r\n<br />\r\nThả c&acirc;y đuốc, Giả T&ocirc;ng mới sẽ kh&ocirc;ng đem tất cả &yacute; nghĩ đều m&oacute;c ra, H&igrave;nh phu nh&acirc;n c&aacute;i g&igrave; t&iacute;nh t&igrave;nh nhưng hắn l&agrave; tương đối r&otilde; r&agrave;ng.<br />\r\n<br />\r\nMột khi kh&ocirc;ng c&oacute; gi&aacute; trị lợi dụng, liền đợi đến bị qua s&ocirc;ng đoạn cầu đi.<br />\r\n<br />\r\nRa H&igrave;nh phu nh&acirc;n chỗ ch&iacute;nh viện, chủ động chạy tới tiện nghi l&atilde;o cha Giả X&aacute; c&aacute;i kia thỉnh an, kết quả liền cửa cũng kh&ocirc;ng v&agrave;o đến liền bị đuổi đi. Hắn cũng l&agrave; lơ đễnh, chỉ cần kh&ocirc;ng gọi người bắt lấy lễ nghi b&ecirc;n tr&ecirc;n nhược điểm đ&atilde; tốt. Hắn cũng kh&ocirc;ng muốn bị người cầm hiếu đạo n&oacute;i chuyện, tiện nghi l&atilde;o tử ch&iacute;nh l&agrave; lớn nhất mặt tr&aacute;i t&agrave;i liệu giảng dạy.<br />\r\n<br />\r\nNguy&ecirc;n chủ trong k&yacute; ức đơn giản, thế nhưng l&agrave; c&oacute; thật nhiều li&ecirc;n quan tới hiếu đạo nội dung, Vinh Quốc Phủ ở phương diện n&agrave;y gi&aacute;o dục vẫn l&agrave; tương đối coi trọng.<br />\r\n<br />\r\nTr&ecirc;n đường trở về, lại mở ra h&iacute;t thở vận chuyển kh&iacute; huyết phương ph&aacute;p, gia tốc ti&ecirc;u h&oacute;a tr&aacute;ng kiện thể ph&aacute;ch...<br />\r\n<br />\r\nTruyện main b&aacute;, hậu cung, c&oacute; ch&uacute;t yy, diễn tả pk r&otilde; r&agrave;ng, văn phong kh&aacute; ổn, đ&atilde; ho&agrave;n th&agrave;nh</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:132.688px; top:24px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'quyen-the-binh-thuong-loi-dung', 6, '7582', '2022-08-03 04:02:24', '2022-08-03 04:02:24'),
(31, '6', 'Tiểu Hà Mới Lộ Sừng Nhọn Nhọn', '<p>Đảo mắt nửa tháng tr&ocirc;i qua...<br />\r\n<br />\r\nNhất Đẳng Tương Qu&acirc;n Phủ bọn hạ nh&acirc;n, từ từ quen thuộc đột nhi&ecirc;n hoạt dược Tam thiếu gia Giả T&ocirc;ng.<br />\r\n<br />\r\nNhất l&agrave; ch&iacute;nh viện nha ho&agrave;n v&agrave; g&atilde; sai vặt, trong mỗi ng&agrave;y thấy được Tam thiếu gia Giả T&ocirc;ng b&oacute;ng người, từ vừa mới bắt đầu kinh ngạc, chẳng qua ngắn ngủi thời gian nửa th&aacute;ng đ&atilde; hết sức quen thuộc.<br />\r\n<br />\r\nLiền vọt l&ecirc;n th&aacute;i th&aacute;i đối với n&oacute;ng hổi sức lực, cả Nhất Đẳng Tương Qu&acirc;n Phủ nha ho&agrave;n g&atilde; sai vặt v&agrave; b&agrave; tử nh&oacute;m, gặp mặt sau nguyện &yacute; ch&agrave;o hỏi cho khu&ocirc;n mặt tươi cười.<br />\r\n<br />\r\nKh&ocirc;ng dễ d&agrave;ng a...<br />\r\n<br />\r\nThật ra th&igrave;, biến h&oacute;a lớn nhất vẫn l&agrave; th&acirc;n thể Giả T&ocirc;ng, chẳng qua l&agrave; mỗi ng&agrave;y gặp mặt, phủ tướng qu&acirc;n bọn hạ nh&acirc;n, c&ograve;n c&oacute; th&aacute;i th&aacute;i h&igrave;nh thị, kh&ocirc;ng c&oacute; ph&aacute;t hiện th&ocirc;i.<br />\r\n&nbsp;</p>', '<p>Đảo mắt nửa tháng tr&ocirc;i qua...<br />\r\n<br />\r\nNhất Đẳng Tương Qu&acirc;n Phủ bọn hạ nh&acirc;n, từ từ quen thuộc đột nhi&ecirc;n hoạt dược Tam thiếu gia Giả T&ocirc;ng.<br />\r\n<br />\r\nNhất l&agrave; ch&iacute;nh viện nha ho&agrave;n v&agrave; g&atilde; sai vặt, trong mỗi ng&agrave;y thấy được Tam thiếu gia Giả T&ocirc;ng b&oacute;ng người, từ vừa mới bắt đầu kinh ngạc, chẳng qua ngắn ngủi thời gian nửa th&aacute;ng đ&atilde; hết sức quen thuộc.<br />\r\n<br />\r\nLiền vọt l&ecirc;n th&aacute;i th&aacute;i đối với n&oacute;ng hổi sức lực, cả Nhất Đẳng Tương Qu&acirc;n Phủ nha ho&agrave;n g&atilde; sai vặt v&agrave; b&agrave; tử nh&oacute;m, gặp mặt sau nguyện &yacute; ch&agrave;o hỏi cho khu&ocirc;n mặt tươi cười.<br />\r\n<br />\r\nKh&ocirc;ng dễ d&agrave;ng a...<br />\r\n<br />\r\nThật ra th&igrave;, biến h&oacute;a lớn nhất vẫn l&agrave; th&acirc;n thể Giả T&ocirc;ng, chẳng qua l&agrave; mỗi ng&agrave;y gặp mặt, phủ tướng qu&acirc;n bọn hạ nh&acirc;n, c&ograve;n c&oacute; th&aacute;i th&aacute;i h&igrave;nh thị, kh&ocirc;ng c&oacute; ph&aacute;t hiện th&ocirc;i.<br />\r\n<br />\r\nB&igrave;nh thường Nội Gia Quyền, b&igrave;nh thường đều l&agrave; đ&aacute;nh trước nhịn g&acirc;n cốt rắn chắc thể ph&aacute;ch tr&agrave;n đầy kh&iacute; huyết, lại th&ocirc;ng qua c&aacute;c m&ocirc;n c&aacute;c ph&aacute;i b&iacute; ph&aacute;p vận chuyển kh&iacute; huyết tiến hơn một bước.<br />\r\n<br />\r\nGiả T&ocirc;ng l&agrave;m ph&eacute;p lại l&agrave; phương ph&aacute;p tr&aacute;i ngược, trực tiếp th&ocirc;ng qua phương ph&aacute;p h&ocirc; hấp thổ nạp vận chuyển kh&iacute; huyết, được cho một bước đ&uacute;ng chỗ.<br />\r\n<br />\r\nNhư vậy từ trong ra ngo&agrave;i, r&egrave;n luyện hiệu quả tốt đến lạ thường!<br />\r\n<br />\r\nTăng th&ecirc;m mỗi ng&agrave;y chạy tới ch&iacute;nh viện, mặt dạn m&agrave;y d&agrave;y ăn nhờ ở đậu, chất b&eacute;o đầy đủ dinh dưỡng phong ph&uacute;, ngắn ngủi thời gian nửa th&aacute;ng r&egrave;n luyện, thể cốt đ&atilde; xong kh&ocirc;ng c&ograve;n l&uacute;c mới tới hư nhược.<br />\r\n<br />\r\nMặt ngo&agrave;i nh&igrave;n kh&ocirc;ng ra hư thực, c&oacute; thể tạng phủ, g&acirc;n cốt c&ugrave;ng m&agrave;ng da đều đang giận m&aacute;u vận chuyển trong qu&aacute; tr&igrave;nh đạt được r&egrave;n luyện, thể cốt bắt đầu trở n&ecirc;n cường tr&aacute;ng, ngay cả th&acirc;n cao đều c&oacute; tăng trưởng.<br />\r\n<br />\r\nĐương nhi&ecirc;n, trong &acirc;m thầm hắn cũng thu được &#39;Th&ugrave;ng cơm&#39; phỉ h&agrave;o.<br />\r\n<br />\r\nAi bảo hắn mỗi lần cũng c&oacute; thể l&agrave;m rơi mất H&igrave;nh phu nh&acirc;n phần lệ trong thức ăn một nửa, lấy Vinh Quốc Phủ xa xỉ diễn xuất, c&aacute;i kia ph&acirc;n lượng v&agrave; chất b&eacute;o cũng kh&ocirc;ng phải n&oacute;i giỡn.<br />\r\n<br />\r\nMay mắn Giả T&ocirc;ng tạm thời dỗ t&ocirc;́t lắm H&igrave;nh phu nh&acirc;n, kh&ocirc;ng phải vậy muốn như thế ngh&ecirc;nh ngang ăn nhờ ở đậu, kh&oacute; khăn đem tăng l&ecirc;n tới Địa Ngục cấp bậc.</p>\r\n\r\n<p><br />\r\nĐối với nha ho&agrave;n b&agrave; tử c&ograve;n c&oacute; g&atilde; sai vặt tự m&igrave;nh giễu cợt, hắn căn bản cũng kh&ocirc;ng để &yacute;.<br />\r\n<br />\r\nTương đối tự th&acirc;n tố chất th&acirc;n thể tăng l&ecirc;n, c&ograve;n c&oacute; v&otilde; lực tăng cường, một điểm tử lời đ&agrave;m tiếu t&iacute;nh l&agrave; g&igrave;?<br />\r\n<br />\r\nNhư vậy gi&oacute; &ecirc;m s&oacute;ng lặng thời gian, hắn ước g&igrave; c&agrave;ng l&acirc;u c&agrave;ng tốt.<br />\r\n<br />\r\nL&acirc;m Đại Ngọc từ lần trước b&aacute;i phỏng Đại cữu cữu Giả X&aacute; kh&ocirc;ng th&agrave;nh, về sau lại chưa đến đ&acirc;y Nhất Đẳng Tương Qu&acirc;n Phủ.<br />\r\n<br />\r\nNghe Vương Thiện Bảo Gia mi&ecirc;u tả, tiểu c&ocirc; nương ở Vinh Kh&aacute;nh Đường rất được l&atilde;o th&aacute;i th&aacute;i coi trọng, lại c&oacute; một Vinh Quốc Phủ cục cưng qu&yacute; gi&aacute; Giả Bảo Ngọc v&ocirc; cớ g&acirc;y rối, đang đứng ở hai nhỏ v&ocirc; tư mỹ hảo trạng th&aacute;i, chỗ n&agrave;o nghĩ đến nổi l&ecirc;n đại ph&ograve;ng b&ecirc;n n&agrave;y?<br />\r\n<br />\r\nB&agrave;i trừ c&aacute;i n&agrave;y b&agrave; tử trong lời n&oacute;i chỗ kh&ocirc;ng thật, cơ bản c&oacute; thể x&aacute;c định Hồng L&acirc;u Mộng kh&uacute;c dạo đầu, cũng kh&ocirc;ng v&igrave; hắn đột nhi&ecirc;n đến, c&oacute; bao nhi&ecirc;u thay đổi địa phương.<br />\r\n<br />\r\nCũng thế, Vinh Kh&aacute;nh Đường v&agrave; Vinh Hi Đường mới l&agrave; Vinh Quốc Phủ chỗ hạch t&acirc;m, cả trong phủ t&agrave;i nguy&ecirc;n v&agrave; nh&acirc;n lực đều hướng về ph&iacute;a b&ecirc;n kia nghi&ecirc;ng về, Nhất Đẳng Tương Qu&acirc;n Phủ sớm đ&atilde; bị bi&ecirc;n duyến h&oacute;a.<br />\r\n<br />\r\nB&ecirc;n kia ph&aacute;t sinh sự t&igrave;nh, đối với Nhất Đẳng Tương Qu&acirc;n Phủ ảnh hưởng qu&aacute; mức b&eacute; nhỏ. Đồng dạng đạo l&yacute;, Nhất Đẳng Tương Qu&acirc;n Phủ chuyện nhỏ, đối với Vinh Kh&aacute;nh Đường b&ecirc;n kia cũng kh&ocirc;ng c&oacute; nhiều ảnh hưởng.<br />\r\n<br />\r\nGiả T&ocirc;ng chẳng qua l&agrave; gần nhất đến đem qu&acirc;n phủ ch&iacute;nh viện thường xuy&ecirc;n một ch&uacute;t, vừa kh&ocirc;ng c&oacute; đoạt ban đoạt quyền, l&agrave;m sao lại đưa tới Vinh Kh&aacute;nh Đường b&ecirc;n kia ch&uacute; &yacute;?<br />\r\n<br />\r\nĐương nhi&ecirc;n, kh&ocirc;ng thay đổi được l&agrave; kh&ocirc;ng c&oacute;, &iacute;t nhất H&igrave;nh phu nh&acirc;n gần nhất th&aacute;i độ đối với L&acirc;m Đại Ngọc, c&oacute; thể n&oacute;i tương đương nhiệt t&igrave;nh.<br />\r\n<br />\r\nVương Thiện Bảo Gia đang c&ugrave;ng hắn l&uacute;c n&oacute;i chuyện phiếm, trong l&uacute;c v&ocirc; t&igrave;nh tiết lộ rất nhiều tin tức.<br />\r\n<br />\r\nV&iacute; dụ như, H&igrave;nh phu nh&acirc;n đ&atilde; viết thư cho Kim Lăng huynh đệ, khiến hắn mau sớm v&agrave;o kinh một chuyến. Mặt kh&aacute;c l&agrave; được, L&acirc;m Đại Ngọc tiểu c&ocirc; nương đối với H&igrave;nh phu nh&acirc;n th&acirc;n mật, cũng biểu hiện ra đầy đủ th&acirc;n mật, th&ecirc;m &iacute;t sức mạnh lấy được tiểu c&ocirc; nương thư nh&agrave; cũng kh&ocirc;ng kh&oacute; khăn.<br />\r\n<br />\r\nGiả T&ocirc;ng mặc d&ugrave; kh&ocirc;ng phải rất để &yacute;, nhưng cũng đối với nh&agrave; m&igrave;nh thuận miệng nhấc l&ecirc;n &yacute; nghĩ tiến triển thuận lợi, nhiều một ch&uacute;t tử hứng th&uacute;.<br />\r\n<br />\r\nTrước mắt tự th&acirc;n tốt đẹp t&igrave;nh cảnh, c&ugrave;ng chuyện n&agrave;y thuận lợi hay kh&ocirc;ng c&ugrave;ng một nhịp thở, kh&ocirc;ng n&oacute;i được l&uacute;c mấu chốt, c&ograve;n phải cho H&igrave;nh phu nh&acirc;n ra ch&uacute;t &iacute;t chủ &yacute;.</p>\r\n\r\n<p><br />\r\n<br />\r\nNg&agrave;y h&ocirc;m đ&oacute;, &aacute;nh nắng tươi s&aacute;ng chim h&oacute;t hoa nở.<br />\r\n<br />\r\nGiả T&ocirc;ng bước nhẹ nh&agrave;ng bước ch&acirc;n, mang theo mới tới tiểu nha ho&agrave;n Linh Tước, h&agrave;o hứng cao đi đến ch&iacute;nh viện ăn nhờ ở đậu.<br />\r\n<br />\r\nTiểu nha ho&agrave;n xưng h&ocirc; l&agrave; hắn lấy, đ&uacute;ng l&agrave; chủ thế giới mẹ cả b&ecirc;n người đại nha ho&agrave;n danh xưng, cũng coi l&agrave; hắn một điểm tử &aacute;c th&uacute; vị.<br />\r\n<br />\r\nLời n&oacute;i trước hắn kh&ocirc;ng c&oacute; nha ho&agrave;n phục thị,<br />\r\n<br />\r\nTiểu nha ho&agrave;n Linh Tước l&agrave; H&igrave;nh phu nh&acirc;n đối với hắn nghĩ kế phần thưởng.<br />\r\n<br />\r\nLẽ ra, l&agrave;m Nhất Đẳng Tương Qu&acirc;n Phủ x&ecirc;́p hạng thứ ba con thứ, b&ecirc;n người trừ nhũ mẫu, c&ograve;n phải c&oacute; một đại nha ho&agrave;n v&agrave; hai c&aacute;i tiểu nha ho&agrave;n phục thị, chỉ tiếc H&igrave;nh phu nh&acirc;n qu&aacute; mức keo kiệt, ph&iacute;a trước căn bản l&agrave; kh&ocirc;ng c&oacute; đem Giả T&ocirc;ng để ở trong mắt, nơi n&agrave;o sẽ dụng t&acirc;m an b&agrave;i.<br />\r\n<br />\r\nĐối với những n&agrave;y, Giả T&ocirc;ng tự nhi&ecirc;n kh&ocirc;ng phải rất để &yacute;.<br />\r\n<br />\r\nV&agrave; chủ thế giới chờ đợi ở hậu trạch c&ocirc;ng phủ kh&ocirc;ng sai biệt lắm đồng dạng t&igrave;nh h&igrave;nh, hắn trước mắt mục đ&iacute;ch quan trọng nhất ngọn, ch&iacute;nh l&agrave; thuận thuận lợi lợi trưởng th&agrave;nh.<br />\r\n<br />\r\nLấy hắn nắm giữ phong ph&uacute; kiến thức, coi như xong kh&ocirc;ng c&oacute; Vinh Quốc Phủ hoặc l&agrave; Nhất Đẳng Tương Qu&acirc;n Phủ t&agrave;i nguy&ecirc;n tương trợ, muốn sống cho thoải m&aacute;i tương đương dễ d&agrave;ng.<br />\r\n<br />\r\nLại n&oacute;i, lấy Vinh Quốc Phủ có th&ecirc;̉ tính được bệnh trạng t&agrave;i nguy&ecirc;n ph&acirc;n phối, hắn cũng đừng nghĩ được c&aacute;i g&igrave; chỗ tốt, thậm ch&iacute; liền nối liền th&agrave;nh năm sau dựa theo quy củ, xuất phủ tự lập c&aacute;i kia một ng&agrave;n lượng bạc, cũng đừng nghĩ tuỳ tiện đạt được.<br />\r\n<br />\r\nDựa theo Hồng L&acirc;u Mộng t&igrave;nh tiết diễn biến, nếu l&agrave; hắn kh&ocirc;ng rất sớm l&agrave;m ch&uacute;t &iacute;t chuẩn bị m&agrave; n&oacute;i, đo&aacute;n chừng c&ograve;n kh&ocirc;ng đợi hắn ch&acirc;n ch&iacute;nh trưởng th&agrave;nh, Vinh Quốc Phủ muốn ho&agrave;n to&agrave;n suy t&agrave;n.<br />\r\n<br />\r\nMột khi bị li&ecirc;n lụy tiến v&agrave;o H&igrave;nh bộ đại lao lắc lư một v&ograve;ng, lại nghĩ ho&agrave;n th&agrave;nh t&iacute;ch lũy kh&iacute; vận nhiệm vụ, cũng kh&ocirc;ng phải b&igrave;nh thường kh&oacute; khăn, tr&ecirc;n cơ bản chỉ c&oacute; gia nhập qu&acirc;n đội một con đường tử.</p>\r\n\r\n<p><br />\r\nThế nhưng l&agrave;, hắn l&agrave; đem lần n&agrave;y Hồng l&acirc;u chuyến đi, xem như chủ thế giới th&iacute; nghiệm tr&agrave;ng, tốt nhất đối ứng chủ thế giới t&igrave;nh cảnh, tiến v&agrave;o trong qu&acirc;n ch&eacute;m giết b&aacute;c c&ocirc;ng danh, b&acirc;y giờ kh&ocirc;ng phải một c&aacute;i lựa chọn tốt.<br />\r\n<br />\r\nMột khi trong linh hồn nhiều thiết huyết s&aacute;t phạt chi kh&iacute;, về tới chủ thế giới sau căn bản l&agrave; kh&ocirc;ng thể gạt được người s&aacute;ng suốt, đơn giản ch&iacute;nh l&agrave; tự t&igrave;m đường chết a.<br />\r\n<br />\r\nTrước mắt Vinh Quốc Phủ v&agrave; Nhất Đẳng Tương Qu&acirc;n Phủ mặc d&ugrave; đều ở đi xuống dốc, vẫn c&ograve;n c&oacute; thể miễn cưỡng chống đỡ quốc c&ocirc;ng phủ mặt b&agrave;i, kh&ocirc;ng cần qu&aacute; mức lo lắng những thứ n&agrave;y.<br />\r\n<br />\r\nĐương nhi&ecirc;n, thực lực bản th&acirc;n l&agrave; căn bản, cường đại c&aacute; nh&acirc;n v&otilde; lực l&agrave; tự vệ cơ sở, hắn kh&ocirc;ng gi&acirc;y ph&uacute;t n&agrave;o đều kh&ocirc;ng qu&ecirc;n r&egrave;n luyện th&acirc;n thể.<br />\r\n<br />\r\nĐi lại b&ocirc;̣ pháp tần suất, h&iacute;t thở chập tr&ugrave;ng tiết tấu, đều l&agrave; một loại ho&agrave; v&agrave;o sinh hoạt r&egrave;n luyện h&igrave;nh thức, một l&uacute;c sau hiệu quả vẫn l&agrave; tương đối r&otilde; r&agrave;ng.<br />\r\n<br />\r\nTiểu nha ho&agrave;n Linh Tước mới vừa vặn điều đến b&ecirc;n người Giả T&ocirc;ng kh&ocirc;ng bao l&acirc;u, đ&uacute;ng l&agrave; quen thuộc ho&agrave;n cảnh mới thận trọng, cũng kh&ocirc;ng c&oacute; Vinh Quốc Phủ nh&oacute;m đại nha ho&agrave;n n&agrave;y kiều kiều chi kh&iacute;, tr&ecirc;n đường đi kh&ocirc;ng n&oacute;i một lời đ&agrave;ng ho&agrave;ng an phận cực kỳ.<br />\r\n<br />\r\nĐ&uacute;ng l&uacute;c n&agrave;y, một đạo th&acirc;n ảnh nho nhỏ đột nhi&ecirc;n từ chỗ ngoặt tr&ecirc;n đường x&ocirc;ng ra, trực tiếp đ&aacute;nh tới khoan thai độ bước Giả T&ocirc;ng.<br />\r\n<br />\r\n&quot;Tam gia cẩn thận!&quot;<br />\r\n<br />\r\nLinh Tước sợ đến mức khu&ocirc;n mặt nhỏ trắng bệch &acirc;m thanh kinh h&ocirc;, tay ch&acirc;n luống cuống lập vu tại chỗ liền v&agrave; cọc gỗ giống như.<br />\r\n<br />\r\n&quot;Ở đ&acirc;u ra tiểu tặc, lại d&aacute;m đ&aacute;nh l&eacute;n ta!&quot;<br />\r\n<br />\r\nGiả T&ocirc;ng t&acirc;m th&aacute;i cần phải trấn định hơn nhiều, phản ứng nhanh ch&oacute;ng k&eacute;o một ph&aacute;t k&eacute;o một c&aacute;i, đưa tay kho&aacute;c l&ecirc;n đ&aacute;nh tới th&acirc;n ảnh nho nhỏ đầu vai, trực tiếp dắt tiểu tử n&agrave;y lăng kh&ocirc;ng dạo qua một v&ograve;ng, nới lỏng tay ng&atilde; xuống đất ng&atilde; ch&oacute; gặm b&ugrave;n.<br />\r\n<br />\r\nTập trung nh&igrave;n v&agrave;o, l&agrave; một tuổi c&ugrave;ng hắn kh&ocirc;ng sai biệt lắm s&aacute;u bảy tuổi đứa b&eacute;, d&aacute;ng dấp mi thanh mục t&uacute; tr&ecirc;n người y phục cũng so với hắn muốn hoa lệ một bậc.<br />\r\n<br />\r\nC&ograve;n kh&ocirc;ng đợi Giả T&ocirc;ng mở miệng hỏi thăm, tiểu tử n&agrave;y xoay người ngồi chồm hổm ở oa oa kh&oacute;c lớn: &quot;C&aacute;c ngươi đều khi dễ ta, ch&ecirc; ta kh&ocirc;ng phải th&aacute;i th&aacute;i nu&ocirc;i...&quot;<br />\r\n<br />\r\nTruyện main b&aacute;, hậu cung, c&oacute; ch&uacute;t yy, diễn tả pk r&otilde; r&agrave;ng, văn phong kh&aacute; ổn, đ&atilde; ho&agrave;n th&agrave;nh</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:605px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'tieu-ha-moi-lo-sung-nhon-nhon', 6, '7272', '2022-08-03 04:03:29', '2022-08-03 04:03:29'),
(36, '5', 'Tôi muốn đến ở nhà cô', '<p>Hạ Chi T&igrave;nh v&agrave; Hạ Chi Sơ tuy rằng l&agrave; sinh đ&ocirc;i c&aacute;ch nhau chỉ năm ph&uacute;t, nhưng hai người từ trong ra ngo&agrave;i chẳng giống nhau x&iacute;u n&agrave;o.<br />\r\n<br />\r\nKhi đầy th&aacute;ng, Hạ Chi Sơ đ&atilde; lớn l&ecirc;n rất đẹp, trắng trẻo mập mạp, đ&ocirc;i mắt to tr&ograve;n, l&ocirc;ng mi rất d&agrave;i, mọi người tr&ocirc;ng thấy đều cảm th&aacute;n hắn đ&aacute;ng y&ecirc;u kh&ocirc;ng th&ocirc;i; c&ograve;n c&ocirc; vẫn giống như l&uacute;c mới sinh ra, nhỏ nhỏ gầy gầy, mắt nhỏ miệng nhỏ, giống như con chuột nhỏ suy dinh dưỡng, mẹ T&ocirc; &ocirc;m c&ocirc; nh&igrave;n tr&aacute;i nh&igrave;n phải, rất nhiều lần ho&agrave;i nghi bệnh viện đưa nhầm rồi.<br />\r\n<br />\r\nHạ Chi Sơ l&uacute;c lớn l&agrave; em g&aacute;i t&acirc;m cơ, c&ograve;n l&uacute;c nhỏ l&agrave; thằng nh&oacute;c t&acirc;m cơ, từ nhỏ đ&atilde; biết trước mặt mọi người giả vờ dễ thương khiến mọi người h&ocirc;n kh&ocirc;ng ngừng c&ograve;n cho qu&agrave; b&aacute;nh. M&agrave; c&ocirc; trước một tuổi đều l&agrave; ăn no rồi ngủ, ngủ dậy lại ăn, thỉnh thoảng mọi người sẽ tr&ecirc;u c&ocirc; một ch&uacute;t, nh&igrave;n bộ d&aacute;ng ngốc nghếch của c&ocirc;, mẹ T&ocirc; nghi ngờ rằng c&ocirc; bị thiểu năng tr&iacute; tuệ bẩm sinh, bởi vậy c&ograve;n dẫn c&ocirc; đi kh&aacute;m b&aacute;c sĩ.</p>', '<p>Hạ Chi T&igrave;nh v&agrave; Hạ Chi Sơ tuy rằng l&agrave; sinh đ&ocirc;i c&aacute;ch nhau chỉ năm ph&uacute;t, nhưng hai người từ trong ra ngo&agrave;i chẳng giống nhau x&iacute;u n&agrave;o.<br />\r\n<br />\r\nKhi đầy th&aacute;ng, Hạ Chi Sơ đ&atilde; lớn l&ecirc;n rất đẹp, trắng trẻo mập mạp, đ&ocirc;i mắt to tr&ograve;n, l&ocirc;ng mi rất d&agrave;i, mọi người tr&ocirc;ng thấy đều cảm th&aacute;n hắn đ&aacute;ng y&ecirc;u kh&ocirc;ng th&ocirc;i; c&ograve;n c&ocirc; vẫn giống như l&uacute;c mới sinh ra, nhỏ nhỏ gầy gầy, mắt nhỏ miệng nhỏ, giống như con chuột nhỏ suy dinh dưỡng, mẹ T&ocirc; &ocirc;m c&ocirc; nh&igrave;n tr&aacute;i nh&igrave;n phải, rất nhiều lần ho&agrave;i nghi bệnh viện đưa nhầm rồi.<br />\r\n<br />\r\nHạ Chi Sơ l&uacute;c lớn l&agrave; em g&aacute;i t&acirc;m cơ, c&ograve;n l&uacute;c nhỏ l&agrave; thằng nh&oacute;c t&acirc;m cơ, từ nhỏ đ&atilde; biết trước mặt mọi người giả vờ dễ thương khiến mọi người h&ocirc;n kh&ocirc;ng ngừng c&ograve;n cho qu&agrave; b&aacute;nh. M&agrave; c&ocirc; trước một tuổi đều l&agrave; ăn no rồi ngủ, ngủ dậy lại ăn, thỉnh thoảng mọi người sẽ tr&ecirc;u c&ocirc; một ch&uacute;t, nh&igrave;n bộ d&aacute;ng ngốc nghếch của c&ocirc;, mẹ T&ocirc; nghi ngờ rằng c&ocirc; bị thiểu năng tr&iacute; tuệ bẩm sinh, bởi vậy c&ograve;n dẫn c&ocirc; đi kh&aacute;m b&aacute;c sĩ.<br />\r\n<br />\r\nSau n&agrave;y dần dần lớn l&ecirc;n, hai người lại c&agrave;ng kh&aacute;c nhau hơn nữa, Hạ Chi Sơ c&oacute; l&agrave;n da trắng n&otilde;n, khu&ocirc;n mặt xinh đẹp, d&aacute;ng người cao gầy, l&agrave;m gi&aacute;o thảo* một đường từ nh&agrave; trẻ đến đại học, m&agrave; c&ocirc; d&aacute;ng người thấp b&eacute;, ngũ quan b&igrave;nh thường, l&agrave;m người qua đường từ nh&agrave; trẻ đến cao trung, đến khi thi đậu đại học, bỗng nhi&ecirc;n con g&aacute;i mười t&aacute;m biến đổi to lớn m&agrave; trở n&ecirc;n xinh đẹp.<br />\r\n<br />\r\n*Nam sinh đẹp trai nhất trường.<br />\r\n<br />\r\nT&iacute;nh c&aacute;ch hai người cũng c&aacute;ch biệt nghi&ecirc;ng trời lệch đất: Hạ Chi Sơ th&ocirc;ng minh, đức tr&iacute; thể ph&aacute;t triển to&agrave;n diện, xảo quyệt giống như con hồ ly, vĩnh viễn chỉ c&oacute; hắn bắt nạt người kh&aacute;c; m&agrave; c&ocirc; lu&ocirc;n lu&ocirc;n phản ứng chậm nửa nhịp, điểm số lu&ocirc;n lu&ocirc;n gi&atilde;y dụa ở ngưỡng đạt ti&ecirc;u chuẩn, nh&aacute;t như thỏ đế, bị người kh&aacute;c ăn hiếp chỉ biết chạy trốn.<br />\r\n<br />\r\nDo gen c&ugrave;ng với tuổi thơ đ&atilde; dưỡng t&iacute;nh c&aacute;ch Hạ Chi T&igrave;nh c&oacute; gan chửi thầm nhưng kh&ocirc;ng c&oacute; gan phản kh&aacute;ng. Cho n&ecirc;n khi đồng nghiệp nữ c&ugrave;ng bộ phận trong c&ocirc;ng ty nghỉ ph&eacute;p sinh em b&eacute;, sếp thấy gần cuối năm rồi kh&ocirc;ng tuyển người th&ecirc;m được nữa n&ecirc;n đem hết c&ocirc;ng việc của nữ đồng nghiệp đ&oacute; rơi xuống đầu c&ocirc;, c&ocirc; cũng chỉ d&aacute;m ở trong l&ograve;ng mắng chửi sếp kh&ocirc;ng dưới một ng&agrave;n lần, rồi y&ecirc;n lặng chấp nhận an b&agrave;i.<br />\r\n<br />\r\nTăng ca suốt một buổi tối, rốt cuộc đ&atilde; đến 10 giờ k&eacute;m 15 ph&uacute;t, c&ocirc; đ&atilde; chuẩn bị xong t&agrave;i liệu cho kh&aacute;ch h&agrave;ng ng&agrave;y h&ocirc;m sau gặp rồi. C&ocirc; nh&eacute;o nh&eacute;o bả vai đau nhức, đứng l&ecirc;n r&oacute;t cho m&igrave;nh một ly nước ấm. Tối nay chỉ c&oacute; một m&igrave;nh c&ocirc; tăng ca, xung quanh rất y&ecirc;n tĩnh, c&ocirc; đi đến dựa v&agrave;o cửa sổ ngắm nh&igrave;n cảnh đ&ecirc;m đường phố phồn hoa của th&agrave;nh phố S n&agrave;y, hưởng thụ khoảnh khắc y&ecirc;n tĩnh hiếm c&oacute; l&uacute;c n&atilde;y m&agrave; thả lỏng.<br />\r\n<br />\r\nĐứng một hồi c&ocirc; đổi tư thế, l&uacute;c cuối đầu kh&oacute;e mắt bỗng nhi&ecirc;n nh&igrave;n đến một chiếc xe thể thao m&agrave;u v&agrave;ng quen mắt trước cửa t&ograve;a cao ốc, th&aacute;i dương c&ocirc; đột nhi&ecirc;n giật giật, c&oacute; một dự cảm kh&ocirc;ng hay d&acirc;ng l&ecirc;n trong l&ograve;ng.<br />\r\n<br />\r\nCả người c&ocirc; phản ứng c&oacute; điều kiện m&agrave; co r&uacute;t lại ph&iacute;a sau, hy vọng đối phương kh&ocirc;ng nh&igrave;n thấy c&ocirc;, đ&aacute;ng tiếc c&ocirc; vẫn chậm một bước, người đ&agrave;n &ocirc;ng đứng b&ecirc;n cạnh xe thể thao đ&atilde; sớm nhanh tay lẹ mắt m&agrave; nh&igrave;n đến c&ocirc;. Chưa đến hai gi&acirc;y, điện thoại c&ocirc; đặt tr&ecirc;n b&agrave;n l&agrave;m việc vui vẻ m&agrave; reo l&ecirc;n, tr&ecirc;n m&agrave;n h&igrave;nh hiện l&ecirc;n ba chữ to đ&ugrave;ng &quot;Tống An Thần&quot;.<br />\r\n<br />\r\nBắt m&aacute;y hay kh&ocirc;ng bắt m&aacute;y, đ&acirc;y quả l&agrave; vấn đề a! Hạ Chi T&igrave;nh nh&igrave;n di động, mặt m&agrave;y rối đến ra c&aacute;i chữ xuy&ecirc;n 川.<br />\r\n<br />\r\nAnh ta sao lại đến c&ocirc;ng ty của c&ocirc;? Chẳng lẽ đến đ&acirc;y đ&ograve;i nợ? Kh&ocirc;ng phải đ&atilde; n&oacute;i mỗi th&aacute;ng trả hai ng&agrave;n đồng sao? Chẳng lẽ anh ta đổi &yacute;? Ch&iacute;nh l&agrave; đổi &yacute; cũng v&ocirc; dụng, c&ocirc; kh&ocirc;ng c&oacute; tiền m&agrave;! Nhớ đến l&uacute;c trước xem tivi những người v&igrave; trốn nợ c&aacute;i g&igrave; cũng l&agrave;m được, c&ocirc; kh&oacute;c kh&ocirc;ng ra nước mắt, chưa từng nghĩ c&ocirc; cũng c&oacute; ng&agrave;y v&igrave; trốn nợ m&agrave; biến th&agrave;nh như vậy.<br />\r\n<br />\r\n《You still do that to me》của Chirs Cagle rốt cuộc cũng dừng lại ở lần thứ năm, nh&igrave;n chiếc di động đ&atilde; y&ecirc;n tĩnh lại, c&ocirc; thở d&agrave;i một hơi, cảm gi&aacute;c thật nhẹ nh&otilde;m.<br />\r\n<br />\r\nChỉ l&agrave; c&ocirc; chưa kịp thở xong một hơi xuống dưới đ&atilde; nghe tiếng bước ch&acirc;n b&ecirc;n ngo&agrave;i h&agrave;nh lang tối thui xa xa truyền đến.</p>\r\n\r\n<p><br />\r\nBảo vệ t&ograve;a nh&agrave; n&agrave;y phải đến 12 giờ mới tới tuần tra, giờ n&agrave;y c&ograve;n c&oacute; ai đến c&ocirc;ng ty? Chẳng lẽ...... Chẳng lẽ l&agrave; anh ta?! Suy nghĩ n&agrave;y khiến to&agrave;n th&acirc;n c&ocirc; nổi cả da g&agrave;, c&ocirc; chạy nhanh đi dọn đồ t&iacute;nh to&aacute;n chuồn đi trước khi Tống An Thần tới.<br />\r\n<br />\r\nTiếng bước ch&acirc;n c&agrave;ng ng&agrave;y c&agrave;ng gần, l&uacute;c n&agrave;y lao ra chỉ sợ sẽ đụng phải, tr&ograve;ng mắt c&ocirc; vừa chuyển, lanh tr&iacute; m&agrave; tắt đ&egrave;n đi, dựa v&agrave;o cửa ch&uacute; &yacute; động tĩnh b&ecirc;n ngo&agrave;i, tiếng bước ch&acirc;n rốt cuộc dừng lại ở cửa.<br />\r\n<br />\r\nTống An Thần đứng ở cửa văn ph&ograve;ng, nh&igrave;n vừa rồi c&ograve;n s&aacute;ng đ&egrave;n, m&agrave; giờ n&agrave;y văn ph&ograve;ng lại đen nh&aacute;nh một mảnh, kh&oacute;e miệng khẽ nhếch, mắt phượng hiện l&ecirc;n một tia &aacute;nh s&aacute;ng nhạt, T&ocirc;n Ngộ Kh&ocirc;ng muốn chạy trốn khỏi Ngũ Chỉ Sơn của Phật Như Lai? Kh&ocirc;ng c&oacute; khả năng!<br />\r\n<br />\r\nAnh cầm lấy di động, ng&oacute;n tay trắng n&otilde;n thon d&agrave;i nhấn n&uacute;t, trong văng ph&ograve;ng lập tức vang l&ecirc;n &acirc;m thanh từ t&iacute;nh của Chirs Cagle.<br />\r\n<br />\r\nHạ Chi T&igrave;nh vừa rồi c&ograve;n nghi ngờ sao b&ecirc;n ngo&agrave;i kh&ocirc;ng c&oacute; &acirc;m thanh g&igrave; hết th&igrave; di động liền reo l&ecirc;n. Heo l&agrave;m sao m&agrave; chết, đ&oacute; l&agrave; ngu m&agrave; chết! C&ocirc; cho rằng tắt đ&egrave;n đi l&agrave; mọi chuyện ổn thỏa, ho&agrave;n to&agrave;n qu&ecirc;n mất bật chế độ im lặng cho điện thoại!<br />\r\n<br />\r\nHạ Chi T&igrave;nh ph&aacute;t đi&ecirc;n m&agrave; nh&igrave;n di động, hận kh&ocirc;ng thể quăng n&oacute; ra ngo&agrave;i, nhưng l&uacute;c n&agrave;y l&agrave;m g&igrave; cũng đ&atilde; muộn, c&ocirc; ngậm ng&ugrave;i m&agrave; nhấn n&uacute;t nghe, tiếng &quot;Alo&quot; c&ograve;n chưa ra khỏi miệng liền nghe được Tống An Thần lạnh l&ugrave;ng n&oacute;i:<br />\r\n<br />\r\n&quot;Tốt, rất tốt.&quot;<br />\r\n<br />\r\nMột trận kh&iacute; lạnh xuy&ecirc;n qua cửa gỗ tới, đối với những lời n&agrave;y hiện tại c&ocirc; lu&ocirc;n sinh ra phản ứng c&oacute; điều kiện như vậy, chỉ cần nghe thấy c&acirc;u n&agrave;y c&ocirc; liền c&oacute; cảm gi&aacute;c kinh hồn bạt v&iacute;a, trước khi đại n&atilde;o hoạt động m&agrave; kh&ocirc;ng tiền đồ duỗi tay ra mở cửa ra.<br />\r\n<br />\r\n*******************<br />\r\n<br />\r\n5-【2】<br />\r\n<br />\r\nTống An Thần mặc một thường phục m&agrave;u đen đứng ở cửa, th&acirc;n h&igrave;nh vốn dĩ đ&atilde; thon d&agrave;i đĩnh bạc nay lại c&agrave;ng th&ecirc;m lạnh l&ugrave;ng.<br />\r\n<br />\r\nC&ocirc; cảm thấy tim m&igrave;nh đập lỡ một nhịp, giả vờ nở một nụ cười ti&ecirc;n hạ thủ vi cường n&oacute;i: &quot;Tống ti&ecirc;n sinh, anh l&ecirc;n đ&acirc;y như thế n&agrave;o? T&ocirc;i định đi xuống đ&acirc;y.&quot;<br />\r\n<br />\r\n&quot;Ồ? Phải kh&ocirc;ng?&quot; Tống An Thần m&agrave;y đẹp khẽ nhếch, kh&oacute;e miệng cong một ch&uacute;t &yacute; cười, &quot;Vậy sao c&ocirc; kh&ocirc;ng nghe điện thoại?&quot;<br />\r\n<br />\r\nV&igrave; c&aacute;i g&igrave;? N&agrave;y c&ograve;n kh&ocirc;ng phải kh&ocirc;ng muốn bắt m&aacute;y sao? Tất nhi&ecirc;n c&ocirc; kh&ocirc;ng ngu đến mức đi n&oacute;i ra, c&ocirc; quơ quơ điện thoại trong tay n&oacute;i: &quot;L&uacute;c n&atilde;y định nghe m&aacute;y nhưng l&agrave; do di động sau khi bị rớt th&igrave; một số n&uacute;t kh&ocirc;ng linh hoạt nữa.&quot;<br />\r\n<br />\r\nAnh nghe vậy th&igrave; liếc nh&igrave;n điện thoại bị rớt hỏng trong tay c&ocirc; một c&aacute;i, kh&ocirc;ng c&oacute; hỏi nữa, coi như chấp nhận c&aacute;i l&yacute; do n&agrave;y.<br />\r\n<br />\r\n&quot;Tống ti&ecirc;n sinh t&ocirc;i đi trước, kh&ocirc;ng c&oacute; việc g&igrave; kh&ocirc;ng cần li&ecirc;n hệ, b&aacute;i bai.&quot; C&ocirc; chưa n&oacute;i hết c&acirc;u liền quay đầu hướng thang m&aacute;y đi đến.<br />\r\n<br />\r\nTống An Thần cũng kh&ocirc;ng ngăn cản c&ocirc;, khoanh tay trước ngực, sau lưng lạnh l&ugrave;ng mở miệng n&oacute;i: &quot;50 vạn......&quot;<br />\r\n<br />\r\nMột con số khiến cho Hạ Chi T&igrave;nh chợt dừng lại bước ch&acirc;n muốn chạy trốn, c&ocirc; chậm r&atilde;i xoay người, trưng ra một khu&ocirc;n mặt đắng như khổ qua n&oacute;i: &quot;T&ocirc;i thật sự kh&ocirc;ng c&ograve;n tiền, nếu anh &eacute;p t&ocirc;i chỉ c&ograve;n nước đi b&aacute;n nh&agrave;.&quot;<br />\r\n<br />\r\n&quot;C&ocirc; kh&ocirc;ng cần b&aacute;n, t&ocirc;i đ&atilde; b&aacute;n.&quot;<br />\r\n<br />\r\n&quot;B&aacute;n? B&aacute;n c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Nh&agrave;.&quot;<br />\r\n<br />\r\n&quot;Anh đ&atilde; đem nh&agrave; b&aacute;n đi?!&quot; C&ocirc; h&aacute; to miệng, bộ d&aacute;ng giống quỷ nh&igrave;n anh.<br />\r\n<br />\r\nAnh gật gật đầu, &quot;B&aacute;n, cho n&ecirc;n hiện tại t&ocirc;i kh&ocirc;ng c&ograve;n chỗ ở.&quot;<br />\r\n<br />\r\nHiện tại t&ocirc;i kh&ocirc;ng c&ograve;n chỗ ở, bảy chữ n&agrave;y từ tai tr&aacute;i c&ocirc; tiến v&agrave;o trong n&atilde;o, tức khắc cảm thấy l&ograve;ng đầy &aacute;y n&aacute;y.<br />\r\n<br />\r\nNếu kh&ocirc;ng phải c&ocirc;, xe anh ấy thu&ecirc; cũng kh&ocirc;ng bị đụng hỏng, anh ấy cũng kh&ocirc;ng cần phải b&aacute;n nh&agrave; lấy tiền sửa xe, đến nỗi hiện tại ngay cả chỗ đặt ch&acirc;n cũng kh&ocirc;ng c&oacute;, &quot;Thực xin lỗi, đều l&agrave; lỗi của t&ocirc;i, anh muốn t&ocirc;i hỗ trợ g&igrave; cứ việc n&oacute;i, chỉ cần l&agrave;m được t&ocirc;i nhất định sẽ kh&ocirc;ng từ chối.&quot;<br />\r\n<br />\r\nTống An Thần cười lộ ra h&agrave;m răng trắng xinh đẹp, &quot;Quả thật c&oacute; việc muốn nhờ c&ocirc; gi&uacute;p, hơn nữa chắc chắn c&ocirc; sẽ l&agrave;m được.&quot;<br />\r\n<br />\r\nKhoảnh khắc đ&oacute; c&ocirc; tựa hồ ngửi được một m&ugrave;i vị &acirc;m mưu, &acirc;m trầm giống như s&oacute;i nh&igrave;n b&agrave; ngoại tươi cười.<br />\r\n<br />\r\nC&ocirc; ho&agrave;i nghi m&agrave; nh&igrave;n anh: &quot;Đ&oacute; l&agrave; chuyện g&igrave;?&quot;<br />\r\n<br />\r\n&quot;T&ocirc;i muốn đến ở nh&agrave; c&ocirc;.&quot; Vẻ mặt anh l&uacute;c n&oacute;i ra lời n&agrave;y rất b&igrave;nh đạm, tự nhi&ecirc;n như n&oacute;i t&ocirc;i muốn đến nh&agrave; c&ocirc; uống ly tr&agrave; vậy.<br />\r\n<br />\r\nC&ocirc; vừa nghe l&ocirc;ng mao dựng đứng: &quot;NO WAY! NO DOOR! NO WINDOW!!&quot;<br />\r\n<br />\r\nAnh kh&ocirc;ng nhanh kh&ocirc;ng chậm m&agrave; đưa ra lời dụ hoặc: &quot;Kh&ocirc;ng phải nh&agrave; c&ocirc; c&oacute; hai ph&ograve;ng ngủ một ph&ograve;ng kh&aacute;ch sao? T&ocirc;i thu&ecirc; c&ocirc; một ph&ograve;ng, tiền thu&ecirc; ba ng&agrave;n, khấu trừ trong khoản nợ 50 mươi vạn, c&ocirc; cảm thấy như thế n&agrave;o?&quot;<br />\r\n<br />\r\nC&oacute; c&acirc;u &quot;c&oacute; tiền mua ti&ecirc;n cũng được&quot;, Hạ Chi T&igrave;nh nhanh ch&oacute;ng trong l&ograve;ng t&iacute;nh to&aacute;n một phen, một th&aacute;ng trừ đi ba ng&agrave;n, mỗi th&aacute;ng c&ocirc; lại trả th&ecirc;m hai ng&agrave;n cho anh như vậy c&ocirc; c&oacute; thể đem kỳ hạn trả nợ r&uacute;t c&ograve;n trong v&ograve;ng mười năm, kiến nghị n&agrave;y xem ra kh&ocirc;ng tồi, chỉ l&agrave;......<br />\r\n<br />\r\n&quot;Nam nữ ở chung kh&ocirc;ng ổn cho lắm......&quot; Nếu như bị mẹ T&ocirc; biết c&ograve;n c&oacute; thể sống sao?<br />\r\n<br />\r\nC&ocirc; c&ograve;n chưa n&oacute;i dứt lời đ&atilde; bị vẻ mặt nghi&ecirc;m t&uacute;c của Tống An Thần đ&aacute;nh g&atilde;y, &quot;Hạ tiểu thư, ch&uacute;ng ta đ&acirc;y l&agrave; thu&ecirc; chung, kh&ocirc;ng phải ở chung, hiện tại trong th&agrave;nh phố n&agrave;y c&oacute; bao nhi&ecirc;u nam nữ v&igrave; c&ocirc;ng việc m&agrave; thu&ecirc; ở chung, theo như c&ocirc; n&oacute;i bọn họ đều l&agrave; loại người d&acirc;ʍ đ&atilde;иɠ? Hay l&agrave; n&oacute;i Hạ tiểu thư cho rằng t&ocirc;i kh&ocirc;ng phải l&agrave; ch&iacute;nh nh&acirc;n qu&acirc;n tử?&quot;<br />\r\n<br />\r\nVẻ mặt anh nghi&ecirc;m t&uacute;c như vậy, thật sự nghi&ecirc;m t&uacute;c, phảng phất như bị lời c&ocirc; n&oacute;i l&agrave;m x&uacute;c phạm, so với h&igrave;nh tượng ch&iacute;nh nh&acirc;n qu&acirc;n tử của anh l&agrave;m c&ocirc; c&oacute; ch&uacute;t dơ bẩn, giống t&acirc;m tư kẻ tiểu nh&acirc;n l&agrave;m nội t&acirc;m c&ocirc; c&oacute; cảm gi&aacute;c đầy tội lỗi.<br />\r\n<br />\r\n&quot;...... Kh&ocirc;ng phải, t&ocirc;i kh&ocirc;ng c&oacute; &yacute; đ&oacute;, trừ ba v&agrave; anh trai, t&ocirc;i chưa từng c&ugrave;ng người đ&agrave;n &ocirc;ng n&agrave;o ở c&ugrave;ng, chỉ sợ l&agrave; kh&ocirc;ng quen.&quot;<br />\r\n<br />\r\nNghe được c&ocirc; trước nay kh&ocirc;ng c&ugrave;ng người đ&agrave;n &ocirc;ng kh&aacute;c ở c&ugrave;ng, kh&oacute;e miệng anh nhịn kh&ocirc;ng được m&agrave; nhếch l&ecirc;n, giọng n&oacute;i &ocirc;n h&ograve;a hơn nhiều: &quot;Th&oacute;i quen th&igrave; c&oacute; thể thay đổi, hơn nữa c&ocirc; n&oacute;i muốn t&ocirc;i giả l&agrave;m bạn trai c&ocirc; c&ugrave;ng c&ocirc; về nh&agrave; ăn Tết, b&acirc;y giờ c&aacute;ch ng&agrave;y Tết chỉ c&ograve;n c&oacute; 20 ng&agrave;y, nếu kh&ocirc;ng nhanh ch&oacute;ng quen thuộc lẫn nhau, c&ocirc; c&oacute; thể đảm bảo sau khi trở về nhất định sẽ kh&ocirc;ng lộ tẩy sao?&quot;<br />\r\n<br />\r\n&quot;Anh n&oacute;i cũng c&oacute; l&yacute;, chỉ l&agrave;......&quot; Kh&ocirc;ng biết v&igrave; c&aacute;i g&igrave; c&ocirc; cảm thấy c&oacute; chỗ n&agrave;o đ&oacute; kh&ocirc;ng đ&uacute;ng, rồi lại kh&ocirc;ng biết n&ecirc;n n&oacute;i c&aacute;i g&igrave;.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng cần nhưng nhị g&igrave; hết, đ&acirc;y l&agrave; biện ph&aacute;p một vốn bốn lời đối với Hạ tiểu thư, thứ nhất c&ocirc; c&oacute; thể nhanh ch&oacute;ng trả hết nợ cho t&ocirc;i, thứ hai c&oacute; thể đảm bảo kh&ocirc;ng bị lộ tẩy khi về nh&agrave; c&ocirc; ăn Tết.&quot; N&oacute;i đến đ&acirc;y, anh thu lại nụ cười tươi vừa rồi, nghi&ecirc;m mặt n&oacute;i: &quot;Bất qu&aacute; nếu Hạ tiểu thư thật sự cảm thấy kh&oacute; xử, t&ocirc;i cũng kh&ocirc;ng l&agrave;m kh&oacute; người kh&aacute;c, chỉ l&agrave; t&ocirc;i hiện tại nh&agrave; ở đ&atilde; b&aacute;n kh&ocirc;ng c&ograve;n chỗ ở, đ&agrave;nh phải mời Hạ tiểu thư lập tức đem tiền trả lại cho t&ocirc;i.&quot;<br />\r\n<br />\r\nKhi n&agrave;o n&ecirc;n b&aacute; đạo, khi n&agrave;o n&ecirc;n cường quyền, khi n&agrave;o lại n&ecirc;n đ&aacute;nh một c&aacute;i t&aacute;t cho một quả t&aacute;o, đối với điểm n&agrave;y, Tống An Thần quả thực l&agrave; l&agrave;m được l&ocirc; hỏa thuần thanh*.<br />\r\n<br />\r\n* 炉火纯青 (l&uacute;huǒch&uacute;nqīn): d&agrave;y c&ocirc;ng t&ocirc;i luyện (tương truyền Đạo gia luyện đan, nh&igrave;n v&agrave;o l&ograve;, thấy ngọn lửa l&ecirc; m&agrave;u xanh, coi l&agrave; đ&atilde; th&agrave;nh c&ocirc;ng, v&iacute; với sự th&agrave;nh thục của học vấn, kĩ thuật....)<br />\r\n<br />\r\nC&ocirc; c&oacute; tiền cần g&igrave; phải đợi tới b&acirc;y giờ? Nếu khiến c&ocirc; b&aacute;n đi nh&agrave; ở vừa mới mua năm nay thật l&agrave; kh&ocirc;ng vui, c&ocirc; đ&atilde; bỏ rất nhiều t&acirc;m huyết trang tr&iacute; ng&ocirc;i nh&agrave; n&agrave;y, từ thiết kế nh&agrave; cửa đến mua đồ gia dụng đều tốn rất nhiều thời gian v&agrave; tiền bạc, muốn c&ocirc; đem nh&agrave; đi b&aacute;n chẳng kh&aacute;c g&igrave; cắt thịt tr&ecirc;n người c&ocirc;.<br />\r\n<br />\r\nSau một hồi c&acirc;n nhắc, c&ocirc; cắn răn, quyết định bất cứ gi&aacute; n&agrave;o: &quot;VậyTống ti&ecirc;n sinh chuẩn bị khi n&agrave;o th&igrave; dọn qua?&quot;<br />\r\n<br />\r\n&quot;B&acirc;y giờ.&quot;</p>', '0', 'toi-muon-den-o-nha-co', 4, '224', '2022-08-03 04:09:17', '2022-08-03 04:09:17');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(37, '6', 'Dẫn sói vào nhà', '<p>Hạ Chi T&igrave;nh ngồi b&ecirc;n trong chiếc xe đầy vẻ hu&ecirc;nh hoang n&agrave;y, ngồi đến thẳng tắp, biểu t&igrave;nh hoảng hốt.<br />\r\n<br />\r\nV&agrave;o l&uacute;c cuối tuần, c&ocirc; cố &yacute; đến ch&ugrave;a t&igrave;m &quot;đại sư&quot;, nhưng &quot;đại sư&quot; n&oacute;i rằng c&ocirc; bị sao Ma Vương theo chiếu, b&ugrave;a hộ mệnh đ&atilde; kh&ocirc;ng c&ograve;n t&aacute;c dụng lắm, nếu cần thiết th&igrave; l&agrave;m ph&aacute;p sự, c&ocirc; yếu ớt hỏi bao nhi&ecirc;u tiền, &quot;đại sư&quot; d&ugrave;ng ng&oacute;n tay c&aacute;i xoa xoa tay, c&ocirc; lại lần nữa m&agrave; mang vẻ mặt khinh bỉ &quot;đại sư&quot;, một lần l&agrave;m ph&aacute;p sự cần hai vạn tệ, c&ocirc; t&igrave;nh nguyện tiếp tục xui xẻo.<br />\r\n<br />\r\nChỉ l&agrave; khi c&ocirc; đến b&atilde;i đỗ xe nh&igrave;n thấy lốp xe bị t&ecirc;n n&agrave;o xả kh&iacute; m&agrave; kh&ocirc;ng động đậy được, c&ocirc; lại nảy l&ecirc;n &yacute; niệm đi t&igrave;m &quot;đại sư&quot;.<br />\r\n<br />\r\nSo với người n&agrave;o đ&oacute; t&acirc;m t&igrave;nh hoang mang uể oải, Tống An Thần t&acirc;m t&igrave;nh tr&ocirc;ng kh&ocirc;ng tồi, anh cầm l&ecirc;n vỉ kẹo cao su Extra, bỏ v&agrave;o miệng hai vi&ecirc;n, đưa tới cho Hạ Chi T&igrave;nh hỏi muốn hay kh&ocirc;ng.</p>', '<p><br />\r\nHạ Chi T&igrave;nh ngồi b&ecirc;n trong chiếc xe đầy vẻ hu&ecirc;nh hoang n&agrave;y, ngồi đến thẳng tắp, biểu t&igrave;nh hoảng hốt.<br />\r\n<br />\r\nV&agrave;o l&uacute;c cuối tuần, c&ocirc; cố &yacute; đến ch&ugrave;a t&igrave;m &quot;đại sư&quot;, nhưng &quot;đại sư&quot; n&oacute;i rằng c&ocirc; bị sao Ma Vương theo chiếu, b&ugrave;a hộ mệnh đ&atilde; kh&ocirc;ng c&ograve;n t&aacute;c dụng lắm, nếu cần thiết th&igrave; l&agrave;m ph&aacute;p sự, c&ocirc; yếu ớt hỏi bao nhi&ecirc;u tiền, &quot;đại sư&quot; d&ugrave;ng ng&oacute;n tay c&aacute;i xoa xoa tay, c&ocirc; lại lần nữa m&agrave; mang vẻ mặt khinh bỉ &quot;đại sư&quot;, một lần l&agrave;m ph&aacute;p sự cần hai vạn tệ, c&ocirc; t&igrave;nh nguyện tiếp tục xui xẻo.<br />\r\n<br />\r\nChỉ l&agrave; khi c&ocirc; đến b&atilde;i đỗ xe nh&igrave;n thấy lốp xe bị t&ecirc;n n&agrave;o xả kh&iacute; m&agrave; kh&ocirc;ng động đậy được, c&ocirc; lại nảy l&ecirc;n &yacute; niệm đi t&igrave;m &quot;đại sư&quot;.<br />\r\n<br />\r\nSo với người n&agrave;o đ&oacute; t&acirc;m t&igrave;nh hoang mang uể oải, Tống An Thần t&acirc;m t&igrave;nh tr&ocirc;ng kh&ocirc;ng tồi, anh cầm l&ecirc;n vỉ kẹo cao su Extra, bỏ v&agrave;o miệng hai vi&ecirc;n, đưa tới cho Hạ Chi T&igrave;nh hỏi muốn hay kh&ocirc;ng.<br />\r\n<br />\r\nC&ocirc; lắc lắc, quay đầu nh&igrave;n khu&ocirc;n mặt tuấn nhan họa thủy n&agrave;y, đối với sự ph&aacute;t triển mối quan hệ của hai người thật sự kh&ocirc;ng thể tưởng tượng nổi. Hai tuần trước, bọn họ vẫn l&agrave; hai người xa lạ, hiện tại quan hệ lại rắc rối phức tạp như vậy&mdash;&mdash; anh l&agrave; chủ nợ của c&ocirc;, bạn trai giả, c&ograve;n c&oacute; sắp tới trở th&agrave;nh bạn c&ugrave;ng ph&ograve;ng.<br />\r\n<br />\r\nNh&igrave;n chằm chằm sườn mặt anh một hồi l&acirc;u, c&ocirc; bỗng nhi&ecirc;n nghĩ đến một vấn đề quan trọng, &quot;Đ&uacute;ng rồi, anh như thế n&agrave;o m&agrave; tiến v&agrave;o c&ocirc;ng ty ch&uacute;ng t&ocirc;i?&quot;<br />\r\n<br />\r\nBọn họ ra v&agrave;o c&ocirc;ng ty đều l&agrave; d&ugrave;ng thẻ, c&ocirc; vừa rồi một l&ograve;ng nghĩ trốn nợ, cư nhi&ecirc;n kh&ocirc;ng nghĩ tới vấn đề n&agrave;y.<br />\r\n<br />\r\nNghe được c&ocirc; hỏi, Tống An Thần hơi hơi ng&acirc;y ra một l&uacute;c, tia s&aacute;ng l&oacute;e l&ecirc;n trong mắt phượng chợt n&oacute;i: &quot;Trực tiếp đi v&agrave;o, cửa kh&ocirc;ng kh&oacute;a.&quot;<br />\r\n<br />\r\nC&ocirc; ho&agrave;i nghi: &quot;Phải kh&ocirc;ng?&quot;<br />\r\n<br />\r\nAnh cười: &quot;Bằng kh&ocirc;ng đ&acirc;u? Hạ tiểu thư cảm thấy t&ocirc;i l&agrave; như thế n&agrave;o đi v&agrave;o?&quot;<br />\r\n<br />\r\nC&ocirc; nh&iacute;u m&agrave;y, &quot;Kh&ocirc;ng biết l&agrave; ai lại kh&ocirc;ng c&oacute; &yacute; tứ như vậy, ng&agrave;y mai nhờ b&aacute;c Trương kiểm tra camera mới được.&quot;<br />\r\n<br />\r\nHạ Chi T&igrave;nh n&oacute;i xong ngẩng đầu, thấy anh cư nhi&ecirc;n vượt đ&egrave;n đỏ, cũng may hiện tại đ&ecirc;m đ&atilde; khuya, tr&ecirc;n đường kh&ocirc;ng c&oacute; g&igrave; người.<br />\r\n<br />\r\nAnh cười khẽ một tiếng n&oacute;i xin lỗi, quẹo đến giao lộ ph&iacute;a trước, dừng trước cửa một cửa h&agrave;ng tiện lợi, &quot;C&ocirc; ở chỗ n&agrave;y chờ t&ocirc;i một ch&uacute;t, t&ocirc;i đi mua ch&uacute;t đồ.&quot;<br />\r\n<br />\r\nC&ocirc; gật đầu, thấy anh cầm di động đi v&agrave;o cửa h&agrave;ng tiện lợi, chưa đến mười ph&uacute;t liền x&aacute;ch ra một t&uacute;i đồ uống, anh quay lại ghế điều khiển, tự m&igrave;nh cầm một chai nước kho&aacute;ng, sau đ&oacute; đem đồ uống kh&aacute;c n&eacute;m cho c&ocirc;.<br />\r\n<br />\r\nC&ocirc; mở t&uacute;i ra vừa thấy, thế nhưng l&agrave; một t&uacute;i sữa chua nguy&ecirc;n vị, &quot;Anh cũng th&iacute;ch uống sữa chua?&quot;<br />\r\n<br />\r\nAnh lắc đầu, &quot;Kh&ocirc;ng th&iacute;ch, đ&oacute; l&agrave; mua cho c&ocirc;.&quot;<br />\r\n<br />\r\nC&ocirc; nhịn kh&ocirc;ng được m&agrave; sửa lại vẻ mặt như th&acirc;m cừu đại hận l&uacute;c trước, vui mừng ra mặt, &quot;L&agrave;m sao anh biết t&ocirc;i th&iacute;ch uống sữa chua?&quot;<br />\r\n<br />\r\nKh&oacute;e miệng anh hơi nhếch, &quot;Nữ sinh ngốc như c&ocirc; rất dễ đo&aacute;n.&quot;</p>\r\n\r\n<p><br />\r\n<br />\r\nTiếng phổ th&ocirc;ng anh c&oacute; ch&uacute;t cứng nhắc, ngẫu nhi&ecirc;n sẽ đọc sai mấy chữ, cho n&ecirc;n c&ocirc; kh&ocirc;ng ch&uacute; &yacute; tới lời n&oacute;i h&agrave;i hước của anh, vui vẻ m&agrave; mở ra một b&igrave;nh sữa chua nhỏ, kh&ocirc;ng l&atilde;ng ph&iacute; ch&uacute;t n&agrave;o m&agrave; liếm c&aacute;i nắp.<br />\r\n<br />\r\nKh&oacute;e mắt anh liếc thấy động t&aacute;c của c&ocirc;, kh&ocirc;ng nhịn được bất đắc dĩ bật cười, tr&ecirc;n k&iacute;nh chiếu hậu hiện l&ecirc;n đ&ocirc;i mắt đầy sủng nịch của anh, nhiều năm như vậy, bản t&iacute;nh tham ăn một ch&uacute;t cũng kh&ocirc;ng c&oacute; đổi, chỉ cần c&oacute; đồ ăn, hai mắt liền sẽ lập tức cong th&agrave;nh h&igrave;nh trăng nhỏ.<br />\r\n<br />\r\n6-【2】<br />\r\n<br />\r\nTống An Thần đứng trong ph&ograve;ng kh&aacute;ch, qu&eacute;t nh&igrave;n sương sương căn nh&agrave;, to&agrave;n bộ thiết kế nội thất ch&iacute;nh l&agrave; phong c&aacute;ch tươi m&aacute;t tự nhi&ecirc;n, hơi mang hương vị &quot;tiểu tư sản&quot;, so với tưởng tượng của anh kh&ocirc;ng kh&aacute;c biệt lắm.<br />\r\n<br />\r\n&quot;Ph&ograve;ng n&agrave;y về sau ch&iacute;nh l&agrave; ph&ograve;ng của anh.&quot; Hạ Chi T&igrave;nh chỉ v&agrave;o ph&ograve;ng ngủ phụ nh&igrave;n anh n&oacute;i.<br />\r\n<br />\r\nAnh k&eacute;o vali đến ph&ograve;ng ngủ phụ vừa thấy, sắc mặt lập tức đen, &quot;T&ocirc;i kh&ocirc;ng cần, t&ocirc;i muốn ở ph&ograve;ng ngủ ch&iacute;nh.&quot;<br />\r\n<br />\r\nC&ocirc; lập tức cự tuyệt: &quot;Ph&ograve;ng ngủ ch&iacute;nh t&ocirc;i ở rồi.&quot;<br />\r\n<br />\r\nVẻ mặt anh đương nhi&ecirc;n: &quot;C&ocirc; dọn đến ph&ograve;ng ngủ phụ đi.&quot;<br />\r\n<br />\r\nC&ocirc; giận: &quot;Dựa v&agrave;o c&aacute;i g&igrave; hả?&quot;<br />\r\n<br />\r\nAnh cười: &quot;Chỉ bằng mỗi th&aacute;ng t&ocirc;i trả tiền thu&ecirc; ba ng&agrave;n cũng đủ để thu&ecirc; trọn bộ ph&ograve;ng n&agrave;y, m&agrave; kh&ocirc;ng phải chỉ c&oacute; một ph&ograve;ng ngủ phụ với chiếc giường 1m2 n&agrave;y.&quot;<br />\r\n<br />\r\nBởi v&igrave; chỉ c&oacute; một người ở, c&ocirc; đem ph&ograve;ng ngủ phụ n&agrave;y kết hợp với thư ph&ograve;ng, như vậy ph&ograve;ng đ&atilde; nhỏ nay c&ograve;n nhỏ hơn, kệ s&aacute;ch rồi &aacute;n thư linh tinh, cho n&ecirc;n chỉ c&oacute; thể đặt vừa chiếc giường 1m2 n&agrave;y.<br />\r\n<br />\r\nAnh n&oacute;i xong, trực tiếp l&ocirc;i k&eacute;o vali đi đến ph&ograve;ng ngủ ch&iacute;nh, c&ocirc; nhanh ch&oacute;ng đuổi theo sau m&ocirc;ng, hai tay ngăn ở cửa ph&ograve;ng ngủ ch&iacute;nh, thề sống chết bảo vệ l&atilde;nh thổ của m&igrave;nh.<br />\r\n<br />\r\n&quot;Tr&aacute;nh ra.&quot;<br />\r\n<br />\r\n&quot;T&ocirc;i kh&ocirc;ng.&quot;<br />\r\n<br />\r\nAnh nh&igrave;n c&ocirc; dang hai c&aacute;nh tay, bộ d&aacute;ng g&agrave; mẹ bảo vệ g&agrave; con m&agrave; thật muốn cười ra tiếng, nhưng lời n&oacute;i ra lại l&atilde;nh khốc v&ocirc; t&igrave;nh: &quot;Cho c&ocirc; hai sự lựa chọn, một l&agrave; tr&aacute;nh ra, hai lập tức trả tiền.&quot;<br />\r\n<br />\r\n&quot;......&quot; A a a!! Thật muốn th&eacute;t ch&oacute;i tai, thật muốn rống giận, anh c&oacute; gan th&igrave; đừng lấy thứ n&agrave;y tới uy hiếp c&ocirc;? Ức hiếp người ngh&egrave;o a!! Đời n&agrave;y số lần c&ocirc; v&igrave; tiền m&agrave; nghẹn khuất c&ograve;n chưa nhiều hơn so với th&aacute;ng n&agrave;y!!<br />\r\n<br />\r\nVẻ mặt c&ocirc; kh&ocirc;ng cam l&ograve;ng rồi lại thấy đối phương một bộ dạng v&ocirc; t&igrave;nh, c&ocirc; đem th&acirc;n m&igrave;nh từng ch&uacute;t từng ch&uacute;t dịch ra khỏi chỗ, anh quyết đo&aacute;n k&eacute;o vali đi v&agrave;o, vẻ mặt nghẹn cười m&agrave; n&oacute;i một c&acirc;u: &quot;Thật ra c&ograve;n c&oacute; sự lựa chọn thứ ba.&quot;<br />\r\n<br />\r\nC&ocirc; nghe vậy đột nhi&ecirc;n ngẩng đầu, hai mắt lấp l&aacute;nh, &quot;Lựa chọn thứ ba l&agrave; g&igrave;?&quot;<br />\r\n<br />\r\nAnh chỉ về hướng chiếc giường, kh&oacute;e miệng như c&oacute; như kh&ocirc;ng m&agrave; nhếch l&ecirc;n n&oacute;i: &quot;Nếu c&ocirc; thật sự th&iacute;ch ph&ograve;ng ngủ ch&iacute;nh, c&oacute; thể ở lại, d&ugrave; sao giường đủ lớn, t&ocirc;i kh&ocirc;ng ngại c&ugrave;ng c&ocirc; ngủ một giường.&quot;<br />\r\n<br />\r\n&quot;...... T&ocirc;i để &yacute;!&quot; Anh đương nhi&ecirc;n kh&ocirc;ng ngại! Biếи ŧɦ&aacute;i, sắc lang! Mới vừa tiến v&agrave;o liền muốn chiếm tiện nghi c&ocirc;, Hạ Chi T&igrave;nh một b&ecirc;n chạy thu thập đồ vật của m&igrave;nh, một b&ecirc;n tự hỏi c&oacute; phải hay kh&ocirc;ng tự m&igrave;nh dẫn s&oacute;i v&agrave;o nh&agrave;?<br />\r\n<br />\r\nChạy đi chạy lại bốn năm lần, cuối c&ugrave;ng c&ocirc; cũng đem đa số đồ đạc dọn lại đ&acirc;y, bởi v&igrave; ph&ograve;ng ngủ phụ kh&ocirc;ng c&oacute; tủ quần &aacute;o, cho n&ecirc;n trừ bỏ mấy bộ quần &aacute;o mặc sau khi tắm rửa, quần &aacute;o kh&aacute;c vẫn l&agrave; tạm thời đặt ở b&ecirc;n trong ph&ograve;ng ngủ ch&iacute;nh, chờ cuối tuần đi mua tủ quần &aacute;o lại dọn tới đ&acirc;y.<br />\r\n<br />\r\nC&ocirc; mệt đến ng&atilde; v&agrave;o chiếc giường nhỏ 1m2, đi&ecirc;n cuồng đ&aacute; ch&acirc;n, nam nh&acirc;n th&uacute;i kh&ocirc;ng biết độ lượng, lớn l&ecirc;n đẹp c&oacute; lợi g&igrave; chứ? Cả ng&agrave;y chỉ biết lấy tiền tới uy hiếp c&ocirc;! Ha, vừa rồi c&ocirc; thu dọn đồ đạc đến mồ h&ocirc;i đầy đầu, anh ta th&igrave; hay rồi, hai tay &ocirc;m ngực m&agrave; nh&igrave;n c&ocirc; bận l&ecirc;n bận xuống, kh&ocirc;ng c&oacute; một ch&uacute;t &yacute; muốn gi&uacute;p đỡ!<br />\r\n<br />\r\nNam nh&acirc;n lớn l&ecirc;n đẹp trai quả nhi&ecirc;n kh&ocirc;ng c&oacute; thứ g&igrave; tốt, đều c&ugrave;ng với Hạ Chi Sơ như nhau &mdash;&mdash; b&ecirc;n ngo&agrave;i t&ocirc; v&agrave;ng nạm ngọc b&ecirc;n trong thối rữa, từ h&ocirc;m nay trở đi, trong mục ti&ecirc;u muốn ngược chết cả đời của c&ocirc; trừ bỏ Hạ Chi Sơ b&acirc;y giờ c&ograve;n th&ecirc;m Tống An Thần.<br />\r\n<br />\r\nCả ph&ograve;ng b&ecirc;n n&agrave;y tỏa ra o&aacute;n kh&iacute;, m&agrave; c&aacute;ch một bức tường trong căn ph&ograve;ng kh&aacute;c lại l&agrave; một t&igrave;nh cảnh kh&aacute;c nhau.<br />\r\n<br />\r\nAnh mở tủ quần &aacute;o ra, đem quần &aacute;o của m&igrave;nh nhất nhất treo l&ecirc;n b&ecirc;n tr&aacute;i tủ quần &aacute;o, b&ecirc;n phải kia treo đầy quần &aacute;o của c&ocirc;, anh nh&igrave;n chằm chằm một quầy quần &aacute;o, kh&oacute;e miệng nhịn kh&ocirc;ng được nhếch l&ecirc;n, tr&agrave;n đầy nhu t&igrave;nh.<br />\r\n<br />\r\nĐ&oacute;ng lại cửa tủ quần &aacute;o, vừa l&uacute;c điện thoại reo l&ecirc;n:<br />\r\n<br />\r\n&quot;Tống tổng, video gi&aacute;m s&aacute;t đ&ecirc;m nay của c&ocirc;ng ty đ&atilde; dựa theo anh ph&acirc;n ph&oacute; x&oacute;a rồi, anh c&ograve;n c&oacute; chuyện g&igrave; ph&acirc;n ph&oacute; nữa kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute;, c&oacute; điều t&ocirc;i kh&ocirc;ng hy vọng nghe được trong miệng người thứ ba bất luận c&aacute;i g&igrave; về chuyện đ&ecirc;m nay, anh hiểu kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;Hiểu r&otilde;, v&ocirc; c&ugrave;ng hiểu r&otilde;! Tống tổng anh cứ việc y&ecirc;n t&acirc;m, chuyện đ&ecirc;m nay trừ bỏ anh c&ugrave;ng với t&ocirc;i, t&ocirc;i bảo đảm tuyệt đối sẽ kh&ocirc;ng c&oacute; người thứ ba biết.&quot;<br />\r\n<br />\r\nSau khi c&uacute;p điện thoại, anh đi đến ban c&ocirc;ng, gi&oacute; đ&ecirc;m thổi v&agrave;o mặt, &aacute;nh trăng b&ecirc;n cửa sổ, chiếu s&aacute;ng cả căn ph&ograve;ng.<br />\r\n<br />\r\nB&oacute;ng tối đ&ecirc;m nay kh&ocirc;ng tồi!<br />\r\n<br />\r\n6-【3】<br />\r\n<br />\r\nTr&ecirc;n người Hạ Chi T&igrave;nh c&oacute; hai thứ l&agrave;m c&ocirc; lấy l&agrave;m tự h&agrave;o nhất, một c&aacute;i l&agrave; mắt biến th&agrave;nh trăng rằm khi cười rộ l&ecirc;n, một c&aacute;i nữa l&agrave; bản lĩnh nằm đ&acirc;u ngủ đ&oacute;. C&aacute;i trước l&agrave;m c&ocirc; tươi cười tr&ocirc;ng thật dễ thương, c&aacute;i sau l&agrave;m c&ocirc; kh&ocirc;ng biết mất ngủ l&agrave; g&igrave;.<br />\r\n<br />\r\nC&ocirc; c&oacute; thể nằm ngủ, ngồi ngủ, nằm b&ograve; ngủ, ngay cả đứng cũng c&oacute; thể ngủ, c&ocirc; tự phong m&igrave;nh l&agrave; Đ&ocirc;ng phương gi&aacute;o* chủ, c&ograve;n cảm thấy rất c&oacute; kh&iacute; thế, Hạ Chi Sơ lại kh&ocirc;ng cho mặt mũi m&agrave; khịt mũi coi thường, ban cho c&ocirc; một c&aacute;i danh kh&aacute;c: Đ&ocirc;ng phương gi&aacute;o trư**.<br />\r\n<br />\r\n* 东方觉主 (dōng fāng ji&agrave;o zhǔ): gi&aacute;o ở đ&acirc;y l&agrave; ngủ nha mọi người.<br />\r\n<br />\r\n** 东方叫猪 (dōng fāng ji&agrave;o zhū): đồng &acirc;m, &yacute; của Hạ Chi Sơ l&agrave; Hạ Chi T&igrave;nh ngủ như heo.<br />\r\n<br />\r\nNhớ năm xưa, c&ocirc; c&ugrave;ng Hạ Chi Sơ ngồi xe bus c&ugrave;ng nhau đi học, nhờ Hạ Chi Sơ tới trạm đ&aacute;nh thức c&ocirc; dậy, ch&iacute;nh l&agrave; Hạ Chi Sơ c&aacute;i t&ecirc;n đ&ecirc; tiện n&agrave;y lại bỏ c&ocirc; một m&igrave;nh xuống xe, để c&ocirc; bị t&agrave;i xế đ&aacute;nh thức khi đến cuối trạm, v&igrave; thế n&ecirc;n đến muộn, to&agrave;n bộ năm trung học đều bị phạt đứng.<br />\r\n<br />\r\nBuổi s&aacute;ng ng&agrave;y h&ocirc;m sau bị đồng hồ b&aacute;o thức ồn tỉnh, Hạ Chi T&igrave;nh ph&aacute;t hiện ch&iacute;nh m&igrave;nh cuộn lại tr&ecirc;n s&agrave;n nh&agrave;, c&ocirc; duỗi c&aacute;i eo lười hai c&aacute;i, l&uacute;c n&agrave;y mới lưu luyến kh&ocirc;ng rời m&agrave; b&ograve; dậy rửa mặt.<br />\r\n<br />\r\nC&ocirc; nhắm mắt bay tới cửa toilet, duỗi tay vặn cửa toilet, xoay v&agrave;i c&aacute;i cũng kh&ocirc;ng kh&oacute;a được, c&ocirc; trực tiếp dựa v&agrave;o cửa ngủ lu&ocirc;n.<br />\r\n<br />\r\nBỗng nhi&ecirc;n, cửa &quot;phanh&quot; một tiếng bị mở ra, bởi v&igrave; qu&aacute;n t&iacute;nh cả người c&ocirc; ng&atilde; về ph&iacute;a trước, anh tay mắt lanh lẹ m&agrave; bắt được c&ocirc; &quot;nh&agrave;o v&agrave;o trong ngực&quot;.<br />\r\n<br />\r\nC&ocirc; &quot;phịch&quot; một tiếng giống như đụng phải tấm v&aacute;n sắt l&agrave;m bằng thịt, cứng như vậy tự nhi&ecirc;n c&aacute;i tr&aacute;ng chịu tội, chỉ l&agrave; tr&ecirc;n mặt cọ cọ cảm x&uacute;c thật l&agrave; tốt, ấm &aacute;p rắn chắc, so với cửa lạnh như băng, dựa l&ecirc;n thoải m&aacute;i rất nhiều, c&ocirc; lấy mặt cọ cọ, mắt thấy lại muốn ngủ tiếp đi.<br />\r\n<br />\r\nTống An Thần c&uacute;i đầu, xem bộ d&aacute;ng c&ocirc; giống như m&egrave;o con tham ngủ, mắt phượng hiện l&ecirc;n một tia &yacute; cười, duỗi tay đẩy đẩy c&ocirc;, trước giờ c&ocirc; lu&ocirc;n tức giận mỗi khi rời giường, c&ocirc; nh&iacute;u lại m&agrave;y đang muốn mắng chửi người, mở to mắt lại nh&igrave;n thấy ch&iacute;nh m&igrave;nh dựa tr&ecirc;n một bộ ngực rộng lớn, c&ocirc; ngẩng ngẩng đầu, một khu&ocirc;n mặt tuấn nhan họa thủy c&uacute;i đầu nh&igrave;n c&ocirc;.<br />\r\n<br />\r\nĐầu &oacute;c c&ocirc; nhất thời phản ứng kh&ocirc;ng kịp, anh ta v&igrave; sao sẽ xuất hiện ở chỗ n&agrave;y, bỗng nghe được bộ d&aacute;ng anh thần th&aacute;nh kh&ocirc;ng thể x&acirc;m phạm n&oacute;i: &quot;Hạ tiểu thư, xin c&ocirc; tự trọng.&quot;<br />\r\n<br />\r\nTầm mắt anh chỉ v&agrave;o c&ocirc; d&aacute;n ở tr&ecirc;n ngực m&igrave;nh, c&ocirc; phản ứng chậm nửa nhịp, l&uacute;c n&agrave;y mới phản ứng lại, h&eacute;t l&ecirc;n một tiếng đẩy ngực anh ra, đầu lưỡi như l&agrave; bị con thỏ cắn, &quot;Anh...... Anh......&quot;<br />\r\n<br />\r\nC&ocirc; vốn định hỏi v&igrave; sao anh sẽ xuất hiện ở chỗ n&agrave;y, đầu &oacute;c lại ở ngay l&uacute;c n&agrave;y chuyển động, nhớ tới chuyện thu&ecirc; ở chung của bọn họ.<br />\r\n<br />\r\n&quot;H&agrave;nh vi của c&ocirc; thật khiến t&ocirc;i bối rối, hy vọng Hạ tiểu thư về sau c&oacute; thể rụt r&egrave; một ch&uacute;t.&quot; Biểu t&igrave;nh anh l&uacute;c n&atilde;y giống như Đường Tăng đi phương T&acirc;y lấy kinh bị nữ y&ecirc;u t&igrave;nh m&ecirc; quấy nhiễu như đ&uacute;c.<br />\r\n<br />\r\n&quot;...... T&ocirc;i......&quot; Rụt r&egrave;! C&ocirc; cả đời rụt r&egrave;, chưa bao giờ ph&oacute;ng t&uacute;ng, v&igrave; c&aacute;i g&igrave; sau khi gặp được anh ta, l&uacute;c n&agrave;o cũng gặp phải chuyện nhẫm lẫn xấu hổ n&agrave;y? L&uacute;c trước l&agrave; ở qu&aacute;n c&agrave; ph&ecirc; nắm nhầm quần anh ta, lần n&agrave;y lại &quot;nh&agrave;o v&agrave;o trong ngực&quot;, c&ocirc; cho d&ugrave; nhảy xuống s&ocirc;ng Ho&agrave;ng H&agrave; cũng kh&ocirc;ng rửa sạch a!<br />\r\n<br />\r\nMặt c&ocirc; như bốc ra lửa, cảm gi&aacute;c như thi&ecirc;u đốt, chột dạ m&agrave; c&uacute;i đầu, lại nh&igrave;n thấy trước ngực của m&igrave;nh nh&ocirc; ra hai c&aacute;i hạt đậu phộng.<br />\r\n<br />\r\nC&ocirc;! Lộ! Ngực! Rồi!!!<br />\r\n<br />\r\nNhư một cơn gi&oacute; lốc, c&ocirc; h&eacute;t l&ecirc;n một tiếng chạy về trong ph&ograve;ng, &quot;Phanh&quot; một tiếng đ&oacute;ng cửa lại!<br />\r\n<br />\r\nThật l&agrave; qu&aacute; mất mặt! Mất mặt qu&aacute; b&agrave; nội ơi!! C&ocirc; giống như con đ&agrave; điểu, v&ugrave;i đầu v&agrave;o b&ecirc;n trong chăn, ph&aacute;t đi&ecirc;n đến k&ecirc;u oa oa, &aacute;o ngủ tr&ecirc;n người c&ocirc; l&agrave; kiểu d&aacute;ng b&agrave; gi&agrave;, loại n&agrave;y rất mỏng, xấu kh&ocirc;ng n&oacute;i, c&ograve;n rất dễ d&agrave;ng nh&igrave;n thấy hai hạt đậu phộng.<br />\r\n<br />\r\nKiểu d&aacute;ng &aacute;o ngủ như vậy c&ocirc; c&oacute; ba bộ, to&agrave;n bộ đều l&agrave; do mẹ T&ocirc; l&ecirc;n Taobao mua cho c&ocirc;, n&oacute;i ngọt l&agrave; đồng phục của mẹ v&agrave; con g&aacute;i, kh&ocirc;ng cho c&ocirc; cự tuyệt, l&uacute;c trước c&ocirc; sống một m&igrave;nh, nghĩ n&eacute;m th&igrave; thấy tiếc, anh ấy h&ocirc;m qua mới dọn tới ở, c&ocirc; dọn dẹp đồ đạc một ch&uacute;t liền đi ngủ cũng kh&ocirc;ng suy x&eacute;t nhiều như vậy.<br />\r\n<br />\r\nChuyện bi ai nhất thế gian kh&ocirc;ng g&igrave; hơn khi c&oacute; mẹ l&agrave; người kỳ ba như vậy.<br />\r\n<br />\r\nNghe được trong ph&ograve;ng thường truyền đến tiếng k&ecirc;u r&ecirc;n của c&ocirc;, Tống An Thần kh&oacute;e miệng nhịn kh&ocirc;ng được giơ l&ecirc;n, c&oacute; điều phẩm vị &aacute;o ngủ của c&ocirc; thật kh&ocirc;ng d&aacute;m khen.</p>', '0', 'dan-soi-vao-nha', 4, '272', '2022-08-03 04:09:58', '2022-08-03 04:09:58');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(38, '6', 'Máu tanh thịnh yến (một)', '<p>C&aacute;i n&agrave;y th&ocirc;n trang nhỏ, nơi n&agrave;o c&oacute; cảnh s&aacute;t? C&oacute; đại sự xảy ra, cũng ch&iacute;nh l&agrave; th&ocirc;n trưởng quản sự, m&agrave; mọi người ai kh&ocirc;ng biết ai a? trộm cướp, mưu s&aacute;t xưa nay đều sẽ kh&ocirc;ng ph&aacute;t sinh, cho n&ecirc;n, Di&ecirc;m Hạo Thi&ecirc;n mất t&iacute;ch, trở th&agrave;nh th&ocirc;n n&agrave;y một kiện đại sự.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; Di&ecirc;m Hạo Thi&ecirc;n mất t&iacute;ch cũng th&agrave;nh một kiện đại sự. Người trong th&ocirc;n thậm ch&iacute; tự ph&aacute;t tổ chức, đến phụ cận c&aacute;c nơi t&igrave;m kiếm hắn. Di&ecirc;m Hạo Thi&ecirc;n d&ugrave; sao nh&acirc;n duy&ecirc;n cũng kh&ocirc;ng sai, mọi người cũng kh&ocirc;ng hi vọng hắn xảy ra chuyện.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ph&aacute;t hiện hắn mất t&iacute;ch thời điểm, c&ograve;n ph&aacute;t sinh một bạo động.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đ&acirc;y l&agrave; L&yacute; Băng b&aacute;o th&ugrave;, l&agrave; n&agrave;ng b&aacute;o th&ugrave; a!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c ấy, La Hằng Vi&ecirc;m liền đi theo Trương th&ocirc;n trưởng b&ecirc;n người, đi Di&ecirc;m Hạo Thi&ecirc;n trong nh&agrave; xem x&eacute;t, cũng biết Di&ecirc;m Hạo Thi&ecirc;n, ch&iacute;nh l&agrave; ng&agrave;y h&ocirc;m qua trung ni&ecirc;n nam nh&acirc;n. Nhưng m&agrave;, liền tại bọn họ đi ra Di&ecirc;m Hạo Thi&ecirc;n gia thời điểm, một l&agrave;n da hơi đen thui nữ t&iacute;nh, bỗng nhi&ecirc;n hoảng sợ h&ocirc; l&ecirc;n ph&iacute;a tr&ecirc;n c&acirc;u n&oacute;i n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Băng c&aacute;i t&ecirc;n n&agrave;y, đ&atilde; kh&ocirc;ng phải l&agrave; lần đầu ti&ecirc;n nghe được, La Hằng Vi&ecirc;m liệu định, lần n&agrave;y chỉ thị chữ bằng m&aacute;u, hơn ph&acirc;n nửa c&ugrave;ng n&agrave;ng c&oacute; quan hệ. Thế l&agrave;, kh&ocirc;ng ch&uacute;t nghĩ ngợi hỏi: &quot;C&oacute; &yacute; tứ g&igrave;? L&yacute; Băng n&agrave;ng đến c&ugrave;ng l&agrave; ai?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia m&agrave;u da đen thui nữ t&iacute;nh xem l&ecirc;n ước chừng kh&ocirc;ng đến hai mươi tuổi, v&oacute;c d&aacute;ng rất cao, n&agrave;ng vừa muốn n&oacute;i c&aacute;i g&igrave;, ph&iacute;a sau n&agrave;ng một cao lớn th&ocirc; kệch nam nh&acirc;n vội v&agrave;ng ngăn cản n&agrave;ng n&oacute;i tiếp, qu&aacute;t bảo ngưng lại n&oacute;i: &quot;A Cầm, ngươi chớ c&oacute; n&oacute;i hươu n&oacute;i vượn! Kh&ocirc;ng c&oacute; sự! C&aacute;c vị, c&aacute;c ngươi chớ để &yacute;. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ai biết được?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bỗng nhi&ecirc;n một thanh &acirc;m lạnh l&ugrave;ng truyền đến, La Hằng Vi&ecirc;m lập tức theo tiếng nh&igrave;n lại, lại l&agrave; h&ocirc;m qua bọn họ gặp được kia g&aacute;nh th&ugrave;ng nước nữ h&agrave;i A T&uacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A T&uacute;, ngươi. . .&quot; Kia cao lớn th&ocirc; kệch nam nh&acirc;n vội n&oacute;i: &quot;Ngươi, ngươi đang n&oacute;i c&aacute;i g&igrave;. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Những năm gần đ&acirc;y, th&ocirc;n cũng kh&ocirc;ng chỉ một lần ph&aacute;t sinh qu&aacute;i sự đi? Ha ha, c&aacute;c ngươi đều c&oacute; tật giật m&igrave;nh, kh&ocirc;ng d&aacute;m n&oacute;i th&ecirc;m c&aacute;i g&igrave;, nhưng l&agrave;. . . Ta rất r&otilde; r&agrave;ng đ&acirc;u. Đ&acirc;y l&agrave; Băng Nhi tỷ tỷ đối với c&aacute;c ngươi nguyền rủa a!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c n&agrave;y người trong th&ocirc;n đa số đều tụ tập tại n&agrave;y, mọi người nh&igrave;n A T&uacute; &aacute;nh mắt, nhất thời đều kh&ocirc;ng d&aacute;m n&oacute;i chuyện, mỗi người, đều l&agrave; thần sắc hoảng hốt, cảm thấy bất an.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>La Hằng Vi&ecirc;m bắt đầu cảm gi&aacute;c được, ở trong đ&oacute; x&aacute;c thực tồn tại tương đối lớn Huyền Cơ. Mặc d&ugrave; l&agrave; nghe n&oacute;i c&oacute; nh&aacute;o quỷ thuyết ph&aacute;p, nhưng cụ thể c&ograve;n kh&ocirc;ng r&otilde; r&agrave;ng lắm.</p>', '<p>C&aacute;i n&agrave;y th&ocirc;n trang nhỏ, nơi n&agrave;o c&oacute; cảnh s&aacute;t? C&oacute; đại sự xảy ra, cũng ch&iacute;nh l&agrave; th&ocirc;n trưởng quản sự, m&agrave; mọi người ai kh&ocirc;ng biết ai a? trộm cướp, mưu s&aacute;t xưa nay đều sẽ kh&ocirc;ng ph&aacute;t sinh, cho n&ecirc;n, Di&ecirc;m Hạo Thi&ecirc;n mất t&iacute;ch, trở th&agrave;nh th&ocirc;n n&agrave;y một kiện đại sự.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&agrave; Di&ecirc;m Hạo Thi&ecirc;n mất t&iacute;ch cũng th&agrave;nh một kiện đại sự. Người trong th&ocirc;n thậm ch&iacute; tự ph&aacute;t tổ chức, đến phụ cận c&aacute;c nơi t&igrave;m kiếm hắn. Di&ecirc;m Hạo Thi&ecirc;n d&ugrave; sao nh&acirc;n duy&ecirc;n cũng kh&ocirc;ng sai, mọi người cũng kh&ocirc;ng hi vọng hắn xảy ra chuyện.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ph&aacute;t hiện hắn mất t&iacute;ch thời điểm, c&ograve;n ph&aacute;t sinh một bạo động.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Đ&acirc;y l&agrave; L&yacute; Băng b&aacute;o th&ugrave;, l&agrave; n&agrave;ng b&aacute;o th&ugrave; a!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c ấy, La Hằng Vi&ecirc;m liền đi theo Trương th&ocirc;n trưởng b&ecirc;n người, đi Di&ecirc;m Hạo Thi&ecirc;n trong nh&agrave; xem x&eacute;t, cũng biết Di&ecirc;m Hạo Thi&ecirc;n, ch&iacute;nh l&agrave; ng&agrave;y h&ocirc;m qua trung ni&ecirc;n nam nh&acirc;n. Nhưng m&agrave;, liền tại bọn họ đi ra Di&ecirc;m Hạo Thi&ecirc;n gia thời điểm, một l&agrave;n da hơi đen thui nữ t&iacute;nh, bỗng nhi&ecirc;n hoảng sợ h&ocirc; l&ecirc;n ph&iacute;a tr&ecirc;n c&acirc;u n&oacute;i n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Băng c&aacute;i t&ecirc;n n&agrave;y, đ&atilde; kh&ocirc;ng phải l&agrave; lần đầu ti&ecirc;n nghe được, La Hằng Vi&ecirc;m liệu định, lần n&agrave;y chỉ thị chữ bằng m&aacute;u, hơn ph&acirc;n nửa c&ugrave;ng n&agrave;ng c&oacute; quan hệ. Thế l&agrave;, kh&ocirc;ng ch&uacute;t nghĩ ngợi hỏi: &quot;C&oacute; &yacute; tứ g&igrave;? L&yacute; Băng n&agrave;ng đến c&ugrave;ng l&agrave; ai?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia m&agrave;u da đen thui nữ t&iacute;nh xem l&ecirc;n ước chừng kh&ocirc;ng đến hai mươi tuổi, v&oacute;c d&aacute;ng rất cao, n&agrave;ng vừa muốn n&oacute;i c&aacute;i g&igrave;, ph&iacute;a sau n&agrave;ng một cao lớn th&ocirc; kệch nam nh&acirc;n vội v&agrave;ng ngăn cản n&agrave;ng n&oacute;i tiếp, qu&aacute;t bảo ngưng lại n&oacute;i: &quot;A Cầm, ngươi chớ c&oacute; n&oacute;i hươu n&oacute;i vượn! Kh&ocirc;ng c&oacute; sự! C&aacute;c vị, c&aacute;c ngươi chớ để &yacute;. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ai biết được?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bỗng nhi&ecirc;n một thanh &acirc;m lạnh l&ugrave;ng truyền đến, La Hằng Vi&ecirc;m lập tức theo tiếng nh&igrave;n lại, lại l&agrave; h&ocirc;m qua bọn họ gặp được kia g&aacute;nh th&ugrave;ng nước nữ h&agrave;i A T&uacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A T&uacute;, ngươi. . .&quot; Kia cao lớn th&ocirc; kệch nam nh&acirc;n vội n&oacute;i: &quot;Ngươi, ngươi đang n&oacute;i c&aacute;i g&igrave;. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Những năm gần đ&acirc;y, th&ocirc;n cũng kh&ocirc;ng chỉ một lần ph&aacute;t sinh qu&aacute;i sự đi? Ha ha, c&aacute;c ngươi đều c&oacute; tật giật m&igrave;nh, kh&ocirc;ng d&aacute;m n&oacute;i th&ecirc;m c&aacute;i g&igrave;, nhưng l&agrave;. . . Ta rất r&otilde; r&agrave;ng đ&acirc;u. Đ&acirc;y l&agrave; Băng Nhi tỷ tỷ đối với c&aacute;c ngươi nguyền rủa a!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c n&agrave;y người trong th&ocirc;n đa số đều tụ tập tại n&agrave;y, mọi người nh&igrave;n A T&uacute; &aacute;nh mắt, nhất thời đều kh&ocirc;ng d&aacute;m n&oacute;i chuyện, mỗi người, đều l&agrave; thần sắc hoảng hốt, cảm thấy bất an.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>La Hằng Vi&ecirc;m bắt đầu cảm gi&aacute;c được, ở trong đ&oacute; x&aacute;c thực tồn tại tương đối lớn Huyền Cơ. Mặc d&ugrave; l&agrave; nghe n&oacute;i c&oacute; nh&aacute;o quỷ thuyết ph&aacute;p, nhưng cụ thể c&ograve;n kh&ocirc;ng r&otilde; r&agrave;ng lắm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Quả thật. . . L&agrave; kia gọi L&yacute; Băng, oan hồn bất t&aacute;n?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Hừ! A T&uacute;, ngươi đừng y&ecirc;u ng&ocirc;n hoặc ch&uacute;ng!&quot; L&uacute;c n&agrave;y, một người đeo k&iacute;nh k&iacute;nh t&oacute;c ngắn nữ h&agrave;i, c&ugrave;ng một th&acirc;n thể mập mạp nam nh&acirc;n đều đi tới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kia nam nh&acirc;n mập th&igrave; l&agrave; hung tợn nh&igrave;n chằm chằm n&agrave;ng, n&oacute;i: &quot;Ngươi cũng đừng qu&ecirc;n, L&yacute; Băng l&agrave; tự s&aacute;t! Việc kh&ocirc;ng li&ecirc;n quan đến ch&uacute;ng ta!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Tự s&aacute;t? Tống Thi&ecirc;n, ngươi thật đ&uacute;ng l&agrave; kh&ocirc;ng biết xấu hổ n&oacute;i a! L&uacute;c trước, ngươi l&agrave; thế n&agrave;o đối đ&atilde;i Băng Nhi tỷ tỷ?&quot; A T&uacute; hung tợn nh&igrave;n chằm chằm hắn, n&oacute;i: &quot;Băng Nhi tỷ tỷ l&agrave; c&ugrave;ng ch&uacute;ng ta c&ugrave;ng nhau lớn l&ecirc;n, nhưng l&agrave;, chuyện kia về sau, c&aacute;c ngươi tất cả mọi người coi n&agrave;ng l&agrave; l&agrave;m &ocirc;n thần đối đ&atilde;i, đối n&agrave;ng ch&acirc;m chọc khi&ecirc;u kh&iacute;ch, thậm ch&iacute; c&ograve;n hướng n&agrave;ng n&eacute;m tảng đ&aacute;! Ta tận mắt nh&igrave;n thấy qua nhiều lần!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi. . .&quot; Kia nam nh&acirc;n mập Tống Thi&ecirc;n bị n&agrave;ng một trận mỉa mai, n&oacute;i: &quot;Vậy, vậy lại như thế n&agrave;o! N&agrave;ng l&agrave; mẹ của n&agrave;ng trộm h&aacute;n tử sinh con hoang! Ch&uacute;ng ta lưu n&agrave;y con hoang ở tại th&ocirc;n, đ&atilde; l&agrave; đối n&agrave;ng lớn lao &acirc;n huệ, n&agrave;ng c&ograve;n c&oacute; tư c&aacute;ch gh&eacute;t bỏ c&aacute;i g&igrave;?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi c&ograve;n d&aacute;m đem hai chữ kia n&oacute;i một lần!&quot; L&uacute;c n&agrave;y A T&uacute; l&ecirc;n cơn giận dữ, thậm ch&iacute; muốn vọt qua đến, nhưng m&agrave; Tống Thi&ecirc;n b&ecirc;n người kia đeo mắt k&iacute;nh c&ocirc; g&aacute;i t&oacute;c ngắn tốc độ c&agrave;ng nhanh, một bước nhanh về ph&iacute;a trước, liền đối n&agrave;ng vả một bạt tai!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi c&oacute; gan động đến hắn thử một ch&uacute;t! Ở trước mặt ta đ&aacute;nh ta nam nh&acirc;n, ngươi coi ta kh&ocirc;ng tồn tại sao?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi. . . C&aacute;t Linh. . .&quot; A T&uacute; nhất thời c&oacute; ch&uacute;t kinh ngạc, che lấy bị đ&aacute;nh mặt, nh&igrave;n n&agrave;ng một bộ kh&iacute; thế hung hăng bộ d&aacute;ng, bỗng nhi&ecirc;n một đầu vọt tới kia t&oacute;c ngắn k&iacute;nh mắt nữ, tiếp gắt gao nắm chặt t&oacute;c của n&agrave;ng, h&ocirc;: &quot;Ngươi cũng l&agrave; đồng l&otilde;a! Băng Nhi tỷ tỷ chết ngươi cũng c&oacute; phần! Ta cũng sẽ kh&ocirc;ng bỏ qua ngươi!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cứ việc tất cả mọi người tiến l&ecirc;n muốn k&eacute;o ra A T&uacute;, nhưng l&agrave; kh&iacute; lực n&agrave;ng qu&aacute; lớn, đem t&oacute;c ngắn k&iacute;nh mắt nữ t&oacute;c nắm chặt được một đo&agrave;n loạn, thậm ch&iacute; c&ograve;n đi c&agrave;o mặt n&agrave;ng, xem một b&ecirc;n La Hằng Vi&ecirc;m mở rộng tầm mắt, h&ocirc;m qua đối c&ocirc; b&eacute; n&agrave;y ấn tượng đầu ti&ecirc;n l&agrave; rất văn tĩnh chất ph&aacute;c n&ocirc;ng gia nữ h&agrave;i, kh&ocirc;ng nghĩ tới thế m&agrave; như vậy mạnh mẽ? Hẳn l&agrave; đầu năm nay ngay cả n&ocirc;ng th&ocirc;n cũng bắt đầu nhận phi chủ lưu độc hại rồi?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;, cuối c&ugrave;ng cơ bản minh bạch L&yacute; Băng sự kiện ch&acirc;n tướng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&oacute;i c&aacute;ch kh&aacute;c. . . N&agrave;ng l&agrave; c&ugrave;ng trong l&agrave;ng thế hệ trẻ tuổi người c&ugrave;ng nhau lớn l&ecirc;n, c&ugrave;ng c&aacute;i n&agrave;y A T&uacute; quan hệ tốt nhất. M&agrave; sau đ&oacute; n&agrave;ng bị ph&aacute;t hiện kỳ thực l&agrave; mẫu th&acirc;n y&ecirc;u đương vụng trộm sinh hạ, đối quan niệm bảo thủ n&ocirc;ng th&ocirc;n nh&acirc;n tới n&oacute;i, tự nhi&ecirc;n l&agrave; kh&ocirc;ng quen nh&igrave;n n&agrave;ng. Đo&aacute;n chừng đối n&agrave;ng tiến h&agrave;nh phi thường lớn vũ nhục, n&agrave;ng kh&ocirc;ng chịu nổi, cho n&ecirc;n lựa chọn ph&iacute; ho&agrave;i bản th&acirc;n m&igrave;nh.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;, liền t&iacute;nh n&agrave;ng thật &acirc;m hồn bất t&aacute;n, ch&iacute;nh m&igrave;nh c&ugrave;ng L&yacute; Ẩn bọn họ, c&ugrave;ng n&agrave;y ho&agrave;n to&agrave;n kh&ocirc;ng o&aacute;n kh&ocirc;ng cừu, kh&ocirc;ng c&oacute; đạo l&yacute; tổn thương bọn họ a? Nhưng l&agrave;, kia chung cư l&agrave; kh&ocirc;ng thể n&agrave;o khiến bọn họ đến nơi kh&ocirc;ng c&oacute; nguy hiểm đi, nếu muốn bọn họ ở một th&aacute;ng, vậy c&aacute;i n&agrave;y một th&aacute;ng tuyệt đối kh&ocirc;ng thể n&agrave;o kh&ocirc;ng ph&aacute;t sinh bất cứ chuyện g&igrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sau n&agrave;y, hỏi thăm một ch&uacute;t th&ocirc;n trưởng, hắn hiểu c&agrave;ng th&ecirc;m tỉ mỉ ch&uacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Băng phụ mẫu đều l&agrave; bản th&ocirc;n nh&acirc;n, n&agrave;ng từ nhỏ c&ugrave;ng trong l&agrave;ng h&agrave;i tử c&ugrave;ng nhau lớn l&ecirc;n, nh&acirc;n duy&ecirc;n vẫn lu&ocirc;n rất tốt, t&iacute;nh c&aacute;ch phi thường hoạt b&aacute;t s&aacute;ng sủa. Trong đ&oacute;, A T&uacute; c&ugrave;ng n&agrave;ng t&igrave;nh cảm l&agrave; tốt nhất, c&ugrave;ng chị em ruột cơ hồ kh&ocirc;ng c&oacute; kh&aacute;c biệt. Người trẻ tuổi b&ecirc;n trong, c&ugrave;ng L&yacute; Băng ni&ecirc;n kỷ tiếp cận nhất, ch&iacute;nh l&agrave; nh&agrave; trưởng th&ocirc;n Hồng Vũ, Tố Nguyệt, Lương Nh&acirc;n B&acirc;n, vừa rồi Tống Thi&ecirc;n, C&aacute;t Linh vợ chồng, c&ugrave;ng với kia l&agrave;n da ngăm đen Thiết Cầm, kia cao lớn th&ocirc; kệch nam nh&acirc;n l&agrave; phụ th&acirc;n n&agrave;ng Thiết Mục, l&agrave; trong l&agrave;ng thợ đ&aacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Năm năm trước kia một đ&ecirc;m b&ecirc;n tr&ecirc;n, L&yacute; Băng mẫu th&acirc;n, bởi v&igrave; uống rượu qu&aacute; nhiều, lại c&ograve;n n&oacute;i lỡ miệng, nguy&ecirc;n lai, L&yacute; Băng l&agrave; n&agrave;ng c&ugrave;ng trước kia một đến th&ocirc;n người ngo&agrave;i th&ocirc;n th&ocirc;ng d&acirc;m sinh hạ. N&agrave;ng cẩn thận t&iacute;nh to&aacute;n qua, h&agrave;i tử hẳn l&agrave; kia người ngo&agrave;i th&ocirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Chuyện n&agrave;y, khiến L&yacute; Băng phụ th&acirc;n giận t&iacute;m mặt, cơ hồ đem th&ecirc; tử đ&aacute;nh chết. Sau n&agrave;y, hắn c&agrave;ng l&agrave; nổi giận đ&ugrave;ng đ&ugrave;ng rời khỏi th&ocirc;n, n&oacute;i muốn đi t&igrave;m đến kia gian phu, đến nay cũng kh&ocirc;ng trở về nữa. M&agrave; L&yacute; Băng mẫu nữ, tự nhi&ecirc;n ở trong th&ocirc;n nhận hết bạch nh&atilde;n, kết quả L&yacute; Băng mẫu th&acirc;n tại sầu n&atilde;o uất ức b&ecirc;n dưới bệnh chết. M&agrave; l&uacute;c kia, chỉ c&oacute; A T&uacute; trợ gi&uacute;p L&yacute; Băng xử l&yacute; mẫu th&acirc;n của n&agrave;ng tang sự.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Những ng&agrave;y tiếp theo. . . Đối L&yacute; Băng tới n&oacute;i, x&aacute;c thực giống như Địa Ngục.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Người trong th&ocirc;n, nh&igrave;n n&agrave;ng &aacute;nh mắt, đều tựa như l&agrave; đang nh&igrave;n r&aacute;c rưởi. Tất cả mọi người cảm gi&aacute;c n&agrave;ng rất dơ bẩn, vừa nh&igrave;n thấy n&agrave;ng hoặc l&agrave; liền tr&aacute;nh đi, hoặc l&agrave; liền đối n&agrave;ng tiến h&agrave;nh vũ nhục.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cuối c&ugrave;ng, dạng n&agrave;y ng&agrave;y k&eacute;o d&agrave;i đại kh&aacute;i thời gian một năm, một ng&agrave;y n&agrave;o đ&oacute;, L&yacute; Băng nhảy v&agrave;o dưới th&aacute;c nước, tự s&aacute;t. A T&uacute; bởi vậy kh&oacute;c v&agrave;i ng&agrave;y thời gian, cuối c&ugrave;ng n&agrave;ng c&ograve;n một tay v&igrave; L&yacute; Băng xử l&yacute; tang sự.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trở lại nh&agrave; trưởng th&ocirc;n sau, La Hằng Vi&ecirc;m đem chứng kiến hết thảy, đều n&oacute;i cho vừa mới tỉnh lại L&yacute; Ẩn, c&ugrave;ng với Tần Thủ Thi&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn đầu ti&ecirc;n l&agrave; x&aacute;c nhận cửa ph&ograve;ng đ&oacute;ng chặt, ngay sau đ&oacute;, thấp giọng, đối La Hằng Vi&ecirc;m c&ugrave;ng Tần Thủ Thi&ecirc;n n&oacute;i ra: &quot;Đại kh&aacute;i t&igrave;nh huống ta hiểu được. Nơi n&agrave;y, kh&ocirc;ng thể lại ở đi xuống.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A?&quot; La Hằng Vi&ecirc;m c&ograve;n kh&ocirc;ng c&oacute; kịp phản ứng, Tần Thủ Thi&ecirc;n cũng đ&atilde; lĩnh hội.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Trương Hồng Vũ c&ugrave;ng Trương Tố Nguyệt. . . C&ograve;n c&oacute; hai người mẫu th&acirc;n Trương Anh Lan, đều ban đầu đối L&yacute; Băng mặn nhạt ch&acirc;m chọc đi?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;L&agrave;. . . N&agrave;y, như vậy lời n&oacute;i. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nếu như L&yacute; Băng, thật h&oacute;a th&agrave;nh oan hồn muốn b&aacute;o th&ugrave;, như vậy, c&aacute;i nh&agrave; n&agrave;y b&ecirc;n trong liền c&oacute; ba người, khả năng l&agrave; n&agrave;ng b&aacute;o th&ugrave; đối tượng. Ch&uacute;ng ta đợi ở chỗ n&agrave;y, chẳng lẽ sẽ kh&ocirc;ng bị tai họa sao?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn m&agrave; n&oacute;i x&aacute;c thực c&oacute; đạo l&yacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng. . . Ở chỗ n&agrave;o đi tốt đ&acirc;u?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A T&uacute;. . .&quot; L&yacute; Ẩn kh&ocirc;ng ch&uacute;t nghĩ ngợi n&oacute;i: &quot;A T&uacute; gia, ta cũng nghĩ thế an to&agrave;n nhất, bởi v&igrave; theo đạo l&yacute; tới n&oacute;i, L&yacute; Băng ho&agrave;n to&agrave;n kh&ocirc;ng thể n&agrave;o hướng n&agrave;ng b&aacute;o th&ugrave;.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A?&quot; La Hằng Vi&ecirc;m ng&acirc;y ngẩn, ngay sau đ&oacute; n&oacute;i: &quot;Nhưng, nhưng l&agrave;. . . Cha mẹ của n&agrave;ng đều qua đời, một th&acirc;n một m&igrave;nh sinh hoạt, ba người ch&uacute;ng ta đại nam nh&acirc;n v&agrave;o ở đi. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Sống chết trước mắt, c&ograve;n để &yacute; loại sự t&igrave;nh n&agrave;y?&quot; Tần Thủ Thi&ecirc;n đẩy tr&ecirc;n sống mũi k&iacute;nh mắt, n&oacute;i: &quot;Ngươi nghĩ ra được so A T&uacute; gia c&agrave;ng th&ecirc;m an to&agrave;n nơi chốn sao?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&iacute;ch x&aacute;c. . . C&aacute;i n&agrave;y U Thủy th&ocirc;n, nếu như n&oacute;i c&oacute; một địa phương tuyệt đối an to&agrave;n, như vậy. . . Khẳng định l&agrave; tại A T&uacute; trong nh&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nhưng. . . Ch&uacute;ng ta chẳng lẽ cường h&agrave;nh v&agrave;o ở đi?&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Thương lượng với n&agrave;ng nh&igrave;n xem, &quot; Tần Thủ Thi&ecirc;n giờ ph&uacute;t n&agrave;y cũng rất buồn rầu: &quot;N&agrave;ng h&ocirc;m qua đối với ch&uacute;ng ta cũng coi như nhiệt t&igrave;nh, c&oacute; lẽ sẽ đ&aacute;p ứng. Bất qu&aacute; n&ocirc;ng th&ocirc;n nh&acirc;n quan niệm bảo thủ, kh&ocirc;ng quen kh&ocirc;ng biết, thu lưu ch&uacute;ng ta, kh&oacute; tr&aacute;nh khỏi l&agrave;m cho người ta nh&agrave;n thoại.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cũng kh&ocirc;ng c&oacute; biện ph&aacute;p kh&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>M&ocirc;̣t phương di&ecirc;̣n khác, th&ocirc;n phụ cận trong rừng. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi t&igrave;m chết a ngươi!&quot; C&aacute;t Linh đem kia l&agrave;n da đen thui Thiết Cầm một phen đẩy tới tr&ecirc;n mặt đất, mắng: &quot;N&oacute;i c&aacute;i g&igrave; L&yacute; Băng đến b&aacute;o th&ugrave; rồi? Ngươi c&ograve;n ngại nh&aacute;o quỷ lời đồn kh&ocirc;ng đủ nhiều a!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nhưng. . . nhưng l&agrave;. . .&quot; Thiết Cầm to&agrave;n th&acirc;n ph&aacute;t run n&oacute;i: &quot;C&aacute;c ngươi qu&ecirc;n rồi sao? Hạo Thi&ecirc;n th&uacute;c đối nh&aacute;o quỷ thuyết ph&aacute;p một mực chẳng th&egrave;m ng&oacute; tới, c&ograve;n n&oacute;i, nếu c&oacute; quỷ, hắn thật đ&uacute;ng l&agrave; muốn gặp một lần đ&acirc;u! Lời n&agrave;y, ch&iacute;nh l&agrave; một tuần lễ trước kia n&oacute;i a!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Vậy th&igrave; thế n&agrave;o? L&agrave;m sao lại c&oacute; ma!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Nhưng. . . Nhưng l&agrave;. . . Thật sự quỷ dị a, nh&agrave; trưởng th&ocirc;n con rể. . . Cũng l&agrave; kh&ocirc;ng giải th&iacute;ch được liền chết, mọi người mặc d&ugrave; ngo&agrave;i miệng kh&ocirc;ng n&oacute;i, nhưng trong l&ograve;ng đều đang đ&aacute;nh trống a. . . Hơn nữa, mỗi lần, đều tại L&yacute; Băng ng&agrave;y giỗ bắt đầu một th&aacute;ng thời gian b&ecirc;n trong xảy ra chuyện, c&aacute;i n&agrave;y. . . Đ&acirc;y cũng qu&aacute; quỷ dị. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi đi lu&ocirc;n đi!&quot; C&aacute;t Linh hận hận lại đ&aacute; n&agrave;ng một cước, n&oacute;i: &quot;N&oacute;i &iacute;t v&agrave;i c&acirc;u ngươi sẽ chết a! Quỷ? Quỷ ở nơi n&agrave;o, L&yacute; Băng, ngươi c&oacute; gan hiện tại liền chạy ra khỏi đến l&agrave;m ta sợ a! Ha ha, kh&ocirc;ng c&oacute; loại đi, ngươi kh&ocirc;ng c&oacute;. . . Ngươi. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;t Linh n&oacute;i tới một nửa liền n&oacute;i kh&ocirc;ng nổi nữa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bởi v&igrave;. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;ng r&otilde; r&agrave;ng tr&ocirc;ng thấy, ng&atilde; tr&ecirc;n mặt đất Thiết Cầm, mở lớn miệng b&ecirc;n trong, th&igrave;nh l&igrave;nh nh&igrave;n thấy một trương &acirc;m bạch, t&oacute;c tai b&ugrave; x&ugrave; gương mặt!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi. . .&quot; Bỗng nhi&ecirc;n C&aacute;t Linh k&eacute;o l&ecirc;n một c&aacute;i Thiết Cầm, n&oacute;i: &quot;Miệng. . . Mi&ecirc;̣ng há lớn một ch&uacute;t!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Linh, Linh tỷ, ngươi, ngươi muốn l&agrave;m g&igrave; a. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Cho ta đem mi&ecirc;̣ng há, mở lớn!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&uacute;c n&agrave;y, kia gương mặt đ&atilde; kh&ocirc;ng thấy được. Thiết Cầm trong miệng, xem l&ecirc;n rất b&igrave;nh thường. Nhưng l&agrave;, C&aacute;t Linh kh&ocirc;ng thể tin được vừa rồi một m&agrave;n kia l&agrave; ảo gi&aacute;c, bởi v&igrave; qu&aacute; r&otilde; r&agrave;ng!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;ng mở to con mắt, nh&igrave;n về ph&iacute;a Thiết Cầm yết hầu, cơ hồ muốn đem mặt cũng d&aacute;n v&agrave;o Thiết Cầm miệng b&ecirc;n trong.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhưng l&agrave;, như cũ kh&ocirc;ng thu hoạch được g&igrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;t Linh lập tức đem th&acirc;n thể kh&ocirc;ng ngừng l&ugrave;i lại, hoảng sợ xem Thiết Cầm. Thời gian dần qua. . . N&agrave;ng bắt đầu ph&aacute;t hiện kh&ocirc;ng đ&uacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Ngươi. . . Tr&ecirc;n người của ngươi, l&agrave;m sao nhiều như vậy nước!&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A?&quot; Thiết Cầm nh&igrave;n kỹ, x&aacute;c thực, tr&ecirc;n th&acirc;n li&ecirc;n tục kh&ocirc;ng ngừng m&agrave; tu&ocirc;n ra nước đến, quần &aacute;o h&uacute;t nước, d&aacute;n s&aacute;t th&acirc;n thể.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kế tiếp C&aacute;t Linh nh&igrave;n thấy ch&iacute;nh l&agrave; l&agrave;m n&agrave;ng mắt muốn nứt một m&agrave;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thiết Cầm phần bụng quần &aacute;o d&aacute;n chặt lấy th&acirc;n thể sau, n&agrave;ng r&otilde; r&agrave;ng thấy được. . . Quần &aacute;o đột xuất một r&otilde; r&agrave;ng, mặt người đường n&eacute;t!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;A!&quot; C&aacute;t Linh lập tức hoảng sợ che lấy gương mặt, ngay sau đ&oacute; nắm l&ecirc;n tr&ecirc;n mặt đất một khối đ&aacute;, tiến l&ecirc;n một ch&uacute;t đ&aacute; ng&atilde; Thiết Cầm, nhắm ngay ngực n&agrave;ng kia mặt người đường n&eacute;t, liền hung hăng đập xuống! M&agrave; tảng đ&aacute; kia vừa vặn c&oacute; b&eacute;n nhọn một mặt, kia b&eacute;n nhọn một mặt liều mạng đ&acirc;m v&agrave;o Thiết Cầm ngực. M&agrave; Thiết Cầm vội v&agrave;ng muốn ngăn cản C&aacute;t Linh, nhưng m&agrave; đ&atilde; mất đi l&yacute; tr&iacute; C&aacute;t Linh, kh&iacute; lực lớn đến kinh người, hai ba c&aacute;i đập xuống, Thiết Cầm ngực đ&atilde; m&aacute;u thịt be b&eacute;t!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cũng kh&ocirc;ng biết đập bao l&acirc;u, n&agrave;ng bỗng nhi&ecirc;n &yacute; thức được c&aacute;i g&igrave;, nh&igrave;n về ph&iacute;a Thiết Cầm mặt. Giờ ph&uacute;t n&agrave;y. . . N&agrave;ng đ&atilde; ho&agrave;n to&agrave;n kh&ocirc;ng c&oacute; m&agrave;u m&aacute;u, nguy&ecirc;n bản da tay ngăm đen trở n&ecirc;n t&aacute;i nhợt v&ocirc; c&ugrave;ng, trong miệng tu&ocirc;n ra đại lượng m&aacute;u tươi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;ng th&aacute;o ra Thiết Cầm quần &aacute;o, lộ ra bụng của n&agrave;ng. Giờ ph&uacute;t n&agrave;y. . . Vỏn vẹn chỉ c&oacute; thể nh&igrave;n thấy ch&iacute;nh m&igrave;nh đập ra vết thương, nơi n&agrave;o c&ograve;n nh&igrave;n thấy kia mặt người?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Quỷ. . . L&agrave; quỷ. . .&quot; N&agrave;ng lập tức vứt xuống tr&ecirc;n tay h&ograve;n đ&aacute;, to&agrave;n th&acirc;n co r&uacute;m lại kh&ocirc;ng ngừng đem th&acirc;n thể l&ugrave;i ra sau, tự nhủ: &quot;Kh&ocirc;ng, kh&ocirc;ng phải ta. . . L&yacute;, L&yacute; Băng, kh&ocirc;ng c&oacute; quan hệ g&igrave; với ta, ngươi l&agrave; tự s&aacute;t, ta, ta l&agrave; mắng qua ngươi, đ&aacute;nh qua ngươi, nhưng, nhưng ta kh&ocirc;ng c&oacute; giết ngươi a. . . Kh&ocirc;ng phải lỗi của ta, ngươi đừng đến t&igrave;m ta, đừng đến. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngay sau đ&oacute;, n&agrave;ng ch&egrave;o chống ch&iacute;nh m&igrave;nh b&ograve; l&ecirc;n, chạy như bay nhanh ch&oacute;ng trốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;C&oacute; thể a, c&aacute;c ngươi nghĩ ở bao l&acirc;u đều được.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A T&uacute; trả lời khiến L&yacute; Ẩn bọn họ rất l&agrave; ngo&agrave;i &yacute; muốn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Thật. . . C&oacute; thể?&quot; L&yacute; Ẩn lại hỏi một c&acirc;u: &quot;Ch&uacute;ng ta bốn người người. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;C&oacute; thể a.&quot; A T&uacute; nụ cười ch&acirc;n th&agrave;nh n&oacute;i: &quot;C&aacute;c ngươi l&agrave; ph&oacute;ng vi&ecirc;n ti&ecirc;n sinh đi? Chỉ cần c&aacute;c ngươi đem Băng Nhi tỷ tỷ chết b&aacute;o c&aacute;o ra, vạch trần th&ocirc;n tội &aacute;c, ở bao l&acirc;u cũng kh&ocirc;ng quan hệ nha.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thế m&agrave; c&ograve;n l&agrave; bị coi l&agrave; ph&oacute;ng vi&ecirc;n. . . L&yacute; Ẩn nghĩ thầm, chẳng lẽ ta d&aacute;ng dấp cứ như vậy giống paparazzi sao?</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A T&uacute; ph&ograve;ng ở cũng kh&ocirc;ng t&iacute;nh lớn, ở năm người m&agrave; n&oacute;i đ&iacute;ch x&aacute;c chật chội một điểm, bất qu&aacute; d&ugrave; sao ban đ&ecirc;m chỉ cần ngủ hai người, hai người kh&aacute;c phụ tr&aacute;ch trực ban cảnh giới. Như vậy, đủ rồi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;V&acirc;̣y thì t&ocirc;́t, ch&uacute;ng ta đi trước thu thập h&agrave;nh l&yacute;, thật l&agrave;m phiền ngươi.&quot; L&yacute; Ẩn li&ecirc;n tục đối A T&uacute; n&oacute;i lời cảm tạ: &quot;Ch&uacute;ng ta khẳng định trả tiền thu&ecirc; ph&ograve;ng, y&ecirc;n t&acirc;m đi, sẽ kh&ocirc;ng để cho ngươi thua thiệt.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&yacute; Ẩn rời khỏi sau, A T&uacute; kh&eacute;p cửa ph&ograve;ng lại , l&ecirc;n then cửa. Sau đ&oacute;, đi trở về đến gian ph&ograve;ng trong ph&ograve;ng bếp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nh&agrave; n&agrave;ng ph&ograve;ng bếp lại tương đối lớn, đặt một rất lớn vại nước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A T&uacute; đi đến vại nước trước, đem nắp vại mở ra, nh&igrave;n về ph&iacute;a b&ecirc;n trong.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Một nam nh&acirc;n th&acirc;n thể ch&iacute;nh ng&acirc;m tại trong chum nước, sắc mặt đ&atilde; trở n&ecirc;n so giấy c&agrave;ng t&aacute;i nhợt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nam nh&acirc;n n&agrave;y. . . Ch&iacute;nh l&agrave; mất t&iacute;ch Di&ecirc;m Hạo Thi&ecirc;n!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Hạo Thi&ecirc;n th&uacute;c a, ta cũng kh&ocirc;ng c&oacute; qu&ecirc;n, l&uacute;c trước, ngươi l&agrave;m sao đối Băng Nhi tỷ tỷ. Ỷ v&agrave;o ngươi nh&acirc;n duy&ecirc;n tương đối tốt, như người nhiều chuyện khắp nơi tuy&ecirc;n dương Băng Nhi tỷ tỷ sự t&igrave;nh. Cho n&ecirc;n nha. . .&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A T&uacute; lấy ra một phen c&aacute;i k&eacute;o đến, nắm chặt Di&ecirc;m Hạo Thi&ecirc;n t&oacute;c, đem hắn đầu trảo ra mặt nước, sau đ&oacute;, cạy mở miệng của hắn. Tiếp. . . Đem c&aacute;i k&eacute;o th&ograve; v&agrave;o trong miệng của hắn, dĩ nhi&ecirc;n cắt xuống hắn đầu lưỡi!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&quot;Băng Nhi tỷ tỷ, ngươi y&ecirc;n t&acirc;m đi.&quot; A T&uacute; kia xinh đẹp tr&ecirc;n mặt lộ ra lại l&agrave; l&agrave;m cho người sợ h&atilde;i t&agrave;n nhẫn: &quot;Ngươi cứ việc đem bọn họ từng c&aacute;i đưa đến nơi n&agrave;y đi, ta sẽ hảo hảo xử l&yacute; bọn họ.&quot;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tiếp theo, n&agrave;ng lại đi v&agrave;o gian ph&ograve;ng ph&ograve;ng trong, đem một kh&oacute;a lại tủ mở ra, lấy ra một c&aacute;i b&igrave;nh nhỏ, đem đầu lưỡi bỏ v&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong ngăn tủ, c&ograve;n tồn ph&oacute;ng rất nhiều đồng dạng chứa đầu lưỡi c&aacute;i b&igrave;nh!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&agrave;ng đ&oacute;ng lại tủ , l&ecirc;n kh&oacute;a, chợt nghe ph&ograve;ng bếp lại truyền tới một tiếng đại đại tiếng nước. N&agrave;ng vội v&agrave;ng chạy về trong ph&ograve;ng bếp đi, xem x&eacute;t. . .</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong vại nước, kh&ocirc;ng c&ograve;n l&agrave; Di&ecirc;m Hạo Thi&ecirc;n, m&agrave; l&agrave; vừa mới bị C&aacute;t Linh giết chết Thiết Cầm!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>A T&uacute; cười lạnh, lại lần nữa giơ l&ecirc;n trong tay sắc nhọn c&aacute;i k&eacute;o. . .</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:1884px; top:839px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'mau-tanh-thinh-yen-mot', 3, '42242', '2022-08-03 05:09:55', '2022-08-03 05:09:55');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(39, '1', 'Ta thật không phải là Ngộ Không!', '<p>Xanh thẳm tr&ecirc;n bầu trời, bảy m&agrave;u m&acirc;y t&iacute;a, một vị vạn trượng to lớn cổ Phật ngồi xếp bằng ở kim li&ecirc;n b&ecirc;n tr&ecirc;n, Ni&ecirc;m Hoa mỉm cười, nụ cười kia khiến người ta kh&ocirc;ng khơi ra nửa điểm tỳ vết, nhưng để T&ocirc;n Thiệu cảm thấy thấu xương lạnh gi&aacute;.<br />\r\n<br />\r\n&quot;Ha ha, con khỉ ngang ngược, ngươi phục rồi sao.&quot;<br />\r\n<br />\r\n&quot;Phục, phục, ta phục, bất qu&aacute;, ta c&oacute; thể hay kh&ocirc;ng n&oacute;i một c&acirc;u. . .&quot; T&ocirc;n Thiệu ngơ ng&aacute;c nh&igrave;n cổ Phật năm ng&oacute;n tay lớn, kh&ocirc;ng ngừng k&ecirc;u khổ. Trong đ&oacute; một ng&oacute;n tay lớn b&ecirc;n tr&ecirc;n, viết &quot;Tề Thi&ecirc;n Đại Th&aacute;nh, từng du lịch qua đ&acirc;y&quot; tám c&aacute;i xi&ecirc;u xi&ecirc;u vẹo vẹo chữ viết, m&agrave; ở c&aacute;i kia ng&oacute;n tay lớn b&ecirc;n dưới, thậm ch&iacute; c&ograve;n c&oacute; một b&atilde;i nước tiểu.<br />\r\n<br />\r\nLoại n&agrave;y kh&ocirc;ng c&oacute; có hình tượng sự t&igrave;nh, tự hồ chỉ c&oacute; T&acirc;y Du K&yacute;, b&ecirc;n trong T&ocirc;n Ngộ Kh&ocirc;ng mới l&agrave;m được. M&agrave; vị n&agrave;y vạn trượng to lớn cổ Phật, kh&ocirc;ng phải l&agrave; Như Lai Phật Tổ sao!<br />\r\n<br />\r\n&quot;Ha ha, kh&ocirc;ng nghĩ tới ngươi n&agrave;y con khỉ ngang ngược, c&ograve;n sẽ thuyết phục . Khổ hải v&ocirc; bi&ecirc;n, quay đầu lại l&agrave; bờ, ngươi muốn n&oacute;i c&aacute;i g&igrave;, tạm thời n&oacute;i một ch&uacute;t. . .&quot;</p>', '<p>Xanh thẳm tr&ecirc;n bầu trời, bảy m&agrave;u m&acirc;y t&iacute;a, một vị vạn trượng to lớn cổ Phật ngồi xếp bằng ở kim li&ecirc;n b&ecirc;n tr&ecirc;n, Ni&ecirc;m Hoa mỉm cười, nụ cười kia khiến người ta kh&ocirc;ng khơi ra nửa điểm tỳ vết, nhưng để T&ocirc;n Thiệu cảm thấy thấu xương lạnh gi&aacute;.<br />\r\n<br />\r\n&quot;Ha ha, con khỉ ngang ngược, ngươi phục rồi sao.&quot;<br />\r\n<br />\r\n&quot;Phục, phục, ta phục, bất qu&aacute;, ta c&oacute; thể hay kh&ocirc;ng n&oacute;i một c&acirc;u. . .&quot; T&ocirc;n Thiệu ngơ ng&aacute;c nh&igrave;n cổ Phật năm ng&oacute;n tay lớn, kh&ocirc;ng ngừng k&ecirc;u khổ. Trong đ&oacute; một ng&oacute;n tay lớn b&ecirc;n tr&ecirc;n, viết &quot;Tề Thi&ecirc;n Đại Th&aacute;nh, từng du lịch qua đ&acirc;y&quot; tám c&aacute;i xi&ecirc;u xi&ecirc;u vẹo vẹo chữ viết, m&agrave; ở c&aacute;i kia ng&oacute;n tay lớn b&ecirc;n dưới, thậm ch&iacute; c&ograve;n c&oacute; một b&atilde;i nước tiểu.<br />\r\n<br />\r\nLoại n&agrave;y kh&ocirc;ng c&oacute; có hình tượng sự t&igrave;nh, tự hồ chỉ c&oacute; T&acirc;y Du K&yacute;, b&ecirc;n trong T&ocirc;n Ngộ Kh&ocirc;ng mới l&agrave;m được. M&agrave; vị n&agrave;y vạn trượng to lớn cổ Phật, kh&ocirc;ng phải l&agrave; Như Lai Phật Tổ sao!<br />\r\n<br />\r\n&quot;Ha ha, kh&ocirc;ng nghĩ tới ngươi n&agrave;y con khỉ ngang ngược, c&ograve;n sẽ thuyết phục . Khổ hải v&ocirc; bi&ecirc;n, quay đầu lại l&agrave; bờ, ngươi muốn n&oacute;i c&aacute;i g&igrave;, tạm thời n&oacute;i một ch&uacute;t. . .&quot;<br />\r\n<br />\r\nNhư Lai Phật Tổ tựa hồ đối với T&ocirc;n Thiệu lạc đường biết quay lại kh&aacute; l&agrave; kinh ngạc, hơi sững sờ, lập tức mỉm cười n&oacute;i.<br />\r\n<br />\r\nNh&igrave;n Như Lai Phật Tổ &quot;H&ograve;a &aacute;i dễ gần&quot; nụ cười, T&ocirc;n Thiệu g&atilde;i đầu một c&aacute;i, cười gượng hai tiếng, &quot;C&aacute;i kia, ta kh&ocirc;ng phải T&ocirc;n Ngộ Kh&ocirc;ng, Phật Tổ gia gia, ngươi nhận lầm người. . .&quot;<br />\r\n<br />\r\n&quot;Hừ, con khỉ ngang ngược, tốt nghiệp chướng! Ta ch&iacute;nh l&agrave; người trong phật m&ocirc;n, sao l&agrave; gia gia ngươi! Ngu xuẩn mất kh&ocirc;n, đ&atilde; như vậy, liền trấn &aacute;p ngươi năm trăm năm, cố gắng hối lỗi!&quot;<br />\r\n<br />\r\nNhư Lai Phật Tổ lật b&agrave;n tay một c&aacute;i, c&aacute;i kia che khuất bầu trời cự chưởng, ở h&agrave;o quang năm m&agrave;u b&ecirc;n trong h&oacute;a th&agrave;nh kim, xanh, lam, x&iacute;ch, vàng kh&ocirc;ng to&agrave; n&uacute;i to, hướng về l&ograve;ng b&agrave;n tay T&ocirc;n Thiệu &eacute;p một ch&uacute;t m&agrave; xuống.<br />\r\n<br />\r\nXoay tay th&agrave;nh n&uacute;i, n&agrave;y Như Lai Phật Tổ ph&aacute;p lực thật l&agrave; mạnh mẽ!<br />\r\n<br />\r\nNăm loại m&agrave;u sắc n&uacute;i to, đối ứng Kim, Mộc, Thủy, Hỏa, Thổ thi&ecirc;n địa Ngũ h&agrave;nh, quen thuộc T&acirc;y Du K&yacute;, T&ocirc;n Thiệu nơi n&agrave;o kh&ocirc;ng biết, đ&acirc;y cũng l&agrave; đại danh đỉnh đỉnh Ngũ H&agrave;nh Sơn!<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng phải T&ocirc;n Ngộ Kh&ocirc;ng, ta thật kh&ocirc;ng phải l&agrave; T&ocirc;n Ngộ Kh&ocirc;ng a!&quot;<br />\r\n<br />\r\nChỉ nghe &quot;Ầm ầm&quot; một tiếng vang thật lớn, ph&agrave;m giới từ đ&acirc;y nhiều hơn một t&ograve;a ngũ sắc ngọn n&uacute;i, kh&ocirc;ng c&oacute; một người, chịu nghe T&ocirc;n Thiệu biện giải.<br />\r\n<br />\r\n... ... ... ... ... ... ... ... ... ... . .<br />\r\n<br />\r\n&quot;Ta kh&ocirc;ng phải T&ocirc;n Ngộ Kh&ocirc;ng, ta thật kh&ocirc;ng phải l&agrave; T&ocirc;n Ngộ Kh&ocirc;ng a!&quot;<br />\r\n<br />\r\nMột tiếng th&ecirc; lương k&ecirc;u r&ecirc;n ph&iacute;a sau, T&ocirc;n Thiệu đầy đầu mồ h&ocirc;i lạnh từ dưới đất b&ograve; dậy, xoa xoa mồ h&ocirc;i lạnh tr&ecirc;n tr&aacute;n, thở ph&agrave;o nhẹ nh&otilde;m, &quot;H&oacute;a ra l&agrave; nằm mơ a. Cũng c&ograve;n tốt, cũng c&ograve;n tốt, bất qu&aacute; dĩ nhi&ecirc;n mơ tới tự th&agrave;nh T&ocirc;n Ngộ Kh&ocirc;ng, thực sự l&agrave; t&agrave; m&ocirc;n. Ai nha, ng&agrave;y đều như thế s&aacute;ng, buổi s&aacute;ng giờ học muốn tới trễ rồi!&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu l&agrave; đại học n&agrave;o đ&oacute; sinh vi&ecirc;n năm thứ 2, dường như rất nhiều sinh vi&ecirc;n đại học giống như vậy, qu&aacute; lười biếng sinh hoạt, đến muộn đối với hắn m&agrave; n&oacute;i từ l&acirc;u l&agrave; chuyện thường như cơm bữa.<br />\r\n<br />\r\nT&ocirc;n Thiệu ng&agrave;y xưa thuần thục vươn m&igrave;nh xuống giường động t&aacute;c, đến rồi h&ocirc;m nay, ch&iacute;nh l&agrave; ở tr&ecirc;n cỏ lăn một v&ograve;ng. Ở tr&ecirc;n cỏ lăn hai v&ograve;ng, nh&igrave;n bốn ph&iacute;a Nguy&ecirc;n Thủy t&ugrave;ng l&acirc;m gi&ocirc;́ng như ho&agrave;n cảnh xa lạ, T&ocirc;n Thiệu con mắt mở so với chu&ocirc;ng đồng c&ograve;n lớn hơn,</p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Nơi n&agrave;y l&agrave; nơi n&agrave;o? Ta kh&ocirc;ng biết c&ograve;n đang nằm mơ chứ!&quot;<br />\r\n<br />\r\nNơi n&agrave;y n&agrave;o c&oacute; c&aacute;i g&igrave; giường, n&agrave;o c&oacute; c&aacute;i g&igrave; ph&ograve;ng ngủ, n&agrave;o c&oacute; c&aacute;i g&igrave; trường học.<br />\r\n<br />\r\nT&ocirc;n Thiệu dưới ch&acirc;n, một c&aacute;i trong suốt khe n&uacute;i chảy xiết chảy qua, vẫn d&acirc;ng tr&agrave;o đến dưới ch&acirc;n n&uacute;i, ở ch&acirc;n n&uacute;i ở ngo&agrave;i, th&ocirc;ng suốt đến trong biển rộng. Bu&ocirc;ng xuống đầu, nh&igrave;n khe suối b&ecirc;n trong ph&aacute; to&aacute;i h&igrave;nh chiếu, T&ocirc;n Thiệu kh&ocirc;ng cho l&agrave; đ&uacute;ng địa cười ha ha, &quot;Ta đi, dĩ nhi&ecirc;n l&agrave; trong mộng chi mộng. L&agrave;m sao giấc mộng n&agrave;y vẫn l&agrave; mộng thấy m&igrave;nh l&agrave; T&ocirc;n Ngộ Kh&ocirc;ng.&quot;<br />\r\n<br />\r\nC&aacute;i kia khe suối b&ecirc;n trong T&ocirc;n Thiệu h&igrave;nh chiếu, r&otilde; r&agrave;ng l&agrave; một con m&agrave;u v&agrave;ng đất vi&ecirc;n hầu!<br />\r\n<br />\r\nT&ocirc;n Thiệu theo bản năng cho rằng, n&agrave;y nhưng vẫn l&agrave; đang nằm mơ, trong mộng nằm mơ, hắn trước đ&acirc;y cũng kh&ocirc;ng phải chưa từng gặp qua. Chỉ l&agrave; trong mộng c&oacute; thể nghe đến m&ugrave;i hoa, c&oacute; thể c&oacute; ch&acirc;n thật như vậy x&uacute;c cảm, đ&uacute;ng l&agrave; đệ nhất hẹn gặp lại đến.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng biết mấy giờ rồi, ngủ một hồi nữa đ&acirc;y đi. . .&quot;<br />\r\n<br />\r\nLười nh&aacute;c địa y vi&ecirc;n hầu th&acirc;n b&ograve; l&ecirc;n tr&ecirc;n một vi&ecirc;n c&acirc;y đ&agrave;o, T&ocirc;n Thiệu ngủ say.<br />\r\n<br />\r\nTrong mộng, hắn mộng thấy phụ th&acirc;n c&ocirc;ng ty ph&aacute; sản, hắn mơ thấy gia cảnh sa s&uacute;t sau, mẫu th&acirc;n t&oacute;c bạc từng sợi, đối với m&igrave;nh tha thiết dặn, &quot;Thiệu Thiệu, kh&ocirc;ng muốn lại tới x&atilde; hội thượng l&ecirc;u lổng. Cha ngươi đ&atilde; kh&ocirc;ng c&oacute; tiền, ngoan, đi đại học cố gắng đọc s&aacute;ch, đừng để mẹ quan t&acirc;m.&quot; Hắn mộng thấy hai năm qua, ch&iacute;nh m&igrave;nh ngơ ngơ ng&aacute;c ng&aacute;c cuộc sống đại học.<br />\r\n<br />\r\nĂn cơm, trốn học, xem tiểu thuyết, chơi game, đơn điệu m&agrave; rớt xuống sinh hoạt.<br />\r\n<br />\r\n&quot;Rống!&quot;<br />\r\n<br />\r\nMột tiếng khuấy l&ecirc;n gi&oacute; n&uacute;i tiếng hổ gầm, đem T&ocirc;n Thiệu từ trong mộng thức tỉnh. Từ trong giấc mộng tỉnh lại, ph&aacute;t hiện m&igrave;nh nhưng vẫn l&agrave; hầu th&acirc;n, T&ocirc;n Thiệu hầu như đều ph&aacute;t đi&ecirc;n hơn, &quot;Mẹ kiếp, tại sao c&ograve;n nằm mơ, ta cũng kh&ocirc;ng phải T&ocirc;n Ngộ Kh&ocirc;ng a!&quot;<br />\r\n<br />\r\nD&ugrave; l&agrave; ai h&atilde;m ở trong &aacute;c mộng, l&agrave;m sao cũng kh&ocirc;ng c&aacute;ch n&agrave;o tỉnh lại, n&oacute;i vậy đều sẽ ph&aacute;t đi&ecirc;n đi.<br />\r\n<br />\r\nMấy giọt ấm &aacute;p huyết dịch từ c&acirc;y đ&agrave;o ở dưới b&atilde;i cỏ tung to&eacute; l&ecirc;n, gắn T&ocirc;n Thiệu một mặt, huyết dịch n&agrave;y mang theo nhiệt độ, mang theo m&ugrave;i tanh, để T&ocirc;n Thiệu nhất thời đứng ở đ&oacute;, &quot;Đ&acirc;y kh&ocirc;ng phải l&agrave; mộng!&quot;<br />\r\n<br />\r\nTrong mộng l&agrave; kh&ocirc;ng c&oacute; c&oacute; khứu gi&aacute;c, kh&ocirc;ng c&oacute; vị gi&aacute;c, n&agrave;y, lẽ n&agrave;o cũng kh&ocirc;ng phải l&agrave; mộng.<br />\r\n<br />\r\nC&acirc;y đ&agrave;o b&ecirc;n dưới, một con dài hơn một trượng Hổ vằn đang bắt giết n&ocirc; đ&ugrave;a bầy khỉ, tr&ecirc;n b&atilde;i cỏ, đ&atilde; c&oacute; mấy chục con khỉ nhỏ chết thảm. M&agrave; T&ocirc;n Thiệu trong linh hồn, lập tức truyền đến một đạo cuồng ngạo tiếng, thanh &acirc;m n&agrave;y r&otilde; r&agrave;ng l&agrave; hầu ngữ, nhưng m&agrave; T&ocirc;n Thiệu c&oacute; thể nghe hiểu, &quot;Ta c&ograve;n kh&ocirc;ng c&oacute; l&ecirc;n l&agrave;m Hầu Vương, ngươi một c&aacute;i con cọp liền d&aacute;m ức hiếp ta khỉ con khỉ ch&aacute;u!&quot;<br />\r\n<br />\r\nĐạo kia linh hồn gầm l&ecirc;n giận dữ ph&iacute;a sau, c&agrave;ng chậm r&atilde;i c&ugrave;ng T&ocirc;n Thiệu linh hồn h&ograve;a v&agrave;o nhau, lại kh&ocirc;ng nửa phần dấu vết. Cảm nhận được l&ograve;ng b&agrave;n tay bỗng nhi&ecirc;n d&acirc;ng l&ecirc;n sức mạnh, T&ocirc;n Thiệu buồn bực địa g&atilde;i đầu một c&aacute;i, &quot;Thanh &acirc;m n&agrave;y, c&ugrave;ng với trước Ngũ h&agrave;nh sơn mộng, xem ra, ta thật xuy&ecirc;n qua rồi, hơn nữa c&ograve;n xuy&ecirc;n qua th&agrave;nh T&ocirc;n Ngộ Kh&ocirc;ng. Bất kể, T&ocirc;n Ngộ Kh&ocirc;ng liền T&ocirc;n Ngộ Kh&ocirc;ng đi! Ch&iacute; &iacute;t kh&ocirc;ng xuy&ecirc;n qua th&agrave;nh Trư B&aacute;t Giới! Đi trước bảo vệ bảo vệ n&agrave;y đ&aacute;m khỉ con khỉ ch&aacute;u!&quot;<br />\r\n<br />\r\nTừ cao một trượng c&acirc;y đ&agrave;o tr&ecirc;n lăng kh&ocirc;ng nhảy xuống, T&ocirc;n Thiệu h&eacute;t lớn một tiếng, &quot;Khỉ con khỉ ch&aacute;u nh&oacute;m nghe, kể từ h&ocirc;m nay, l&atilde;o T&ocirc;n ch&iacute;nh l&agrave; của c&aacute;c ngươi Hầu Vương! C&aacute;c ngươi, từ L&atilde;o Tử bảo vệ!&quot;</p>\r\n\r\n<p><br />\r\nT&ocirc;n Thiệu th&acirc;n ở giữa kh&ocirc;ng trung, một tiếng h&eacute;t lớn, nhất thời đ&egrave; xuống to&agrave;n trường, một quần tiểu hầu trở về từ c&otilde;i chết, rất vui mừng b&ograve; đến bốn ph&iacute;a tr&ecirc;n c&acirc;y bắt đầu trốn. M&agrave; T&ocirc;n Thiệu nhất thời c&oacute; ch&uacute;t l&acirc;ng l&acirc;ng, c&oacute; thể trở th&agrave;nh l&agrave; ngang ngược T&ocirc;n Ngộ Kh&ocirc;ng, đại n&aacute;o Thi&ecirc;n Cung, cỡ n&agrave;o th&iacute;ch &yacute; sự t&igrave;nh.<br />\r\n<br />\r\nHai ch&acirc;n sau khi hạ xuống, T&ocirc;n Thiệu liền cũng kh&ocirc;ng cười nổi nữa. Từ cao một trượng c&acirc;y đ&agrave;o tr&ecirc;n trực tiếp nhảy xuống, c&aacute;i kia lực phản chấn để T&ocirc;n Thiệu hầu như đau đến sắp giao ra. M&agrave; c&aacute;i kia Hổ vằn c&agrave;ng là bổ một c&aacute;i mấy trượng, hướng T&ocirc;n Thiệu một trảo k&eacute;o tới.<br />\r\n<br />\r\nChỉ nghe &quot;X&igrave; x&igrave;&quot; một tiếng, T&ocirc;n Thiệu ngực c&agrave;ng bị lấy ra một c&aacute;i lỗ m&aacute;u.<br />\r\n<br />\r\nT&ocirc;n Thiệu sắc mặt nhất thời kinh h&atilde;i, trong tiểu thuyết T&ocirc;n Ngộ Kh&ocirc;ng kh&ocirc;ng phải kim cương bất hoại th&acirc;n thể sao, l&agrave;m sao từ chỗ cao nhảy xuống đều sẽ đau ch&acirc;n, l&agrave;m sao một con hổ đều c&oacute; thể đem n&oacute; qu&agrave;o trầy!<br />\r\n<br />\r\n&quot;Mẹ kiếp, lẽ n&agrave;o ta kh&ocirc;ng phải T&ocirc;n Ngộ Kh&ocirc;ng, ta xuy&ecirc;n qua th&agrave;nh một con phổ th&ocirc;ng hầu tử!&quot;<br />\r\n<br />\r\nHổ vằn gặp T&ocirc;n Thiệu bị thương, nh&uacute;n người nhảy m&ocirc;̣t cái, k&eacute;o ra c&aacute;i miệng lớn như chậu m&aacute;u, hướng T&ocirc;n Thiệu cổ cắn tới. M&agrave; khi 20 năm sinh vi&ecirc;n T&ocirc;n Thiệu, nơi n&agrave;o đối mặt qu&aacute; con cọp ăn thịt người. Nhắm mắt lại, một trận lung tung quyền đấm cước đ&aacute;.<br />\r\n<br />\r\nKhi T&ocirc;n Thiệu lại mở mắt ra thời gian, con cọp đang tại ch&iacute;nh m&igrave;nh trong l&ograve;ng, bỏ m&igrave;nh đ&atilde; l&acirc;u, bị ch&iacute;nh m&igrave;nh ch&ugrave;y th&agrave;nh một vũng m&aacute;u b&ugrave;n.<br />\r\n<br />\r\nM&igrave;nh tr&ecirc;n nắm tay, &iacute;t n&oacute;i cũng c&oacute; ngh&igrave;n c&acirc;n kh&iacute; lực!<br />\r\n<br />\r\nTrở về từ c&otilde;i chết, T&ocirc;n Thiệu vui mừng sau khi, đối với m&igrave;nh n&agrave;y l&agrave; hầu th&acirc;n c&oacute; nhận thức r&otilde; r&agrave;ng hơn.<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh xuy&ecirc;n việt bộ th&acirc;n thể n&agrave;y, t&aacute;m ch&iacute;n phần mười ch&iacute;nh l&agrave; T&ocirc;n Ngộ Kh&ocirc;ng. Bất qu&aacute; tựa hồ c&ugrave;ng nguy&ecirc;n t&aacute;c c&oacute; ch&uacute;t bất đồng, kh&ocirc;ng c&oacute; Thạch Hầu si&ecirc;u cường ph&ograve;ng ngự, kh&iacute; lực đ&uacute;ng l&agrave; lớn đến mức đ&aacute;ng sợ.<br />\r\n<br />\r\nMột đem bỏ xuống trong ngực x&aacute;c hổ, T&ocirc;n Thiệu thấp đầu vừa nh&igrave;n, bộ ngực m&igrave;nh ch&iacute;nh l&agrave; c&aacute;i kia lỗ m&aacute;u dĩ nhi&ecirc;n tại chậm r&atilde;i kh&eacute;p lại. Lại ngắm nh&igrave;n bốn ph&iacute;a, một đ&aacute;m khỉ nhỏ đều l&agrave; sợ h&atilde;i đang nh&igrave;n m&igrave;nh. Kh&ocirc;ng biết cái nào khỉ nhỏ k&ecirc;u tiếng thứ nhất, lập tức, như thủy triều &acirc;m thanh vang l&ecirc;n,<br />\r\n<br />\r\n&quot;Đại vương vạn tuế! Đại vương vạn tuế!&quot;<br />\r\n<br />\r\nĐ&ugrave;a giỡn, c&oacute; thể đ&aacute;nh chết vua b&aacute;ch th&uacute; hầu tử, để hắn khi Đại vương, chuyện thật tốt, c&aacute;i n&agrave;o con khỉ sẽ mắt kh&ocirc;ng mở từ chối.<br />\r\n<br />\r\nMột đ&aacute;m vi&ecirc;n hầu từ tr&ecirc;n c&acirc;y lật hạ, v&acirc;y tụ ở T&ocirc;n Thiệu bốn ph&iacute;a, lật tới lăn đi, tựa hồ cực kỳ mừng rỡ. Kh&ocirc;ng &iacute;t vi&ecirc;n hầu c&agrave;ng là h&aacute;i đến đủ loại hoa quả, trong đ&oacute; kh&ocirc;ng &iacute;t T&ocirc;n Thiệu đều l&agrave; chưa bao giờ ăn rồi.<br />\r\n<br />\r\nTrong tay c&oacute; sức mạnh, b&ecirc;n người c&oacute; tiểu đệ, đ&acirc;y l&agrave; kiếp trước bao nhi&ecirc;u người trẻ tuổi tha thiết ước mơ sinh hoạt. T&ocirc;n Thiệu thở d&agrave;i, nếu xuy&ecirc;n qua rồi, liền cẩn thận khi c&aacute;i n&agrave;y Hầu Vương đi. Tuy rằng đời sau rất nhiều tiểu thuyết, đều n&oacute;i T&acirc;y Du K&yacute;, b&ecirc;n trong cất giấu kinh thi&ecirc;n &acirc;m mưu, T&ocirc;n Ngộ Kh&ocirc;ng c&agrave;ng là &acirc;m mưu vật hy sinh, bất qu&aacute; những n&agrave;y đối với T&ocirc;n Thiệu tới n&oacute;i, đều trả qu&aacute; mức xa x&ocirc;i.<br />\r\n<br />\r\nLiền ở T&ocirc;n Thiệu hưởng thụ một đ&aacute;m khỉ nhỏ chi&ecirc;u đ&atilde;i thời gian, một con m&agrave;u l&ocirc;ng x&aacute;m trắng l&atilde;o hầu từ bầy khỉ b&ecirc;n trong đi ra, xem ra tựa hồ rất c&oacute; tư lịch. L&atilde;o hầu l&agrave; Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu, h&agrave;nh vi cũng c&ugrave;ng vi&ecirc;n hầu kh&ocirc;ng kh&aacute;c nhau ch&uacute;t n&agrave;o, nhưng m&agrave; &aacute;nh mắt hắn, nhưng linh động để T&ocirc;n Thiệu sản sinh một loại cảm gi&aacute;c cực kỳ nguy hiểm.<br />\r\n<br />\r\nC&aacute;i kia linh quang chỉ nh&aacute;y mắt liền biến mất, nhưng m&agrave; T&ocirc;n Thiệu nhưng r&otilde; r&agrave;ng đặt ở trong mắt, n&agrave;y Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu, tuyệt kh&ocirc;ng b&igrave;nh thường.<br />\r\n<br />\r\nNhư l&agrave; kiếp trước T&ocirc;n Ngộ Kh&ocirc;ng, lấy hắn n&oacute;ng nảy t&iacute;nh c&aacute;ch, chắc chắn sẽ kh&ocirc;ng tỉ mĩ như vậy, c&oacute; thể T&ocirc;n Thiệu d&ugrave; sao l&agrave;m 20 ni&ecirc;n nh&acirc;n loại, ng&agrave;y ng&agrave;y xem người sắc mặt, đ&atilde; quen thuộc từ l&acirc;u nghe lời đo&aacute;n &yacute;.<br />\r\n<br />\r\nL&atilde;o kia hầu đi tới T&ocirc;n Thiệu trước người, đem một đ&aacute;m khỉ nhỏ từ T&ocirc;n Thiệu b&ecirc;n người oanh mở, chậm r&atilde;i n&oacute;i rằng,<br />\r\n<br />\r\n&quot;Khái khái ho. . . Hầu Vương cũng kh&ocirc;ng thể như thế chọn, kh&ocirc;ng thể như thế chọn, ha ha. . . N&agrave;y khe suối ngọn nguồn, l&agrave; một chỗ d&ograve;ng nước xiết th&aacute;c nước, con n&agrave;o hầu nhi c&oacute; thể nhảy v&agrave;o th&aacute;c nước, kh&ocirc;ng tổn hại th&acirc;n thể, ch&uacute;ng ta liền t&ocirc;n hắn v&igrave; l&agrave; Đại vương, l&agrave;m sao. . .&quot;<br />\r\n<br />\r\nMột đ&aacute;m khỉ nhỏ, nghe được Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu như vậy ng&ocirc;n ngữ, nhất thời ồn &agrave;o l&ecirc;n, muốn đi khe suối ngọn nguồn th&aacute;c nước th&aacute;m hiểm. M&agrave; T&ocirc;n Thiệu đối với n&agrave;y Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu, cũng kh&ocirc;ng d&aacute;m lấy ra Hầu Vương tư thế, chỉ l&agrave; suy tư về Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu trong giọng n&oacute;i th&acirc;m &yacute;.<br />\r\n<br />\r\nKhe suối ngọn nguồn trong th&aacute;c nước, tự nhi&ecirc;n ch&iacute;nh l&agrave; Thủy Li&ecirc;m Động, c&aacute;i n&agrave;y mặc d&ugrave; Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu kh&ocirc;ng n&oacute;i, T&ocirc;n Thiệu ng&agrave;y sau cũng biết đi t&igrave;m kiếm một phen,<br />\r\n<br />\r\nNhưng m&agrave; n&oacute; n&oacute;i rồi, vẫn l&agrave; lấy kh&ocirc;ng cho cự tuyệt khẩu kh&iacute; n&oacute;i. Nếu l&agrave; l&uacute;c trước đời T&ocirc;n Ngộ Kh&ocirc;ng c&aacute; t&iacute;nh, chỉ sợ lập tức sẽ nhảy dựng l&ecirc;n, qu&aacute;t to một tiếng, &quot;Ta ch&iacute;nh l&agrave; Hầu Vương! Nh&igrave;n ta đi cấp c&aacute;c ngươi nhảy th&aacute;c nước!&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu một ch&uacute;t hồi tưởng, bất luận l&agrave; sau đ&oacute; hải ngoại cầu Ti&ecirc;n, vẫn l&agrave; Ngạo Lai Quốc y&ecirc;u cầu binh kh&iacute;, thậm ch&iacute; l&agrave; đi Long Cung mượn bảo, tựa hồ cũng l&agrave; n&agrave;y l&atilde;o hầu n&oacute;i cho T&ocirc;n Ngộ Kh&ocirc;ng.<br />\r\n<br />\r\nMột con khỉ, mặc d&ugrave; sống được lại l&acirc;u, ng&agrave;y ng&agrave;y ở tại Hoa Quả Sơn, l&agrave;m sao c&oacute; thể biết được nhiều như vậy b&iacute; ẩn.<br />\r\n<br />\r\nMặc d&ugrave; kh&ocirc;ng biết c&aacute;i n&agrave;y l&atilde;o hầu tử lai lịch, T&ocirc;n Thiệu nhưng đo&aacute;n ra, c&aacute;i n&agrave;y l&atilde;o hầu tử tuyệt đối c&ugrave;ng thần ti&ecirc;n tr&ecirc;n trời nh&oacute;m rất nhiều li&ecirc;n hệ.<br />\r\n<br />\r\nĐ&ugrave;a giỡn, kiếp trước nh&igrave;n nhiều như vậy tiểu thuyết, c&aacute;i kia trồng ở chủ gi&aacute;c then chốt ph&iacute;a sau điểm một c&acirc;u thần b&iacute; l&atilde;o đầu, kh&ocirc;ng người n&agrave;o l&agrave; khủng bố như vậy tồn tại!<br />\r\n<br />\r\nNhớ tới ở đ&acirc;y, T&ocirc;n Thiệu cười ha ha, nh&igrave;n như mạn bất kinh t&acirc;m n&oacute;i rằng, &quot;Nhảy th&aacute;c nước, bất qu&aacute; s&iacute;nh dũng đấu t&agrave;n nhẫn, n&ecirc;́u dùng phương thức n&agrave;y lựa chọn Đại vương, l&atilde;o T&ocirc;n t&igrave;nh nguyện kh&ocirc;ng l&agrave;m n&agrave;y Hầu Vương! L&atilde;o T&ocirc;n khi Hầu Vương, chỉ l&agrave; v&igrave; bảo vệ những n&agrave;y khỉ con khỉ ch&aacute;u th&ocirc;i! Mặc d&ugrave; l&atilde;o T&ocirc;n đi v&agrave;o nhảy th&aacute;c nước, cũng tuyệt kh&ocirc;ng phải v&igrave; s&iacute;nh dũng đấu t&agrave;n nhẫn!&quot;<br />\r\n<br />\r\nDứt lời, T&ocirc;n Thiệu dẫn một đ&aacute;m khỉ nhỏ, dọc theo khe suối, hướng về khe suối ngọn nguồn t&igrave;m kiếm, nơi đ&oacute; sẽ c&oacute; một đạo th&aacute;c nước, th&aacute;c nước ph&iacute;a sau, t&ecirc;n l&agrave; Thủy Li&ecirc;m Động!<br />\r\n<br />\r\nM&agrave; ở T&ocirc;n Thiệu chờ hầu rời đi ph&iacute;a sau, c&aacute;i kia Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu &aacute;nh s&aacute;ng m&agrave;u xanh đại ph&aacute;t hiện, mấy hơi thở ph&iacute;a sau, h&oacute;a th&agrave;nh một &ocirc;ng gi&agrave; d&aacute;ng dấp. &Ocirc;ng l&atilde;o thần kỵ Thanh Ngưu, một bộ b&aacute;t qu&aacute;i b&agrave;o dược kh&iacute; kh&aacute; n&ugrave;ng, r&acirc;u t&oacute;c bạc phơ, hai mắt nhưng lấp l&aacute;nh c&oacute; thần. Như c&oacute; Thi&ecirc;n Binh Thi&ecirc;n Tướng ở đ&acirc;y, nhất định sẽ ph&aacute;t hiện, c&aacute;i n&agrave;y cưỡi Thanh Ngưu &ocirc;ng l&atilde;o, th&igrave;nh l&igrave;nh c&agrave;ng l&agrave; danh chấn tam giới Đạo Tổ Th&aacute;i Thượng L&atilde;o Qu&acirc;n!<br />\r\n<br />\r\nM&agrave; Th&aacute;i Thượng L&atilde;o Qu&acirc;n th&acirc;m th&uacute;y &aacute;nh mắt, dường như c&oacute; thể xuy&ecirc;n qua tầng tầng rừng rậm, hơi kinh ngạc sau, lộ ra kh&aacute; l&agrave; h&agrave;i l&ograve;ng vẻ mặt,<br />\r\n<br />\r\n&quot;Như vậy đạo t&acirc;m, như vậy tuệ căn, bản th&acirc;n vẫn l&agrave; Ti&ecirc;n Thi&ecirc;n đạo thể, như l&agrave; v&agrave;o ta Đạo M&ocirc;n, trăm ng&agrave;n năm sau nhất định l&agrave; một nh&acirc;n kiệt. Đ&aacute;ng tiếc, Ngọc Ho&agrave;ng Đại Thi&ecirc;n T&ocirc;n c&ograve;n muốn đem lương t&agrave;i như thế, chắp tay tặng cho Phật m&ocirc;n. . .&quot;</p>', '0', 'ta-that-khong-phai-la-ngo-khong', 8, '788', '2022-08-03 05:50:42', '2022-08-03 05:50:42'),
(40, '2', 'Ban ngày thấy ma, Hỏa Nhãn Kim Tình?', '<p>Nhảy l&ecirc;n th&aacute;c nước, tiến nhập Thủy Li&ecirc;m Động, đối với c&oacute; ngh&igrave;n c&acirc;n kh&iacute; lực T&ocirc;n Thiệu tới n&oacute;i, bất qu&aacute; việc nhỏ như con thỏ. Nhảy v&agrave;o Thủy Li&ecirc;m Động, T&ocirc;n Thiệu nước chảy th&agrave;nh s&ocirc;ng trở th&agrave;nh hết thảy vi&ecirc;n hầu thừa nhận Hầu Vương.<br />\r\n<br />\r\nThống ngự to&agrave;n bộ Hoa Quả Sơn h&agrave;ng ng&agrave;n h&agrave;ng vạn vi&ecirc;n hầu, ra lệnh một tiếng, vạn hầu c&ugrave;ng nhau quỳ xuống, cảm gi&aacute;c kia, kiếp trước T&ocirc;n Thiệu l&agrave; chưa bao giờ cảm gi&aacute;c được.<br />\r\n<br />\r\nQuen thuộc T&acirc;y Du K&yacute;,, cộng th&ecirc;m nh&igrave;n v&ocirc; số lần kịch truyền h&igrave;nh T&ocirc;n Thiệu, đối với T&ocirc;n Ngộ Kh&ocirc;ng cuộc đời vẫn l&agrave; c&oacute; hiểu biết.<br />\r\n<br />\r\nT&ocirc;n Ngộ Kh&ocirc;ng, ph&aacute; thạch ra sau trở th&agrave;nh Hoa Quả Sơn Hầu Vương, ph&iacute;a sau hai trăm năm, ban ng&agrave;y sống ph&oacute;ng t&uacute;ng, buổi tối c&ugrave;ng c&aacute;c loại tiểu mẫu hầu sản xuất khỉ nhỏ. M&atilde;i đến tận hai trăm năm sau, T&ocirc;n Ngộ Kh&ocirc;ng nh&igrave;n khắp n&uacute;i khỉ con khỉ ch&aacute;u, mới thở d&agrave;i sinh mệnh tr&ocirc;i qua, sinh ra tu ti&ecirc;n &yacute; nghĩ.</p>', '<p>Nhảy l&ecirc;n th&aacute;c nước, tiến nhập Thủy Li&ecirc;m Động, đối với c&oacute; ngh&igrave;n c&acirc;n kh&iacute; lực T&ocirc;n Thiệu tới n&oacute;i, bất qu&aacute; việc nhỏ như con thỏ. Nhảy v&agrave;o Thủy Li&ecirc;m Động, T&ocirc;n Thiệu nước chảy th&agrave;nh s&ocirc;ng trở th&agrave;nh hết thảy vi&ecirc;n hầu thừa nhận Hầu Vương.<br />\r\n<br />\r\nThống ngự to&agrave;n bộ Hoa Quả Sơn h&agrave;ng ng&agrave;n h&agrave;ng vạn vi&ecirc;n hầu, ra lệnh một tiếng, vạn hầu c&ugrave;ng nhau quỳ xuống, cảm gi&aacute;c kia, kiếp trước T&ocirc;n Thiệu l&agrave; chưa bao giờ cảm gi&aacute;c được.<br />\r\n<br />\r\nQuen thuộc T&acirc;y Du K&yacute;,, cộng th&ecirc;m nh&igrave;n v&ocirc; số lần kịch truyền h&igrave;nh T&ocirc;n Thiệu, đối với T&ocirc;n Ngộ Kh&ocirc;ng cuộc đời vẫn l&agrave; c&oacute; hiểu biết.<br />\r\n<br />\r\nT&ocirc;n Ngộ Kh&ocirc;ng, ph&aacute; thạch ra sau trở th&agrave;nh Hoa Quả Sơn Hầu Vương, ph&iacute;a sau hai trăm năm, ban ng&agrave;y sống ph&oacute;ng t&uacute;ng, buổi tối c&ugrave;ng c&aacute;c loại tiểu mẫu hầu sản xuất khỉ nhỏ. M&atilde;i đến tận hai trăm năm sau, T&ocirc;n Ngộ Kh&ocirc;ng nh&igrave;n khắp n&uacute;i khỉ con khỉ ch&aacute;u, mới thở d&agrave;i sinh mệnh tr&ocirc;i qua, sinh ra tu ti&ecirc;n &yacute; nghĩ.<br />\r\n<br />\r\nMười năm phi&ecirc;u bạt qua biển, mười năm Phương Thốn Sơn khổ tu, khi T&ocirc;n Ngộ Kh&ocirc;ng lại trở lại Hoa Quả Sơn, đ&atilde; l&agrave; c&oacute; thể so với Y&ecirc;u vương tồn tại!<br />\r\n<br />\r\nPh&iacute;a sau hơn 100 năm, T&ocirc;n Ngộ Kh&ocirc;ng liền bắt đầu tiếp tục ăn chơi ch&egrave; ch&eacute;n hưởng thụ Y&ecirc;u vương sinh hoạt, m&atilde;i đến tận 342 tuổi thời gian, bị đầu tr&acirc;u mặt ngựa c&acirc;u v&agrave;o Địa Phủ, bắt đầu rồi hạ náo Địa Phủ, tr&ecirc;n náo Thi&ecirc;n Cung lịch tr&igrave;nh.<br />\r\n<br />\r\nTừ Bật M&atilde; &Ocirc;n đến tr&ocirc;ng giữ B&agrave;n Đ&agrave;o Vi&ecirc;n, lại tới đ&egrave; xuống Ngũ H&agrave;nh Sơn, cuối c&ugrave;ng đến năm trăm năm sau t&acirc;y thi&ecirc;n thỉnh kinh, T&ocirc;n Ngộ Kh&ocirc;ng nhưng là kh&ocirc;ng ch&uacute;t n&agrave;o tu luyện!<br />\r\n<br />\r\nNghĩ tới những thứ n&agrave;y, T&ocirc;n Thiệu cảnh gi&aacute;c sau khi, c&agrave;ng là k&iacute;ch động kh&ocirc;ng th&ocirc;i. K&iacute;ch động l&agrave; T&ocirc;n Ngộ Kh&ocirc;ng Ti&ecirc;n Thi&ecirc;n đạo thể, vẻn vẹn tu luyện 20 năm liền trở th&agrave;nh c&oacute; thể so với Y&ecirc;u vương tồn tại, c&agrave;ng l&agrave; trở th&agrave;nh Na Tra, Nhị Lang Thần cấp bậc cường giả. N&agrave;y l&agrave;m sao c&oacute; thể kh&ocirc;ng để T&ocirc;n Thiệu k&iacute;ch động.<br />\r\n<br />\r\nPhải biết, thỉnh kinh tr&ecirc;n đường gặp phải y&ecirc;u qu&aacute;i, kh&ocirc;ng người n&agrave;o l&agrave; tu luyện h&agrave;ng trăm h&agrave;ng ng&agrave;n năm, lại như Nhị Lang Thần c&ugrave;ng Na Tra, đều l&agrave; từ nh&agrave; &Acirc;n cuộc chiến Phong Thần ra trải qua luyện ra được cường giả trẻ tuổi. T&ocirc;n Ngộ Kh&ocirc;ng vẻn vẹn tu luyện 20 năm liền c&oacute; thể chống đỡ l&ecirc;n người kh&aacute;c h&agrave;ng trăm h&agrave;ng ng&agrave;n năm, như l&agrave; n&agrave;y một đời T&ocirc;n Thiệu kh&ocirc;ng l&atilde;ng ph&iacute; thời gian, khổ tu 342 năm, đến rồi nội dung vở kịch bắt đầu thời gian, thật l&agrave; l&agrave; như thế n&agrave;o kinh khủng tu vi!<br />\r\n<br />\r\nE sợ mặc d&ugrave; l&agrave; Như Lai Phật Tổ, muốn đem ch&iacute;nh m&igrave;nh đ&egrave; xuống Ngũ H&agrave;nh Sơn, cũng kh&ocirc;ng d&ecirc;̃ dàng!<br />\r\n<br />\r\nBất qu&aacute;, cũng ch&iacute;nh l&agrave; bởi v&igrave; T&ocirc;n Ngộ Kh&ocirc;ng tư chất th&acirc;n thể phi ph&agrave;m, T&ocirc;n Thiệu mới c&agrave;ng th&ecirc;m cảnh gi&aacute;c, ch&iacute;nh m&igrave;nh n&agrave;y một đời, tuyệt kh&ocirc;ng c&oacute; thể giống T&ocirc;n Ngộ Kh&ocirc;ng giống như vậy, sống uổng thời gian, nhất định phải gia tăng tu luyện! Nhất định kh&ocirc;ng thể v&agrave;o Phật m&ocirc;n l&agrave;m h&ograve;a thượng, thật vất vả khi một lần T&ocirc;n Ngộ Kh&ocirc;ng, kh&ocirc;ng gặp gỡ Bạch Cốt Tinh muội muội, Tử H&agrave; tỷ tỷ, liền qu&aacute; l&atilde;ng ph&iacute;.<br />\r\n<br />\r\nThời gian hơn ba năm b&ecirc;n trong, T&ocirc;n Thiệu th&acirc;n l&agrave; Hầu Vương, kh&ocirc;ng c&oacute; ăn chơi ch&egrave; ch&eacute;n hưởng thụ sinh hoạt, m&agrave; l&agrave; mang theo kh&ocirc;ng &iacute;t vi&ecirc;n hầu thủ hạ, dọc theo Hoa Quả Sơn mấy phạm vi trăm dặm, c&agrave;n qu&eacute;t hổ b&aacute;o m&atilde;nh th&uacute;, v&igrave; ch&iacute;nh m&igrave;nh khỉ con khỉ ch&aacute;u tạo ph&uacute;c. Lấy T&ocirc;n Thiệu ngh&igrave;n c&acirc;n cự lực, th&ecirc;m v&agrave;o thủ hạ v&ocirc; số, tầm thường hổ b&aacute;o ở đ&acirc;u l&agrave; T&ocirc;n Thiệu đối thủ. Săn được hổ b&aacute;o da l&ocirc;ng, T&ocirc;n Thiệu mệnh lệnh thủ hạ chế th&agrave;nh c&aacute;c loại &aacute;o b&agrave;o, để thủ hạ của ch&iacute;nh m&igrave;nh mấy trăm vi&ecirc;n hầu thị vệ đều mặc tr&ecirc;n &aacute;o da th&uacute; vật.</p>\r\n\r\n<p><br />\r\nNguy&ecirc;n nh&acirc;n ở trong, tự nhi&ecirc;n l&agrave; bởi v&igrave; T&ocirc;n Thiệu th&acirc;n v&igrave; nh&acirc;n loại &yacute; thức t&aacute;c qu&aacute;i. Nh&igrave;n mấy trăm c&ocirc;ng hầu tử rũ tiểu đệ đệ, th&acirc;n thể trần truồng theo sau lưng, T&ocirc;n Thiệu lu&ocirc;n cảm thấy hoa c&uacute;c đ&aacute;ng lo. M&agrave; T&ocirc;n Thiệu ch&iacute;nh m&igrave;nh, c&agrave;ng là kho&aacute;c một th&acirc;n m&agrave;u trắng da hổ chiến b&agrave;o, xem ra hơi c&oacute; ch&uacute;t uy phong lẫm lẫm.<br />\r\n<br />\r\nT&ocirc;n Thiệu những h&agrave;nh vi n&agrave;y, rơi v&agrave;o Th&aacute;i Thượng L&atilde;o Qu&acirc;n h&oacute;a th&agrave;nh Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu trong mắt, kinh ngạc sau khi, h&agrave;i l&ograve;ng gật đầu kh&ocirc;ng ngớt, &quot;C&oacute; &yacute; tứ, xem ra l&atilde;o phu vẫn l&agrave; đ&aacute;nh gi&aacute; thấp c&aacute;i n&agrave;y đầu khỉ. Th&acirc;n l&agrave; Hầu Vương, kh&ocirc;ng ham muốn hưởng lạc, lấy m&igrave;nh l&agrave;m gương, v&igrave; l&agrave; con d&acirc;n thanh trừ d&atilde; th&uacute; mối họa. Kh&ocirc;ng chỉ c&oacute; như vậy, c&agrave;ng là hiểu được gi&aacute;o h&oacute;a con d&acirc;n, để d&atilde; t&iacute;nh nan tuần vi&ecirc;n hầu mặc v&agrave;o y vật. Xưa kia c&oacute; Đế Vũ ch&iacute;n qu&aacute; gia tộc kh&ocirc;ng v&agrave;o, b&acirc;y giờ c&oacute; Hầu Vương ba năm qu&aacute; gia tộc kh&ocirc;ng v&agrave;o, ha ha, c&oacute; &yacute; tứ a, n&agrave;y đầu khỉ nếu l&agrave; ở thời đại hồng hoang, nhất định c&oacute; thể trở th&agrave;nh Nh&acirc;n vương, Y&ecirc;u Đế một loại nh&acirc;n vật c&aacute;i thế. . . Đưa cho Phật m&ocirc;n qu&aacute; đ&aacute;ng tiếc, l&atilde;o phu c&oacute; hay kh&ocirc;ng n&ecirc;n gi&uacute;p hắn một ch&uacute;t đ&acirc;y? Bị hồ đồ rồi, l&atilde;o phu bị hồ đồ rồi, hết thảy đều đổi tu&acirc;n theo đạo ph&aacute;p tự nhi&ecirc;n, cứ nh&igrave;n đầu khỉ tự th&acirc;n tạo h&oacute;a đi. . .&quot;<br />\r\n<br />\r\nThời gian hơn ba năm, T&ocirc;n Thiệu rốt cục đem Hoa Quả Sơn mấy phạm vi trăm dặm m&atilde;nh th&uacute; đều c&agrave;n qu&eacute;t xong xu&ocirc;i, cả người đều mỏi mệt trở lại Thủy Li&ecirc;m Động, nhất thời chịu đến m&acirc;́y vạn con vượn long trọng ngh&ecirc;nh tiếp!<br />\r\n<br />\r\nBởi hổ b&aacute;o rắn độc đều bị T&ocirc;n Thiệu dọn dẹp gần như, đ&aacute;m khỉ vượn ở thiếu thi&ecirc;n địch, c&oacute; thể an cư lạc nghiệp, trong v&ograve;ng ba năm số lượng tăng mấy lần,<br />\r\n<br />\r\nNh&igrave;n thấy thủ hạ quản l&yacute; vi&ecirc;n hầu c&agrave;ng ng&agrave;y c&agrave;ng nhiều, cực khổ rồi ba năm T&ocirc;n Thiệu nhất thời vui kh&ocirc;n tả.<br />\r\n<br />\r\nTheo chiếu tốc độ như vậy sinh s&ocirc;i xuống, 340 năm sau, ch&iacute;nh m&igrave;nh n&ecirc;n c&oacute; bao nhi&ecirc;u vi&ecirc;n hầu b&agrave;y xuống, chỉ sợ sẽ c&oacute; mấy trăm triệu đi! Khi đ&oacute;, ch&iacute;nh m&igrave;nh kh&ocirc;ng động thủ, c&aacute;i g&igrave; Cự Linh Tướng c&aacute;c loại nhị lưu Thi&ecirc;n Tướng cũng kh&ocirc;ng d&aacute;m đ&aacute;nh tới cửa!<br />\r\n<br />\r\nD&ugrave; sao con kiến hơn nhiều, cũng l&agrave; c&oacute; thể cắn chết voi lớn! Huống chi l&agrave; ở T&ocirc;n Thiệu huấn luyện hạ, mỗi người long tinh hổ m&atilde;nh vi&ecirc;n hầu chiến tướng!<br />\r\n<br />\r\nTượng đ&aacute; tr&ecirc;n vương tọa b&agrave;y ra mấy tầng da hổ, T&ocirc;n Thiệu ngồi ở hổ tr&ecirc;n da, uống đ&aacute;m khỉ vượn sản xuất &quot;Hầu Nhi Tửu&quot;, ăn c&aacute;c loại hiếm qu&yacute; hoa quả, thoả th&iacute;ch thả lỏng ba năm b&ocirc;n ba uể oải.<br />\r\n<br />\r\nKh&ocirc;ng thể kh&ocirc;ng n&oacute;i, so với kiếp trước y&ecirc;u th&iacute;ch g&acirc;y hấn g&acirc;y chuyện T&ocirc;n Ngộ Kh&ocirc;ng, T&ocirc;n Thiệu c&aacute;i n&agrave;y Hầu Vương l&agrave;m được cực kỳ xứng chức! M&agrave; hắn xứng chức, đổi lấy ch&iacute;nh l&agrave; vi&ecirc;n hầu con d&acirc;n ch&acirc;n t&acirc;m ủng hộ. So với lo&agrave;i người c&acirc;u t&acirc;m đấu gi&aacute;c, hầu nhi nh&oacute;m hiển nhi&ecirc;n đơn giản hơn nhiều. Như giờ khắc này c&oacute; Thi&ecirc;n Binh Thi&ecirc;n Tướng d&aacute;m đến nắm bắt T&ocirc;n Thiệu, n&agrave;y m&acirc;́y vạn hầu nhi tuyệt đối sẽ liều mạng che ở T&ocirc;n Thiệu trước người.<br />\r\n<br />\r\n&quot;Đại vương, đ&acirc;y l&agrave; Hoa Quả Sơn con d&acirc;n v&igrave; l&agrave; Đại vương chọn lựa khu&ocirc;n mặt đẹp hầu nương, mời Đại vương t&ugrave;y &yacute; hưởng dụng. . .&quot;<br />\r\n<br />\r\nỞ T&ocirc;n Thiệu ước mơ cuộc sống tốt đẹp thời điểm, một c&aacute;i lưng trắng tinh tinh x&ocirc; ra hai b&ecirc;n ch&uacute;ng hầu, đi tới vương tọa b&ecirc;n dưới quỳ xuống, hung &aacute;c tr&ecirc;n gương mặt, nhưng tr&agrave;n đầy đối với T&ocirc;n Thiệu t&ocirc;n k&iacute;nh. Ở tinh tinh ph&iacute;a sau, theo mấy chục mẫu hầu, đều l&agrave; &aacute;nh mắt n&oacute;ng rực m&agrave; nh&igrave;n T&ocirc;n Thiệu.<br />\r\n<br />\r\n&Aacute;nh mắt kia, như hồn nhi&ecirc;n thiếu nữ nh&igrave;n thấy t&igrave;nh nh&acirc;n trong mộng kh&ocirc;ng kh&aacute;c nhau ch&uacute;t n&agrave;o!</p>\r\n\r\n<p><br />\r\n<br />\r\nM&agrave; nghe được n&agrave;y lưng trắng tinh tinh lời n&oacute;i, T&ocirc;n Thiệu nhất thời một c&aacute;i Hầu Nhi Tửu phun ra ngo&agrave;i, uống nửa ng&agrave;y mới chậm qu&aacute; kh&iacute;, &quot;T&ocirc;n Đại, bản Vương kh&ocirc;ng cần hầu nương hầu hạ, để cho bọn họ đi xuống đi. . .&quot;<br />\r\n<br />\r\nĐ&ugrave;a giỡn, như l&agrave; T&ocirc;n Thiệu thật sự c&ugrave;ng những n&agrave;y mẫu hầu nh&oacute;m giao phối, đ&oacute; thật đ&uacute;ng l&agrave; qu&aacute; khẩu vị nặng. T&ocirc;n Thiệu kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; ngồi trong l&ograve;ng m&agrave; vẫn kh&ocirc;ng loạn qu&acirc;n tử, bất qu&aacute; để hắn c&ugrave;ng mẫu hầu c&ugrave;ng giường c&ugrave;ng gối, n&oacute;i thật, hắn kh&ocirc;ng l&agrave;m được, chết cũng kh&ocirc;ng l&agrave;m được!<br />\r\n<br />\r\nHắn cũng kh&ocirc;ng muốn c&ugrave;ng kiếp trước T&ocirc;n Ngộ Kh&ocirc;ng giống như, ở Hoa Quả Sơn lưu lại v&ocirc; số vi&ecirc;n hầu con ch&aacute;u. . . D&ugrave; sao, hắn l&agrave; T&ocirc;n Ngộ Kh&ocirc;ng, cũng kh&ocirc;ng phải hầu!<br />\r\n<br />\r\nT&ecirc;n l&agrave; T&ocirc;n Đại lưng trắng tinh tinh, gặp Hầu Vương l&atilde;o đại kh&ocirc;ng h&aacute;o sắc, trong mắt c&agrave;ng th&ecirc;m k&iacute;nh nể, ra lệnh một tiếng, để một đ&aacute;m hầu nương tản đi, m&igrave;nh th&igrave; đối với T&ocirc;n Thiệu c&agrave;ng th&ecirc;m khăng khăng một mực.<br />\r\n<br />\r\nM&agrave; Th&aacute;i Thượng L&atilde;o Qu&acirc;n biến h&oacute;a Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu, th&igrave; lại nh&igrave;n T&ocirc;n Thiệu, lộ ra nụ cười &yacute; vị th&acirc;m trường, &quot;Phật m&ocirc;n Th&iacute;ch Tổ ở đắc đạo trước, đều c&ograve;n lưu lại con ch&aacute;u hậu duệ, ngươi nếu muốn v&agrave;o Phật m&ocirc;n, sinh s&ocirc;i con ch&aacute;u cũng kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; đại sự. Bất qu&aacute;, ngươi dĩ nhi&ecirc;n kh&ocirc;ng h&aacute;o sắc. . . Ha ha, c&oacute; &yacute; tứ, chẳng lẽ ngươi muốn v&agrave;o ta Đạo M&ocirc;n hay sao? Chỉ c&oacute; ta Đạo M&ocirc;n, ở căn cơ vững chắc trước, mới kh&ocirc;ng th&iacute;ch hợp hư th&acirc;n. . .&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu cũng kh&ocirc;ng biết, trong ba năm hắn c&aacute;c loại th&agrave;nh tựu, c&agrave;ng để tam giới nổi tiếng Đạo Tổ khen kh&ocirc;ng dứt miệng. Đối với c&aacute;i n&agrave;y &aacute;nh mắt sắc b&eacute;n đến đ&aacute;ng sợ Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu, hắn vẫn lu&ocirc;n ở cẩn thận đề ph&ograve;ng, chỉ lo n&agrave;y Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu g&agrave;i bẫy m&igrave;nh.<br />\r\n<br />\r\nT&ocirc;n Đại đưa tới kh&uacute;c nhạc dạo ngắn kết th&uacute;c, Thủy Li&ecirc;m Động tiệc rượu cũng đến rồi kết th&uacute;c, T&ocirc;n Thiệu mặt khỉ đỏ hơn, hai mắt trời đất quay cuồng, đột nhi&ecirc;n, hắn mơ hồ nh&igrave;n thấy trước người kh&ocirc;ng xa, c&oacute; hai b&oacute;ng người đi qua.<br />\r\n<br />\r\nB&oacute;ng người kia, T&ocirc;n Thiệu nh&igrave;n ra rất mơ hồ, nhưng m&agrave; hắn nhưng ch&acirc;n ch&acirc;n thực thực thấy được b&oacute;ng người kia. D&ugrave;ng sức m&agrave; nh&aacute;y mắt một c&aacute;i, T&ocirc;n Thiệu mắt đột nhi&ecirc;n dường như hỏa thi&ecirc;u gi&ocirc;́ng như bỏng, l&agrave;m cho hắn cuống qu&iacute;t nhắm hai mắt lại.<br />\r\n<br />\r\nKhi hắn lại mở hai mắt ra thời điểm, hai mắt c&agrave;ng nổi l&ecirc;n kim quang, m&agrave; c&aacute;i kia hai đ&aacute;m bỗng dưng b&oacute;ng người xuất hiện, cũng kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o tr&aacute;nh được T&ocirc;n Thiệu &aacute;nh mắt.<br />\r\n<br />\r\nHai người kia, một c&aacute;i đầu tr&acirc;u th&acirc;n người, một c&aacute;i ngựa mặt nh&acirc;n th&acirc;n, th&acirc;n mang m&agrave;u đen sai dịch trang phục, đi tới một c&aacute;i n&agrave;o đ&oacute; say rượu khỉ nhỏ b&ecirc;n người, lấy ra một c&aacute;i t&uacute;i vải m&agrave;u đen, đối với khỉ nhỏ một tấm, con khỉ nhỏ kia hư huyễn hồn ph&aacute;ch liền bị hai người thu v&agrave;o trong t&uacute;i. Sau đ&oacute;, đầu tr&acirc;u th&acirc;n người sai dịch đối với ngựa mặt n&oacute;i rằng, &quot;Được rồi, Hoa Quả Sơn sống cũng l&agrave;m xong, tiếp đ&oacute;, liền đi bắt c&aacute;i kia Khiếu Nguyệt Lang Vương đi! Kh&agrave; kh&agrave;, d&ugrave; sao cũng l&agrave; một đời Y&ecirc;u vương, ta c&ograve;n thực sự hơi sốt sắng.&quot;<br />\r\n<br />\r\n&quot;Căng thẳng c&aacute;i g&igrave;, mặc d&ugrave; hắn l&agrave; một đời Y&ecirc;u vương, cũng bất qu&aacute; trọng thương gần chết. Đừng qu&ecirc;n ch&uacute;ng ta ca hai đầu m&ocirc;i ch&oacute;t lưỡi hương vị, Di&ecirc;m Vương muốn hắn canh ba chết, kh&ocirc;ng biết lưu hắn đến năm canh! Chỉ tiếc, một đời Y&ecirc;u vương, độc th&acirc;n chết ở hoang sơn d&atilde; lĩnh, lưu lại v&ocirc; số bảo vật, chưa từng người đi nắm. Kh&agrave; kh&agrave;, c&oacute; thể là anh ta hai đời &acirc;m ty sai dịch, mang theo Nh&acirc;n giới đồ vật sẽ Địa Phủ, nhưng l&agrave; phải chịu đến phạt nặng.&quot;</p>\r\n\r\n<p><br />\r\nĐầu tr&acirc;u mặt ngựa hai đạo hư huyễn b&oacute;ng người, dẹp xong khỉ nhỏ hồn ph&aacute;ch sau, liền hướng về Thủy Li&ecirc;m Động ở ngo&agrave;i đi đến, m&agrave; giờ khắc n&agrave;y T&ocirc;n Thiệu, trong l&ograve;ng vừa mừng vừa sợ,<br />\r\n<br />\r\nSợ ch&iacute;nh l&agrave; m&igrave;nh r&otilde; r&agrave;ng ng&agrave;y, dĩ nhi&ecirc;n thấy được trong truyền thuyết Quỷ sai đầu tr&acirc;u mặt ngựa, vui ch&iacute;nh l&agrave; nghe xong ngưu đầu m&atilde; diện lời, c&aacute;i kia sắp sửa bị c&acirc;u đến Địa Phủ Lang Vương, sẽ lưu lại rất nhiều bảo vật.<br />\r\n<br />\r\nBảo vật đ&uacute;ng l&agrave; thứ yếu, d&ugrave; sao chiếm được T&ocirc;n Thiệu cũng kh&ocirc;ng biết l&agrave;m như thế n&agrave;o d&ugrave;ng, bất qu&aacute; T&ocirc;n Thiệu hiện tại v&ocirc; c&ugrave;ng cần thiết một &iacute;t tu luyện ph&aacute;p m&ocirc;n, ch&iacute;nh m&igrave;nh trong ba năm d&ugrave;ng hết Hầu Vương tr&aacute;ch nhiệm, cần tu h&agrave;nh thời điểm, nhưng lại kh&ocirc;ng biết tu luyện ph&aacute;p m&ocirc;n. M&agrave; khoảng c&aacute;ch đi tới Linh Đ&agrave;i Phương Thốn Sơn b&aacute;i sư nội dung vở kịch, c&ograve;n c&oacute; r&ograve;ng r&atilde; hai trăm năm mới bắt đầu. T&ocirc;n Thiệu c&oacute; thể kh&ocirc;ng muốn l&atilde;ng ph&iacute; n&agrave;y hai trăm năm thời gian tu luyện!<br />\r\n<br />\r\nC&aacute;i kia Khiếu Nguyệt Lang Vương lưu lại bảo vật b&ecirc;n trong, lẽ n&agrave;o sẽ kh&ocirc;ng c&oacute; tu luyện ph&aacute;p m&ocirc;n sao! Ha ha, l&agrave;m sao c&oacute; khả năng sẽ kh&ocirc;ng c&oacute; đ&acirc;y?<br />\r\n<br />\r\n&quot;Kh&agrave; kh&agrave;, với bọn hắn đến xem nh&igrave;n, l&atilde;o T&ocirc;n số tuổi thọ kh&ocirc;ng tới, lượng bọn họ cũng kh&ocirc;ng d&aacute;m đụng đến ta! Y&ecirc;u vương truyền thừa, thuộc về l&atilde;o T&ocirc;n!&quot;<br />\r\n<br />\r\nKhi xuy&ecirc;n qua Thủy Li&ecirc;m Động th&aacute;c nước thời điểm, T&ocirc;n Thiệu trong l&uacute;c v&ocirc; t&igrave;nh tho&aacute;ng nh&igrave;n, nhất thời giật nảy cả m&igrave;nh, xuy&ecirc;n thấu qua dưới th&aacute;c nước t&agrave;n tạ h&igrave;nh chiếu, T&ocirc;n Thiệu r&otilde; r&agrave;ng nh&igrave;n thấy, con mắt của ch&iacute;nh m&igrave;nh, c&agrave;ng đ&atilde; biến th&agrave;nh m&agrave;u v&agrave;ng. Trong l&ograve;ng hơi động, n&agrave;y m&agrave;u v&agrave;ng liền t&ugrave;y theo thối lui, lại hơi động, hai mắt lại một lần h&oacute;a th&agrave;nh m&agrave;u v&agrave;ng. Mắt gặp ở đ&acirc;y, T&ocirc;n Thiệu cao hứng hầu như liền muốn ngửa l&ecirc;n trời cười ha hả, &quot;Hỏa Nh&atilde;n Kim T&igrave;nh! Kh&oacute; tr&aacute;ch ta c&oacute; thể nh&igrave;n thấy đầu tr&acirc;u mặt ngựa, nguy&ecirc;n lai ta c&ograve;n c&oacute; Hỏa Nh&atilde;n Kim T&igrave;nh!&quot;<br />\r\n<br />\r\nBất qu&aacute; giờ khắc này ngược lại kh&ocirc;ng phải l&agrave; vui sướng thời điểm, T&ocirc;n Thiệu ổn ổn t&acirc;m t&igrave;nh, lẫm lẫm liệt liệt theo đầu tr&acirc;u mặt ngựa hướng Hoa Quả Sơn tr&ecirc;n một c&aacute;i n&agrave;o đ&oacute; tiểu đường đi tới. T&ocirc;n Thiệu kh&ocirc;ng ch&uacute;t n&agrave;o sợ đầu tr&acirc;u mặt ngựa sẽ l&agrave;m kh&oacute; m&igrave;nh, đ&ugrave;a giỡn, bọn họ kh&ocirc;ng tất cả n&oacute;i, Địa Phủ l&agrave; c&oacute; quy củ.<br />\r\n<br />\r\nM&agrave; ở T&ocirc;n Thiệu rời đi ph&iacute;a sau, Th&aacute;i Thượng L&atilde;o Qu&acirc;n biến th&agrave;nh Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu, lu&ocirc;n lu&ocirc;n mưa gi&oacute; bất động tr&ecirc;n mặt, lần đầu lộ ra kinh sợ, &quot;Ph&aacute;p Mục ! N&agrave;y đầu khỉ dĩ nhi&ecirc;n c&oacute; Ti&ecirc;n Thi&ecirc;n Ph&aacute;p Mục !&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu kh&ocirc;ng biết c&aacute;i g&igrave; l&agrave; Ti&ecirc;n Thi&ecirc;n Ph&aacute;p Mục, m&agrave; Th&aacute;i Thượng L&atilde;o Qu&acirc;n th&acirc;n l&agrave; Đạo M&ocirc;n thuỷ tổ, h&aacute; c&oacute; thể kh&ocirc;ng biết. Ti&ecirc;n Thi&ecirc;n Ph&aacute;p Mục, l&agrave; c&oacute; thể l&ecirc;n cấp , c&ograve;n cuối c&ugrave;ng sẽ c&oacute; thần th&ocirc;ng n&agrave;o, cũng kh&ocirc;ng người n&agrave;o biết! Như l&agrave; trong Thi&ecirc;n Đ&igrave;nh Thi&ecirc;n L&yacute; Nh&atilde;n, liền nắm giữ Ti&ecirc;n Thi&ecirc;n Ph&aacute;p Mục, m&agrave; hắn Ph&aacute;p Mục năng lực, ch&iacute;nh l&agrave; nh&igrave;n thấy ngo&agrave;i vạn dặm sự vật!<br />\r\n<br />\r\nCho tới Nhị Lang Thần thứ ba mắt, c&ugrave;ng với kiếp trước T&ocirc;n Ngộ Kh&ocirc;ng Hỏa Nh&atilde;n Kim T&igrave;nh, đều l&agrave; hậu thi&ecirc;n tạo th&agrave;nh, kh&ocirc;ng c&aacute;ch n&agrave;o đang tăng l&ecirc;n Ph&aacute;p Mục đẳng cấp, ngoại trừ nh&igrave;n thấu y&ecirc;u h&igrave;nh, cũng kh&ocirc;ng c&oacute; qu&aacute; to lớn c&ocirc;ng năng.<br />\r\n<br />\r\nM&agrave; T&ocirc;n Thiệu c&oacute; thể mang theo Ti&ecirc;n Thi&ecirc;n Ph&aacute;p Mục xuy&ecirc;n qua, tiền đồ quyết định kh&ocirc;ng thể đo lường!</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:587px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'ban-ngay-thay-ma-hoa-nhan-kim-tinh', 8, '378', '2022-08-03 05:52:05', '2022-08-03 05:52:05');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(41, '3', '36 biến?', '<p>T&ocirc;n Thiệu lẫm lẫm liệt liệt c&ugrave;ng sau lưng đầu tr&acirc;u mặt ngựa, đầy đủ c&ugrave;ng hơn một canh giờ, dọc theo hắn hai người đi đường nhỏ, đi tới một chỗ trọc lốc v&aacute;ch đ&aacute; trước. Ngay v&agrave;o l&uacute;c n&agrave;y, đầu tr&acirc;u mặt ngựa dừng lại.<br />\r\n<br />\r\nM&atilde; Diện: &quot;Ngưu đại ca, ngươi n&oacute;i c&ugrave;ng sau lưng ch&uacute;ng ta c&aacute;i kia tiểu tử ngốc, kh&ocirc;ng sẽ l&agrave; nh&igrave;n thấy ch&uacute;ng ta chứ? Mọi người kh&ocirc;ng phải thường n&oacute;i sao, kẻ ngu si l&agrave; c&oacute; thể nh&igrave;n thấy quỷ, ta nh&igrave;n tiểu tử ngốc n&agrave;y, liền rất ngốc. . .&quot;<br />\r\n<br />\r\nNgưu Đầu: &quot;M&atilde; hiền đệ, ngươi lo xa rồi. Hắn như kh&ocirc;ng phải người ngu, liền kh&ocirc;ng nh&igrave;n thấy ch&uacute;ng ta, hắn như l&agrave; người ngu, liền nhất định kh&ocirc;ng t&igrave;m được tiến nhập c&aacute;i n&agrave;y động phủ con đường. Quản hắn c&oacute; phải hay kh&ocirc;ng kẻ ngu si, đều ngại kh&ocirc;ng tới hai ta ban sai.&quot;<br />\r\n<br />\r\nN&oacute;i xong, đầu tr&acirc;u mặt ngựa lay động th&acirc;n thể, tại chỗ biến mất, c&agrave;ng chẳng biết đi đ&acirc;u.<br />\r\n<br />\r\nM&agrave; T&ocirc;n Thiệu cố n&eacute;n trong l&ograve;ng nổi giận, một mạch mắng l&ecirc;n, &quot;Ngươi mới l&agrave; người ngu, cả nh&agrave; ngươi đều l&agrave; người ngu!&quot;<br />\r\n&nbsp;</p>', '<p>T&ocirc;n Thiệu lẫm lẫm liệt liệt c&ugrave;ng sau lưng đầu tr&acirc;u mặt ngựa, đầy đủ c&ugrave;ng hơn một canh giờ, dọc theo hắn hai người đi đường nhỏ, đi tới một chỗ trọc lốc v&aacute;ch đ&aacute; trước. Ngay v&agrave;o l&uacute;c n&agrave;y, đầu tr&acirc;u mặt ngựa dừng lại.<br />\r\n<br />\r\nM&atilde; Diện: &quot;Ngưu đại ca, ngươi n&oacute;i c&ugrave;ng sau lưng ch&uacute;ng ta c&aacute;i kia tiểu tử ngốc, kh&ocirc;ng sẽ l&agrave; nh&igrave;n thấy ch&uacute;ng ta chứ? Mọi người kh&ocirc;ng phải thường n&oacute;i sao, kẻ ngu si l&agrave; c&oacute; thể nh&igrave;n thấy quỷ, ta nh&igrave;n tiểu tử ngốc n&agrave;y, liền rất ngốc. . .&quot;<br />\r\n<br />\r\nNgưu Đầu: &quot;M&atilde; hiền đệ, ngươi lo xa rồi. Hắn như kh&ocirc;ng phải người ngu, liền kh&ocirc;ng nh&igrave;n thấy ch&uacute;ng ta, hắn như l&agrave; người ngu, liền nhất định kh&ocirc;ng t&igrave;m được tiến nhập c&aacute;i n&agrave;y động phủ con đường. Quản hắn c&oacute; phải hay kh&ocirc;ng kẻ ngu si, đều ngại kh&ocirc;ng tới hai ta ban sai.&quot;<br />\r\n<br />\r\nN&oacute;i xong, đầu tr&acirc;u mặt ngựa lay động th&acirc;n thể, tại chỗ biến mất, c&agrave;ng chẳng biết đi đ&acirc;u.<br />\r\n<br />\r\nM&agrave; T&ocirc;n Thiệu cố n&eacute;n trong l&ograve;ng nổi giận, một mạch mắng l&ecirc;n, &quot;Ngươi mới l&agrave; người ngu, cả nh&agrave; ngươi đều l&agrave; người ngu!&quot;<br />\r\n<br />\r\nHồi tưởng kiếp trước thấy qua ti&ecirc;n hiệp tiểu thuyết, T&ocirc;n Thiệu trong l&ograve;ng biết, ở đ&acirc;y nhất định cất giấu một c&aacute;i lối v&agrave;o, chỉ l&agrave; lấy ch&iacute;nh m&igrave;nh thể x&aacute;c ph&agrave;m tục, l&agrave; kh&ocirc;ng nh&igrave;n ra.<br />\r\n<br />\r\nTrong l&ograve;ng hơi động, T&ocirc;n Thiệu hai mắt h&oacute;a th&agrave;nh m&agrave;u v&agrave;ng, trước mắt v&aacute;ch đ&aacute; nhất thời lộ ra một c&aacute;i hư ảo lối v&agrave;o, &quot;L&atilde;o T&ocirc;n c&oacute; Hỏa Nh&atilde;n Kim T&igrave;nh, lẽ n&agrave;o cũng sẽ t&igrave;m kh&ocirc;ng được lối v&agrave;o!&quot;<br />\r\n<br />\r\nDứt lời, T&ocirc;n Thiệu một mạch chui v&agrave;o c&aacute;i kia v&aacute;ch đ&aacute; lối v&agrave;o b&ecirc;n trong.<br />\r\n<br />\r\nSơn động cũng kh&ocirc;ng lớn, chỉ c&oacute; điều đi rồi m&acirc;́y trăm m&eacute;t liền đến t&acirc;̣n đầu, ngoại trừ lối v&agrave;o ảo trận, hang n&uacute;i n&agrave;y kh&ocirc;ng c&oacute; ph&ograve;ng ngự ch&uacute;t n&agrave;o, kh&ocirc;ng c&ograve;n g&igrave; kh&aacute;c. Sơn động t&acirc;̣n đầu đống một ch&uacute;t cỏ kh&ocirc;, m&agrave; một c&aacute;i gi&agrave; nua ng&acirc;n b&agrave;o &ocirc;ng l&atilde;o, đang h&ocirc;n m&ecirc; ở tr&ecirc;n đống cỏ kh&ocirc;, kh&ocirc;ng r&otilde; sống chết.<br />\r\n<br />\r\nMắt gặp ở đ&acirc;y, T&ocirc;n Thiệu trong l&ograve;ng kinh h&atilde;i, &quot;Kh&ocirc;ng nghĩ tới tại ch&iacute;nh m&igrave;nh Thủy Li&ecirc;m Động kh&ocirc;ng xa, dĩ nhi&ecirc;n cất giấu một c&aacute;i Y&ecirc;u vương. Nếu như thương thế hắn hồi phục, trực tiếp chiếm Hoa Quả Sơn, l&atilde;o T&ocirc;n n&agrave;y Hầu Vương c&ograve;n c&oacute; l&agrave;m được hay kh&ocirc;ng! Chết thật tốt! Đầu tr&acirc;u mặt ngựa, bắt đi hắn, kh&aacute; lắm!&quot;<br />\r\n<br />\r\nM&atilde; Diện: &quot;Ngưu đại ca, n&agrave;y kẻ ngu si l&agrave;m sao c&ugrave;ng v&agrave;o được, ngươi kh&ocirc;ng phải n&oacute;i, kẻ ngu si kh&ocirc;ng v&agrave;o được sao?&quot;<br />\r\n<br />\r\nNgưu Đầu: &quot;M&atilde; hiền đệ, tin tưởng Ngưu đại ca, kh&ocirc;ng sai. Thỉnh thoảng c&oacute; l&atilde;o lời n&oacute;i, kẻ ngốc c&oacute; ngốc ph&uacute;c, n&agrave;y kẻ ngu si c&oacute; thể đi nhầm v&agrave;o nơi n&agrave;y, được Y&ecirc;u vương lưu lại bảo vật, đại kh&aacute;i l&agrave; &ocirc;ng trời đối với hắn đ&aacute;ng thương.&quot;<br />\r\n<br />\r\nM&atilde; Diện: &quot;Ngưu đại ca, nhưng là n&agrave;y kẻ ngu si l&agrave;m sao nh&igrave;n chết người cười đấy? Ta c&oacute; thể sợ nhất kẻ ngu, năm trước đi n&agrave;o đ&oacute; n&agrave;o đ&oacute; th&ocirc;n bắt người, đ&atilde; bị một kẻ ngu tạt một th&acirc;n ch&oacute; nước tiểu. . .&quot;<br />\r\n<br />\r\nNgưu Đầu: &quot;Ai, &Acirc;m sai kh&oacute;c, &Acirc;m sai mệt, &Acirc;m sai kh&ocirc;ng c&oacute; bảo hộ ph&iacute;. Đừng động c&aacute;i kia kẻ ngu, ch&uacute;ng ta mau mau bắt người, cẩn thận đợi l&aacute;t nữa n&oacute; nước tiểu ngươi một th&acirc;n hầu nước tiểu.&quot;<br />\r\n<br />\r\nDứt lời, Ngưu Đầu quay về nửa chết nửa sống Lang Vương một tấm hồn t&uacute;i, đem một c&aacute;i hư huyễn b&oacute;ng s&oacute;i thu h&uacute;t trong t&uacute;i. Lập tức, đầu tr&acirc;u mặt ngựa hai người b&oacute;ng người lay động, tại chỗ biến mất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<br />\r\nM&agrave; c&aacute;i kia nằm ở tr&ecirc;n đống cỏ kh&ocirc; ng&acirc;n b&agrave;o &ocirc;ng l&atilde;o, ở mất đi y&ecirc;u hồn sau, chậm r&atilde;i h&oacute;a l&agrave;m một bộ mấy trượng lớn nhỏ th&acirc;y s&oacute;i.<br />\r\n<br />\r\nM&agrave; ở th&acirc;y s&oacute;i b&ecirc;n cạnh, chỉ rớt xuống một c&aacute;i &aacute;nh bạc ch&oacute;i mắt nhẫn.<br />\r\n<br />\r\n&quot;Quả nhi&ecirc;n a, Y&ecirc;u vương l&agrave; c&oacute; thể h&oacute;a h&igrave;nh th&agrave;nh người, kiếp trước T&ocirc;n Ngộ Kh&ocirc;ng đến th&agrave;nh Phật đều vẫn l&agrave; hầu th&acirc;n, quả nhi&ecirc;n l&agrave; bị h&atilde;m hại sao? Trư B&aacute;t Giới vẫn lợn th&acirc;n, cũng l&agrave; bị c&aacute;c đại l&atilde;o h&atilde;m hại a? L&atilde;o T&ocirc;n n&agrave;y một đời, &iacute;t nhất phải tu luyện ra c&aacute; nh&acirc;n th&acirc;n, &iacute;t nhất phải giống như một Y&ecirc;u vương dạng!&quot;<br />\r\n<br />\r\nMắt thấy một đời Y&ecirc;u vương ng&atilde; xuống, T&ocirc;n Thiệu thuận miệng o&aacute;n giận một tiếng, sau đ&oacute; liền ở trong sơn động t&igrave;m kiếm bảo vật. Quay một v&ograve;ng, n&agrave;y lớn như vậy trong hang n&uacute;i, dĩ nhi&ecirc;n kh&ocirc;ng hề c&oacute; thứ g&igrave;. Nhặt l&ecirc;n tr&ecirc;n đất màu bạc nhẫn, T&ocirc;n Thiệu trong l&ograve;ng hơi động, &quot;Đ&acirc;y chẳng lẽ l&agrave; tu ch&acirc;n trong tiểu thuyết mi&ecirc;u tả nhẫn kh&ocirc;ng gian chứ?&quot;<br />\r\n<br />\r\nĐem nhẫn bạc mang ở tr&ecirc;n tay, một tia t&acirc;m thần tương li&ecirc;n cảm gi&aacute;c nhất thời truyền đến. T&ocirc;n Thiệu t&acirc;m thần thăm d&ograve; v&agrave;o trong nhẫn, nhẫn b&ecirc;n trong lại c&oacute; một chỗ mấy lập phương m&eacute;t lớn nhỏ kh&ocirc;ng gian.<br />\r\n<br />\r\nN&agrave;y màu bạc nhẫn, quả nhi&ecirc;n l&agrave; một nhẫn kh&ocirc;ng gian.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải chứ, đường đường Y&ecirc;u vương, cũng chỉ c&oacute; ngần ấy thu gom!&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu giơ l&ecirc;n mang theo nhẫn ng&oacute;n tay, quay về mặt đất chỉ tay, chỉ thấy tr&ecirc;n mặt đất, nhất thời xuất hiện một đống tạp vật. Mấy c&aacute;i b&igrave;nh nhỏ b&ecirc;n trong tựa hồ chứa một &iacute;t đan dược, bất qu&aacute; mặt tr&ecirc;n c&oacute; hay kh&ocirc;ng nh&atilde;n m&aacute;c, T&ocirc;n Thiệu cũng kh&ocirc;ng d&aacute;m ăn bậy. Ngoại trừ một &iacute;t kim ng&acirc;n tục vật, c&ograve;n c&oacute; một ch&uacute;t như thủy tinh s&aacute;ng long lanh tinh khối, linh kh&iacute; bức người, quen thuộc tu ch&acirc;n tiểu thuyết, T&ocirc;n Thiệu lường trước đ&acirc;y cũng l&agrave; trong truyền thuyết linh thạch,<br />\r\n<br />\r\nChỉ l&agrave; kh&ocirc;ng biết phẩm cấp l&agrave;m sao, c&oacute; đ&aacute;ng tiền hay kh&ocirc;ng.<br />\r\n<br />\r\nTrừ những thứ n&agrave;y ra tạp vật, c&ograve;n c&oacute; một bạch cốt chiến phủ, bất qu&aacute; n&agrave;y bạch cốt chiến phủ đ&acirc;u đ&acirc;u cũng c&oacute; tổn hại, hư kh&ocirc;ng thể xấu nữa, nghĩ đến n&agrave;y Khiếu Nguyệt Lang Vương gặp phải cường địch sau, ch&iacute;nh m&igrave;nh bị thương sống dở chết dở, vũ kh&iacute; cũng bị người l&agrave;m bể.<br />\r\n<br />\r\nĐem những n&agrave;y tạm thời v&ocirc; dụng tạp vật thu về trong nhẫn, tr&ecirc;n đất chỉ có để lại một quyển cổ điển quyển sách. Nhặt l&ecirc;n tr&ecirc;n đất quyển sách, T&ocirc;n Thiệu kh&oacute; c&oacute; thể che giấu trong mắt sắc mặt vui mừng, đem quyển sách mở ra.<br />\r\n<br />\r\nSau nửa canh giờ, T&ocirc;n Thiệu đem quyển sách triệt để xem xong, tr&ecirc;n mặt lại kh&ocirc;ng nửa điểm sắc mặt vui mừng, ngược lại c&oacute; chửi mẹ n&oacute; k&iacute;ch động, &quot;Mẹ kiếp, nguy&ecirc;n lai c&aacute;i gọi l&agrave; Y&ecirc;u vương, ở Ti&ecirc;n giới bất qu&aacute; l&agrave; l&oacute;t đ&aacute;y tồn tại!&quot;<br />\r\n<br />\r\nThi&ecirc;n Đ&igrave;nh vị tr&iacute;, ch&iacute;nh l&agrave; Ti&ecirc;n giới, m&agrave; Hoa Quả Sơn vị tr&iacute; Đ&ocirc;ng Thắng Thần Ch&acirc;u, đ&oacute; l&agrave; thuộc về ti&ecirc;n hạ Nh&acirc;n giới.<br />\r\n<br />\r\nM&agrave; ở ti&ecirc;n hạ Nh&acirc;n giới hạ tầng, ch&iacute;nh l&agrave; Ch&acirc;n Linh giới, m&agrave; ở Ch&acirc;n Linh giới hạ tầng, đ&oacute; l&agrave; thuộc về ph&agrave;m giới.</p>\r\n\r\n<p><br />\r\n<br />\r\nC&aacute;i gọi l&agrave; đặt xuống ph&agrave;m giới, liền đem phạm sai lầm Thi&ecirc;n Binh Thi&ecirc;n Tướng phế bỏ ph&aacute;p lực, phạt đến cuối c&ugrave;ng giới diện. Kiếp trước T&ocirc;n Ngộ Kh&ocirc;ng c&ugrave;ng Trư B&aacute;t Giới, liền bị đ&aacute;nh xuống ph&agrave;m giới.<br />\r\n<br />\r\nTu ch&acirc;n đẳng cấp, từ Luyện Kh&iacute; kỳ bắt đầu, theo thứ tự l&agrave; Tr&uacute;c Cơ kỳ, Kim đan kỳ, Nguy&ecirc;n Anh kỳ, H&oacute;a Thần kỳ, Luyện Hư kỳ, Hợp Đạo kỳ, Đại Thừa kỳ, Độ Kiếp kỳ, m&agrave; ở Độ Kiếp kỳ ph&iacute;a sau, mới c&oacute; thể bước v&agrave;o Ti&ecirc;n đạo. M&agrave; b&ecirc;n tr&ecirc;n, ch&iacute;nh l&agrave; ba Ti&ecirc;n chi cảnh, Nh&acirc;n ti&ecirc;n, Địa ti&ecirc;n, Thi&ecirc;n ti&ecirc;n, kh&ocirc;ng c&oacute; ph&acirc;n chia cao thấp, tu luyện phương hướng nhưng kh&ocirc;ng như thế. Thi&ecirc;n Binh Thi&ecirc;n Tướng đại thể c&ograve;n dừng lại ở tu ch&acirc;n ch&iacute;n cảnh, nhưng m&agrave; những Thi&ecirc;n Đ&igrave;nh kia c&aacute;c đại l&atilde;o, tuyệt đối đều l&agrave; ở ba Ti&ecirc;n chi cảnh trở l&ecirc;n!<br />\r\n<br />\r\nCăn cứ quyển trục n&agrave;y b&ecirc;n tr&ecirc;n ghi ch&eacute;p, Ngọc Ho&agrave;ng Đại Đế tu vi c&agrave;ng là ở ba Ti&ecirc;n chi cảnh b&ecirc;n tr&ecirc;n, vẻn vẹn hắn sức lực của một người, giơ tay c&oacute; thể diệt mười vạn Thi&ecirc;n Binh!<br />\r\n<br />\r\nC&aacute;i n&agrave;y năm, nhất thời đem T&ocirc;n Thiệu trong l&ograve;ng nhu nhược v&ocirc; năng Ngọc Đế h&igrave;nh tượng lật đổ. Mặc d&ugrave; l&agrave; kiếp trước T&ocirc;n Ngộ Kh&ocirc;ng, cũng chỉ c&oacute; thể dựa v&agrave;o kim cương bất hoại th&acirc;n thể ở mười vạn Thi&ecirc;n Binh dưới sự v&acirc;y c&ocirc;ng tự vệ, m&agrave; Ngọc Đế phất tay liền c&oacute; thể giết chết mười vạn Thi&ecirc;n Binh, n&agrave;y l&agrave; nh&acirc;n vật cấp bậc n&agrave;o!<br />\r\n<br />\r\nLoại n&agrave;y đại l&atilde;o, tuyệt đối kh&ocirc;ng thể cho T&ocirc;n Ngộ Kh&ocirc;ng đại n&aacute;o Thi&ecirc;n Cung cơ hội! M&agrave; căn cứ quyển sách b&ecirc;n tr&ecirc;n Khiếu Nguyệt Lang Vương mi&ecirc;u tả, dường như Na Tra, Nhị Lang Thần loại cấp bậc n&agrave;y Thi&ecirc;n Tướng, nhiều nhất cũng l&agrave; Hợp Đạo kỳ m&agrave; th&ocirc;i. T&ocirc;n Thiệu trong l&ograve;ng cảm gi&aacute;c nặng nề, T&ocirc;n Ngộ Kh&ocirc;ng c&oacute; thể th&agrave;nh c&ocirc;ng đại n&aacute;o Thi&ecirc;n Cung, quả nhi&ecirc;n c&oacute; nội t&igrave;nh a.<br />\r\n<br />\r\nN&agrave;y đ&aacute;m đại l&atilde;o đem T&ocirc;n Ngộ Kh&ocirc;ng chi&ecirc;u l&ecirc;n Thi&ecirc;n Cung, khắp nơi ra vẻ đ&aacute;ng thương, để T&ocirc;n Ngộ Kh&ocirc;ng ngạo mạn tự đại sau đ&oacute; phạm sai lầm, quả nhi&ecirc;n l&agrave; có &yacute; đồ ri&ecirc;ng.<br />\r\n<br />\r\nQuyển sách b&ecirc;n tr&ecirc;n ghi lại sự thực, lật đổ T&ocirc;n Thiệu đối với T&acirc;y Du nhất qu&aacute;n hiểu r&otilde;, nhưng đem T&acirc;y Du b&ecirc;n trong kh&ocirc;ng &iacute;t kh&ocirc;ng hợp l&yacute; địa phương, nh&aacute;y mắt chỉ r&otilde;.<br />\r\n<br />\r\nV&igrave; sao Ti&ecirc;n Phật t&ugrave;y tiện một c&aacute;i coi cửa linh th&uacute;, luyện đan đồng tử, đến rồi hạ giới, liền c&oacute; thể t&ugrave;y tiện ngược T&ocirc;n Ngộ Kh&ocirc;ng. C&aacute;c đại l&atilde;o ra vẻ đ&aacute;ng thương, nhưng người ta đệ tử kh&ocirc;ng giả bộ a, thực lực ch&acirc;n thật vừa hiện ra lộ, cuối c&ugrave;ng c&ograve;n phải Ti&ecirc;n Phật cứu tr&agrave;ng.<br />\r\n<br />\r\nNghĩ tới đ&acirc;y, T&ocirc;n Thiệu ngo&agrave;i miệng hiện l&ecirc;n một tia nụ cười t&agrave; &aacute;c, &quot;May l&agrave; l&atilde;o T&ocirc;n đ&uacute;ng l&uacute;c ph&aacute;t hiện, nếu kh&ocirc;ng kh&ocirc;ng cẩn thận, lại sẽ bị Ngọc Đế ra vẻ đ&aacute;ng thương h&atilde;m hại. N&agrave;y một đời, l&atilde;o T&ocirc;n muốn đề cao tu vi, tới một lần ch&acirc;n ch&iacute;nh đại n&aacute;o Thi&ecirc;n Cung, để Tề Thi&ecirc;n Đại Th&aacute;nh thật đến danh quy!&quot;<br />\r\n<br />\r\nQuyển sách b&ecirc;n tr&ecirc;n, ngoại trừ Khiếu Nguyệt Lang Vương ở Nh&acirc;n giới r&acirc;́t nhi&ecirc;̀u hiểu biết, c&ograve;n ghi lại hắn th&acirc;n l&agrave; Y&ecirc;u vương tu luyện ph&aacute;p m&ocirc;n.<br />\r\n<br />\r\nY&ecirc;u tộc th&agrave;nh ti&ecirc;n trước đ&acirc;y , tương tự chia l&agrave;m ch&iacute;n cảnh. Từ đệ nhất cấp y&ecirc;u th&uacute; bắt đầu, chia ra l&agrave;m tinh qu&aacute;i, y&ecirc;u, Y&ecirc;u vương, Y&ecirc;u qu&acirc;n, Địa Y&ecirc;u Thi&ecirc;n y&ecirc;u cảnh, Y&ecirc;u T&ocirc;n, Y&ecirc;u Th&aacute;nh, Y&ecirc;u Đế. M&agrave; ở Y&ecirc;u Đế ph&iacute;a sau, mới c&oacute; thể cửu chuyển th&agrave;nh ti&ecirc;n.<br />\r\n<br />\r\nC&aacute;i gọi l&agrave; Y&ecirc;u vương, ở đại l&atilde;o trong mắt, chỉ l&agrave; tiện tay c&oacute; thể b&oacute;p chết tồn tại. M&agrave; n&agrave;y Khiếu Nguyệt Lang Vương, liền hai c&aacute;i Quỷ sai đều kh&ocirc;ng đối ph&oacute; được.<br />\r\n<br />\r\nĐ&oacute; cũng kh&ocirc;ng phải bởi v&igrave; Y&ecirc;u vương qu&aacute; yếu, m&agrave; thật sự l&agrave; Ti&ecirc;n Nh&acirc;n qu&aacute; mạnh mẽ!<br />\r\n<br />\r\nY&ecirc;u tộc tu h&agrave;nh h&agrave;m nghĩa, ở chỗ nuốt chửng đồ ăn. D&atilde; th&uacute; b&igrave;nh thường nuốt chửng số lượng nhất định huyết nhục ph&iacute;a sau, liền c&oacute; thể tấn cấp đến y&ecirc;u th&uacute; cấp bậc, chỉ c&oacute; khi tu luyện tới Y&ecirc;u vương cảnh giới sau, mới c&oacute; thể th&ocirc;ng qua hệ thống c&ocirc;ng ph&aacute;p tăng l&ecirc;n đẳng cấp. M&agrave; thịt người so với d&atilde; th&uacute; thịt, ẩn chứa linh t&iacute;nh c&agrave;ng cao hơn, c&agrave;ng dễ d&agrave;ng tăng l&ecirc;n Y&ecirc;u tộc đẳng cấp. Nh&acirc;n tộc tu vi c&agrave;ng cao, huyết nhục năng lượng c&agrave;ng nhiều. M&agrave; Ti&ecirc;n Phật huyết nhục, năng lượng ẩn chứa c&agrave;ng l&agrave; thịt người ng&agrave;n tỉ lần.</p>\r\n\r\n<p><br />\r\n<br />\r\nNh&igrave;n đến đ&acirc;y, T&ocirc;n Thiệu bỗng nhi&ecirc;n tỉnh ngộ, chẳng tr&aacute;ch y&ecirc;u qu&aacute;i đều muốn ăn thịt người, chẳng tr&aacute;ch T&acirc;y Du K&yacute;, b&ecirc;n trong nhiều như vậy y&ecirc;u qu&aacute;i muốn ăn Đường Tăng thịt, nguy&ecirc;n lai nh&igrave;n tr&uacute;ng ch&iacute;nh l&agrave; Kim Thiền Tử Ti&ecirc;n Phật huyết mạch. C&aacute;i g&igrave; trường sinh bất l&atilde;o, đều l&agrave; gạt người.<br />\r\n<br />\r\nĐ&aacute;ng tiếc l&agrave;, Khiếu Nguyệt Lang Vương đồng dạng kh&ocirc;ng c&oacute; Y&ecirc;u vương ph&iacute;a sau tu luyện ph&aacute;p m&ocirc;n, c&aacute;i n&agrave;y cũng l&agrave; hắn đứng ở Y&ecirc;u vương cấp bậc kh&ocirc;ng thể l&ecirc;n cấp tiếp theo cấp nguy&ecirc;n nh&acirc;n.<br />\r\n<br />\r\nMặc d&ugrave; kh&ocirc;ng c&oacute; tu luyện ph&aacute;p m&ocirc;n, nhưng &iacute;t ra thu được l&ecirc;n cấp Y&ecirc;u vương ph&iacute;a trước phương ph&aacute;p tu luyện, T&ocirc;n Thiệu vẫn l&agrave; đại vi m&atilde;n &yacute;. Nh&igrave;n tr&ecirc;n mặt đất h&atilde;y c&ograve;n ấm &aacute;p th&acirc;y s&oacute;i, T&ocirc;n Thiệu m&iacute;m m&iacute;m hầu miệng, cười hắc hắc n&oacute;i, &quot;Trực tiếp nuốt chửng Y&ecirc;u vương huyết nhục, kh&ocirc;ng biết c&oacute; thể tăng l&ecirc;n tới cấp bậc g&igrave;!&quot;<br />\r\n<br />\r\nCả một đ&ecirc;m, b&ecirc;n trong hang n&uacute;i, thỉnh thoảng truyền ra nướng thịt s&oacute;i vị. M&agrave; ở một đ&ecirc;m ph&iacute;a sau, sơn động ở một trận to lớn s&oacute;ng ph&aacute;p lực hạ, ầm ầm sụp xuống.<br />\r\n<br />\r\nỞ đầy trời trong bụi m&ugrave;, một c&aacute;i nửa người nửa hầu, th&acirc;n mang Bạch Hổ da &aacute;o l&ocirc;ng qu&aacute;i nh&acirc;n, một c&aacute;i cầm trong tay c&ograve;n dư lại nửa đoạn nướng s&oacute;i ch&acirc;n n&agrave;y v&agrave;o trong miệng, cười ha ha n&oacute;i, &quot;Ăn một con Y&ecirc;u vương th&acirc;n thể, trực tiếp để l&atilde;o T&ocirc;n tăng l&ecirc;n tới thứ ba cấp, b&acirc;y giờ l&atilde;o T&ocirc;n, cuối c&ugrave;ng cũng coi như nhưng là xưng l&agrave; một c&aacute;i y&ecirc;u!&quot;<br />\r\n<br />\r\nGiờ khắc này T&ocirc;n Thiệu nửa người nửa hầu d&aacute;ng dấp, c&ugrave;ng kịch truyền h&igrave;nh b&ecirc;n trong T&ocirc;n Ngộ Kh&ocirc;ng h&igrave;nh tượng, kh&ocirc;ng kh&aacute;c nhau ch&uacute;t n&agrave;o! Nguy&ecirc;n t&aacute;c trong T&ocirc;n Ngộ Kh&ocirc;ng, r&otilde; r&agrave;ng kh&ocirc;ng hề tăng l&ecirc;n đến Y&ecirc;u vương cấp bậc, bằng kh&ocirc;ng định c&oacute; thể như Khiếu Nguyệt Lang Vương giống như vậy, h&oacute;a th&agrave;nh h&igrave;nh người!<br />\r\n<br />\r\nCho tới v&igrave; sao T&ocirc;n Ngộ Kh&ocirc;ng kh&ocirc;ng c&aacute;ch n&agrave;o thăng cấp th&agrave;nh Y&ecirc;u vương, đ&acirc;y cũng kh&ocirc;ng phải l&agrave; T&ocirc;n Thiệu cũng biết.<br />\r\n<br />\r\nSử dụng một c&aacute;i Hạn Địa Bạt Th&ocirc;ng, T&ocirc;n Thiệu một bước nhảy l&ecirc;n, dưới ch&acirc;n m&acirc;y m&ugrave; lượn quanh, dĩ nhi&ecirc;n bay l&ecirc;n.<br />\r\n<br />\r\nTừ một c&aacute;i Thạch Hầu, một bước th&agrave;nh y&ecirc;u, T&ocirc;n Thiệu hai tay phảng phất c&oacute; m&acirc;́y vạn c&acirc;n kh&iacute; lực, trong đan điền, c&agrave;ng l&agrave; c&oacute; th&ecirc;m cuồn cuộn ph&aacute;p lực, nhưng khổ nỗi kh&ocirc;ng biết ph&aacute;p thuật, m&agrave; kh&ocirc;ng biết l&agrave;m sao điều động.<br />\r\n<br />\r\nTrong l&ograve;ng hơi động, T&ocirc;n Thiệu sử dụng tới Hỏa Nh&atilde;n Kim T&igrave;nh, hai mắt nổi l&ecirc;n kim quang, c&oacute; thể nh&igrave;n thấy b&ecirc;n ngo&agrave;i trăm dặm cảnh sắc. Nh&igrave;n lại trong tay quyển sách, tăng l&ecirc;n v&ocirc; số lần thị lực, nh&igrave;n quyển sách b&ecirc;n tr&ecirc;n một đo&agrave;n điểm đen, giật nảy cả m&igrave;nh.<br />\r\n<br />\r\nN&agrave;y điểm đen, c&agrave;ng l&agrave; từ so với con kiến c&ograve;n nhỏ m&acirc;́y trăm c&aacute;i chữ nhỏ ngưng kết th&agrave;nh. Kh&ocirc;ng c&oacute; Hỏa Nh&atilde;n Kim T&igrave;nh cấp bậc thị lực, tuyệt kh&ocirc;ng chỉ c&aacute;i n&agrave;y điểm đen viết l&agrave; c&aacute;i g&igrave;!<br />\r\n<br />\r\nKhởi đ&ocirc;̣ng Hỏa Nh&atilde;n Kim T&igrave;nh, T&ocirc;n Thiệu đem điểm đen ghi lại văn tự lẩm bẩm đọc l&ecirc;n,<br />\r\n<br />\r\n&quot;Thi&ecirc;n Cương ba mươi s&aacute;u biến, thứ ba mươi biến, Hồi phong phản hỏa. . .&quot;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:651px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', '36-bien', 8, '377', '2022-08-03 05:52:48', '2022-08-03 05:52:48'),
(42, '4', 'Cương Phong Chi Nhãn, Mai Sơn thất quái', '<p>Ba mươi s&aacute;u biến, Thi&ecirc;n Binh Thi&ecirc;n Tướng đều biết một ch&uacute;t, nh&igrave;n ghi lại ph&eacute;p thuật cũng chỉ l&agrave; ba mươi s&aacute;u biến, T&ocirc;n Thiệu chợt cảm thấy mất hứng. Ba mươi s&aacute;u biến, đ&oacute; l&agrave; Trư B&aacute;t Giới tuyệt học kh&ocirc;ng phải sao. M&igrave;nh n&oacute;i như thế n&agrave;o cũng muốn bắt chước c&aacute;i bảy mươi hai biến chứ?<br />\r\n<br />\r\nBất qu&aacute; tiếp tục tiếp tục đọc, T&ocirc;n Thiệu bị chữ mực cuối c&ugrave;ng một đoạn văn s&acirc;u sắc hấp dẫn. C&acirc;u n&oacute;i n&agrave;y kh&ocirc;ng phải &quot;Hồi Phong Phản Hỏa&quot; tu luyện ph&aacute;p m&ocirc;n, m&agrave; l&agrave; Khiếu Nguyệt Lang Vương một đoạn t&acirc;m đắc lĩnh hội,<br />\r\n<br />\r\n&quot;Bản Vương khổ tu trăm năm, tr&ecirc;n bị cương phong ch&eacute;m th&acirc;n thể nỗi đau, hạ bị Địa hỏa đốt người chi chước, thuật n&agrave;y mới mới c&oacute; thể tiểu th&agrave;nh, bằng thuật n&agrave;y ngang dọc Đ&ocirc;ng Thắng Thần Ch&acirc;u nam vực hai trăm y&ecirc;u động, kh&ocirc;ng người n&agrave;o c&oacute; thể địch. Chỉ tiếc phong hỏa trước sau kh&ocirc;ng c&aacute;ch n&agrave;o dung hợp l&agrave;m một, đại th&agrave;nh ng&agrave;y, kh&ocirc;ng biết c&ograve;n cần mấy trăm năm. Đạo Tổ s&aacute;ng chế thuật, coi l&agrave; thật v&ocirc; c&ugrave;ng ảo diệu.&quot;</p>', '<p>Ba mươi s&aacute;u biến, Thi&ecirc;n Binh Thi&ecirc;n Tướng đều biết một ch&uacute;t, nh&igrave;n ghi lại ph&eacute;p thuật cũng chỉ l&agrave; ba mươi s&aacute;u biến, T&ocirc;n Thiệu chợt cảm thấy mất hứng. Ba mươi s&aacute;u biến, đ&oacute; l&agrave; Trư B&aacute;t Giới tuyệt học kh&ocirc;ng phải sao. M&igrave;nh n&oacute;i như thế n&agrave;o cũng muốn bắt chước c&aacute;i bảy mươi hai biến chứ?<br />\r\n<br />\r\nBất qu&aacute; tiếp tục tiếp tục đọc, T&ocirc;n Thiệu bị chữ mực cuối c&ugrave;ng một đoạn văn s&acirc;u sắc hấp dẫn. C&acirc;u n&oacute;i n&agrave;y kh&ocirc;ng phải &quot;Hồi Phong Phản Hỏa&quot; tu luyện ph&aacute;p m&ocirc;n, m&agrave; l&agrave; Khiếu Nguyệt Lang Vương một đoạn t&acirc;m đắc lĩnh hội,<br />\r\n<br />\r\n&quot;Bản Vương khổ tu trăm năm, tr&ecirc;n bị cương phong ch&eacute;m th&acirc;n thể nỗi đau, hạ bị Địa hỏa đốt người chi chước, thuật n&agrave;y mới mới c&oacute; thể tiểu th&agrave;nh, bằng thuật n&agrave;y ngang dọc Đ&ocirc;ng Thắng Thần Ch&acirc;u nam vực hai trăm y&ecirc;u động, kh&ocirc;ng người n&agrave;o c&oacute; thể địch. Chỉ tiếc phong hỏa trước sau kh&ocirc;ng c&aacute;ch n&agrave;o dung hợp l&agrave;m một, đại th&agrave;nh ng&agrave;y, kh&ocirc;ng biết c&ograve;n cần mấy trăm năm. Đạo Tổ s&aacute;ng chế thuật, coi l&agrave; thật v&ocirc; c&ugrave;ng ảo diệu.&quot;<br />\r\n<br />\r\nKhiếu Nguyệt Lang Vương đoạn n&agrave;y tự thuật, để T&ocirc;n Thiệu đối với n&agrave;y &quot;Hồi Phong Phản Hỏa&quot; th&aacute;i độ, trực tiếp ph&aacute;t sinh 180&deg; chuyển biến lớn.<br />\r\n<br />\r\nKhiếu Nguyệt Lang Vương dựa v&agrave;o n&agrave;y một c&aacute;i ph&eacute;p thuật, liền c&oacute; thể ngang dọc Đ&ocirc;ng Thắng Thần Ch&acirc;u nam vực hai trăm y&ecirc;u động. C&oacute; thể thấy được ph&aacute;p thuật n&agrave;y cũng kh&ocirc;ng phải T&acirc;y Du K&yacute;, ghi lại như vậy kh&ocirc;ng thể tả. Hơn nữa nghe Khiếu Nguyệt Lang Vương ng&ocirc;n ngữ, n&agrave;y Hồi Phong Phản Hỏa ph&eacute;p thuật, l&agrave; Đạo Tổ s&aacute;ng chế.<br />\r\n<br />\r\nĐạo Tổ l&agrave; ai, đ&acirc;y ch&iacute;nh l&agrave; Th&aacute;i Thượng L&atilde;o Qu&acirc;n! Đạo M&ocirc;n Nh&acirc;n gi&aacute;o thuỷ tổ! Hắn B&aacute;t Qu&aacute;i L&ocirc; ng&atilde;, l&agrave; c&oacute; thể l&agrave;m ra Hỏa Diễm Sơn. Hắn tiện tay luyện chế một c&aacute;i Tử Kim Linh, l&agrave; c&oacute; thể để T&ocirc;n Ngộ Kh&ocirc;ng hết sức nhức đầu. Hắn một c&aacute;i tr&ocirc;ng l&ograve; đồng tử, một c&aacute;i vật cưỡi Thanh Ngưu, liền c&oacute; thể dễ d&agrave;ng ch&agrave; đạp nguy&ecirc;n t&aacute;c b&ecirc;n trong T&ocirc;n Ngộ Kh&ocirc;ng.<br />\r\n<br />\r\nTh&aacute;i Thượng L&atilde;o Qu&acirc;n s&aacute;ng lập ra ph&eacute;p thuật, uy lực kia c&oacute; thể thấp sao?<br />\r\n<br />\r\nGiờ khắc này T&ocirc;n Thiệu mới hiểu được, nguy&ecirc;n lai ba mươi s&aacute;u biến c&ugrave;ng bảy mươi hai thay đổi mỗi một biến, kh&ocirc;ng phải học xong liền xong việc, c&ograve;n cần kh&ocirc;ng ngừng tu luyện, đạt đến sự biến đổi n&agrave;y tầng thứ c&agrave;ng cao hơn sau, uy lực cực k&igrave; khủng bố. Vẻn vẹn Hồi Phong Phản Hỏa sự biến đổi n&agrave;y, Lang Vương liền tu luyện trăm năm mới tiểu th&agrave;nh, kiếp trước T&ocirc;n Ngộ Kh&ocirc;ng bỏ ra 10 năm c&ocirc;ng phu, chỉ l&agrave; đem bảy mươi hai biến học xong, nếu n&oacute;i l&agrave; tinh th&ocirc;ng, đ&oacute; l&agrave; một c&aacute;i cũng kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nNắm giữ ph&aacute;p thuật n&agrave;y vận c&ocirc;ng ph&aacute;p m&ocirc;n c&ugrave;ng với h&agrave;nh c&ocirc;ng lộ tuyến, T&ocirc;n Thiệu một tay phất l&ecirc;n, đem quyển sách thu v&agrave;o nhẫn bạc, nhấc l&ecirc;n m&acirc;y m&ugrave;, mấy c&aacute;i nh&aacute;y mắt, liền bay trở về Thủy Li&ecirc;m Động.<br />\r\n<br />\r\nL&uacute;c n&agrave;y b&oacute;ng đ&ecirc;m đ&atilde; hết, kh&ocirc;ng &iacute;t uống say m&egrave;m vi&ecirc;n hầu cũng hỗn loạn tỉnh lại, nh&igrave;n tiến nhập Thủy Li&ecirc;m Động T&ocirc;n Thiệu, dồn dập cầm lấy c&agrave;nh c&acirc;y mộc c&ocirc;n, đem T&ocirc;n Thiệu bao bọc v&acirc;y quanh,<br />\r\n<br />\r\n&quot;Người tới người phương n&agrave;o, d&aacute;m x&ocirc;ng v&agrave;o ch&uacute;ng ta Đại vương tẩm cung!&quot;<br />\r\n<br />\r\n&quot;Được rồi được rồi, canh g&aacute;c &yacute; thức kh&ocirc;ng sai, bất qu&aacute; &aacute;nh mắt c&ograve;n k&eacute;m ch&uacute;t, c&aacute;c ngươi kh&ocirc;ng thấy được, l&atilde;o T&ocirc;n ch&iacute;nh l&agrave; của c&aacute;c ngươi Đại vương sao!&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu tiện tay vung l&ecirc;n, v&acirc;̣n l&ecirc;n Hồi Phong Phản Hỏa ph&aacute;p m&ocirc;n, ph&aacute;p lực g&acirc;y n&ecirc;n một trận cuồng phong, đem đ&aacute;m khỉ vượn binh kh&iacute; trong tay cuốn đi, ở ch&uacute;ng vi&ecirc;n hầu &aacute;nh mắt kh&oacute; m&agrave; tin nổi b&ecirc;n trong, đem một đ&ecirc;m trải qua qua loa n&oacute;i một lần.<br />\r\n<br />\r\nĐương nhi&ecirc;n, Hỏa Nh&atilde;n Kim T&igrave;nh c&ugrave;ng ba mươi s&aacute;u biến những b&iacute; mật n&agrave;y, T&ocirc;n Thiệu l&agrave; sẽ kh&ocirc;ng n&oacute;i cho đ&aacute;m khỉ vượn.<br />\r\n<br />\r\nT&ocirc;n Thiệu trong một đ&ecirc;m, từ nhất giới Thạch Hầu tấn cấp th&agrave;nh y&ecirc;u, trong l&uacute;c n&agrave;y qu&aacute; tr&igrave;nh cũng kh&ocirc;ng c&oacute; tr&aacute;nh được Th&aacute;i Thượng L&atilde;o Qu&acirc;n &aacute;nh mắt. Bất qu&aacute; nh&igrave;n thấy T&ocirc;n Thiệu giơ tay đ&atilde; c&oacute; thể sử dụng tới Hồi Phong Phản Hỏa, Th&aacute;i Thượng L&atilde;o Qu&acirc;n biến th&agrave;nh Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu, sắc mặt nhất thời cổ qu&aacute;i.<br />\r\n<br />\r\nHồi Phong Phản Hỏa c&oacute; thể l&agrave; của m&igrave;nh bản lĩnh sở trường, kh&ocirc;ng chỉ c&oacute; l&agrave; luyện đan luyện kh&iacute;, hay l&agrave; đối với địch, đều l&agrave; một đại tuyệt học. Phối hợp với &quot;Tử H&ocirc;i Thanh Y&ecirc;n&quot; c&ugrave;ng &quot;Tam Muội Ch&acirc;n Hỏa&quot;, Th&aacute;i Thượng L&atilde;o Qu&acirc;n một tay Hồi Phong Phản Hỏa, Thi&ecirc;n Đ&igrave;nh người kh&ocirc;ng d&ugrave;ng tới ph&aacute;p bảo, c&oacute; thể đỡ lấy c&oacute; thể kh&ocirc;ng c&oacute; mấy c&aacute;i.<br />\r\n<br />\r\nT&ocirc;n Thiệu chi&ecirc;u thức ấy Hồi Phong Phản Hỏa, tuy rằng uy lực qu&aacute; nhỏ, liền tầm thường hổ b&aacute;o đều đ&aacute;nh kh&ocirc;ng chết, bất qu&aacute; trong một đ&ecirc;m liền c&oacute; thể học sẽ tự m&igrave;nh tr&ocirc;ng cửa ph&eacute;p thuật, vẫn l&agrave; để Th&aacute;i Thượng L&atilde;o Qu&acirc;n rất l&agrave; kinh ngạc. Chỉ l&agrave; kh&ocirc;ng biết, n&agrave;y đầu khỉ l&agrave; học xong liền xong việc, vẫn l&agrave; nhẫn nại đau khổ, đem thuật n&agrave;y tu luyện tới cảnh giới cao hơn.<br />\r\n<br />\r\nGiờ khắc này Th&aacute;i Thượng L&atilde;o Qu&acirc;n trong l&ograve;ng, c&agrave;ng mơ hồ hi vọng T&ocirc;n Thiệu lựa chọn tiếp tục tu luyện, đem ch&iacute;nh m&igrave;nh c&aacute;i m&ocirc;n n&agrave;y b&iacute; thuật ph&aacute;t dương quang đại.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><br />\r\n<br />\r\nM&agrave; T&ocirc;n Thiệu lời kế tiếp, quả nhi&ecirc;n kh&ocirc;ng c&oacute; để Th&aacute;i Thượng L&atilde;o Qu&acirc;n thất vọng, &quot;Được rồi, bản Vương quyết định ra ngo&agrave;i tu luyện ph&eacute;p thuật, Hoa Quả Sơn sự t&igrave;nh, liền giao cho T&ocirc;n Đại to&agrave;n quyền phụ tr&aacute;ch. . . C&aacute;c ngươi n&agrave;y bang thằng nh&oacute;c con, cần phải nghe T&ocirc;n Đại!&quot;<br />\r\n<br />\r\nN&oacute;i tới chỗ n&agrave;y, T&ocirc;n Thiệu ngừng n&oacute;i, c&oacute; th&acirc;m &yacute; kh&aacute;c địa đối với Th&ocirc;ng T&iacute; l&atilde;o hầu nở nụ cười,<br />\r\n<br />\r\n&quot;Bản Vương tu luyện ph&eacute;p thuật, cần phải t&igrave;m gi&oacute; lớn địa phương, kh&ocirc;ng biết l&atilde;o ti&ecirc;n sinh c&oacute; biết hay kh&ocirc;ng nơi n&agrave;o gi&oacute; to lớn nhất?&quot;<br />\r\n<br />\r\nBị T&ocirc;n Thiệu n&agrave;y vừa nh&igrave;n, Th&ocirc;ng T&iacute; l&atilde;o hầu hơi sững sờ, lập tức lộ ra nụ cười thỏa m&atilde;n, &quot;Kh&ocirc;ng biết Đại vương muốn muốn gi&oacute; lớn như n&agrave;o?&quot;<br />\r\n<br />\r\n&quot;C&agrave;ng lớn c&agrave;ng tốt, c&oacute; thể đem l&atilde;o T&ocirc;n thổi biến th&agrave;nh tro bụi, c&agrave;ng tốt hơn!&quot;<br />\r\n<br />\r\nN&oacute;i ra c&acirc;u n&oacute;i n&agrave;y, T&ocirc;n Thiệu tr&ecirc;n người c&agrave;ng c&oacute; mấy phần th&ocirc; bạo lộ ra ngo&agrave;i cảm gi&aacute;c, sự th&ocirc; bạo n&agrave;y, r&otilde; r&agrave;ng bắt nguồn từ c&ugrave;ng T&ocirc;n Thiệu dung hợp l&agrave;m một T&ocirc;n Ngộ Kh&ocirc;ng linh hồn,<br />\r\n<br />\r\n&quot;C&agrave;ng lớn c&agrave;ng tốt a? Ha ha, vừa vặn, Đại vương ph&aacute; thạch ra ng&agrave;y ấy, tử kh&iacute; x&ocirc;ng thẳng đấu ngưu, ở Hoa Quả Sơn phương bắc ngo&agrave;i ng&agrave;n dặm tr&ecirc;n trời, tạo th&agrave;nh một c&aacute;i Cương Phong Chi Nh&atilde;n. Ha ha, c&aacute;i kia Cương Phong Chi Nh&atilde;n c&oacute; thể ng&agrave;n năm mới gặp bảo địa, Đại vương c&oacute; thể gặp gỡ, cũng coi như ph&uacute;c duy&ecirc;n kh&ocirc;ng cạn. . . Bất qu&aacute; c&aacute;i kia Cương Phong Chi Nh&atilde;n ở v&agrave;o bầu trời 90 ng&agrave;n trượng b&ecirc;n tr&ecirc;n, như l&agrave; Đại vương bị cương phong thổi rơi xuống, thể x&aacute;c ph&agrave;m tục, nhưng l&agrave; sẽ ng&atilde; th&agrave;nh thịt n&aacute;t.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sao, tr&ecirc;n đời kh&ocirc;ng c&oacute; kh&ocirc;ng l&agrave;m m&agrave; hưởng sự t&igrave;nh, kh&ocirc;ng mạo ch&uacute;t hiểm, h&aacute; c&oacute; thể đem ph&eacute;p thuật luyện tốt! L&atilde;o T&ocirc;n mang ch&uacute;t tr&aacute;i c&acirc;y, n&agrave;y liền đi!&quot;<br />\r\n<br />\r\nDứt lời, T&ocirc;n Thiệu mệnh lệnh một đ&aacute;m khỉ nhỏ mang tới v&ocirc; số sơn tr&acirc;n quả dại, to&agrave;n bộ đựng v&agrave;o nhẫn bạc b&ecirc;n trong. Những n&agrave;y hoa quả, tiết kiệm một &iacute;t, đủ T&ocirc;n Thiệu ăn chừng mười năm, đặt ở nhẫn bạc b&ecirc;n trong, cũng kh&ocirc;ng sợ xấu.<br />\r\n<br />\r\nChuẩn bị xong xu&ocirc;i, T&ocirc;n Thiệu nhấc l&ecirc;n m&acirc;y m&ugrave;, hướng về Hoa Quả Sơn bắc mặt bay đi. T&ocirc;n Thiệu cũng kh&ocirc;ng biết, giờ khắc này Th&aacute;i Thượng L&atilde;o Qu&acirc;n trong l&ograve;ng, c&agrave;ng d&acirc;ng l&ecirc;n giữ g&igrave;n T&ocirc;n Thiệu t&acirc;m tư,<br />\r\n<br />\r\n&quot;Ha ha, t&ecirc;n tiểu tử n&agrave;y, đ&uacute;ng l&agrave; c&oacute; gan ph&aacute;ch. Cũng được, như hắn c&oacute; thể sống s&oacute;t trở về, l&atilde;o phu liền Ti&ecirc;n giới, từ chối Ngọc Ho&agrave;ng Đại Thi&ecirc;n T&ocirc;n y&ecirc;u cầu, kh&ocirc;ng l&agrave;m n&agrave;y t&ecirc;n &aacute;c nh&acirc;n. . .&quot;<br />\r\n<br />\r\n. . . . .<br />\r\n<br />\r\nCưỡi m&acirc;y sương m&ugrave; một đường đi về ph&iacute;a bắc, T&ocirc;n Thiệu r&otilde; r&agrave;ng cảm gi&aacute;c được, c&agrave;ng đi đi về ph&iacute;a bắc, tr&ecirc;n mặt biển gi&oacute; biển liền c&agrave;ng lớn, chờ gần như đến rồi Hoa Quả Sơn bắc mặt ngàn dặm chỗ, vẻn vẹn vị ở tr&ecirc;n mặt biển, T&ocirc;n Thiệu liền đ&atilde; đứng thẳng bất ổn.<br />\r\n<br />\r\nMiễn cưỡng bay đến tr&ecirc;n bầu trời ba ngàn trượng, lạnh thấu xương Thi&ecirc;n Phong, đem T&ocirc;n Thiệu l&ocirc;ng khỉ d&agrave;y đặc g&ograve; m&aacute; thổi đến đau đớn. Kh&oacute; c&oacute; thể tưởng tượng, như l&agrave; xa hơn cao hơn địa phương bay, so với Thi&ecirc;n Phong c&agrave;ng lạnh thấu xương cương phong, lại n&ecirc;n l&agrave; uy lực g&igrave;? Chỉ sợ trực tiếp sẽ đem thể x&aacute;c ph&agrave;m tục T&ocirc;n Thiệu, cắt th&agrave;nh từng đoạn đi.<br />\r\n<br />\r\n&Aacute;nh mắt qu&eacute;t qua, ba ng&agrave;n trượng vị tr&iacute;, c&agrave;ng đồng dạng c&oacute; mấy trăm con y&ecirc;u đang ở nhẫn nại Thi&ecirc;n Phong thổi tới, hấp thu sức gi&oacute;. Những n&agrave;y y&ecirc;u đại thể đều l&agrave; lo&agrave;i chim bộ d&aacute;ng y&ecirc;u qu&aacute;i, hấp thu sức gi&oacute;, liền c&oacute; thể tăng cao ph&aacute;p lực, tăng cường tốc độ phi h&agrave;nh.<br />\r\n<br />\r\nTương tự T&ocirc;n Thiệu như vậy hầu y&ecirc;u, chỉ c&aacute;i n&agrave;y một c&aacute;i. Một đ&aacute;m lo&agrave;i chim y&ecirc;u qu&aacute;i đối xử T&ocirc;n Thiệu &aacute;nh mắt, giống như l&agrave; ở như nh&igrave;n qu&aacute;i vật, bọn họ kh&ocirc;ng nghĩ ra, một con khỉ, hấp thu thi&ecirc;n phong lực lượng c&oacute; thể c&oacute; t&aacute;c dụng g&igrave;.<br />\r\n<br />\r\n&quot;Nh&igrave;n tới nơi n&agrave;y c&oacute; Cương Phong Chi Nh&atilde;n tin tức, cũng kh&ocirc;ng phải l&agrave; chỉ c&oacute; ta một người biết a. Bất kể, trước ti&ecirc;n hấp thu một &iacute;t sức gi&oacute;, chờ trong cơ thể ta sức gi&oacute; nhiều hơn ch&uacute;t, th&igrave; c&oacute; thể đến cao hơn tr&ecirc;n bầu trời.&quot;</p>\r\n\r\n<p><br />\r\n<br />\r\nKh&ocirc;ng nh&igrave;n lo&agrave;i chim y&ecirc;u qu&aacute;i nh&igrave;n đứa ngốc gi&ocirc;́ng như &aacute;nh mắt, T&ocirc;n Thiệu ngồi ngay ngắn ở m&acirc;y m&ugrave; b&ecirc;n tr&ecirc;n, chịu nhịn Thi&ecirc;n Phong cắt v&agrave;o - cơ thể đau nhức. Thi&ecirc;n Phong như lưỡi dao giống như vậy, đem T&ocirc;n Thiệu th&acirc;n thể hoa xuất ra đạo đạo vết thương, bất qu&aacute; những vết thương n&agrave;y vừa rồi xuất hiện, liền đ&atilde; mắt thường tốc độ r&otilde; rệt kh&ocirc;i phục.<br />\r\n<br />\r\nBa th&aacute;ng sau đ&oacute;, mấy trăm con lo&agrave;i chim y&ecirc;u qu&aacute;i bị Thi&ecirc;n Phong thổi đến thương t&iacute;ch khắp người, trước sau rời đi. Vẫn cứ lưu lại hấp thu sức gi&oacute;, chỉ c&oacute; mười mấy ph&aacute;p lực đối lập y&ecirc;u qu&aacute;i mạnh mẽ. Bất qu&aacute; những n&agrave;y y&ecirc;u qu&aacute;i đồng dạng vết thương chồng chất, dường như T&ocirc;n Thiệu như vậy kh&ocirc;ng bị thương ch&uacute;t n&agrave;o, một c&aacute;i cũng kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nTrời sinh Thạch Hầu, Ti&ecirc;n Thi&ecirc;n đạo thể, T&ocirc;n Thiệu ba th&aacute;ng trong gi&oacute; tu luyện, so với tầm thường y&ecirc;u qu&aacute;i, tốc độ tự nhi&ecirc;n l&agrave; kh&aacute;c biệt một trời một vực. M&agrave; c&oacute; si&ecirc;u cường tốc độ kh&ocirc;i phục, T&ocirc;n Thiệu giờ khắc này c&agrave;ng l&agrave; ở v&agrave;o đầy m&aacute;u đầy xanh trạng th&aacute;i đỉnh cao.<br />\r\n<br />\r\nGiương đ&ocirc;i mắt, T&ocirc;n Thiệu t&ugrave;y &yacute; vung tay l&ecirc;n, dưới l&ograve;ng b&agrave;n tay, nhất thời cuồng phong g&agrave;o th&eacute;t, thẳng th&ocirc;̉i đ&ecirc;́n mức c&aacute;i kh&aacute;c lo&agrave;i chim y&ecirc;u qu&aacute;i sắc mặt đại biến,<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải chứ, n&agrave;y hầu y&ecirc;u dĩ nhi&ecirc;n nắm giữ Thi&ecirc;n Phong lực! N&agrave;y mới mấy th&aacute;ng! Hắn đ&uacute;ng l&agrave; một con khỉ &agrave;!&quot;<br />\r\n<br />\r\nMặc d&ugrave; l&agrave; những ph&aacute;p lực n&agrave;y tương đối cường đại lo&agrave;i chim y&ecirc;u qu&aacute;i, muốn phải nắm giữ Thi&ecirc;n Phong lực lượng, cũng đều cần mấy năm c&ocirc;ng phu. M&agrave; T&ocirc;n Thiệu nhưng là một con khỉ, thuộc về kh&aacute;c loại trong kh&aacute;c loại, dĩ nhi&ecirc;n sớm tất cả mọi người nắm giữ Thi&ecirc;n Phong lực lượng, c&oacute; thể n&agrave;o kh&ocirc;ng khiến người ta khiếp sợ!<br />\r\n<br />\r\n&quot;Loại tr&igrave;nh độ n&agrave;y sức gi&oacute; đ&atilde; kh&ocirc;ng c&oacute; tu luyện hiệu quả, đi cao hơn địa phương đi.&quot;<br />\r\n<br />\r\nBa ng&agrave;n trượng Thi&ecirc;n Phong, cũng kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o gợi l&ecirc;n T&ocirc;n Thiệu mảy may. Kh&aacute; l&agrave; đồng t&igrave;nh nh&igrave;n mười mấy thương t&iacute;ch khắp người lo&agrave;i chim y&ecirc;u qu&aacute;i, ở bọn họ &aacute;nh mắt h&acirc;m mộ b&ecirc;n trong, T&ocirc;n Thiệu cuốn l&ecirc;n một trận cuồng phong, hướng về cao hơn bầu trời bay đi.<br />\r\n<br />\r\nLần n&agrave;y, T&ocirc;n Thiệu bay thẳng đến một vạn trượng độ cao, nơi n&agrave;y gi&oacute;, đ&atilde; kh&ocirc;ng phải l&agrave; v&ocirc; h&igrave;nh v&ocirc; sắc, dĩ nhi&ecirc;n mang theo một tia bạch quang, như m&agrave;u trắng lưỡi dao gi&ocirc;́ng như, trực tiếp đem T&ocirc;n Thiệu Bạch Hổ da &aacute;o l&ocirc;ng cắt được vết thương nằm d&agrave;y đặc, trắng như tuyết da hổ b&agrave;o, khắp nơi c&oacute; thể thấy được vết m&aacute;u.<br />\r\n<br />\r\nMặc d&ugrave; c&oacute; si&ecirc;u cường năng lực hồi phục, trong chớp mắt những n&agrave;y vết thương nhỏ liền kh&eacute;p lại, nhưng n&agrave;y ray rức đau, vẫn l&agrave; để T&ocirc;n Thiệu nhe răng trợn mắt, đau đến k&ecirc;u l&ecirc;n.<br />\r\n<br />\r\n&quot;Hừ, từ đ&acirc;u tới hầu y&ecirc;u, kh&ocirc;ng biết tự lượng sức m&igrave;nh, d&aacute;m đi tới một vạn trượng vị tr&iacute;. Kh&ocirc;ng cho k&ecirc;u la nữa, bằng kh&ocirc;ng ảnh hưởng bản Vương tu luyện, cương phong qu&aacute;t kh&ocirc;ng chết được ngươi, bản Vương cũng biết ăn sống rồi ngươi!&quot;<br />\r\n<br />\r\n&quot;Lăn xa điểm! Chỉ l&agrave; tiểu y&ecirc;u, cũng xứng c&ugrave;ng ch&uacute;ng ta Y&ecirc;u vương c&ugrave;ng tu luyện!&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu g&agrave;o l&ecirc;n đau đớn, trực tiếp ngh&ecirc;nh đ&oacute;n mấy đạo tiếng khiển tr&aacute;ch thanh &acirc;m. Vội v&agrave;ng dừng &acirc;m thanh, T&ocirc;n Thiệu nh&igrave;n bốn ph&iacute;a một c&aacute;i, chỉ thấy ở n&agrave;y một vạn trượng độ cao b&ecirc;n tr&ecirc;n, c&oacute; m&acirc;́y chục đạo b&oacute;ng người. Những b&oacute;ng người n&agrave;y hoặc l&agrave; trung ni&ecirc;n d&aacute;ng dấp, hoặc l&agrave; l&atilde;o đầu d&aacute;ng dấp, hoặc l&agrave; mỹ nữ d&aacute;ng dấp, nhưng m&agrave; tr&ecirc;n người mỗi một người s&oacute;ng ph&aacute;p lực, đều để T&ocirc;n Thiệu trong l&ograve;ng run sợ.<br />\r\n<br />\r\nN&agrave;y m&acirc;́y chục người, dĩ nhi&ecirc;n to&agrave;n bộ l&agrave; Y&ecirc;u vương c&acirc;́p b&acirc;̣c t&ocirc;̀n tại, t&ugrave;y tiện một người, đều c&oacute; thể dễ d&agrave;ng b&oacute;p chết T&ocirc;n Thiệu!<br />\r\n<br />\r\n&quot;Ha ha, thật kh&ocirc;ng tiện. . .&quot;<br />\r\n<br />\r\nĐịch mạnh ta l&ugrave;i, am hiểu s&acirc;u n&agrave;y b&ocirc;́n c&aacute;i ch&acirc;n ng&ocirc;n T&ocirc;n Thiệu, cười khổ đi tới bi&ecirc;n giới vị tr&iacute;, l&uacute;c n&agrave;y mới lắng lại tiếp theo ch&uacute;ng Y&ecirc;u vương bất m&atilde;n.<br />\r\n<br />\r\nKhoanh ch&acirc;n ngồi ở m&acirc;y m&ugrave; b&ecirc;n tr&ecirc;n, T&ocirc;n Thiệu cũng kh&ocirc;ng lấy đ&oacute; yếu lấy l&agrave;m hổ thẹn, đ&aacute;nh kh&ocirc;ng lại người kh&aacute;c, c&ograve;n cứng rắn l&agrave;m hảo h&aacute;n, bị những n&agrave;y Y&ecirc;u vương ăn đi chưa từng nơi k&ecirc;u oan. D&ugrave; sao m&igrave;nh mặc d&ugrave; l&agrave; xuy&ecirc;n qua ch&uacute;ng, bất qu&aacute; hiển nhi&ecirc;n thiếu hụt chủ gi&aacute;c vầng s&aacute;ng, lần trước một con phổ th&ocirc;ng con cọp đều su&yacute;t ch&uacute;t nữa tự tử. M&igrave;nh b&acirc;y giờ, kh&ocirc;ng phải l&agrave; Kim Cương bất diệt T&ocirc;n Ngộ Kh&ocirc;ng, kh&ocirc;ng cẩn thận, nhưng l&agrave; sẽ ngỏm củ tỏi.</p>\r\n\r\n<p><br />\r\n<br />\r\nĐương nhi&ecirc;n, yếu thế cũng l&agrave; c&oacute; nguy&ecirc;n tắc, thừa nhận m&igrave;nh t&agrave;i nghệ kh&ocirc;ng bằng người c&oacute; thể, nhưng để T&ocirc;n Thiệu thấp đầu c&uacute;i người xin tha, đ&oacute; l&agrave; tuyệt đối kh&ocirc;ng được. Yếu thế l&agrave; n&oacute;i r&otilde; m&igrave;nh kh&ocirc;ng phải l&agrave; cậy mạnh đấu t&agrave;n nhẫn người, cũng kh&ocirc;ng phải n&oacute;i ch&iacute;nh m&igrave;nh mềm yếu c&oacute; thể bắt nạt!<br />\r\n<br />\r\nBi&ecirc;n giới vị tr&iacute; cương phong, cũng kh&ocirc;ng c&oacute; vị tr&iacute; trung t&acirc;m m&atilde;nh liệt như vậy, bất qu&aacute; đối với b&acirc;y giờ T&ocirc;n Thiệu tới n&oacute;i, nhưng l&agrave; vừa đ&uacute;ng.<br />\r\n<br />\r\nBa năm ph&iacute;a sau, T&ocirc;n Thiệu hai mắt bỗng nhi&ecirc;n trợn mở, song chưởng c&ugrave;ng vung, cương phong m&atilde;nh liệt hạ, trực tiếp để một đ&aacute;m Y&ecirc;u vương sắc mặt đại biến,<br />\r\n<br />\r\n&quot;Kh&ocirc;ng thể, n&agrave;y đầu khỉ tiểu y&ecirc;u, l&agrave;m sao c&oacute; khả năng nắm giữ cương phong lực lượng! Mặc d&ugrave; l&agrave; ch&uacute;ng ta những n&agrave;y Y&ecirc;u vương c&acirc;́p b&acirc;̣c t&ocirc;̀n tại, kh&ocirc;ng c&oacute; trăm năm khổ tu, cũng kh&ocirc;ng c&aacute;ch n&agrave;o khởi đ&ocirc;̣ng cương phong!&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu tự nhi&ecirc;n kh&ocirc;ng thể n&oacute;i cho bọn họ biết Ti&ecirc;n Thi&ecirc;n đạo thể b&iacute; mật, mặt kh&ocirc;ng đổi sắc đối với một đ&aacute;m Y&ecirc;u vương kh&aacute;ch kh&iacute; nở nụ cười, cuốn l&ecirc;n một trận cương phong, ở một đ&aacute;m Y&ecirc;u vương kh&ocirc;ng thể tin trong &aacute;nh mắt, hướng về cao hơn tr&ecirc;n bầu trời bay đi.<br />\r\n<br />\r\nLĩnh ngộ cương phong lực lượng, dọc theo đường đi cương phong, đối với T&ocirc;n Thiệu thương tổn tiếp cận kh&ocirc;ng. Đ&acirc;y cũng n&oacute;i, nơi n&agrave;y cương phong đối với T&ocirc;n Thiệu tu luyện hiệu quả tiếp cận l&agrave; số 0.<br />\r\n<br />\r\nĐứng ở t&aacute;m vạn trượng độ cao, T&ocirc;n Thiệu nh&igrave;n đỉnh đầu, tự lẩm bẩm, &quot;80 ng&agrave;n trượng, nơi n&agrave;y cương phong tuy rằng m&atilde;nh liệt, kh&ocirc;ng sai m&agrave; đối với ta tu luyện t&aacute;c dụng vẫn cứ kh&ocirc;ng lớn, c&oacute; n&ecirc;n hay kh&ocirc;ng tiếp tục đi l&ecirc;n đ&acirc;y?&quot;<br />\r\n<br />\r\nỞ đi l&ecirc;n, ch&iacute;nh l&agrave; Cương Phong Chi Nh&atilde;n phạm vi, ở nơi đ&oacute;, lạnh thấu xương cương phong, c&agrave;ng mang theo một tia hắc mang, m&agrave; hắc mang n&agrave;y, để T&ocirc;n Thiệu bản năng cảm gi&aacute;c nguy hiểm.<br />\r\n<br />\r\nT&aacute;m vạn trượng vị tr&iacute;, chỉ c&oacute; linh tinh mấy c&aacute;i Thi&ecirc;n y&ecirc;u c&oacute; thể thừa nhận được cuồng bạo cương phong, m&agrave; những n&agrave;y Thi&ecirc;n y&ecirc;u, ph&oacute;ng tầm mắt to&agrave;n bộ Nh&acirc;n giới bốn ch&acirc;u, đều l&agrave; khủng bố như vậy tồn tại. Chỉ l&agrave; mặc d&ugrave; l&agrave; những n&agrave;y Thi&ecirc;n y&ecirc;u, cũng kh&ocirc;ng c&oacute; nhảy v&agrave;o Cương Phong Chi Nh&atilde;n dũng cảm.<br />\r\n<br />\r\nM&agrave;u đen kia cương phong, qu&aacute; mức nguy hiểm, mặc d&ugrave; l&agrave; Thi&ecirc;n y&ecirc;u, t&ugrave;y tiện tiến nhập, cũng biết chết kh&ocirc;ng to&agrave;n th&acirc;y!<br />\r\n<br />\r\nRất l&acirc;u, T&ocirc;n Thiệu vẻ do dự tr&ecirc;n mặt vừa đi, nhấc l&ecirc;n cương phong, bỗng nhi&ecirc;n nhảy v&agrave;o m&agrave;u đen b&atilde;o t&aacute;p trong v&ograve;ng.<br />\r\n<br />\r\nCũng trong l&uacute;c đ&oacute;, một c&aacute;i đủ c&oacute; mấy trăm trượng to lớn ủng chiến, hướng về T&ocirc;n Thiệu một cước đạp xuống, ở n&agrave;y ủng chiến b&ecirc;n dưới, th&ecirc;u rết đồ đằng, m&agrave; m&agrave;u đen cương phong v&ograve;ng trực tiếp bị n&agrave;y ủng chiến một cước đ&aacute; t&aacute;n. Sau đ&oacute;, một đạo &acirc;m trầm như x&agrave; hạt thanh &acirc;m, uy nghi&ecirc;m đ&aacute;ng sợ vang l&ecirc;n, &quot;Ta chủ Nhị Lang Ch&acirc;n qu&acirc;n ở đ&acirc;y tu h&agrave;nh, kh&ocirc;ng quan hệ người, mau chóng lảng tr&aacute;nh! Bằng kh&ocirc;ng, chết!&quot;<br />\r\n<br />\r\nN&agrave;y ủng chiến xen lẫn v&ocirc; thượng uy thế, đem T&ocirc;n Thiệu quanh th&acirc;n kh&ocirc;ng gian kh&oacute;a chặt, ngay cả chạy trốn độn đều l&agrave;m kh&ocirc;ng được đến. T&ocirc;n Thiệu sắc mặt đại biến, n&agrave;y ủng chiến liền cương phong đều c&oacute; thể đ&aacute; t&aacute;n, như để n&agrave;y ủng chiến t&ugrave;y tiện s&aacute;t một hồi, ch&iacute;nh m&igrave;nh n&agrave;y thể x&aacute;c ph&agrave;m tục, trực tiếp sẽ bị đ&aacute; cho thịt n&aacute;t!<br />\r\n<br />\r\nThời khắc sống c&ograve;n, một c&aacute;i gần ng&agrave;n trượng m&agrave;u v&agrave;ng m&oacute;ng to, vồ một c&aacute;i ở ủng chiến b&ecirc;n tr&ecirc;n, đem uy thế n&agrave;y v&ocirc; c&ugrave;ng ủng chiến, miễn cưỡng b&oacute;p n&aacute;t, m&aacute;u thịt tung to&eacute;,<br />\r\n<br />\r\n&quot;Ta Bằng Ma Vương ở đ&acirc;y, h&aacute; c&oacute; thể cho ph&eacute;p ngươi Mai sơn thất qu&aacute;i bắt nạt ta Y&ecirc;u tộc tiểu bối!&quot;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:99.4688px; top:1706px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'cuong-phong-chi-nhan-mai-son-that-quai', 8, '3453', '2022-08-03 05:53:33', '2022-08-03 05:53:33');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(43, '5', 'To gan thiếu nữ?', '<p>&quot;Ta Bằng Ma Vương ở đ&acirc;y, h&aacute; c&oacute; thể cho ph&eacute;p ngươi Mai Sơn thất qu&aacute;i bắt nạt ta Y&ecirc;u tộc tiểu bối!&quot;<br />\r\n<br />\r\nĐạo n&agrave;y th&ocirc; bạo v&ocirc; c&ugrave;ng &acirc;m thanh ph&iacute;a sau, ngàn trượng kim trảo c&ograve;n muốn tiếp tục c&ocirc;ng k&iacute;ch c&aacute;i kia ủng chiến chủ nh&acirc;n, bỗng nhi&ecirc;n một đạo kh&aacute;c trầm thấp m&agrave; thanh &acirc;m h&ugrave;ng hậu vang l&ecirc;n,<br />\r\n<br />\r\n&quot;C&ocirc;ng L&acirc;n, trở về, ngươi kh&ocirc;ng phải l&agrave; đối thủ của hắn. Hừ, Ph&aacute;p Thi&ecirc;n Tượng Địa sao, đ&aacute;ng tiếc so với bản Qu&acirc;n, hỏa hầu c&ograve;n k&eacute;m ch&uacute;t!&quot;<br />\r\n<br />\r\nThanh &acirc;m nam tử chưa nghỉ, một c&aacute;i hai ngàn trượng to lớn b&agrave;n tay bằng thịt, quay về kim trảo vỗ một c&aacute;i m&agrave; xuống, hai người chạm v&agrave;o nhau b&ecirc;n dưới, ph&aacute;t sinh kim thiết chạm v&agrave;o nhau tiếng, c&ograve;n như l&ocirc;i đ&igrave;nh gi&ocirc;́ng như nổ vang, kh&ocirc;ng gian đều mơ hồ x&eacute; r&aacute;ch, to lớn s&oacute;ng ph&aacute;p lực, đem v&agrave;i t&ecirc;n Thi&ecirc;n y&ecirc;u trực tiếp đẩy lui m&acirc;́y ngàn m&eacute;t. M&agrave; khoảng c&aacute;ch gợn s&oacute;ng gần nhất T&ocirc;n Thiệu, trực tiếp bị chấn động đến mức miệng mũi chảy máu.</p>', '<p>&quot;Ta Bằng Ma Vương ở đ&acirc;y, h&aacute; c&oacute; thể cho ph&eacute;p ngươi Mai Sơn thất qu&aacute;i bắt nạt ta Y&ecirc;u tộc tiểu bối!&quot;<br />\r\n<br />\r\nĐạo n&agrave;y th&ocirc; bạo v&ocirc; c&ugrave;ng &acirc;m thanh ph&iacute;a sau, ngàn trượng kim trảo c&ograve;n muốn tiếp tục c&ocirc;ng k&iacute;ch c&aacute;i kia ủng chiến chủ nh&acirc;n, bỗng nhi&ecirc;n một đạo kh&aacute;c trầm thấp m&agrave; thanh &acirc;m h&ugrave;ng hậu vang l&ecirc;n,<br />\r\n<br />\r\n&quot;C&ocirc;ng L&acirc;n, trở về, ngươi kh&ocirc;ng phải l&agrave; đối thủ của hắn. Hừ, Ph&aacute;p Thi&ecirc;n Tượng Địa sao, đ&aacute;ng tiếc so với bản Qu&acirc;n, hỏa hầu c&ograve;n k&eacute;m ch&uacute;t!&quot;<br />\r\n<br />\r\nThanh &acirc;m nam tử chưa nghỉ, một c&aacute;i hai ngàn trượng to lớn b&agrave;n tay bằng thịt, quay về kim trảo vỗ một c&aacute;i m&agrave; xuống, hai người chạm v&agrave;o nhau b&ecirc;n dưới, ph&aacute;t sinh kim thiết chạm v&agrave;o nhau tiếng, c&ograve;n như l&ocirc;i đ&igrave;nh gi&ocirc;́ng như nổ vang, kh&ocirc;ng gian đều mơ hồ x&eacute; r&aacute;ch, to lớn s&oacute;ng ph&aacute;p lực, đem v&agrave;i t&ecirc;n Thi&ecirc;n y&ecirc;u trực tiếp đẩy lui m&acirc;́y ngàn m&eacute;t. M&agrave; khoảng c&aacute;ch gợn s&oacute;ng gần nhất T&ocirc;n Thiệu, trực tiếp bị chấn động đến mức miệng mũi chảy máu.<br />\r\n<br />\r\nHai người va chạm kết quả, kim trảo bị đập xuất ra đạo đạo vết r&aacute;ch, m&agrave; b&agrave;n tay bằng thịt đồng dạng kh&ocirc;ng dễ chịu, bị t&oacute;m ra một c&aacute;i to lớn lỗ m&aacute;u.<br />\r\n<br />\r\nC&aacute;i kia kim trảo chủ nh&acirc;n gặp một đ&ograve;n bất ph&acirc;n thắng bại, lạnh r&ecirc;n một tiếng, lập tức d&ugrave;ng kh&ocirc;ng cho cự tuyệt khẩu kh&iacute;, như l&ocirc;i đ&igrave;nh nổ ầm &acirc;m thanh n&oacute;i với T&ocirc;n Thiệu, &quot;Lỗ m&atilde;ng tiểu y&ecirc;u, ở đ&acirc;y kh&ocirc;ng phải ngươi n&ecirc;n tới địa phương, mau chóng thối lui!&quot;<br />\r\n<br />\r\n&Acirc;m thanh n&agrave;y ph&iacute;a sau, kh&ocirc;ng c&ograve;n &acirc;m thanh nữa, m&agrave; trước bị đ&aacute;nh tan m&agrave;u đen cương phong v&ograve;ng, cũng cấp tốc hợp lại.<br />\r\n<br />\r\nNh&igrave;n tr&ecirc;n người đỏ như m&aacute;u da hổ b&agrave;o, T&ocirc;n Thiệu nhẫn nhịn thấu xương đau, khoanh ch&acirc;n ngồi ở trong m&acirc;y m&ugrave;, điều tức thương thế b&ecirc;n trong cơ thể.<br />\r\n<br />\r\nMặc d&ugrave; T&ocirc;n Thiệu năng lực hồi phục cường h&atilde;n, nhưng đ&oacute; cũng chỉ l&agrave; tương đối tiểu thương m&agrave; n&oacute;i. Loại thương thế n&agrave;y, kh&ocirc;ng c&oacute; mấy th&aacute;ng, quyết định kh&ocirc;ng c&aacute;ch n&agrave;o kh&ocirc;i phục.<br />\r\n<br />\r\nT&ocirc;n Thiệu hồi tưởng vừa n&atilde;y c&aacute;i kia đ&aacute; hướng m&igrave;nh một đạo ủng chiến, trong mắt lửa giận ngất trời, nếu kh&ocirc;ng c&oacute; Bằng Ma Vương xuất thủ cứu gi&uacute;p, ch&iacute;nh m&igrave;nh nhất định đ&atilde; bị người kia một cước đ&aacute; chết.<br />\r\n<br />\r\n&quot;Mai Sơn s&aacute;u hữu trong Di&ecirc;u C&ocirc;ng L&acirc;n sao, hừ, kh&ocirc;ng hỏi đ&uacute;ng sai phải tr&aacute;i liền lấy l&atilde;o T&ocirc;n t&iacute;nh mạng, l&atilde;o T&ocirc;n nhớ kỹ ngươi!&quot;<br />\r\n<br />\r\nBất qu&aacute; đối với tướng cứu m&igrave;nh Bằng Ma Vương, T&ocirc;n Thiệu vẫn l&agrave; rất cảm k&iacute;ch, nguy&ecirc;n t&aacute;c b&ecirc;n trong T&ocirc;n Ngộ Kh&ocirc;ng c&ugrave;ng Bằng Ma Vương l&agrave; huynh đệ kết nghĩa, kiếp n&agrave;y Bằng Ma Vương c&agrave;ng là tại ch&iacute;nh m&igrave;nh nguy nan thời gian cứu m&igrave;nh một mạng, tuy rằng cuối c&ugrave;ng đối với m&igrave;nh rầy một phen, bất qu&aacute; cũng l&agrave; muốn tốt cho m&igrave;nh. Ng&agrave;y sau c&oacute; cơ hội, Bằng Ma Vương nh&acirc;n t&igrave;nh n&agrave;y, T&ocirc;n Thiệu nhất định phải trả lại.<br />\r\n<br />\r\nỞ T&ocirc;n Thiệu một mặt hờn dỗi đả tọa điều tức thời gian, trước mắt một tia s&aacute;ng trắng l&oacute;e l&ecirc;n, bỗng dưng xuất hiện một c&aacute;i c&ocirc; g&aacute;i mặc &aacute;o trắng. T&ocirc;n Thiệu nhấc đầu nh&igrave;n tới, n&agrave;y nữ tử quần trắng xem ra bất qu&aacute; bộ d&aacute;ng mười lăm mười s&aacute;u tuổi, đang thanh nh&atilde; xinh đẹp ở cương phong b&ecirc;n trong, những cương phong kia thổi qua thiếu nữ b&ecirc;n người, lập tức ph&acirc;n đạo tản ra.<br />\r\n<br />\r\nMặt của c&ocirc; g&aacute;i tr&ecirc;n bao bọc m&agrave;u trắng khăn che mặt, kh&ocirc;ng thấy r&otilde; dung nhan, chỉ c&oacute; hai c&aacute;i linh động con ngươi, đối diện T&ocirc;n Thiệu một nh&aacute;y mắt một nh&aacute;y mắt, cực kỳ đẹp đẽ đ&aacute;ng y&ecirc;u,<br />\r\n<br />\r\n&quot;H&igrave; h&igrave;, khỉ nhỏ ca sinh hờn dỗi đ&acirc;y? L&agrave;m g&igrave; ngồi ở chỗ n&agrave;y bất động đ&acirc;y?&quot;<br />\r\n<br />\r\nC&oacute; thể xuất hiện ở cương phong t&aacute;m vạn trượng, ngoại trừ T&ocirc;n Thiệu ch&iacute;nh m&igrave;nh, đều l&agrave; Thi&ecirc;n y&ecirc;u cấp bậc l&atilde;o qu&aacute;i vật, như l&agrave; b&igrave;nh thường, T&ocirc;n Thiệu đối với t&ecirc;n thiếu nữ n&agrave;y tất nhi&ecirc;n sẽ cẩn thận một ch&uacute;t, kh&ocirc;ng dễ d&agrave;ng đắc tội đối phương. Bất qu&aacute; giờ khắc này T&ocirc;n Thiệu trong l&ograve;ng đang k&igrave;m n&eacute;n một luồng hờn dỗi, kh&ocirc;ng chỗ ph&aacute;t tiết, tuy rằng biết r&otilde; t&ecirc;n thiếu nữ n&agrave;y khả năng lai lịch to lớn, T&ocirc;n Thiệu như cũ tức giận n&oacute;i rằng,<br />\r\n<br />\r\n&quot;Đi đi đi, ai l&agrave; của ngươi khỉ nhỏ ca! Một b&ecirc;n chơi trứng đi!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng được kh&ocirc;ng được, trứng l&agrave; kh&ocirc;ng thể đ&ugrave;a, chơi hỏng rồi, tiểu C&ocirc;n Bằng nh&oacute;m liền kh&ocirc;ng thể ấp trứng. Huống hồ, ở đ&acirc;y cũng kh&ocirc;ng c&oacute; trứng nha. . .&quot;<br />\r\n<br />\r\nNghe T&ocirc;n Thiệu dĩ nhi&ecirc;n để ch&iacute;nh m&igrave;nh chơi trứng, thiếu nữ mặc &aacute;o trắng vội v&atilde; xua tay, linh động trong con ngươi hơi c&oacute; ch&uacute;t cấp thiết.<br />\r\n<br />\r\n&quot;&Acirc;y. . .&quot; Thấy m&igrave;nh lời n&oacute;i th&ocirc; tục c&agrave;ng bị tiểu c&ocirc; nương như vậy l&yacute; giải, tuy l&agrave; T&ocirc;n Thiệu da mặt d&agrave;y, cũng kh&ocirc;ng khỏi sắc mặt đỏ bừng, trong nơi n&agrave;y vẫn l&agrave; mặt khỉ, r&otilde; r&agrave;ng th&agrave;nh đ&iacute;t khỉ.<br />\r\n<br />\r\nCh&iacute;nh m&igrave;nh dĩ nhi&ecirc;n đối với một c&aacute;i lần thứ nhất gặp mặt tiểu c&ocirc; nương đ&ugrave;a kiểu n&agrave;y, l&agrave;m người hai đời thực sự l&agrave; sống uổng.<br />\r\n<br />\r\nGặp T&ocirc;n Thiệu sắc mặt nghẹn hồng, thiếu nữ &aacute;nh mắt lộ ra hốt hoảng vẻ mặt, &quot;Ai nha, mặt của ngươi thật l&agrave; đỏ, c&oacute; phải l&agrave; bị ca ca ta đấu ph&aacute;p dư &acirc;m chấn động đến rồi. . .&quot;<br />\r\n<br />\r\n&quot;Ph&iacute; lời, g&aacute;i ngố, kh&ocirc;ng thấy ta cả người l&agrave; huyết &agrave;!&quot;</p>\r\n\r\n<p><br />\r\n&quot;Xin lỗi. . .&quot; Nghe T&ocirc;n Thiệu l&agrave; ca ca của m&igrave;nh k&iacute;ch thương, thiếu nữ trong mắt tr&agrave;n đầy hổ thẹn, &quot;L&agrave;m sao b&acirc;y giờ, lần n&agrave;y ra ngo&agrave;i, ta cũng kh&ocirc;ng mang đan dược g&igrave;. . .&quot;<br />\r\n<br />\r\nTrong b&oacute;ng tối th&ocirc;i th&uacute;c Hỏa Nh&atilde;n Kim T&igrave;nh, T&ocirc;n Thiệu hai mắt hiện l&ecirc;n kim quang,<br />\r\n<br />\r\nHướng về thiếu nữ nh&igrave;n tới, chỉ thấy bản thể, r&otilde; r&agrave;ng l&agrave; một con m&agrave;u trắng Đại Bằng. M&agrave; dung mạo , tương tự bị T&ocirc;n Thiệu liếc mắt một c&aacute;i l&agrave; r&otilde; mồn một. Non nớt khu&ocirc;n mặt nhỏ, như Thanh Li&ecirc;n tỏa ra, mang theo hồn nhi&ecirc;n c&ugrave;ng hờ hững.<br />\r\n<br />\r\nTrong l&ograve;ng lường trước thiếu nữ ca ca, nhất định l&agrave; cứu viện m&igrave;nh Bằng Ma Vương, T&ocirc;n Thiệu trong l&ograve;ng hờn dỗi cũng l&agrave; ti&ecirc;u mất ch&uacute;t, đối với thiếu nữ ngữ kh&iacute; trở n&ecirc;n nhu h&ograve;a, &quot;Qu&ecirc;n đi, sự t&igrave;nh đ&atilde; qua, kh&ocirc;ng cần để ở trong l&ograve;ng.&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng được, ngươi bị thương nặng như vậy, vạn c&aacute;i chết l&agrave;m sao b&acirc;y giờ.&quot; Thiếu nữ trong tr&ograve;ng mắt cấp thiết, ph&aacute;t ra từ ch&acirc;n t&acirc;m, kh&ocirc;ng c&oacute; c&oacute; một tia ngụy trang. Kh&ocirc;ng biết nghĩ tới điều g&igrave;, thanh t&uacute; lỗ tai nhất thời biến đến đỏ bừng, &quot;Ngươi, ngươi trước nhắm mắt lại. . .&quot;<br />\r\n<br />\r\n&quot;Ta đi, kh&ocirc;ng phải chứ. Chẳng lẽ muốn ph&aacute;t sinh tiểu thuyết huyền ảo b&ecirc;n trong nhất khu&ocirc;n s&aacute;o cũ t&igrave;nh tiết? Nữ tử v&igrave; cho t&igrave;nh cảm ch&acirc;n th&agrave;nh chữa thương, c&ugrave;ng nam nh&acirc;n xxoo v&igrave; đ&oacute; giải độc, như thế khu&ocirc;n s&aacute;o cũ nội dung vở kịch, để l&atilde;o T&ocirc;n gặp được? !&quot;<br />\r\n<br />\r\nNghĩ tới những thứ n&agrave;y, T&ocirc;n Thiệu mặt đồng dạng biến đến đỏ bừng, khẩn trương nhắm hai mắt lại.<br />\r\n<br />\r\nĐừng xem T&ocirc;n Thiệu phạm l&ecirc;n h&ocirc;̃n đến một kh&ocirc;ng biết xấu hổ hai kh&ocirc;ng muốn sống, nhưng là đến rồi khẩn yếu quan đầu, so với hắn c&aacute;c trai t&acirc;n đều phải thẹn th&ugrave;ng.<br />\r\n<br />\r\nTựa hồ nghe được khăn lụa cởi ra &acirc;m thanh, tựa hồ c&oacute; thể nghe đến thiếu nữ c&agrave;ng ng&agrave;y c&agrave;ng gần m&ugrave;i thơm, thanh &acirc;m của thiếu nữ, đột nhi&ecirc;n ở T&ocirc;n Thiệu b&ecirc;n tai thăm thẳm, &quot;C&oacute; thể sẽ c&oacute; ch&uacute;t đau, ta l&agrave; lần đầu ti&ecirc;n. . . L&agrave;m như thế, ngươi nếu như đau, liền gọi ra. . .&quot;<br />\r\n<br />\r\nN&agrave;y lời truyền đến T&ocirc;n Thiệu trong l&ograve;ng, l&yacute; tr&iacute; rốt cục chiến thắng dục vọng, bỗng nhi&ecirc;n mở mắt ra, &quot;Kh&ocirc;ng, kh&ocirc;ng được, nhiều người ở đ&acirc;y nh&atilde;n tạp, l&agrave;m như thế, l&atilde;o T&ocirc;n kh&ocirc;ng chịu được!&quot;<br />\r\n<br />\r\nỞ tại mở mắt ra đồng thời, hơi thở hổn hển c&ugrave;ng ngưu tựa như, hầu trong mũi c&agrave;ng c&oacute; mũi m&aacute;u chảy ra. Giờ khắc này T&ocirc;n Thiệu trong đầu, tr&agrave;n đầy c&aacute;i kia ch&uacute;t rung động l&ograve;ng người chữ, lần thứ nhất, đau, gọi ra. . .<br />\r\n<br />\r\nBất qu&aacute; trước mắt, đ&uacute;ng l&agrave; kh&ocirc;ng như trong tưởng tượng thiếu nữ cởi &aacute;o th&aacute;o d&acirc;y lưng cảnh tượng. Thời khắc n&agrave;y thiếu nữ mặc &aacute;o trắng đ&atilde; giải ph&iacute;a dưới ra, trong mi&ecirc;̣ng ngậm lấy một vi&ecirc;n bạch quang lượn quanh vi&ecirc;n ch&acirc;u, khu&ocirc;n mặt nhỏ đỏ hồng hồng. Đột nhi&ecirc;n nh&igrave;n thấy T&ocirc;n Thiệu mở mắt ra, thiếu nữ một c&aacute;i nuốt v&agrave;o vi&ecirc;n ch&acirc;u, xấu hổ n&oacute;i rằng,<br />\r\n<br />\r\n&quot;Kh&ocirc;ng phải n&oacute;i kh&ocirc;ng cho mở mắt sao, ngươi chơi xấu!&quot;<br />\r\n<br />\r\n&quot;&Acirc;y. . . Ta mở mắt &agrave;. . . Nha, thật kh&ocirc;ng tiện, ngươi tiếp tục. . .&quot;<br />\r\n<br />\r\nXoa xoa m&aacute;u mũi, T&ocirc;n Thiệu l&uacute;ng t&uacute;ng nhắm hai mắt lại, trong l&ograve;ng v&igrave; ch&iacute;nh m&igrave;nh &yacute; nghĩ xấu xa xấu hổ kh&ocirc;ng ngớt. Tiểu c&ocirc; nương n&agrave;y nhiều thuần a, m&igrave;nh tại sao c&oacute; thể sử dụng bẩn thỉu &yacute; nghĩ suy nghĩ nàng đ&acirc;y, sẽ nh&igrave;n một ch&uacute;t nàng muốn l&agrave;m c&aacute;i g&igrave; đi.<br />\r\n<br />\r\nỞ T&ocirc;n Thiệu nhắm mắt tỉnh lại thời điểm, hầu tr&ecirc;n m&ocirc;i, đột nhi&ecirc;n truyền tới một lạnh lẽo m&agrave; mềm mại x&uacute;c cảm.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng, kh&ocirc;ng thể n&agrave;o. . .&quot;<br />\r\n<br />\r\nỞ n&agrave;y x&uacute;c cảm b&ecirc;n dưới, T&ocirc;n Thiệu nhất thời nằm ở sung huyết n&atilde;o trạng th&aacute;i, sau một khắc, một c&aacute;i trơn nhẵn c&aacute;i lưỡi đưa đến T&ocirc;n Thiệu trong mi&ecirc;̣ng. Lần n&agrave;y, T&ocirc;n Thiệu kh&ocirc;ng thể kiềm được. Cũng kh&ocirc;ng phải l&agrave; kh&ocirc;ng nhịn được muốn &ocirc;m chặt, m&agrave; l&agrave; muốn đem thiếu nữ đẩy ra.<br />\r\n<br />\r\n&quot;A a. . . Kh&ocirc;ng muốn. . . Kh&ocirc;ng n&ecirc;n cử động. . .&quot;<br />\r\n<br />\r\nBốn mắt nh&igrave;n nhau, trần trụi linh khoảng c&aacute;ch nh&igrave;n nhau, thiếu nữ to&agrave;n bộ g&ograve; m&aacute; đều phủ k&iacute;n đỏ ửng, lập tức vội v&agrave;ng đem trong mi&ecirc;̣ng mượt m&agrave; trắng ch&acirc;u độ v&agrave;o T&ocirc;n Thiệu trong cơ thể, rời m&ocirc;i, hươu con gi&ocirc;́ng như chạy trốn tới một b&ecirc;n, lau miệng tr&ecirc;n ti&ecirc;n dịch, ấn lại gấp g&aacute;p phập phồng ngực nhỏ, lắp bắp n&oacute;i,<br />\r\n<br />\r\n&quot;Ngươi. . . Ngươi nhanh d&ugrave;ng. . . Nhanh d&ugrave;ng của ta y&ecirc;u đan chữa thương. . . D&ugrave;ng hết rồi nhanh l&ecirc;n một ch&uacute;t trả lại cho ta. . . Ca ca n&oacute;i. . . Chưa tới Y&ecirc;u Th&aacute;nh cảnh giới. . . Y&ecirc;u đan kh&ocirc;ng thể ly thể qu&aacute; l&acirc;u. . .&quot;<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave; của ngươi y&ecirc;u đan? Ngươi đem y&ecirc;u đan cho ta chữa thương d&ugrave;ng!&quot;</p>\r\n\r\n<p><br />\r\n<br />\r\nKh&ocirc;ng đợi T&ocirc;n Thiệu phản ứng lại, trong miệng vi&ecirc;n ch&acirc;u thả ra cực kỳ kinh khủng y&ecirc;u lực, th&ecirc;m v&agrave;o T&ocirc;n Thiệu bản th&acirc;n năng lực hồi phục, chỉ trong chốc l&aacute;t, liền đem T&ocirc;n Thiệu thương thế b&ecirc;n trong cơ thể c&ugrave;ng nhau đ&atilde; trừ.<br />\r\n<br />\r\nM&agrave; c&ograve;n thừa lại y&ecirc;u lực, trữ h&agrave;ng ở T&ocirc;n Thiệu trong đan điền, cuồn cuộn y&ecirc;u lực đem T&ocirc;n Thiệu như kh&iacute; cầu gi&ocirc;́ng như no đến mức tr&ograve;n phồng, mặt khỉ c&agrave;ng là xanh hồng biến h&oacute;a, bất cứ l&uacute;c n&agrave;o đều c&oacute; bạo thể nguy hiểm.<br />\r\n<br />\r\nY&ecirc;u đan, T&ocirc;n Thiệu kh&ocirc;ng phải l&agrave; kh&ocirc;ng c&oacute;, tại ch&iacute;nh m&igrave;nh th&agrave;nh y&ecirc;u một khắc, b&ecirc;n trong đan điền của m&igrave;nh đồng dạng ngưng tụ ra một vi&ecirc;n y&ecirc;u đan. Chỉ l&agrave; T&ocirc;n Thiệu y&ecirc;u đan bất qu&aacute; lớn chừng hạt đậu, ẩn chứa y&ecirc;u lực so với thiếu nữ y&ecirc;u đan, c&agrave;ng là &iacute;t đến mức đ&aacute;ng thương. Thiếu nữ y&ecirc;u đan như l&agrave; đem to&agrave;n bộ y&ecirc;u lực ph&oacute;ng th&iacute;ch, ch&iacute;nh l&agrave; c&oacute; một trăm c&aacute;i T&ocirc;n Ngộ Kh&ocirc;ng, đều sẽ to&agrave;n bộ căng nứt!<br />\r\n<br />\r\n&quot;Y&ecirc;u đan, l&atilde;o T&ocirc;n cũng c&oacute;! Cho ta hấp!&quot;<br />\r\n<br />\r\nTrầm t&iacute;ch ở trong người y&ecirc;u lực, ở T&ocirc;n Thiệu dưới sự dẫn đường, tụ hợp v&agrave;o lục lớn chừng hạt đậu y&ecirc;u đan b&ecirc;n trong, như t&igrave;m tới một c&aacute;i tuy&ecirc;n tiết khẩu, cuồn cuộn y&ecirc;u lực như giang v&agrave;o biển rộng.<br />\r\n<br />\r\nNửa n&eacute;n hương ph&iacute;a sau, T&ocirc;n Thiệu y&ecirc;u đan đ&atilde; c&oacute; m&oacute;ng tay x&aacute;c k&iacute;ch cỡ tương đương, lại nh&igrave;n thiếu nữ mặc &aacute;o trắng, giờ khắc này sắc mặt đ&atilde; t&aacute;i nhợt, m&agrave; tr&ecirc;n bầu trời, c&agrave;ng c&oacute; đạo đạo thi&ecirc;n l&ocirc;i ngưng tụ.<br />\r\n<br />\r\nKh&ocirc;ng kịp phỏng chừng thi&ecirc;n l&ocirc;i, T&ocirc;n Thiệu nhất thời &yacute; thức được, thiếu nữ chỉ sợ l&agrave; v&igrave; cứu m&igrave;nh, y&ecirc;u đan ly thể qu&aacute; l&acirc;u, mới c&oacute; thể suy yếu như vậy. Nghĩ đến đ&acirc;y, T&ocirc;n Thiệu kh&ocirc;ng ch&uacute;t do dự m&agrave; một đem &ocirc;m chầm thiếu nữ mặc &aacute;o trắng, một c&aacute;i h&ocirc;n, đem thiếu nữ y&ecirc;u đan độ trở lại.<br />\r\n<br />\r\n&quot;Đa tạ. . . A!&quot;<br />\r\n<br />\r\nMột đem đẩy ra thiếu nữ, T&ocirc;n Thiệu chỉ kịp n&oacute;i ra một tiếng tạ, đột nhi&ecirc;n k&ecirc;u thảm một tiếng, cả người trở n&ecirc;n ch&aacute;y đen như than, c&agrave;ng l&agrave; bị đạo thi&ecirc;n l&ocirc;i n&agrave;y ph&aacute;ch ở tr&ecirc;n người,<br />\r\n<br />\r\n&quot;L&atilde;o T&ocirc;n. . . L&atilde;o T&ocirc;n biết, đ&acirc;y l&agrave; tu ch&acirc;n tiểu thuyết. . . B&ecirc;n trong độ kiếp. . . Chỉ l&agrave; l&ocirc;i kiếp, a!&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu cả người bị s&eacute;t đ&aacute;nh địa mất cảm gi&aacute;c kh&ocirc;ng thể tả, kh&ocirc;ng c&aacute;ch n&agrave;o nh&uacute;c nh&iacute;ch, lắp ba lắp bắp vừa mới n&oacute;i c&acirc;u lời hay, lại l&agrave; li&ecirc;n tiếp mấy đạo thi&ecirc;n l&ocirc;i đ&aacute;nh xuống. Lần n&agrave;y, trực tiếp đem T&ocirc;n Thiệu l&ocirc;ng khỉ c&ugrave;ng với da hổ b&agrave;o ph&aacute;ch địa n&aacute;t tan.<br />\r\n<br />\r\n&quot;Ầm ầm&quot; kh&ocirc;ng ngừng b&ecirc;n tai, nửa n&eacute;n hương ph&iacute;a sau, L&ocirc;i V&acirc;n chậm r&atilde;i tản đi, m&agrave; T&ocirc;n Thiệu đ&atilde; th&agrave;nh một đống than đen, nơi n&agrave;o c&ograve;n c&oacute; nửa điểm h&igrave;nh người.<br />\r\n<br />\r\nMỗi một khắc, than đen đột nhi&ecirc;n vỡ vụn mở, m&agrave; trong đ&oacute;, c&oacute; thể so với Y&ecirc;u vương cuồn cuộn y&ecirc;u lực g&agrave;o th&eacute;t ra, cũng trong l&uacute;c đ&oacute;, nửa người nửa hầu th&acirc;n thể T&ocirc;n Thiệu, từ tro than b&ecirc;n trong nhảy ra ngo&agrave;i.<br />\r\n<br />\r\n&quot;L&atilde;o T&ocirc;n đột ph&aacute; Y&ecirc;u vương! L&atilde;o T&ocirc;n c&oacute; thể h&oacute;a h&igrave;nh th&agrave;nh người! Ha ha!&quot;<br />\r\n<br />\r\nGiờ khắc này T&ocirc;n Thiệu th&acirc;n thể c&ugrave;ng ph&aacute;p lực, so với trước mạnh mẻ mấy lần. Cảm thụ được loại cường đại n&agrave;y cảm gi&aacute;c, T&ocirc;n Thiệu k&iacute;ch động kh&ocirc;ng th&ocirc;i.<br />\r\n<br />\r\nHắn rất muốn biết, T&ocirc;n Ngộ Kh&ocirc;ng hầu th&acirc;n biến h&oacute;a th&agrave;nh h&igrave;nh người, đến c&ugrave;ng c&oacute; đẹp trai hay kh&ocirc;ng, n&agrave;y c&oacute; thể quan hệ đến ng&agrave;y sau Thi&ecirc;n Cung đem muội đại sự!<br />\r\n<br />\r\nBất qu&aacute;, khi T&ocirc;n Thiệu &yacute; thức được ch&iacute;nh m&igrave;nh nhưng vẫn l&agrave; hầu th&acirc;n thời điểm, nhất thời sắc mặt kh&oacute; xem, &quot;Ta đi, ta đều được Y&ecirc;u vương, l&agrave;m sao vẫn c&aacute;i hầu th&acirc;n! C&aacute;i n&agrave;y kh&ocirc;ng khoa học!&quot;<br />\r\n<br />\r\nHay l&agrave; đột ph&aacute; đến cảnh giới tiếp theo, l&agrave; c&oacute; thể biến th&agrave;nh h&igrave;nh người đi, T&ocirc;n Thiệu cũng chỉ c&oacute; thể như thế an ủi m&igrave;nh.<br />\r\n<br />\r\nTừ nhẫn bạc b&ecirc;n trong lấy ra một c&aacute;i m&agrave;u đen da hổ b&agrave;o đổi, nh&igrave;n trước mắt kh&iacute; sắc kh&ocirc;i phục một ch&uacute;t thiếu nữ mặc &aacute;o trắng, T&ocirc;n Thiệu g&atilde;i g&atilde;i đầu, &aacute;y n&aacute;y n&oacute;i rằng, &quot;Đa tạ c&ocirc; nương &acirc;n cứu mạng.&quot;<br />\r\n<br />\r\n&quot;T&ecirc;n lừa đảo! N&oacute;i rồi y&ecirc;u đan l&agrave; mượn ngươi chữa thương, ngươi dĩ nhi&ecirc;n nuốt chửng ta y&ecirc;u lực! Ta cũng kh&ocirc;ng để &yacute; tới ngươi nữa!&quot;<br />\r\n<br />\r\nThiếu nữ tuy rằng kh&iacute; sắc kh&ocirc;i phục, bất qu&aacute; ph&aacute;p lực thật giống yếu hơi c&oacute; ch&uacute;t, tuy rằng điểm n&agrave;y đối với n&agrave;ng m&agrave; n&oacute;i b&eacute; nhỏ kh&ocirc;ng đ&aacute;ng kể, bất qu&aacute; bị gạt ch&iacute;nh l&agrave; bị gạt. Ch&iacute;nh m&igrave;nh hảo t&acirc;m hảo &yacute; bang T&ocirc;n Thiệu, T&ocirc;n Thiệu lại d&aacute;m gạt nàng, qu&aacute; gh&ecirc; tởm!<br />\r\n<br />\r\nNghĩ tới đ&acirc;y, thiếu nữ mặc &aacute;o trắng ngồi ngay ngắn ở đ&aacute;m m&acirc;y tr&ecirc;n, đưa lưng về ph&iacute;a T&ocirc;n Thiệu, tự m&igrave;nh mọc ra hờn dỗi.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><br />\r\n<br />\r\n&quot;Khái khái ho. . . Ta c&ograve;n tưởng rằng ngươi tr&aacute;ch ta h&ocirc;n ngươi đ&acirc;y, y&ecirc;u lực m&agrave;, bao lớn ch&uacute;t chuyện m&agrave;. . .&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu giờ khắc này l&uacute;ng t&uacute;ng da đầu đều phải c&agrave;o n&aacute;t, ngo&agrave;i miệng vẫn như cũ bồi cười, n&oacute;i hiện đại cổ đại c&aacute;c loại chuyện cười. Bất kể n&oacute;i thế n&agrave;o, thiếu nữ cứu hắn l&agrave; sự thực, m&agrave; hắn nuốt chửng thiếu nữ dược lực đột ph&aacute; Y&ecirc;u vương cảnh giới cũng l&agrave; sự thật.<br />\r\n<br />\r\nBất qu&aacute; thiếu nữ n&oacute;ng giận, t&iacute;nh kh&iacute; đ&uacute;ng l&agrave; rất quật, bất luận T&ocirc;n Thiệu l&agrave;m sao đ&ugrave;a, đều kh&ocirc;ng l&ecirc;n tiếng phản ứng.<br />\r\n<br />\r\nTự chuốc nhục nh&atilde; T&ocirc;n Thiệu, khoanh ch&acirc;n ngồi ở m&acirc;y m&ugrave; tr&ecirc;n, hai tay c&ugrave;ng vung, đi&ecirc;n cuồng hấp thu t&aacute;m cao vạn trượng trống kh&ocirc;ng cương phong.<br />\r\n<br />\r\nĐột ph&aacute; Y&ecirc;u vương ph&iacute;a sau, T&ocirc;n Thiệu hấp thu cương phong lực r&otilde; r&agrave;ng nhanh hơn mấy lần, hơn nữa ở hấp thu xong cương phong lực lượng sau, T&ocirc;n Thiệu mơ hồ cảm nhận được, c&aacute;i kia cương phong lực lượng b&ecirc;n trong, c&oacute; cực vi tiểu một tia sức mạnh tr&ocirc;i mất.<br />\r\n<br />\r\nLần thứ hai đưa tới một trận cuồng bạo cương phong, T&ocirc;n Thiệu mở ra Hỏa Nh&atilde;n Kim T&igrave;nh, nh&igrave;n trước mắt cương phong. Tăng l&ecirc;n tới Y&ecirc;u vương cảnh giới sau, T&ocirc;n Thiệu thị lực tăng l&ecirc;n nhiều gấp mấy lần, Hỏa Nh&atilde;n Kim T&igrave;nh uy lực tăng mạnh hạ, c&agrave;ng từ hiện ra bạch quang cương phong tr&ocirc;ng được đến một tia m&agrave;u đen sức mạnh.<br />\r\n<br />\r\nM&agrave; m&agrave;u đen kia sức mạnh, c&ugrave;ng đỉnh đầu m&agrave;u đen cương phong v&ograve;ng rất giống nhau.<br />\r\n<br />\r\nThử đem trận n&agrave;y cương phong h&uacute;t v&agrave;o trong cơ thể, T&ocirc;n Thiệu tỉ mỉ m&agrave; quan s&aacute;t đến c&aacute;i kia một tia m&agrave;u đen sức mạnh, T&ocirc;n Thiệu ph&aacute;t hiện, tại ch&iacute;nh m&igrave;nh thu nạp cương phong v&agrave;o cơ thể thời điểm, c&aacute;i kia tia m&agrave;u đen sức mạnh sẽ phi tốc chạy ra T&ocirc;n Thiệu trong mi&ecirc;̣ng, ti&ecirc;u tan ở trong kh&ocirc;ng kh&iacute;.<br />\r\n<br />\r\nN&agrave;y tia m&agrave;u đen sức mạnh, rốt cuộc c&aacute;i g&igrave;!<br />\r\n<br />\r\nLần thứ hai đưa tới một trận cương phong, T&ocirc;n Thiệu cẩn thận nuốt chửng, tất cả c&ugrave;ng trước kia kh&ocirc;ng kh&aacute;c nhau ch&uacute;t n&agrave;o, chỉ l&agrave; ở m&agrave;u đen sức mạnh sắp chạy ra trong miệng một khắc trước, T&ocirc;n Thiệu đem Hỏa Nh&atilde;n Kim T&igrave;nh sức mạnh th&ocirc;i th&uacute;c đến to lớn nhất, mắt nhanh nhanh miệng địa đem c&aacute;i kia tia m&agrave;u đen sức mạnh một c&aacute;i nuốt v&agrave;o.<br />\r\n<br />\r\nM&agrave; trong cơ thể cương phong sức mạnh, ở cắn nuốt mất c&aacute;i kia một tia m&agrave;u đen sức mạnh sau, c&agrave;ng bỗng dưng tăng trưởng một th&agrave;nh!<br />\r\n<br />\r\nPh&aacute;t hiện b&iacute; mật n&agrave;y, T&ocirc;n Thiệu nội t&acirc;m nếu n&oacute;i l&agrave; kh&ocirc;ng k&iacute;ch động, đ&oacute; l&agrave; tuyệt đối kh&ocirc;ng thể. Ch&iacute;nh m&igrave;nh ti&ecirc;u tốn ba năm rưỡi mới hấp thu được nhiều như vậy sức gi&oacute;, m&agrave; c&aacute;i kia một tia m&agrave;u đen sức mạnh, cũng đủ để tăng l&ecirc;n một th&agrave;nh sức gi&oacute;, như l&agrave; chỉ cần hấp thu m&agrave;u đen sức mạnh, T&ocirc;n Thiệu sức gi&oacute; n&ecirc;n tăng l&ecirc;n địa bao nhanh!<br />\r\n<br />\r\nHỏa Nh&atilde;n Kim T&igrave;nh nh&igrave;n ph&iacute;a c&aacute;ch đ&oacute; kh&ocirc;ng xa mấy c&aacute;i Thi&ecirc;n y&ecirc;u tiền bối, chỉ thấy mấy c&aacute;i Thi&ecirc;n y&ecirc;u ở nuốt chửng cương phong ph&iacute;a sau, m&agrave;u đen sức mạnh đều l&agrave; tr&ocirc;i đi, cũng kh&ocirc;ng c&oacute; bị th&ocirc;n phệ.<br />\r\n<br />\r\nB&iacute; mật n&agrave;y, tại chỗ chỉ c&oacute; T&ocirc;n Thiệu một người ph&aacute;t hiện!<br />\r\n<br />\r\nLần thứ hai t&aacute;ch ra một tia m&agrave;u đen sức mạnh, T&ocirc;n Thiệu kh&ocirc;ng c&oacute; đem nuốt chửng, ngược lại lấy y&ecirc;u lực đem cầm cố ở trong tay, lẫm lẫm liệt liệt đưa tới thiếu nữ m&ocirc;i một b&ecirc;n,<br />\r\n<br />\r\n&quot;Ầy, l&atilde;o T&ocirc;n nhưng là rất c&oacute; th&agrave;nh &yacute; m&agrave; xin lỗi, ngươi xem một ch&uacute;t c&aacute;i n&agrave;y c&oacute; đầy đủ hay kh&ocirc;ng bồi thường của ngươi y&ecirc;u đan tổn thất. . .&quot;<br />\r\n<br />\r\nNh&igrave;n T&ocirc;n Thiệu trong tay phổ th&ocirc;ng hết sức một đo&agrave;n y&ecirc;u lực, thiếu nữ lộ ra &aacute;nh mắt nghi hoặc, t&ograve; m&ograve;, do dự chậm r&atilde;i k&eacute;o ra miệng nhỏ, đem T&ocirc;n Thiệu l&ograve;ng b&agrave;n tay y&ecirc;u lực nuốt v&agrave;o.<br />\r\n<br />\r\nTrong nh&aacute;y mắt, thiếu nữ mắt to xinh đẹp, tr&agrave;n đầy vẻ kinh ngạc, bất khả tư nghị che miệng nhỏ,<br />\r\n<br />\r\n&quot;Ngươi, ngươi l&agrave;m sao sẽ nắm giữ Phong diệt lực lượng !&quot;<br />\r\n<br />\r\n(Ngộ Kh&ocirc;ng c&oacute; thể nh&igrave;n thấy lực lượng ph&aacute;p tắc, khoảng c&aacute;ch th&agrave;nh ti&ecirc;n c&ograve;n sẽ xa sao? )</p>', '0', 'to-gan-thieu-nu', 8, '77', '2022-08-03 05:54:23', '2022-08-03 05:54:23');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(44, '6', 'Bạch Phiên Tiên, kính chiếu yêu?', '<p>Phong diệt lực lượng l&agrave; c&aacute;i g&igrave;, T&ocirc;n Thiệu cũng kh&ocirc;ng quan t&acirc;m, hắn chỉ cần biết, n&agrave;y m&agrave;u đen sức gi&oacute; hết sức lợi hại đ&atilde; đủ rồi.<br />\r\n<br />\r\nNh&igrave;n thiếu nữ mặc &aacute;o trắng vẻ mặt kh&oacute; m&agrave; tin được, T&ocirc;n Thiệu chợt cảm thấy thở ph&agrave;o nhẹ nh&otilde;m, cũng c&ograve;n tốt, đối phương tựa hồ đối với n&agrave;y m&agrave;u đen sức gi&oacute; rất h&agrave;i l&ograve;ng, như vậy T&ocirc;n Thiệu liền c&oacute; biện ph&aacute;p bồi thường c&aacute;i n&agrave;y qu&aacute;i lạ thiếu nữ.<br />\r\n<br />\r\n&quot;Ngươi trước ăn ch&uacute;t chuối ti&ecirc;u xin bớt giận, chờ ta lại thu thập một &iacute;t m&agrave;u đen sức gi&oacute;, ph&acirc;n ngươi một &iacute;t. . .&quot;<br />\r\n<br />\r\nTừ nhẫn bạc b&ecirc;n trong lấy ra một &iacute;t chuối ti&ecirc;u, n&eacute;m tới thiếu nữ trong l&ograve;ng, lập tức T&ocirc;n Thiệu ngồi ở một b&ecirc;n, bắt đầu rồi kh&ocirc; khan m&agrave; rườm r&agrave; tu luyện.<br />\r\n<br />\r\n&quot;L&agrave; Phong diệt lực lượng, Phong diệt lực lượng c&oacute; được hay kh&ocirc;ng! C&ograve;n c&oacute;, ta l&agrave; Bạch Bằng, cũng kh&ocirc;ng phải hầu tử, l&agrave;m g&igrave; cho ta chuối ti&ecirc;u! Ta th&iacute;ch ăn c&aacute;, kh&ocirc;ng th&iacute;ch ăn chuối ti&ecirc;u!&quot; Nh&igrave;n một &ocirc;m ấp chuối ti&ecirc;u, thiếu nữ mặc &aacute;o trắng chu mỏ một c&aacute;i, bất qu&aacute; c&aacute;i bụng cũng bắt đầu &ugrave;ng ục &ugrave;ng ục k&ecirc;u, hết c&aacute;ch rồi, th&iacute;ch hợp ăn đi.</p>', '<p>Phong diệt lực lượng l&agrave; c&aacute;i g&igrave;, T&ocirc;n Thiệu cũng kh&ocirc;ng quan t&acirc;m, hắn chỉ cần biết, n&agrave;y m&agrave;u đen sức gi&oacute; hết sức lợi hại đ&atilde; đủ rồi.<br />\r\n<br />\r\nNh&igrave;n thiếu nữ mặc &aacute;o trắng vẻ mặt kh&oacute; m&agrave; tin được, T&ocirc;n Thiệu chợt cảm thấy thở ph&agrave;o nhẹ nh&otilde;m, cũng c&ograve;n tốt, đối phương tựa hồ đối với n&agrave;y m&agrave;u đen sức gi&oacute; rất h&agrave;i l&ograve;ng, như vậy T&ocirc;n Thiệu liền c&oacute; biện ph&aacute;p bồi thường c&aacute;i n&agrave;y qu&aacute;i lạ thiếu nữ.<br />\r\n<br />\r\n&quot;Ngươi trước ăn ch&uacute;t chuối ti&ecirc;u xin bớt giận, chờ ta lại thu thập một &iacute;t m&agrave;u đen sức gi&oacute;, ph&acirc;n ngươi một &iacute;t. . .&quot;<br />\r\n<br />\r\nTừ nhẫn bạc b&ecirc;n trong lấy ra một &iacute;t chuối ti&ecirc;u, n&eacute;m tới thiếu nữ trong l&ograve;ng, lập tức T&ocirc;n Thiệu ngồi ở một b&ecirc;n, bắt đầu rồi kh&ocirc; khan m&agrave; rườm r&agrave; tu luyện.<br />\r\n<br />\r\n&quot;L&agrave; Phong diệt lực lượng, Phong diệt lực lượng c&oacute; được hay kh&ocirc;ng! C&ograve;n c&oacute;, ta l&agrave; Bạch Bằng, cũng kh&ocirc;ng phải hầu tử, l&agrave;m g&igrave; cho ta chuối ti&ecirc;u! Ta th&iacute;ch ăn c&aacute;, kh&ocirc;ng th&iacute;ch ăn chuối ti&ecirc;u!&quot; Nh&igrave;n một &ocirc;m ấp chuối ti&ecirc;u, thiếu nữ mặc &aacute;o trắng chu mỏ một c&aacute;i, bất qu&aacute; c&aacute;i bụng cũng bắt đầu &ugrave;ng ục &ugrave;ng ục k&ecirc;u, hết c&aacute;ch rồi, th&iacute;ch hợp ăn đi.<br />\r\n<br />\r\nCho tới T&ocirc;n Thiệu, hắn đối với n&agrave;y gặp mặt mới một hồi thiếu nữ mặc &aacute;o trắng, ngoại trừ &aacute;y n&aacute;y c&ugrave;ng cảm tạ, thật kh&ocirc;ng c&oacute; c&aacute;i kh&aacute;c cảm t&igrave;nh.<br />\r\n<br />\r\nL&atilde;o T&ocirc;n quản ngươi th&iacute;ch ăn c&aacute;i g&igrave;, y&ecirc;u c&oacute; ăn hay kh&ocirc;ng. L&atilde;o T&ocirc;n c&ograve;n bận hơn hấp thu sức gi&oacute; đề cao tu vi đ&acirc;y!<br />\r\n<br />\r\nKhởi đầu mấy th&aacute;ng, T&ocirc;n Thiệu phải hao ph&iacute; nửa n&eacute;n hương tr&aacute;i phải, mới c&oacute; thể từ Cương phong b&ecirc;n trong lấy ra một tia Phong diệt lực lượng. M&agrave; hắn ch&iacute;nh l&agrave; ch&iacute;nh m&igrave;nh nuốt chửng v&agrave;i tia, ph&acirc;n cho thiếu nữ v&agrave;i tia.<br />\r\n<br />\r\nN&agrave;y để thiếu nữ đối với T&ocirc;n Thiệu bất m&atilde;n, tan th&agrave;nh m&acirc;y kh&oacute;i. Bất qu&aacute; T&ocirc;n Thiệu cũng y&ecirc;u cầu thiếu nữ, kh&ocirc;ng thể đem b&iacute; mật n&agrave;y n&oacute;i ra, buồn bực tiếng mới c&oacute; thể gi&agrave;u to kh&ocirc;ng phải? Như l&agrave; để Nhị Lang Thần c&ugrave;ng Bằng Ma Vương biết b&iacute; mật n&agrave;y, ch&iacute;nh m&igrave;nh c&ograve;n c&oacute; muốn hay kh&ocirc;ng tu luyện. E sợ đừng n&oacute;i tu luyện, c&oacute; thể giữ được hay kh&ocirc;ng t&iacute;nh mạng cũng l&agrave; c&aacute;i vấn đề.<br />\r\n<br />\r\nThất phu v&ocirc; tội, mang ngọc mắc tội, đạo l&yacute; n&agrave;y, quen thuộc c&aacute;c loại tiểu thuyết T&ocirc;n Thiệu l&agrave; hiểu.<br />\r\n<br />\r\nNửa năm ph&iacute;a sau, T&ocirc;n Thiệu trong cơ thể sức gi&oacute;, xảy ra lần thứ ba chuyển biến. Mới đầu l&agrave; từ v&ocirc; h&igrave;nh Thi&ecirc;n Phong biến th&agrave;nh m&agrave;u trắng Cương phong, m&agrave; b&acirc;y giờ, trong cơ thể Cương phong lực lượng đ&atilde; h&oacute;a th&agrave;nh đen thui vẻ. Giờ khắc này T&ocirc;n Thiệu phất tay liền c&oacute; thể ngưng tụ ra một tia Phong diệt lực lượng, nhưng m&agrave; đồng thời T&ocirc;n Thiệu cũng ph&aacute;t hiện, m&igrave;nh sức gi&oacute; đ&atilde; kh&ocirc;ng c&aacute;ch n&agrave;o nữa tăng cường.<br />\r\n<br />\r\nNgưng luyện m&agrave;u đen sức gi&oacute;, kh&ocirc;ng c&aacute;ch n&agrave;o hấp thu, T&ocirc;n Thiệu lại tốn thời gian nửa năm, đem m&agrave;u đen sức gi&oacute; ngưng tụ th&agrave;nh từng vi&ecirc;n một m&agrave;u đen vi&ecirc;n ch&acirc;u, cũng v&igrave; gọi l&agrave; &quot;Phong Diệt Ch&acirc;u&quot; .<br />\r\n<br />\r\nTừng c&aacute;i Phong Diệt Ch&acirc;u b&ecirc;n trong, đều ẩn chứa cực kỳ kinh khủng uy lực, y theo T&ocirc;n Thiệu phỏng chừng, c&aacute;ch đ&oacute; kh&ocirc;ng xa mấy vị kia Thi&ecirc;n y&ecirc;u tiền bối, cũng kh&ocirc;ng c&oacute; thể chịu được một vi&ecirc;n Phong Diệt Ch&acirc;u uy lực.<br />\r\n<br />\r\nGiờ khắc này đỉnh đầu m&agrave;u đen Cương phong v&ograve;ng, đ&atilde; kề b&ecirc;n ti&ecirc;u tan, d&ugrave; sao n&agrave;y Cương Phong Chi Nh&atilde;n tồn tại thời gian năm năm, cũng gần như phải biến mất.<br />\r\n<br />\r\nNh&igrave;n nhẫn bạc nằm mấy chục vi&ecirc;n Phong Diệt Ch&acirc;u, T&ocirc;n Thiệu lộ ra vẻ h&agrave;i l&ograve;ng, suy nghĩ c&oacute; phải l&agrave; n&ecirc;n Hoa Quả Sơn. M&agrave; v&agrave;o l&uacute;c n&agrave;y, một c&aacute;i ho&agrave;ng b&agrave;o t&oacute;c v&agrave;ng trung ni&ecirc;n đại h&aacute;n, kim quang l&oacute;e l&ecirc;n, xuất hiện ở T&ocirc;n Thiệu c&ugrave;ng thiếu nữ mặc &aacute;o trắng trước mặt, th&ocirc; cuồng tr&ecirc;n mặt s&uacute;c r&acirc;u ngắn, d&ugrave;ng kh&ocirc;ng cho cự tuyệt khẩu kh&iacute; n&oacute;i rằng,<br />\r\n<br />\r\n&quot;Phi&ecirc;n Ti&ecirc;n, cần phải đi!&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu trong l&ograve;ng suy nghĩ, nguy&ecirc;n lai thiếu nữ mặc &aacute;o trắng n&agrave;y, t&ecirc;n l&agrave; Phi&ecirc;n Ti&ecirc;n. C&aacute;i n&agrave;y t&oacute;c v&agrave;ng đại h&aacute;n, phỏng chừng ch&iacute;nh l&agrave; thiếu nữ ca ca Bằng Ma Vương đi.<br />\r\n<br />\r\n&quot;Ồ. . .&quot; T&ecirc;n l&agrave; Phi&ecirc;n Ti&ecirc;n thiếu nữ mặc &aacute;o trắng, tựa hồ c&oacute; hơi kh&ocirc;ng nỡ đi, trở lại đầu, đối với T&ocirc;n Thiệu chớp chớp mắt to, &quot;C&aacute;i kia, khỉ nhỏ ca, ta liền đi trước. . . Sau đ&oacute; c&oacute; cơ hội, nhớ đến C&ocirc;n Bằng Hải xem ta. . . Nhớ mang c&aacute; tươi, kh&ocirc;ng muốn mang chuối ti&ecirc;u. . .&quot;<br />\r\n<br />\r\nM&agrave; Bằng Ma Vương, th&igrave; lại kh&aacute; l&agrave; kh&ocirc;ng ki&ecirc;n nhẫn liếc mắt một c&aacute;i T&ocirc;n Thiệu, &quot;Tiểu tử, c&oacute; thể đến n&agrave;y t&aacute;m cao vạn trượng độ, c&ograve;n c&oacute; thể trong v&ograve;ng một năm đột ph&aacute; Y&ecirc;u vương, bản Th&aacute;nh kh&ocirc;ng thể kh&ocirc;ng n&oacute;i, tiểu tử ngươi rất c&oacute; tu h&agrave;nh tiềm lực. Ngươi tới C&ocirc;n Bằng Hải, bản Th&aacute;nh hoan ngh&ecirc;nh, bất qu&aacute; ngươi nếu muốn đ&aacute;nh muội muội ta chủ &yacute;, hoặc l&agrave; d&aacute;m chạm nàng một căn chỉ đầu, bản Th&aacute;nh kh&ocirc;ng ngại ăn sống rồi ngươi!&quot;<br />\r\n<br />\r\nDứt lời, Bằng Ma Vương trong tay cuốn l&ecirc;n một vệt kim quang, c&ugrave;ng thiếu nữ mặc &aacute;o trắng h&oacute;a th&agrave;nh kim quang nhắm hướng đ&ocirc;ng mặt bay đi.<br />\r\n<br />\r\n&quot;Phi, l&atilde;o T&ocirc;n kh&ocirc;ng c&oacute; chuyện l&agrave;m m&agrave; đ&aacute;nh em g&aacute;i ngươi chủ &yacute;, l&atilde;o T&ocirc;n sẽ kh&ocirc;ng động nàng một căn chỉ đầu. . .&quot; Ở Bằng Ma Vương rời đi ph&iacute;a sau, T&ocirc;n Thiệu tức giận mắng một c&acirc;u, lập tức nghĩ đến, ch&iacute;nh m&igrave;nh trước nhưng là c&ugrave;ng thiếu nữ mặc &aacute;o trắng c&oacute; m&ocirc;i tiếp x&uacute;c, n&agrave;y thật giống đ&atilde; kh&ocirc;ng phải l&agrave; đụng đụng tay đầu ng&oacute;n tay tr&igrave;nh độ.<br />\r\nThiếu nữ mặc &aacute;o trắng hay l&agrave; kh&ocirc;ng hiểu sự đời,<br />\r\n<br />\r\nKh&ocirc;ng hiểu được h&ocirc;n h&agrave;m nghĩa, nhưng là Bằng Ma Vương hiểu a! Như l&agrave; để Bằng Ma Vương biết, m&igrave;nh c&ugrave;ng muội muội của hắn &quot;H&ocirc;n m&ocirc;i&quot; hai lần, chỉ hắn c&oacute; g&igrave; m&agrave; sợ nguy&ecirc;n nh&acirc;n cũng sẽ kh&ocirc;ng hỏi, trực tiếp lại bi&ecirc;́t bay trở về, đem ch&iacute;nh m&igrave;nh đ&aacute;nh th&agrave;nh tro!<br />\r\n<br />\r\n&quot;Khái khái ho. . . L&atilde;o T&ocirc;n c&ograve;n kh&ocirc;ng c&oacute; c&ugrave;ng hắn kết l&agrave;m nghĩa huynh đệ, b&acirc;y giờ bị hắn đ&aacute;nh giết, vậy cũng qu&aacute; oan uổng. . . Vẫn l&agrave; đi trước một bước đi. . .&quot;<br />\r\n<br />\r\nDường như T&ocirc;n Thiệu dự liệu, mấy ng&agrave;y ph&iacute;a sau, Bằng Ma Vương quả nhi&ecirc;n nổi giận đ&ugrave;ng đ&ugrave;ng nơi n&agrave;y, la h&eacute;t phải đem một con y&ecirc;u hầu ch&eacute;m th&agrave;nh mu&ocirc;n mảnh. M&agrave; cũng trong l&uacute;c đ&oacute;, to&agrave;n bộ Đ&ocirc;ng Thắng Thần Ch&acirc;u v&ocirc; số y&ecirc;u động đều biết một c&aacute;i tin.<br />\r\n<br />\r\nDĩ nhi&ecirc;n c&oacute; một con Y&ecirc;u vương cấp bậc hầu y&ecirc;u, khinh bạc &quot;Hỗn Thi&ecirc;n Y&ecirc;u Th&aacute;nh&quot; Bằng Ma Vương em g&aacute;i ruột, n&agrave;y hầu y&ecirc;u hẳn l&agrave; kh&ocirc;ng muốn sống nữa kh&ocirc;ng th&agrave;nh!<br />\r\n<br />\r\nĐương nhi&ecirc;n đ&oacute; l&agrave; n&oacute;i sau, khi đ&oacute; T&ocirc;n Thiệu đ&atilde; sớm Hoa Quả Sơn, ung dung tự tại đi tới.<br />\r\n<br />\r\nỞ T&ocirc;n Thiệu sau khi rời đi kh&ocirc;ng l&acirc;u, m&agrave;u đen Cương phong v&ograve;ng &quot;Ầm ầm&quot; một tiếng, muốn nổ tung l&ecirc;n, ở tr&ecirc;n trời 90 ng&agrave;n trượng, cả người t&aacute;m trảo Long văn ho&agrave;ng b&agrave;o, người mặc kh&oacute;a bạc gi&aacute;p thanh ni&ecirc;n, ngạo nghễ lập ở trong cơn b&atilde;o t&aacute;p. Thanh ni&ecirc;n tướng mạo đường đường, uy nghi bất ph&agrave;m, ở mi t&acirc;m của hắn, c&agrave;ng mọc ra vi&ecirc;n thứ ba mắt dọc, m&agrave; c&aacute;i kia dựng thẳng trong mắt, đầy rẫy từng tia từng tia m&agrave;u đen sức gi&oacute;, những n&agrave;y sức gi&oacute; mặc dù so sánh lại T&ocirc;n Thiệu hấp thu &iacute;t hơn rất nhiều, nhưng vẫn cứ để thanh ni&ecirc;n sau lưng sáu c&aacute;i đại h&aacute;n k&iacute;nh nể kh&ocirc;ng th&ocirc;i,<br />\r\n<br />\r\n&quot;Ch&uacute;c mừng chủ nh&acirc;n Th&ocirc;ng Thi&ecirc;n Ph&aacute;p Nh&atilde;n tu luyện th&agrave;nh c&ocirc;ng! Chủ nh&acirc;n c&agrave;ng c&oacute; thể mượn Thi&ecirc;n Nh&atilde;n hấp thu Phong diệt lực lượng, tuyệt đối l&agrave; trong Thi&ecirc;n Đ&igrave;nh duy nhất người!&quot;<br />\r\n<br />\r\n&quot;Th&agrave;nh c&ocirc;ng đ&uacute;ng l&agrave; th&agrave;nh c&ocirc;ng, đ&aacute;ng tiếc, d&ugrave; sao cũng l&agrave; Hậu Thi&ecirc;n Ph&aacute;p Mục, tr&igrave;nh độ như thế n&agrave;y đ&atilde; l&agrave; cực hạn. Th&ocirc;i, ch&uacute;ng ta b&ecirc;n n&agrave;y Qu&aacute;n Giang Khẩu đi. C&ocirc;ng L&acirc;n, trong mắt ngươi tr&agrave;n đầy s&aacute;t kh&iacute;, v&igrave; chuyện g&igrave;?&quot;<br />\r\n<br />\r\nNg&acirc;n gi&aacute;p thanh ni&ecirc;n mi t&acirc;m l&oacute;e l&ecirc;n &aacute;nh bạc, đem thứ ba mắt thu hồi, mặt quang lạnh l&ugrave;ng nghi&ecirc;m nghị nh&igrave;n ph&iacute;a trước người hắc gi&aacute;p nam tử.<br />\r\n<br />\r\nN&agrave;y hắc gi&aacute;p nam tử th&ecirc;u rắn rết đồ &aacute;n, ủng chiến tr&ecirc;n th&ecirc;u rết đồ đằng, sắc mặt c&oacute; ch&uacute;t trắng x&aacute;m, đang l&agrave; trước kia bị Bằng Ma Vương một trảo đ&aacute;nh bị thương Mai Sơn Lục huynh đệ một trong, Di&ecirc;u C&ocirc;ng L&acirc;n! Giờ khắc này Di&ecirc;u C&ocirc;ng L&acirc;n trong mắt, s&aacute;t &yacute; lộ, đừng n&oacute;i ng&acirc;n gi&aacute;p thanh ni&ecirc;n, ch&iacute;nh l&agrave; Di&ecirc;u C&ocirc;ng L&acirc;n b&ecirc;n cạnh c&aacute;i kh&aacute;c Mai Sơn năm hữu, đều nh&igrave;n ra, dồn dập hỏi, &quot;Di&ecirc;u tứ ca (di&ecirc;u tứ đệ), ngươi chẳng lẽ vẫn c&ograve;n ở căm ghét Bằng Ma Vương c&aacute;i kia một trảo kh&ocirc;ng th&agrave;nh! Ai, Bằng Ma Vương c&oacute; Y&ecirc;u Th&aacute;nh tu vi, ch&iacute;nh l&agrave; Ch&acirc;n qu&acirc;n, đều kh&ocirc;ng thể dễ d&agrave;ng thắng hắn. Ngươi cắt kh&ocirc;ng thể hướng về hắn trả th&ugrave;, tự t&igrave;m đường chết!&quot;<br />\r\n<br />\r\nNghe xong c&aacute;i kh&aacute;c Mai Sơn năm hữu, Di&ecirc;u C&ocirc;ng L&acirc;n sắc mặt cực kỳ kh&oacute; coi, cắn răng nghiến lợi n&oacute;i rằng, &quot;C&aacute;c ca ca y&ecirc;n t&acirc;m, C&ocirc;ng L&acirc;n chỉ l&agrave; Hợp Đạo kỳ tu vi, tự biết kh&ocirc;ng phải Bằng Ma Vương Y&ecirc;u Th&aacute;nh cấp bậc đối thủ, bất qu&aacute; c&aacute;i kia khỉ nhỏ y&ecirc;u, ta tuyệt kh&ocirc;ng bu&ocirc;ng tha hắn, như kh&ocirc;ng l&ecirc;n hắn, ta l&agrave;m sao bị Bằng Ma Vương k&iacute;ch thương! C&aacute;c ca ca cũng kh&ocirc;ng cần cản ta!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng sao, nhanh đi nhanh về, chỉ l&agrave; một c&aacute;i tiểu y&ecirc;u, kh&ocirc;ng phải hao ph&iacute; qu&aacute; l&acirc;u thời gian. . .&quot; Ng&acirc;n gi&aacute;p thanh ni&ecirc;n đối với Di&ecirc;u C&ocirc;ng L&acirc;n gật gật đầu, đối với Mai Sơn năm hữu chỉ tay, nhấc l&ecirc;n một vệt kim quang, cuốn lấy mọi người tại chỗ biến mất.<br />\r\n<br />\r\nĐối với T&ocirc;n Thiệu chết sống, ng&acirc;n gi&aacute;p thanh ni&ecirc;n căn bản kh&ocirc;ng c&oacute; để ở trong l&ograve;ng, hoặc c&oacute; lẽ l&agrave;, c&otilde;i đời n&agrave;y, trừ hắn ra muội muội ở ngo&agrave;i, lại kh&ocirc;ng một người đ&aacute;ng gi&aacute; hắn quan t&acirc;m.<br />\r\n<br />\r\nĐưa mắt nh&igrave;n ng&acirc;n gi&aacute;p thanh ni&ecirc;n rời đi, Di&ecirc;u C&ocirc;ng L&acirc;n thu hồi lửa giận tr&ecirc;n mặt, quay về T&ocirc;n Thiệu rời đi phương hướng cười lạnh một tiếng,<br />\r\n<br />\r\n&quot;Kh&agrave; kh&agrave;, nguy&ecirc;n bản chỉ l&agrave; muốn giết ngươi cho hả giận, mới ở tr&ecirc;n th&acirc;n thể ngươi lưu lại một đ&aacute;nh dấu, kh&ocirc;ng nghĩ tới, ngươi dĩ nhi&ecirc;n hiểu được tinh luyện Phong diệt lực lượng! Như l&agrave; để ta thu được c&aacute;i n&agrave;o Phong Diệt Ch&acirc;u tử, nhất định c&oacute; thể đột ph&aacute; Hợp Đạo kỳ, tiến v&agrave;o đại thừa, sau đ&oacute; độ kiếp th&agrave;nh ti&ecirc;n!&quot;<br />\r\n<br />\r\nNghe hắn, n&agrave;y Di&ecirc;u C&ocirc;ng L&acirc;n dĩ nhi&ecirc;n thẳng đến lưu &yacute; T&ocirc;n Thiệu nhất cử nhất động, cũng ph&aacute;t hiện T&ocirc;n Thiệu tinh luyện Phong diệt lực tin tức kinh người!<br />\r\n<br />\r\nChỉ l&agrave; tin tức n&agrave;y, hắn cũng kh&ocirc;ng c&oacute; n&oacute;i cho Nhị Lang Thần c&ugrave;ng với kh&aacute;c Mai Sơn Ngũ huynh đệ, xem t&igrave;nh h&igrave;nh, dĩ nhi&ecirc;n l&agrave; muốn nuốt một m&igrave;nh khoản n&agrave;y Phong Diệt Ch&acirc;u!<br />\r\n<br />\r\nCuồng phong cuốn một c&aacute;i, Di&ecirc;u C&ocirc;ng L&acirc;n hướng về T&ocirc;n Thiệu rời đi phương hướng nhanh ch&oacute;ng đuổi theo.<br />\r\n<br />\r\nLấy T&ocirc;n Thiệu b&acirc;y giờ Y&ecirc;u vương tu vi, muốn muốn phi h&agrave;nh ngàn dặm khoảng c&aacute;ch trở lại Hoa Quả Sơn, chỉ cần nửa ng&agrave;y liền c&oacute; thể, bất qu&aacute; T&ocirc;n Thiệu cũng kh&ocirc;ng c&oacute; lập tức trở lại Hoa Quả Sơn, m&agrave; l&agrave; quẹo tr&aacute;i quẹo phải địa ở tr&ecirc;n mặt biển gạt xoay chuyển hai ng&agrave;y.<br />\r\n<br />\r\nNgưng tụ Phong Diệt Ch&acirc;u, l&agrave; ở mấy c&aacute;i Thi&ecirc;n y&ecirc;u dưới m&iacute; mắt tiến h&agrave;nh, T&ocirc;n Thiệu v&igrave; lấy ph&ograve;ng ngừa vạn nhất, cũng kh&ocirc;ng c&oacute; lập tức trở lại sào huy&ecirc;̣t Hoa Quả Sơn, m&agrave; l&agrave; v&igrave; lấy ph&ograve;ng ngừa vạn nhất, ở tr&ecirc;n biển Đ&ocirc;ng gạt nổi l&ecirc;n v&ograve;ng tr&ograve;n.<br />\r\n<br />\r\nT&ocirc;n Thiệu kh&ocirc;ng c&oacute; nghĩ tới l&agrave;, những Thi&ecirc;n y&ecirc;u kia e ngại Bằng Ma Vương mặt mũi của, kh&ocirc;ng c&oacute; truy s&aacute;t T&ocirc;n Thiệu đoạt bảo, ngược lại l&agrave; Mai Sơn s&aacute;u hữu một trong Di&ecirc;u C&ocirc;ng L&acirc;n, lặng lẽ đuổi tới.<br />\r\n<br />\r\nT&ocirc;n Thiệu hấp thu Phong diệt lực lượng, cương quyết tốc độ c&ugrave;ng Di&ecirc;u C&ocirc;ng L&acirc;n đ&atilde; kh&ocirc;ng ph&acirc;n cao thấp, hai ng&agrave;y truy đuổi, Di&ecirc;u C&ocirc;ng L&acirc;n dĩ nhi&ecirc;n kh&ocirc;ng đuổi kịp T&ocirc;n Thiệu, kinh ngạc sau khi, c&agrave;ng là ki&ecirc;n quyết giết chết T&ocirc;n Thiệu t&acirc;m tư.<br />\r\n<br />\r\nĐ&ugrave;a giỡn, T&ocirc;n Thiệu chỉ l&agrave; một c&aacute;i tiểu y&ecirc;u, hấp thu một &iacute;t Phong diệt lực lượng l&agrave; c&oacute; thể so với m&igrave;nh bay c&agrave;ng nhanh hơn, nếu l&agrave; m&igrave;nh hấp thu Phong diệt lực lượng, chỉ sợ Nhị Lang Ch&acirc;n qu&acirc;n đều kh&ocirc;ng phải l&agrave; m&igrave;nh đối thủ.<br />\r\n<br />\r\nTham niệm đồng thời, Di&ecirc;u C&ocirc;ng L&acirc;n ra sức hơn truy đuổi T&ocirc;n Thiệu, m&agrave; trời xanh kh&ocirc;ng phụ l&ograve;ng người, ở thứ ba ng&agrave;y thời điểm, Di&ecirc;u C&ocirc;ng L&acirc;n rốt cục đuổi kịp T&ocirc;n Thiệu , c&ograve;n nguy&ecirc;n nh&acirc;n, l&agrave; bởi v&igrave; T&ocirc;n Thiệu ph&aacute;p lực ti&ecirc;u hao gần đủ rồi, bay bất động.<br />\r\n<br />\r\nT&ocirc;n Thiệu l&agrave; Y&ecirc;u vương cảnh giới, ở v&agrave;o tu y&ecirc;u cảnh giới thứ tư, m&agrave; Di&ecirc;u C&ocirc;ng L&acirc;n ở v&agrave;o Hợp Đạo kỳ, ở v&agrave;o tu ch&acirc;n cảnh giới thứ bảy. T&ocirc;n Thiệu ph&aacute;p lực tự nhi&ecirc;n kh&ocirc;ng đuổi kịp Di&ecirc;u C&ocirc;ng L&acirc;n chất ph&aacute;c.<br />\r\n<br />\r\nĐ&aacute;nh gi&aacute; ngăn cản ở trước người hắc gi&aacute;p nam tử, T&ocirc;n Thiệu sắc mặt ch&igrave;m xuống, &quot;L&agrave; ngươi! Mai Sơn s&aacute;u hữu, Di&ecirc;u C&ocirc;ng L&acirc;n!&quot;<br />\r\n<br />\r\n&quot;Ồ? Ngươi nhận ra ta? Vậy th&igrave; đơn giản hơn nhiều, đem Phong Diệt Ch&acirc;u gọi ra, ta lưu ngươi một c&aacute;i to&agrave;n th&acirc;y!&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu nhận kh&ocirc;ng ra n&agrave;y hắc gi&aacute;p đại h&aacute;n diện mạo, lại nhận được c&aacute;i kia ủng chiến, ủng chiến b&ecirc;n tr&ecirc;n, th&ecirc;u rất kh&aacute;c biệt rết đồ &aacute;n, m&agrave; n&agrave;y hắc gi&aacute;p đại h&aacute;n &acirc;m thanh, &acirc;m trầm như x&agrave; hạt, r&otilde; r&agrave;ng c&ugrave;ng trước kia &acirc;m thanh kia giống như đ&uacute;c.<br />\r\n<br />\r\nNhư c&ograve;n kh&ocirc;ng biết trước người người l&agrave; Mai Sơn s&aacute;u hữu Di&ecirc;u C&ocirc;ng L&acirc;n, T&ocirc;n Thiệu cũng l&agrave; qu&aacute; cho&aacute;ng v&aacute;ng.<br />\r\n<br />\r\nSử dụng tới Hỏa Nh&atilde;n Kim T&igrave;nh, T&ocirc;n Thiệu nh&igrave;n ng&oacute; Phương Vi&ecirc;n hai phạm vi trăm dặm, lập tức ch&acirc;m chọc nh&igrave;n Di&ecirc;u C&ocirc;ng L&acirc;n, &quot;L&agrave;m sao, Mai Sơn s&aacute;u hữu, cũng chỉ đến rồi ngươi một c&aacute;i? C&ograve;n l&agrave; n&oacute;i, ngươi muốn nuốt một m&igrave;nh ta Phong Diệt Ch&acirc;u, ngươi sẽ kh&ocirc;ng sợ Nhị Lang Thần bọn họ biết kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;Hừ, chỉ l&agrave; một c&aacute;i đầu khỉ tiểu y&ecirc;u, ta giơ tay cũng c&oacute; thể diệt giết, h&agrave; tất để những người kh&aacute;c huynh trưởng ra tay . C&ograve;n n&oacute;i độc chiếm, kh&agrave; kh&agrave;, giết ngươi, ai sẽ biết ta độc th&ocirc;n Phong Diệt Ch&acirc;u!&quot;<br />\r\n<br />\r\nDứt lời, Di&ecirc;u C&ocirc;ng L&acirc;n quay về T&ocirc;n Thiệu chỉ tay, trong tay &aacute;o, c&agrave;ng bay ra mấy trăm con x&agrave;, hạt, rết, mỗi một con độc vật đều c&oacute; Y&ecirc;u vương cấp bậc tu vi, m&agrave; như trúng r&ocirc;̀i những độc vật n&agrave;y độc, mặc d&ugrave; l&agrave; Y&ecirc;u vương trở l&ecirc;n Y&ecirc;u qu&acirc;n, đều phải &ocirc;m nỗi hận m&agrave; chết!<br />\r\n<br />\r\nDi&ecirc;u C&ocirc;ng L&acirc;n kh&ocirc;ng c&oacute; n&oacute;i l&aacute;o, lấy bản l&atilde;nh của hắn, mặc d&ugrave; đến th&ecirc;m một trăm c&aacute;i T&ocirc;n Thiệu, cũng c&oacute; thể dễ d&agrave;ng b&oacute;p chết.<br />\r\n<br />\r\n&quot;Nếu chỉ c&oacute; ngươi một c&aacute;i, vậy ta an t&acirc;m. . .&quot; Đối mặt đầy trời độc vật, T&ocirc;n Thiệu kh&ocirc;ng c&oacute; sợ h&atilde;i ch&uacute;t n&agrave;o, thu hồi nụ cười, trong &aacute;nh mắt lửa giận ngất trời, lấy ra một đem Phong Diệt Ch&acirc;u, hướng về Di&ecirc;u C&ocirc;ng L&acirc;n một đem quăng đến, &quot;Ngươi kh&ocirc;ng phải muốn l&atilde;o T&ocirc;n Phong Diệt Ch&acirc;u sao, như ngươi mong muốn!&quot;<br />\r\n<br />\r\nGặp T&ocirc;n Thiệu b&igrave;nh tĩnh d&aacute;ng dấp, Di&ecirc;u C&ocirc;ng L&acirc;n trong l&ograve;ng cảm gi&aacute;c nặng nề, mơ hồ cảm gi&aacute;c thấy hơi kh&ocirc;ng ổn, m&agrave; nghe được T&ocirc;n Thiệu ng&ocirc;n ngữ, Di&ecirc;u C&ocirc;ng L&acirc;n liền lại c&agrave;ng kỳ qu&aacute;i. C&aacute;i g&igrave; gọi l&agrave; chỉ c&oacute; ta một c&aacute;i, ngươi an t&acirc;m? Chỉ c&oacute; ta một c&aacute;i Thi&ecirc;n Tướng, ngươi kh&ocirc;ng phải n&ecirc;n nguy hiểm sao?<br />\r\n<br />\r\nTiện tay, Di&ecirc;u C&ocirc;ng L&acirc;n tựa hồ nh&igrave;n thấy T&ocirc;n Thiệu đ&agrave;o rơi ra c&aacute;i g&igrave; vậy, c&aacute;ch qu&aacute; xa, nh&igrave;n kh&ocirc;ng r&otilde; lắm. M&agrave; sau một khắc, ch&iacute;nh m&igrave;nh liền bị mấy ng&agrave;n trượng m&agrave;u đen b&atilde;o t&aacute;p bao v&acirc;y, &yacute; thức vĩnh viễn dừng lại ở thời khắc cuối c&ugrave;ng,<br />\r\n<br />\r\n&quot;H&aacute;, nguy&ecirc;n lai hắn m&oacute;c ra ch&iacute;nh l&agrave; Phong Diệt Ch&acirc;u a. . .&quot;<br />\r\n<br />\r\nMười mấy vi&ecirc;n Phong Diệt Ch&acirc;u, cơ hồ l&agrave; T&ocirc;n Thiệu trong tay một phần ba c&ograve;n nhiều. Một vi&ecirc;n Phong Diệt Ch&acirc;u, liền đầy đủ giết chết tu ch&acirc;n cảnh giới thứ s&aacute;u cường giả, ở n&agrave;y mười mấy vi&ecirc;n Phong Diệt Ch&acirc;u khủng bố sức gi&oacute; hạ, Di&ecirc;u C&ocirc;ng L&acirc;n bất qu&aacute; chớp mắt thời gian, liền bị n&agrave;y m&agrave;u đen b&atilde;o t&aacute;p nuốt chửng.<br />\r\n<br />\r\nN&agrave;y m&agrave;u đen b&atilde;o t&aacute;p, so với Cương Phong Chi Nh&atilde;n trung t&acirc;m b&atilde;o t&aacute;p đều mạnh hơn tr&ecirc;n nhiều lắm!</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><br />\r\nB&atilde;o t&aacute;p ph&iacute;a sau, đừng n&oacute;i l&agrave; Di&ecirc;u C&ocirc;ng L&acirc;n h&agrave;i cốt, liền ngay cả Di&ecirc;u C&ocirc;ng L&acirc;n kh&ocirc;ng gian nhẫn cũng kh&ocirc;ng c&oacute; lưu giữ.<br />\r\n<br />\r\nTr&ecirc;n bầu trời, duy c&oacute; một t&aacute;m sừng đồng thau k&iacute;nh, nhẹ nh&agrave;ng tr&ocirc;i nổi.<br />\r\n<br />\r\nMột tay cuốn l&ecirc;n một trận cuồng phong, đem xa xa đồng thau k&iacute;nh thu v&agrave;o trong l&ograve;ng b&agrave;n tay, T&ocirc;n Thiệu nh&igrave;n trong gương, m&igrave;nh vi&ecirc;n hầu h&igrave;nh ảnh, hơi run run,<br />\r\n<br />\r\n&quot;Đ&acirc;y l&agrave;, k&iacute;nh chiếu y&ecirc;u? Nhị Lang Thần ph&aacute;p bảo k&iacute;nh chiếu y&ecirc;u? !&quot;<br />\r\n<br />\r\nM&agrave; cũng trong l&uacute;c đ&oacute;, Qu&aacute;n Giang Khẩu b&ecirc;n tr&ecirc;n, Nhị Lang Ch&acirc;n qu&acirc;n Ti&ecirc;n phủ b&ecirc;n trong, một c&aacute;i Hắc gi&aacute;p qu&acirc;n h&aacute;n run rẩy quỳ tr&ecirc;n mặt đất, tr&ecirc;n tay cao giơ cao một bản Ti&ecirc;n b&ugrave;a ch&uacute;,<br />\r\n<br />\r\n&quot;Nhị gia, kh&ocirc;ng. . . Kh&ocirc;ng xong. . . Di&ecirc;u C&ocirc;ng L&acirc;n Đại tướng qu&acirc;n t&ecirc;n, biến mất rồi!&quot;<br />\r\n<br />\r\nKh&ocirc;ng đến lượt qu&acirc;n h&aacute;n kh&ocirc;ng sợ, ti&ecirc;n b&ugrave;a ch&uacute; b&ecirc;n tr&ecirc;n họ t&ecirc;n biến mất, người n&agrave;y kh&ocirc;ng phải số tuổi thọ ti&ecirc;u hao hết, liền t&aacute;m ch&iacute;n phần mười l&agrave; tao ngộ rồi bất trắc.<br />\r\n<br />\r\nDi&ecirc;u C&ocirc;ng L&acirc;n ch&iacute;nh l&agrave; tr&aacute;ng ni&ecirc;n, họ t&ecirc;n đột nhi&ecirc;n biến mất, ngoại trừ bất ngờ bỏ m&igrave;nh, kh&ocirc;ng c&ograve;n c&aacute;i kh&aacute;c duy&ecirc;n cớ.<br />\r\n<br />\r\nTay &aacute;o b&agrave;o cuốn một c&aacute;i, đem ti&ecirc;n b&ugrave;a ch&uacute; quyển tới trong tay, một th&acirc;n ng&acirc;n gi&aacute;p Nhị Lang Ch&acirc;n qu&acirc;n nh&igrave;n ti&ecirc;n b&ugrave;a ch&uacute; b&ecirc;n tr&ecirc;n biến mất họ t&ecirc;n, tr&ecirc;n mặt hết sức &acirc;m trầm,<br />\r\n<br />\r\n&quot;C&oacute; từng hỏi thăm qua Địa Phủ, c&oacute; từng mang về C&ocirc;ng L&acirc;n ba hồn bảy v&iacute;a!&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute;. . . Kh&ocirc;ng c&oacute;. . . Địa Phủ b&ecirc;n trong, cũng kh&ocirc;ng c&oacute; Tứ gia hồn ph&aacute;ch, chỉ sợ. . . Chỉ sợ l&agrave; bị chết qu&aacute; thảm, liền hồn ph&aacute;ch đều trốn kh&ocirc;ng tho&aacute;t. .. C&ograve;n nguy&ecirc;n nh&acirc;n c&aacute;i chết, Sinh Tử bộ tr&ecirc;n chỉ xuất hiện sáu c&aacute;i chữ, mưu s&aacute;t hầu y&ecirc;u kh&ocirc;ng c&oacute; kết quả . . .&quot;<br />\r\n<br />\r\n&quot;Hầu y&ecirc;u? Chẳng lẽ l&agrave; c&aacute;i kia tiểu y&ecirc;u g&acirc;y n&ecirc;n! Kh&ocirc;ng thể! Chỉ l&agrave; cảnh giới thứ ba y&ecirc;u qu&aacute;i, tại sao l&agrave; C&ocirc;ng L&acirc;n đối thủ!&quot;<br />\r\n<br />\r\nĐem ti&ecirc;n b&ugrave;a ch&uacute; n&eacute;m xuống đất, Nhị Lang Thần giận dữ b&ecirc;n dưới, v&ocirc;̃ bàn đứng d&acirc;̣y, rất l&acirc;u, Nhị Lang Thần tựa hồ nhớ ra c&aacute;i g&igrave; đ&oacute;, l&uacute;c n&atilde;y tỉnh t&aacute;o lại, kh&ocirc;i phục trước sau như một lạnh l&ugrave;ng khu&ocirc;n mặt,<br />\r\n<br />\r\n&quot;Lẽ n&agrave;o c&aacute;i kia hầu y&ecirc;u, l&agrave; Vi&ecirc;n Hồng chuyển thế kh&ocirc;ng th&agrave;nh. . . C&aacute;o biết thi&ecirc;n hạ bầy y&ecirc;u, ph&agrave;m l&agrave; gặp phải hầu y&ecirc;u, giết kh&ocirc;ng tha! T&agrave;n s&aacute;t hầu y&ecirc;u qu&aacute; ng&agrave;n, lần kế tiếp Đ&atilde;ng Ma cuộc chiến b&ecirc;n trong, bản Qu&acirc;n ngoại lệ tha cho hắn một mạng! Hừ, bản Qu&acirc;n ngược lại muốn xem xem, rốt cuộc l&agrave; phương n&agrave;o hầu y&ecirc;u, c&agrave;ng d&aacute;m đụng đến ta Nhị Lang Thần người!&quot;<br />\r\n<br />\r\nỞ Nhị Lang Thần nổi giận thời gian, Nam Chi&ecirc;m Bộ Ch&acirc;u nơi n&agrave;o đ&oacute; tiểu y&ecirc;u trong động, một con Lục Nhĩ Mi Hầu hắt hơi một c&aacute;i,<br />\r\n<br />\r\n&quot;Kỳ qu&aacute;i, ta Vi&ecirc;n Hồng mấy c&aacute;i huynh đệ sớm đ&atilde; bị Nhị Lang Thần giết chết, c&aacute;i n&agrave;y quan đầu, ai c&ograve;n sẽ nhắc tới l&atilde;o phu. . . Bất kể, trước ti&ecirc;n tu luyện Linh Th&iacute;nh Vạn Vật Thuật đi, n&agrave;y một đời chuyển thế th&acirc;n, dĩ nhi&ecirc;n mang theo Ti&ecirc;n Thi&ecirc;n linh gi&aacute;c, đợi một thời gian, l&atilde;o phu nhất định c&oacute; thể hướng về Nhị Lang Thần b&aacute;o th&ugrave; rửa hận!&quot;<br />\r\n<br />\r\nHoa Quả Sơn T&ocirc;n Thiệu, cũng kh&ocirc;ng biết, hắn xuất hiện, sửa Di&ecirc;u C&ocirc;ng L&acirc;n kết cục, thay đổi viết Lục Nhĩ Mi Hầu vận mệnh.<br />\r\n<br />\r\nThời khắc n&agrave;y T&ocirc;n Thiệu, trơ mắt nh&igrave;n Th&ocirc;ng T&iacute; l&atilde;o hầu ở &aacute;nh s&aacute;ng m&agrave;u xanh b&ecirc;n trong, h&oacute;a th&agrave;nh một c&aacute;i th&acirc;n kỵ Thanh Ngưu &ocirc;ng l&atilde;o d&aacute;ng dấp. M&agrave; T&ocirc;n Thiệu miệng, cả kinh lại cũng kh&ocirc;ng đ&oacute;ng lại được,<br />\r\n<br />\r\n&quot;Ngươi l&agrave;. . . Ngươi l&agrave; Nh&acirc;n gi&aacute;o Gi&aacute;o chủ, Đạo Tổ Th&aacute;i Thượng L&atilde;o Qu&acirc;n!&quot;</p>', '0', 'bach-phien-tien-kinh-chieu-yeu', 8, '767', '2022-08-03 05:55:20', '2022-08-03 05:55:20'),
(45, '7', 'Lão Quân 3 bảo', '<p>T&ocirc;n Thiệu trơ mắt nh&igrave;n Th&ocirc;ng T&iacute; l&atilde;o hầu ở &aacute;nh s&aacute;ng m&agrave;u xanh b&ecirc;n trong, h&oacute;a th&agrave;nh một c&aacute;i th&acirc;n kỵ Thanh Ngưu &ocirc;ng l&atilde;o d&aacute;ng dấp. M&agrave; T&ocirc;n Thiệu miệng, cả kinh lại cũng kh&ocirc;ng đ&oacute;ng lại được,<br />\r\n<br />\r\n&quot;Ngươi l&agrave;. . . Ngươi l&agrave; Nh&acirc;n gi&aacute;o Gi&aacute;o chủ, Th&aacute;i Thượng L&atilde;o Qu&acirc;n!&quot;<br />\r\n<br />\r\n&quot;Ha ha, tiểu tử nh&atilde;n lực kh&ocirc;ng tệ, l&atilde;o phu ở n&agrave;y Hoa Quả Sơn ng&acirc;y ng&ocirc; ch&aacute;n ng&aacute;n, n&agrave;y liền rời đi, thăm bạn vấn đạo. Ở mấy ng&agrave;y n&agrave;y, l&atilde;o phu ở Hoa Quả Sơn chịu ngươi n&agrave;y Hầu Vương kh&ocirc;ng &iacute;t chăm s&oacute;c, để b&aacute;o đ&aacute;p lại, l&atilde;o phu liền cho ngươi một &iacute;t lễ vật đi. L&atilde;o phu sẽ cho ngươi ba m&oacute;n đồ, bất qu&aacute; thanh minh trước, ngươi chỉ c&oacute; thể lựa chọn như thế. . .&quot;<br />\r\n<br />\r\nCuối c&ugrave;ng &quot;Lựa chọn như thế&quot; c&acirc;u n&oacute;i n&agrave;y, Th&aacute;i Thượng L&atilde;o Qu&acirc;n đọc r&otilde; chữ cực kỳ chầm chậm, trong mắt c&agrave;ng là bao h&agrave;m th&acirc;m &yacute;, ở L&atilde;o Qu&acirc;n trong l&ograve;ng, c&agrave;ng l&agrave; muốn d&ugrave;ng n&agrave;y một lần cuối c&ugrave;ng ly biệt tặng bảo, khảo nghiệm một ch&uacute;t T&ocirc;n Ngộ Kh&ocirc;ng t&acirc;m t&iacute;nh.<br />\r\n&nbsp;</p>', '<p>T&ocirc;n Thiệu trơ mắt nh&igrave;n Th&ocirc;ng T&iacute; l&atilde;o hầu ở &aacute;nh s&aacute;ng m&agrave;u xanh b&ecirc;n trong, h&oacute;a th&agrave;nh một c&aacute;i th&acirc;n kỵ Thanh Ngưu &ocirc;ng l&atilde;o d&aacute;ng dấp. M&agrave; T&ocirc;n Thiệu miệng, cả kinh lại cũng kh&ocirc;ng đ&oacute;ng lại được,<br />\r\n<br />\r\n&quot;Ngươi l&agrave;. . . Ngươi l&agrave; Nh&acirc;n gi&aacute;o Gi&aacute;o chủ, Th&aacute;i Thượng L&atilde;o Qu&acirc;n!&quot;<br />\r\n<br />\r\n&quot;Ha ha, tiểu tử nh&atilde;n lực kh&ocirc;ng tệ, l&atilde;o phu ở n&agrave;y Hoa Quả Sơn ng&acirc;y ng&ocirc; ch&aacute;n ng&aacute;n, n&agrave;y liền rời đi, thăm bạn vấn đạo. Ở mấy ng&agrave;y n&agrave;y, l&atilde;o phu ở Hoa Quả Sơn chịu ngươi n&agrave;y Hầu Vương kh&ocirc;ng &iacute;t chăm s&oacute;c, để b&aacute;o đ&aacute;p lại, l&atilde;o phu liền cho ngươi một &iacute;t lễ vật đi. L&atilde;o phu sẽ cho ngươi ba m&oacute;n đồ, bất qu&aacute; thanh minh trước, ngươi chỉ c&oacute; thể lựa chọn như thế. . .&quot;<br />\r\n<br />\r\nCuối c&ugrave;ng &quot;Lựa chọn như thế&quot; c&acirc;u n&oacute;i n&agrave;y, Th&aacute;i Thượng L&atilde;o Qu&acirc;n đọc r&otilde; chữ cực kỳ chầm chậm, trong mắt c&agrave;ng là bao h&agrave;m th&acirc;m &yacute;, ở L&atilde;o Qu&acirc;n trong l&ograve;ng, c&agrave;ng l&agrave; muốn d&ugrave;ng n&agrave;y một lần cuối c&ugrave;ng ly biệt tặng bảo, khảo nghiệm một ch&uacute;t T&ocirc;n Ngộ Kh&ocirc;ng t&acirc;m t&iacute;nh.<br />\r\n<br />\r\n&quot;H&iacute;c, Th&aacute;i Thượng L&atilde;o Qu&acirc;n kh&ocirc;ng đem l&atilde;o T&ocirc;n ph&oacute;ng tới B&aacute;t Qu&aacute;i L&ocirc; luyện th&agrave;nh Kim đan, ngược lại đưa ta bảo vật? L&atilde;o T&ocirc;n vận mệnh sửa?&quot; Nghe xong Th&aacute;i Thượng L&atilde;o Qu&acirc;n lời n&oacute;i, T&ocirc;n Thiệu trong l&ograve;ng vui vẻ, ti&ecirc;u trừ Th&aacute;i Thượng L&atilde;o Qu&acirc;n đối với m&igrave;nh địch &yacute;, thiếu Đạo Tổ c&aacute;i n&agrave;y uy hiếp thật lớn, ng&agrave;y sau đại n&aacute;o Thi&ecirc;n Cung liền dễ d&agrave;ng hơn nhiều!<br />\r\n<br />\r\n&quot;Bản Vương đa tạ Đạo Tổ &yacute; tốt!&quot;<br />\r\n<br />\r\nNgay sau đ&oacute;, T&ocirc;n Thiệu thần sắc nghi&ecirc;m lại, đứng đắn đối với Th&aacute;i Thượng L&atilde;o Qu&acirc;n chắp tay thi lễ, tuy rằng một lễ n&agrave;y từ một con khỉ sử dụng, hơi c&oacute; ch&uacute;t tức cười d&aacute;ng dấp, nhưng T&ocirc;n Thiệu tr&ecirc;n mặt nhưng kh&ocirc;ng c&oacute; một tia tản mạn t&ugrave;y tiện vẻ, m&agrave; Th&aacute;i Thượng L&atilde;o Qu&acirc;n đối với một lễ n&agrave;y, hiển nhi&ecirc;n kh&aacute; l&agrave; thoả m&atilde;n, lật b&agrave;n tay một c&aacute;i, một c&aacute;i kim quang ch&oacute;i mắt vi&ecirc;n ho&agrave;n xuất hiện ở l&ograve;ng b&agrave;n tay.<br />\r\n<br />\r\nN&agrave;y vi&ecirc;n ho&agrave;n dường như tay c&ocirc; g&aacute;i v&ograve;ng tay to nhỏ, nhưng m&agrave; b&ecirc;n tr&ecirc;n biểu lộ ph&aacute;p tắc gợn s&oacute;ng, để T&ocirc;n Thiệu cảm thấy nh&igrave;n thấy m&agrave; giật m&igrave;nh!<br />\r\n<br />\r\nPh&aacute;p bảo n&agrave;y, uy lực qu&aacute; mức khủng bố, vẻn vẹn đối mặt với Kim Cương Tr&aacute;c, T&ocirc;n Thiệu liền cảm thấy được tứ chi kh&ocirc;ng c&ograve;n ch&uacute;t sức lực n&agrave;o!<br />\r\n<br />\r\n&quot;Vật ấy t&ecirc;n l&agrave; Kim Cương Tr&aacute;c, l&atilde;o phu ti&ecirc;u tốn trăm năm c&ocirc;ng phu mới mới luyện th&agrave;nh, một v&ograve;ng lấy ra, mặc d&ugrave; l&agrave; Đại La Kim Ti&ecirc;n, cũng phải lột da, ngươi như y&ecirc;u th&iacute;ch, c&oacute; thể cầm!&quot;<br />\r\n<br />\r\n&quot;Kim Cương Tr&aacute;c, dĩ nhi&ecirc;n l&agrave; Kim Cương Tr&aacute;c!&quot;<br />\r\n<br />\r\nĐại danh đỉnh đỉnh Kim Cương Tr&aacute;c, T&ocirc;n Thiệu h&aacute; c&oacute; thể kh&ocirc;ng biết! ? Nguy&ecirc;n t&aacute;c b&ecirc;n trong, T&ocirc;n Ngộ Kh&ocirc;ng Kim Th&acirc;n Bất Diệt, đao kiếm kh&ocirc;ng bị thương, nước lửa bất x&acirc;m, mười vạn Thi&ecirc;n Binh kh&ocirc;ng một người c&oacute; thể g&acirc;y tổn thương cho T&ocirc;n Ngộ Kh&ocirc;ng, nhưng m&agrave; Th&aacute;i Thượng L&atilde;o Qu&acirc;n Kim Cương Tr&aacute;c chỉ một ch&uacute;t, liền đem T&ocirc;n Ngộ Kh&ocirc;ng k&iacute;ch thương, n&agrave;y mới cho hao ng&agrave;y cẩu cắn T&ocirc;n Ngộ Kh&ocirc;ng, Nhị Lang Thần nắm bắt T&ocirc;n Ngộ Kh&ocirc;ng cơ hội!<br />\r\n<br />\r\nC&oacute; c&aacute;i n&agrave;y Kim Cương Tr&aacute;c, mặc d&ugrave; l&agrave; đối mặt mười hai Kim ti&ecirc;n c&acirc;́p b&acirc;̣c t&ocirc;̀n tại, T&ocirc;n Thiệu cũng kh&ocirc;ng sợ ch&uacute;t n&agrave;o!<br />\r\n<br />\r\nNh&igrave;n Th&aacute;i Thượng L&atilde;o Qu&acirc;n trong tay kim quang ch&oacute;i mắt vi&ecirc;n ho&agrave;n, T&ocirc;n Thiệu kh&ocirc;ng ch&uacute;t n&agrave;o che giấu kh&aacute;t vọng trong l&ograve;ng, nhưng m&agrave; cắn răng kềm chế.<br />\r\n<br />\r\nTh&aacute;i Thượng L&atilde;o Qu&acirc;n kh&ocirc;ng phải n&oacute;i, đưa ra mấy loại đồ vật để m&igrave;nh lựa chọn như thế sao. L&uacute;c n&agrave;y mới c&aacute;i thứ nhất, ph&iacute;a sau c&ograve;n c&oacute; v&agrave;i loại đ&acirc;y.<br />\r\n<br />\r\nT&ocirc;n Thiệu cũng kh&ocirc;ng c&oacute; che giấu ch&iacute;nh m&igrave;nh đối với Kim Cương Tr&aacute;c kh&aacute;t vọng, nhưng kềm chế kh&ocirc;ng c&oacute; lập tức lựa chọn Kim Cương Tr&aacute;c.<br />\r\n<br />\r\nKh&ocirc;ng che giấu bản t&acirc;m, n&oacute;i r&otilde; T&ocirc;n Thiệu cũng kh&ocirc;ng phải l&agrave; dối tr&aacute; người. Kềm chế trong l&ograve;ng kh&aacute;t vọng, n&oacute;i r&otilde; T&ocirc;n Thiệu l&agrave; c&aacute;i hiểu được trong khống chế t&acirc;m cảm t&igrave;nh người. T&ocirc;n Thiệu lần n&agrave;y cử động, hợp người bản t&iacute;nh, vừa c&oacute; sự ki&ecirc;n tr&igrave; của ch&iacute;nh m&igrave;nh, để Th&aacute;i Thượng L&atilde;o Qu&acirc;n h&agrave;i l&ograve;ng nở nụ cười, lật b&agrave;n tay một c&aacute;i, thu hồi Kim Cương Tr&aacute;c, lại vung l&ecirc;n, một c&aacute;i cổ xưa xanh ngọc thẻ tre, xuất hiện ở Th&aacute;i Thượng L&atilde;o Qu&acirc;n trong tay,<br />\r\n<br />\r\n&quot;N&agrave;y thẻ ngọc ghi lại l&atilde;o phu b&igrave;nh sinh luyện đan t&acirc;m đắc, nếu ngươi c&oacute; thể học c&oacute; th&agrave;nh tựu, tr&ecirc;n trời dưới đất, cầu ngươi luyện đan Thần Ti&ecirc;n nhiều v&ocirc; số kể, kh&ocirc;ng người sẽ dễ d&agrave;ng đắc tội ngươi. M&agrave; n&agrave;y trong ngọc giản, c&agrave;ng ghi lại l&atilde;o phu hai thức ph&eacute;p thuật, một, t&ecirc;n l&agrave; Tam Muội Ch&acirc;n Hỏa, ngươi đ&atilde; học được Thi&ecirc;n Cương Biến Hồi Phong Phản Hỏa, như lại tập luyện được Tam Muội Ch&acirc;n Hỏa, mặc d&ugrave; Thi&ecirc;n ti&ecirc;n, cũng phải nhượng bộ lui binh. Thứ hai, t&ecirc;n l&agrave; Tam Thanh Quyết, ch&iacute;nh l&agrave; tế luyện th&acirc;n ngoại h&oacute;a th&acirc;n thuật, ảo diệu trong đ&oacute;, thời khắc n&agrave;y ngươi kh&ocirc;ng c&aacute;ch n&agrave;o lĩnh hội. Bất qu&aacute; nếu ngươi c&oacute; thể tăng l&ecirc;n tới ba Ti&ecirc;n chi cảnh, đem sẽ hiểu, thuật n&agrave;y, v&ocirc; c&ugrave;ng ảo diệu. Tu tập thuật n&agrave;y, ngươi vượt qua ch&eacute;m kiếp trở th&agrave;nh Đại La Kim Ti&ecirc;n cơ hội, ch&iacute; &iacute;t so với thường nh&acirc;n c&oacute; th&ecirc;m ba phần mười!&quot;<br />\r\n<br />\r\nDứt lời, Th&aacute;i Thượng L&atilde;o Qu&acirc;n mỉm cười kh&ocirc;ng n&oacute;i, c&ugrave;ng đợi T&ocirc;n Thiệu trả lời.<br />\r\n<br />\r\n&quot;C&aacute;i g&igrave;! Luyện đan t&acirc;m đắc, Tam Muội Ch&acirc;n Hỏa, c&ugrave;ng với Tam Thanh Quyết!&quot;<br />\r\n<br />\r\nLần n&agrave;y T&ocirc;n Thiệu tr&ecirc;n mặt khiếp sợ, so với trước c&oacute; th&ecirc;m nhiều lắm. N&agrave;y ba m&oacute;n đồ, b&ecirc;n n&agrave;o gi&aacute; trị, đều vượt xa Kim Cương Tr&aacute;c!<br />\r\n<br />\r\nNhư c&oacute; L&atilde;o Qu&acirc;n luyện đan t&acirc;m đắc,<br />\r\n<br />\r\nLấy T&ocirc;n Thiệu l&agrave;m người hai đời t&acirc;m tr&iacute;, tuyệt đối c&oacute; cơ hội trở th&agrave;nh L&atilde;o Qu&acirc;n b&ecirc;n dưới đệ nhất Luyện đan sư! Đến thời điểm, t&ugrave;y tiện luyện chế một vi&ecirc;n Kim đan, bầu trời Ti&ecirc;n Thần c&ograve;n kh&ocirc;ng đứng xếp h&agrave;ng cướp kết giao ch&iacute;nh m&igrave;nh!<br />\r\n<br />\r\nNhư học được Tam Muội Ch&acirc;n Hỏa, T&ocirc;n Thiệu Hồi Phong Phản Hỏa liền c&oacute; thể đại th&agrave;nh, ch&iacute;nh như Th&aacute;i Thượng L&atilde;o Qu&acirc;n từng n&oacute;i, mặc d&ugrave; l&agrave; ba Ti&ecirc;n chi cảnh Ti&ecirc;n Thần, cũng c&oacute; thể một trận chiến! Đến rồi c&aacute;i cấp bậc đ&oacute;, T&ocirc;n Thiệu trở tay một chưởng, đều c&oacute; thể đem Nhị Lang Thần đập chết!<br />\r\n<br />\r\nCho tới Tam Thanh Quyết, lai lịch c&agrave;ng lớn hơn! Đối với tăng cao đột ph&aacute; Đại La Kim Ti&ecirc;n tỷ lệ, T&ocirc;n Thiệu cũng kh&ocirc;ng biết, bất qu&aacute; T&ocirc;n Thiệu xem qua Phong Thần diễn nghĩa, a! Năm đ&oacute; Th&aacute;i Thượng L&atilde;o Qu&acirc;n c&ugrave;ng Tiệt gi&aacute;o Gi&aacute;o chủ đối chiến thời gian, liền ỷ v&agrave;o Tam Thanh Quyết Nhất Kh&iacute; H&oacute;a Tam Thanh thuật, vượt qua Tiệt gi&aacute;o Gi&aacute;o chủ một bậc! Tam Thanh Quyết, tuyệt đối l&agrave; cấp độ nghịch thi&ecirc;n ph&aacute;p thuật kh&aacute;c!<br />\r\n<br />\r\nNuốt một c&aacute;i nước bọt, T&ocirc;n Thiệu thực sự c&oacute; ch&uacute;t khắc chế kh&ocirc;ng nổi k&iacute;ch động của m&igrave;nh, hầu như liền phải lập tức lựa chọn c&aacute;i thẻ ngọc n&agrave;y. T&ocirc;n Thiệu, ch&iacute;nh l&agrave; một c&aacute;i b&igrave;nh thường người, kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; sống mấy ng&agrave;n năm l&atilde;o qu&aacute;i vật, lại như người ph&agrave;m đột nhi&ecirc;n gặp phải thế giới thủ phủ, thế giới thủ phủ n&oacute;i cho ngươi, ta chịu sự gi&uacute;p đỡ của ngươi, cho ngươi 100 triệu nguy&ecirc;n b&aacute;o đ&aacute;p ngươi, ngươi hoặc l&agrave;!<br />\r\n<br />\r\nC&aacute;i n&agrave;o một ph&agrave;m nh&acirc;n, gặp phải t&igrave;nh huống như thế, d&aacute;m n&oacute;i ch&iacute;nh m&igrave;nh kh&ocirc;ng động l&ograve;ng! T&ocirc;n Thiệu từng đọc nhiều lắm ti&ecirc;n hiệp tiểu thuyết, b&ecirc;n trong từng c&aacute;i chủ gi&aacute;c đều l&agrave; t&acirc;m tr&iacute; như y&ecirc;u, đối mặt ng&agrave;n tỉ t&agrave;i bảo trong l&ograve;ng kh&ocirc;ng lay được. Đ&aacute;ng tiếc, T&ocirc;n Thiệu l&agrave; một con người thực sự, hắn kh&ocirc;ng l&agrave;m được kh&ocirc;ng động l&ograve;ng.<br />\r\n<br />\r\n&quot;Xin hỏi Đạo Tổ, c&ograve;n c&oacute; một m&oacute;n đồ, l&agrave; c&aacute;i g&igrave;. . .&quot;<br />\r\n<br />\r\nCố n&eacute;n k&iacute;ch động trong l&ograve;ng, T&ocirc;n Thiệu trong linh hồn một tia Hầu ca ki&ecirc;u ngạo, đưa hắn k&eacute;o về hiện thực, dần dần tỉnh t&aacute;o lại. Đằng trước hai m&oacute;n đồ đều l&agrave; như thế bảo bối, giờ khắc này T&ocirc;n Thiệu rất muốn biết, thứ ba m&oacute;n bảo vật, rốt cuộc l&agrave; c&aacute;i g&igrave;!<br />\r\n<br />\r\n&quot;Ồ? Nhiều năm như vậy, c&oacute; thể ở l&atilde;o phu liệt ra n&agrave;y hai vật ph&iacute;a sau c&ograve;n mặt kh&ocirc;ng đổi sắc, cũng kh&ocirc;ng nhiều a. L&atilde;o phu thật kh&ocirc;ng c&oacute; nh&igrave;n lầm, của ngươi đạo t&acirc;m tuy rằng c&ograve;n hết sức hỗn tạp, bất qu&aacute; cũng rất ki&ecirc;n định, kh&ocirc;ng sai, kh&ocirc;ng sai. Hơn nữa c&ograve;n c&oacute; thể kh&ocirc;ng che giấu kh&aacute;t vọng trong l&ograve;ng, kh&ocirc;ng giả bộ, điểm ấy r&acirc;́t t&ocirc;́t. Dối tr&aacute; người, th&igrave; kh&ocirc;ng c&aacute;ch n&agrave;o đến đạo chi cực cảnh.&quot;<br />\r\n<br />\r\nNh&igrave;n T&ocirc;n Thiệu từ hừng hực đến thanh minh &aacute;nh mắt, Th&aacute;i Thượng L&atilde;o Qu&acirc;n &aacute;nh mắt nhất động, lập tức h&agrave;i l&ograve;ng gật đầu kh&ocirc;ng ngớt, h&agrave;o kh&ocirc;ng tiếc rẻ khen ngợi T&ocirc;n Thiệu một c&acirc;u, thu hồi thẻ ngọc, lật b&agrave;n tay một c&aacute;i, lần n&agrave;y trong l&ograve;ng b&agrave;n tay nhưng xuất hiện một bản m&agrave;u xanh thẻ tre. N&agrave;y thẻ tre nh&igrave;n như b&igrave;nh thường kh&ocirc;ng c&oacute; g&igrave; lạ, m&agrave; L&atilde;o Qu&acirc;n đồng dạng kh&ocirc;ng c&oacute; qu&aacute; nhiều kinh người chi ngữ,<br />\r\n<br />\r\n&quot;N&agrave;y thẻ tre, l&agrave; năm đ&oacute; ta ngộ đạo thời gian, rời khỏi ph&iacute;a t&acirc;y H&agrave;m Cốc Quan lưu lại, hơn năm ng&agrave;n chữ ghi lại l&atilde;o phu đối với đại đạo cảm ngộ. Ha ha, Doãn Hỉ tiểu từ kia, đem hắn gọi l&agrave; Đạo Đức Kinh,, ngươi nếu c&oacute; hứng th&uacute;, c&oacute; thể nh&igrave;n một ch&uacute;t.&quot;<br />\r\n<br />\r\n&quot; Đạo Đức Kinh,?&quot;<br />\r\n<br />\r\nLần n&agrave;y, T&ocirc;n Thiệu triệt để bối rối. Kh&ocirc;ng phải l&agrave; bởi v&igrave; thứ ba m&oacute;n bảo vật kinh người, m&agrave; l&agrave; bởi v&igrave; thứ ba m&oacute;n bảo vật qu&aacute; mức b&igrave;nh thường.<br />\r\n<br />\r\nKh&ocirc;ng n&oacute;i đời sau quen thuộc ti&ecirc;n hiệp tiểu thuyết T&ocirc;n Thiệu, sau đ&oacute; liền c&oacute; thể tụng v&agrave;i c&acirc;u &quot;Đạo khả đạo&quot;, &quot;Thượng thiện nhược thủy&quot;, mặc d&ugrave; l&agrave; ở n&agrave;y T&acirc;y Du thế giới, muốn ở Nh&acirc;n giới t&igrave;m được một bản Đạo Đức Kinh, cũng kh&ocirc;ng phải l&agrave; c&aacute;i g&igrave; việc kh&oacute;.<br />\r\n<br />\r\nD&ugrave; sao Đạo Đức Kinh, ở Đạo M&ocirc;n danh tiếng rất lớn, nhưng cũng kh&ocirc;ng phải g&igrave; đ&oacute; tu luyện ph&aacute;p m&ocirc;n, c&agrave;ng nhiều hơn ghi lại l&agrave; v&igrave; người nơi đời c&ugrave;ng với dưỡng sinh chi đạo.<br />\r\n<br />\r\nT&ocirc;n Thiệu kh&ocirc;ng hiểu, trước hai c&aacute;i bảo vật đều l&agrave; kinh thi&ecirc;n động địa cấp bậc, tại sao đến rồi &aacute;p trục thứ ba m&oacute;n, phản ngược lại th&agrave;nh b&igrave;nh thường nhất vật tầm thường.<br />\r\n<br />\r\nNh&igrave;n Th&aacute;i Thượng L&atilde;o Qu&acirc;n phong khinh v&acirc;n đạm nụ cười, T&ocirc;n Thiệu cảm gi&aacute;c m&igrave;nh hầu n&atilde;o kh&ocirc;ng đủ d&ugrave;ng. Trong n&agrave;y đến tột c&ugrave;ng l&agrave; c&oacute; &yacute; g&igrave;? T&ocirc;n Thiệu mơ hồ cảm gi&aacute;c, nếu như đọc kh&ocirc;ng hiểu L&atilde;o Qu&acirc;n loại thứ ba lựa chọn &yacute; tứ, ch&iacute;nh m&igrave;nh sẽ bỏ qua một c&aacute;i thi&ecirc;n đại cơ duy&ecirc;n.<br />\r\n<br />\r\nCẩn thận suy tư về L&atilde;o Qu&acirc;n &yacute; tại ng&ocirc;n ngoại, nửa n&eacute;n hương ph&iacute;a sau, T&ocirc;n Thiệu vỗ một c&aacute;i hầu n&atilde;o, ho&agrave;n to&agrave;n tỉnh ngộ, &quot;Đ&uacute;ng rồi, Doãn Hỉ!&quot;<br />\r\n<br />\r\nNghi&ecirc;ng đi &aacute;nh mắt, T&ocirc;n Thiệu mắt lộ ra vẻ ki&ecirc;n định, quay về L&atilde;o Qu&acirc;n c&uacute;i người h&agrave;nh lễ, lập tức tiếp nhận c&aacute;i n&agrave;y m&agrave;u xanh thẻ tre, cung k&iacute;nh n&oacute;i rằng, &quot;Đa tạ Đạo Tổ trọng thưởng, v&atilde;n bối lựa chọn n&agrave;y thứ ba m&oacute;n đồ. . .&quot;<br />\r\n<br />\r\nT&ocirc;n Thiệu đồng dạng lời n&oacute;i mang th&acirc;m &yacute;, n&oacute;i l&agrave; lựa chọn thứ ba m&oacute;n đồ, nhưng kh&ocirc;ng c&oacute; n&oacute;i lựa chọn Đạo Đức Kinh,.<br />\r\n<br />\r\n&quot;Trẻ nhỏ dễ dạy! Ha ha, ng&agrave;y tụng kinh văn ba lần, trăm năm ph&iacute;a sau, đến cho ta l&agrave;m tr&ocirc;ng l&ograve; đồng tử đi. . . Trước đ&oacute;, cũng kh&ocirc;ng n&ecirc;n để lộ tin tức. . .&quot;<br />\r\n<br />\r\nĐối với T&ocirc;n Thiệu thoả m&atilde;n nở nụ cười, Th&aacute;i Thượng L&atilde;o Qu&acirc;n h&oacute;a th&agrave;nh một đạo thanh quang, trong chốc l&aacute;t, tại chỗ biến mất, kh&ocirc;ng biết tung t&iacute;ch.<br />\r\n<br />\r\nNghe xong Th&aacute;i Thượng L&atilde;o Qu&acirc;n trước khi đi lưu lại lời n&oacute;i, T&ocirc;n Thiệu tr&ecirc;n mặt nở nụ cười, lần n&agrave;y thật để ch&iacute;nh m&igrave;nh đ&atilde; đo&aacute;n đ&uacute;ng, sự lựa chọn n&agrave;y, kh&ocirc;ng c&oacute; thiệt th&ograve;i! Gi&aacute; trị, qu&aacute; đ&aacute;ng gi&aacute;!<br />\r\n<br />\r\nDoãn Hỉ l&agrave; ai, đ&acirc;y ch&iacute;nh l&agrave; Đạo Tổ đệ tử k&yacute; danh! L&atilde;o Qu&acirc;n ban cho T&ocirc;n Thiệu Đạo Đức Kinh,, bao h&agrave;m th&acirc;m &yacute;, ch&iacute;nh l&agrave; n&agrave;y một c&aacute;i, như l&agrave; T&ocirc;n Thiệu nh&igrave;n kh&ocirc;ng thấu n&agrave;y tầng th&acirc;m &yacute;, liền kh&ocirc;ng xứng l&agrave;m L&atilde;o Qu&acirc;n đệ tử k&yacute; danh!<br />\r\n<br />\r\nNhưng m&agrave; thu đệ tử liền thu đệ tử, v&igrave; sao L&atilde;o Qu&acirc;n c&ograve;n n&oacute;i kh&ocirc;ng cần đi lậu tin tức, n&oacute;i như thế h&agrave;m s&uacute;c, thật giống sợ ai biết tựa như. Hơn nữa tự hồ chỉ l&agrave; muốn thu m&igrave;nh l&agrave;m đệ tử k&yacute; danh, trong n&agrave;y tựa hồ có nhi&ecirc;̀u bí &acirc;̉n.<br />\r\n<br />\r\n&quot;Kh&ocirc;ng nghĩ ra, kh&ocirc;ng nghĩ ra!&quot;<br />\r\n<br />\r\nTrong n&agrave;y quanh co, đ&atilde; vượt ra khỏi T&acirc;y Du K&yacute;, ghi lại phạm tr&ugrave;, T&ocirc;n Thiệu kh&ocirc;ng nghĩ ra, cũng liền kh&ocirc;ng suy nghĩ th&ecirc;m nữa.<br />\r\n<br />\r\nNhanh ch&acirc;n đi ra Thủy Li&ecirc;m Động, T&ocirc;n Thiệu đưa tới một con kh&aacute;c kh&aacute; l&agrave; trẻ tuổi Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu. N&agrave;y con Th&ocirc;ng T&iacute; Vi&ecirc;n Hầu kh&ocirc;ng am hiểu tranh đấu, bất qu&aacute; đầu &oacute;c linh hoạt, T&ocirc;n Thiệu liền sắp xếp hắn t&igrave;m hiểu khắp nơi tin tức, cũng v&igrave; đặt t&ecirc;n l&agrave; T&ocirc;n Nhị, &quot;T&ocirc;n Nhị, bản Vương để cho ngươi d&ograve; x&eacute;t tin tức, ngươi c&oacute; thể d&ograve; thăm?&quot;<br />\r\n<br />\r\n&quot;Bẩm b&aacute;o Đại vương, Hoa Quả Sơn ph&iacute;a nam 500 dặm, vừa vặn c&oacute; một n&uacute;i lửa đảo, t&ecirc;n l&agrave; Vi&ecirc;m Ma đảo, nơi đ&oacute; n&ecirc;n ph&ugrave; hợp Đại vương y&ecirc;u cầu.&quot;<br />\r\n<br />\r\n&quot;Chuẩn bị một &iacute;t tr&aacute;i c&acirc;y, muốn đầy đủ mười năm t&aacute;c dụng. Một th&aacute;ng sau, chờ bản Vương vững chắc Y&ecirc;u vương cảnh giới, liền khởi h&agrave;nh đi Vi&ecirc;m Ma đảo nh&igrave;n!&quot;</p>', '0', 'lao-quan-3-bao', 8, '87', '2022-08-03 06:00:51', '2022-08-03 06:00:51');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(46, '1', 'Đi Nhặt Rác Nào!', '<p>Tinh lịch năm 1307, tinh cầu r&aacute;c số 101.Mặt trời ch&oacute;i chang treo tr&ecirc;n tinh kh&ocirc;ng, bầu kh&ocirc;ng kh&iacute; cực kỳ nặng nề, đột nhi&ecirc;n c&oacute; một chiếc phi thuyền do li&ecirc;n minh chế tạo xuy&ecirc;n qua tầng kh&iacute; quyển, sau đ&oacute; phi thuyền hướng về mặt đất lao thẳng xuống, khi phi thuyền c&aacute;ch mặt đất 500m th&igrave; cửa cabin mở ra, một đống lớn r&aacute;c thải tr&uacute;t xuống---Rầm rập ---Tiếng ồn vang l&ecirc;n như tiếng sấm rền k&eacute;o d&agrave;i hai mươi ph&uacute;t mới dừng lại.Phi thuyền đổ r&aacute;c thải xong liền đ&oacute;ng lại cửa khoang một lần nữa rồi bay về ph&iacute;a tầng kh&iacute; quyển đi mất ---Nhưng kh&ocirc;ng đến nửa giờ lại c&oacute; một chiếc phi thuyền theo s&aacute;t m&agrave; đến ---Lại lần nữa k&ecirc;u rầm rập ---Phi thuyền bốc xếp v&agrave; vận chuyển r&aacute;c thải kh&ocirc;ng ngừng hạ xuống rồi cất c&aacute;nh, mang theo từng đợt gi&oacute; lốc, trận gi&oacute; mạnh mẽ n&agrave;y thổi qu&eacute;t đi v&ocirc; số c&aacute;t bụi c&ugrave;ng số lượng lớn r&aacute;c thải&hellip;&hellip; &lsquo;Ch&eacute;m giết lung tung&rsquo; tinh cầu r&aacute;c số 101 n&agrave;y.Bụi quấn l&ecirc;n đen ngh&igrave;n nghịt che khuất cả bầu trời&hellip;&hellip;Qu&yacute; Dữu đứng c&aacute;ch một lớp chắn ph&ograve;ng hộ, nh&igrave;n khung cảnh đối diện c&oacute; thể v&iacute; như tận thế, trong mắt lại kh&ocirc;ng c&oacute; ch&uacute;t cảm x&uacute;c n&agrave;o.</p>', '<p>Tinh lịch năm 1307, tinh cầu r&aacute;c số 101.Mặt trời ch&oacute;i chang treo tr&ecirc;n tinh kh&ocirc;ng, bầu kh&ocirc;ng kh&iacute; cực kỳ nặng nề, đột nhi&ecirc;n c&oacute; một chiếc phi thuyền do li&ecirc;n minh chế tạo xuy&ecirc;n qua tầng kh&iacute; quyển, sau đ&oacute; phi thuyền hướng về mặt đất lao thẳng xuống, khi phi thuyền c&aacute;ch mặt đất 500m th&igrave; cửa cabin mở ra, một đống lớn r&aacute;c thải tr&uacute;t xuống---Rầm rập ---Tiếng ồn vang l&ecirc;n như tiếng sấm rền k&eacute;o d&agrave;i hai mươi ph&uacute;t mới dừng lại.Phi thuyền đổ r&aacute;c thải xong liền đ&oacute;ng lại cửa khoang một lần nữa rồi bay về ph&iacute;a tầng kh&iacute; quyển đi mất ---Nhưng kh&ocirc;ng đến nửa giờ lại c&oacute; một chiếc phi thuyền theo s&aacute;t m&agrave; đến ---Lại lần nữa k&ecirc;u rầm rập ---Phi thuyền bốc xếp v&agrave; vận chuyển r&aacute;c thải kh&ocirc;ng ngừng hạ xuống rồi cất c&aacute;nh, mang theo từng đợt gi&oacute; lốc, trận gi&oacute; mạnh mẽ n&agrave;y thổi qu&eacute;t đi v&ocirc; số c&aacute;t bụi c&ugrave;ng số lượng lớn r&aacute;c thải&hellip;&hellip; &lsquo;Ch&eacute;m giết lung tung&rsquo; tinh cầu r&aacute;c số 101 n&agrave;y.Bụi quấn l&ecirc;n đen ngh&igrave;n nghịt che khuất cả bầu trời&hellip;&hellip;Qu&yacute; Dữu đứng c&aacute;ch một lớp chắn ph&ograve;ng hộ, nh&igrave;n khung cảnh đối diện c&oacute; thể v&iacute; như tận thế, trong mắt lại kh&ocirc;ng c&oacute; ch&uacute;t cảm x&uacute;c n&agrave;o.<br />\r\n<br />\r\n<br />\r\nD&ugrave; cảnh sắc c&oacute; kỳ quan đến mức n&agrave;o th&igrave; chỉ cần nh&igrave;n m&atilde;i rồi cũng sẽ cảm thấy v&ocirc; vị m&agrave; th&ocirc;i.Từ l&uacute;c c&ocirc; xuy&ecirc;n tới tinh cầu r&aacute;c số 101 n&agrave;y đ&atilde; được nửa năm, trong nửa năm n&agrave;y, mỗi ng&agrave;y đều c&oacute; phi thuyền vũ trụ bay đến tinh cầu số 101 đổ r&aacute;c, v&igrave; vậy mỗi ng&agrave;y cảnh tượng n&agrave;y đều xảy ra.Đến khi phi thuyền vũ trụ cuối c&ugrave;ng bay đi, gi&oacute; lốc cũng dần dần ngừng lại&hellip;&hellip; Qu&yacute; Dữu chờ ở b&ecirc;n cạnh lớp chắn ph&ograve;ng hộ, trong mắt chợt s&aacute;ng l&ecirc;n tia s&aacute;ng: Muốn lật ra lớp chắn ph&ograve;ng hộ đi đến trước mặt nhặt r&aacute;c qu&aacute;, c&ocirc; nghĩ thầm c&oacute; lẽ h&ocirc;m nay kh&ocirc;ng biết chừng c&oacute; thể đ&agrave;o được đồ vật g&igrave; đ&oacute; c&oacute; gi&aacute; trị.Canh giữ b&ecirc;n cạnh lớp chắn ph&ograve;ng hộ để chờ đi đ&agrave;o r&aacute;c kh&ocirc;ng chỉ c&oacute; mỗi m&igrave;nh Qu&yacute; Dữu m&agrave; c&ograve;n c&oacute; ba vị h&agrave;ng x&oacute;m: Người đ&agrave;n &ocirc;ng trung ni&ecirc;n c&oacute; vẻ mặt đầy tang thương l&agrave; ch&uacute; Tạ Nghị, lấm tấm t&oacute;c bạc l&agrave; b&agrave; Jenny, v&agrave; người đ&agrave;n &ocirc;ng trẻ tuổi với th&acirc;n h&igrave;nh cao lớn cứng cỏi, khu&ocirc;n mặt đẹp trai với vẻ mặt rất nhi&ecirc;m t&uacute;c l&agrave; Ryan.Sau khi gi&oacute; lốc ngừng hẳn, mấy người kh&ocirc;ng n&oacute;i c&acirc;u n&agrave;o, y&ecirc;n lặng mặc l&ecirc;n dụng cụ ph&ograve;ng hộ v&igrave; ho&agrave;n cảnh của khu b&ecirc;n ngo&agrave;i ph&ograve;ng hộ cực kỳ &aacute;c liệt, trừ c&aacute;t bụi c&ugrave;ng m&ugrave;i r&aacute;c h&ocirc;i thối ra c&ograve;n c&oacute; kh&ocirc;ng kh&iacute; &ocirc; nhiễm nghi&ecirc;m trọng v&agrave; ph&oacute;ng xạ vũ trụ&hellip;&hellip; Nếu kh&ocirc;ng mặc dụng cụ ph&ograve;ng hộ m&agrave; đ&atilde; rời khỏi khu vực ph&ograve;ng hộ đ&uacute;ng l&agrave; đi t&igrave;m chết, Qu&yacute; Dữu y&ecirc;n lặng mặc xong, c&ocirc; quay sang ba người c&ograve;n lại, Tạ Nghị v&agrave; Ryan đ&atilde; mặc xong từ l&acirc;u, chỉ c&oacute; b&agrave; Jenny lớn tuổi l&agrave; hơi chậm một ch&uacute;t, thấy mọi người nh&igrave;n về ph&iacute;a m&igrave;nh, b&agrave; Jenny cười tủm tỉm n&oacute;i: &ldquo;b&agrave; mặc xong rồi, mọi người xuất ph&aacute;t n&agrave;o.&rdquo;Ryan nhấc ch&acirc;n, l&agrave; người đầu ti&ecirc;n bước khỏi lớp chắn.Qu&yacute; Dữu do dự c&oacute; n&ecirc;n bọc hậu hay kh&ocirc;ng th&igrave; b&agrave; Jenny đ&atilde; cười n&oacute;i: &ldquo;A Dữu, ch&aacute;u đi trước đi, b&agrave; đi sau.&rdquo;Qu&yacute; Dữu gật đầu: &ldquo;V&acirc;ng ạ.&rdquo;Trong bốn người, ngoại trừ Ryan ra, ch&uacute; Tạ Nghị, b&agrave; Jenny v&agrave; Qu&yacute; Dữu nguy&ecirc;n th&acirc;n đều l&agrave; d&acirc;n bản địa ở tinh cầu số 101, tổ ti&ecirc;n của họ đều cắm rễ ở tinh cầu n&agrave;y, mối quan hệ giữa họ rất quen thuộc n&ecirc;n thường xuy&ecirc;n hẹn nhau đi nhặt r&aacute;c, cũng l&agrave; để gi&uacute;p đỡ nhau dễ hơn.<br />\r\n<br />\r\nC&ograve;n Ryan? Năm nay Ryan l&agrave; người mới di d&acirc;n đến tinh cầu số 101 n&agrave;y, nghe n&oacute;i trước đ&acirc;y l&agrave; binh l&iacute;nh li&ecirc;n minh nhưng v&igrave; chấn thương n&ecirc;n xuất ngũ, c&ograve;n việc tại sao Ryan lại lựa chọn di d&acirc;n tới tinh cầu r&aacute;c số 101? L&yacute; do cụ thể th&igrave; kh&ocirc;ng ai biết.Ryan kh&ocirc;ng muốn n&oacute;i, h&agrave;ng x&oacute;m như họ cũng kh&ocirc;ng chủ động thăm d&ograve; b&iacute; mật của anh ta l&agrave;m g&igrave;.<br />\r\n<br />\r\nC&ograve;n nữa, từ khi kẻ c&oacute; vũ lực cường đại l&agrave; Ryan gia nhập đội ngũ, sự an to&agrave;n của mọi người c&agrave;ng được bảo đảm, bởi vậy mọi người cảm ơn rất nhều.Bốn người bước qua lớp chắn ph&ograve;ng hộ đi tới đống r&aacute;c thải, chia ra bốn g&oacute;c đi t&igrave;m kiếm.Ngoại khu ph&ograve;ng hộ c&oacute; ho&agrave;n cảnh &aacute;c liệt, c&aacute;ch một lớp dụng cụ ph&ograve;ng hộ vẫn c&oacute; thể ngửi được m&ugrave;i tanh tưởi bốc l&ecirc;n từ b&atilde;i r&aacute;c thải, Qu&yacute; Dữu c&uacute;i xuống, nh&iacute;u m&agrave;y, mở to đ&ocirc;i mắt cẩn thận t&igrave;m kiếm đồ vật hữu dụng&hellip;&hellip; Giống như t&agrave;i liệu cơ gi&aacute;p bị bỏ đi, linh kiện của người m&aacute;y gia dụng, đ&oacute; l&agrave; loại phế thải c&oacute; gi&aacute; trị nhất trong đống r&aacute;c, cũng l&agrave; mục ti&ecirc;u chủ yếu của mọi người.T&iacute; t&aacute;ch ~T&iacute; t&aacute;ch ~T&iacute; t&aacute;ch ~Thời gian lặng y&ecirc;n tr&ocirc;i qua, Qu&yacute; Dữu mất cả nửa ng&agrave;y vẫn kh&ocirc;ng t&igrave;m được nửa kiện phế phẩm hữu dụng khiến c&ocirc; cực kỳ thất vọng, sau khi ngồi xổm xuống nghỉ xả hơi trong chốc l&aacute;t mới tiếp tục cố gắng lần nữa.Đột nhi&ecirc;n trước mắt xuất hiện một chấm s&aacute;ng l&oacute;e l&ecirc;n, mắt Qu&yacute; Dữu ph&aacute;t s&aacute;ng, tưởng thứ g&igrave; tốt n&ecirc;n vội v&agrave;ng bước tới, sắp lại gần được th&igrave; đột nhi&ecirc;n cảm gi&aacute;c một tiếng x&eacute; gi&oacute; đ&aacute;nh &uacute;p về ph&iacute;a m&igrave;nh, Qu&yacute; Dữu thầm nghĩ kh&ocirc;ng tốt, muốn tr&aacute;nh đi nhưng kh&ocirc;ng kịp, c&aacute;nh tay của c&ocirc; bị con g&igrave; bất ngờ lao tới hung hăng cắn một c&aacute;i, may m&agrave; c&oacute; dụng cụ ph&ograve;ng hộ ngăn cản, cũng kh&ocirc;ng bị nứt vỡ, c&ugrave;ng l&uacute;c n&agrave;y, Ryan ở ngay cạnh kịp thời đuổi tới, anh ta giơ l&ecirc;n kiếm năng lượng trong tay, kh&ocirc;ng ch&uacute;t lưỡng lự g&igrave; lưu lo&aacute;t ch&eacute;m xuống, con vật tập k&iacute;ch qu&yacute; Dữu ng&atilde; vật xuống đất, bị ch&eacute;m th&agrave;nh 2 nửa chỉ trong nh&aacute;y mắt, tr&ecirc;n mặt đất chảy xu&ocirc;i m&aacute;u tươi m&agrave;u đen nh&aacute;nh c&oacute; &aacute;nh lục&hellip;&hellip;Đ&acirc;y l&agrave; ---Ryan xụ mặt n&oacute;i: &ldquo;Thực Hủ chuột cấp 1, cẩn thận một ch&uacute;t.&rdquo;***Thực Hủ chuột: Chuột ăn x&aacute;c thối.C&ograve;n may chỉ l&agrave; cấp 1, nếu l&agrave; cấp 3 trở l&ecirc;n th&igrave; h&agrave;m răng của ch&uacute;ng c&oacute; thể ph&aacute; n&aacute;t dụng cụ ph&ograve;ng hộ, l&uacute;c n&agrave;y Qu&yacute; Dữu cũng đừng nghĩ tới c&aacute;nh tay vẫn c&ograve;n tr&ecirc;n th&acirc;n.<br />\r\n<br />\r\nTrong l&ograve;ng c&ocirc; vẫn c&ograve;n sợ h&atilde;i, &aacute;nh mắt cảm k&iacute;ch n&oacute;i: &ldquo;Ryan, cảm ơn anh.&rdquo;Thực Hủ chuột c&oacute; bề ngo&agrave;i kh&aacute; giống với lo&agrave;i chuột ở thế kỷ 21 nhưng phần đầu rất lớn, trung b&igrave;nh một con đều nặng tr&ecirc;n dưới 5 c&acirc;n, l&agrave; lo&agrave;i tinh th&uacute; thường thấy nhất trong đống r&aacute;c, cơ thể ch&uacute;ng kh&ocirc;ng sợ ph&oacute;ng xạ, c&oacute; thể chống lại ho&agrave;n cảnh &aacute;c liệt, k&egrave;m theo năng lực sinh sản cực kỳ mạnh mẽ, hơn thế nữa, Thực Hủ chuột lấy năng lượng c&ograve;n s&oacute;t lại trong c&aacute;c nguy&ecirc;n liệu bị n&eacute;m v&agrave;o đống r&aacute;c l&agrave;m thức ăn, trước khi những người thường xuy&ecirc;n nhặt m&oacute;t t&igrave;m được phế phẩm c&oacute; gi&aacute; trị th&igrave; ch&uacute;ng đ&atilde; ăn xong mất rồi, ngo&agrave;i ra ch&uacute;ng n&oacute; c&ograve;n chủ động c&ocirc;ng k&iacute;ch con người, cho n&ecirc;n ch&uacute;ng l&agrave; một giống lo&agrave;i l&agrave;m người ta vừa đau đầu vừa ch&aacute;n gh&eacute;t.***1 c&acirc;n Trung Quốc = 596,8g n&ecirc;n 5 c&acirc;n sẽ rơi khoảng xấp xỉ 3kg nh&eacute;.<br />\r\n<br />\r\nBằng 1 em b&eacute; sơ sinh th&igrave; c&aacute;c bạn biết Thực Hủ chuột to thế n&agrave;o rồi &gt;..</p>', '0', 'di-nhat-rac-nao', 7, '786', '2022-08-03 06:03:50', '2022-08-03 06:03:50'),
(47, '2', 'Hộp Năng Lượng Bị Vứt', '<p>&ldquo;Th&iacute;m Jenny, th&iacute;m nhặt được hộp năng lượng n&agrave;y &iacute;t nhất cũng c&oacute; thể b&aacute;n được 400 điểm t&iacute;n dụng, so với th&iacute;m mất mấy th&aacute;ng vừa rồi b&aacute;n phế phẩm l&agrave; bằng nhau.<br />\r\n<br />\r\n&rdquo; Tạ Nghị kh&ocirc;ng nhịn được bước một bước nh&igrave;n chằm chằm v&agrave;o hộp năng lượng c&ugrave;ng Qu&yacute; Dữu.<br />\r\n<br />\r\nHộp năng lượng cấp 1 mới tinh gi&aacute; rao b&aacute;n tr&ecirc;n mạng l&agrave; 500, b&agrave; Jenny nhặt được c&aacute;i hộp n&agrave;y đưa đến cửa h&agrave;ng chuy&ecirc;n thu mua đồ second-hand, chủ cửa h&agrave;ng sẽ &eacute;p gi&aacute; một ch&uacute;t, đại kh&aacute;i sẽ &eacute;p xuống c&ograve;n 400 điểm t&iacute;n dụng.<br />\r\n<br />\r\nNhưng b&agrave; Jenny đ&atilde; thỏa m&atilde;n lắm rồi, nghe tiếng mọi người h&acirc;m mộ, b&agrave; li&ecirc;n tục khi&ecirc;m tốn n&oacute;i:&ldquo;May mắn th&ocirc;i.<br />\r\n<br />\r\n&rdquo;&ldquo;May mắn th&ocirc;i.<br />\r\n<br />\r\n&rdquo;&ldquo;May mắn m&agrave; th&ocirc;i.<br />\r\n<br />\r\n&rdquo;&ldquo;Vận may n&agrave;y kh&ocirc;ng tồi ch&uacute;t n&agrave;o.<br />\r\n<br />\r\n&rdquo; Ryan b&igrave;nh thường lu&ocirc;n b&igrave;nh tĩnh cũng n&oacute;i.<br />\r\n&nbsp;</p>', '<p>&ldquo;Th&iacute;m Jenny, th&iacute;m nhặt được hộp năng lượng n&agrave;y &iacute;t nhất cũng c&oacute; thể b&aacute;n được 400 điểm t&iacute;n dụng, so với th&iacute;m mất mấy th&aacute;ng vừa rồi b&aacute;n phế phẩm l&agrave; bằng nhau.<br />\r\n<br />\r\n&rdquo; Tạ Nghị kh&ocirc;ng nhịn được bước một bước nh&igrave;n chằm chằm v&agrave;o hộp năng lượng c&ugrave;ng Qu&yacute; Dữu.<br />\r\n<br />\r\nHộp năng lượng cấp 1 mới tinh gi&aacute; rao b&aacute;n tr&ecirc;n mạng l&agrave; 500, b&agrave; Jenny nhặt được c&aacute;i hộp n&agrave;y đưa đến cửa h&agrave;ng chuy&ecirc;n thu mua đồ second-hand, chủ cửa h&agrave;ng sẽ &eacute;p gi&aacute; một ch&uacute;t, đại kh&aacute;i sẽ &eacute;p xuống c&ograve;n 400 điểm t&iacute;n dụng.<br />\r\n<br />\r\nNhưng b&agrave; Jenny đ&atilde; thỏa m&atilde;n lắm rồi, nghe tiếng mọi người h&acirc;m mộ, b&agrave; li&ecirc;n tục khi&ecirc;m tốn n&oacute;i:&ldquo;May mắn th&ocirc;i.<br />\r\n<br />\r\n&rdquo;&ldquo;May mắn th&ocirc;i.<br />\r\n<br />\r\n&rdquo;&ldquo;May mắn m&agrave; th&ocirc;i.<br />\r\n<br />\r\n&rdquo;&ldquo;Vận may n&agrave;y kh&ocirc;ng tồi ch&uacute;t n&agrave;o.<br />\r\n<br />\r\n&rdquo; Ryan b&igrave;nh thường lu&ocirc;n b&igrave;nh tĩnh cũng n&oacute;i.<br />\r\n<br />\r\nB&agrave; Jenny được thu hoạch lớn lập tức kh&iacute;ch lệ người kh&aacute;c, đặc biệt l&agrave; Qu&yacute; Dữu v&agrave; ch&uacute; Tạ Nghị, hai người c&agrave;ng dụng t&acirc;m cẩn thận bới r&aacute;c, hận kh&ocirc;ng thể kh&ocirc;ng bu&ocirc;ng tha ch&uacute;t r&aacute;c n&agrave;o, nhưng thật đ&aacute;ng tiếc l&agrave; vận may đ&ecirc;m nay của hai người kh&ocirc;ng bu&ocirc;ng xuống, cuối c&ugrave;ng hai người chỉ nhặt được một &iacute;t phế phẩm gi&aacute; rẻ, đưa to&agrave;n bộ tới trạm thu hồi phế phẩm cũng chỉ lấy được khoảng 2 điểm t&iacute;n dụng m&agrave; th&ocirc;i.<br />\r\n<br />\r\nRyan c&oacute; cơ thể khỏe mạnh, trong tay lại c&oacute; vũ kh&iacute; ph&ograve;ng th&acirc;n, c&oacute; thể đi khu r&aacute;c xa hơn để t&igrave;m kiếm phế phẩm, hơn nữa trong tay anh ta c&ograve;n c&oacute; một n&uacute;t kh&ocirc;ng gian c&oacute; diện t&iacute;ch 20 lập phương c&oacute; thể chứa được rất nhiều đồ n&ecirc;n anh ta thu hoạch cũng kh&aacute;.<br />\r\n<br />\r\n<br />\r\nSau khi cất v&agrave;i m&oacute;n t&agrave;i liệu cấp thấp cơ gi&aacute;p v&agrave;o n&uacute;t kh&ocirc;ng gian, Ryan trầm ổn n&oacute;i: &ldquo;Sắp đến 8 giờ, thời điểm hoạt động mạnh nhất của Thực Hủ chuột sắp đến, trở về mau.<br />\r\n<br />\r\n&rdquo;Nghe thấy Ryan n&oacute;i, Qu&yacute; Dữu, ch&uacute; Tạ Nghị, b&agrave; Jenny kh&ocirc;ng gi&aacute;m nấn n&aacute;, tất cả đều dừng lại, thu thập đồ tốt chuẩn bị trở về.<br />\r\n<br />\r\nTrước khi đi, Qu&yacute; Dữu nhặt v&agrave;i hạt m&agrave; Thực Hủ chuột ăn c&ograve;n s&oacute;t lại nh&eacute;t v&agrave;o t&uacute;i m&igrave;nh.<br />\r\n<br />\r\nCh&uacute; Tạ Nghị kh&ocirc;ng nhịn được tr&ecirc;u chọc n&oacute;i: &ldquo;A Dữu, ch&aacute;u nhặt hạt Thảo Quả v&ocirc; dụng đ&oacute; l&agrave;m c&aacute;i g&igrave;?&rdquo;Hạt n&agrave;y được sinh ra từ lo&agrave;i cỏ dại mọc hoang ở ngoại khu bảo hộ trong tinh cầu số 101, Thảo Quả c&oacute; vị chua, ăn v&agrave;o sẽ l&agrave;m cho yết hầu t&ecirc; dại ngứa ng&aacute;y, c&ograve;n l&agrave;m con người n&ocirc;n mửa, ti&ecirc;u chảy&hellip;&hellip;Nhưng ăn sẽ kh&ocirc;ng g&acirc;y chết người.<br />\r\n<br />\r\nVị Thảo Quả qu&aacute; kh&oacute; ăn, đến ngay cả Thực Hủ chuột kh&ocirc;ng g&igrave; kh&ocirc;ng ăn cũng kh&ocirc;ng th&egrave;m, chỉ khi thật sự kh&ocirc;ng c&oacute; c&aacute;i g&igrave; để gặm th&igrave; Thực Hủ chuột mới chịu cắn một hai c&aacute;i, c&ograve;n hạt th&igrave; nhổ đi.<br />\r\n<br />\r\nLoại cỏ dại mọc hoang d&atilde; n&agrave;y ở nơi bỏ hoang thuộc khu bảo hộ cư d&acirc;n lại rất &iacute;t c&oacute;, v&igrave; kh&ocirc;ng c&oacute; gi&aacute; trị, cũng kh&ocirc;ng c&oacute; ai cố &yacute; ngắt lấy, muốn nhặt được hạt Thảo Quả c&ograve;n cần đến đ&aacute;m Thực Hủ chuột chọc người gh&eacute;t kia phụ tr&aacute;ch vận chuyển.<br />\r\n<br />\r\nChỉ l&agrave; những hạt n&agrave;y kh&ocirc;ng ai muốn n&ecirc;n cũng kh&ocirc;ng ai để &yacute; đến.<br />\r\n<br />\r\nQu&yacute; Dữu lấy to&agrave;n bộ hạt Thảo Quả cất đi, cười ha hả n&oacute;i: &ldquo;Ch&aacute;u thấy hạt Thảo Quả vừa tr&ograve;n vừa b&oacute;ng mịn, b&ecirc;n trong c&ograve;n trong suốt lấp l&aacute;nh, c&oacute; thể xe hạt ch&acirc;u nữa!&rdquo;&ldquo;Xe hạt ch&acirc;u?&rdquo;&ldquo;Đ&oacute; l&agrave; g&igrave;?&rdquo;Tạ Nghị kh&ocirc;ng hiểu lắm.<br />\r\n<br />\r\nQu&yacute; Dữu dơ tay l&ecirc;n khoe c&aacute;i lắc tr&ecirc;n cổ tay, n&oacute;i: &ldquo;Ch&iacute;nh l&agrave; c&aacute;i n&agrave;y.<br />\r\n<br />\r\n&rdquo;Sau khi Tạ Nghị nh&igrave;n thấy liền cười n&oacute;i: &ldquo;C&aacute;c ch&aacute;u đ&uacute;ng l&agrave; trẻ con, chỉ th&iacute;ch đồ vật m&agrave;u sắc sặc sỡ, kh&ocirc;ng thực dụng ch&uacute;t n&agrave;o.<br />\r\n<br />\r\n&rdquo;B&agrave; Jenny nghe xong ngược lại kh&ocirc;ng t&aacute;n đồng n&oacute;i: &ldquo;Hạt Thảo Quả n&agrave;y giản dị tự nhi&ecirc;n, ch&aacute;u xem A Dữu m&agrave;i giũa th&agrave;nh v&ograve;ng tay đẹp qu&aacute;, kh&ocirc;ng thực dụng chỗ n&agrave;o?&rdquo;Bản th&acirc;n l&agrave; một &ocirc;ng ch&uacute; trung ni&ecirc;n n&ecirc;n Tạ Nghị kh&ocirc;ng thưởng thức nổi, &ocirc;ng ta n&oacute;i: &ldquo;Đẹp th&igrave; l&agrave;m được g&igrave;? Kh&ocirc;ng thể ăn, cũng chẳng thế b&aacute;n lấy tiền&hellip;&hellip;&rdquo;Qu&yacute; Dữu vội v&agrave;ng giải th&iacute;ch: &ldquo;Ch&aacute;u t&iacute;nh l&agrave;m nhiều một ch&uacute;t, mở cửa tiệm nho nhỏ tr&ecirc;n Tinh V&otilde;ng, nếu may mắn c&oacute; thể b&aacute;n được th&igrave; sao?&rdquo;***Tinh V&otilde;ng: L&agrave; mạng internet, sau n&agrave;y d&ugrave;ng danh từ n&agrave;y rất nhiều m&agrave; để mỗi chữ mạng hoặc internet th&igrave; cứ thấy kỳ kỳ sao &yacute;, nhiều l&uacute;c để th&agrave;nh danh từ ri&ecirc;ng đứng một m&igrave;nh cộc lốc qu&aacute; n&ecirc;n chốt lại m&igrave;nh sẽ để Tinh V&otilde;ng nha.<br />\r\n<br />\r\nB&agrave; Jenny n&oacute;i: &ldquo;&Yacute; tưởng n&agrave;y tốt đấy.<br />\r\n<br />\r\n&rdquo;Tạ Nghị hắt ngay b&aacute;t nước lạnh n&oacute;i: &ldquo;Lắc tay của ch&aacute;u nh&igrave;n qua l&agrave; biết ngay l&agrave;m từ hạt Thảo Quả đ&acirc;u đ&acirc;u cũng thấy, kh&ocirc;ng c&oacute; t&aacute;c dụng hay c&ocirc;ng hiệu n&agrave;o kh&aacute;c, cũng kh&ocirc;ng tẩm bổ tinh thần lực được, theo ch&uacute; thấy sẽ kh&ocirc;ng ai mua đ&acirc;u.<br />\r\n<br />\r\n&rdquo;Ở thời đại tinh tế n&agrave;y, nh&acirc;n loại đều ch&uacute; &yacute; đến t&iacute;nh thực dụng l&agrave; tr&ecirc;n hết, d&ugrave; chỉ l&agrave; đồ trang tr&iacute; cũng y&ecirc;u cầu t&iacute;nh thực dụng mới được, ở trong mắt Tạ Nghị, hạt Thảo Quả n&agrave;y ch&iacute;nh l&agrave; đồ vứt đi, d&ugrave; Qu&yacute; Dữu đi&ecirc;u khắc m&agrave;i giũa đẹp đến đ&acirc;u th&igrave; cũng kh&ocirc;ng che giấu nổi sự thật n&oacute; l&agrave; hạt Thảo Quả kh&ocirc;ng đ&aacute;ng gi&aacute; tiền.<br />\r\n<br />\r\nN&agrave;y --- sẽ c&oacute; ai bỏ tiền ra mua đ&acirc;u?Qu&yacute; Dữu n&oacute;i: &ldquo;Ai, ch&aacute;u cứ thử xem sao, nếu c&oacute; người chịu mua l&agrave; kh&ocirc;ng thể tốt hơn.<br />\r\n<br />\r\n&rdquo; Kiếp trước c&ocirc; th&iacute;ch l&agrave;m đồ thủ c&ocirc;ng, bện d&acirc;y, lắc tay, đi&ecirc;u khắc, x&acirc;u hạt&hellip;&hellip; Thượng v&agrave;ng hạ c&aacute;m đều học qua một &iacute;t.<br />\r\n<br />\r\nĐến khi xuy&ecirc;n đến nơi đ&acirc;y, thật sự c&ocirc; kh&ocirc;ng t&igrave;m thấy đường ra n&ecirc;n giờ mới t&iacute;nh sử dụng kỹ năng của kiếp trước, mở shop online b&aacute;n mấy đồ chế t&aacute;c thủ c&ocirc;ng, kh&ocirc;ng nghĩ rằng sẽ bị ch&uacute; Tạ Nghị hắt lu&ocirc;n cả bồn nước lạnh.<br />\r\n<br />\r\nNhưng m&agrave; kh&ocirc;ng thử th&igrave; l&agrave;m sao biết sẽ thất bại đ&acirc;u?Qu&yacute; Dữu kh&ocirc;ng nghe khuy&ecirc;n bảo, cố chấp nhồi nh&eacute;t hạt Thảo Quả cho đến khi kh&ocirc;ng nh&eacute;t nổi nữa mới chịu ngừng.<br />\r\n<br />\r\n<br />\r\nTrong l&uacute;c mọi người n&oacute;i chuyện Ryan đều im lặng, kh&ocirc;ng tham gia thảo luận.<br />\r\n<br />\r\nRất nhanh ---Từ Tạ Nghị dẫn đầu, Ryan-vũ-lực-cường-đại bọc hậu, Qu&yacute; dữu c&ugrave;ng b&agrave; Jenny ở giữa, bốn người mở đường c&ugrave;ng nhau trở về.<br />\r\n<br />\r\nL&uacute;c n&agrave;y kh&ocirc;ng xuất hiện trở ngại g&igrave;, v&ocirc; kinh v&ocirc; hiểm theo một đường về tới khu bảo hộ.<br />\r\n<br />\r\nVừa tiến v&agrave;o lớp chắn ph&ograve;ng hộ, cả nh&oacute;m đều thở d&agrave;i nhẹ nh&otilde;m, Ryan đi trước trở về ph&ograve;ng m&igrave;nh.<br />\r\n<br />\r\nLập tức b&agrave; Jenny gọi anh ta lại v&agrave; n&oacute;i: &ldquo;Ryan, ch&aacute;u chờ một ch&uacute;t.<br />\r\n<br />\r\n&rdquo;Ryan dừng bước, quay người lại.<br />\r\n<br />\r\nB&agrave; Jenny nh&igrave;n anh ta, vẻ mặt đầy th&acirc;n thiết cười n&oacute;i: &ldquo;Ryan, d&ugrave; &iacute;t d&ugrave; nhiều ch&aacute;u chịu mang theo b&agrave; gi&agrave; n&agrave;y.<br />\r\n<br />\r\nBằng kh&ocirc;ng b&agrave; sao lại c&oacute; thu hoạch tốt như thế? B&agrave; l&agrave;m b&aacute;nh t&aacute;o nướng hương vị kh&ocirc;ng tệ đ&acirc;u, s&aacute;ng nay nướng cả một khay, ch&aacute;u từ từ đ&atilde;, b&agrave; đi lấy cho ch&aacute;u mấy c&aacute;i.<br />\r\n<br />\r\n&rdquo;Ryan lắc đầu n&oacute;i: &ldquo;Cảm ơn b&agrave;, ch&aacute;u kh&ocirc;ng cần.<br />\r\n<br />\r\n&rdquo;N&oacute;i xong liền nhấc ch&acirc;n đi.<br />\r\n<br />\r\nB&agrave; Jenny thấy anh ta đi c&agrave;ng l&uacute;c c&agrave;ng xa th&igrave; vội v&atilde; la l&ecirc;n: &ldquo;Ai nha! C&aacute;i thằng b&eacute; n&agrave;y, sao kh&ocirc;ng chịu nghe lời chứ?&rdquo;N&oacute;i xong, b&agrave; Jenny xoay người nh&igrave;n sang Qu&yacute; Dữu v&agrave; Tạ Nghị n&oacute;i: &ldquo;A nghị, A Dữu, hai đứa đợi ch&uacute;t, b&agrave; lấy cho c&aacute;c ch&aacute;u hai c&aacute;i b&aacute;nh t&aacute;o đ&atilde;.<br />\r\n<br />\r\n&rdquo;Qu&yacute; Dữu biết ng&agrave;y mai ch&aacute;u g&aacute;i Leah của b&agrave; Jenny sẽ từ trường học trở về, cho n&ecirc;n mỗi tuần b&agrave; sẽ nướng mấy c&aacute;i b&aacute;nh t&aacute;o cho Leah ăn.<br />\r\n<br />\r\n<br />\r\nQu&yacute; Dữu rất ngh&egrave;o nhưng c&ocirc; chỉ c&oacute; một m&igrave;nh, một người ăn no cả nh&agrave; kh&ocirc;ng đ&oacute;i, c&ograve;n nh&agrave; b&agrave; Jenny sống rất thanh bần, huống hồ b&agrave; cao tuổi như vậy c&ograve;n phải nu&ocirc;i Leah.<br />\r\n<br />\r\nQu&yacute; Dữu lắc đầu n&oacute;i: &ldquo;Ch&aacute;u kh&ocirc;ng cần đ&acirc;u b&agrave; Jenny ạ, nh&agrave; ch&aacute;u c&ograve;n thừa mấy chục lọ dinh dưỡng tề, ch&aacute;u đi về trước nh&eacute;.<br />\r\n<br />\r\n&rdquo;N&oacute;i xong, kh&ocirc;ng m&agrave;ng b&agrave; Jenny khuy&ecirc;n can, c&ocirc; vội v&atilde; quay về nh&agrave; m&igrave;nh.<br />\r\n<br />\r\nTạ Nghị đứng b&ecirc;n cạnh cũng như thế, cự tuyệt &yacute; tốt của th&iacute;m Jenny rồi trở về nh&agrave;.<br />\r\n<br />\r\nNh&igrave;n h&agrave;ng x&oacute;m của m&igrave;nh, b&agrave; Jenny chắp tay trước ngực bẩm bẩm c&acirc;u cảm ơn, trong l&ograve;ng vừa buồn cười vừa cảm k&iacute;ch.<br />\r\n<br />\r\nB&ecirc;n n&agrave;y.<br />\r\n<br />\r\nQu&yacute; Dữu đi một l&uacute;c mới về tới ph&ograve;ng nhỏ của m&igrave;nh, cả người mệt đến mức t&ecirc; liệt ng&atilde; xuống, c&ocirc; vuốt c&aacute;i bụng kh&ocirc; quắt, nghĩ đến l&uacute;c m&igrave;nh cự tuyệt &yacute; tốt của b&agrave; Jenny m&agrave; n&oacute;i c&acirc;u &lsquo;nh&agrave; ch&aacute;u c&ograve;n thừa mấy chục lọ dinh dưỡng tề&rsquo; th&igrave; c&ocirc; chỉ muốn kh&oacute;c to&aacute;ng l&ecirc;n.<br />\r\n<br />\r\nMấy chục c&aacute;i ở đ&acirc;u?Mấy chục c&aacute;i ở đ&acirc;u?Mấy chục c&aacute;i ở đ&acirc;u?Nhưng --- Ch&uacute;ng n&oacute; đều ở trong mơ nha.<br />\r\n<br />\r\nC&ocirc; cũng rất muốn c&oacute; m&agrave;.<br />\r\n&nbsp;</p>', '0', 'hop-nang-luong-bi-vut', 7, '677', '2022-08-03 06:04:29', '2022-08-03 06:04:29'),
(48, '3', 'Dinh Dưỡng Tề', '<p><br />\r\nNằm im như con c&aacute; ướp muối được một l&uacute;c, Qu&yacute; Dữu kh&ocirc;ng chỉ đ&oacute;i đến mức bụng s&ocirc;i &ugrave;ng ục m&agrave; c&ocirc; c&ograve;n cảm gi&aacute;c đầu m&igrave;nh c&agrave;ng l&uacute;c c&agrave;ng đau, rơi v&agrave;o đường c&ugrave;ng, c&ocirc; b&ograve; dậy lục tung ph&ograve;ng th&igrave; t&igrave;m được hai lọ dinh dưỡng tề.Trong tay Qu&yacute; Dữu l&agrave; hai lọ dinh dưỡng tề rẻ tiền nhất, được sản xuất gia c&ocirc;ng ph&acirc;n phối từ xưởng thực phẩm lớn nhất li&ecirc;n minh thuộc c&ocirc;ng ty &lsquo;Hoa Th&aacute;i&rsquo;, sản phẩm của c&ocirc;ng ty Hoa Th&aacute;i trải rộng c&aacute;c g&oacute;c trong li&ecirc;n minh n&ecirc;n rất dễ d&agrave;ng mua được, gi&aacute; b&aacute;n cũng rất rẻ, một lọ chỉ mất 2 điểm t&iacute;n dụng m&agrave; th&ocirc;i, l&agrave; thức ăn chủ yếu của đại bộ phận c&ocirc;ng d&acirc;n li&ecirc;n minh thuộc tầng ch&oacute;t khốn c&ugrave;ng nhất.H&igrave;nh d&aacute;ng của lọ dinh dưỡng tề hơi giống que kem ở thế kỷ 21</p>', '<p><br />\r\nNằm im như con c&aacute; ướp muối được một l&uacute;c, Qu&yacute; Dữu kh&ocirc;ng chỉ đ&oacute;i đến mức bụng s&ocirc;i &ugrave;ng ục m&agrave; c&ocirc; c&ograve;n cảm gi&aacute;c đầu m&igrave;nh c&agrave;ng l&uacute;c c&agrave;ng đau, rơi v&agrave;o đường c&ugrave;ng, c&ocirc; b&ograve; dậy lục tung ph&ograve;ng th&igrave; t&igrave;m được hai lọ dinh dưỡng tề.Trong tay Qu&yacute; Dữu l&agrave; hai lọ dinh dưỡng tề rẻ tiền nhất, được sản xuất gia c&ocirc;ng ph&acirc;n phối từ xưởng thực phẩm lớn nhất li&ecirc;n minh thuộc c&ocirc;ng ty &lsquo;Hoa Th&aacute;i&rsquo;, sản phẩm của c&ocirc;ng ty Hoa Th&aacute;i trải rộng c&aacute;c g&oacute;c trong li&ecirc;n minh n&ecirc;n rất dễ d&agrave;ng mua được, gi&aacute; b&aacute;n cũng rất rẻ, một lọ chỉ mất 2 điểm t&iacute;n dụng m&agrave; th&ocirc;i, l&agrave; thức ăn chủ yếu của đại bộ phận c&ocirc;ng d&acirc;n li&ecirc;n minh thuộc tầng ch&oacute;t khốn c&ugrave;ng nhất.H&igrave;nh d&aacute;ng của lọ dinh dưỡng tề hơi giống que kem ở thế kỷ 21, nhưng sau khi mở ra, b&ecirc;n trong c&oacute; m&agrave;u trắng như ch&aacute;o, chỉ cần uống một lọ chẳng kh&aacute;c g&igrave; ăn hai b&aacute;t cơm cỡ lớn, rất no, người d&acirc;n b&igrave;nh thường th&igrave; chỉ cần uống ba lọ một ng&agrave;y l&agrave; c&oacute; thể duy tr&igrave; hoạt động cả buổi.Dinh dưỡng tề chia l&agrave;m: Thấp cấp, trung cấp, cao cấp&hellip;&hellip; C&aacute;c cấp bậc được ph&acirc;n chia dựa theo nguy&ecirc;n liệu sản xuất v&agrave; khẩu vị, gi&aacute; một lọ dinh dưỡng tề trung cấp l&agrave; 50 điểm t&iacute;n dụng, c&ograve;n bậc cao cấp th&igrave; nghe n&oacute;i đều l&agrave;m từ nguy&ecirc;n liệu tự nhi&ecirc;n, một lọ dinh dưỡng tề cao cấp b&igrave;nh thường vị &lsquo;trứng sốt c&agrave; chua&rsquo; đ&atilde; c&oacute; gi&aacute; &iacute;t nhất l&agrave; 500 điểm t&iacute;n dụng, c&ograve;n những loại kh&aacute;c sử dụng nguy&ecirc;n liệu hiếm c&oacute; sản xuất ra dinh dưỡng t&ecirc; cao cấp gi&aacute; cả c&agrave;ng đắt hơn nhiều.Đương nhi&ecirc;n ---V&ocirc; luận l&agrave; trung cấp hay cao cấp th&igrave; Qu&yacute; Dữu cũng kh&ocirc;ng ăn nổi, chỉ c&oacute; thể ngước nh&igrave;n m&agrave; th&ocirc;i.Ai ~Đến nơi đ&acirc;y đ&atilde; được nửa năm m&agrave; đồ Qu&yacute; Dữu c&oacute; thể ăn chỉ c&oacute; dinh dưỡng tề thấp cấp, đương nhi&ecirc;n h&agrave;ng x&oacute;m của c&ocirc; cũng kh&ocirc;ng ngoại lệ, đồ ăn chủ yếu đều l&agrave; dinh dưỡng tề thấp cấp, cho n&ecirc;n đến b&acirc;y giờ c&ocirc; chưa tận mắt thấy được dinh dưỡng tề cao cấp kh&aacute;c thấp cấp ở chỗ n&agrave;o.Cuộc sống thường ng&agrave;y kh&oacute; khăn thật đấy nhưng nhất định kh&ocirc;ng thể để sinh hoạt mất đi quy luật được.Qu&yacute; Dữu chuyển chỗ ngồi l&ecirc;n ghế cạnh c&aacute;i b&agrave;n l&agrave;m bằng hợp kim rồi dọn hết b&aacute;t đũa tr&ecirc;n b&agrave;n đi, sau c&ugrave;ng để tr&ecirc;n b&agrave;n một đ&oacute;a hồng l&agrave;m bằng nhựa dẻo l&agrave;m điểm xuyết, thế l&agrave; nghi thức trước bữa cơm được ho&agrave;n th&agrave;nh rồi.Qu&yacute; Dữu t&igrave;m miền vui trong đau khổ m&agrave; o&aacute;n giận bản th&acirc;n: &ldquo;A~~~ m&igrave;nh đ&aacute;ng chết thật, kh&ocirc;ng c&ograve;n chỗ để đồ mới nha ~~~&rdquo;Sau đ&oacute; ---Qu&yacute; Dữu dơ tay l&ecirc;n chia lọ dinh dưỡng tề ra l&agrave;m hai, chỉ d&aacute;m d&ugrave;ng một nửa, nửa c&ograve;n lại c&ocirc; cẩn thận bọc k&iacute;n miệng lọ rồi để sang một b&ecirc;n, t&iacute;nh giữ lại để ng&agrave;y mai l&agrave;m bữa s&aacute;ng.Kh&ocirc;ng sai.Phải tiết kiệm như vậy.Ừ!C&aacute;i ngh&egrave;o n&oacute; bức đấy, v&igrave; để bớt chi ti&ecirc;u l&agrave; c&oacute; thể nghĩ ra được mọi biện ph&aacute;p h&agrave; khắc ch&iacute;nh m&igrave;nh.Nhưng rất nhanh c&ocirc; nh&iacute;u chặt m&agrave;y lại, khu&ocirc;n mặt trắng n&otilde;n nhăn th&agrave;nh c&aacute;i b&aacute;nh bao.N&agrave;y!N&agrave;y!Dinh dưỡng tề chết bằm n&agrave;y! D&ugrave; c&oacute; ăn biết bao nhi&ecirc;u lần, &iacute;t hay nhiều g&igrave; đi chăng nữa th&igrave; c&aacute;i vị chua x&oacute;t, tanh nồng, sền sệt vẫn khiến con người ta kh&oacute; nuốt tr&ocirc;i a.Tr&ecirc;n thế giới n&agrave;y sao c&oacute; thể tồn tại dinh dưỡng tề kh&oacute; nuốt vậy chứ?Rốt cuộc l&agrave; ai?L&agrave; c&aacute;i t&ecirc;n quỷ xấu xa n&agrave;o ph&aacute;t minh ra c&aacute;i đồ chơi n&agrave;y?Qu&aacute; t&agrave;n &aacute;c rồi, đ&uacute;ng l&agrave; ph&aacute;t minh hủy thi&ecirc;n diệt địa a!Qu&yacute; Dữu nh&eacute;t nửa lọ dinh dưỡng tề đang cầm tr&ecirc;n tay v&agrave;o miệng, to&agrave;n bộ đều nuốt v&agrave;o bụng rồi nhanh ch&oacute;ng cầm cốc nước uống sạch.Một cốc, một cốc nữa, uống liền ba cốc nước, hương vị cổ qu&aacute;i trong miệng c&ocirc; mới bớt đi nhiều.Thực ra, sở dĩ dinh dưỡng tề cấp thấp c&oacute; vị một-lời-kh&oacute;-n&oacute;i-hết như vậy cũng c&oacute; nguy&ecirc;n nh&acirc;n cả.<br />\r\n<br />\r\n<br />\r\nBởi v&igrave; bản th&acirc;n sản phẩm n&agrave;y x&aacute;c định l&agrave; d&ograve;ng rẻ tiền, được gọi l&agrave; thức ăn m&agrave; kẻ lưu lạc đầu đường cũng c&oacute; thể sử dụng.<br />\r\n<br />\r\nCho n&ecirc;n từ khi xuất hiện dinh dưỡng tề đến nay, gi&aacute; ban đầu của n&oacute; cũng chỉ c&oacute; 0.5 điểm t&iacute;n dụng, sau v&agrave;i trăm năm, d&ugrave; c&oacute; lạm ph&aacute;t đến mức n&agrave;o th&igrave; gi&aacute; trị của n&oacute; cũng chỉ từ 0.5 đến 2 điểm t&iacute;n dụng m&agrave; th&ocirc;i.<br />\r\n<br />\r\n<br />\r\nNếu b&agrave;n về vấn đề n&agrave;y, c&ocirc;ng ty thực phẩm muốn đảm bảo th&agrave;nh phần dinh dưỡng trong dinh dưỡng tề kh&ocirc;ng bị x&oacute;i m&ograve;n, lại muốn tận lực hạ thấp chi ph&iacute; th&igrave; chỉ c&oacute; thể hy sinh m&ugrave;i vị.Kỳ thật, qua nhiều năm nghi&ecirc;n cứu, vị của dinh dưỡng tề cấp thấp so với vị của sản phẩm mới ra đời đầu đ&atilde; tốt hơn rất nhiều.<br />\r\n<br />\r\n<br />\r\nM&ugrave;i vị đời đầu đ&oacute; mới đ&uacute;ng l&agrave; khủng bố.Ngược lại, c&oacute; thể sử dụng sản phẩm rẻ tiền để lấp đầy bụng, kh&ocirc;ng đến mức l&agrave;m cả nh&agrave; gi&agrave; trẻ đ&oacute;i chết đầu đường, đ&ocirc;ng đảo c&ocirc;ng d&acirc;n li&ecirc;n minh tầng ch&oacute;t khốn c&ugrave;ng tỏ vẻ rằng họ c&oacute; thể mong mỏi được g&igrave; đ&acirc;y?Vị?Kh&ocirc;ng c&oacute; đ&acirc;u.Chỉ cần gi&aacute; thấp một ch&uacute;t l&agrave; tốt rồi, thấp hơn đương nhi&ecirc;n tốt hơn.Nhưng trong li&ecirc;n minh tinh tế, c&ocirc;ng ty thực phẩm chuy&ecirc;n sản xuất dinh dưỡng tề thấp cấp d&ugrave; lớn d&ugrave; b&eacute; đều kh&ocirc;ng &iacute;t, trong đ&oacute; c&oacute; ba c&ocirc;ng ty lớn chiếm định mức chủ yếu l&agrave;: Hoa Th&aacute;i, Ch&iacute;nh Hưng, Mỹ Vị Giai.<br />\r\n<br />\r\nBa c&ocirc;ng ty cạnh tranh với nhau khiến cho vị của dinh dưỡng tề thấp cấp kh&ocirc;ng ngừng được cải tiến nhưng tr&ecirc;n hết vẫn duy tr&igrave; được dinh dưỡng c&ugrave;ng chi ph&iacute;, &iacute;t nhất th&igrave; vị hiện tại kh&ocirc;ng c&ograve;n chua x&oacute;t v&agrave; tanh nồng nữa.Nhưng m&agrave; ---Từ 50 năm trước, Hoa Th&aacute;i tr&egrave;o l&ecirc;n được một vị quan lớn c&oacute; chức vị quan trọng trong li&ecirc;n minh, sau khi lũng đoạn to&agrave;n bộ thị trường dinh dưỡng tề thấp cấp th&igrave; vị của n&oacute; kh&ocirc;ng c&ograve;n được cải tiến nữa.Thời đại trăm hoa đua nở ch&iacute;nh thức kết th&uacute;c từ đ&acirc;y.C&ocirc;ng ty thực phẩm Ch&iacute;nh Hưng biến mất, đổi h&igrave;nh thức kinh doanh sang c&ocirc;ng ty nghi&ecirc;n cứu t&agrave;i liệu cơ gi&aacute;p.Mỹ Vị Giai c&ograve;n tồn tại nhưng chỉ tận sức nghi&ecirc;n cứu dinh dưỡng tề trung cấp v&agrave; cao cấp, từ bỏ ho&agrave;n to&agrave;n thị trường thấp cấp.C&aacute;c c&ocirc;ng ty nhỏ kh&aacute;c th&igrave; sao? Đ&atilde; sớm bị mai t&aacute;ng ở một g&oacute;c x&oacute; xỉnh n&agrave;o rồi.C&ocirc;ng d&acirc;n li&ecirc;n minh vừa y&ecirc;u vừa hận t&ecirc;n đầu sỏ c&ocirc;ng ty thực phẩm Hoa Th&aacute;i.Y&ecirc;u?L&agrave; bởi v&igrave; c&ocirc;ng ty đ&oacute; c&oacute; rất nhiều sản phẩm gi&aacute; rẻ, chỉ cần trả &iacute;t tiền l&agrave; c&oacute; thể duy tr&igrave; cuộc sống cho cả nh&agrave;.Hận?Đương nhi&ecirc;n l&agrave; v&igrave; c&ocirc;ng ty kh&ocirc;ng c&oacute; ch&uacute;t tiến thủ, mấy chục năm qua khẩu vị của sản phẩm đinh &lsquo;dinh dưỡng tề thấp cấp&rsquo; kh&ocirc;ng hề được cải tiến, thậm ch&iacute; v&igrave; để hạ thấp ph&iacute; tổn m&agrave; nguy&ecirc;n vật liệu đầu v&agrave;o c&agrave;ng k&eacute;m hơn trước dẫn tới khẩu vị c&agrave;ng ng&agrave;y c&agrave;ng kh&oacute; ăn.Hoa Th&aacute;i l&agrave;m đầu sỏ, chiếm cứ to&agrave;n bộ thị trường dinh dưỡng tề thấp cấp, h&agrave;nh vi cực kỳ ngạo mạn, tự đại, kh&ocirc;ng chịu nghe d&acirc;n ch&uacute;ng kiến nghị, ho&agrave;n to&agrave;n tự m&igrave;nh m&igrave;nh l&agrave;m ---&hellip;&hellip;Qu&yacute; Dữu nh&iacute;u chặt m&agrave;y, mới ăn xong dinh dưỡng tề c&ocirc; liền lập tức muốn tế! --- đại kh&aacute;i c&oacute; thể coi đ&oacute; l&agrave; một trong những t&aacute;c dụng phụ của thức ăn khủng bố.Hừ hừ ~Ngay sau đ&oacute; c&ocirc; đăng nhập Tinh V&otilde;ng, nhanh ch&oacute;ng v&agrave;o website ch&iacute;nh thức của Hoa Th&aacute;i xem b&igrave;nh luận ph&iacute;a dưới của dinh dưỡng tề thấp cấp, khi thấy mấy lời khen ngợi &iacute;t ỏi c&ugrave;ng với đ&aacute;nh soa b&igrave;nh k&egrave;m b&igrave;nh luận k&ecirc;u ca vị kh&oacute; ăn, Qu&yacute; Dữu đang định đ&aacute;nh soa b&igrave;nh th&igrave; ngừng lại, c&ocirc; nổi l&ecirc;n &yacute; xấu đầy th&iacute;ch th&uacute;, c&ocirc; đ&aacute;nh năm sao khen ngợi, cũng thổi cầu vồng th&iacute;: [Đ&acirc;y l&agrave; dinh dưỡng tề thần ti&ecirc;n n&agrave;o vậy, t&ocirc;i uống xong lập tức thấy trước mắt v&ocirc; số h&igrave;nh người b&eacute; nhỏ đang bay bay, bọn ho đang ca h&aacute;t nhảy m&uacute;a&hellip;&hellip; Bọn họ hoan h&ocirc;, nhảy nh&oacute;t, ch&uacute;c mừng&hellip;&hellip; Chẳng lẽ t&ocirc;i th&agrave;nh ti&ecirc;n rồi hả? Ngay tại đ&acirc;y t&ocirc;i muốn cảm ơn Hoa Th&aacute;i, cảm ơn c&ocirc;ng ty đ&atilde; l&agrave;m t&ocirc;i cảm nhận được sự sung sướng như s&eacute;t đ&aacute;nh, l&agrave;m t&ocirc;i biết đồ ăn đ&aacute;ng qu&yacute; biết bao, sau khi ăn xong dinh dưỡng tề thấp cấp c&ograve;n c&oacute; thể tồn tại thật qu&aacute; tốt a ~~~]***Đ&aacute;nh Soa b&igrave;nh: đ&aacute;nh gi&aacute; một thương phẩm n&agrave;o đ&oacute; chất lượng k&eacute;m bằng c&aacute;ch cho &iacute;t sao, như đ&aacute;nh gi&aacute; 1-2 sao chẳng hạn.***Cầu vồng th&iacute;: nghĩa như vỗ m&ocirc;ng ngựa vậy, &yacute; của c&acirc;u l&agrave; nịnh h&oacute;t, lấy l&ograve;ng người kh&aacute;c, v&igrave; đạt được mục đ&iacute;ch m&agrave; m&ugrave; qu&aacute;ng khen ngợi để họ th&iacute;ch m&igrave;nh sau đ&oacute; đạt được mục đ&iacute;ch.Sau khi Qu&yacute; Dữu viết b&igrave;nh luận xong lập tức tho&aacute;t ra.<br />\r\n<br />\r\n<br />\r\nC&ocirc; kh&ocirc;ng ph&aacute;t hiện ra vừa rời đi 1 gi&acirc;y sau, dưới b&igrave;nh luận của c&ocirc; xuất hiện v&agrave;i phản hồi, v&igrave; c&ocirc; b&igrave;nh luận qu&aacute; kỳ lạ n&ecirc;n số người phản hồi c&agrave;ng ng&agrave;y c&agrave;ng nhiều, xuất hiện cả những lời tr&ecirc;u chọc.Phản hồi 01: [T&uacute; nhi, T&uacute; nhi l&agrave; em sao?]Phản hồi 02: [Tr&acirc;u, cậu thật sự rất tr&acirc;u.]Phản hồi 03: [Hoa Th&aacute;i: Hừ! m&agrave;y cứ việc im lặng đi! Tao liếc nh&igrave;n m&agrave;y một c&aacute;i coi như tao thua!]Phản hồi 04: [Hoa Th&aacute;i: Kh&ocirc;ng sai nga ~ dinh dưỡng tề nh&agrave; ch&uacute;ng ta khiến t&ocirc;i kh&ocirc;ng ngừng nh&igrave;n thấy h&igrave;nh người nhỏ b&eacute;, ăn xong c&ograve;n c&oacute; thể biến th&agrave;nh h&igrave;nh người bay bay nha.]Phản hồi 05: [Ph&aacute;o năng lượng của tao vừa mới g&aacute;c l&ecirc;n, mẹ n&oacute;! L&agrave;m hại &ocirc;ng đ&acirc;y thiếu ch&uacute;t nữa bắn sai người! Đội trưởng! Đ&acirc;y l&agrave; qu&acirc;n đội bạn!]Phản hồi 06: [Ch&uacute; cảnh s&aacute;t ơi: Ch&iacute;nh l&agrave; người n&agrave;y! L&agrave; c&ocirc; ta! L&agrave; c&ocirc; ta hại t&ocirc;i cũng thấy v&ocirc; số h&igrave;nh người b&eacute; nhỏ đang nhảy m&uacute;a kh&ocirc;ng ngừng, xua m&atilde;i kh&ocirc;ng hết&hellip;&hellip;]&hellip;&hellip;.<br />\r\n&nbsp;</p>', '0', 'dinh-duong-te', 7, '2427', '2022-08-03 06:08:26', '2022-08-03 06:08:26');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(49, '4', 'Đánh Soa Bình', '<p>Tinh V&otilde;ng.Sau khi Qu&yacute; Dữu vừa rời khỏi website ch&iacute;nh thức của Hoa Th&aacute;i th&igrave; lập tức trở về cửa tiệm nhỏ của m&igrave;nh, đầu ti&ecirc;n l&agrave; đi xem hậu đ&agrave;i, vừa thấy, cửa tiệm đăng b&aacute;n năm lắc tay l&agrave;m từ hạt Thảo Quả vẫn được b&agrave;y c&ocirc;ng khai ở đ&oacute;, c&oacute; vẻ đặc biệt ch&oacute;i lọi bắt mắt.Qu&yacute; Dữu lại kiểm tra lượng người đ&atilde; xem: [11]11 người!Đăng b&aacute;n được một tuần!Năm chiếc lắc tay l&agrave;m từ hạt Thảo Quả n&agrave;y c&ocirc; mất c&ocirc;ng m&agrave;i giũa nửa th&aacute;ng, để lắc tay nh&igrave;n c&agrave;ng đẹp c&agrave;ng s&aacute;ng b&oacute;ng hơn, tay của c&ocirc; sắp bị m&agrave;i r&aacute;ch đến nơi, kết quả đăng b&aacute;n một thời gian vẫn chỉ c&oacute; 11 người xem.11 người a.Qu&yacute; Dữu tuyệt vọng trong nh&aacute;y mắt.Quả nhi&ecirc;n ---Ch&uacute; Tạ Nghị n&oacute;i kh&ocirc;ng sai, đồ chơi n&agrave;y kh&ocirc;ng c&oacute; ai chịu kh&oacute; thưởng thức sao?Bản th&acirc;n Qu&yacute; Dữu l&agrave; &lsquo;l&atilde;o đại&rsquo; trong giới thủ c&ocirc;ng, giờ đ&acirc;y t&ocirc;n nghi&ecirc;m đều bị mặt mũi qu&eacute;t sạch.</p>', '<p><br />\r\nTinh V&otilde;ng.Sau khi Qu&yacute; Dữu vừa rời khỏi website ch&iacute;nh thức của Hoa Th&aacute;i th&igrave; lập tức trở về cửa tiệm nhỏ của m&igrave;nh, đầu ti&ecirc;n l&agrave; đi xem hậu đ&agrave;i, vừa thấy, cửa tiệm đăng b&aacute;n năm lắc tay l&agrave;m từ hạt Thảo Quả vẫn được b&agrave;y c&ocirc;ng khai ở đ&oacute;, c&oacute; vẻ đặc biệt ch&oacute;i lọi bắt mắt.Qu&yacute; Dữu lại kiểm tra lượng người đ&atilde; xem: [11]11 người!Đăng b&aacute;n được một tuần!Năm chiếc lắc tay l&agrave;m từ hạt Thảo Quả n&agrave;y c&ocirc; mất c&ocirc;ng m&agrave;i giũa nửa th&aacute;ng, để lắc tay nh&igrave;n c&agrave;ng đẹp c&agrave;ng s&aacute;ng b&oacute;ng hơn, tay của c&ocirc; sắp bị m&agrave;i r&aacute;ch đến nơi, kết quả đăng b&aacute;n một thời gian vẫn chỉ c&oacute; 11 người xem.11 người a.Qu&yacute; Dữu tuyệt vọng trong nh&aacute;y mắt.Quả nhi&ecirc;n ---Ch&uacute; Tạ Nghị n&oacute;i kh&ocirc;ng sai, đồ chơi n&agrave;y kh&ocirc;ng c&oacute; ai chịu kh&oacute; thưởng thức sao?Bản th&acirc;n Qu&yacute; Dữu l&agrave; &lsquo;l&atilde;o đại&rsquo; trong giới thủ c&ocirc;ng, giờ đ&acirc;y t&ocirc;n nghi&ecirc;m đều bị mặt mũi qu&eacute;t sạch.<br />\r\n<br />\r\nKiếp trước, ngẫu nhi&ecirc;n hứng l&ecirc;n l&agrave; c&ocirc; sẽ l&ecirc;n mạng ph&aacute;t s&oacute;ng trực tiếp cảnh c&ocirc; xe hạt ch&acirc;u, mỗi lần ph&aacute;t s&oacute;ng l&agrave; c&oacute; rất nhiều fans v&acirc;y xem.Một khi t&aacute;c phẩm ho&agrave;n th&agrave;nh liền b&aacute;n hết ngay.C&ocirc; vẫn lu&ocirc;n khẳng định l&agrave; tay nghề của m&igrave;nh ở thời đại n&agrave;y sẽ c&oacute; người thưởng thức, nhưng m&agrave; --- Sự thật vả mặt c&aacute;i bốp đầy lạnh lẽo a.Khi Qu&yacute; Dữu c&uacute;i đầu, bỗng nhi&ecirc;n nh&igrave;n tho&aacute;ng qua b&ecirc;n dưới lắc tay thế m&agrave; c&oacute; một b&igrave;nh luận.C&ocirc; tập trung nh&igrave;n, su&yacute;t nữa tức đến mức kh&ocirc;ng thở nổi, chỉ thấy b&igrave;nh luận viết: [Ngay cả hạt Thảo Quả cũng mang đi b&aacute;n, th&egrave;m tiền đến mức đi&ecirc;n rồi hả?]Ph&iacute;a dưới b&igrave;nh luận c&ograve;n bị đ&aacute;nh gi&aacute; một sao!Một sao!Lập tức Qu&yacute; Dữu kh&ocirc;ng h&iacute;t thở được.Đối với tiệm mới mở m&agrave; n&oacute;i, ngay một sản phẩm c&ograve;n kh&ocirc;ng b&aacute;n ra đ&atilde; bị đ&aacute;nh gi&aacute; một sao quả thực ch&iacute;nh l&agrave; sự đả k&iacute;ch tr&iacute; mạng.<br />\r\n<br />\r\n<br />\r\nĐiều n&agrave;y sẽ l&agrave;m tỷ lệ xuất hiện cửa tiệm mới mở của c&ocirc; trong h&agrave;ng tỷ cửa tiệm nhiều như biển hạ xuống thẳng tắp.Bị đ&aacute;nh gi&aacute; một sao sẽ kh&ocirc;ng ngừng hạ thấp tỷ lệ xuất hiện, cũng sẽ ảnh hưởng tới cảm nhận của kh&aacute;ch lần đầu đến tiệm.Ngo&agrave;i ra ---C&ograve;n c&oacute; ---Như l&agrave; ---&hellip;&hellip;Đủ loại hậu quả xấu --- Quả thực đ&oacute; l&agrave; nỗi đau m&agrave; Qu&yacute; Dữu kh&ocirc;ng thể chấp nhận được.Tay Qu&yacute; Dữu run l&ecirc;n, trước khi đ&oacute;ng cửa tiệm, mấy lần c&ocirc; muốn ấn xuống n&uacute;t hạ gi&aacute; lắc tay nhưng đều do dự, sau năm lần bảy lượt, Qu&yacute; Dữu khẽ cắn m&ocirc;i, dứt kho&aacute;t đ&oacute;ng cửa tiệm, quyết định nhắm mắt l&agrave;m ngơ.C&ograve;n tiệm của m&igrave;nh c&ugrave;ng với lắc tay treo tr&ecirc;n kệ h&agrave;ng cứ kệ đi, để ch&uacute;ng n&oacute; chờ người c&oacute; duy&ecirc;n mua vậy.&hellip;&hellip;Ra khỏi đại lộ thương nghiệp, Qu&yacute; Dữu trở về gia vi&ecirc;n của m&igrave;nh.N&oacute;i đến Tinh V&otilde;ng thời đại n&agrave;y.Kh&ocirc;ng giống với mạng internet ở thế kỷ 21, mạng internet ở thời đại tinh tế l&agrave; thực tế ảo hư cấu bắt chước thế giới thực, n&oacute; được coi l&agrave; thế giới tinh tế thứ hai, gần giống với thế giới thật nhưng r&otilde; r&agrave;ng vẫn c&oacute; sự kh&aacute;c biệt.Mỗi một c&ocirc;ng d&acirc;n tinh tế, dựa v&agrave;o chip th&acirc;n phận d&ugrave;ng tinh thần lực của m&igrave;nh đăng nhập Tinh V&otilde;ng, đ&acirc;y l&agrave; t&agrave;i khoản duy nhất.<br />\r\n<br />\r\nV&igrave; để hạ thấp tuần suất phạm tội kh&ocirc;ng tăng l&ecirc;n, Tinh V&otilde;ng cưỡng chế thi h&agrave;nh hệ thống d&ugrave;ng t&ecirc;n thật, từ khi sinh ra mỗi người sẽ được trang bị một t&agrave;i khoản duy nhất kh&ocirc;ng thể sửa đổi, nhưng bạn c&oacute; thể che giấu họ t&ecirc;n thật của m&igrave;nh, cho ph&eacute;p bạn lấy nickname m&agrave; bản th&acirc;n muốn.Nickname của Qu&yacute; Dữu gọi l&agrave; Thanh Dứu, cải bi&ecirc;n từ t&ecirc;n của c&ocirc;.Sau khi tiến v&agrave;o Tinh V&otilde;ng, mỗi người đều c&oacute; gia vi&ecirc;n thuộc sở hữu của bản th&acirc;n, t&ograve;a gia vi&ecirc;n n&agrave;y bắt chước nh&agrave; cửa v&agrave; s&acirc;n vườn ở thế giới thực, bạn c&oacute; thể nghỉ ngơi, c&ocirc;ng t&aacute;c hoặc nu&ocirc;i th&uacute; cưng trong gia vi&ecirc;n, đương nhi&ecirc;n nh&agrave;u gi&agrave;u c&oacute; tiền kh&ocirc;ng chỗ ti&ecirc;u cũng c&oacute; thể mua đồ trang tr&iacute; trang ho&agrave;ng gia vi&ecirc;n của m&igrave;nh.Gia vi&ecirc;n của mỗi người đều độc lập, t&iacute;nh bảo mật cực cao, kh&ocirc;ng c&oacute; chủ nh&agrave; cho mời l&agrave; người ngo&agrave;i ko được đi v&agrave;o.Sau gia vi&ecirc;n l&agrave; tuyến đường ch&iacute;nh trong Tinh V&otilde;ng, tuyến đường ch&iacute;nh n&agrave;y kết nối v&ocirc; số tuyến đường kh&aacute;c, đường n&agrave;y th&ocirc;ng bốn phương t&aacute;m hướng, c&oacute; đường th&ocirc;ng tới khu thương nghiệp, khu hoang d&atilde;, khu chiến đấu&hellip;&hellip;Ngo&agrave;i ra, Tinh V&otilde;ng dựa tr&ecirc;n sinh hoạt của nh&acirc;n loại phạm vi to&agrave;n tinh tế, phai chia bốn khu vực l&agrave; Đ&ocirc;ng, Nam, T&acirc;y, Bắc.Khu vực Qu&yacute; Dữu đang ở thuộc khu Đ&ocirc;ng.<br />\r\n<br />\r\n<br />\r\nCửa tiệm c&ocirc; mở ở hẻm số 66 đại lộ thương nghiệp số 188 khu Đ&ocirc;ng, l&agrave; tiệm bu&ocirc;n b&aacute;n nhỏ rộng chưa đến 10 m&eacute;t.Tinh V&otilde;ng ph&aacute;t cho mỗi c&ocirc;ng d&acirc;n tinh tế gia vi&ecirc;n tư nh&acirc;n độc lập, ở đ&acirc;y kh&ocirc;ng bao gồm cửa tiệm, n&oacute;i c&aacute;ch kh&aacute;c gia vi&ecirc;n tư nh&acirc;n kh&ocirc;ng thể d&ugrave;ng để l&agrave;m ăn bu&ocirc;n b&aacute;n, v&iacute; dụ như mở cửa tiệm, bu&ocirc;n b&aacute;n h&agrave;ng h&oacute;a&hellip;&hellip;C&ocirc;ng d&acirc;n muốn bu&ocirc;n b&aacute;n cần đến Tinh V&otilde;ng xin mở cửa tiệm, sau khi xin th&agrave;nh c&ocirc;ng, y&ecirc;u cầu mỗi th&aacute;ng đều phải giao nộp tiền thu&ecirc; mặt bằng c&ugrave;ng tiền thuế cho Vinh V&otilde;ng.<br />\r\n<br />\r\nMỗi một c&ocirc;ng d&acirc;n đều c&oacute; thể hưởng thụ một lần ch&iacute;nh s&aacute;ch ưu đ&atilde;i khi mở tiệm, cửa tiệm mới mở c&oacute; thể được một năm miễn thuế, miễn tiền thu&ecirc; phục vụ, cho ph&eacute;p cửa tiệm mới mở v&agrave;o trong danh s&aacute;ch gia tăng 10% tỉ lệ xuất hiện trong 3 th&aacute;ng.10% tỉ lệ xuất hiện n&agrave;y cực kỳ quan trọng đối với cửa tiệm mới mở, thậm ch&iacute; c&oacute; li&ecirc;n quan đến sự tồn vong của tiệm.Đương nhi&ecirc;n, nếu tiệm mới mở l&agrave;m tr&aacute;i với quy định ph&aacute;p luật tinh tế hoặc bị đ&aacute;nh soa b&igrave;nh với số lượng nhất định cũng sẽ hạ thấp tỉ lệ xuất hiện, t&igrave;nh huống nghi&ecirc;m trọng sẽ ra lệnh cưỡng chế đ&oacute;ng cửa.<br />\r\n<br />\r\n<br />\r\nĐ&acirc;y cũng l&agrave; nguy&ecirc;n do cho việc Qu&yacute; Dữu để &yacute; đến vụ đ&aacute;nh gi&aacute; một sao như vậy.<br />\r\n<br />\r\nBởi v&igrave; kh&ocirc;ng c&oacute; tiền, kh&ocirc;ng xin được cửa tiệm tốt n&ecirc;n tiệm nhỏ n&agrave;y của c&ocirc; phải mở trong một g&oacute;c x&oacute; xỉnh hẻo l&aacute;nh, tỉ lệ xuất hiện vốn kh&ocirc;ng cao, giờ chịu ảnh hưởng của c&aacute;i đ&aacute;nh gi&aacute; k&eacute;m b&igrave;nh n&agrave;y th&igrave; tỉ lệ xuất hiện sẽ hạ thấp hơn&hellip;&hellip;Hậu quả --- l&agrave; nỗi đau m&agrave; Qu&yacute; Dữu kh&ocirc;ng thể chịu đựng được.Đến kỳ hạn một năm, hưởng ch&iacute;nh s&aacute;ch ph&uacute;c lợi được miễn thuế, miễn thu&ecirc; đều hết hiệu lực, nếu Qu&yacute; Dữu kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o sinh lời th&igrave; chỉ c&oacute; thể ngậm ng&ugrave;i đ&oacute;ng cửa tiệm.Trở lại gia vi&ecirc;n tư nh&acirc;n, bị đ&aacute;nh soa b&igrave;nh ảnh hưởng, Qu&yacute; Dữu ngồi xổm trong g&oacute;c, cuộn tr&ograve;n th&acirc;n thể, t&acirc;m t&igrave;nh cực kỳ nặng nề.Thời đại tinh tế n&agrave;y thật sự kh&ocirc;ng dễ lăn lộn.Ban đầu tưởng rằng c&ocirc; c&oacute; thể xuy&ecirc;n đến nơi đ&acirc;y liền khẳng định cầm được kịch bản nữ chủ, thế nhưng --- cho đến hiện tại, Qu&yacute; Dữu đều kh&ocirc;ng nh&igrave;n thấy b&agrave;n tay v&agrave;ng xuất hiện, kh&ocirc;ng chỉ kh&ocirc;ng c&oacute; b&agrave;n tay v&agrave;ng, v&igrave; sinh tồn c&ocirc; kh&ocirc;ng thể kh&ocirc;ng đi đ&agrave;o r&aacute;c trong khu đổ r&aacute;c thải&hellip;&hellip;***B&agrave;n tay v&agrave;ng: &aacute;m chỉ nh&acirc;n vật được t&aacute;c giả thiết kế giỏi to&agrave;n diện, t&ugrave;y tiện l&agrave; c&oacute; thể v&ocirc; địch thi&ecirc;n hạ, c&oacute; n&oacute; sẽ được buff, buff m&atilde;i, kh&ocirc;ng c&oacute; c&aacute;i n&agrave;y sẽ c&oacute; c&aacute;i kh&aacute;c, t&oacute;m lại c&oacute; b&agrave;n tay v&agrave;ng l&agrave; c&oacute; tất cả.Trừ việc nhặt r&aacute;c ra, c&ocirc; c&ograve;n sử dụng to&agrave;n bộ kỹ năng của kiếp trước như xe hạt ch&acirc;u, tết d&acirc;y thừng&hellip;&hellip; Kết quả? To&agrave;n bộ đều kh&ocirc;ng c&oacute; hiệu quả.Trong l&ograve;ng vẫn c&ograve;n x&oacute;t xa th&igrave; cơn đau đầu của Qu&yacute; Dữu lại xuất hiện, c&ocirc; day day tr&aacute;n một l&uacute;c mới hết đau, sau đ&oacute; &aacute;nh mắt lấp l&oacute;e nh&igrave;n chiếc r&igrave;u để chổng ngược bị n&eacute;m trong g&oacute;c gia vi&ecirc;n.Qu&yacute; Dữu tỉnh t&aacute;o đ&ocirc;i ch&uacute;t rồi đi đến, c&uacute;i người nhặt chiếc r&igrave;u l&ecirc;n chuẩn bị bắt đầu c&ocirc;ng t&aacute;c cu li Tinh V&otilde;ng.Cu li Tinh V&otilde;ng được ch&iacute;nh phủ li&ecirc;n minh chuy&ecirc;n m&ocirc;n thiết kế nhằm v&agrave;o những c&ocirc;ng d&acirc;n thiếu hụt thể chất v&agrave; tinh thần lực để tạo c&ocirc;ng ăn việc l&agrave;m.<br />\r\n<br />\r\n<br />\r\nN&oacute; kh&ocirc;ng y&ecirc;u cầu cứng nhắc cần phải c&oacute; thể lực hoặc tinh thần lực, cũng kh&ocirc;ng c&oacute; bất kỳ h&agrave;m lượng kỹ thuật n&agrave;o, bạn chỉ cần ti&ecirc;u tốn thời gian của bạn l&agrave; c&oacute; thể kiếm đủ điểm t&iacute;n dụng, hơi giống với x&acirc;y gạch ở thế kỷ 21.Qu&yacute; Dữu ở Tinh V&otilde;ng l&agrave;m c&ocirc;ng t&aacute;c cu li đ&oacute; ch&iacute;nh l&agrave; bổ cọc gỗ, đương nhi&ecirc;n r&igrave;u v&agrave; cọc gỗ đều l&agrave; giả thuyết, c&ograve;n sau mỗi lần Qu&yacute; Dữu vung r&igrave;u l&ecirc;n ra sức bổ cọc gỗ th&igrave; năng lương sinh ra khi c&ocirc; h&agrave;nh động lại ch&acirc;n thật, m&agrave; hệ thống Tinh V&otilde;ng c&oacute; c&ocirc;ng cụ chuy&ecirc;n m&ocirc;n hấp thu năng lương sinh ra n&agrave;y, mỗi lần bổ cọc gỗ l&agrave; hệ thống tự động hấp thu một lần, sau khi bổ mười lần gom lại c&oacute; thể hấp thu 1 điểm năng lượng.Mỗi ph&uacute;t &iacute;t nhất Qu&yacute; Dữu c&oacute; thể b&ocirc;t cọc gỗ mười lần, l&agrave;m việc 1 giờ ước chừng c&oacute; thẻ thu thập được 60 điểm năng lượng, cho n&ecirc;n hiệu suất c&ocirc;ng việc của c&ocirc; kh&ocirc;ng tồi ch&uacute;t n&agrave;o.Đương nhi&ecirc;n, cu li Tinh V&otilde;ng kh&ocirc;ng chỉ c&oacute; bổ cọc gỗ m&agrave; c&ograve;n c&oacute; m&agrave;i đao, đốn củi, dọn gạch&hellip;&hellip; Đủ loại h&igrave;nh c&ocirc;ng việc, tuy bề ngo&agrave;i kh&aacute;c nhau nhưng nguy&ecirc;n l&yacute; thu thập năng lượng lại giống nhau.Chỉ l&agrave; Qu&yacute; Dữu tương đối th&iacute;ch biện ph&aacute;p đơn giản cho n&ecirc;n chọn loại h&igrave;nh th&ocirc; bạo l&agrave; bổ cọc gỗ.Hệ thống Tinh V&otilde;ng: [Qu&yacute; c&ocirc; đ&aacute;ng k&iacute;nh: Xin hỏi b&acirc;y giờ c&oacute; thể bắt đầu c&ocirc;ng t&aacute;c được chưa? C&ocirc;ng t&aacute;c lần n&agrave;y sẽ trả c&ocirc;ng theo giờ l&agrave; 2 điểm t&iacute;n dụng/h, chưa vượt qua 1 giờ việc thu thập năng lượng thiếu 50 điểm, kh&ocirc;ng đ&aacute;ng thống k&ecirc;.]Ngang ngược qu&aacute;!Thời gian c&ocirc;ng t&aacute;c thấp hơn 1 giờ th&igrave; hệ thống thu thập năng lượng thiếu 50 điểm liền kh&ocirc;ng đ&aacute;ng t&iacute;nh lương, kh&ocirc;ng phải điều khoản ngang ngược th&igrave; l&agrave; g&igrave;?Điều n&agrave;y l&agrave;m nội t&acirc;m của Qu&yacute; Dữu thật sự kh&oacute; chịu, c&ocirc; nhớ tới kiếp trước từng nghe qua một c&acirc;u, người ngh&egrave;o d&ugrave;ng thời gian đổi lấy tiền bạc, người gi&agrave;u c&oacute; d&ugrave;ng tiền bạc đổi lấy thời gian&hellip;&hellip; Hiện giờ cạnh c&ocirc; chẳng c&oacute; vật dư thừa g&igrave;, cũng kh&ocirc;ng nổi một xu d&iacute;nh t&uacute;i, chỉ c&oacute; thời gian l&agrave; nhiều, đ&agrave;nh phải d&ugrave;ng thời gian đi kiếm mấy đồng bạc vụn vậy.Ai ~Qu&yacute; Dữu bất đắc dĩ n&oacute;i: &ldquo;Bắt đầu.&rdquo;Hệ thống Tinh V&otilde;ng: [Sau 10 gi&acirc;y bắt đầu thu thập.<br />\r\n<br />\r\nCh&uacute;c qu&yacute; c&ocirc; c&oacute; buổi l&agrave;m việc vui vẻ!]Tiếp theo, [10, 9, 8, 7, 6&hellip;&hellip;] sau khi kết th&uacute;c đếm ngược, Qu&yacute; Dữu lập tức n&acirc;ng r&igrave;u hung hăng bổ xuống một c&aacute;i cọc gỗ, trong chốc l&aacute;t cọc gỗ chia l&agrave;m hai phần.Bổ được một cọc, tiếp đến cọc thứ hai.Cọc thứ ba.Cọc thứ tư.Cọc thứ năm.&hellip;&hellip;Bổ mệt rồi liền nghỉ ngơi một ch&uacute;t rồi tiếp tục l&agrave;m.Lặp đi lặp lại.V&ograve;ng đi v&ograve;ng lại.Sinh hoạt của c&ocirc; tầm thường như vậy đấy, qu&aacute; b&igrave;nh thường, dường như kh&ocirc;ng nh&igrave;n thấy nổi một điểm hy vọng n&agrave;o..</p>', '0', 'danh-soa-binh', 7, '747', '2022-08-03 06:08:55', '2022-08-03 06:08:55'),
(50, '5', 'Làm Cu Li Cho Tinh Võng', '<p><br />\r\nLeng keng ---[Qu&yacute; c&ocirc; đ&atilde; c&ocirc;ng t&aacute;c được 1 giờ, đ&atilde; th&agrave;nh c&ocirc;ng thu thập được 60 điểm năng lượng, trong đ&oacute; đ&atilde; vượt qua ngạch độ cơ bản: 10 điểm, t&iacute;ch lũy: 2.2 điểm t&iacute;n dụng, tiền c&ocirc;ng lần n&agrave;y l&agrave; 2.2 điểm t&iacute;n dụng đ&atilde; tự động chuyển v&agrave;o t&agrave;i khoản, xin hỏi qu&yacute; c&ocirc; c&oacute; tiếp tục c&ocirc;ng t&aacute;c nữa hay kh&ocirc;ng?]Nghe được tiếng n&oacute;i nhắc nhở của hệ thống, Qu&yacute; Dữu bu&ocirc;ng r&igrave;u, dơ tay lau mồ h&ocirc;i, c&ocirc; cảm thấy hơi mệt, cảm gi&aacute;c bản th&acirc;n kh&oacute; c&oacute; thể ki&ecirc;n tr&igrave; c&ocirc;ng t&aacute;c th&ecirc;m một giờ liền n&oacute;i: &ldquo;Kh&ocirc;ng.&rdquo;Hệ thống: [Ch&uacute;c qu&yacute; c&ocirc; c&oacute; một buổi tối vui vẻ!]Sau khi hệ thống đ&oacute;ng cửa giao diện c&ocirc;ng t&aacute;c, những cọc gỗ rơi rụng trong s&acirc;n m&agrave; Qu&yacute; Dữu d&ugrave;ng r&igrave;u bổ ra nh&aacute;y mắt biến mất to&agrave;n bộ.Vừa rồi l&agrave;m cu li một giờ đồng hồ kiếm lời được 2.2 điểm t&iacute;n dụng, Qu&yacute; Dữu xem danh ngạch c&ograve;n trống trong t&agrave;i khoản của m&igrave;nh, hiển thị tổng cộng chỉ c&oacute; 152.2 điểm t&iacute;nh dụng, c&ocirc; kh&ocirc;ng khỏi muốn nhảy dựng.Đ&acirc;y l&agrave; to&agrave;n bộ t&agrave;i sản của c&ocirc;.Ngh&egrave;o qu&aacute;.Ch&iacute;nh l&agrave; ---C&oacute; biện ph&aacute;p n&agrave;o đ&acirc;u?To&agrave;n bộ thời đại tinh tế muốn c&oacute; c&ocirc;ng t&aacute;c sở hữu tiền lương cao th&igrave; to&agrave;n bộ đều c&oacute; li&ecirc;n quan đến thể chất c&ugrave;ng tinh thần lực, th&acirc;n thể của Qu&yacute; Dữu hiện giờ: Thể chất cấp E, tinh thần lực cấp F.</p>', '<p>Leng keng ---[Qu&yacute; c&ocirc; đ&atilde; c&ocirc;ng t&aacute;c được 1 giờ, đ&atilde; th&agrave;nh c&ocirc;ng thu thập được 60 điểm năng lượng, trong đ&oacute; đ&atilde; vượt qua ngạch độ cơ bản: 10 điểm, t&iacute;ch lũy: 2.2 điểm t&iacute;n dụng, tiền c&ocirc;ng lần n&agrave;y l&agrave; 2.2 điểm t&iacute;n dụng đ&atilde; tự động chuyển v&agrave;o t&agrave;i khoản, xin hỏi qu&yacute; c&ocirc; c&oacute; tiếp tục c&ocirc;ng t&aacute;c nữa hay kh&ocirc;ng?]Nghe được tiếng n&oacute;i nhắc nhở của hệ thống, Qu&yacute; Dữu bu&ocirc;ng r&igrave;u, dơ tay lau mồ h&ocirc;i, c&ocirc; cảm thấy hơi mệt, cảm gi&aacute;c bản th&acirc;n kh&oacute; c&oacute; thể ki&ecirc;n tr&igrave; c&ocirc;ng t&aacute;c th&ecirc;m một giờ liền n&oacute;i: &ldquo;Kh&ocirc;ng.&rdquo;Hệ thống: [Ch&uacute;c qu&yacute; c&ocirc; c&oacute; một buổi tối vui vẻ!]Sau khi hệ thống đ&oacute;ng cửa giao diện c&ocirc;ng t&aacute;c, những cọc gỗ rơi rụng trong s&acirc;n m&agrave; Qu&yacute; Dữu d&ugrave;ng r&igrave;u bổ ra nh&aacute;y mắt biến mất to&agrave;n bộ.Vừa rồi l&agrave;m cu li một giờ đồng hồ kiếm lời được 2.2 điểm t&iacute;n dụng, Qu&yacute; Dữu xem danh ngạch c&ograve;n trống trong t&agrave;i khoản của m&igrave;nh, hiển thị tổng cộng chỉ c&oacute; 152.2 điểm t&iacute;nh dụng, c&ocirc; kh&ocirc;ng khỏi muốn nhảy dựng.Đ&acirc;y l&agrave; to&agrave;n bộ t&agrave;i sản của c&ocirc;.Ngh&egrave;o qu&aacute;.Ch&iacute;nh l&agrave; ---C&oacute; biện ph&aacute;p n&agrave;o đ&acirc;u?To&agrave;n bộ thời đại tinh tế muốn c&oacute; c&ocirc;ng t&aacute;c sở hữu tiền lương cao th&igrave; to&agrave;n bộ đều c&oacute; li&ecirc;n quan đến thể chất c&ugrave;ng tinh thần lực, th&acirc;n thể của Qu&yacute; Dữu hiện giờ: Thể chất cấp E, tinh thần lực cấp F.<br />\r\n<br />\r\n<br />\r\nD&ugrave;ng hai chữ để h&igrave;nh dung đ&oacute; l&agrave; &lsquo;phế t&agrave;i&rsquo;.Như vậy về cơ bản Qu&yacute; Dữu phế t&agrave;i kh&ocirc;ng c&oacute; biện ph&aacute;p t&igrave;m được c&ocirc;ng việc c&oacute; lương cao ph&uacute;c lợi tốt, đ&acirc;y cũng l&agrave; nguy&ecirc;n nh&acirc;n c&ocirc; xuy&ecirc;n tới đ&acirc;y hơn nửa năm vẫn phải đi nhặt r&aacute;c v&agrave; l&agrave;m cu li cho Tinh V&otilde;ng.Dừng việc bổ cọc gỗ xong, Qu&yacute; Dữu ngồi trong nh&agrave; nhỏ, đun một b&igrave;nh tr&agrave; cho m&igrave;nh rồi nghỉ ngơi trong chốc l&aacute;t, ban đầu c&ograve;n t&iacute;nh đi dạo loanh quanh tr&ecirc;n Tinh V&otilde;ng nhưng h&ocirc;m nay nhặt r&aacute;c mất mấy giờ đồng hồ, bị hoảng sợ, shop online lại bị đ&aacute;nh gi&aacute; một sao, c&ograve;n nữa, c&ocirc; cứ cảm thấy đầu đau hơn.<br />\r\n<br />\r\nV&igrave; nhiều nguy&ecirc;n nh&acirc;n gộp lại khiến thể x&aacute;c v&agrave; tinh thần của Qu&yacute; Dữu đều mệt mỏi n&ecirc;n t&iacute;nh sẽ hạ tuyến sớm.Trở lại thế giới thực.Mặt cỏ v&agrave; hoa tươi, ong mật v&agrave; bươn bướm, trời xanh v&agrave; m&acirc;y trắng, c&ograve;n c&oacute; căn nh&agrave; nhỏ b&eacute; ấm &aacute;p của c&ocirc;&hellip;&hellip; To&agrave;n bộ kh&ocirc;ng thấy.<br />\r\n<br />\r\nThế giới thực chỉ c&oacute; căn nh&agrave; lạnh như băng thiết kế theo ti&ecirc;u chuẩn của tinh tế, c&oacute; ba ph&ograve;ng: một ph&ograve;ng kh&aacute;ch, một ph&ograve;ng bếp, một nh&agrave; vệ sinh, ngoại trừ trang tr&iacute; cơ bản c&ugrave;ng đồ thiết yếu ra cũng kh&ocirc;ng c&oacute; bất luận trang tr&iacute; dư thừa n&agrave;o, cũng --- kh&ocirc;ng c&oacute; cảm gi&aacute;c gia đ&igrave;nh.Qu&yacute; Dữu thương t&acirc;m.Kiếp trước c&ocirc; l&agrave; c&ocirc; nhi, c&ograve;n đời n&agrave;y cha mẹ nguy&ecirc;n chủ ly thế l&uacute;c c&ocirc; được t&aacute;m tuổi, l&agrave; nh&agrave; b&aacute;c cả của nguy&ecirc;n chủ &eacute;p buộc nhận nu&ocirc;i, thuận tiện nhận lu&ocirc;n t&agrave;i sản lưu lại của cha mẹ nguy&ecirc;n chủ.Giống như những kẻ mang danh họ h&agrave;ng lại c&oacute; &yacute; đồ xấu, nh&agrave; b&aacute;c cả cũng l&agrave; họ h&agrave;ng cực phẩm, sau khi nắm trong tay di sản bọn họ trở mặt gh&eacute;t bỏ nguy&ecirc;n chủ, thường ng&agrave;y t&igrave;m c&aacute;ch khắt khe c&ocirc;, t&ugrave;y &yacute; để anh chị họ đ&aacute;nh đập c&ocirc;, khiến nguy&ecirc;n chủ kh&ocirc;ng chịu nổi trộm chạy khỏi nh&agrave; b&aacute;c cả, về nh&agrave; của m&igrave;nh.Nh&agrave; b&aacute;c cả kh&ocirc;ng chỉ kh&ocirc;ng chủ động đ&oacute;n c&ocirc; trở về m&agrave; cứ gặp người l&agrave; n&oacute;i nguy&ecirc;n chủ kh&ocirc;ng biết cảm ơn, n&oacute;i c&ocirc; khi ở nh&agrave; thường hay c&aacute;u kỉnh, c&atilde;i v&atilde; với anh chị họ, nếu Qu&yacute; Dữu muốn tự ở ri&ecirc;ng th&igrave; giao lại ch&igrave;a kh&oacute;a nh&agrave; cho c&ocirc;, để c&ocirc; tự ở một m&igrave;nh đi.V&igrave; thế ---Nh&agrave; của nguy&ecirc;n chủ lại trở về tr&ecirc;n tay của nguy&ecirc;n chủ, chỉ l&agrave; --- tất cả đồ vật đ&aacute;ng gi&aacute; trong nh&agrave; bị nh&agrave; b&aacute;c cả dọn sạch, chỉ để lại cho Qu&yacute; Dữu một căn nh&agrave; trống rỗng.Bởi vậy, từ khi mười tuổi nguy&ecirc;n chủ đ&atilde; học được c&aacute;ch tự sinh hoạt .T&iacute;nh c&aacute;ch của nguy&ecirc;n chủ hướng nội, kh&ocirc;ng th&iacute;ch giao tiếp với người b&ecirc;n ngo&agrave;i, trừ những người h&agrave;ng x&oacute;m c&ocirc; quen biết ra, bạn b&egrave; của c&ocirc; rất &iacute;t, người c&oacute; thể thổ lộ t&igrave;nh cảm c&agrave;ng &iacute;t hơn.Tiếp theo.Cuộc sống bất hạnh tiếp tục bao phủ lấy c&ocirc; b&eacute; nhỏ yếu đ&aacute;ng thương, năm nay c&ocirc; mới tr&ograve;n 18 tuổi, cũng tại năm n&agrave;y c&ocirc; đ&atilde; tốt nghiệp trung học gi&aacute;o dục bắt buộc được miễn ph&iacute; của li&ecirc;n minh, khi thi l&ecirc;n cấp, c&ocirc; tr&uacute;ng tuyển v&agrave;o hệ t&agrave;i liệu cơ gi&aacute;p ở L&atilde;m Nguyệt tinh, nhưng khi thư th&ocirc;ng b&aacute;o tốt nghiệp c&ograve;n chưa gửi đi, bạn học tốt nhất của c&ocirc; l&agrave; Từ Tư Vũ c&aacute;o buộc c&ocirc; ăn cắp th&agrave;nh quả của m&igrave;nh.Từ Tư Vũ đưa ra một phần b&aacute;o c&aacute;o nghi&ecirc;n cứu của m&igrave;nh mong muốn được gi&aacute;m định, phần đề &aacute;n nghi&ecirc;n cứu n&agrave;y, Từ Tư Vũ sớm đưa ra trước Qu&yacute; Dữu một năm v&agrave; cũng đ&atilde; tiến h&agrave;nh nghi&ecirc;n cứu tương quan, nhanh một bước c&ocirc;ng khai trước Qu&yacute; Dữu, dưới c&aacute;c bằng chứng đ&oacute;, c&aacute;o buộc của Từ Tư Vũ được th&agrave;nh lập, th&agrave;nh t&iacute;ch của Qu&yacute; Dữu bị hủy bỏ.C&ograve;n Từ Tư Vũ thay thế Qu&yacute; Dữu trở th&agrave;nh t&acirc;n sinh hệ t&agrave;i liệu cơ gi&aacute;p của L&atilde;m Nguyệt tinh.Ch&acirc;n tướng l&agrave; g&igrave;?L&agrave; Từ Tư Vũ dựa v&agrave;o quan hệ với nguy&ecirc;n chủ, chủ động hỏi han &yacute; tưởng của nguy&ecirc;n chủ, đ&aacute;nh cắp th&agrave;nh quả của nguy&ecirc;n chủ, cũng nhanh ch&acirc;n c&ocirc;ng khai ra ngo&agrave;i.Nhưng dưới những bằng chứng như n&uacute;i, ph&aacute;p luật c&ugrave;ng ph&aacute;p quy, gi&aacute;o vi&ecirc;n c&ugrave;ng bạn học&hellip;&hellip; To&agrave;n bộ đều đứng về ph&iacute;a Từ Tư Vũ, Qu&yacute; Dữu hết đường chối c&atilde;i, dưới sự bất lực v&agrave; tuyệt vọng, v&agrave;o một đ&ecirc;m an tĩnh c&ocirc; dứt kho&aacute;t kết th&uacute;c sinh mệnh của m&igrave;nh.L&uacute;c sau, Qu&yacute; Dữu ở thế kỷ 21 xuy&ecirc;n lại đ&acirc;y.&hellip;&hellip;Qu&yacute; Dữu &ocirc;m ngực than nhẹ một tiếng: &ldquo;L&agrave; c&ocirc; sao? C&ocirc; --- c&ograve;n thấy khổ sở sao?&rdquo;Rất kỳ qu&aacute;i, mỗi khi Qu&yacute; Dữu nghĩ đến nguy&ecirc;n nh&acirc;n nguy&ecirc;n chủ tự s&aacute;t, Qu&yacute; Dữu liền cảm thấy phần ngực của th&acirc;n thể n&agrave;y kh&ocirc;ng hiểu sao co r&uacute;t đau đớn.<br />\r\n<br />\r\n<br />\r\nCho n&ecirc;n c&ocirc; lớn mật ho&agrave;i nghi bị &yacute; thức của nguy&ecirc;n chủ ảnh hưởng tới.Hoặc l&agrave; ---Nguy&ecirc;n chủ vẫn chưa rời khỏi th&acirc;n thể n&agrave;y?Qu&yacute; Dữu nhẹ giọng hỏi: &ldquo;Nếu c&ocirc; c&ograve;n ở mời c&ocirc; n&oacute;i cho t&ocirc;i l&agrave;m thế n&agrave;o mới c&oacute; thể trả lại th&acirc;n thể n&agrave;y lại cho c&ocirc;?&rdquo;C&acirc;u n&oacute;i đ&oacute; đều l&agrave; thật, Qu&yacute; Dữu kh&ocirc;ng muốn chiếm cơ thể của người kh&aacute;c, chẳng sợ cơ thể n&agrave;y nguy&ecirc;n chủ chủ động từ bỏ, nếu c&oacute; khả năng c&ocirc; cũng kh&ocirc;ng muốn chiếm lấy nhưng c&ocirc; xuy&ecirc;n qua đ&acirc;y cũng mơ mơ m&agrave;ng m&agrave;ng, căn bản cũng kh&ocirc;ng biết xảy ra chuyện g&igrave;.Trước khi xuy&ecirc;n qua, tuy rằng Qu&yacute; Dữu chỉ l&agrave; c&ocirc; nhi nhưng t&iacute;nh c&aacute;ch của c&ocirc; rất cứng cỏi, cũng kh&ocirc;ng cảm thấy m&igrave;nh tự ti hay c&ocirc; độc, ngược lại, c&ocirc; nỗ lực nắm lấy mọi thứ m&agrave; học tập, t&igrave;m cơ hội hướng về ph&iacute;a trước, th&ocirc;ng qua sự nỗ lực kh&ocirc;ng ngừng, Qu&yacute; Dữu kh&ocirc;ng chỉ phấn đấu mua cho m&igrave;nh căn hộ ở khu th&agrave;nh thị an ổn nh&agrave; cửa m&agrave; c&ograve;n dư dả gi&uacute;p đỡ c&aacute;c b&eacute; ở c&ocirc; nhi viện n&agrave;ng từng sống đi học&hellip;&hellip;Bởi v&igrave; c&ocirc; biết được tầm quan trọng của việc học tập kiến thức n&ecirc;n Qu&yacute; Dữu sinh hoạt rất tiết kiệm, gần như tiền c&ocirc; kiếm được đều d&ugrave;ng để gi&uacute;p đỡ bọn nhỏ học tập, cho n&ecirc;n căn bản c&ocirc; kh&ocirc;ng rảnh nghĩ đến chuyện y&ecirc;u đương, m&atilde;i cho đến l&uacute;c xuy&ecirc;n qua, c&ocirc; vẫn l&agrave; nữ thanh ni&ecirc;n lớn tuổi chưa lập gia đ&igrave;nh.<br />\r\n<br />\r\nNhưng Qu&yacute; Dữu vẫn kh&ocirc;ng sốt ruột v&igrave; c&ocirc; tin v&agrave;o duy&ecirc;n phận, tương lai nửa kia của c&ocirc; kh&ocirc;ng xuất hiện bởi v&igrave; thời cơ chưa tới m&agrave; th&ocirc;i ---Qu&yacute; Dữu rất thỏa m&atilde;n với sinh hoạt của m&igrave;nh.Nếu c&oacute; thể c&ocirc; thật sự kh&ocirc;ng muốn xuy&ecirc;n qua.Lại n&oacute;i tiếp, Qu&yacute; Dữ xuy&ecirc;n qua bằng c&aacute;ch n&agrave;o? C&ocirc; nhớ r&otilde; h&ocirc;m đ&oacute; c&ocirc; nghĩ đến gần đ&acirc;y th&agrave;nh t&iacute;ch học tập của một số bạn nhỏ trong c&ocirc; nhi viện giảm xuống, trong l&ograve;ng v&ocirc; c&ugrave;ng sốt ruột n&ecirc;n c&ocirc; đi mua một số dụng cụ học tập v&agrave; đề luyện thi cho bọn nhỏ, trong khi đi đường th&igrave; đột nhi&ecirc;n bị thiết phiến tr&ecirc;n một t&ograve;a cao ốc đập tr&uacute;ng đầu, sau khi Qu&yacute; Dữu bị đ&aacute;nh tr&uacute;ng chỉ cảm thấy mắt tối sầm rồi lập tức h&ocirc;n m&ecirc;, khi tỉnh lại th&igrave; đ&atilde; xuy&ecirc;n đến tr&ecirc;n th&acirc;n thể của c&ocirc; b&eacute; đ&atilde; tự s&aacute;t v&igrave; tuyệt vọng bất lực.***Thiết phiến: nghĩa l&agrave; khối sắt.<br />\r\n<br />\r\n<br />\r\nM&igrave;nh để thế sẽ hay hơn l&agrave; để thuần việt v&igrave; sau n&agrave;y sẽ d&ugrave;ng tới từ n&agrave;y kh&aacute; nhiều.Từ việc kh&ocirc;ng c&ugrave;ng tuổi với nguy&ecirc;n chủ ra, hai người kh&ocirc;ng chỉ tr&ugrave;ng t&ecirc;n họ m&agrave; ngay cả d&aacute;ng người hay diện mạo đều ho&agrave;n to&agrave;n giống nhau, điều n&agrave;y khiến Qu&yacute; Dữu cực kỳ kinh ngạc v&agrave; ngạc nhi&ecirc;n, nhưng cứ việc bề ngo&agrave;i giống nhau, Qu&yacute; Dữu vẫn biết th&acirc;n thể n&agrave;y kh&ocirc;ng phải l&agrave; của m&igrave;nh, người n&agrave;y cũng kh&ocirc;ng phải l&agrave; ch&iacute;nh m&igrave;nh.Sau khi Qu&yacute; Dữu n&oacute;i xong, tr&aacute;i tim co r&uacute;t đau đớn đột nhi&ecirc;n tạm dừng một t&iacute;ch tắc, l&aacute;t sau tim đang ngừng đập đột nhi&ecirc;n kh&ocirc;ng hiểu sao run rẩy, c&ocirc; cảm thấy trời đất quay cuồng ---.</p>\r\n\r\n<p>&nbsp;</p>', '0', 'lam-cu-li-cho-tinh-vong', 7, '557', '2022-08-03 06:09:36', '2022-08-03 06:09:36'),
(51, '6', 'Thiết Phiến Ở Trong Đầu', '<p>Trời đất trước mặt như quay cuồng, Qu&yacute; Dữu thầm nghĩ kh&ocirc;ng tốt, chỉ cảm thấy hai đầu gối gập lại khiến cả người lảo đảo một l&uacute;c, thiếu ch&uacute;t nữa đ&acirc;m v&agrave;o b&agrave;n ăn, dưới t&igrave;nh thế cấp b&aacute;ch, c&ocirc; nắm lấy tay vịn b&ecirc;n cạnh mới khiến cơ thể đứng vững.<br />\r\n<br />\r\nSao lại thế n&agrave;y?Đang &ecirc;m đẹp th&igrave; tim đập kh&ocirc;ng b&igrave;nh thường, đầu vốn chỉ đau ch&uacute;t th&ocirc;i thế nhưng trong nh&aacute;y mắt đau đớn tăng đột ngột.<br />\r\n<br />\r\nChẳng lẽ ---Nguy&ecirc;n chủ đang muốn đoạt lại chủ quyền cơ thể sao?Cho c&ocirc;.<br />\r\n<br />\r\nCho c&ocirc;.<br />\r\n<br />\r\nĐừng đoạt.<br />\r\n<br />\r\nTrả lại cho c&ocirc; đấy.<br />\r\n<br />\r\n&hellip;&hellip;Nhưng m&agrave; ---Đợi một l&uacute;c l&acirc;u, cơ thể Qu&yacute; Dữu vẫn kh&ocirc;ng nh&uacute;c nh&iacute;ch, trong linh hồn cũng kh&ocirc;ng cảm nhận được bất kỳ lạ thường n&agrave;o, c&oacute; vẻ c&ocirc; đo&aacute;n sai rồi th&igrave; phải, cũng kh&ocirc;ng phải nguy&ecirc;n chủ muốn cướp lại th&acirc;n thể m&agrave; chỉ l&agrave; tim đập nhanh kh&aacute;c thường th&ocirc;i.<br />\r\n<br />\r\nQu&yacute; Dữu ngồi xổm tr&ecirc;n s&agrave;n nh&agrave; rất mất h&igrave;nh tượng, lưng dựa v&agrave;o v&aacute;ch tường, c&ocirc; kh&ocirc;ng ngừng h&iacute;t thở&hellip;&hellip; Cố gắng kh&ocirc;ng chế tần suất tim đập một c&aacute;ch bất thường, thật l&acirc;u sau tim mới đập chậm lại như cũ.<br />\r\n&nbsp;</p>', '<p>Trời đất trước mặt như quay cuồng, Qu&yacute; Dữu thầm nghĩ kh&ocirc;ng tốt, chỉ cảm thấy hai đầu gối gập lại khiến cả người lảo đảo một l&uacute;c, thiếu ch&uacute;t nữa đ&acirc;m v&agrave;o b&agrave;n ăn, dưới t&igrave;nh thế cấp b&aacute;ch, c&ocirc; nắm lấy tay vịn b&ecirc;n cạnh mới khiến cơ thể đứng vững.<br />\r\n<br />\r\nSao lại thế n&agrave;y?Đang &ecirc;m đẹp th&igrave; tim đập kh&ocirc;ng b&igrave;nh thường, đầu vốn chỉ đau ch&uacute;t th&ocirc;i thế nhưng trong nh&aacute;y mắt đau đớn tăng đột ngột.<br />\r\n<br />\r\nChẳng lẽ ---Nguy&ecirc;n chủ đang muốn đoạt lại chủ quyền cơ thể sao?Cho c&ocirc;.<br />\r\n<br />\r\nCho c&ocirc;.<br />\r\n<br />\r\nĐừng đoạt.<br />\r\n<br />\r\nTrả lại cho c&ocirc; đấy.<br />\r\n<br />\r\n&hellip;&hellip;Nhưng m&agrave; ---Đợi một l&uacute;c l&acirc;u, cơ thể Qu&yacute; Dữu vẫn kh&ocirc;ng nh&uacute;c nh&iacute;ch, trong linh hồn cũng kh&ocirc;ng cảm nhận được bất kỳ lạ thường n&agrave;o, c&oacute; vẻ c&ocirc; đo&aacute;n sai rồi th&igrave; phải, cũng kh&ocirc;ng phải nguy&ecirc;n chủ muốn cướp lại th&acirc;n thể m&agrave; chỉ l&agrave; tim đập nhanh kh&aacute;c thường th&ocirc;i.<br />\r\n<br />\r\nQu&yacute; Dữu ngồi xổm tr&ecirc;n s&agrave;n nh&agrave; rất mất h&igrave;nh tượng, lưng dựa v&agrave;o v&aacute;ch tường, c&ocirc; kh&ocirc;ng ngừng h&iacute;t thở&hellip;&hellip; Cố gắng kh&ocirc;ng chế tần suất tim đập một c&aacute;ch bất thường, thật l&acirc;u sau tim mới đập chậm lại như cũ.<br />\r\n<br />\r\nChỉ l&agrave; ---Đầu vẫn rất đau.<br />\r\n<br />\r\nC&ograve;n c&oacute; ---Cứ cảm thấy giống như trong &oacute;c xuất hiện đồ vật kỳ qu&aacute;i n&agrave;o đ&oacute;, c&ocirc; hơi nhắm mắt lại, trước mắt trừ b&oacute;ng tối ra th&igrave; trong &yacute; thức như c&oacute; th&ecirc;m đồ vật, đ&oacute; l&agrave; g&igrave;?Qu&yacute; Dữu muốn lại gần một ch&uacute;t để nghi&ecirc;m t&uacute;c quan s&aacute;t, động th&aacute;i n&agrave;y khiến c&ocirc; phản xạ c&oacute; điều kiện mở to mắt, nhưng c&ocirc; mới vừa mở mắt th&igrave; đồ vật trong &yacute; thức kia liền biến mất lu&ocirc;n.<br />\r\n<br />\r\n<br />\r\nQ&uacute;y Dữu nhắm mắt lần nữa, cẩn thận cảm thụ một ch&uacute;t, trong &yacute; thức đồ vật đ&oacute; đột nhi&ecirc;n lại xuất hiện, đen như mực, nh&igrave;n kh&ocirc;ng được r&otilde; lắm, Qu&yacute; Dữu an ủi bản th&acirc;n kh&ocirc;ng được n&oacute;ng nảy, c&ocirc; chậm r&atilde;i ch&uacute;t một, dẫn đường tinh thần lực mỏng manh của m&igrave;nh hướng lại gần đồ vật đen như mực đ&oacute;.<br />\r\n<br />\r\nMột ch&uacute;t.<br />\r\n<br />\r\nMột ch&uacute;t.<br />\r\n<br />\r\n&hellip;&hellip;Gần.<br />\r\n<br />\r\nĐến khi thấy r&otilde; đ&oacute; l&agrave; c&aacute;i g&igrave; th&igrave; bỗng con ngươi của Qu&yacute; Dữu trừng r&otilde; to.<br />\r\n<br />\r\nN&agrave;y ---N&agrave;y ---C&aacute;i thứ n&agrave;y chẳng phải l&agrave; khối thiết phiến l&uacute;c trước đ&aacute;nh ngất m&igrave;nh sao?Khi ấy khối thiết phiến n&agrave;y đột nhi&ecirc;n va v&agrave;o c&ocirc;, Qu&yacute; Dữu &yacute; thức được kh&ocirc;ng ổn muốn tr&aacute;nh đi nhưng căn bản tr&aacute;nh kh&ocirc;ng kịp, chỉ mơ hồ liếc mắt một c&aacute;i, k&iacute; ức tương đối khắc s&acirc;u ch&iacute;nh l&agrave; những hoa văn phức tạp đẹp đẽ tr&ecirc;n mặt khối thiết phiến, hoa văn đ&oacute; thể hiện ra một cỗ lực lượng cổ xưa s&acirc;u thẳm, bất kỳ người n&agrave;o liếc mắt một c&aacute;i đều kh&oacute; c&oacute; thể qu&ecirc;n.<br />\r\n<br />\r\nSẽ kh&ocirc;ng sai.<br />\r\n<br />\r\nTuyệt đối c&aacute;i khối đ&oacute; l&agrave; thiết phiến đ&aacute;nh tr&uacute;ng c&ocirc;.<br />\r\n<br />\r\nThiết phiến l&agrave; c&aacute;i t&ecirc;n t&ugrave;y tiện Qu&yacute; Dữu lấy dựa v&agrave;o ngoại h&igrave;nh của n&oacute;, đến nỗi chất liệu của c&aacute;i thứ n&agrave;y chắc g&igrave; đ&atilde; l&agrave; sắt? Trước mắt đến xem Qu&yacute; Dữu cũng kh&ocirc;ng t&agrave;i n&agrave;o biết được.<br />\r\n<br />\r\nTr&ecirc;n thực tế, khi c&oacute; đồ vật rơi từ tr&ecirc;n cao đập tr&uacute;ng đầu muốn đầu kh&ocirc;ng u một cục l&agrave; điều kh&ocirc;ng thể n&agrave;o, l&uacute;c ấy Qu&yacute; Dữ biết bản th&acirc;n chết chắc rồi, nhưng kỳ qu&aacute;i l&agrave; l&uacute;c khối thiết phiến đ&oacute; đ&aacute;nh tr&uacute;ng đầu dưa của c&ocirc; th&igrave; c&ocirc; cũng kh&ocirc;ng cảm gi&aacute;c được cơn đau đớn dữ dội, chỉ thấy trước mắt tối sầm rồi h&ocirc;n m&ecirc;, sau khi tỉnh th&igrave; đ&atilde; xuy&ecirc;n qua rồi.<br />\r\n<br />\r\n<br />\r\nViệc n&agrave;y rất thần kỳ, ho&agrave;n to&agrave;n si&ecirc;u tho&aacute;t phạm tr&ugrave; vật l&yacute; a.<br />\r\n<br />\r\nQu&yacute; Dữu nghĩ trăm lần cũng kh&ocirc;ng ra.<br />\r\n<br />\r\nM&agrave; --- sao hiện tại khối thiết phiến n&agrave;y kh&ocirc;ng đi m&agrave; ở trong &oacute;c c&ocirc; vậy?Rốt cuộc chuyện n&agrave;y l&agrave; sao?Qu&yacute; Dữu xoa m&agrave;y, kh&ocirc;ng hiểu sao thấy đầu c&agrave;ng đau.<br />\r\n<br />\r\nHiện tại c&ocirc; c&oacute; đầy đủ l&yacute; do ho&agrave;i nghi cơn đau đầu hơn nửa năm nay khiến m&igrave;nh bối rối l&agrave; tại khối thiết phiến n&agrave;y giở tr&ograve; quỷ.<br />\r\n<br />\r\nTức giận nha!Đập chết c&ocirc;, l&agrave;m hại c&ocirc; xuy&ecirc;n qua cũng th&ocirc;i đi thế m&agrave; giờ kh&ocirc;ng c&oacute; c&ocirc; cho ph&eacute;p vẫn cắm rễ trong &yacute; thức của c&ocirc;, kh&aacute;c g&igrave; bị cưỡng chế đ&acirc;u.<br />\r\n<br />\r\nKhối thiết phiến đ&aacute;ng chết n&agrave;y nhất định c&ocirc; phải nghĩ ra c&aacute;ch đuổi n&oacute; ra ngo&agrave;i th&ocirc;i!Qu&yacute; Dữu ngồi b&ograve; tr&ecirc;n s&agrave;n nh&agrave;, được một l&uacute;c l&acirc;u c&ocirc; mới cảm thấy hết đau đầu, c&ocirc; b&igrave;nh t&igrave;nh nh&igrave;n lại khối thiết phiến đ&oacute; th&igrave; đột nhi&ecirc;n ph&aacute;t hiện tr&ecirc;n bề mặt của thiết phiến n&agrave;y giống như r&uacute;t đi ch&uacute;t sắc đen, lộ ra ch&uacute;t &aacute;nh s&aacute;ng trong suốt trơn b&oacute;ng --- khi n&agrave;o th&igrave; ph&aacute;t sinh thay đổi vậy? Qu&yacute; Dữu cẩn thận hồi tưởng lại một lần th&igrave; hơi hiểu ra.<br />\r\n<br />\r\nSau khi đầu c&ocirc; hết đau th&igrave; khối thiết phiến n&agrave;y mới y&ecirc;n lặng biến h&oacute;a.<br />\r\n<br />\r\nV&igrave; đầu c&ocirc; kh&ocirc;ng c&ograve;n đau nữa n&ecirc;n thiết phiến trở n&ecirc;n s&aacute;ng hơn.<br />\r\n<br />\r\nC&ograve;n nguy&ecirc;n nh&acirc;n khiến thiết phiến ph&aacute;t sinh biến h&oacute;a th&igrave; Qu&yacute; Dữu vẫn như cũ kh&ocirc;ng hiểu tại sao nhưng c&ocirc; kh&ocirc;ng miệt m&agrave;i theo đuổi việc n&agrave;y m&agrave; chỉ thấy t&acirc;m trạng nặng nề, nh&igrave;n thiết phiến lần nữa ph&aacute;t hiện hoa văn phức tạp tr&ecirc;n bề mặt c&agrave;ng r&otilde; r&agrave;ng hơn, h&igrave;nh như c&ocirc; thấy một đạo ph&ugrave; văn được tạo th&agrave;nh từ v&ocirc; số đường cong quanh co kh&uacute;c khuỷu quấn lấy nhau, cuối c&ugrave;ng v&ograve;ng theo đường cong tạo th&agrave;nh một đạo đồ h&igrave;nh.<br />\r\n<br />\r\n<br />\r\nBỗng chốc ---Cơn đau đầu kịch liệt đ&aacute;nh &uacute;p lại, Qu&yacute; Dữu bất chấp xem đến c&ugrave;ng đồ h&igrave;nh đ&oacute; rốt cuộc l&agrave; g&igrave; m&agrave; mau ch&oacute;ng &ocirc;m đầu, cả người lăn lộn tr&ecirc;n s&agrave;n nh&agrave;.<br />\r\n<br />\r\nThật l&acirc;u sau Qu&yacute; Dữu mệt đến kh&ocirc;ng buồn động đậy nằm tr&ecirc;n s&agrave;n, giống như một con c&aacute; chết vừa mới bị mổ bụng vậy, người run lẩy bẩy nhưng vẫn cố ngoan cường ngồi dậy, sau khi ngồi dậy được th&igrave; c&ocirc; giơ tay day day giữa m&agrave;y rồi từ từ mở mắt ra.<br />\r\n<br />\r\nƠ?Trời s&aacute;ng l&uacute;c n&agrave;o vậy?Qu&yacute; Dữu n&acirc;ng tay l&ecirc;n nh&igrave;n qua thời gian tr&ecirc;n quang n&atilde;o của m&igrave;nh th&igrave; quả nhi&ecirc;n ph&aacute;t hiện đ&atilde; 9 giờ s&aacute;ng.<br />\r\n<br />\r\nSao lại thế n&agrave;y?Tuy rất đau, cả người giống như nhẫm l&ecirc;n mũi đao, c&ograve;n một ch&acirc;n đang bước v&agrave;o địa ngục&hellip;&hellip; Nhưng thời gian kh&ocirc;ng l&acirc;u như vậy m&agrave;, thậm ch&iacute; Qu&yacute; Dữu cảm thấy cơn đau đ&oacute; k&eacute;o d&agrave;i nhều nhất l&agrave; nửa giờ m&agrave; th&ocirc;i.<br />\r\n<br />\r\nThế m&agrave; hiện thực đ&atilde; qua suốt 9 tiếng đồng hồ.<br />\r\n<br />\r\nC&agrave;ng ng&agrave;y c&agrave;ng kỳ qu&aacute;i.<br />\r\n<br />\r\nNếu kh&ocirc;ng hiểu được nguy&ecirc;n nh&acirc;n th&igrave; dứt kho&aacute;t bỏ qua, kh&ocirc;ng việc g&igrave; phải đi t&igrave;m t&ograve;i nghi&ecirc;n cứu, c&ocirc; xoa c&aacute;i bụng kh&ocirc; quắt lại, tối qua chỉ ăn nửa lọ dinh dưỡng tề, hiện tại bụng đ&oacute;i đến mức k&ecirc;u kh&aacute;ng nghị.<br />\r\n<br />\r\nQu&yacute; Dữu lấy nửa lọ dinh dưỡng tề từ tối qua c&ograve;n dư trong ngăn tủ rồi đi b&agrave;y biện b&aacute;t đũa ghế dựa cho chỉnh tề, lau ch&ugrave;i hoa hồng bằng plastic rồi cắm lại v&agrave;o b&igrave;nh, hừ ~ bất kể sinh hoạt c&oacute; khổ đến thế n&agrave;o th&igrave; kh&ocirc;ng thể n&agrave;o bỏ qua quy luật được.<br />\r\n<br />\r\nĂn uống no n&ecirc;.<br />\r\n<br />\r\nQu&yacute; Dữu xem lại lịch tr&igrave;nh m&igrave;nh đ&atilde; sắp xếp: Buổi s&aacute;ng 8h &ndash; 12h, l&agrave;m cu li 4 giờ, buổi chiều 2h đi đến khu 6 tham gia buổi phỏng vấn của một cửa h&agrave;ng c&oacute; th&ocirc;ng b&aacute;n tuyển dụng người b&aacute;n h&agrave;ng.<br />\r\n<br />\r\nBởi v&igrave; chậm trễ thời gian n&ecirc;n khẳng định rằng buổi s&aacute;ng kh&ocirc;ng thể l&agrave;m cu li đủ 4 tiếng đồng hồ được, Qu&yacute; Dữu nghĩ c&ocirc;ng việc cu li n&agrave;y l&uacute;c n&agrave;o cũng c&oacute; thể l&agrave;m, việc cấp b&aacute;ch vẫn l&agrave; t&igrave;m được một c&ocirc;ng việc lương cao, cửa h&agrave;ng c&ocirc; muốn phỏng vấn trả lương cho người b&aacute;n h&agrave;ng l&agrave; 1500 điểm t&iacute;n dụng, hơn nữa tr&ecirc;n th&ocirc;ng b&aacute;o tuyển dụng của cửa h&agrave;ng kh&ocirc;ng đề cập đến y&ecirc;u cầu thể chất v&agrave; tinh thần lực, nếu Qu&yacute; Dữu c&oacute; thể th&agrave;nh c&ocirc;ng được tuyển dụng th&igrave; đương nhi&ecirc;n hơn đứt đi nhặt r&aacute;c v&agrave; l&agrave;m cu li.<br />\r\n<br />\r\n<br />\r\nV&igrave; thế Qu&yacute; Dữu đăng nhập Tinh V&otilde;ng bổ cọc gỗ 3 tiếng đồng hồ, sau đ&oacute; đăng xuất Tinh V&otilde;ng.<br />\r\n<br />\r\nĂn dinh dưỡng tề xong, Qu&yacute; Dữu chỉnh trang lại quần &aacute;o, sau khi x&aacute;c định cả người m&igrave;nh nh&igrave;n thật thoải m&aacute;i v&agrave; đầy tươi trẻ, giỏi giang mới y&ecirc;n t&acirc;m mở cửa đi đến trạm trung chuyển giao th&ocirc;ng c&ocirc;ng cộng.<br />\r\n<br />\r\nTh&acirc;n l&agrave; t&ecirc;n quỷ ngh&egrave;o, đương nhi&ecirc;n Qu&yacute; Dữu kh&ocirc;ng c&oacute; c&ocirc;ng cụ di chuyển tư nh&acirc;n m&agrave; ng&agrave;y thường muốn đi ra ngo&agrave;i c&ocirc; đi bằng xe huyền ph&ugrave; được thiết lập ở c&aacute;c khu vực c&ocirc;ng cộng trong tinh cầu r&aacute;c số 101 n&agrave;y, ph&iacute; dụng l&agrave; 2 điểm t&iacute;n dụng/lần.<br />\r\n<br />\r\n***Xe huyền ph&ugrave;: Từ huyền ph&ugrave; c&oacute; nghĩa l&agrave; nổi lơ lửng, n&ecirc;n nghĩa của xe huyền ph&ugrave; l&agrave; xe được nổi lơ lửng tr&ecirc;n bề mặt đất.<br />\r\n<br />\r\nBởi v&igrave; cảm thấy ph&iacute; tham gia giao th&ocirc;ng c&ocirc;ng cộng hơi đắt n&ecirc;n b&igrave;nh thường Qu&yacute; Dữu sẽ d&ugrave;ng cặp gi&ograve; của m&igrave;nh để giải quyết vấn đề đi lại n&agrave;y, tuyệt đối kh&ocirc;ng ỷ lại xe.<br />\r\n<br />\r\nKhi ra khỏi nh&agrave; c&ocirc; gặp được b&agrave; Jenny.<br />\r\n<br />\r\nB&agrave; Jenny cười n&oacute;i: &ldquo;A Dữu, ch&aacute;u t&iacute;nh đi khu 6 phỏng vấn hả?&rdquo;Qu&yacute; Dữu h&iacute;p mắt cười: &ldquo;V&acirc;ng, ch&aacute;u muốn đi thử một lần xem sao.<br />\r\n<br />\r\n&rdquo;Tối h&ocirc;m qua khi đi nhặt r&aacute;c c&ugrave;ng nhau, ban đầu b&agrave; Jenny muốn hẹn Qu&yacute; Dữu tối nay tiếp tục c&ugrave;ng đi nhặt r&aacute;c nhưng c&ocirc; nghĩ đến buổi chiều c&oacute; cuộc phỏng vấn, từ khu 10 m&agrave; c&ocirc; đang ở đi giao th&ocirc;ng c&ocirc;ng cộng l&agrave; xe huyền ph&ugrave; đến khu 6 mất một giờ, phỏng vấn kh&ocirc;ng biết sẽ mất bao l&acirc;u n&ecirc;n ước chừng s&aacute;u giờ tối Qu&yacute; Dữu kh&ocirc;ng thể trở về ngay được liền từ chối lời mời của b&agrave; Jenny, thế n&ecirc;n b&agrave; Jenny biết chiều nay Qu&yacute; Dữu muốn đi phỏng vấn.<br />\r\n<br />\r\nB&agrave; Jenny n&oacute;i: &ldquo;Cố gắng l&ecirc;n! C&ocirc; g&aacute;i nhỏ, nhất định ch&aacute;u sẽ th&agrave;nh c&ocirc;ng.<br />\r\n<br />\r\n&rdquo;Qu&yacute; Dữu trả lời: &ldquo;V&acirc;ng, ch&aacute;u sẽ cố gắng tranh thủ.</p>', '0', 'thiet-phien-o-trong-dau', 7, '372', '2022-08-03 06:10:27', '2022-08-03 06:10:27');
INSERT INTO `sessions` (`id`, `session`, `title_session`, `tomtat_session`, `content_session`, `kichhoat`, `slug_session`, `id_product`, `view_session`, `created_at`, `updated_at`) VALUES
(52, '1', 'Thị trấn', '<p>Hắn từ trong mộng tỉnh lại, kh&iacute; trời s&aacute;ng sủa, một ng&agrave;y tốt l&agrave;nh.<br />\r\n<br />\r\nĐiều đầu ti&ecirc;n nhảy v&agrave;o t&acirc;m tr&iacute; l&agrave; cơn đ&oacute;i.<br />\r\n<br />\r\nHắn nghĩ, hắn n&ecirc;n dậy ăn g&igrave; đ&oacute;.<br />\r\n<br />\r\nSinh hoạt phải c&oacute; quy luật, nếu kh&ocirc;ng mọi thứ sẽ hỗn loạn.<br />\r\n<br />\r\nLịch tr&igrave;nh thường giống nhau.<br />\r\n<br />\r\nS&aacute;u giờ rời giường.<br />\r\n<br />\r\nTắm rửa.<br />\r\n<br />\r\nĂn s&aacute;ng.<br />\r\n<br />\r\nBảy rưỡi ra ngo&agrave;i dắt ch&oacute; đi dạo.<br />\r\n<br />\r\nHắn c&oacute; một con ch&oacute; Malinois [1] chỉ mới một tuổi, l&uacute;c n&agrave;o cũng bay nhảy khắp nơi.<br />\r\n<br />\r\nT&aacute;m rưỡi về đến nh&agrave;.<br />\r\n<br />\r\nCh&iacute;n giờ bắt đầu c&ocirc;ng việc.<br />\r\n<br />\r\nHắn c&oacute; một c&ocirc;ng việc rất được người y&ecirc;u th&iacute;ch, thợ mộc thủ c&ocirc;ng.<br />\r\n<br />\r\nB&ecirc;n cạnh ph&ograve;ng ngủ của hắn l&agrave; một ph&ograve;ng l&agrave;m việc với nhiều loại dụng cụ - m&aacute;y đục, m&aacute;y b&agrave;o, dao cắt, m&aacute;y cắt, m&aacute;y m&agrave;i. Tr&ecirc;n kệ c&oacute; v&ocirc; số t&aacute;c phẩm v&agrave; b&aacute;n th&agrave;nh phẩm, chủ yếu l&agrave; cơ thể con người v&agrave; thỉnh thoảng cũng c&oacute; động vật.<br />\r\n<br />\r\nC&oacute; l&uacute;c hắn sẽ đưa một &iacute;t t&aacute;c phẩm trong đ&oacute; biếu tặng khu viện dưỡng l&atilde;o v&agrave; trung t&acirc;m ph&uacute;c lợi hoặc cho h&agrave;ng x&oacute;m mới tới.<br />\r\n<br />\r\nMọi người đều rất y&ecirc;u th&iacute;ch hắn, hắn cũng rất y&ecirc;u th&iacute;ch bọn họ.<br />\r\n<br />\r\nKhi đi dạo ng&agrave;y h&ocirc;m nay, hắn bỗng nhi&ecirc;n giật cả m&igrave;nh, t&acirc;m tr&iacute; hắn l&oacute;e l&ecirc;n, hắn c&oacute; một &yacute; tưởng tuyệt vời.<br />\r\n<br />\r\nSau khi trở về nh&agrave;, hắn rơi v&agrave;o trầm tư, kh&ocirc;ng ngừng hưng phấn về sự s&aacute;ng tạo sắp bắt đầu, đồng thời l&agrave;m n&ecirc;n c&ocirc;ng t&aacute;c chuẩn bị.</p>', '<p>Hắn từ trong mộng tỉnh lại, kh&iacute; trời s&aacute;ng sủa, một ng&agrave;y tốt l&agrave;nh.<br />\r\n<br />\r\nĐiều đầu ti&ecirc;n nhảy v&agrave;o t&acirc;m tr&iacute; l&agrave; cơn đ&oacute;i.<br />\r\n<br />\r\nHắn nghĩ, hắn n&ecirc;n dậy ăn g&igrave; đ&oacute;.<br />\r\n<br />\r\nSinh hoạt phải c&oacute; quy luật, nếu kh&ocirc;ng mọi thứ sẽ hỗn loạn.<br />\r\n<br />\r\nLịch tr&igrave;nh thường giống nhau.<br />\r\n<br />\r\nS&aacute;u giờ rời giường.<br />\r\n<br />\r\nTắm rửa.<br />\r\n<br />\r\nĂn s&aacute;ng.<br />\r\n<br />\r\nBảy rưỡi ra ngo&agrave;i dắt ch&oacute; đi dạo.<br />\r\n<br />\r\nHắn c&oacute; một con ch&oacute; Malinois [1] chỉ mới một tuổi, l&uacute;c n&agrave;o cũng bay nhảy khắp nơi.<br />\r\n<br />\r\nT&aacute;m rưỡi về đến nh&agrave;.<br />\r\n<br />\r\nCh&iacute;n giờ bắt đầu c&ocirc;ng việc.<br />\r\n<br />\r\nHắn c&oacute; một c&ocirc;ng việc rất được người y&ecirc;u th&iacute;ch, thợ mộc thủ c&ocirc;ng.<br />\r\n<br />\r\nB&ecirc;n cạnh ph&ograve;ng ngủ của hắn l&agrave; một ph&ograve;ng l&agrave;m việc với nhiều loại dụng cụ - m&aacute;y đục, m&aacute;y b&agrave;o, dao cắt, m&aacute;y cắt, m&aacute;y m&agrave;i. Tr&ecirc;n kệ c&oacute; v&ocirc; số t&aacute;c phẩm v&agrave; b&aacute;n th&agrave;nh phẩm, chủ yếu l&agrave; cơ thể con người v&agrave; thỉnh thoảng cũng c&oacute; động vật.<br />\r\n<br />\r\nC&oacute; l&uacute;c hắn sẽ đưa một &iacute;t t&aacute;c phẩm trong đ&oacute; biếu tặng khu viện dưỡng l&atilde;o v&agrave; trung t&acirc;m ph&uacute;c lợi hoặc cho h&agrave;ng x&oacute;m mới tới.<br />\r\n<br />\r\nMọi người đều rất y&ecirc;u th&iacute;ch hắn, hắn cũng rất y&ecirc;u th&iacute;ch bọn họ.<br />\r\n<br />\r\nKhi đi dạo ng&agrave;y h&ocirc;m nay, hắn bỗng nhi&ecirc;n giật cả m&igrave;nh, t&acirc;m tr&iacute; hắn l&oacute;e l&ecirc;n, hắn c&oacute; một &yacute; tưởng tuyệt vời.<br />\r\n<br />\r\nSau khi trở về nh&agrave;, hắn rơi v&agrave;o trầm tư, kh&ocirc;ng ngừng hưng phấn về sự s&aacute;ng tạo sắp bắt đầu, đồng thời l&agrave;m n&ecirc;n c&ocirc;ng t&aacute;c chuẩn bị.<br />\r\n<br />\r\nTiếp hắn từng bước trải qua một ng&agrave;y, đọc b&aacute;o, cho ch&oacute; ăn, giặt quần &aacute;o, cắt cỏ, c&ugrave;ng tr&ograve; chuyện với những người h&agrave;ng x&oacute;m cũng đang tưới nước v&agrave; l&agrave;m cỏ trong s&acirc;n.<br />\r\n<br />\r\nCh&iacute;n giờ tối, c&uacute;n con tr&egrave;o l&ecirc;n giường hắn.<br />\r\n<br />\r\nĐ&uacute;ng giờ ngủ.<br />\r\n<br />\r\nCHƯƠNG 01: THỊ TRẤN<br />\r\n<br />\r\nFaun ở cột mốc 24 th&igrave; lạc đường.<br />\r\n<br />\r\nDựa v&agrave;o tấm biển vừa rồi, cậu lẽ ra đ&atilde; đến Nashville từ năm ph&uacute;t trước, d&agrave;nh một kỳ nghỉ thư gi&atilde;n d&agrave;i hạn trong kh&aacute;ch sạn nơi đ&oacute;.<br />\r\n<br />\r\nTại giờ khắc n&agrave;y, ngo&agrave;i con đường c&aacute;i m&ecirc;nh m&ocirc;ng v&ocirc; tận b&ecirc;n ngo&agrave;i cửa sổ, chỉ c&oacute; rừng c&acirc;y &acirc;m u ảm đạm tr&ecirc;n cả hai mặt đường c&ugrave;ng cậu l&agrave;m bạn, thậm ch&iacute; cả điện thoại di động v&agrave; định vị GPS đều bị mất t&iacute;n hiệu.<br />\r\n<br />\r\nTiếp tục l&aacute;i xe trong khoảng một ph&uacute;t, Faun đ&agrave;nh tấp xe s&aacute;t s&aacute;t ven đường để d&ograve; bản đồ.</p>\r\n\r\n<p><br />\r\nCậu ước t&iacute;nh thời gian v&agrave; tốc độ, muốn t&igrave;m vị tr&iacute; hiện tại. Từ bản đồ c&oacute; thể thấy được, đoạn lộ n&agrave;y vừa kh&ocirc;ng c&oacute; lối rẽ cũng kh&ocirc;ng c&oacute; g&oacute;c ngoặt, mặt đường rộng r&atilde;i v&agrave; bằng phẳng, lại c&agrave;ng kh&ocirc;ng như con đường n&ocirc;ng th&ocirc;n nhỏ bị bản đồ l&atilde;ng qu&ecirc;n n&agrave;y.<br />\r\n<br />\r\nCứ như vậy m&agrave; lạc tr&ecirc;n quốc lộ cũng thật l&agrave; chuyện kh&oacute;.<br />\r\n<br />\r\nThử bật radio, chỉ c&oacute; một mớ tạp &acirc;m hỗn độn ph&aacute;t ra từ b&ecirc;n trong, v&igrave; vậy cậu chỉ c&oacute; thể tiếp tục di chuyển về ph&iacute;a trước, hi vọng nhận được một số t&iacute;n hiệu ở ng&atilde; tư tiếp theo.<br />\r\n<br />\r\nBiển chỉ đường đột nhi&ecirc;n xuất hiện.<br />\r\n<br />\r\nXe chạy tr&ecirc;n con đường tối, thời gian từng gi&acirc;y từng ph&uacute;t tr&ocirc;i qua, kh&ocirc;ng c&oacute; giao lộ, kh&ocirc;ng c&oacute; trạm xăng để hỏi đường, thậm ch&iacute; kh&ocirc;ng c&oacute; một chiếc xe chạy tới hoặc vượt qua.<br />\r\n<br />\r\nĐiều n&agrave;y kh&ocirc;ng tr&aacute;nh khỏi g&acirc;y ra trong l&ograve;ng Faun cảm gi&aacute;c k&igrave; dị qu&aacute;i lạ.<br />\r\n<br />\r\nTrong c&aacute;c c&acirc;u chuyện kể đ&ecirc;m khuya về đường quốc lộ, biển b&aacute;o đột ngột xuất hiện lu&ocirc;n lu&ocirc;n c&oacute; một số m&agrave;u sắc k&igrave; lạ, bề mặt sơn đốm, graffiti m&agrave;u đỏ đ&aacute;ng ngờ v&agrave; những c&aacute;i t&ecirc;n chưa từng nghe b&aacute;o hiệu một điều chẳng l&agrave;nh sắp xảy ra.<br />\r\n<br />\r\nẤy vậy m&agrave; tấm bảng đường xuất hiện dưới &aacute;nh đ&egrave;n pha l&uacute;c n&agrave;y thật sạch sẽ s&aacute;ng r&otilde;, như thể vừa mới được ai đ&oacute; qu&eacute;t tước, tr&ecirc;n đ&oacute; đề d&ograve;ng chữ m&agrave;u trắng đ&uacute;ng ti&ecirc;u chuẩn: &quot;Trấn Maple(C&acirc;y Phong)&quot;.<br />\r\n<br />\r\nD&ugrave; cho thế n&agrave;o, c&aacute;i từ &quot;Maple&quot; n&agrave;y cũng l&agrave;m cho cậu thấy th&acirc;n thuộc, &iacute;t ra cũng tốt hơn so với thị trấn Silent Hill hay trấn Madison[2].<br />\r\n<br />\r\nSau khoảng gần một c&acirc;y số Faun thấy một thị trấn nhỏ đ&aacute;ng y&ecirc;u, đ&egrave;n đuốc trong thị trấn s&aacute;ng choang, hầu hết c&aacute;c cửa h&agrave;ng vẫn kinh doanh như thường lệ, cả hai mặt của c&acirc;y xanh đường phố treo đầy đ&egrave;n lồng v&agrave; biểu ngữ, tựa hồ ban ng&agrave;y vừa trải qua lễ mừng hết sức n&aacute;o nhiệt.<br />\r\n<br />\r\nCậu đỗ xe b&ecirc;n đường, khi vừa xuống xe, một thanh ni&ecirc;n đi ngang liếc cậu.<br />\r\n<br />\r\n&quot;Xin ch&agrave;o.&quot;<br />\r\n<br />\r\nCậu th&acirc;n thiện ch&agrave;o hỏi, người kia chỉ nh&igrave;n cậu trong im lặng.<br />\r\n<br />\r\nFaun nh&igrave;n ra trong &aacute;nh mắt người n&agrave;y mang theo tia cảnh gi&aacute;c, c&oacute; lẽ trấn nhỏ hiếm khi c&oacute; người lạ viếng thăm, th&aacute;i độ mọi người cẩn thận một ch&uacute;t c&oacute; thể th&ocirc;ng cảm được.<br />\r\n<br />\r\nNh&agrave; h&agrave;ng dọc đường c&oacute; một c&aacute;i t&ecirc;n rất lạ được gọi l&agrave; &quot;Magic Hand&quot; (Ma Thủ), bảng hiệu bằng sắt m&agrave;u đen sơn một b&agrave;n tay đeo v&ograve;ng, m&oacute;ng tay d&agrave;i v&agrave; nhọn, ng&oacute;n trỏ dựa tr&ecirc;n một dấu chấm chữ tr&ocirc;ng giống một giọt m&aacute;u.<br />\r\n<br />\r\nTr&ecirc;n tấm cửa k&iacute;nh của nh&agrave; h&agrave;ng treo biển mở cửa cả đ&ecirc;m, Faun đẩy cửa bước v&agrave;o, tiếng chu&ocirc;ng cửa reo l&ecirc;n, mọi người nh&igrave;n về ph&iacute;a cậu.<br />\r\n<br />\r\nC&oacute; s&aacute;u người trong ph&ograve;ng, một c&ocirc; hầu b&agrave;n, một cặp vợ chồng gi&agrave; đang c&uacute;i đầu d&ugrave;ng cơm, một c&ocirc; g&aacute;i với m&aacute;i t&oacute;c đỏ v&agrave; hai người đ&agrave;n &ocirc;ng ngồi c&aacute;ch xa cửa.<br />\r\n<br />\r\n&quot;Ch&agrave;o buổi tối.&quot;<br />\r\n<br />\r\nC&ocirc; phục vụ đặt tờ b&aacute;o trong tay xuống, khoảng ba mươi tuổi, ph&iacute;a dưới đ&ocirc;i mắt chứa một v&agrave;i nếp nhăn nhỏ v&agrave; đ&ocirc;i m&ocirc;i đỏ bừng.<br />\r\n<br />\r\n&quot;Muốn gọi g&igrave;?&quot;<br />\r\n<br />\r\n&quot;T&ocirc;i muốn hỏi đường, thuận tiện mượn một c&uacute; điện thoại.&quot;<br />\r\n<br />\r\n&quot;Ồ.&quot; &Aacute;nh mắt của c&ocirc; kh&ocirc;ng hiếu kh&aacute;ch hay th&acirc;n thiện, cũng thận trọng như người thanh ni&ecirc;n vừa mới đi ngang qua.<br />\r\n<br />\r\nKh&ocirc;ng biết ph&ograve;ng bị trong l&ograve;ng họ từ đ&acirc;u m&agrave; đến, lẽ n&agrave;o cậu nh&igrave;n giống những phần tử nguy hiểm đến vậy?<br />\r\n<br />\r\nXuất ph&aacute;t từ th&oacute;i quen nghề nghiệp, Faun vẫn mang theo một khẩu s&uacute;ng, nhưng từ ngo&agrave;i v&agrave;o, cậu l&agrave; một người dễ d&agrave;ng khiến người kh&aacute;c bu&ocirc;ng lỏng cảnh gi&aacute;c. Faun c&oacute; đ&ocirc;i mắt m&agrave;u lam m&ecirc; hoặc, cao s&aacute;u feet (~1.83m), t&oacute;c n&acirc;u ngắn v&agrave; sạch sẽ, tướng mạo anh tuấn khiến cậu được nhiều hảo cảm. Alex lu&ocirc;n n&oacute;i rằng cậu kh&ocirc;ng giống như một th&aacute;m tử, ở cậu thiếu sự uy nghi m&agrave; một người thực thi ph&aacute;p luật n&ecirc;n c&oacute;. Nhưng những người c&oacute; thể được Fauntiếp nhận lại c&oacute; c&aacute;i nh&igrave;n tr&aacute;i ngược, tất cả đều nghĩ cảnh s&aacute;t n&ecirc;n như thế n&agrave;y.<br />\r\n<br />\r\n&quot;T&ocirc;i muốn đến Nashville, kh&ocirc;ng cẩn thận bị lạc đường.&quot;<br />\r\n<br />\r\nC&ocirc; phục vụ kh&ocirc;ng hề đồng cảm m&agrave; qua loa đ&aacute;p cho xong: &quot;Thật kh&ocirc;ng may.&quot;<br />\r\n<br />\r\n&quot;C&ocirc; c&oacute; thể cho t&ocirc;i biết n&ecirc;n đi thế n&agrave;o? T&ocirc;i n&ecirc;n tiếp tục đi về ph&iacute;a trước hay quay đầu lại?&quot;<br />\r\n<br />\r\n&quot;T&ocirc;i kh&ocirc;ng biết.&quot; c&ocirc; trả lời. &quot;T&ocirc;i kh&ocirc;ng quen với những thứ b&ecirc;n ngo&agrave;i trấn nhỏ lắm.&quot;<br />\r\n<br />\r\n&quot;T&ocirc;i c&oacute; thể sử dụng điện thoại kh&ocirc;ng?&quot;<br />\r\n<br />\r\nC&ocirc; t&ugrave;y tiện ra một động t&aacute;c mời rồi tiếp tục nh&igrave;n xuống tờ b&aacute;o.<br />\r\n<br />\r\nFaun lấy số kh&aacute;ch sạn v&agrave; gọi cho b&agrave;n tiếp t&acirc;n. Thật k&igrave; lạ, kh&ocirc;ng c&oacute; ai trả lời, cậu ki&ecirc;n nhẫn chờ một l&uacute;c l&acirc;u, m&atilde;i cho đến khi tiếng chu&ocirc;ng gi&aacute;n đoạn vang l&ecirc;n cậu mới bỏ m&aacute;y.<br />\r\n<br />\r\n&quot;Nơi n&agrave;y c&oacute; trạm xăng kh&ocirc;ng?&quot;<br />\r\n<br />\r\n&quot;C&oacute; một c&aacute;i. Ở cuối con đường ph&iacute;a trước, gần hướng của khu rừng.&quot;<br />\r\n<br />\r\nFaun liếc nh&igrave;n đường phố b&ecirc;n ngo&agrave;i, &aacute;nh đ&egrave;n đầy m&agrave;u sắc rực rỡ l&agrave;m l&ograve;ng cậu t&ograve; m&ograve;.<br />\r\n<br />\r\n&quot;Lễ mừng h&ocirc;m nay l&agrave; g&igrave; vậy?&quot;<br />\r\n<br />\r\n&quot;Kh&ocirc;ng c&oacute; g&igrave;. N&oacute; lu&ocirc;n như thế n&agrave;y.&quot;<br />\r\n<br />\r\nC&oacute; thể l&agrave; một đặc điểm địa phương, Faun thấy rằng c&ocirc; thực sự kh&ocirc;ng c&oacute; hứng th&uacute; trong việc n&oacute;i chuyện, kh&ocirc;ng thể l&agrave;m g&igrave; kh&aacute;c hơn l&agrave; rời đi. To&agrave;n bộ nh&agrave; h&agrave;ng y&ecirc;n tĩnh như một viện bảo t&agrave;ng s&aacute;p, v&agrave; mọi người đều giống như m&ocirc; h&igrave;nh để trưng b&agrave;y, nhưng Faun biết họ đang theo d&otilde;i cậu.<br />\r\n<br />\r\nCậu đi dọc theo con đường, bất kể mọi người lập dị thế n&agrave;o cũng kh&ocirc;ng thể phủ nhận sự mỹ lệ v&agrave; quyến rũ của thị trấn nhỏ n&agrave;y. Hạ cửa k&iacute;nh, gi&oacute; buổi tối tươi mới &ecirc;m dịu chứa đựng hương thơm ngọt ng&agrave;o vuốt l&ecirc;n mặt. Cậu suy đo&aacute;n c&oacute; một vườn c&acirc;y ăn quả gần đ&oacute; v&igrave; phả v&agrave;o trong gi&oacute; l&agrave; từng l&agrave;n hương qu&yacute;t.<br />\r\n<br />\r\nTrạm xăng nằm ở cuối trung t&acirc;m con phố về ph&iacute;a t&acirc;y, nối với một khu rừng hẻo l&aacute;nh &acirc;m u, ngo&agrave;i rừng c&acirc;y c&oacute; thể l&agrave; quốc lộ.<br />\r\n<br />\r\nChỉ c&oacute; một nh&acirc;n vi&ecirc;n trẻ tại trạm xăng nhỏ n&agrave;y, mặc bộ &aacute;o liền quần m&agrave;u x&aacute;m, đầu gối v&agrave; khuỷu tay m&agrave;i đến bạc m&agrave;u, m&aacute;i t&oacute;c v&agrave;ng d&agrave;i rối bời, khu&ocirc;n mặt r&acirc;u ria l&uacute;n ph&uacute;n tăng th&ecirc;m vẻ thăng trầm v&agrave; mơ hồ.<br />\r\n<br />\r\nFaun dừng lại trước c&acirc;y xăng, nh&acirc;n vi&ecirc;n tiếp nhi&ecirc;n liệu kh&ocirc;ng coi ai ra g&igrave; ngủ tr&ecirc;n băng ghế.<br />\r\n<br />\r\n&quot;N&agrave;y anh&quot; Faun gọi, anh chậm r&atilde;i đứng dậy.<br />\r\n<br />\r\n&quot;C&oacute; chuyện g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Đổ xăng.&quot;<br />\r\n<br />\r\n&quot;Bao nhi&ecirc;u?&quot;<br />\r\n<br />\r\n&quot;Đầy b&igrave;nh.&quot;<br />\r\n<br />\r\n&quot;Cậu muốn đi xa?&quot;<br />\r\n<br />\r\n&quot;T&ocirc;i muốn đi Nashville, kh&ocirc;ng c&aacute;ch nơi n&agrave;y xa chứ?&quot; Faun chưa bao giờ nghe n&oacute;i về trấn Maple, nhưng thị trấn nhỏ kh&ocirc;ng r&otilde; t&ecirc;n c&ograve;n nhiều lắm.<br />\r\n<br />\r\n&quot;T&ocirc;i kh&ocirc;ng biết, c&oacute; lẽ n&oacute; rất gần, c&oacute; lẽ n&oacute; rất xa.&quot; Người nh&acirc;n vi&ecirc;n trả lời trong khi bơm đầy chiếc xe. &quot;T&ocirc;i kh&ocirc;ng chắc chắn.&quot;<br />\r\n<br />\r\nTh&aacute;i độ anh tuy giống với c&ocirc; hầu b&agrave;n lạnh l&ugrave;ng hời hợt, nhưng &iacute;t hơn một phần đề ph&ograve;ng khiến người ta khẩn trương, n&oacute;i kh&ocirc;ng chừng c&oacute; thể t&aacute;n ngẫu th&ecirc;m v&agrave;i c&acirc;u nữa. Faun lu&ocirc;n cảm thấy c&aacute;i trấn nhỏ n&agrave;y hơi kh&aacute;c thường.<br />\r\n<br />\r\n&quot;Ở đ&acirc;y thật đẹp.&quot;<br />\r\n<br />\r\n&quot;Đ&uacute;ng vậy.&quot;<br />\r\n<br />\r\n&quot;Anh hẳn y&ecirc;u n&oacute; rất nhiều.&quot;<br />\r\n<br />\r\n&quot;Mọi người đều y&ecirc;u n&oacute;, quả thực y&ecirc;u chết n&oacute;.&quot;<br />\r\n<br />\r\n&quot;N&oacute; kh&ocirc;ng qu&aacute; lớn, c&oacute; bao nhi&ecirc;u người ở trong thị trấn?&quot;<br />\r\n<br />\r\n&quot;Hơn một trăm, kh&ocirc;ng chắc, c&oacute; thể nhiều hơn một ch&uacute;t... 37 đ&ocirc;, chỉ nhận tiền mặt.&quot;<br />\r\n<br />\r\nFaun trả tiền xăng, hỏi tiếp: &quot;T&ocirc;i c&oacute; thể ra ngo&agrave;i v&agrave; quay trở lại Quốc lộ 24 ở đ&acirc;u?&quot;<br />\r\n<br />\r\n&quot;C&aacute;i n&agrave;o cũng kh&ocirc;ng thể.&quot; Anh nh&acirc;n vi&ecirc;n nh&igrave;n cậu n&oacute;i. &quot;Cậu kh&ocirc;ng n&ecirc;n lạc đường.&quot;<br />\r\n<br />\r\nFaun kh&ocirc;ng ho&agrave;n to&agrave;n hiểu &yacute; anh, đang định hỏi lại th&igrave; cổ cậu bất ngờ bị bao lấy. Một đ&ocirc;i tay d&agrave;i to khỏe l&ocirc;ng l&aacute; siết lấy cậu, nhất thời cậu kh&ocirc;ng kịp thở.<br />\r\n<br />\r\n&quot;Coi ch&uacute;ng ta c&oacute; g&igrave; k&igrave;a.&quot; Người sau lưng gh&eacute; v&agrave;o tai cậu n&oacute;i. &quot;Một b&eacute; ngoan, để tao xem m&agrave;y c&oacute; thể l&agrave;m được những g&igrave;.&quot;<br />\r\n<br />\r\nFaun đột nhi&ecirc;n h&uacute;c mạnh về ph&iacute;a sau, một tiếng động lớn, kẻ đ&aacute;nh l&eacute;n bị cậu đ&aacute;nh v&agrave;o tr&ecirc;n cửa xe ph&aacute;t ra t&ecirc;n k&ecirc;u sợ h&atilde;i, cậu ngay lập tức tho&aacute;t khỏi khống chế, r&uacute;t khẩu s&uacute;ng lục v&agrave; quay sang nhắm.<br />\r\n<br />\r\n&quot;Đừng nh&uacute;c nh&iacute;ch, cảnh s&aacute;t đ&acirc;y.&quot;<br />\r\n<br />\r\n&quot;Wow, cảnh s&aacute;t.&quot; Người đ&agrave;n &ocirc;ng xoa g&aacute;y n&oacute;i. Hắn mặc một chiếc &aacute;o kho&aacute;c da kaki, cao s&aacute;u feet, cạo đầu, c&oacute; một vết sẹo d&agrave;i một inch (~2.5cm) tr&ecirc;n m&aacute; tr&aacute;i. C&oacute; hai người b&ecirc;n cạnh hắn, một thấp mập ục ịch, khu&ocirc;n mặt sưng ph&ugrave;, đ&ocirc;i mắt nhỏ gần như ho&agrave;n to&agrave;n được bọc trong m&iacute; mắt, chỉ để lại hai khe hẹp. Người kia dường như c&oacute; những đặc điểm của người Andeans* với b&agrave;n tay thả sau lưng v&agrave; đ&ocirc;i mắt nh&igrave;n đăm đăm về ph&iacute;a trước.<br />\r\n<br />\r\n*Nguy&ecirc;n văn: &quot;安第斯山人&quot; - &quot;an đệ tư sơn nh&acirc;n&quot;, &quot;người Andeans&quot; (?). Andes l&agrave; một d&atilde;y n&uacute;i ở Bắc Mỹ, nơi sinh sống của bộ phận nh&oacute;m người Anh-đi&ecirc;ng(?), American Indians, người da đỏ.<br />\r\n<br />\r\n&quot;C&oacute; một cảnh s&aacute;t ở đ&acirc;y, l&atilde;o gọi l&agrave; Warren. B&acirc;y giờ lại th&ecirc;m một người nữa.&quot;<br />\r\n<br />\r\n&quot;T&ecirc;n n&agrave;y tr&ocirc;ng trẻ hơn&quot; t&ecirc;n b&eacute;o l&ugrave;n n&oacute;i. &quot;Tr&ocirc;ng cương trực hơn ch&uacute;t.&quot;<br />\r\n<br />\r\n&quot;C&aacute;c người muốn l&agrave;m g&igrave;?&quot; Faun hỏi.<br />\r\n<br />\r\n&quot;C&ugrave;ng người bạn mới ch&agrave;o hỏi. Đừng lo lắng, vừa n&atilde;y chỉ l&agrave; đ&ugrave;a th&ocirc;i.&quot;<br />\r\n<br />\r\n&quot;T&ocirc;i kh&ocirc;ng cho đ&oacute; l&agrave; một tr&ograve; đ&ugrave;a.&quot; Cậu ph&acirc;n r&otilde; được h&agrave;i hước v&agrave; &aacute;c &yacute;, sức mạnh c&aacute;nh tay của t&ecirc;n kia đủ khiến một người b&igrave;nh thường kh&ocirc;ng ph&ograve;ng bị ngất xỉu trong nh&aacute;y mắt.<br />\r\n<br />\r\n&quot;Được rồi, để t&ocirc;i n&oacute;i lời xin lỗi.&quot; Đầu trọc n&oacute;i đưa tay về ph&iacute;a cậu. Faun nhận ra v&ocirc; luận tr&ecirc;n đường phố hay trong t&ograve;a nh&agrave; gần đ&oacute; đều c&oacute; người, m&agrave; tất cả đều thờ ơ lạnh nhạt.<br />\r\n<br />\r\n&quot;T&ocirc;i đ&atilde; n&oacute;i l&agrave; đừng nh&uacute;c nh&iacute;ch.&quot; Trong l&ograve;ng nảy l&ecirc;n dự cảm chẳng l&agrave;nh, cảnh s&aacute;t lu&ocirc;n c&oacute; một trực gi&aacute;c nhạy b&eacute;n đối với nguy hiểm.<br />\r\n<br />\r\nBa người trước mặt nh&igrave;n cậu, người đ&agrave;n &ocirc;ng h&oacute;i giang hai tay ưỡn ngực trước họng s&uacute;ng.<br />\r\n<br />\r\n&quot;T&ocirc;i muốn nếm thử tư vị vi&ecirc;n đạn, nổ s&uacute;ng đi, ng&agrave;i cảnh s&aacute;t.&quot; Hắn nghi&ecirc;ng đầu n&oacute;i: &quot;Hay ng&agrave;i kh&ocirc;ng biết d&ugrave;ng s&uacute;ng?&quot;<br />\r\n<br />\r\nT&ecirc;n b&eacute;o mập h&aacute; miệng cười rộ l&ecirc;n. Tại thời điểm đ&oacute;, b&agrave;n tay Faun cầm s&uacute;ng n&oacute;ng l&ecirc;n một trận, cảm gi&aacute;c như cậu đang cầm một ngọn lửa b&ugrave;ng ch&aacute;y. Cậu theo bản năng bu&ocirc;ng tay, c&ugrave;ng l&uacute;c đ&oacute; cậu bị đ&aacute;nh mạnh một c&uacute; l&ecirc;n &oacute;t.<br />\r\n<br />\r\nNhất thời, &yacute; thức rời khỏi cơ thể, ch&igrave;m v&agrave;o b&oacute;ng tối s&acirc;u kh&ocirc;ng đ&aacute;y.<br />\r\n<br />\r\n- ----Hết chương 01-----<br />\r\n<br />\r\n[1]Malinois l&agrave; một giống ch&oacute; thuộc nh&oacute;m ch&oacute; chăn cừu Bỉ c&oacute; ngoại h&igrave;nh kh&aacute; giống ch&oacute; chăn cừu Đức nhưng kh&aacute;c biệt với c&aacute;i m&otilde;m đen do đ&oacute; c&ograve;n được gọi l&agrave; B&eacute;c-gi&ecirc; m&otilde;m đen. (Wikipedia)<br />\r\n<br />\r\n[2]Silent Hill v&agrave; Madison l&agrave; những địa điểm trong những phim kinh dị chết ch&oacute;c nổi tiếng của Mỹ.</p>', '0', 'thi-tran', 9, NULL, '2022-08-03 07:39:55', '2022-08-03 07:39:55'),
(53, '1', 'Thiên Phạt hệ thống', '<p>&quot;Mọi người kh&ocirc;ng n&ecirc;n coi thường trung khảo, thi đậu như thế n&agrave;o cấp ba khả năng sẽ quyết định c&aacute;c ngươi sau đ&oacute; nh&acirc;n sinh!&quot; Một c&aacute;i gầy trung ni&ecirc;n l&atilde;o sư đứng ở tr&ecirc;n bục giảng miệng lưỡi lưu lo&aacute;t cho vừa thăng nhập lớp 9 c&aacute;c bạn học l&agrave;m khai giảng động vi&ecirc;n, hắn bỗng nhi&ecirc;n đem trong tay phấn viết bẻ th&agrave;nh hai đoạn hung hăng đ&acirc;̣p v&ecirc;̀ phía ngồi ở h&agrave;ng cuối c&ugrave;ng ch&iacute;nh nằm b&ograve; ở tr&ecirc;n b&agrave;n ngủ một học sinh: &quot;Nhậm H&ograve;a, ngươi đứng l&ecirc;n cho ta! Khai giảng ng&agrave;y thứ nhất liền ngủ, quả thực kh&ocirc;ng hề c&oacute; một ch&uacute;t li&ecirc;m sỉ!&quot;<br />\r\n<br />\r\nBị nện Nhậm H&ograve;a ngẩng đầu m&ocirc;ng lung nh&igrave;n tất cả xung quanh ho&agrave;n cảnh, vừa n&atilde;y cục phấn đập ở tr&ecirc;n đầu c&ograve;n miễn cưỡng đau, tất cả những thứ n&agrave;y ch&acirc;n thật cho hắn biết ch&iacute;nh m&igrave;nh cũng kh&ocirc;ng phải đang nằm mơ.</p>', '<p>&nbsp;</p>\r\n\r\n<p>&quot;Mọi người kh&ocirc;ng n&ecirc;n coi thường trung khảo, thi đậu như thế n&agrave;o cấp ba khả năng sẽ quyết định c&aacute;c ngươi sau đ&oacute; nh&acirc;n sinh!&quot; Một c&aacute;i gầy trung ni&ecirc;n l&atilde;o sư đứng ở tr&ecirc;n bục giảng miệng lưỡi lưu lo&aacute;t cho vừa thăng nhập lớp 9 c&aacute;c bạn học l&agrave;m khai giảng động vi&ecirc;n, hắn bỗng nhi&ecirc;n đem trong tay phấn viết bẻ th&agrave;nh hai đoạn hung hăng đ&acirc;̣p v&ecirc;̀ phía ngồi ở h&agrave;ng cuối c&ugrave;ng ch&iacute;nh nằm b&ograve; ở tr&ecirc;n b&agrave;n ngủ một học sinh: &quot;Nhậm H&ograve;a, ngươi đứng l&ecirc;n cho ta! Khai giảng ng&agrave;y thứ nhất liền ngủ, quả thực kh&ocirc;ng hề c&oacute; một ch&uacute;t li&ecirc;m sỉ!&quot;<br />\r\n<br />\r\nBị nện Nhậm H&ograve;a ngẩng đầu m&ocirc;ng lung nh&igrave;n tất cả xung quanh ho&agrave;n cảnh, vừa n&atilde;y cục phấn đập ở tr&ecirc;n đầu c&ograve;n miễn cưỡng đau, tất cả những thứ n&agrave;y ch&acirc;n thật cho hắn biết ch&iacute;nh m&igrave;nh cũng kh&ocirc;ng phải đang nằm mơ.<br />\r\n<br />\r\nChủ nhiệm lớp nh&igrave;n hắn b&ograve; l&ecirc;n cũng kh&ocirc;ng n&oacute;i th&ecirc;m g&igrave; nữa tiếp theo đề t&agrave;i mới vừa rồi, c&aacute;i th&agrave;nh t&iacute;ch n&agrave;y n&aacute;t tới nơi học sinh hắn cơ bản đ&atilde; bu&ocirc;ng tha cho.<br />\r\n<br />\r\nCh&iacute;nh l&agrave; Nhậm H&ograve;a nh&igrave;n xung quanh quen thuộc b&ecirc;n trong lại c&oacute; ch&uacute;t ho&agrave;n cảnh xa lạ, c&ugrave;ng với ch&iacute;nh m&igrave;nh một lần nữa biến thon thả b&agrave;n tay, hắn r&otilde; r&agrave;ng ch&iacute;nh m&igrave;nh l&agrave; xuy&ecirc;n qua rồi.<br />\r\n<br />\r\nKhổng lồ tin tức v&agrave;o đ&uacute;ng l&uacute;c n&agrave;y tr&agrave;n v&agrave;o đầu &oacute;c của hắn, nhức đầu muốn nứt ra cơ hồ muốn chảy ra m&aacute;u mũi tới! Những n&agrave;y chủ nh&acirc;n cũ lưu lại tin tức để hắn &yacute; thức được n&agrave;y kh&ocirc;ng phải c&aacute;i g&igrave; sống lại, m&agrave; l&agrave; bỗng nhi&ecirc;n đi tới một c&aacute;i thế giới song song, xuy&ecirc;n qua đến một c&aacute;i kh&aacute;c &quot;Ch&iacute;nh m&igrave;nh&quot; tr&ecirc;n người.<br />\r\n<br />\r\nL&uacute;c n&agrave;y, hắn vẫn l&agrave; một c&aacute;i cấp 2 năm thứ ba học sinh.<br />\r\n<br />\r\nĐời trước Nhậm H&ograve;a thật y&ecirc;n lặng sống đến 26 tuổi, kh&ocirc;ng c&oacute; c&aacute;i g&igrave; kh&oacute; khăn, cũng kh&ocirc;ng c&oacute; c&aacute;i g&igrave; th&agrave;nh tựu, m&atilde;i đến tận hắn bỗng nhi&ecirc;n c&oacute; một ng&agrave;y mộng tỉnh, dĩ nhi&ecirc;n ở mỗi một khắc bắt đầu m&atilde;nh liệt nghi vấn ch&iacute;nh m&igrave;nh m&ocirc;̣t đời, sau đ&oacute; &yacute; thức c&agrave;ng l&uacute;c c&agrave;ng mơ hồ, m&atilde;i đến tận l&acirc;m v&agrave;o tối tăm, lại xuy&ecirc;n qua tới đ&acirc;y.<br />\r\n<br />\r\nNhậm H&ograve;a đi&ecirc;n cuồng lật xem đại n&atilde;o tr&agrave;n v&agrave;o tin tức: Lịch sử đại phương hướng vẫn kh&ocirc;ng thay đổi, ch&iacute;nh l&agrave; rất nhiều chi tiết cũng đ&atilde; thay đổi, T&acirc;n Trung Quốc th&agrave;nh lập kh&ocirc;ng c&oacute; như vậy nhấp nh&ocirc;, hơn nữa khoa học kỹ thuật cũng l&agrave; dị thường dẫn trước, trong nước ph&aacute;p chế c&ugrave;ng quy tắc cũng so kiếp trước c&agrave;ng th&ecirc;m ho&agrave;n thiện, nhất l&agrave; tri thức Quyền t&agrave;i sản lĩnh vực.<br />\r\n<br />\r\nM&agrave; quan trọng nhất ch&iacute;nh l&agrave;: Ngữ văn s&aacute;ch b&agrave;i tập thay đổi!<br />\r\n<br />\r\nLẽ ra n&ecirc;n xuất hiện ở lớp 9 ngữ văn s&aacute;ch b&agrave;i tập b&ecirc;n trong Xuất Sư Biểu, Tẩm Vi&ecirc;n xu&acirc;n tuyết, nước ngo&agrave;i thơ hai thủ chờ ch&uacute;t tất cả cũng kh&ocirc;ng c&oacute;, m&agrave; l&agrave; đổi th&agrave;nh mới nội dung! Nhậm H&ograve;a ho&agrave;n to&agrave;n chưa từng thấy nội dung!<br />\r\n<br />\r\nBởi v&igrave; từ đối với 《 Xuất Sư Biểu 》 y&ecirc;u th&iacute;ch duy&ecirc;n cớ, Nhậm H&ograve;a đối với kiếp trước cấp 2 năm thứ ba ngữ văn s&aacute;ch b&agrave;i tập nội dung ấn tượng vẫn l&agrave; rất s&acirc;u sắc, hắn kiếp trước liền kh&aacute; l&agrave; y&ecirc;u th&iacute;ch văn học phương diện đồ vật, bao qu&aacute;t văn học cổ điển c&ugrave;ng hiện đại văn học, cho d&ugrave; l&agrave; v&otilde;ng lạc văn học.<br />\r\n<br />\r\nHơn nữa hắn c&ograve;n c&oacute; một c&aacute;i si&ecirc;u cấp d&ugrave;ng tốt đầu &oacute;c, hắn từng nghe n&oacute;i t&acirc;n đ&ocirc;ng phương Anh ngữ người s&aacute;ng lập c&oacute; thể đủ thời gian mấy tiếng lưng dưới một quyển bộ luật, đọc thuộc l&ograve;ng Anh ngữ Từ Điển cũng l&agrave; dễ d&agrave;ng, loại n&agrave;y sự t&igrave;nh đ&atilde; từng bị người sợ h&atilde;i than, thế nhưng Nhậm H&ograve;a kh&ocirc;ng c&oacute; sợ h&atilde;i than, bởi v&igrave; hắn cũng c&oacute; thể l&agrave;m được.<br />\r\n<br />\r\nChẳng qua loại n&agrave;y k&yacute; ức cũng kh&ocirc;ng phải kh&ocirc;ng giới hạn, c&oacute; thể nhớ kỹ, kh&ocirc;ng c&oacute; nghĩa l&agrave; m&atilde;i m&atilde;i cũng c&oacute; thể nhớ kỹ, n&atilde;o dung lượng d&ugrave; sao cũng c&oacute; hạn.<br />\r\n<br />\r\nNhậm H&ograve;a c&oacute; một loại n&agrave;o đ&oacute; suy đo&aacute;n, thế giới song song văn h&oacute;a lĩnh vực, xuất hiện một loại n&agrave;o đ&oacute; lệch lạc, cho n&ecirc;n đ&atilde; từng bị t&ocirc;n s&ugrave;ng l&agrave; kinh điển đồ vật, đều kh&ocirc;ng ở.</p>\r\n\r\n<p>QUẢNG C&Aacute;O</p>\r\n\r\n<p><br />\r\n<br />\r\nKh&ocirc;ng biết bởi v&igrave; c&aacute;i g&igrave;, thế giới n&agrave;y văn h&oacute;a sản nghiệp cũng rất phồn thịnh, nhưng tr&igrave;nh độ cũng kh&ocirc;ng c&oacute; tr&ecirc;n Tr&aacute;i Đất cao như vậy.<br />\r\n<br />\r\nĐơn giản nhất v&iacute; dụ, thơ cổ l&agrave; c&oacute;, thế nhưng kh&ocirc;ng c&oacute; nhiều như vậy ai cũng kho&aacute;i truyền tụng ngàn năm c&acirc;u thơ.<br />\r\n<br />\r\nV&otilde;ng lạc văn học phương diện c&agrave;ng khoa trương, dường như c&ograve;n dừng lại ở mới vừa từ thực thể văn học b&ecirc;n trong tho&aacute;t thai đi ra giai đoạn, liền ngay cả luyện c&ocirc;ng thăng cấp s&aacute;o lộ đều kh&ocirc;ng c&oacute;.<br />\r\n<br />\r\nHắn nh&igrave;n về ph&iacute;a ngồi c&ugrave;ng b&agrave;n, c&oacute; ch&uacute;t sự t&igrave;nh vẫn l&agrave; x&aacute;c định một ch&uacute;t t&ocirc;́t hơn: &quot;Ngươi nghe n&oacute;i qua Tống Giang, Đường Tăng, T&ocirc;n Ngộ Kh&ocirc;ng sao?&quot; Thiếu ni&ecirc;n người th&iacute;ch nhất phim truyền h&igrave;nh kh&ocirc;ng ngo&agrave;i ch&iacute;nh l&agrave; Thủy Hử Truyện c&ugrave;ng T&acirc;y Du K&yacute;, hỏi n&agrave;y hai bộ si&ecirc;u t&aacute;c b&ecirc;n trong nh&acirc;n vật c&agrave;ng đ&aacute;ng tin một ch&uacute;t.<br />\r\n<br />\r\nMập mạp ngồi c&ugrave;ng b&agrave;n xuy&ecirc;n qua d&agrave;y đặc mắt k&iacute;nh b&igrave;nh tĩnh nh&igrave;n Nhậm H&ograve;a: &quot;Ngươi đang n&oacute;i c&aacute;i g&igrave;?&quot;<br />\r\n<br />\r\n&quot;Ngươi con b&agrave; n&oacute; quản ta n&oacute;i c&aacute;i g&igrave;, &quot; Nhậm H&ograve;a nội t&acirc;m bắt đầu cuồng hỉ, hắn am hiểu nhất kh&ocirc;ng phải l&agrave; phương diện n&agrave;y đồ vật?<br />\r\n<br />\r\nHai c&aacute;i thế giới văn h&oacute;a lĩnh vực phương hướng như l&agrave; xe lửa đường ray giống nhau, ở một c&aacute;i n&agrave;o đ&oacute; tiết điểm bắt đầu mỗi người đi một ng&atilde;, xuất hiện khổng lồ bất đồng, m&agrave; Nhậm H&ograve;a, th&igrave; lại mang theo trong đầu những kia kinh điển t&aacute;c phẩm văn học xuy&ecirc;n qua m&agrave; tới.<br />\r\n<br />\r\nD&ugrave; cho kh&ocirc;ng sao những kia kinh điển,<br />\r\n<br />\r\nNhậm H&ograve;a dựa v&agrave;o trong đầu m&igrave;nh những kia si&ecirc;u việt c&aacute;i thời đại n&agrave;y văn học mạng s&aacute;o lộ, giống nhau c&oacute; thể rất lớn hồng đến t&iacute;m.<br />\r\n<br />\r\nNghĩ đến Xuất Sư Biểu, Nhậm H&ograve;a trong đầu bắt đầu hiển hiện Xuất Sư Biểu c&acirc;u thơ: Ti&ecirc;n đế g&acirc;y dựng sự nghiệp chưa nửa m&agrave; nửa đường chết. . .<br />\r\n<br />\r\nỒ, mặt sau l&agrave; c&aacute;i g&igrave; tới? Nhậm H&ograve;a chợt ph&aacute;t hiện, Xuất Sư Biểu c&acirc;u hắn đều đang kh&ocirc;ng nhớ ra được!<br />\r\n<br />\r\nNhậm H&ograve;a bỗng nhi&ecirc;n c&oacute; ch&uacute;t sợ h&atilde;i những kia trong đầu kinh điển t&aacute;c phẩm bị l&atilde;ng qu&ecirc;n, tuy rằng tr&iacute; nhớ si&ecirc;u cường, nhưng cũng kh&ocirc;ng c&oacute; nghĩa l&agrave; tr&iacute; nhớ của hắn l&agrave; c&oacute; thể kh&ocirc;ng hạn chế khuếch trương xuống, đều sẽ c&oacute; năm xưa k&yacute; ức bị tro bụi bao tr&ugrave;m, cũng lại kh&ocirc;ng nhớ ra được. Giống Xuất Sư Biểu như vậy học được sau khi cũng rất &iacute;t nhấc l&ecirc;n văn chương, đ&uacute;ng l&agrave; như thế.<br />\r\n<br />\r\nHắn muốn thử một ch&uacute;t đem kinh điển trước to&agrave;n bộ viết xuống tới, tỉnh đưa hết cho đ&atilde; qu&ecirc;n, một b&ecirc;n nghĩ, động t&aacute;c tr&ecirc;n tay li&ecirc;n tục bắt đầu ch&eacute;p lại đứng đầu học vỡ l&ograve;ng đồ vật: Tam Tự Kinh.</p>\r\n\r\n<p><br />\r\n<br />\r\nNh&acirc;n chi sơ, t&iacute;nh bổn thiện, t&iacute;nh gần gũi, tập tương xa. . .<br />\r\n<br />\r\nC&oacute; thể n&oacute;i Tam Tự Kinh ch&iacute;nh l&agrave; Nhậm H&ograve;a hồi nhỏ đọc thuộc l&ograve;ng phần đầu ti&ecirc;n kinh điển cổ văn, ở nh&agrave; trẻ c&ograve;n chưa biết chữ thời điểm liền bắt đầu c&otilde;ng.<br />\r\n<br />\r\nCần c&oacute; c&ocirc;ng, h&iacute; v&ocirc; &iacute;ch, giới tai, nghi nỗ lực. L&uacute;c n&agrave;y c&acirc;u cuối c&ugrave;ng viết cho tới khi n&agrave;o xong Nhậm H&ograve;a c&oacute; loại cảm gi&aacute;c thỏa m&atilde;n, đ&acirc;y ch&iacute;nh l&agrave; c&oacute; thể truyền lại đời sau kinh điển a!<br />\r\n<br />\r\nNhưng v&agrave;o đ&uacute;ng l&uacute;c n&agrave;y, trong đầu hắn bỗng nhi&ecirc;n vang l&ecirc;n x&igrave; x&igrave; r&ocirc;̀i r&ocirc;̀i luồng điện thanh: Thi&ecirc;n Phạt hệ thống đ&atilde; kh&oacute;a chặt {Kí Chủ}!<br />\r\n<br />\r\nNhậm H&ograve;a giật nảy m&igrave;nh, thứ đồ g&igrave;? Thi&ecirc;n Phạt hệ thống? M&oacute;n đồ quỷ qu&aacute;i g&igrave; vậy?<br />\r\n<br />\r\nMột c&aacute;i trung t&iacute;nh thanh &acirc;m ở Nhậm H&ograve;a trong đầu vang l&ecirc;n: Nh&acirc;n {Kí Chủ} mang theo thế giới song song văn h&oacute;a th&agrave;nh quả tiến v&agrave;o bản thế giới, l&agrave; bảo vệ nguy&ecirc;n bản thế giới vận h&agrave;nh đường ray, Thi&ecirc;n Phạt hệ thống đem ph&aacute;t h&agrave;nh Thi&ecirc;n Phạt nhiệm vụ, nếu như may mắn ho&agrave;n th&agrave;nh, Thi&ecirc;n Phạt hệ thống đem cam chịu {Kí Chủ} thủ đoạn hợp ph&aacute;p, hơn nữa c&oacute; khen thưởng th&ecirc;m.<br />\r\n<br />\r\nNhậm H&ograve;a bỗng nhi&ecirc;n tỉnh ngộ, h&oacute;a ra l&agrave; bởi v&igrave; ch&iacute;nh m&igrave;nh đem thế giới song song văn h&oacute;a th&agrave;nh quả cho mang đến nơi n&agrave;y, tương đương với cưỡng &eacute;p thay đổi lịch sử xe lửa quỹ t&iacute;ch, cho n&ecirc;n xuất hiện như thế một c&aacute;i Thi&ecirc;n Phạt hệ thống, nếu như nhiệm vụ th&agrave;nh c&ocirc;ng, Thi&ecirc;n Phạt hệ thống mới t&aacute;n th&agrave;nh những n&agrave;y văn h&oacute;a th&agrave;nh quả!<br />\r\n<br />\r\nCh&iacute;nh l&agrave; từ nơi n&agrave;y c&oacute; thể nh&igrave;n ra, đối với thế giới song song văn h&oacute;a th&agrave;nh quả bị mang đến nơi n&agrave;y, Thi&ecirc;n Phạt hệ thống cũng chưa chắc c&oacute; cỡ n&agrave;o phản đối, đại kh&aacute;i n&oacute; to lớn nhất ước nguyện ban đầu, hẳn l&agrave; kh&ocirc;ng muốn để cho Nhậm H&ograve;a kh&ocirc;ng l&agrave;m m&agrave; hưởng!<br />\r\n<br />\r\nBằng kh&ocirc;ng trực tiếp h&agrave;ng nhất đạo l&ocirc;i đ&aacute;nh chết hắn l&agrave; tốt rồi n&agrave;o c&ograve;n c&oacute; nhiều chuyện như vậy, hơn nữa ho&agrave;n th&agrave;nh nhiệm vụ sau khi c&ograve;n c&oacute; thể cho khen thưởng!<br />\r\n<br />\r\nNhậm H&ograve;a kh&ocirc;ng cảm gi&aacute;c được c&aacute;i g&igrave; địch &yacute;, Thi&ecirc;n Phạt n&agrave;y hệ thống c&agrave;ng như l&agrave; một loại g&ograve; b&oacute;, chỉ l&agrave; để hắn kh&ocirc;ng thể kh&ocirc;ng ki&ecirc;ng nể g&igrave; sử dụng thế giới song song văn h&oacute;a th&agrave;nh quả.<br />\r\n<br />\r\nChẳng qua Nhậm H&ograve;a c&oacute; một ch&uacute;t nghi vấn: Thi&ecirc;n Phạt hệ thống cũng kh&ocirc;ng c&oacute; kh&ocirc;ng cho hắn hiện tại liền khiến cho d&ugrave;ng hoặc l&agrave; c&ocirc;ng bố Tam Tự Kinh, n&oacute;i c&aacute;ch kh&aacute;c d&ugrave; cho hắn hiện tại liền lợi dụng Tam Tự Kinh kiếm tiền cũng c&oacute; thể, hơn nữa. . . Cũng kh&ocirc;ng n&oacute;i thất bại sẽ như thế n&agrave;o!<br />\r\n<br />\r\nN&oacute; chỉ n&oacute;i l&agrave;, th&agrave;nh c&ocirc;ng c&ograve;n sẽ c&oacute; khen thưởng th&ecirc;m!<br />\r\n<br />\r\nTrung t&iacute;nh thanh &acirc;m lại vang l&ecirc;n: Nhiệm vụ: Nh&acirc;n {Kí Chủ} mang theo thế giới song song văn h&oacute;a th&agrave;nh quả Tam Tự Kinh, hạn trong v&ograve;ng một th&aacute;ng, đang kh&ocirc;ng c&oacute; bất kỳ bảo hộ biện ph&aacute;p, mưu lợi thủ đoạn t&igrave;nh huống, nhảy lướt qua 1, số 2 lầu dạy học, c&oacute; thể chạy lấy đ&agrave;.</p>\r\n\r\n<p><br />\r\nTiếng n&oacute;i vừa dứt, trong c&otilde;i u minh c&oacute; một sợi li&ecirc;n quan với chạy lấy đ&agrave; nhảy kinh nghiệm r&oacute;t v&agrave;o Nhậm H&ograve;a trong đầu, phảng phất đ&acirc;y ch&iacute;nh l&agrave; li&ecirc;n quan với chạy lấy đ&agrave; nhảy ch&iacute;nh x&aacute;c nhất phương thức c&ugrave;ng kinh nghiệm dung nhập v&agrave;o th&acirc;n thể của Nhậm H&ograve;a b&ecirc;n trong, hắn chỉ cần đi qua r&egrave;n luyện đi cầm chắc n&oacute; l&agrave; c&oacute; thể.<br />\r\n<br />\r\nNhậm H&ograve;a mộng bức. . .<br />\r\n<br />\r\nTrong trường học số 1 lầu dạy học c&ugrave;ng số 2 lầu dạy học l&agrave; giống nhau cao 7 tầng lầu, ch&iacute;nh l&agrave; hai đống kề b&ecirc;n lầu dạy học ch&iacute;nh giữa c&oacute; tới 3 m&eacute;t nhiều khoảng c&aacute;ch! Vậy cũng l&agrave; 7 tầng lầu độ cao a, chẳng may nhảy thời điểm thất bại, chẳng phải l&agrave; muốn quỳ?<br />\r\n<br />\r\nHắn bỗng nhi&ecirc;n r&otilde; r&agrave;ng l&agrave; c&aacute;i Thi&ecirc;n Phạt g&igrave; hệ thống kh&ocirc;ng c&oacute; giải th&iacute;ch nếu như thất bại sẽ như thế n&agrave;o, bởi v&igrave; kh&ocirc;ng cần giải th&iacute;ch, nh&igrave;n thấy nhiệm vụ thời điểm Nhậm H&ograve;a một c&aacute;ch tự nhi&ecirc;n liền c&oacute; thể r&otilde; r&agrave;ng, thất bại ch&iacute;nh l&agrave; c&aacute;i chết. . . Kh&ocirc;ng chết cũng t&agrave;n phế!<br />\r\n<br />\r\nN&agrave;y con b&agrave; n&oacute; kh&ocirc;ng phải c&aacute;i Thi&ecirc;n Phạt g&igrave; hệ thống, n&agrave;y con b&agrave; n&oacute; ch&iacute;nh l&agrave; c&aacute;i hố cha hệ thống a. . .<br />\r\n<br />\r\nTuy rằng trong n&agrave;y c&ograve;n c&oacute; c&ograve;n sống khả năng, bởi v&igrave; đối với chạy lấy đ&agrave; nhảy m&agrave; n&oacute;i hơn ba thước cũng kh&ocirc;ng phải c&aacute;i g&igrave; qu&aacute; kh&oacute; sự t&igrave;nh, c&oacute; thể Thi&ecirc;n Phạt hệ thống l&agrave; hạ thủ lưu t&igrave;nh, Nhậm H&ograve;a ho&agrave;n th&agrave;nh nhiệm vụ cũng kh&ocirc;ng xem như đặc biệt kh&oacute; khăn.<br />\r\n<br />\r\nC&oacute; thể đ&oacute; l&agrave; 7 tầng lầu a, Nhậm H&ograve;a đứng bảy tầng l&acirc;u b&ecirc;n cạnh nh&igrave;n xuống lầu đều c&oacute; ch&uacute;t cho&aacute;ng. . .<br />\r\n<br />\r\nHơn nữa, chẳng may sau đ&oacute; c&ograve;n sẽ c&oacute; c&aacute;i g&igrave; c&agrave;ng biến th&aacute;i nhiệm vụ đ&acirc;y? Chẳng hạn như l&acirc;u ch&iacute;nh giữa khoảng c&aacute;ch biến th&agrave;nh 4 m&eacute;t, 5 m&eacute;t. . . Hoặc l&agrave; đổi th&agrave;nh c&ograve;n lại c&aacute;i g&igrave; hạng mục?<br />\r\n<br />\r\nChẳng hạn như sống s&oacute;t suất 30% c&aacute;nh trang phi h&agrave;nh c&aacute;i g&igrave;? Lại chẳng hạn như tay kh&ocirc;ng leo n&uacute;i c&aacute;i g&igrave;? Nhậm H&ograve;a tưởng tượng đ&atilde; mở ra, dường như trước mắt đ&atilde; c&oacute; một phiến mạo hiểm con đường cửa lớn ở trước mặt hắn mở ra.<br />\r\n<br />\r\nNhậm H&ograve;a ở trong đầu hỏi: &quot;Ta c&oacute; thể hay kh&ocirc;ng. . . Kh&ocirc;ng chấp nhận nhiệm vụ n&agrave;y? Nếu như kh&ocirc;ng chấp nhận hội c&oacute; hậu quả g&igrave; kh&ocirc;ng? Đưa ta về nguy&ecirc;n thế giới cũng được a đại ca!&quot;<br />\r\n<br />\r\n&quot;Ng&agrave;y quy định tới l&uacute;c chưa tiếp thu nhiệm vụ, gạt bỏ.&quot;</p>\r\n\r\n<div class=\"ddict_btn\" style=\"left:132.688px; top:21px\"><img src=\"chrome-extension://bpggmmljdiliancllaapiggllnkbjocb/logo/48.png\" /></div>', '0', 'thien-phat-he-thong', 10, NULL, '2022-08-03 07:44:04', '2022-08-03 07:44:04');

